				['23'] = [[Seguilo nella casa]],
				['33'] = [[Dimmi cos'è successo qui, bastardo!']],
				['43'] = [[Non lo so, forse hai ragione, ma non hai alcun diritto di giudicarmi.']],
				['53'] = [[Volevo scoprire cos'è successo a tuo marito.']],
				['63'] = [[Hai rifiutato?']],
				['73'] = [[Gena, non ucciderlo! Lascialo andare nelle terre desolate da dove non ritornerà più. Quella sarebbe giustizia!']],
				['104'] = [[E con chi?']],
				['93'] = [[Per come la vedo io, avete torto entrambi. Non si beve durante una spedizione. Se foste stati sobri, passare dal muro non vi sarebbe sembrata un'idea così brillante.']],
				['85'] = [[Non ci hai pensato?! E allora a cosa stavi pensando?!']],
				['111'] = [[Carica la pistola e porgila al ragazzo]],
				['84'] = [[Porta il corpo di Gena a Nadezhda]],
				['83'] = [[Grazie. Spero che ti riprenderai presto. Tornerò a farti visita, se ci riesco.']],
				['100'] = [[Lascialo scappare]],
				['82'] = [[Vai e scava una tomba]],
				['81'] = [[Ed è colpa mia? Ottimo. Torniamo all'accampamento. Lì decideranno di chi è la responsabilità. La madre di Gena, forse.']],
				['13'] = [[Abbiamo già perso troppo tempo. Torniamo indietro.']],
				['8'] = [[Porta Gena fuori e accompagnalo all'accampamento fuori Tula]],
				['79'] = [[Prendi il bicchiere]],
				['78'] = [[Ascolta bevendo lentamente il liquore]],
				['77'] = [[Gena, non farlo! Consegniamo Vanya al tribunale dell'accampamento. Lo puniranno per averti abbandonato!']],
				['106'] = [[Ok, hai vinto. Vedrò cosa posso fare, ma non mi piace affatto questa storia.']],
				['70'] = [[Porta Gena all'accampamento]],
				['75'] = [[Gena, abbassa la pistola! Possiamo trovare una soluzione pacificamente!']],
				['74'] = [[Puoi rilassarti un'altra volta. Se non vuoi problemi, vestiti e andiamo.']],
				['37'] = [[Esamina il corpo]],
				['47'] = [[Aspetta che Vanya vada via]],
				['57'] = [[Allora, grazie. Suppongo di dover andare a Oryol adesso.']],
				['67'] = [[Sai, questa conversazione sarebbe molto più produttiva senza la pistola.']],
				['72'] = [[Digli che Gena è sopravvissuto e vuole vederlo]],
				['9'] = [[E poi, devi. Chi baderà a tua madre, se non ci sei?']],
				['102'] = [[Colpisci Vanya sullo stinco]],
				['27'] = [[Porta Gena all'accampamento]],
				['56'] = [[Mi sembra giusto...']],
				['46'] = [[Va bene, se hai deciso così, faremo a modo tuo.']],
				['36'] = [[Seppellisci Vanya e porta il corpo di Gena a Nadezhda]],
				['52'] = [[Ho fatto quello che dovevo. So come ti senti.']],
				['42'] = [[Giustizia? Sei pazzo? Hai ucciso una persona. Sei un assassino!']],
				['32'] = [[Torna indietro]],
				['76'] = [[Un po' di relax fa bene. Non mi offri qualcosa da mangiare? Mi hai invitato tu, ricordi?']],
				['12'] = [[Cosa sarà troppo tardi?']],
				['7'] = [[Aspetta]],
