	['npc'] = {
		['nadezhda'] = {
			['episode'] = {
				['9'] = [[(Nadezhda resta seduta immobile per vari minuti. Poi si alza a fatica e si versa dell'acqua da una brocca. A quel punto noto che ci sono due tazze sul tavolo.<new_line>Sulla seconda tazza c'è raffigurato un cucciolo di leone. Una rivista per ragazzi sbuca da sotto il cuscino sul secondo materasso)]],
				['8'] = [[(Il labbro della donna trema. Mi guarda infuriata)<new_line><new_line>Importante? Non potevi scegliere momento migliore? Vattene!]],
				['7'] = [[(Nadezhda ha una specie di spasmo nel sentir nominare il marito. Poi si imbroncia e si gira dall'altro lato)<new_line><new_line>Vattene, per favore.]],
				['10'] = [[(Mi ricordo degli sguardi ostili degli abitanti dell'accampamento. Erano forse coinvolti nella scomparsa del compagno di stanza di Nadezhda? Dopo aver bevuto un po' d'acqua ed essersi calmata, Nadezhda si gira verso di me)<new_line><new_line>Sei ancora qui? Perché mi guardi? Lasciami in pace!]],
				['11'] = [[Se vuoi aiutarmi, allora vattene subito!]],
				['12'] = [[(Il marito della donna è l'unica pista che possa portarmi alla verità sugli eventi di tanto tempo fa. Dovevo convincerla a parlare)<new_line><new_line>Gena, mio figlio.]],
				['16'] = [[Ok, beh... Torna presto.]],
				['15'] = [[Ah, sei tu. E Gena?]],
				['14'] = [[Ancora tu? Cosa vuoi da me?]],
				['13'] = {
					['2'] = [[Non chiedevo molto. Volevo solo seppellire mio figlio. Guardare il suo viso per l'ultima volta... Ho chiesto agli altri di riportare qui il suo corpo, ma si sono rifiutati. Perché rischiare vite per un cadavere? Non ne valeva la pena, mi hanno detto. Era tutto ciò che chiedevo, ma non ho potuto farci nulla.]],
					['1'] = [[(Nadezhda singhiozza e avvicina un fazzoletto al naso. Cala di nuovo un silenzio teso. Poi, finalmente, Nadezhda mi guarda di nuovo)<new_line><new_line>È uscito con uno dei suoi amici, Vanya, per andare a Novomoskovsk. È stato due giorni fa, per cercare cibo. Ma Vanya è tornato da solo. Ha detto che l'edificio è crollato e che Gena è morto...]]
				},
				['6'] = [[Chi sei? Cosa vuoi da me?]],
				['5'] = [[(Nadezhda probabilmente fa fatica a credere che un estraneo sia disposto ad aiutarla, quando tutti gli altri abitanti dell'accampamento si sono rifiutati di farlo. Poi, si ricompone e sussurra)<new_line><new_line>Ok, allora... torna al più presto. E stai attento.]],
				['4'] = [[Vattene, per favore...]],
				['3'] = [[(Nadezhda mi guarda, ma è scettica)<new_line><new_line>Davvero lo faresti? Davvero mi porteresti Gena?]],
				['2'] = [[(La vecchia, o per essere precisi, la donna invecchiata prematuramente non si spaventa. Mi guarda con degli occhi grigi stanchi e sospettosi e non parla)]],
				['1'] = [[(Fa freddo ed è buio nella casetta. Una donna esile con i capelli grigi è seduta su un vecchio materasso appoggiato alla parete. Dev'essere Nadezhda Kulik. Un altro materasso è appoggiato alla parete opposta, ma non c'è nessun altro.<new_line>Mi avvicino a Nadezhda)]],
				['17'] = [[Ah, sei tu. Ti prego di scusarmi, ma sono un po' impegnata. Ma grazie ancora per tutto.]]
			},
			['name'] = [[Nadezhda]],
			['choice'] = {
				['14'] = [[No. Non sono andato ancora a prenderlo.]],
				['13'] = [[Niente... Scusa.]],
				['10'] = [[Voglio solo aiutarti.]],
				['9'] = [[(Continua ad aspettare)]],
				['8'] = [[(Ritirati verso la porta e aspetta)]],
				['7'] = [[Nadezhda, ascoltami, è importante...]],
				['6'] = [[Volevo chiederti una cosa su tuo marito.]],
				['5'] = [[Scusa per l'intrusione. Ho proprio bisogno del tuo aiuto. Puoi dedicarmi un paio di minuti? Poi me ne andrò e non ti disturberò mai più.]],
				['4'] = [[Sì, te lo riporterò.]],
				['3'] = [[Hai la mia compassione...]],
				['2'] = [[Posso trovare il corpo di tuo figlio e riportarlo da te.]],
				['1'] = [[Nadezhda? Non ti spaventare. Non ti farò del male.]],
				['11'] = [[Qualcuno a te caro è scomparso. Chi?]],
				['12'] = [[Cosa gli è successo?]]
			}
		},
		['gavrilov'] = {
			['episode'] = {
				['19'] = [[(L'ex ispettore ci riflette su, lanciandomi uno sguardo serio)<new_line><new_line>Affare fatto. Conoscerai tutta la verità, ma prima devi rispettare la tua promessa.]],
				['9'] = [[Un amico? Non mi ricordo di te. Da quale accampamento vieni?]],
				['17'] = [[La verità? E cosa te ne fai della verità, girovago senza nome? La racconterai a tutti?]],
				['27'] = [[Accomodati. Suppongo che sia questo il segreto della lunga vita dei lupi solitari.]],
				['23'] = [[Ci abbiamo già provato. Non funziona.<new_line>Quindi, lo farai? Se ci aiuterai, salverai molte vite. Dopodiché ti diremo tutto. Se non lo farai, dovrai cercare qualcun altro.]],
				['24'] = [[Perfetto. A giudicare dal tuo equipaggiamento, sei un combattente esperto, quindi avremo più possibilità di successo con te dalla nostra parte. Quando sarai pronto per cacciare, prendi munizioni, cibo e bende, e vai al punto di partenza. Ti aspetterò lì.]],
				['25'] = [[(La bocca di Gavrilov accenna un sorriso, come se si aspettasse quella risposta)<new_line><new_line>La decisione spetta a te, ovviamente, ma questo vuol dire che io non proferirò parola. Un patto è un patto. Se cambi idea, torna pure. Abbiamo ancora un po' di tempo.]],
				['26'] = [[Allora? Ci hai pensato? La mia offerta è ancora valida.]],
				['20'] = [[A Kiev hai detto che ci avresti aiutato a risolvere il problema dei lupi. Quindi, fallo, e dopo ti diremo tutto.]],
				['10'] = [[Cosa? Sei arrivato fin qui da Mosca? E perché dovremmo crederti? Anche io me la cavo con le parole.]],
				['22'] = {
					['2'] = [[Ci siamo difesi al meglio delle nostre possibilità, ma il branco si è allargato, mentre noi abbiamo perso sempre più persone. Una settimana fa, sono entrati in una casa di notte e hanno sbranato una famiglia intera. Abbiamo mandato una squadra intera per abbatterli, ma nessuno è mai ritornato vivo.]],
					['1'] = [[Queste bestie, i lupi neri, sono comparse nelle nostre terre circa tre anni fa. Una lupa e due cuccioli, enormi, neri come la notte, e ciechi. I primi due anni abbiamo coabitato senza problemi. Ma poi la selvaggina è scomparsa dai boschi. Forse i lupi hanno contratto la rabbia da qualche parte, o forse è successo qualcos'altro, ma fatto sta che hanno iniziato a dare la caccia alle persone.]],
					['4'] = [[Non possiamo più aspettare. La lupa è nascosta in un vecchio mattatoio. La attaccheremo lì. È la nostra unica occasione di eliminarli una volta per tutte.]],
					['3'] = [[Ma abbiamo ancora speranza. Tutti i lupi che abbiamo ucciso erano maschi. Questo significa che se uccidiamo la lupa, non nasceranno nuovi cuccioli.]]
				},
				['21'] = [[Niente di pesante. Devi solo partecipare alla caccia. Probabilmente non sai cos'è successo qui, vero?]],
				['8'] = [[(Tutti e tre si girano improvvisamente verso di me. Ora tutte e tre le Makarov sono puntate al mio ventre)<new_line><new_line>Ehi! E tu chi diavolo sei?]],
				['12'] = [[Chi te l'ha detto?]],
				['11'] = [[(Gli occhi sospettosi di Gavrilov mi squadrano dalla testa ai piedi, poi scuote la testa)<new_line><new_line>No, ce la caveremo da soli.]],
				['16'] = [[(L'ispettore e il direttore si scambiano uno sguardo senza dire niente. Zaitsev, corrucciato, sta per parlare, ma Gavrilov gli fa segno di stare zitto. Si avvicina a me e mi studia da capo a piedi)<new_line><new_line>Day R? Perché vuoi saperlo? Saperlo cambierà qualcosa? Ormai è passato tanto tempo.]],
				['15'] = [[Perché così silenzioso? Di cosa volevi parlarmi?]],
				['14'] = [[(Sentendo il nome, mi sono emozionato. Il direttore dell'Istituto di virologia! Finalmente avrò le risposte a tutte le mie domande)]],
				['13'] = {
					['2'] = [[Eccolo. Fa' come se fossi a casa tua, come si suol dire. Ma prima, voglio presentarti il mio amico. Anche lui vuole parlare con te. Timofey Ziatsev, ex direttore dell'Istituto di virologia. Come me, anche lui sperava di lasciare la Polonia, ma non ha avuto il tempo.]],
					['1'] = [[(Pavel Gavrilov mi sta aspettando nell'ufficio del direttore dell'ex centro sociale. C'è qualcun altro in stanza con lui, un uomo sulla settantina appisolato su una poltrona vicino alla stufa. Dopo avermi visto, Gavrilov riempie un bicchiere d'acqua, sveglia il vecchio e gli porge una pillola che deve prendere)]]
				},
				['6'] = {
					['2'] = [[Siete... Siete solo dei codardi! Kirill si è sacrificato per impedire a un lupo di attaccarti, Gavrilov! Che c'è? Non accetti la verità? Quindi adesso andrai lì e li ucciderai tutti!]],
					['1'] = [[(Il giovane guarda Gavrilov e l'altro uomo, e poi lentamente estrae una pistola e gliela punta contro)]]
				},
				['5'] = [[Continuare? Siamo rimasti solo in tre! E siamo sopravvissuti a malapena in quattro. Avrai un'altra possibilità di vendicare il tuo patrigno, ma non ora. Ora dobbiamo tornare indietro!]],
				['4'] = [[(Il giovane alza gli occhi sbalorditi e arrossati verso l'uomo e balza in piedi)<new_line><new_line>Ce ne andiamo?! È troppo pericoloso? Che stai dicendo, Gavrilov? Dobbiamo continuare! Ci sono ancora dei mostri là fuori! Abbiamo abbastanza cartucce per uno o due.]],
				['3'] = [[Nikita, noi torneremo indietro. O vieni con noi o puoi uccidere i lupi da solo.]],
				['2'] = [[(Gavrilov socchiude gli occhi)<new_line><new_line>Hai stuzzicato il mio interesse. Ok, possiamo discuterne. Quando saremo tornati. Gorenichi, il centro sociale.]],
				['1'] = [[Nikita, so come ti senti, ma dobbiamo andare. Riporteremo Kirill a casa e lo seppelliremo.<new_line>Nikita, mi stai ascoltando? Ce ne andiamo. Torniamo a casa a Gorenichi. È troppo pericoloso qui. Seva ha difficoltà a camminare e ha bisogno di un medico!]],
				['7'] = [[(L'uomo si china minacciosamente sul ragazzo)<new_line><new_line>Se qualcuno non avesse preso l'ultima pistola e non avesse inseguito il lupo come un coglione, Kirill sarebbe ancora vivo!]],
				['18'] = [[Va bene (mette le braccia dietro la schiena e inizia a camminare nella stanza). E cosa sei disposto a fare per conoscere questa verità? Per esempio, rischieresti la vita?]]
			},
			['name'] = [[Gavrilov]],
			['choice'] = {
				['19'] = [[No. Ho visto quei lupi e so di cosa sono capaci. Sono disposto a rischiare la mia vita, ma quello che suggerisci, andare nella tana di un branco intero, è un suicidio preannunciato, non un rischio calcolato.]],
				['9'] = [[Voglio sapere degli scienziati che sono stati giustiziati; dell'epidemia che è scoppiata contemporaneamente in tutto il Paese; e del 17 agosto 1984, il Day R.]],
				['17'] = [[Ci sto. Si deve fare. Quei lupi neri sono troppo pericolosi.]],
				['18'] = [[Se altrimenti non resterai fregato... Ok, ci sto.]],
				['20'] = [[Ci capiamo.]],
				['10'] = [[Non importa... Voglio sapere la verità.]],
				['11'] = [[Quelli sono affari miei.]],
				['21'] = [[No. Non ho ancora istinti suicidi.]],
				['8'] = [[...]],
				['16'] = [[Capito. Ma perché non incendiate il mattatoio con la lupa dentro? O non la attirate fuori?]],
				['15'] = [[No, non lo so.]],
				['14'] = [[E cosa volete da me?]],
				['13'] = [[La mia promessa? Di cosa parli?]],
				['6'] = [[Come posso dimostrarlo? Non lo so... Posso aiutarvi a trasportare il corpo, proteggerlo dai lupi.]],
				['5'] = [[(Menti) Sono di Tver.]],
				['4'] = [[Calmi... Sono un amico.]],
				['3'] = [[(Tossisci per attirare la loro attenzione)]],
				['2'] = [[Ok, ne parleremo lì.]],
				['1'] = [[Non è importante. Devo discutere una cosa con te, riguardo agli scienziati dell'Istituto di virologia.]],
				['7'] = [[Aspetta, non andare. Sei Pavel Gavrilov, l'ex ispettore, vero?]],
				['12'] = [[Ne ho già passate tante qui. Ho già rischiato la vita.]]
			}
		},
		['vanya'] = {
			['episode'] = {
				['9'] = [[(Vanya è a casa ed è evidente che non è solo. Entrare lì dentro è l'ultima cosa che voglio fare.)]],
				['8'] = [[Ehi, girovago, ascoltami. Gena non andrà da nessuna parte, ma questa bottiglia non ti aspetterà. Hai cambiato idea?]],
				['7'] = [[Cosa? E ti reputi un russo!]],
				['6'] = [[(Vanya ride di nuovo.)<new_line><new_line>Andrai davvero fino a Novomoskovsk per recuperare un cadavere? (Il ragazzo si zittisce e poi mi guarda incuriosito.) Ascolta, girovago, ho un'idea. Andiamo a casa mia. Ho portato degli alcolici da Novomoskovsk. Sull'etichetta c'è scritto 'Brjanskaya'. Conosci questa marca? L'unico problema è che non ho nessuno con cui berli: ci sono solo donne qui.]],
				['5'] = [[In un edificio a cinque piani, di lato ad alcuni garage, in un appartamento al terzo piano. Perché lo vuoi sapere?]],
				['4'] = [[(Vanya ride e lancia la sigaretta tra i cespugli. Poi mi guarda con uno sguardo canzonatorio. Che ragazzino insolente.)<new_line><new_line>E cosa desidera sapere, mio signore?]],
				['3'] = [[Su Gena, vero? Ho visto che sei andato a trovare sua madre. Ma non ti dirò niente. Ho dovuto raccontare la storia a tutti almeno per tre volte. Mi sono stufato.]],
				['2'] = [[(Vanya, addirittura, fischia.)<new_line><new_line>Un girovago? Non pensavo che esistessero ancora degli idioti che se ne vanno a spasso da soli nelle terre desolate.]],
				['1'] = [[(Vanya fuma una sigaretta seduto sul portico. Dopo poche parole, capisco che è ubriaco.)<new_line><new_line>Ehi. È da tanto che non vediamo un volto nuovo da queste parti. Da dove vieni? Da quale tana sbuchi?]]
			},
			['name'] = [[Vanya]],
			['choice'] = {
				['6'] = [[Sai, non mi va molto di bere con te.]],
				['5'] = [[Voglio riportare il corpo di Gena alla madre, per dargli una degna sepoltura.]],
				['4'] = [[Dimmi solo in quale edificio l'hai lasciato.]],
				['3'] = [[Vorrà dire che dovrai raccontarla un'altra volta.]],
				['2'] = [[Beh, esistono. Voglio chiederti una cosa.]],
				['1'] = [[Sono un girovago.]],
				['7'] = [[Grazie, un'altra volta.]]
			}
		},
		['spiridon'] = {
			['episode'] = {
				['19'] = [[Non pensare ai ringraziamenti. Portami la vodka.]],
				['9'] = [[Oh, ora ci credo! Neanche lui ti conosce! Cerchi di nuovo di prendermi in giro?<new_line><new_line>(Lyuba Kugal dà uno schiaffone al marito)]],
				['8'] = [[(Un uomo minuto fa capolino dalle spalle larghe della donna. Ha un volto intelligente ma logoro)<new_line><new_line>Lyuba, aspetta. Non lo conosco. Non è del nostro insediamento.]],
				['7'] = [[So perché sei qui! Per ubriacarti, gridare e sbraitare, ecco perché! Quindi te ne vai o vuoi una mano? Lo faccio! Ti aiuto io...]],
				['23'] = [[(Dopo aver terminato una breve e spiacevole conversazione con Lyuba, riesco a parlare di nuovo con Spiridon)]],
				['24'] = [[(Spiridon mi saluta a voce volutamente alta, guardando con sospetto la porta dietro cui si nascondeva Lyuba)<new_line>Ciao, ciao, ciao!<new_line>(Poi, a voce più bassa, continua)<new_line>Hai preso la vodka? Quattro bottiglie?]],
				['25'] = [[Ho capito. Va bene, aspetto un altro po'.]],
				['26'] = [[In bocca al lupo. Come procede? Bene? Beh, ci vediamo in giro. Lyuba non è di buon umore oggi. Ci aggredirà entrambi se ne avrà l'occasione.]],
				['20'] = [[Non c'è bisogno di riaccompagnarmi. Ho visto una moto nel garage. Quasi nuova, serie limitata. Arriverò a casa molto più in fretta con quella. L'ho anche modificata, così le api non mi prenderanno. Ci vediamo!]],
				['10'] = [[Aiuto? Che razza di aiuto può dare questo ubriacone? Nessuno! Passa le giornate seduto in casa! Spiridon, torna in cucina!]],
				['11'] = [[Che vuoi dire, a cosa servo? Il Paese ha sempre bisogno di ingegneri preparati! Io ho persino un certificato di ringraziamento dal comitato regionale del partito!]],
				['12'] = [[(La moglie schiaffeggia di nuovo il marito, così forte da farlo gridare)<new_line><new_line>Questo è il ringraziamento! E puoi andare a fanculo, bastardo, suino! Mi ha rovinato la vita! Ho sprecato con lui gli anni migliori della mia vita! Mia madre aveva ragione, pace all'anima sua... Avrei dovuto sposare Ptichkin! Forse saresti morto affogato nell'alcol!]],
				['27'] = [[Di nuovo tu? Te l'ho già detto. Non vengo da nessuna parte! È fuori discussione!]],
				['17'] = [[Guarda. Bello, eh? Il treppiedi è un po' difettoso, quindi dovrai usare un mattone come sostegno. Il coordinatore vira verso il centro, ma questo è un difetto di fabbrica: tutti i lotti avevano lo stesso problema. E non è neppure molto stabile nelle scanalature, quindi devi tenerlo dolcemente. E un'altra cosa...]],
				['22'] = [[Grazie! Bottiglie pregiate! La offrirò ai ragazzi domani. Fammi un favore però: non dire niente di fronte a mia moglie. La mia signora ha un gancio destro da paura. Chi l'avrebbe mai detto... Un tempo era dolcissima. Bei tempi...]],
				['16'] = [[Presto! Non sai cosa potrebbe farmi mia moglie?]],
				['15'] = [[Beh, perdonami. La mia Lyuba non è un raggio di sole, ma lei è una sola, e ci sono tante api. Non esiste che io venga a Ufa!]],
				['14'] = [[Beh, pensavo solo che mi ricordi un mio vecchio amico... Aspetta, che hai detto? Ufa? È pieno di api lì!<new_line>Non se ne parla. Non andrò a Ufa, ci tengo alla mia vita! Trovati un altro imbecille.]],
				['13'] = [[(Lyuba sbatte la porta, rinchiudendosi in cucina. Spiridon Kugel mi lancia un'occhiataccia)<new_line><new_line>È nervosa da stamattina, per qualche motivo. Di' agli altri che oggi non uscirò. Non posso, l'hai visto. Uscirò domani, prometto!]],
				['6'] = [[Sciocchezze! Non ci si dimentica come fare il proprio mestiere! E nel mio campo, l'età è un punto a favore. Guarda i giovani d'oggi: sono solo un mucchio di scimmie maldestre!<new_line>Preparati per andare a Ufa!]],
				['5'] = [[Un sistema missilistico? Cosa? So riparare un carrarmato a occhi chiusi! Sono stato persino invitato a Severodvinsk per costruire un sottomarino nucleare!]],
				['4'] = [[Ah... mi dispiace... ma capisci... Non posso lasciare Lyuba qui da sola... ma chiamami se ci sono novità.]],
				['3'] = [[Boris chi? Su cosa aveva ragione?]],
				['2'] = [[(Kugel si bagna le labbra e fa un rumore simile a uno schiocco)<new_line><new_line>Da una distilleria? Buona. La nostra vodka fatta in casa non vale niente.<new_line>Ok, ora ci siamo. Tre bottiglie, questo è l'accordo. Nel frattempo, devo capire dopo posso nascondermi da Lyuba.]],
				['1'] = [[(I Kugel vivono al primo piano dell'edificio a cinque piani. Una donna robusta mi apre la porta con uno sguardo fiero)<new_line><new_line>Ancora tu? Vattene, ubriacone maledetto! Ci siamo stancati! Per l'ultima volta, non verrà da nessuna parte!]],
				['21'] = [[(Kugel scuote il capo con disapprovazione)<new_line><new_line>Ok, compagno, la prossima volta. Non vuoi mica che smonti di nuovo il lanciamissili, vero? Già mi devi quattro bottiglie.]],
				['18'] = [[Comunque, di' a Boris che i vecchi capi sono ancora operativi! Può venire a trovarmi. Lyuba gli preparerà delle torte e potremo chiacchierare e ricordare il passato.]]
			},
			['name'] = [[Spiridon Kugel]],
			['choice'] = {
				['19'] = [[Grazie, mi hai aiutato tantissimo.]],
				['9'] = [[Ehi, non picchiarlo. Sta dicendo la verità! Sono qui per lavoro. Mi serve aiuto.]],
				['17'] = [[Vacci piano.]],
				['18'] = [[Andiamo, andiamo.]],
				['23'] = [[Tieni. Tre bottiglie, come ti avevo promesso.]],
				['24'] = [[Quattro? Maledizione. Le porterò al più presto.]],
				['25'] = [[Ok, ok, non le dirò niente.]],
				['26'] = [[Ciao.]],
				['20'] = [[Grazie, mi hai aiutato tantissimo.]],
				['22'] = [[Senti, non ce l'ho con me. Te le do la prossima volta.]],
				['21'] = [[Riferirò.]],
				['16'] = [[A Ufa? Ok, come vuoi.]],
				['15'] = [[Affare fatto!]],
				['14'] = [[E dove troverò un altro ingegnere militare?]],
				['13'] = [[Non so di cosa parli. Mi serve davvero il tuo aiuto. Devo trovare e assemblare una cosa. A Ufa.]],
				['6'] = [[Penso che mi hai confuso con qualcun altro. Sono qui per incontrare tuo marito.]],
				['5'] = [[È successo tanto tempo fa, compagno. L'età, l'alcol...]],
				['4'] = [[Boris, il maggiore di Stepnoy. Discutevamo se Kugel, l'ingegnere che ha ricevuto un riconoscimento ufficiale dal comitato regionale, fosse in grado di costruire un sistema missilistico anti-carro. E Boris sosteneva che tu non hai più la scintilla di una volta.]],
				['3'] = [[Che non c'è motivo di uccidere gli ingegneri intelligenti di cui il Paese ha un disperato bisogno.... È un peccato. Dovrò cavarmela da solo.]],
				['2'] = [[(Manipolalo)<new_line>Che peccato. Pare che Boris avesse ragione. Stai invecchiando, ingegnere.]],
				['1'] = [[E se ti porto della vodka? Roba buona, da una distilleria.]],
				['28'] = [[Sì, ecco, tieni. Come da accordi, più gli interessi.]],
				['27'] = [[No, Spiridon, scusa. Non ce l'ho ancora. Lo sai anche tu... Questa roba non si trova mica sui bordi della strada.]]
			}
		},
		['boris'] = {
			['episode'] = {
				['19'] = [[(Questa volta, non c'era nessuno nell'unità di commando a parte Boris. Quando sono entrato, ho notato una costosa bottiglia di vino e due bicchieri. Boris mi ha detto di bere con lui. Era davvero di ottimo umore. Ho accettato)<new_line><new_line>Alla vittoria! Possano tutte le vittorie essere come questa!]],
				['9'] = [[E io che ne so? Ascolta, mi dispiace per te, davvero, ma... sarebbe meglio se lo accettassi e basta. Vivi. Non ritroverai tua figlia. Per prima cosa, perché ti servirebbe un esercito intero per affrontare quelli di Abakan. E seconda cosa...<new_line><new_line>(Guardando il maggiore, mi rendo conto di quanto voglia fargliela pagare a quei criminali. Dunque decido di riprovarci)]],
				['17'] = [[(Boris sta per dire qualcosa, ma poi si perde nei suoi pensieri. Dopo alza il braccio e sbatte il pugno sul tavolo)<new_line><new_line>È deciso. Gliela faremo pagare! Vai a dormire. Io preparerò i ragazzi. Partiremo domani all'alba. Vendicheremo Vasily e gli altri!]],
				['7'] = [[(Boris sospira)<new_line><new_line>Tua figlia? Capisco. Hanno ucciso anche mio figlio. Gli stessi tizi di Abakan. E ci hanno lanciato la sua testa al cancello. Il mio Vasily era un vero eroe per noi, un leader nato. Eravamo tutti orgogliosi di lui. Ancora sogno di sgozzare l'uomo che l'ha ucciso.]],
				['23'] = [[Indovina. Ho visto che eri nei guai. Un secondo di più e quella ragazza ti avrebbe ucciso. Ma io ho sparato per primo. Questo significa che sei in debito con me, amico.]],
				['24'] = [[Quasi. Ha i riflessi di un gatto selvatico. Il proiettile non ha colpito alcun organo vitale, ma è caduta lo stesso. I ragazzi l'hanno trattenuta e ti hanno soccorso. Poi ti hanno portato qui il più in fretta possibile. Pensavo che non ce l'avresti fatta.]],
				['25'] = [[Beh, diciamo che questo posto è una prigione, ma in realtà non lo è. È una normale cantina. Non resterà lì a lungo. Andrà a finire sul muro.]],
				['26'] = [[(Boris si siede e comincia a tamburellare con le dita sul tavolo)<new_line><new_line>Ascoltami. C'è una guerra in corso. Non è il momento di rammollirsi. Tua figlia e i banditi erano insieme. Ha ucciso la mia gente. E per poco non mandava all'aldilà anche te! E ora vieni a piagnucolare da me?]],
				['20'] = [[(Boris mi dà una pacca sulla spalla)<new_line><new_line>Bevi, su. Devi aumentare le tue forze. E il vino di prima scelta è migliore di qualsiasi mix di vitamine.<new_line>Sì, la battaglia è stata perfetta, da manuale della guerra! Mi sento vent'anni più giovane.<new_line>Sei stato fondamentale. Se non avessimo avuto quel lanciamissili, non ce l'avremmo fatta!]],
				['10'] = [[(Boris si zittisce)<new_line><new_line>Sì, ma tutti questi ragazzi, i miei soldati... sono tutti figli di qualcuno! E diventerebbero bestie al macello. Non posso farlo.]],
				['11'] = {
					['2'] = [[Una volta avevamo un sistema di missili anticarro, il Metis. Un cretino, senza fare nomi, ha avuto la brillante idea di gettarlo nella palude. Abbiamo un paio di missili, ma niente con cui lanciarli.]],
					['1'] = [[(Boris si gratta il mento)<new_line><new_line>In teoria, potremmo. Il cancello a sud è più debole di quello principale. Potremmo fare breccia con un'esplosione, ma non ti lascerebbero mai entrare dal cancello con della dinamite in mano. Ci sono delle vecchie trincee e poi uno spazio aperto. Basterebbe una mitragliatrice per fare i nostri a brandelli.]]
				},
				['12'] = [[(Boris ride)<new_line><new_line>Lo dici come se stessi chiedendo una pistola Makarov. Hai idea di che cosa sia?]],
				['29'] = [[(Boris, in silenzio, incrocia il mio sguardo, senza chiedermi niente. Probabilmente era un gesto di empatia per lui, quello.<new_line>Non avevo niente da dirgli)]],
				['27'] = [[(Il maggiore si gratta il capo. Poi si avvicina a me e mi poggia una mano sulla spalla)<new_line><new_line>Va bene. Sei suo padre, dopotutto. Ma, sai, non riesco neppure a immaginare come mi sentirei se il mio Vasily avesse fatto... quello che ha fatto Sasha, insieme a quei banditi. Lo avrei ucciso con le mie stesse... Cioè... fatti forza, è questo che voglio dire.]],
				['22'] = [[Beh, avevo iniziato a preoccuparmi, non sapendo dove fosse il mio combattente migliore. Ho chiesto agli uomini, e abbiamo iniziato a perlustrare il territorio circostante.]],
				['21'] = [[Abbiamo subito più perdite di quanto sperassi, ovviamente. E quei tre se ne sono andati nell'UAZ. Ma abbiamo preso gli altri e abbiamo distrutto la base. Un'impresa non da poco.]],
				['16'] = [[(Boris è veramente scioccato di vedermi. Non pensava che sarei tornato)<new_line><new_line>Hai le palle. Che mi venga un colpo, hai davvero il lanciamissili?]],
				['15'] = [[Ascolta tu prima. Non troverai niente, e nel caso in cui trovassi qualcosa, dovrai montarlo. C'è un ingegnere militare nell'insediamento vicino Čeljabinsk, Spiridon Kugel. Ha lavorato nell'unità di Ufa per un po'. Può darti una mano, sempre se non si è già ubriacato fino alla morte. Ma digli che il suo vecchio compagno di bevute lo saluta.]],
				['14'] = [[Domande?]],
				['13'] = [[Aspetta, ho un'idea. Se sei proprio disperato, vai alla base militare di Ufa. Nel 1983 un carico di sistemi Metis fu inviato lì direttamente dalla fabbrica. Dubito che siano stati utilizzati tutti, e dopo l'arrivo delle api giganti nella zona, se ne sono andati tutti.]],
				['6'] = [[No, non persone del posto. Non di qui, di Abakan. Perché la stai cercando? È tua parente?]],
				['5'] = [[(Boris fa un sorrisetto. Vuole dire dell'altro, ma ci ripensa all'ultimo secondo.)<new_line><new_line>In bocca al lupo, allora. Non conosco altri posti più vicini o più sicuri. Dovrai trovarli da solo.]],
				['4'] = [[Beh, è tutta un'altra storia. Non troverai niente, e nel caso in cui trovassi qualcosa, dovrai montarlo. C'è un ingegnere militare nell'insediamento vicino Čeljabinsk, Spiridon Kugel. Ha lavorato nell'unità di Ufa per un po'. Può darti una mano, sempre se non si è già ubriacato fino alla morte. Ma digli che il suo vecchio compagno di bevute lo saluta.]],
				['3'] = [[Sì, mi ricordo di lei. Uno spirito ribelle... Ma sei arrivato tardi, di uno o due anni (Boris scuote la testa). I banditi l'hanno catturata dopo l'ultimo assalto. Non l'abbiamo più vista.]],
				['2'] = [[(Boris, un ex maggiore e leader della piccola città militare di Stepnoy, mi accoglie freddamente. Fumando una sigaretta schiacciata, mi dice di sbrigarmi e di dire cosa voglio)]],
				['1'] = [[(Inizialmente, non vogliono farmi entrare nella città militare. Mi perquisiscono e si assicurano che non sia una spia delle gang locali e solo dopo mi concedono di vedere il loro leader. Al magazzino, devo lasciare le mie armi temporaneamente. Per tutto il giorno, due uomini armati mi seguono come un'ombra)]],
				['18'] = [[(Il maggiore non è qui. Pare che stia preparando l'offensiva.)]],
				['8'] = [[Credimi, tutti vogliamo macellare quei bastardi, ma non è possibile. Hanno occupato una vecchia base militare con delle fortificazioni dieci volte più grandi di quelle di Stepnoy. Amavo mio figlio, ma non posso condannare queste persone a morte certa.]]
			},
			['name'] = [[Boris]],
			['choice'] = {
				['19'] = [[Mi ha fatto piacere aiutare.]],
				['9'] = [[C'è forse un modo per occupare la base di Abakan senza un esercito? Potremmo farlo?]],
				['8'] = [[Vuoi vendicare la morte di tuo figlio?]],
				['7'] = [[Hai ragione. Pensi che Sasha sia ancora lì?]],
				['23'] = [[Dov'è adesso?]],
				['24'] = [[Vuoi giustiziare Sasha?]],
				['25'] = [[Boris, posso vederla per l'ultima volta?]],
				['26'] = [[Ok, ci proverò.]],
				['20'] = [[Boris, come mi hai salvato sull'ultimo piano di quell'edificio?]],
				['10'] = [[Forse posso trovare un altro lanciamissili?]],
				['11'] = [[Ci proverò comunque.]],
				['12'] = [[Sì... Mi servono informazioni su Ufa.]],
				['22'] = [[L'hai... l'hai uccisa?]],
				['21'] = [[E poi?]],
				['16'] = [[No, non ce l'ho. Ci vediamo.]],
				['15'] = [[L'ho preso, l'ho assemblato e l'ho portato qui. Iniziamo. Non c'è tempo da perdere.]],
				['14'] = [[No.]],
				['13'] = [[Perfetto, lo farò.]],
				['6'] = [[E allora che aspetti? Perché non ti sei vendicato attaccandoli? Hai gli uomini e le armi per farlo.]],
				['5'] = [[È mia figlia.]],
				['4'] = [[L'hanno rapita dei banditi? Gente del posto?]],
				['3'] = [[E ora capisco il perché... Ecco perché non mandi lì la tua gente. E il viaggio è lungo. Cercherò il lanciamissili dove non ci saranno api giganti.]],
				['2'] = [[A quanto pare, andrò a Ufa.]],
				['1'] = [[Salve! Vengo dalle terre desolate. Sto cercando una ragazza. Si chiama Sasha, e dev'essere arrivata qui da Barnaul nel 1986...]],
				['18'] = [[Alla vittoria... (bevi un sorso)]],
				['17'] = [[Ce l'ho, maggiore.]]
			}
		},
		['zaycev'] = {
			['episode'] = {
				['19'] = [[Purtroppo, sì. Pare che Nesterov fosse già riuscito a consegnare la coltura contaminata alla CIA. Il 17 agosto 1984, gli agenti nemici dispersero l'R-17 nell'aria in varie città usando delle bombolette spray speciali.]],
				['9'] = [[Sì. Lanciammo tutto il nostro arsenale. Subito, su tutti gli obiettivi. Anche se era spacciato, il Paese non voleva morire. L'ondata di rifugiati infetti approfittò del caos e sovrastò gli eserciti degli alleati. E fu così che l'R-17 si diffuse nel resto del mondo.]],
				['17'] = {
					['2'] = [[Nesterov e i suoi colleghi stavano sviluppando una nuova piaga. No, non una piaga, questo virus, l'R-17, era molto più pericoloso della peste bubbonica. E c'era dell'altro: era stata realizzata appositamente per un acquirente: la CIA! Nesterov aveva creato una nuova arma biologica per il nemico!]],
					['1'] = [[Beletsky trovò la scorta segreta di Nesterov. La trascinò fuori e mi portò il quaderno degli appunti di Nesterov e una registrazione. Inizialmente ero incredulo, ma la scrittura e la voce erano le sue!]]
				},
				['7'] = [[Certo. Ed erano quasi tutti infetti. I paesi vicini chiusero le frontiere, introdussero le leggi marziali, organizzarono le truppe e spararono a migliaia di rifugiati...]],
				['20'] = [[Sì? Ti serve altro, giovane?]],
				['10'] = [[(Zaitev fa una risata amara.)<new_line>No, ma a quel punto ogni nazione era rimasta da sola. Gli USA iniziarono a bombardare l'Europa, l'India attaccò la Cina. Conflitti già radicati divamparono... Era come se tutto il mondo fosse impazzito. L'unico posto non intaccato da tutto questo era l'Antartide. Ma, come sempre, l'umanità riuscì persino a peggiorare le cose...]],
				['11'] = [[A essere sincero, non lo so. Scusami, giovane, ma stiamo parlando da tanto e devo prendere le mie pillole.]],
				['12'] = [[Ok, come preferisci. Mi ricordi cosa volevi sapere?]],
				['16'] = {
					['2'] = [[Nascosero agli altri il loro lavoro. Io sospettavo che non trascorressero le serate in laboratorio bevendo tè, ma decisi di non ficcare il naso. Finché non fui costretto a farlo.]],
					['1'] = [[Genetica molecolare. Studiavano le proteine e i geni dei virus più diffusi. Uno studio assolutamente innocuo finché, ovviamente, non si supera il limite.<new_line>Nesterov lo superò. Iniziò a fare esperimenti con le mutazioni e coinvolse anche Kulik e Karbyshev.]]
				},
				['15'] = [[Potrei anche spiegartelo, ma non capiresti comunque.]],
				['14'] = [[Cosa vuoi che dica? Georgy Nesterov era uno scienziato di fama mondiale, l'orgoglio del nostro istituto. Abbiamo collaborato con buoni risultati con lui, Kulik e Shashkov per molti anni. Karbyshev si trasferì da noi da Leningrado. Beletsky, il più giovane del team, si unì più tardi, alla fine dell'83.]],
				['13'] = [[(Il direttore annuisce, toglie gli occhiali e inizia a pulirli minuziosamente con un panno. Li pulisce per vari minuti.)]],
				['6'] = [[Esatto. E dopo aver versato un po' di sangue, avrebbero avuto accesso alle infinite e ricche terre della nostra Madrepatria. Ma l'R-17 li tradì. L'epidemia si diffuse troppo lentamente, il periodo di incubazione era troppo lungo e troppe persone ne erano immuni. E già verso ottobre, decine e centinaia di migliaia di persone erano partite in massa verso i confini dell'URSS...]],
				['5'] = [[E va bene, ok, ma è tutto molto semplice. Ovviamente, le agenzie di intelligence americane sopravvalutarono l'efficacia del loro piccolo killer, l'R-17. O forse lo sottovalutarono. Pensavano che avrebbe portato alla quasi totale estinzione della grande Nazione Sovietica, ormai fuori di senno e indebolita dalla malattia...]],
				['4'] = [[(Il direttore mi guarda confuso, poi annuisce di nuovo e inizia a pulirsi gli occhiali.)]],
				['3'] = [[Sì, è così che è scoppiata l'epidemia, spazzando via gran parte della popolazione dell'URSS.<new_line>Ora sei contento?]],
				['2'] = [[(Zaitsev rigira la zanna tra le mani e mi guarda. Sembra un po' a disagio.)<new_line><new_line>Dov'è Pavel?]],
				['1'] = [[(L'ex direttore dell'Istituto di virologia sta sonnecchiando sulla stessa sedia della scorsa volta. Lo sveglio e mi siedo sulla sedia accanto a lui. Zaitsev mi guarda un po' stordito. Poi gli porgo la zanna della lupa.)<new_line><new_line>Cos'è?]],
				['18'] = {
					['2'] = [[Poi vennero a trovarli degli uomini del KGB. Ci interrogarono e perquisirono tutto l'istituto. Sequestrarono tutto quello che trovarono nel laboratorio di Nesterov, persino la targhetta sulla porta. Distrussero tutti il materiale biologico.]],
					['1'] = [[Io... non ebbi scelta. Li denunciammo tutti e tre alle autorità. Anche Beletsky e Shashkov firmarono la dichiarazione.<new_line>Il caso si concluse presto. Nesterov, Kulik e Karbyshev furono arrestati.]],
					['3'] = [[L'udienza si svolse a porte chiuse, ma mi concessero di assistere come testimone. Successe tutto in fretta. Le prove erano schiaccianti, e Karbyshev confessò tutto. Articolo 64: tradimento della Madrepatria. Furono portati direttamente dal tribunale di fronte al plotone d'esecuzione.]]
				},
				['8'] = {
					['2'] = [[Si tenne una riunione segreta del 'club nucleare' alla fine di ottobre in cui venne ratificata la mozione per purificare i 'territori infetti'. Usarono tutta la potenza di fuoco a loro disposizione. Persino i villaggi furono colpiti dalle bombe 'disinfettanti'. Ma ancora una volta fecero male i calcoli...]],
					['1'] = [[Infatti. Per ogni persona uccisa, ne arrivavano altre due. E la frontiera era estesa, quindi le persone abbastanza ingegnose o disperate trovavano un modo per passare. E quando i primi focolai di epidemia scoppiarono in Cina, Mongolia, Turchia, Polonia e Finlandia, si decise di adottare misure estreme.]]
				}
			},
			['name'] = [[Zaitsev]],
			['choice'] = {
				['19'] = [[Sì, grazie per aver parlato con me. Ora devo andare...]],
				['9'] = [[E non c'era niente che si potesse fare?]],
				['8'] = [[L'URSS rispose all'attacco?]],
				['7'] = [[Ma erano in troppi...]],
				['20'] = [[Grazie per aver parlato con me.]],
				['10'] = [[Significa che non è rimasto niente neppure negli altri continenti? Tutto il mondo è una terra desolata?]],
				['11'] = [[I lupi hanno preso Gavrilov. Ti ascolto.]],
				['12'] = [[Raccontami di Nesterov e degli altri scienziati che lavoravano con lui. E anche tutto il resto.]],
				['22'] = [[No. Ora so tutto quello che volevo sapere. Grazie.]],
				['21'] = [[Sì, voglio sapere altro. Com'è iniziata la guerra nucleare?]],
				['16'] = [[Cos'è successo?]],
				['15'] = [[Prova a farmi capire.]],
				['14'] = [[A cosa lavoravate?]],
				['13'] = [[Fa' pure, senza imbarazzo.]],
				['6'] = [[Per fuggire da un paese morente?]],
				['5'] = [[E avrebbero ucciso i sopravvissuti loro stessi.]],
				['4'] = [[Sto aspettando.]],
				['3'] = [[No, voglio sapere altro. Com'è iniziata la guerra nucleare?]],
				['2'] = [[Era il Day R.]],
				['1'] = [[Questi erano i patti. Ho fatto la mia parte. La lupa è morta. Ora tocca a te.]],
				['18'] = [[Ma il virus venne usato comunque.]],
				['17'] = [[E a quel punto che hai fatto?]]
			}
		},
		['sasha'] = {
			['episode'] = {
				['48'] = {
					['2'] = [[Ruslan propose loro un'offerta: dirgli tutto quello che sapevano su Stepnoy - le persone, l'arsenale, i punti deboli delle fortificazioni - e, in cambio, Ruslan li avrebbe lasciati andare.<new_line>Non aveva neppure finito di parlare che quei vermi pallidi e tremanti gli avevano già detto tutto.]],
					['1'] = [[Il racconto non era finito. Una settimana dopo, due dei tuoi 'innocenti' furono portati nella mia stanza: Vasily e altri due di Novokuzneck.]]
				},
				['38'] = [[Rispetto, riconoscimento... Questo solo all'inizio. Andando avanti mi hanno dato molto di più, qualcosa che non avrei mai ottenuto dai tuoi amici di Novokuzneck.]],
				['28'] = [[Cosa mi è successo? Beh, per prima cosa, mamma mi ha messo alla luce.]],
				['18'] = [[(Sasha fa una risata nasale e poi si gira. La luce della torcia illumina brevemente le sue braccia, che erano coperte di lividi. Lividi recenti, molto recenti)<new_line><new_line>Pensalo pure se ti fa sentire felice. Non me ne frega niente. Domani mi giustizieranno.]],
				['23'] = [[Boris. Cos'hai in mente?]],
				['33'] = [[(Il suono distante di uno sparo risuona in direzione di Novokuzneck. Poi un altro. Prendo la mia pistola e salgo sulla collina più vicina per guardare in lontananza. Le grigie terre desolate si distendevano davanti a me, spente come sempre.<new_line>Quando ritorno da Sasha, sta già dormento)]],
				['25'] = [[(Sasha non si lamenta più. Era piombata in una strana apatia. Gli occhi si muovevano su e giù in modo strano)]],
				['35'] = {
					['2'] = [[Poi, alla fine, il leader venne a trovarmi, Ruslan. Più tardi scoprii che un tempo era stato prigioniero politico. Rimase in silenzio per un bel po', e poi all'improvviso mi chiese cosa avessi fatto per spingere i miei compagni ad abbandonarmi.]],
					['1'] = [[Ok, ascoltami (accetta per sfinimento). Mi portarono nella base di Abakan, mi imprigionarono. Pensavo che mi avrebbero violentato, ma non fu così. Durante la prima settimana, una ragazza venne a portarmi tutti i giorni da mangiare. Non vidi nessun altro.]]
				},
				['31'] = {
					['2'] = [[Andavo d'accordo con la squadra. Dicevano di rispettarmi, che ero una di loro. E io, la solita cretina, mi tuffavo contro i proiettili per dimostrare a tutti di che pasta ero fatta. (Sasha serra i denti) Mamma quanto ero stupida...]],
					['1'] = [[Sì. Dopodiché, mi restituirono la pistola, mi fecero partecipare alle spedizioni di caccia e mi reclutarono persino in una squadra di combattimento. Il comandante di squadra era Vasily. Sì, il figlio di Boris.]],
					['4'] = [[Sparai a quattro di loro, ma poi finii le munizioni e mi catturarono. Volevano uccidermi all'istante, ma il loro leader li fermò e ordinò che mi portassero alla loro base.]],
					['3'] = [[In breve, la favola finì presto. Ci trovammo contro gli Abakan. Sì, ci tesero un agguato, ma noi eravamo di più e armati meglio. Ma quel maiale codardo di Vasily ordinò la ritirata, anche se aveva visto che ero stata isolata dal resto della squadra.]]
				},
				['41'] = {
					['2'] = [[(Uno degli uomini aiuta Sasha ad alzarsi. Si sente il rombo di un motore tra la boscaglia e appare un vecchio UAZ sull'argine roccioso)<new_line><new_line>Siamo pari. Una vita per una vita.<new_line>Ora vai. Vattene lontano, abbastanza lontano da non incontrarci mai più. Non voglio più né vederti né sentirti.]],
					['1'] = [[No. Mi hai salvato, anche se ho provato a ucciderti. Quindi per questa volta ti risparmieremo. È giusto. La giustizia prima di tutto.<new_line>Ruslan, aiutami.]],
					['3'] = [[(Ruslan solleva attentamente Sasha per farla salire sul veicolo e poi si sistema sul sedile del passeggero. Il terzo bandito sta per chiudere la portiera, ma Sasha la lascia aperta)<new_line><new_line>Non dimenticherò e non ti perdonerò mai per averci abbandonato, a me e alla mamma. Se non l'avessi fatto, niente di tutto questo sarebbe successo. Hai capito? Niente!]]
				},
				['11'] = [[(La vista inizia ad annebbiarsi e sento un ronzio nelle orecchie, mentre mi indebolisco sempre di più per la perdita di sangue. Sasha solleva di nuovo il fucile)<new_line><new_line>È un peccato che la casa del nonno sia andata in fiamme. Avresti trovato tantissimi disegni che ti raffiguravano, papà caro. Immagini disegnate dalla tua bambina. E in ogni... singolo... disegno... muori...]],
				['21'] = [[Voleva sapere chi avesse ucciso la luce dei suoi occhi, Vasily. Non gliel'ho detto.<new_line><new_line>(Sasha sorride mestamente)<new_line><new_line>Quando sarò appoggiata a quel muro, gli dirò tutto. Tutti i dettagli. Sei contento? Ora sparisci.]],
				['15'] = {
					['2'] = [[(La risata isterica di Sasha si trasforma improvvisamente in singhiozzi. Avevo già sentito suoni del genere: tanto tempo fa, quando dissi a mia figlia che me ne sarei andato per sempre. Sasha si ricompone, soffocando le lacrime con un urlo furioso e allontanandosi dalle sbarre)]],
					['1'] = [[(Sasha scoppia a ridere con le lacrime)<new_line><new_line>E Boris ha chiamato me una sporca traditrice?! Io?! Tu sei cento volte peggio! Il tradimento per te è facile come respirare! Boris ti ha salvato la vita, e ora vuoi far evadere la sua prigioniera?!]]
				},
				['13'] = [[(Nella cantina, è così buio e freddo, che neppure i topi vorrebbero viverci. Illuminando con la torcia, oltre le sbarre spesse, vedo tre robuste panche di legno. Sasha è seduta su una di esse. La guardia mi fa andare fino alla cella, e poi esce fuori a fumare)]],
				['6'] = [[(Sasha mi guarda incuriosita. Poi il suo sguardo diventa più serio. Alla fine capisce e il suo volto si riempie di odio furioso, come non avevo mai visto prima. Non ho mai visto nessuno guardare un'altra persona in quel modo)<new_line><new_line>Guarda chi si rivede. Papà è tornato.]],
				['5'] = [[(La ragazza sputa e asciuga le gocce di sangue che le colano sul viso. Poi prende un fucile da uno dei morti e prende la mira.<new_line>In quel momento, vedo per bene il suo volto.<new_line>No...<new_line>Non può essere...)]],
				['4'] = [[(Le sparo, ma si scansa. Poi mi colpisce con un'agilità mai vista e mi pugnala<new_line>Rimango senza fiato, getto l'arma e cado a terra. La ragazza fa un salto e mi dà un calcio in faccia)<new_line><new_line>Corrotto proprio come gli altri!<new_line><new_line>(Indica i corpi crivellati di pallottole)]],
				['3'] = {
					['2'] = [[Sempre la stessa storia. Visto che sono una ragazza vuol dire che sono una vittima indifesa, eh?]],
					['1'] = [[(La rabbia cade dal suo volto come una maschera. La ragazza scoppia a ridere e annuisce abbassando il coltello. Mi avvicino e le porgo la mano.<new_line>Un secondo dopo, mi pugnala.<new_line>Rimango senza fiato, getto l'arma e cado a terra. La ragazza fa un salto e mi dà un calcio in faccia)]]
				},
				['2'] = [[Non avvicinarti! Se fai un altro passo, ti squarto!]],
				['1'] = [[(Corro all'ultimo piano. I corpi dei nostri compagni sono fatti a pezzi, e tra di loro c'è una ragazza ferita. Il suo viso è coperto in parte da tinta mimetica, in parte da sangue. Appena mi vede, estrae un coltello sottile con il manico viola)]],
				['37'] = [[A prescindere da quello che dirò, tu non capirai comunque.]],
				['47'] = [[No, papà. Non siamo affatto dei banditi. Siamo qualcosa di più interessante.]],
				['39'] = [[La differenza tra vivere e sopravvivere! (Gli occhi di Sasha si infuocano). La parola 'missione', ti dice niente?<new_line>Non siamo banditi, papà. Siamo colonizzatori. Strappiamo le erbacce del vecchio mondo dalla faccia della terra e costruiamo un nuovo mondo, uno migliore e più gusto.<new_line>]],
				['49'] = {
					['2'] = [[(Una pausa)<new_line><new_line>Neppure uccidere i mostri che assassinarono il nonno fu così soddisfacente come quella volta. Li guardai morire dissanguati e mi sentii libera. Felice. Non era solo sete di sangue o vendetta per il loro tradimento. Era giustizia.]],
					['1'] = [[Sì, ma non fecero in tempo a uscire. Presi il coltello di Ruslan e li sgozzai.]]
				},
				['19'] = [[A fare cosa?]],
				['9'] = [[(Sasha storce il labbro, disgustata)<new_line><new_line>Bleah. Piangi pure, papà. Forse ti aiuterà. Forse ti perdonerò. Oh, già, quasi dimenticavo. Hai tu il mio coltello. Ora me lo riprendo.<new_line><new_line>(Sasha afferra il manico viola e estrae con violenza la lama. La ferita perde tanto sangue)]],
				['17'] = [[Idiota, io... mi stavo gustando il momento!]],
				['7'] = [[(Sasha si avvicina finché i nostri nasi quasi non si toccano. Sorride dolcemente)<new_line><new_line>Oh, mi stavi cercando? Scusa, papà, ma sei arrivato tardi.]],
				['34'] = [[(Sasha stava molto meglio. Riusciva a stare in piedi e persino a camminare appoggiata al mio braccio)]],
				['24'] = [[(Sasha è seduta in fondo alla sua cella nella prigione improvvisata e mi guarda con un certo disprezzo. È difficile capire se mi ha creduto o meno)]],
				['36'] = [[Il giorno dopo, Ruslan tornò e appoggiò una pistola davanti a me. Disse che non mi avrebbe più dato da mangiare gratis, che non aveva bisogno di prigionieri, ma di combattenti. Mi diede la possibilità di scegliere se unirmi a lui o se essere riportata a Novokuzneck. E feci la mia scelta.]],
				['26'] = [[(Sasha, stanca di guardarmi, sospira profondamente)<new_line><new_line>Di cosa?]],
				['20'] = [[Ti stupisci? Pensavi che mi avrebbero riservato un trattamento di favore, Giuda? (per un secondo, la sua voce fa trapelare il rancore) Beh, Boris non ci è andato piano.]],
				['32'] = [[Figurati... Non direbbe mai neanche mezza parola che possa screditare il suo dolce figliolo. Sai, lui...]],
				['22'] = [[(Sasha non reagisce)]],
				['30'] = [[Dopo... dopo siamo stati attaccati. Erano già morte più di dieci persone prima che l'allarme ci svegliasse. Un gruppo di banditi aveva attraversato il cancello. Le ragazze erano spaventate a morte, ma io ne feci fuori uno, gli presi la pistola e iniziai a sparare agli altri.]],
				['46'] = {
					['2'] = [[Guardati attorno. Non esiste più una nazione. Non esiste più un recinto sicuro in cui ruminare in pace e farsi crescere la lana per la tosatura. Siamo nella foresta. E qui, o sei pecora... o sei lupo.]],
					['1'] = [[(Sasha, per tutta risposta, ride con sdegno)<new_line><new_line>Innocenti? Innocenti?! Dove li hai visti questi innocenti?! O è così che chiami quelle pecore pigre e avare che belano senza senso quando vengono uccise?]]
				},
				['16'] = [[Eh?! Cosa vuol dire?]],
				['45'] = [[No, non in quel momento. Ma si vedeva che mi capiva. Mi capiva meglio di quella gentaglia di Novokuzneck. Quelle persone che... mi hanno usato e gettato via come spazzatura.]],
				['14'] = [[(Sentendo la mia voce, Sasha alza la testa e fa una smorfia, come se le facesse improvvisamente male un dente)<new_line><new_line>Tu?! Speravo che fossi morto!]],
				['44'] = [[Certo. Ma, sai, le sue parole erano vere, e lo sapevo. Venne a trovarmi tutti i giorni per una settimana. Mi raccontò della sua vita e della sua famiglia, e poi qualcosa anche sui combattenti. Poi io gli raccontai qualcosa su di me. Mi ascoltava veramente e faceva domande.]],
				['43'] = [[No, fumava solo e parlava. Tornò il secondo giorno. Disse che la mia gente si era rifiutata di pagare il riscatto e che non aveva chiesto altro che un paio di pistole e una cassa di cibo in scatola. E altra roba del genere. Da quel giorno, venne personalmente a portarmi da mangiare. Porzioni molto più grandi di quelle del mio accampamento. E anche più buone.]],
				['42'] = [[Sì. Ha detto che ho avuto fegato, che valevo quanto dieci dei suoi uomini, e che non avrebbe mai fatto quello che Vasily aveva fatto né abbandonato un soldato contro una squadra intera. Disse che l'accampamento di Novokuzneck avrebbe potuto vincere, ma che avevano perso il controllo. Disse molte cose così, ma io non replicai. Volevo contraddirlo, ma...]],
				['40'] = [[No, siamo in tanti, in migliaia. E presto saremo ancora di più. (Sasha fa un sorrisetto) E in ogni caso, tre o quattro persone possono fare tanto. Dietro di te!<new_line><new_line>(Mi giro. Ci sono due banditi a pochi metri da me che mirano alla mia testa)]],
				['8'] = [[Dai, papà, non essere ingenuo. Per cosa ti stai scusando? Beh, mamma è morta per colpa tua. Ma chi se ne frega. Anche zio Arthur e zia Marina sono morti. Beh, e poi il nonno, ma tanto lui era vecchio. Ora spunti dal nulla e uccidi i miei amici. Ma tanto posso sempre trovarne di nuovi, vero? Vero?]],
				['27'] = [[(Sasha si gira dall'altra parte)<new_line><new_line>Non è un bell'argomento.]],
				['12'] = [[(La pozza di sangue ai miei piedi sembrava più un oceano. A faccia in giù e senza forse, non posso fare altro che aspettare. I secondi sembrano ore...)]],
				['10'] = [[(Rimane in silenzio per un po' e poi si abbassa di nuovo verso di me)<new_line><new_line>Non puoi neppure immaginare, papà, quanto godrò nel conficcarti un proiettile in testa. E lo farò tante volte finché non ci sarà neppure una cartuccia in tutto questo cazzo di edificio.]],
				['29'] = {
					['2'] = [[Ma il lavoro in cucina era disgustoso. Mi rifiutai di lavorare lì. Le ragazze iniziarono a portarmi rancore e si lamentarono della mia 'pigrizia' col capo, ma il bastardo disse solo: 'Non c'è problema. Qualche giorno senza la sua razione e vedrete che cambierà idea...'.]],
					['1'] = [[Strano. Non ricordo tutto questo interesse sulla mia vita da parte tua. (Sasha scuote la testa) Ok. La gente di Novokuzneck mi ha portato nel suo accampamento. Ma non mi permettevano di combattere; anzi, mi confiscarono anche la pistola del nonno. Andai dal maggiore, che però mi disse che non c'era posto per una ragazzina tra i suoi uomini e mi assegnò alle mansioni domestiche.]]
				}
			},
			['name'] = [[Sasha]],
			['choice'] = {
				['38'] = [[Ti ha fatto pressioni?]],
				['28'] = [[Silenzio!]],
				['18'] = [[Chi ha la chiave della cella?]],
				['23'] = [[Come pensavo. Allora, se non vuoi parlare di quello, raccontami cosa ti è successo.]],
				['33'] = [[Ti sbagli. Capisco le tue motivazioni. Volevi rispetto e riconoscimento, volevi essere libera. E li hai trovati nei tuoi nuovi amici.]],
				['25'] = [[E dopo?]],
				['35'] = [[Non sei un po' troppo ambiziosa? Siete rimasti solo in tre o quattro.]],
				['31'] = [[Incredibile...]],
				['41'] = [[È una persona orrenda! Un criminale! Le sue mani sono sporche del sangue di innocenti!]],
				['11'] = [[Posso aiutarti a evadere.]],
				['21'] = [[Possiamo parlare?]],
				['15'] = [[Chi è stato?]],
				['13'] = [[Non sei riuscita a uccidermi. L'ultima cosa che ricordo è il silenzio. Hai esitato. Avresti potuto uccidermi e scappare, ma hai perso tempo. Poi Boris ti ha ferito.]],
				['6'] = [[Sasha, mi dispiace, perdonami...]],
				['5'] = [[Sasha, io... so che è colpa mia. È colpa mia se tu e tua madre e...]],
				['4'] = [[Sasha... È da tanto tempo che ti cerco...]],
				['3'] = [[Sasha?]],
				['2'] = [[(Spara alla ragazza)]],
				['1'] = [[Non aver paura di me. Ti tirerò fuori da qui!]],
				['37'] = [[Ti ha parlato dello scontro?]],
				['39'] = [[Non avevi capito che stava cercando di portarti dalla sua parte?]],
				['19'] = [[Chi ha la chiave della cella?!]],
				['29'] = [[(Lasciala in pace)]],
				['8'] = [[Sasha... aspetta...]],
				['7'] = [[Mi... mi avevano detto che eri loro prigioniera. Io... Io ho cercato... di salvarti...]],
				['34'] = [[Cosa non capirò?]],
				['24'] = [[Smettila di scherzare. Dimmi cosa ti è successo dopo che hai ucciso i banditi a casa del nonno.]],
				['36'] = [[Hai intenzione di uccidermi?]],
				['26'] = [[Respingeste l'attacco?]],
				['20'] = [[Stasera uscirai di qui. Preparati.]],
				['32'] = [[Sei felice della tua scelta?]],
				['22'] = [[Di cosa? Forse del fatto che hai tentato di uccidermi e che ti ho salvato da un plotone di esecuzione?]],
				['30'] = [[Non mi hai ancora raccontato tutta la storia.]],
				['16'] = [[Le braccia. Ti hanno picchiato?]],
				['14'] = [[Non sei riuscita a farlo.]],
				['44'] = [[E Ruslan li lasciò andare?]],
				['43'] = [[Che stai dicendo?]],
				['42'] = [[E tu volevi essere un lupo? Un bandito?]],
				['40'] = [[Iniziasti a fidarti?]],
				['17'] = [[Ma perché...]],
				['27'] = [[Boris non mi ha mai detto che la tua squadra ti ha abbandonato.]],
				['12'] = [[Non avresti dovuto aggrapparti alla speranza. Avresti dovuto sparare quando ne avevi l'occasione, ma non ce l'hai fatta.]],
				['10'] = [[Sasha?]],
				['9'] = [[Sasha...]]
			}
		},
		['svetlana'] = {
			['episode'] = {
				['48'] = [[Vattene, per favore. Devo lavorare.]],
				['38'] = [[(Busso alla porta di Svetlana, ma non si apre subito.<new_line>C'è una ragazza seduta sul divanetto dell'ufficio. Quando mi vede, si imbarazza, ringrazia Svetlana per qualcosa e scappa via. Svetlana mi sorride e mi fa l'occhiolino.)<new_line><new_line>Che sciocchina! Allora? Novità?]],
				['28'] = [[Devo scoprire la verità. La verità sulla catastrofe.]],
				['18'] = [[Attivare Day R il 17 agosto, come da ordini'... Cos'è questo? Dove l'hai preso?]],
				['23'] = [[Vado a prepararmi per uscire. Aspetti un altro po'?]],
				['33'] = [[(Quando mi vede, Svetlana sorride per un secondo, poi reprime l'impulso e assume un'espressione forzata di distacco.)<new_line><new_line>Allora? Come procedono le ricerche di tua moglie e tua figlia?]],
				['25'] = [[Sicuro?]],
				['35'] = [[Va tutto bene.<new_line><new_line>(Svetlana ha un tono di voce freddo e austero. È chiaramente arrabbiata.)]],
				['31'] = [[Non lo so. Sto cercando di scoprirlo sin da quando quei banditi mi hanno presa prigioniera. Ho provato a trovare una spiegazione razionale. Poi qui, fuori da Tver, ho chiesto agli altri rifugiati. Ho letto i giornali, le storie sulla malattia...]],
				['41'] = [[(Svetlana inizia di nuovo a ridere. È proprio di buon umore.)<new_line><new_line>Non farci caso. È solo di cattivo umore. È la sua prima volta.]],
				['11'] = [[(Svetlana mi guarda con empatia)<new_line><new_line>È terribile! È un miracolo che sei arrivato fin qui! (Sorride dolcemente) Ma ora è acqua passata. Ci prenderemo noi cura di te. Ti daremo una stanza e cibo. E a poco a poco, i tuoi ricordi torneranno, ne sono sicura.]],
				['21'] = [[(Svetlana trattiene il respiro)<new_line><new_line>Allora, accetti di aiutarmi?]],
				['15'] = [[(Quando mi sveglio, Svetlana è di fianco a me. Mi guarda con uno sguardo serio, mordendosi il labbro.)<new_line><new_line>Hai cambiato idea? C'è la possibilità che resti?]],
				['13'] = [[(Svetlana non risponde, poi si alza e va alla finestra)<new_line><new_line>Perché?]],
				['6'] = [[(Svetlana si stringe il capo tra le mani)<new_line><new_line>Tu non capisci...]],
				['5'] = [[(Svetlana mi fissa negli occhi per molto tempo e poi distoglie lo sguardo)<new_line><new_line>Anche io ho lottato finché ho potuto. Credevo che potessimo ancora fare qualcosa, rimediare...]],
				['4'] = [[(Svetlana mi guarda, triste)<new_line><new_line>Sai, anche io ho cercato i miei parenti quanto sono arrivata qui. E anche altri sopravvissuti. Ho cercato un modo per mettermi in contatto con i miei colleghi, per scoprire di più su questa maledetta epidemia, sui bombardamenti. Ho viaggiato da una città all'altra, finché non rimasi a secco di carburante. Tutti i miei sforzi andarono in fumo!]],
				['3'] = [[Certo, ci sono altri insediamenti, ma perché dovrei mai lasciare Tver? Abbiamo abbastanza cibo. I banditi ci lasciano stare. Non fare l'idiota, rimani qui!]],
				['2'] = [[(Svetlana si girò dall'altra parte)<new_line><new_line>Guarda fuori dalla finestra. Prima l'epidemia, poi la guerra nucleare... La Terra è morta. Non si può sopravvivere là fuori. Radiazioni, predatori, pazzi che non hanno niente da perdere, banditi... Non troverai nessuno là fuori. Ma se partirai, non tornerai. Non proveremo a trattenerti, ma riflettici bene!]],
				['1'] = [[(La donna mi guarda con gli occhi tristi e mi sorride)<new_line><new_line>Ciao. È da tanto che non si vede un volto nuovo. Chi sei? Da dove vieni?]],
				['37'] = [[Tutto bene, se si riesce a combattere la disperazione e lo sconforto.]],
				['47'] = [[Sì, la verità. Ma quello che mi stai raccontando è una disgustosa bugia senza capo né coda. Non so chi se la sia inventata, ma spero che non sia stato tu. Non intendo continuare ad ascoltare.]],
				['39'] = [[Tutta questa formalità non è necessaria. Dopotutto, siamo come dei vecchi amici, non credi?]],
				['19'] = [[No, non lo so, ma credo di aver già sentito quel nome, da mio padre. Quando venne in visita nell'istituto, parlò con qualcuno al telefono e nominò questo 'Day R'. Gli chiesi cosa significasse, ma scansò la risposta con una battuta. Ma presi una specie di appunto mentale per non dimenticarlo.]],
				['9'] = [[Ok, ma... perché mi stavi cercando?]],
				['17'] = [[(Svetlana si calma e abbassa di nuovo la voce)<new_line><new_line>Anche se, ancora qualcosa la sappiamo. Le prime ondate letali dell'epidemia avvennero tra il 2 e 3 settembre. Considerando il periodo di incubazione, credo che l'infezione sia avvenuta attorno al 17-19 agosto. Il che significa...]],
				['7'] = [[Davvero? (la donna è stupita) È un viaggio lungo e pericoloso. Beh, entra, accomodati. Sono Svetlana.]],
				['34'] = {
					['2'] = [[Vado a prepararmi per uscire. Aspetti un altro po'?]],
					['1'] = [[(Il sorriso di Svetlana ora brilla di una gioia evidente.)]]
				},
				['24'] = [[(Svetlana guarda insospettita, poi si gira e va alla finestra)<new_line><new_line>Allora torna quando avrai il tempo.]],
				['36'] = [[(Svetlana mi saluta con un sorriso.)<new_line><new_line>Ciao! Allora? Novità?]],
				['26'] = [[(Svetlana fa un sorriso strano)<new_line><new_line>E allora? Non sto rischiando niente. Ok, maledizione, se vuoi partire, parti. Ma se andrai comunque...<new_line><new_line>(Fa una pausa di riflessione, per scegliere le parole)]],
				['20'] = [[Era un microbiologo, uno dei migliori del Paese. Lavorava a Mosca nell'Istituto di virologia. Quella fu l'ultima volta che lo vidi. Morì di infarto nel giugno dell'84. Mia madre mi comunicò la notizia per telefono. Non ebbi neppure l'opportunità di dirgli addio. Non andai neppure al funerale, perché ero costretta a letto per una tonsillite.]],
				['32'] = [[(Svetlana sobbalza e mi guarda con uno sguardo infuriato.)<new_line><new_line>Lo so! Ma non è molto più folle che andare a cercare la tua famiglia nelle terre desolate!]],
				['22'] = {
					['2'] = [[Ma possiamo fare una cosa: trovare la verità. Scoprire chi sono i responsabili per la morte di milioni di persone e per l'aver trasformato il nostro Paese, forse persino il mondo, in una terra desolata. E scoprire anche perché l'hanno fatto. È importante, non credi? Non solo per me, ma per tutti noi.<new_line>So che non sei costretta a farlo. Posso solo chiedertelo e offrire una ricompensa.]],
					['1'] = [[Capisco, ma devi anche capire me. Non ho più una famiglia, e non c'è più nessuno che ha bisogno di una cura per la malattia. Cosa mi rimane? La sopravvivenza. Il mio scopo nella vita è quello di vivere ancora un po', ma sono già stufa di questa esistenza!]]
				},
				['30'] = {
					['2'] = [[Ho controllato. Il virus ha colpito tutti, ovunque, nello stesso momento. Non c'era modo di fermarlo.<new_line>Sembra una follia, ma è possibile che qualcuno abbia scatenato l'epidemia volontariamente, infettando persone in città diverse allo stesso tempo?]],
					['1'] = [[(Svetlana parla a voce ancora più bassa)<new_line><new_line>L'epidemia! L'epidemia è scoppiata contemporaneamente in città diverse ai lati opposti dell'URSS! Nessun virus della storia si è mai diffuso così. Una malattia può sempre essere localizzata, si organizzano le quarantene, si adottano misure preventive. O almeno, si può provare. Ma non è stato il caso di questa malattia.]]
				},
				['46'] = [[(Svetlana si gira verso di me. Gli occhi sono completamente indifferenti, ma le tremano le mani.)<new_line><new_line>E perché me lo stai raccontando?]],
				['16'] = [[(Svetlana annuisce lentamente. La mia risposta non l'aveva sorpresa, ma perché era così in ansia?)<new_line><new_line>Mi dispiace per te. Quelli come te, quelli che amano, sono i primi a morire. Quelli che rischiano per salvare gli altri. Pensavo che tutti quelli come te fossero morti nell'84, che in giro fossero rimasti solo banditi e quelli come me, che hanno smesso di fregarsene del tutto.]],
				['45'] = [[(Quando finisco, noto che Svetlana sta fissando con sguardo assente la parete alle mie spalle. Mi aspettavo una qualche reazione, ma non questa. Sposto la testa, provando ad attirare la sua attenzione, ma non reagisce. Cala un silenzio carico di tensione.)]],
				['14'] = [[(La porta della stanza di Svetlana è chiusa, ma sembra che sia in stanza. Avrei potuto bussare, ma pensai che non volesse essere disturbata)]],
				['44'] = {
					['2'] = [[(Vedendo la faccia concentrata ma felice di Svetlana, esito, non sapendo da dove iniziare. Farfuglio qualcosa di incomprensibile. La voce diventa improvvisamente rauca. Svetlana mi passa un bicchiere d'acqua. Lo bevo tutto d'un sorso e mi decido a parlare.)]],
					['1'] = [[Ok. Si capisce che devi dirmi qualcosa. Hai scoperto qualcosa su quello di cui abbiamo parlato?]]
				},
				['43'] = [[Questa non è una gioia solo per lei. È un'occasione gioiosa per tutti noi. Siamo sopravvissuti a un'epidemia e a una guerra nucleare. Poi abbiamo combattuto anni contro la fame! Ma c'è un nemico ancora più forte della fame: la disperazione. E sai una cosa? Oggi quel nemico non ha vinto. Il primo bambino dopo non si sa quanto tempo. Significa che abbiamo un futuro!]],
				['42'] = [[Sì, la prima. Non l'hai capito? È incinta!]],
				['40'] = {
					['2'] = [[(Singhiozzi amari e tristi che mi fanno male al cuore. Piange e io, come un idiota, la guardo e basta, senza sapere cosa dire. A un certo punto, inizio persino a prendermela con me stesso per non aver inventato una scusa convincente, anziché sbatterle in faccia la verità.)]],
					['1'] = [[(Svetlana mi afferra improvvisamente per la maglietta, e io sobbalzo.)<new_line><new_line>Basta! Non dire neppure una parola su mio padre! Era una persona meravigliosa! Mi voleva bene, amava mia madre, amava il suo Paese! È morto di infarto, capito?! Me lo ha scritto mia madre! Di infarto, ok? Infarto...]],
					['4'] = [[Grazie per aver scoperto la verità. Scusa se ti ho urlato contro. Non è colpa tua. Come hai detto tu: sofferenza mia, problema mio e solo mio.<new_line>Grazie ancora e... addio.]],
					['3'] = [[(Dopo un po' Svetlana si calma. Si soffia il naso con un fazzoletto e si siede sul divano. Mi avvicino per sedermi accanto a lei, ma alza una mano e mi chiede di non farlo. Annuisco e mi avvio verso la porta. Svetlana deve riflettere su molte cose, e io non posso aiutarla.)<new_line><new_line>Aspetta...]]
				},
				['8'] = [[(Svetlana guarda insospettita)<new_line><new_line>Sì, sono io. Ma come fai a saperlo?]],
				['27'] = [[(Svetlana abbassa la voce, come se temesse che qualcuno potesse sentirla)<new_line><new_line>C'è una cosa che puoi fare. L'ho iniziata tanto tempo fa, ma non l'ho mai finita. Non ci sono riuscita e l'ho lasciata a metà (Svetlana fa una smorfia). Ma tu potresti farcela!]],
				['29'] = [[Hai letto il mio diario, vero? Su una delle pagine andate perse, scrissi una strana idea. Non so ancora perché non ci pensai prima. Ma questa idea... non mi fa dormire la notte.]],
				['12'] = [[Ma qui lavoriamo tutti per il bene della collettività. La nostra comunità è piccola: tutti hanno un compito ma c'è ancora molto da fare. Un paio di braccia in più ci faranno comodo.]],
				['10'] = [[Non ricordi niente? Cos'è successo?]]
			},
			['name'] = [[Svetlana]],
			['choice'] = {
				['38'] = [[Tuo padre...]],
				['28'] = [[Non capisco. Cos'altro c'è da scoprire? Sai tutto. È scoppiata l'epidemia, poi c'è stata la guerra nucleare...]],
				['18'] = [[Interessante. Chi era tuo padre?]],
				['23'] = [[Non te la prendere, ma... non sono sicuro di avere il tempo per questo...]],
				['33'] = [[In realtà, non proprio. Stavo proprio pensando che andrò comunque a Mosca, all'Istituto di virologia. Cercherò degli indizi. Chissà, magari farò progressi anche con la mia ricerca.]],
				['25'] = [[Scusa, Svetlana, ma non mi sembri una persona che ha smesso di fregarsene. Dopotutto, stai provando a salvarmi.]],
				['35'] = [[Ciao. No, ancora niente. Come va?]],
				['31'] = [[Svetlana, anche se avessi ragione, se l'epidemia fosse opera dell'uomo, anche se fosse una sorta di cospirazione, come pensi di arrivare alla verità? Sono passati tanti anni. È un pensiero folle.]],
				['41'] = [[Ah, ecco...]],
				['11'] = [[Te l'ho detto, non lo so. Mi sono svegliato a Nikel con un'amnesia totale su chi fossi, da dove venissi e su come fossi arrivato lì. Ero circondato da corpi in putrefazione e macerie... Allora ho iniziato a cercare risposte e persone che potessero darmele. Ma nessuno sapeva cos'era successo, né perché.]],
				['21'] = [[No, aspetta. Non ho ancora preso una decisione. In fin dei conti, la mia priorità è trovare mia moglie e mia figlia.]],
				['15'] = [[(Non parlare)]],
				['13'] = [[Svetlana, grazie. Grazie per l'offerta, ma non posso rimanere qui con voi.]],
				['6'] = [[Probabilmente no. In ogni caso è stato un piacere conoscerti, Svetlana. Spero di rivederti un giorno... (vai via)]],
				['5'] = [[Ma non stai facendo qualcosa? Indossi un camice. Curi le persone. Non ti sei arresa.<new_line>E... ti sei arresa quando i banditi ti hanno reso prigioniera? No, li hai avvelenati, anche a rischio della tua stessa vita!]],
				['4'] = [[Mi dispiace, e so che è quasi impossibile, ma devo provarci. Le andrò a cercare. Supererò gli Urali, se necessario, fino anche ad altri Paesi...]],
				['3'] = [[Svetlana, hai ragione su tutto quello che dici, ma... se fossi al posto mio, non andresti a cercare la tua famiglia? A prescindere da quanto fosse pericoloso?]],
				['2'] = [[Hai ragione, ma devo provare. Non posso abbandonare la mia famiglia. E dopo di quello, beh, credo che ci saranno altri insediamenti. Continuerò a cercare finché non capirò che è successo.]],
				['1'] = [[Ho una moglie e una figlia. Non so dove sono, ma sono sicuro che sono vive e che mi stanno aspettando. Devo trovarle.]],
				['37'] = [[Ciao di nuovo, Svetlana.]],
				['47'] = [[Addio (vattene e chiudi la porta senza fare rumore).]],
				['39'] = [[Sì, giusto. Ma... ehm... perché quella ragazza era così spaventata da me?]],
				['19'] = [[All'Istituto di virologia? A Mosca? Visto che è l'unico indizio che ho, allora sarà Mosca la mia prossima tappa.]],
				['29'] = [[Non ti seguo.]],
				['8'] = [[Svetlana di Leningrado, vero? Sei la persona che ha trovato la cura per l'epidemia, che è stata catturata dai banditi, che è riuscita a scappare e che è arrivata fin qui, a Tver, a piedi?]],
				['7'] = [[Chi sono? Un girovago. Vengo da nord.]],
				['34'] = [[Lentamente, ma procede... Sono passato a vedere come stavi.]],
				['24'] = [[Svetlana, è una pessima idea. Le terre desolate sono troppo pericolose per gli inesperti. Non posso proteggerti, e lasceresti la tua gente senza un medico. È meglio se rimani.]],
				['36'] = [[Ci vediamo.]],
				['26'] = [[Sì? Ti ascolto.]],
				['20'] = [[Ci proverò, ma non ti prometto niente. La mia priorità è trovare mia moglie e mia figlia.]],
				['32'] = [[Ne sono sicuro.]],
				['22'] = [[Va bene, vedrò che posso fare.]],
				['30'] = [[Svetlana, è una follia. Chi lo farebbe mai e a che scopo?]],
				['46'] = [[Svetlana, ascolta... Mi ricordo che tua madre ti ha scritto per dirti che tuo padre è morto d'infarto, ma forse non voleva...]],
				['16'] = [[17 agosto... Aspetta... Ho già visto questa data... (mostra a Svetlana il telegramma dell'uomo morto a Novgorod)]],
				['45'] = [[Che vuol dire perché? Eri tu quella che voleva scoprire la verità.]],
				['14'] = [[No, devo andare.]],
				['44'] = [[Svetlana?]],
				['43'] = [[(Raccontale tutto)]],
				['42'] = [[Sì, sono d'accordo.]],
				['40'] = [[La sua prima volta?]],
				['17'] = [[A Novgorod, da un cadavere. Ma c'è dell'altro. Ho trovato questa bomboletta e anche qui c'è scritto 'Day R'.<new_line>Sai cosa significa?]],
				['27'] = [[Ok, e di cosa si tratta?]],
				['12'] = [[Svetlana, io...]],
				['10'] = [[Stavo cercando dei sopravvissuti, persone che potessero raccontarmi la verità su cosa è accaduto nel 1984. Sai, io non ricordo niente... Ho persino dimenticato come mi chiamo.]],
				['9'] = [[Ti stavo cercando. Ho trovato il tuo registratore a Leningrado. Anche io ero a Novgorod, quando la base dei banditi...]]
			}
		},
		['varya'] = {
			['episode'] = {
				['9'] = [[(Confesso che per poco non mi cadeva dalle braccia per lo shock.)<new_line><new_line>Sì, perché? Sei forte, coraggioso e puoi proteggermi. Hai rischiato la vita per salvarmi. Ora devo ripagarti! Cosa farò a casa di mia zia? Mi annoierò a morte!]],
				['8'] = [[Certo. Ma sai cos'ho appena pensato? Non voglio tornare da mia zia. Non è che potrei viaggiare con te?]],
				['7'] = [[Ho detto che è gelosa di me.]],
				['6'] = [[(Mentre attraversiamo la foresta, Varya mi racconta tutte le vicissitudini che ha subito per mano dei banditi. Poi mi racconta gli atroci dettagli del suo rapimento e di quanto fosse terribile sua zia, con cui ha viveva in un villaggio fuori Brjanks. La ascolto a malapena... Sono troppo preso dai miei pensieri.)]],
				['5'] = [[Come puoi vedere...]],
				['4'] = [[(Varya fa un passo incerto e traballa. Si appoggia alla parete.)<new_line><new_line>Scusa, le mie gambe non collaborano. Che cosa facciamo? Potresti... portarmi in braccio? Solo per un po', finché le mie gambe non si riprenderanno.]],
				['3'] = [[Li hai uccisi? (Spalanca la bocca per lo stupore.) Da solo? Contro tutti quelli? Impossibile! Come ci sei riuscito? Sono Varya, comunque.]],
				['2'] = [[(All'inizio, la ragazza mi guarda con odio, ma poi la sua espressione cambia.)<new_line><new_line>Ehi! Non mi ricordo di te! Non sei uno di loro?]],
				['10'] = [[Tornare da mia zia? Uhm... Ok. Ehi, puoi insegnarmi a sparare?]],
				['11'] = [[Annoiata e senza niente da fare... Beh, non mi va di fare niente, suppongo. Ehi, di che calibro sono le tue cartucce? Grandi come quelle di mio zio o più piccole?]],
				['1'] = [[(Non ci sono fonti di luce nel fienile tranne quella degli isolati raggi di luce solare che passano dai buchi sul tetto. Le assi del pavimento scricchiolano a ogni mio passo, quasi come una protesta.<new_line>La prigioniera stava seduta accovacciata in un angolo buio.)]]
			},
			['name'] = [[Varya]],
			['choice'] = {
				['9'] = [[Uhm... Facciamo così: ti riporto da tua zia, dopodiché io andrò via per un po'. Potrai approfittarne per pensare a cosa fare per ringraziarmi, ok?]],
				['8'] = [[Con me?!]],
				['7'] = [[No, ancora prima. C'è un villaggio di sopravvissuti fuori Brjansk? Puoi indicarmi dove si trova?]],
				['6'] = [[Scusa, cos'hai detto?]],
				['5'] = [[(Prendi in braccio Varya e portala fuori dal fienile)]],
				['4'] = [[Uhm... Non riesci proprio a camminare da sola?]],
				['3'] = [[Piacere di conoscerti, Varya. Ti racconterò tutto per strada. Dunque riesci a camminare da sola?]],
				['2'] = [[No, non sono uno di loro. E ora non ci sono più: li ho uccisi. Alzati, dobbiamo andarcene da qua. Riesci a camminare da sola?]],
				['1'] = [[(Liberala)]],
				['10'] = [[(Termina la conversazione deciso ma con tatto)]]
			}
		},
		['default'] = {
			['episode'] = {
				['1003'] = [[Sei hai tutto il materiale necessario, puoi creare quello che vuoi.]],
				['1005'] = [[Posso creare]],
				['1004'] = [[Posso vendere]],
