				['33'] = [[Vou com o primeiro grupo, contra os lobos que estão dormindo.']],
				['43'] = [[Lutar!]],
				['13'] = [[Mas isso não teve nada a ver comigo! E você poderia ter feito alguma coisa?']],
				['23'] = [[Entrar na seção de frigorífico]],
				['17'] = [[Correr até lá]],
				['47'] = [[Pegar a posição mais arriscada e passar pelo arco primeiro]],
				['57'] = [[Pegar a mala e abrir]],
				['73'] = [[Foi algo terrível, mas eu não poderia ter feito nada. Você também não poderia ter feito nada.']],
				['37'] = [[Tá, vou encontrar ele.']],
				['62'] = [[Tive uma ideia. Vamos deixar à sorte. Quem tirar o palitinho menor, vai.']],
				['52'] = [[Virar]],
				['42'] = [[É. Essa é a faca do Kirill?']],
				['66'] = [[Nikita vai.'<new_line>]],
				['22'] = [[Yury, não, não desista!']],
				['12'] = [[Acha que eu sou responsável pela morte dele?']],
				['16'] = [[Matar a loba!]],
				['56'] = [[Atirar nos lobos]],
				['46'] = [[Atirar para cima para desviar a atenção dos lobos das vítimas]],
				['82'] = [[Sinto muito pelo que aconteceu, mas o que o Gavrilov fez de errado? Ele quase morreu também.']],
				['26'] = [[Esperar]],
				['31'] = [[Continuar andando]],
				['41'] = [[Continuar ouvindo]],
				['51'] = [[Continuar atirando]],
				['61'] = [[Se isso é o que prefere, vão lá então.']],
				['59'] = [[Desculpa, Nikita, mas você não vai. Eu escolhi o Yury, e a decisão é final.']],
				['69'] = [[Por que não impediu ele?']],
				['11'] = [[Continuar...]],
				['21'] = [[Ir para o matadouro]],
				['19'] = [[Ele deve ter se sentido culpado por você.']],
				['29'] = [[Ir na direção do corredor do matadouro]],
				['39'] = [[Talvez ainda possa me dizer o que está te incomodando. Eu sei que sou um estranho, mas vou embora logo. Então, por que não experimenta falar? Vai fazer você se sentir melhor.']],
				['15'] = [[Desviar e atirar]],
				['25'] = [[Levantar e ligar a lanterna]],
				['35'] = [[Acho que o Yury tem razão. Não adianta dividir a unidade. A gente consegue lidar com os quatro juntos, depois a gente dá conta do resto.']],
				['45'] = [[Ir até ele]],
				['9'] = [[Me diga, você acha mesmo que o Gavrilov é responsável pelo que aconteceu?']],
				['81'] = [[Quem te ensinou a entalhar tão bem?']],
				['80'] = [[Ficar em silêncio]],
				['76'] = [[Você vai.']],
				['78'] = [[Que faca bonita. Nunca tinha visto uma assim.']],
				['77'] = [[Vamos matar ela! Agora!']],
				['40'] = [[Escuta, eu sinto muito...']],
				['64'] = [[O Yury vai.']],
				['60'] = [[Desculpa, Nikita, mas você não vai. Eu escolhi o Grigory, e a decisão é final.']],
				['50'] = [[Não tenho como garantir que não haverá outros. A sala de limpeza, ela é grande?']],
				['68'] = [[Desculpa, mas eu sou o comandante, e essa decisão é minha.']],
				['58'] = [[Escopetas de cano duplo?']],
				['20'] = [[Voltar até os feridos]],
				['1'] = [[Eu já vi eles, matei um... em Moscou.']],
				['6'] = [[Eu não tenho mais perguntas.']],
				['7'] = [[Gavrilov acha que você pensa que ele é responsável pela morte do seu padrasto.']],
				['14'] = [[Tá bom. Vamos lá.']],
				['38'] = [[Por quê?']],
				['34'] = [[Vou com o segundo grupo, contra os lobos que já estarão acordados.']],
				['70'] = [[Lutar]],
				['8'] = [[Entendi. Quem te ensinou a entalhar?']],
				['44'] = [[Voltar até ele]],
				['75'] = [[Por que não o impediu?']],
				['71'] = [[Me diga, você acha mesmo que o Gavrilov é responsável pelo que aconteceu em Kiev?']],
				['4'] = [[Quando vai me contar o que prometeu?']],
				['28'] = [[Avançar]],
				['2'] = [[Quem está liderando a unidade?']],
				['10'] = [[Outro? Quem?']],
				['48'] = [[Deixar o açougueiro ir na sua frente. Você cuida da retaguarda. (Não é tão arriscado quanto ir na frente.)]],
				['49'] = [[Deixar o açougueiro ir na sua frente. Você fica no meio do grupo. (Esta é a posição mais segura.)]],
				['5'] = [[Qual é o plano?']],
				['24'] = [[Nikita, pare!']],
				['18'] = [[Yury, ele tem razão. Se não partirmos agora, toda essa gente vai ter morrido em vão. Enquanto a gente se reúne, os lobos vão vir de toda parte. Precisamos matar a loba agora.']],
				['3'] = [[Onde é que a loba está escondida?']],
				['32'] = [[E?']],
				['30'] = [[Ouvir atentamente]],
				['65'] = [[O Grigory vai.']],
				['67'] = [[Estou indo.']]
