		['frankenstein'] = {
			['name'] = [[Monstro de Frankenstein]],
			['step'] = {
				['1'] = [[Fui instruído a interromper a reanimação do monstro de Frankenstein.]]
			},
			['text'] = [[A julgar pela nota do coveiro, Igor está tentando trazer um monstro amaldiçoado de volta à vida. Eu sei onde ele pode estar. Ouvi falar de uma casa que sempre é atingida por um raio. Como isso já começou, agora tem que ir até o fim.]]
		},
		['new_year_boss_2022_3'] = {
			['step'] = {
				['1'] = [[Houve um grande erro no parque de diversões. Mas ainda podemos negociar com os representantes da gangue de Pelznickel.]]
			}
		},
		['progress_olonec'] = {
			['step'] = {
				['1'] = [[O primeiro assentamento povoado da minha jornada. É muito estressante estar entre pessoas novamente. Aqui, todos se acostumaram a confiar uns nos outros e em seus próprios recursos para lidar com as adversidades. Eles vivem da mesma maneira que provavelmente viviam há um século.<new_line><new_line>Os pescadores não vão além do Lago Ladoga, e duvido que eu encontre respostas para todas as minhas perguntas aqui, mas é um bom lugar para ter notícias e me preparar para chegar mais longe.]]
			}
		},
		['gigahive_ufa'] = {
			['step'] = {
				['2'] = [[Há uma colmeia em Ufa que é governada por uma abelha-rainha. Para chegar lá, vou precisar de um antídoto. Só que, para isso, vou ter que matar algumas abelhas-mães e levar a cera até um especialista em Izhevsk. Além disso, o garçom prometeu uma recompensa pela cabeça da rainha.]],
				['3'] = [[Eu matei a abelha-rainha. Preciso levar a cabeça dela ao garçom em Izhevsk para receber a minha recompensa.]],
