				['3'] = [[Tire o equipamento dos bandidos.]],
				['1'] = [[Preciso chegar à estação ecológica e trazer o cientista de volta ao assentamento.]],
				['6'] = [[Encontrei o cientista, agora preciso trazê-lo de volta ao assentamento.<new_line><new_line>Antes de ir, ajudei o cientista a fazer um curativo num pequeno ferimento.]],
				['7'] = [[Ajude o cientista a fazer um curativo num pequeno ferimento.]],
				['4'] = [[Encontrei o cientista, agora preciso trazê-lo de volta ao assentamento.<new_line><new_line>Na volta, recolhemos o equipamento que foi roubado do cientista.]],
				['5'] = [[Encontrei o cientista e ele está esperando para que eu o leve de volta ao assentamento.]],
				['8'] = [[Encontrei o cientista, agora preciso trazê-lo de volta ao assentamento.<new_line><new_line>Antes de ir, ajudei o cientista a tirar de lá o dispositivo que tinha sobrado.]],
				['9'] = [[Retire do telhado o dispositivo que sobrou.]]
