			['text'] = [["Camarada, seus esforços não foram em vão! Pelo menos, descobrimos onde estão os documentos conectados à bioestação mais próxima! Vá até o Laboratório Abandonado, os documentos devem estar lá."]]
		},
		['halloween_boss_2022_3'] = {
			['step'] = {
				['2'] = [[Encontrei uma sobrevivente do ataque dos vampiros. Primeiro preciso escoltá-la até sua casa e depois vou perguntar detalhes sobre a emboscada.]],
				['3'] = [[Eu me encontrei no menu do Conde Drácula. Ele me convidou a relaxar e me divertir — só depois disso as coisas começariam a ficar confusas.]],
				['1'] = [[Há relatos de que um grupo de viajantes foi atacado por vampiros. Preciso ir até o local do incidente.]]
			},
			['text'] = [[Conde Drácula é um vampiro antigo, conhecedor de arte e admirador da companhia de belas damas. Dizem que ele é galante e educado, mas isso não o impede de caçar pessoas e se deleitar do sangue delas.]]
		},
		['progress_tula'] = {
			['step'] = {
				['1'] = [[O Kremlin de Tula recebe os viajantes com uma bateria de canhões modernos saindo pelas antigas ameias das muralhas da fortaleza. Ganhar a confiança dos antigos trabalhadores da Fábrica de Armas não será fácil.<new_line><new_line>Existem várias partes que observam gananciosamente os ricos estoques de armas de Tula, especialmente as gangues que administram Ryazan. No entanto, os ferozes Tulans ficam de fora dos negócios e alianças de outras pessoas, satisfeitos apenas em negociar e manter a guarda vigilante de suas paredes.]]
			}
		},
		['forester'] = {
			['name'] = [[Lenhador]],
			['step'] = {
				['1'] = [[Alguém deixou um pedido pessoal para mim. Preciso ajudar um lenhador. Ele prometeu pagar bem.]]
			},
			['text'] = [[Um cara entrou e deixou um pedido para você. Um lenhador precisa de ajuda. Parece moleza, e tem uma bela recompensa. Eu aceitaria.]]
