		['comrade_in_trouble'] = {
			['episode'] = {
				['13'] = [[Alguns caras assumiram uma posição do outro lado da casa. Suas armas estão apontadas para o prédio. Parece que nosso batedor está lá dentro e está cercado.]],
				['3'] = [[Ao me aproximar do acampamento, ouvi um ronco estridente. Ao lado das brasas de uma fogueira, encontrei um homem adormecido, abraçando calorosamente uma garrafa vazia.<new_line>Não foi difícil descobrir a causa do atraso. Talvez eu deva jogar um pouco de lenha na fogueira? Afinal, a noite está bem fria.]],
				['11'] = [[Há caixotes e caixas espalhados por todo o acampamento. O batedor está tentando prender tudo em uma moto velha.<new_line>"Veja, uma aquisição incrível, mas não posso carregar tudo. O quê? Eles já estão sentindo minha falta em casa? Tudo bem, estou a caminho.<new_line>Mas aquela caixa enorme simplesmente não vai caber. Me ajuda aí e pega ela para mim, vai?"]],
				['1'] = [[Um homem saiu do acampamento para me encontrar:<new_line>"Você é um dos nossos? Faça pouco barulho ou haverá uma grande correria. Diga à base que ficarei aqui por mais um ou dois dias — e não é que esse é um trabalho difícil?"]],
				['16'] = [[O bandido cantarola, pega o dinheiro e leva seus homens embora. Um cara feliz sai pela janela.<new_line>"Uau, amigo, uau! Você me salvou! Eu pensei que era o fim, que eu ficaria sem balas e pronto."]],
				['7'] = [[Afastando-me alguns metros, ouvi um choro fraco. Um homem camuflado estava rastejando em direção ao acampamento. Quando ele me viu, resmungou:<new_line>"Eu ouvi um barulho no acampamento... Você tocou no hardware? Não para de zumbir! Eu sou um idiota e deixei meu medidor na mochila. Então, fui feito luz para o acampamento. Tomei uma dose e mal consigo respirar."]],
				['14'] = [[Isso realmente fez os bandidos pularem, mas um deles rapidamente recuperou o juízo:<new_line>"Ei, você aí! Calma! É o seu amigo emparedado naquela ruína?<new_line>Não vamos fazer nada estúpido — você joga um pouco de dinheiro para a gente, e vocês dois saem daqui inteiros."]],
				['15'] = [[Um cara feliz sai pela janela.<new_line>"Uau, amigo, uau! Você me salvou! Eu pensei que era o fim, que eu ficaria sem balas e pronto."]],
				['25'] = [[Foi bom eu ter chegado a tempo de ajudá-lo, pois ele perdeu muito sangue. Mas agora sua vida não está em perigo.]],
				['24'] = [[Dei um pouco do meu combustível para ele. Deve ser mais do que suficiente para ele voltar ao assentamento.]],
				['8'] = [[Ouvi barulhos estranhos por todo o acampamento. Eu os segui e vi que os predadores cercaram uma torre de alta tensão enferrujada, e o infeliz batedor estava sentado em cima dela.]],
				['9'] = [[O batedor desce e me oferece uma mão firme:<new_line>"Obrigado! Eu realmente pensei que era o meu fim. Eles me pegaram desarmado. Eu só conseguia pensar em subir mais alto."]],
				['23'] = [[Eu dei uns remédios de radiação para ele, daqui a pouco ele vai estar ótimo.]],
				['22'] = [[Fui enviado muito tarde. O batedor morreu. A julgar pelas feridas pelo corpo, ele foi atacado por um desses predadores bem grandes.]],
				['21'] = [[Perto do fogo no acampamento, um homem está mexendo água em uma panela. Ele sorri para mim:<new_line>"Aaah, eles estão preocupados comigo em casa? Não tem por quê. Diga a eles que volto em breve. Foi uma expedição bem-sucedida e fiquei retido."]],
				['20'] = [[Um homem está sentado perto do fogo. Ele não parece estar muito bem: vê-se cortes profundos em seu peito e suas calças estão molhadas de sangue.]],
				['19'] = [[O cara rastejou alegremente para a liberdade, empurrando um saco à sua frente.<new_line>"Eu estava tentando explodir um cofre de aço lá dentro", disse o batedor, dando de ombros. "E acabei ficando preso. Obrigado!"]],
				['18'] = [[Do outro lado da casa encontrei uma janela com grades. Assim que me aproximei, ouvi um grito vindo de dentro:<new_line>"Ei, ei! Estou preso aqui! Me ajude a sair e você receberá uma recompensa do pessoal do assentamento."]],
				['17'] = [[Um acampamento foi montado nas ruínas de um prédio de um andar. Não se vê ninguém, mas a entrada da casa está bloqueada por uma pilha de tijolos e escombros.]],
				['10'] = [[Um acampamento foi montado ao lado do veículo. Há um homem por perto, descarregando ou carregando todo tipo de coisa.<new_line>"O quê? Eles já estão sentindo minha falta em casa? Estava carregando uma pilha de saque e me esqueci da gasolina — acabou no meio do caminho."]],
				['12'] = [[Um acampamento foi montado nas ruínas de um prédio de um andar. Não se vê ninguém, mas a entrada da casa está bloqueada por uma pilha de tijolos e escombros. Ouvem-se tiros e xingamentos do outro lado.]],
				['5'] = [[O acampamento está vazio, mas não há sinais de luta ou retirada precipitada. Há um objeto de metal retorcido ao lado da fogueira.]],
				['4'] = [[Agora nosso heroico batedor não vai morrer congelado.]],
				['6'] = [[O item está danificado, aparentemente por uma explosão. Espere, é a parte de um míssil militar! Meu medidor está enlouquecendo — essa maldita coisa é radioativa!]],
				['2'] = [[No acampamento, há um homem sentado em uma cadeira dobrável em frente a um cavalete. Ele pulou quando me viu, mas então sorriu com culpa e estendeu as mãos.]]
			},
			['choice'] = {
				['16'] = [[Pegue a caixa]],
				['15'] = [[Leve o corpo de volta para o assentamento]],
				['14'] = [[Administre os primeiros socorros]],
				['13'] = [[Limpe os escombros na entrada]],
				['2'] = [[Jogue madeira no fogo]],
				['3'] = [[Examine o item]],
				['11'] = [[Ataque os bandidos e chame a atenção]],
				['1'] = [[Volte ao assentamento com novidades]],
				['6'] = [[Dê remédios de radiação]],
				['7'] = [[Busque ajuda]],
				['4'] = [[Procure o batedor nas proximidades]],
				['5'] = [[Afaste-se ainda mais do projétil]],
				['12'] = [[Serre as barras da janela]],
				['10'] = [[Examine a casa]],
				['8'] = [[Lute!]],
				['9'] = [[Compartilhe sua gasolina]]
			}
		},
		['quest_passenger'] = {
			['episode'] = {
				['1'] = [[Chegamos à base do meu companheiro. Ele ficou muito aliviado.<new_line>'Obrigado. E pode deixar, não vou agradecer apenas em palavras.']]
			},
			['choice'] = {
				['1'] = [[Aceitar a recompensa e sair]]
			}
