		['new_year_boss_2_2'] = {
			['episode'] = {
				['2'] = [[Na praça do vilarejo, os bandidos jogam as decorações de Natal em uma pilha perto de uma fogueira. Alguns habitantes se amontoam nas paredes das casas. Eles são liderados por um homem sombrio com alguns presentes nas mãos.]],
				['3'] = [["Ei, ancião! Você pegou os presentes de todo mundo? Me dá aqui"!<new_line>O velho de máscara assustadora pega uma caixa com um laço das mãos do homem e joga no fogo.]],
				['11'] = [["Ah, você não o reconheceu? Aquele velho é o Papai Noel! Não sei como se transformou nisso...<new_line>Não consigo entender aquela abominação. Talvez o Comerciante Noel saiba o que fazer. Vamos encontrá-lo."]],
				['1'] = [[O fugitivo me leva a uma vila toda enfeitada para o Natal. Bem diante dos meus olhos, bandidos desenrolam fios de luz de postes, arrancam enfeites de árvores e desaparecem por detrás das casas.]],
				['6'] = [[Um monte de enfeites de Natal voa para o fogo, criando um jato de fumaça penetrante e ainda mais espesso que o primeiro. O velho puxa o pobre ancião do vilarejo para si.]],
				['7'] = [[A fumaça se dissipa. Agora se vê uma espécie de monstro ao lado do velho, onde o ancião estava.<new_line>"Vejam, seus malandros, a gangue do Pelznickel está de recruta novo! Ei, novato, seu primeiro trabalho é acabar com esse babaca!"]],
				['4'] = [[Foi como se ninguém tivesse me ouvido. O presente caiu no fogo, surgiram chamas azuis e, em seguida, fumaça azul. O velho e seu pessoal ficaram em volta do fogo inalando a fumaça, os olhos revirando.]],
				['5'] = [[De repente, o velho se vira para mim e começa a rir.<new_line>"Ah, você quer falar com o ancião? Ei, seus malandros, joguem essa porcaria toda no fogo!"]],
				['10'] = [["Isso não vem ao caso. Mas a maneira como eles a ingerem... enquanto destroem tudo que tem a ver com o Natal. Seu mundo está tão corrompido assim?"]],
				['8'] = [[Em resposta, o velho começa a rir novamente. Ainda rindo, corre para sua moto e vai embora.<new_line>Os bandidos, liderados pelo ancião desfigurado, se aproximam de mim.]],
				['9'] = [["Que horror! Por causa daquela fumaça terrível, não consegui ajudá-lo."]]
			},
			['choice'] = {
				['6'] = [["O que você fez com ele?!"]],
				['7'] = [["É tóxica?"]],
