		['new_year_boss_6'] = {
			['episode'] = {
				['14'] = [["Depois conversamos, parceiro! Depois daquela grande descarga de magia, esse lugar está começando a desmoronar! Vamos pegar Pelznickel e dar o fora!"]],
				['13'] = [["Hã? O que está acontecendo?"]],
				['2'] = [[Em um porão, veem pilhas de peças de decoração de Natal e todo tipo de parafusos e ferramentas.<new_line><new_line>Do outro lado da pilha, ouço a voz de Frosty.<new_line>"Não, eu estou no comando agora! Graças a você, estamos começando do zero em um porão mofado!"]],
