			['episode'] = {
				['2'] = [["Malandros. Nossa fábrica usa o Natal e os milagres como forma de publicidade, e eles são contra isso. Eles são nossos concorrentes diretos e temos que cuidar deles. Pronto para restaurar a justiça?"]],
				['3'] = [["Tenho 100% de certeza! A competição é o bem maior. Nossa civilização se baseia em pilares. E como saber quem é o pilar e quem é o novato se não fizermos o possível para competir uns com os outros? Ou eles vencem e provam que estão certos, ou nós vencemos! Então, pronto?"]],
				['11'] = [[Eu peguei o pergaminho e comecei a ler. Quando Comerciante me viu, ele começou a fazer o mesmo. Ao terminar de ler, vi um clarão azul brilhante e, alguns segundos depois, me vi em um deserto.]],
				['1'] = [["Ho ho, olá, meu querido amigo! Nossa fábrica está passando por uma série de problemas e finalmente descobri a razão. Nossos malditos concorrentes estão interferindo em nossa operação! Você pode me ajudar a sabotar as fábricas deles um pouco?"]],
				['6'] = [[Assim que terminamos de esmagar os veículos, continuamos explorando o bloco de gelo. A próxima sala era onde se armazenava o combustível.<new_line>"Ótimo, podemos plantar minas aqui! Pegue os fogos de artifício."<new_line>Comerciante me jogou uma caixa de fogos de artifício.]],
				['7'] = [[Quando terminamos de plantar as minas, continuamos e nos deparamos com uma sala com enfeites de Natal.<new_line>"Ah, que belo presente! Ainda bem que viemos aqui. Ponha tudo nos sacos."<new_line>Com estas palavras, Comerciante tirou dois sacos vermelhos.]],
				['4'] = [[Tivemos que caminhar alguns quilômetros antes de acabarmos ao lado de um grande bloco de gelo. Comerciante sussurrou para mim.<new_line>"Ei, me siga. E este é um pergaminho do portal para você. Se perceber que não vai conseguir, leia o pergaminho: ele vai te tirar daqui."]],
				['5'] = [[Descobrimos que havia várias entradas ocultas no bloco de gelo e entramos por uma. Chegamos ao que parecia ser uma garagem com uma variedade de veículos.<new_line>"Pegue uma marreta e quebre tudo o que vir!"]],
				['10'] = [[Assim que nos afastamos a uma distância segura do bloco de gelo, Comerciante pegou o detonador e o pressionou. Ouvimos um estrondo alto.<new_line>"É assim que se faz! Fizemos um bom trabalho e uma boa ação! Ao derrotar nossos concorrentes, a fábrica vai prosperar!"]],
				['8'] = [["Não se preocupe, meu querido amigo. Há muitos anos que ofereço bondade e milagres às pessoas. Sei muito bem o que é certo e o que é errado. Pegue um saco e jogue os enfeites nele."]],
				['9'] = [[Quando terminamos de recolher os enfeites, jogamos os sacos sobre os ombros e fomos para a saída. Mas um bando de malandros estava bloqueando o caminho. Depois de se proteger num canto, Comerciante sussurrou:<new_line>"Podemos encontrar outra saída, mas vai ser difícil porque os sacos estão pesados. Cuidamos desse grupo e seguimos direto?"]]
			},
			['choice'] = {
				['2'] = [["Estou pronto."]],
				['3'] = [["Eles são bandidos... Tem certeza de que estão por trás dos problemas na fábrica?"]],
				['1'] = [["Que concorrentes? Como eles chegaram aqui?"]],
				['6'] = [[Fuja usando um portal]],
				['7'] = [[Plante minas na sala]],
				['4'] = [[Siga o Comerciante Noel]],
				['5'] = [[Quebre tudo com uma marreta]],
				['10'] = [[Encontre outra saída]],
				['8'] = [[Coloque os enfeites no saco]],
				['9'] = [["Não é errado roubar?"]]
			}
		},
		['emba_navigator_fight'] = {
			['episode'] = {
				['2'] = [["Muito bem, eu andei te observando. Vá em frente, fale com o Biólogo, você tem que ajudar com a pesquisa dele. Leve a caixa preta. E enquanto estiver fazendo isso, seja útil no laboratório.<new_line>Não se esqueça de extrair a biomassa dos pedaços de micélio. Você pode fazer isso com uma faca, mas sugiro um extrator. Troque os disquetes por um extrator com os cientistas, em Consumíveis.<new_line>O que mais... Você já memorizou tudo sobre a cidade científica, ou preciso repetir?"]],
				['3'] = [["Resumindo: pra melhorar o laboratório, você precisa de esporoflores e genofrutos, que crescem em cidades científicas tomadas de vegetação.<new_line>Lá, você vai precisar de um traje AIDA e de herbicida, que pode conseguir com os cientistas em troca de disquetes, em Consumíveis.<new_line>Pra conseguir armas e todo tipo de equipamento com o Biólogo, você precisa de biomassa, que dá pra extrair dos biomutantes. E isso é tudo. Saúde, até a próxima!"]],
				['1'] = [[Um bando de biomutantes caminha pela estrada. Eles buscam algo para comer.]]
			},
			['choice'] = {
				['2'] = [["Memorizei tudo. Até a próxima."]],
				['1'] = [["Repita."]]
			}
		},
		['lair1'] = {
			['episode'] = {
				['1'] = [[Estava tudo coberto de teias. Lutei com eles e finalmente encontrei o coração da toca, onde aranhas agressivas me aguardavam.]]
			}
		},
		['lair5'] = {
			['episode'] = {
				['1'] = [[Aposto que os vampiros estavam esperando por mim. Antes mesmo de entrar no covil, eu já estava cercado por eles.]]
			}
		},
		['newyearEvent_commander_medium'] = {
			['episode'] = {
				['6'] = [[Os outros entendem a lição e também começam a praticar ativamente. Depois de um tempo, Noel das Cavernas se aproxima de mim.<new_line>"Os malandros estão se aproximando. Os aldeões devem combatê-los para consolidar o aprendido. Mostre a eles como lutar."]],
				['7'] = [["Eles fizeram um ótimo trabalho, mas posso ver outro grupo de malandros no horizonte. Passe a eles outra lição rápida. Talvez alguns deles aprendam e usem em batalha."]],
				['4'] = [[A tropa não é muito grande, apenas quatro pessoas. Um deles vem até mim e resmunga de forma interrogativa.<new_line>"Ooogrooooom ah ooom?"]],
				['5'] = [[Dois dos quatro soldados entendem minha lição perfeitamente, e começam a praticar. Os outros dois, ao que parece, não aprendem nada. Um deles pergunta:<new_line>"Ah ooomaroov?"]],
				['2'] = [[Ao me ver, Cavernas pula de alegria e bate a clava na minha cabeça, balançando.<new_line>"Ooooooooooooooh."]],
				['3'] = [[Minhas habilidades intelectuais diminuíram e a fala de Cavernas começa a fazer sentido.<new_line>"Graças aos deuses você me entende! Quero ensinar os aldeões a enfrentarem os inimigos. Até consegui formar uma tropa. Mas, sinceramente, não sou o melhor professor, me ajuda."]],
				['8'] = [[Durante a luta, gradualmente meu intelecto começou a voltar ao normal.<new_line>Após a batalha, um dos soldados se aproximou de mim.<new_line>"Graças a você, agora podemos nos proteger em hora de necessidade. Esse é o maior presente!"]],
				['1'] = [[Cercado por aldeões, Noel das Cavernas tenta explicar algo a eles, da forma de sempre.<new_line>"Oooorgh", diz, apontando para a clava.<new_line>"Brrrrr!" ele continua, apontando um dedo para a floresta.]]
			},
			['choice'] = {
				['13'] = [["Feliz em ajudar." (Vá embora)]],
				['2'] = [[Deixe ele bater na sua cabeça]],
				['3'] = [["Não, tem outro jeito!" (Beba vodca)]],
				['11'] = [[Mostre como contra-atacar]],
				['1'] = [["O que está acontecendo aqui?"]],
				['6'] = [[Mostre como usar uma granada]],
				['7'] = [[Mostre como usar uma lança]],
				['4'] = [["Beleza."]],
				['5'] = [[Mostre como usar um fuzil]],
				['12'] = [[Mostre como esquivar]],
				['10'] = [[Faça uma pose defensiva]],
				['8'] = [[Mostre como usar uma espingarda serrada]],
				['9'] = [[Dê um grito de guerra para atacar]]
			}
		},
		['pharmacy_freak'] = {
			['episode'] = {
				['4'] = [[Quando me virei para sair da farmácia, notei que o corpo da pessoa tinha sumido. Antes de ter a chance de registrar a minha surpresa, o homem desaparecido me atacou por trás com uma faca!]],
				['5'] = [[Resolvi não arriscar e atirei na cabeça do homem por precaução. A julgar pela contração do corpo dele, o homem ainda estava vivo.]],
				['2'] = [[Quando eu fui tentar tocar o homem, ele abriu os olhos e mordeu a minha mão! Depois, ele ficou em pé com um salto e sacou uma faca.]],
				['3'] = [[Como eu suspeitava, não sobrou nada aqui, nem mesmo curativos ou carvão ativado.]],
				['1'] = [[A farmácia já havia sido saqueada de cima a baixo e, no chão, no meio de todo o caos, havia um homem deitado, imóvel, com algumas seringas vazias ao lado. Parece que já limparam este lugar.]]
			},
			['choice'] = {
				['2'] = [[Vasculhar a farmácia]],
				['3'] = [[Atirar no corpo]],
				['4'] = [[Sair correndo da farmácia]],
				['1'] = [[Revistar a pessoa]]
			}
		},
		['catch_thief_quest'] = {
			['episode'] = {
				['31'] = [[Depois de algum tempo, o chefe entrou no depósito, nervoso.<new_line><new_line>'O que está acontecendo? Como os bandidos entraram aqui?']],
				['30'] = [[Derrotei os bandidos, mas agora preciso decidir o que fazer.]],
				['5'] = [[Coloquei uma armadilha na escada, mas fiz com que ela ainda parecesse segura. Agora estou ouvindo barulho nas proximidades. Alguém está vindo; é hora de se esconder!]],
				['32'] = [[Que ótimo plano. Excelente! No começo, nem tinha entendido, mas você merece uma bela recompensa.']],
				['13'] = [[Enquanto amarrava o ladrão, percebi que ele havia se empalado nas barras de ferro e jorrava sangue.]],
				['12'] = [[Ouvi o som baixo de alguém apoiando a escada do outro lado da cerca. Quando o ladrão escalou para o outro lado, eu já estava esperando.]],
				['20'] = [[O lobo me levou até um acampamento cheio de bandidos.]],
				['21'] = [[Depois de derrotá-los, olhei ao redor do acampamento. Eu não era forte o bastante para levar de volta tudo o que eles roubaram, mas peguei o que pude.]],
				['19'] = [[De manhã, contei tudo ao chefe do depósito. Ele balançou a cabeça, desapontado.<new_line>'Desculpe, mas você não conseguiu pegar o ladrão, então não posso dar a recompensa.']],
				['17'] = [[Terminado o serviço, o ladrão voltou e começou a subir a escada. Felizmente, os degraus nos quais eu mexi quebraram com o peso e ele caiu no chão.]],
				['4'] = [[Vi pegadas perto da cerca que levavam até a floresta. E, pelo visto, estavam arrastando algo.<new_line>Encontrei uma escada artesanal no mato.]],
				['15'] = [[O chefe do depósito olhou para o ladrão.<new_line><new_line>'Ele está vivo? Ótimo, agora podemos interrogá-lo.']],
				['25'] = [[Bem, consegui alcançar o ladrão, mas ele me levou direto para os companheiros dele, que claramente não ficaram felizes em me ver.]],
				['24'] = [[O canalha claramente conhecia o caminho melhor do que eu. Após alguns minutos, ele sumiu de vista.]],
				['8'] = [[Ouvi um estrondo na floresta. Claramente, minha armadilha funcionou.<new_line>Vou compartilhar as notícias com o chefe do depósito.]],
				['41'] = [["Bem, obrigado. Agora está tudo bem. Aqueles ladrões estavam me sugando! Veja só, é isso o que eu devo a você."]],
				['40'] = [[O capataz do depósito pode ser bastante convincente. O ladrão confessou, delatando seus cúmplices. O chefe me disse:<new_line>"Bem, isso é novidade. Agora vou precisar pedir ajuda da base. Ou talvez você possa cuidar daquela gangue? Eu pagarei por seus esforços."]],
				['39'] = [[O capataz do depósito pode ser bastante convincente. O ladrão confessou, contando onde escondeu o saque. O chefe me perguntou:<new_line>"Você poderia buscar o que esse vagabundo levou? Eu pagarei por seus esforços."]],
				['38'] = [[O criminoso não admite nada. Talvez ele seja apenas um ladrão de passagem, e foi outra pessoa que fez o grande trabalho no depósito.]],
				['35'] = [[É como se fosse uma pista de obstáculos, e agora o ladrão não vai a lugar nenhum. Mas então ouvi um barulho nas proximidades. Alguém está vindo, é hora de se esconder!]],
				['34'] = [[Eu vi quando o ladrão jogou a escada na cerca e começou a descer. Mas, de repente, nos degraus finais, ele caiu. Torceu a perna, se não pior. Mas pelo menos, agora, ele não vai a lugar nenhum!]],
				['29'] = [[O líder me deu a minha parte dos bens roubados.]],
				['28'] = [[Os bandidos guardavam rancor de mim, mas quando eu mostrei a chave do depósito, seu humor mudou. Não demoramos para esvaziar o depósito.]],
				['23'] = [[O ladrão me viu e largou tudo que pudesse atrapalhar e saiu correndo na direção da floresta.]],
				['22'] = [[O chefe do depósito ficou satisfeito com os resultados.<new_line><new_line>'Eu sabia que podia confiar em você. Aqui está sua recompensa. Você merece.']],
				['1'] = [[De acordo com o chefe, alguém adquiriu o hábito de entrar no depósito e levar embora tudo o que estiver dando sopa. Precisamos pegar esse ladrão.]],
				['14'] = [[O ladrão jogou a escada para o nosso lado da cerca. Enquanto ele descia, saltei do meu esconderijo. Ele tremeu, e a escada quebrou sob o peso, jogando-o no chão.]],
				['16'] = [[Ouvi um farfalhar nos arbustos e vi a silhueta escura de um homem com algo nas mãos, mas não consegui discernir o que era.]],
				['7'] = [[É noite. Preciso definir um esconderijo.]],
				['6'] = [[A tirolesa está pronta e bem escondida.]],
				['10'] = [[O chefe do depósito me agradeceu e acrescentou:<new_line><new_line>'Tenho a sensação de que ele não estava trabalhando sozinho. Mas agora é impossível descobrir.']],
				['18'] = [[Não consegui pegar o ladrão, que desapareceu na noite.]]
			},
			['choice'] = {
				['31'] = [[Pegar a recompensa]],
				['42'] = [[Não tem jeito. Eu não tenho chance contra tanta gente. É melhor ir embora.]],
				['32'] = [[Perseguir]],
				['22'] = [[Esperar o ladrão voltar]],
				['11'] = [[Esconder-se entre a cerca e a parede do depósito]],
				['21'] = [[Emboscar]],
				['28'] = [[Deixar o ladrão fuja e mandar o lobo atrás dele]],
				['17'] = [[Pare aí!']],
				['14'] = [[Voltar ao depósito e esperar até de manhã]],
				['49'] = [[Vá pegar os bandidos]],
				['34'] = [[Ei, pessoal! O que acham de trabalharmos juntos?']],
				['24'] = [[Desista!']],
				['27'] = [[Emboscada: 'Não se mexa!']],
				['9'] = [[Esconder-se perto da cerca]],
				['48'] = [[Vá pegar o saque]],
				['47'] = [[Volte para o abrigo]],
				['46'] = [[Escave um fosso]],
				['44'] = [[Esperar pelo chefe do depósito]],
				['43'] = [[Pegar tudo de útil e sair]],
				['40'] = [[Explicar o seu plano]],
				['36'] = [[E então, cadê a minha parte?']],
				['29'] = [[Atacar]],
				['18'] = [[Esperar até de manhã]],
				['19'] = [[Tratar a ferida]],
				['4'] = [[Voltar para o chefe do depósito]],
				['7'] = [[Destruir os degraus inferiores da escada]],
				['6'] = [[Montar um gatilho-armadilha no caminho]],
				['1'] = [[Esperar até o anoitecer para o ladrão chegar]],
				['2'] = [[Olhar ao redor da área]]
			}
		},
		['scientist_rescue_bar'] = {
			['episode'] = {
				['2'] = [["Vivo e bem? Excelente! Saiba que você salvou a vida de um homem bom. E obrigado também pelo dosímetro, é um modelo raro. E isso aqui é um extra pelo trabalho de primeira qualidade."]],
				['3'] = [["Vivo e bem? Excelente! Saiba que você salvou a vida de um homem bom. E eu já estou sabendo de seus serviços médicos, então isso aqui é um extra. Valorizamos quem cuida bem de nosso povo."]],
				['1'] = [["Vivo e bem? Excelente! Saiba que você salvou a vida de um homem bom. E isso aqui é pelo seu trabalho."]]
			}
		},
		['handcar'] = {
			['episode'] = {
				['31'] = [[A trilha de ossos levava à floresta. De algum lugar entre as árvores, o uivo de um lobo quebrou o silêncio. É melhor eu dar o fora daqui!]],
				['41'] = [[Posso estar sendo exposto à radiação, mas, pelo menos, animais selvagens não vão pegar o corpo dele. Peguei algumas pedras e construí um pequeno sepulcro sobre ele.]],
				['33'] = [[Eu estava chegando perto de Murmansk quando notei algo nos trilhos à frente. Parei o trole e me aproximei. Era um cadáver.]],
				['43'] = [[Quanto mais eu ficar perto do corpo, mais me exponho à radiação. É melhor eu voltar ao trole o mais rápido possível.]],
				['13'] = [[Abri os olhos e me vi deitado no trole. Eu devo ter sucumbido ao sono. Durante aquele pesadelo delirante, também joguei minha pistola em algum lugar. Depois de algum tempo procurando, finalmente a encontrei um pouco mais abaixo dos trilhos.]],
				['23'] = [[Puxei minha pistola e atirei no lince. A bala atingiu o trole, causando faíscas. Assustado, o gato saltou para longe e desapareceu na neblina. Agora eu posso sair daqui.]],
				['11'] = [[Eu abro meus olhos. Um muro de névoa me rodeia. O único som no silêncio é a minha respiração pesada. Devo ter adormecido enquanto segurava a alavanca do trole. E o que foi aquela coisa louca que vi no meu sonho?!]],
				['21'] = [[Tudo o que encontrei depois de investigar lá dentro foi uma corda e uma garrafa de vodca escondida. Quando voltei, tinha um baita lince rondando o trole. Quando ele me notou, arqueou as costas e rosnou.]],
				['19'] = [[Alguns golpes de machado resolveram o problema da porta trancada. Eu entrei.]],
				['29'] = [[Nos vagões de passageiros, não encontrei nada além de algumas malas saqueadas, um pacote de ataduras e... muitas manchas de sangue. Mas onde estão todas as pessoas?]],
				['39'] = [[Quando apertei o botão, o dosímetro começou a zumbir. Agitei o tubo do sensor e, quando o passei sobre o cadáver, o zumbido aumentou. O cadáver é extremamente radioativo!]],
				['15'] = [[Cheguei ao pé do aterro. Devo ter adormecido e caído do trole. Tenho inchaços e contusões por todo o corpo. Aquele sonho louco quase quebrou o meu pescoço.]],
				['25'] = [[O nevoeiro recuou e os pântanos deram lugar à floresta. Das profundezas da mata, emergia outra ferrovia de bitola estreita, paralela à que eu estava. Um pouco mais adiante, vi um trem descarrilado.]],
				['35'] = [[Conversas com os EUA pioram...]],
				['27'] = [[O vagão de controle estava vazio, mas encontrei um pequeno pacote com algumas balas de pistola na cabine do maquinista. Mas a pistola em si, não achei em lugar algum.]],
				['37'] = [[Havia algumas ataduras na bolsa, além de um pequeno dispositivo em um estojo de couro com uma alça.]],
				['40'] = [[Eu arrastei o corpo para o pé do aterro. Agora eu sabia o que provavelmente matara o pobre rapaz: o envenenamento por radiação o consumiu de dentro para fora.]],
				['30'] = [[Ao sair do vagão, notei um esqueleto humano na grama. Os animais selvagens haviam feito um trabalho completo nos sobreviventes dos destroços do trem.]],
				['42'] = [[Quando eu estava saindo, vi um pequeno pedaço de papel saindo do bolso em sua bolsa. Tinha o endereço de uma casa na cidade de Kola. O que tem lá? Sobreviventes? Ou suprimentos?]],
				['32'] = [[O resto da matilha de lobos se juntou aos uivos. Eu corri para o trole e empurrei a alavanca a toda velocidade, tentando evitar olhar por cima do ombro.]],
				['22'] = [[Eu gritei e balancei minha tocha sobre o lince. Assustado, ele saltou para longe e desapareceu na neblina. Agora eu posso sair daqui.]],
				['3'] = [[Depois de examinar o prado, peguei algumas folhas de língua-de-ovelha. No entanto, enquanto eu estava ocupado com isso, o céu ficou completamente escuro. A noite logo chegaria.]],
				['20'] = [[Encontrei uma lata de óleo pela metade sem procurar muito. Voltei para o trole, lubrifiquei a alavanca do AMV e mudei de trilhos. Agora eu posso continuar... ou ficar por aqui e continuar investigando.]],
				['1'] = [[Depois de percorrer alguns quilômetros no trole, vi um riacho ao pé de um aterro. Era cercado por um prado densamente coberto de ervas selvagens e morangos que cresciam junto à água. Um local puro e imperturbável.]],
				['16'] = [[Algumas horas depois, o contorno de uma pequena estação onde a estrada de ferro bifurcava emergia do nevoeiro. O aparelho de mudança de via está ajustado para conduzir o trole a um beco sem saída, mas sua alavanca está completamente enferrujada. Eu tenho que encontrar uma maneira de desemperrá-la.]],
				['7'] = [[Mesmo pescando de sono, eu continuei. De repente, ouvi um som estridente cada vez mais alto atrás de mim, como se um motor a vapor estivesse se aproximando. Através do nevoeiro, vislumbrei a silhueta de algo enorme...]],
				['14'] = [[Eu pulei e caí no barranco. Com um golpe, o porco-espinho derrubou o trole dos trilhos. A última coisa que vi foi o peso do enorme pedaço de metal vindo na minha direção.]],
				['5'] = [[Eu parei o trole. Com certeza, eu não pretendia adormecer e cair dos trilhos enquanto andava no trole. Acendi uma fogueira ao pé do aterro e me arrumei para dormir.]],
				['34'] = [[O corpo tinha um casaco imundo, uma máscara de gás sem filtro e uma bolsa de viagem em sobre os ombros. No bolso do casaco, descobri um jornal enrolado em um cone.]],
				['24'] = [[Minha garrafa de vodca, conquistada com muito esforço, quebrou bem na frente do lince. Assustado, ele saltou para longe e desapareceu na neblina. Agora eu posso sair daqui.]],
				['8'] = [[Do nevoeiro emergiu um porco-espinho do tamanho de um caminhão. Seus olhos estavam iluminados como dois holofotes, e o vapor escorria da boca aberta. Seu rugido ensurdecedor quase me derrubou do trole.]],
				['9'] = [[O porco-espinho estava se aproximando rapidamente. Suas patas amassaram os trilhos como papel. Em questão de segundos, seria o meu fim.]],
				['38'] = [[Parece um rádio estranho e tem um fio saindo com um pequeno tubo de metal na ponta. No estojo de couro, encontrei um folheto de instruções para o Dosímetro-Radiômetro DP-5V.]],
				['36'] = [[Através da máscara de gás, pude vislumbrar o rosto emaciado de um homem que ainda não havia começado a se decompor.]],
				['2'] = [[Comi meus morangos e até peguei alguns para a estrada. Quando terminei, no entanto, o sol havia desaparecido quase completamente além do horizonte. Logo estaria escuro.]],
