			['choice'] = {
				['6'] = [["Acho que ele também repassou a tecnologia a Kurganov."]],
				['4'] = [["Por que isso é uma ameaça?"]],
				['5'] = [["Que novidades você descobriu?"]],
				['2'] = [["Mas os biomutantes surgiram por causa do lançamento do projeto."]],
				['3'] = [["De onde?"]]
			}
