		['cover_the_fighter'] = {
			['episode'] = {
				['2'] = [[O batedor está sentado fumando na entrada de um hangar enferrujado. Ao me ver, ele dá um pulo:<new_line>"Reforços. Ótima notícia, amigo! Já arrombei o cadeado, que tal começarmos, hein?"]],
				['3'] = [[O batedor está sentado fumando na entrada de um abrigo antiaéreo. Ao me ver, ele dá um pulo:<new_line>"Reforços. Ótima notícia, amigo! Já arrombei o cadeado, que tal começarmos, hein?"]],
				['11'] = [["Nenhuma pilha de espólios, mas pelo menos é tudo nosso. Tem outra porta ali. Vamos ver se tem alguma coisa atrás dela que vai nos manter vivos. Mas pelo som, vamos ter que lutar primeiro. Corremos o risco ou voltamos para casa?"]],
				['1'] = [[O batedor está sentado fumando na entrada de uma garagem longa. Ao me ver, ele dá um pulo:<new_line>"Reforços. Ótima notícia, amigo! Já arrombei o cadeado, que tal começarmos, hein?"]],
				['6'] = [[Meu companheiro abaixa a arma e, respirando pesado, examina o local:<new_line><new_line>"Ufa, ainda bem que esperei por você, senão não sairia daqui vivo. Agora preciso inspecionar tudo aqui antes que esses monstros voltem. Obrigado pela ajuda e boa sorte!"]],
				['7'] = [["Uau, olha só esse cofre! Seja lá o que for atrás de um cadeado desses deve valer muito. Pena não termos tempo de ficar por aqui. Fizemos o trabalho principal, mas se der para você abrir esse aí também, o pessoal vai pagar extra."]],
				['4'] = [[Ouvimos um sussurro e um som de raspagem na escuridão. Pegamos nossas lanternas bem na hora: criaturas desprezíveis que fizeram seus covis aqui estão se preparando para atacar.]],
				['5'] = [[Meu companheiro abaixa a arma e, respirando pesado, examina o local:<new_line><new_line>"Ufa, ainda bem que esperei por você, senão não sairia daqui vivo. Agora preciso inspecionar tudo aqui antes que esses monstros voltem."]],
				['12'] = [[O batedor morreu. Fracassei em minha missão de protegê-lo.]],
				['10'] = [[Não havia ninguém dentro. O batedor deu uma olhada ao redor e voltou.]],
				['8'] = [[Depois de bastante esforço, consegui abrir o cofre. Dentro encontramos alguns aparelhos eletrônicos muito bem preservados e caixas de munição. O batedor assobiou de espanto e me deu um tapinha no ombro.]],
				['9'] = [[O batedor está ocupado examinando todos os armários e cantos escuros do local. O cofre está lá, intocado.]]
