		['new_year_deliver_cloth'] = {
			['episode'] = {
				['2'] = [["Ah, é mesmo? Muito gentil da sua parte, eu agradeço! Desculpa por... Bom, estamos vivendo uma época difícil, você sabe."]],
				['3'] = [[Quando cheguei ao local, descobri uma pequena colina com uma escotilha no topo e bati à portinhola. Um homem descabelado colocou a cabeça para fora:<new_line>"Opa, oi! Quem é você?"]],
				['11'] = [["Não vou conseguir entregar a todos, alguns caras partiram a negócios. Você também tem que falar com eles, mas não sei quando voltarão. Obrigado mais uma vez. Isso vai nos manter aquecidos!"]],
				['1'] = [[Havia um comboio de caminhões à minha frente. Uma mulher surgiu debaixo de um deles, o rosto coberto de graxa.<new_line>"Ei! A gente encontrou primeiro! Vai roubar outro lugar."]],
				['6'] = [[Um homem com roupas camufladas saiu dos arbustos.<new_line>"Ah, que gentil da parte delas! Muito legal...<new_line>Peço desculpas pela minha grosseria de boas-vindas. Nós estamos fazendo a segurança da estrada, assim os saqueadores não chegam ao assentamento."]],
				['7'] = [[Eu andei até a fogueira, onde pessoas faziam um estardalhaço por causa de carcaças de animais. Uma menina foi a primeira a me ver chegar:<new_line>"Oi! Você é um de nós, né? Como estão todos por lá?"]],
				['4'] = [["Oh, uau! Que presentão! Obrigado! Quer entrar e se aquecer?"]],
				['5'] = [[Eu caminhei pela estrada de terra, surpreso de não encontrar ninguém por perto...<new_line>Mas, então, ouvi alguém gritar dos arbustos:<new_line>"Eu tenho uma arma! Fique onde está! O que você está fazendo aqui?"]],
				['12'] = [["Ah, que ótimo, ficaremos todos aquecidos. Quando voltar ao assentamento, diga às crianças que mandei um oi! Nós vamos levar um brinquedo ou alguma coisa pra elas brincarem."]],
				['10'] = [["Caramba! Eu conto para os meus parças. Aposto que ficarão felizes em ouvir notícias de casa. Faz dois dias que estamos demolindo essas ruínas... Mas encontramos umas coisas interessantes!"]],
				['8'] = [["Que maravilhoso! Agradeça as crianças por mim. Nós vamos levar um pedação de carne, fazer um banquete!"]],
				['9'] = [[Eu caminhei até um velho edifício. Parecia algum tipo de depósito. Um homem surgiu pela porta frágil:<new_line>"Oi, eu te vi na base! Então eles te mandaram aqui?"]]
			},
			['choice'] = {
				['4'] = [["Com certeza. Você pode entregá-las aos outros?"]],
				['5'] = [["Que bom! Você pode entregá-las aos outros?"]],
				['2'] = [["Certo. Você pode entregá-las aos outros?"]],
				['3'] = [["Não, estou com pressa. Você pode entregá-las aos outros?"]],
				['1'] = [["As crianças do assentamento tricotaram algumas roupas para vocês."]]
			}
		},
		['halloween_boss_2022_5_2'] = {
			['episode'] = {
				['2'] = [[Encontrei um cadáver no quarto. Ele parecia estar inteiro, mas meu dosímetro começou a zumbir feito louco. Ele deve ter ficado emparedado aqui e morreu de fome. Ou de radiação. É melhor eu ir embora se quiser evitar o mesmo destino.]],
				['3'] = [[Ouvi uma voz rouca e decidi abrir caminho entre os arbustos para não assustar ninguém.<new_line><new_line>"Parece que você nem se importa! É um FÍGADO, seu idiota!"]],
				['1'] = [[Alguém neste edifício vai morrer. Mas não há ninguém aqui... exceto que a entrada de um quarto está bloqueada. Talvez a pessoa esteja lá.]],
				['6'] = [[Eu vi um baixinho corcunda e um monstrão humanoide. Eles se viraram para mim e, surpresos, o monstro soltou o homem machucado que segurava. O homem imediatamente desapareceu, e o rosto do monstro se abriu em um sorriso ameaçador:<new_line>"Iiiigor! Essa é uma história totalmente diferente. Eu quero o fígado DELE!"]],
				['7'] = [[O corcunda respondeu:<new_line>"Ele realmente tem uma boa espécime. Vou te mostrar uma coisa, meu amigo, veja esse lindo eletrodo aqui..."]],
				['4'] = [[A resposta veio de um grunhido retumbante:<new_line>"Igor, é de algum bêbado esquisito. Eu não quero isso. Eu teria vergonha de sair em público com um fígado desses!"]],
				['5'] = [[Eu já estava quase ao lado deles e ouvi a voz novamente:<new_line>"Que público? Sair para onde, exatamente? Você é um monstro! E isso é um fígado!"]],
				['10'] = [["Bom, não se pode fugir do destino. Especialmente quando está escrito no Livro da Morte. Venha me ver assim que estiver pronto, o contrato final está esperando por você."]],
				['8'] = [[O corcunda correu para o grande eletrodo, e várias outras criaturas — claramente também experimentos do laboratório de Victor Frankenstein — juntaram-se ao monstro.]],
				['9'] = [[Depois de cuidar do monstro, fui procurar o homem que escapou. Encontrei-o ali perto: aparentemente, enquanto fugia sem olhar para trás, caiu numa ravina. Quebrou o pescoço.]]
			},
			['choice'] = {
				['6'] = [["O quê?"]],
				['7'] = [["Parados!"]],
				['4'] = [[Aproxime-se mais...]],
				['5'] = [[Ataque dos arbustos]],
				['2'] = [[Remova o bloqueio com uma pá]],
				['3'] = [[Aproxime-se rastejando]],
				['1'] = [[Remova o bloqueio com as mãos]]
