		['halloween_boss_2022_6_2'] = {
			['episode'] = {
				['15'] = [["Não, também gostaríamos de lhe dar algo em compensação — aqui. Falando nisso, ainda tenho negócios a tratar em seu mundo, então, passe em nossa loja se precisar de alguma coisa."]],
				['14'] = [["Não tenha medo, meu amigo. Eu sou um novo Ceifador. Fui enviado aqui para substituir meu colega sombrio. Toda essa fraude com contratos e subcontratações... uma vergonha...<new_line>Em nome de todo o mundo dos mortos, peço desculpas. Vou mandá-lo para o seu mundo natal imediatamente."]],
				['13'] = [[O Ceifador me viu tentando alcançar a Gigawattor e sibilou com raiva: "Ah, seu porco! Não importa, você não pode fechar todos os meus portais. Mero mortal, você não é assim tão forte!"]],
				['2'] = [[A floresta estava ficando mais sombria a cada passo, e os olhos das caveiras brilhavam em vermelho. A estrada deu uma guinada acentuada e, na curva, quase dei de cara com um bando variado de monstros.]],
				['3'] = [[Tantos problemas, tudo por causa daquele Burro sarnento...<new_line>Mas estou ouvindo ele zurrar não muito longe daqui e, desta vez, não vou deixá-lo escapar!]],
				['11'] = [["Que diabos... Como assim?! Do outro lado da mesma página?! Mas eu mesmo entreguei a você..."]],
				['1'] = [[No meio do caminho, descobri que estava em uma floresta sombria. Havia crânios empalados em estacas ao longo da estrada, e vozes assustadoras uivavam entre as árvores.]],
				['6'] = [[A outra margem do rio revelou-se escura e deserta, como se pertencesse a outro mundo. E o rio ficou instantaneamente envolto em neblina. Além disso, começou a chover. Com um relâmpago, começou uma tempestade.<new_line>Não se via o Burro em parte alguma, mas o Ceifador estava bem ali.]],
				['7'] = [["Este, mortal, é o meu mundo. Bem-vindo! E não se preocupe com o Burro, ele está exatamente onde deveria estar.<new_line>Sabe, todas essas criaturas e monstros são peixes graúdos, com certeza... mas eu quero é pegar um tubarão de verdade."]],
				['4'] = [[Fui em direção ao rio. O estranho é que não me lembro de vê-lo em nenhum mapa.<new_line>O Ceifador estava na outra margem, acenando para mim:<new_line>"Essa parte é um baixio, atravesse rápido, o Burro está fugindo!"<new_line>Assim que ele disse isso, ouvi galhos estalando atrás de mim.]],
				['5'] = [[Vi mais monstros emergindo da floresta! Eu nunca teria chance contra uma horda dessas. Não tive escolha a não ser atravessar para a outra margem do rio.]],
				['12'] = [[Como se para confirmar o que eu acabei de dizer, a iluminação caiu com um rugido. E atingiu um dos portais! Com um clarão ofuscante, desapareceu. Nada mal! Isso significa que minha Gigawattor pode destruir portais...]],
				['10'] = [["Sua força realmente aumentou desde que lhe dei o primeiro contrato. Mas há uma ressalva.<new_line>Comecei a fazer planos para você quando vi seu nome no Livro da Morte. O que significa que... não se pode fugir do destino, lembra?"<new_line>(O Ceifador pega o Livro e começa a folheá-lo)]],
				['8'] = [["Esqueça o contrato — eu tenho outros objetivos em mente. Você desenvolveu um grande poder demoníaco. A maioria dos Ceifadores nem sonharia em reivindicar uma alma tão poderosa."]],
				['9'] = [["Duvido muito que você consiga — você cruzou o Rio do Esquecimento. E agora nossos amigos em comum virão aqui e acabarão com você."<new_line>Com um estalar de dedos dele, portais apareceram ao redor, brilhando com faíscas elétricas.]]
			},
			['choice'] = {
				['2'] = [["Onde estamos? E onde está aquele Burro?"]],
				['3'] = [["Do que você está falando? Eu cumpri o contrato, ou o quê?"]],
				['1'] = [[Atravesse o rio]],
				['6'] = [["Procurando por isso?" (Saia da página com a lista de vítimas do Monstro de Frankenstein)]],
				['7'] = [["Testemunhe o poder da papelada e da burocracia!" (Rasgue a página com seu nome)]],
				['4'] = [["Vá se ferrar, eu vou para casa."]],
				['5'] = [["Você não pode me vencer!"]],
				['10'] = [[Continue andando]],
				['8'] = [[Saque arma]],
				['9'] = [["O quê? Acabou?"]]
			}
		},
		['halloween_def_easy2'] = {
			['episode'] = {
				['2'] = [[Enquanto a bruxa distraía os monstros, corri direto para a cabana. Assim que entrei, a casa ficou de pé.<new_line><new_line>"Ah, bem melhor assim! Você ajuda essa velhinha? Juntos, expulsamos esse pessoal mais rápido."]],
				['3'] = [["Eu preparei uma poção especial e acho que eles gostaram dela demais. Fico feliz em receber convidados, mas só quando os convido. Tenho uma armadilha interessante que podemos usar com eles, feita de cogumelos. Devo prepará-la ou cuidamos deles como estamos?"]],
				['11'] = [["Minhas costas travaram. Já não sou mais tão jovem. Mas nada de ressentimentos, não é, meu rapaz? Você se deu bem! Pegue sua recompensa e siga seu caminho."]],
				['10'] = [["Ah, muito obrigada, meu menino!"]],
				['6'] = [["Acho que tudo está pronto. Saltamos no três. Preparado?"]],
				['7'] = [["Um..."]],
				['4'] = [["A escolha é sua, meu rapaz. Que tal uma poção antes do combate? Falando nisso, ainda não tive a chance de testá-la."]],
				['5'] = [["Garoto esperto! Acho que está tudo pronto. Saltamos no três. Preparado?"]],
				['1'] = [[Uma multidão se reuniu ao redor da cabana parecendo fãs num show de rock.<new_line>A Velha Bruxa enfiou a cabeça para fora da janela:<new_line>"Corre aqui, rapaz! Eu distraio eles!"]],
				['8'] = [["Dois..."]],
				['9'] = [["Três!"]]
