				['6'] = [["Comerciante Noel está negociando com o gerente. Vou entrar em contato com a gangue. E seu trabalho é "cuidar" do depósito de presentes do parque. Beleza, vamos começar a operação antes que Pelznickel passe na nossa frente!"]],
				['4'] = [["Não sei como vão as coisas nas suas fábricas, mas aqui? Vejam onde chegamos! Os trabalhadores não podem ficar alienados dos meios de produção. Temos que redistribuir os bens materiais."]],
				['5'] = [["Hum, se fizermos uma oferta favorável a Pelznickel... Há um parque de diversões não muito longe daqui.<new_line>Conheço muito bem o gerente de lá — dei presentes e decorações a ele. Talvez a gangue só não tenha encontrado o parque."]],
				['2'] = [["Sabe qual é o problema do sistema social? Vocês exploram o complexo industrial do período de festas há muitos anos."]],
				['3'] = [["Lá vem você de novo com essa coisa de exploração..."]],
				['1'] = [["Olá, camarada! Comerciante, e aí, seu safado? Ooh ah, das Cavernas!"]]
			},
			['choice'] = {
				['2'] = [["O que você quer dizer exatamente?"]],
				['3'] = [["Nós só vamos pegar os presentes dele?"]],
				['1'] = [["É uma tradição de Natal."]]
			}
		},
		['cafe_wolves'] = {
			['episode'] = {
				['2'] = [[A explosão da granada destruiu completamente o que restava do vidro das janelas, mas, pelo menos, matou dois lobos também. Os outros correram para fora, mas, infelizmente, dois deles vieram na minha direção!]],
				['3'] = [[Para desviar a atenção deles, eu joguei alguns pedaços de carne. Três dos lobos agarraram a carne imediatamente e saíram correndo, enquanto o resto correu atrás deles, rosnando famintos.]],
				['1'] = [[Conforme eu me aproximava da lanchonete, notei uma movimentação lá dentro. Pela janela, vi que uma matilha de lobos — pelo menos cinco dos grandes — havia se instalado no lugar. Tenho que me livrar deles para investigar a lanchonete.]]
			},
			['choice'] = {
				['2'] = [[Entrar]],
				['1'] = [[Jogar carne para eles]]
			}
		},
		['santa_wizard'] = {
			['episode'] = {
				['4'] = [["Claro que ele tem certeza! Ele é um mago, caramba!<new_line>E mais uma coisa, amigão, para você ficar tranquilo. Eu descobri que Pelznickel tem umas comportas de energia. Agora eu entendo o que são."]],
				['5'] = [["Exatamente! Se destruirmos essas primeiro, com certeza a arma não vai sobreviver a um ataque direto. Pelo menos, às vezes, é bom trabalhar com alguém competente além de mim. Cyber, leve a gente até as comportas de energia."]],
				['2'] = [["Esta arma é um aspirador de pó mágico ridículo! Quanto mais eu aprendo sobre ela, menos eu entendo o que fazer."]],
				['3'] = [["Isso mesmo. Mas também é verdade que um ataque direto é o que o inimigo menos espera. Se atacarmos todos de uma vez, a superarma não vai aguentar o poder que absorve e vai explodir."]],
				['1'] = [["O que aconteceu aqui? Não conseguiu cuidar das coisas sem mim, é?"]]
			},
			['choice'] = {
				['2'] = [["Tem certeza?"]],
				['3'] = [["Planos B em caso de sobrecarga?"]],
				['1'] = [["Se o atacarmos, perderemos nossa magia."]]
			}
		},
		['emba_isaev_kiev'] = {
			['episode'] = {
				['2'] = [["É provável que alguns dos cientistas sobreviventes tenham continuado trabalhando por um tempo. Por inércia, digamos assim. Mas temos outras hipóteses, então você precisa ficar vigilante."]],
				['3'] = [["Mal encostamos nos dados científicos, mas já temos informações sobre a cidade científica. É provável que você encontre um objeto semelhante ao que viu da última vez."]],
				['1'] = [["Temos novos dados da caixa preta. Ela ainda ligava de vez em quando, mesmo depois da grande guerra. Só que esse processo não é automático, então alguém devia estar usando os equipamentos do laboratório."]],
				['6'] = [["Bem, acontece que a operação correu bem, parabéns!"]],
				['7'] = [["Graças a tudo o que você fez pelo laboratório do Biólogo, tivemos muito sucesso com mutações micélicas controladas!<new_line>Até agora, conseguimos uma cepa semelhante à que está sendo desenvolvida para fins militares."]],
				['4'] = [["O Navegador vai ajudar com as torretas de novo. Deciframos o código de acesso ao compartimento secreto do laboratório. Aqui, eu anotei pra você."<new_line>(Estende uma folha de papel com o código)]],
				['5'] = [["Foram desenvolvidas para fins militares. Você não se lembra? Os militares colaboraram com o Projeto E.M.B.A. Foi notícia em todos os jornais."]],
				['8'] = [["Infelizmente, ainda não. Precisamos continuar pesquisando. Você já viu as consequências do uso de tecnologia sem controle. Isso pode piorar ainda mais as coisas."]],
				['9'] = [["Os dados decodificados apontam para o laboratório onde essas cepas de micélio de "batalha" foram desenvolvidas. É para lá que temos que ir. Vamos partir assim que você estiver pronto."]]
			},
			['choice'] = {
				['6'] = [["Obrigado. Como vai a pesquisa?"]],
				['7'] = [["Então agora o micélio pode nos ajudar?"]],
				['4'] = [["Estou pronto pra prosseguir."]],
				['5'] = [["Acabei me passando. Estou pronto pra prosseguir."]],
				['2'] = [["O que vamos fazer agora?"]],
				['3'] = [["De onde vieram essas criaturas, afinal?"]],
				['8'] = [["Outro Quebrador de Ossos? Eu cuido disso."]],
				['1'] = [["E quem poderia ser?"]]
			}
		},
		['comrade_in_trouble_gas'] = {
			['episode'] = {
				['1'] = [[Montaram um acampamento ao lado do veículo. Um homem está ali por perto, descarregando ou carregando tudo o que se pode imaginar.]]
			}
		},
		['frankenstein'] = {
			['episode'] = {
				['2'] = [[Não havia porta — ou melhor, ela estava caída ao lado da batente. Lá dentro, pairava um cheiro distinto de ozônio e havia cabos cruzando todo o lugar. Eu os segui enquanto me aprofundava no edifício. Eles me levaram a uma sala no centro, onde o Monstro ainda estava deitado sobre uma mesa de metal, enquanto um homem corcunda e estranho corria ao seu redor. Quando ele me viu, gritou de susto e apertou um interruptor na parede. Houve uma descarga instantânea de eletricidade, e o Monstro ganhou consciência.]],
				['1'] = [[O fio chegou ao fim e ficou claro onde o monstro estava localizado. Uma nuvem negra de tempestade pairava sobre um prédio próximo e lançava raios nas hastes tortas do telhado.]]
			}
		},
		['emba_time_medium2'] = {
			['episode'] = {
				['14'] = [["Droga, ele se foi! Deixa pra lá, um dia... algum dia. E obrigada por sua ajuda, você está livre agora. Tenho que refletir agora."]],
				['13'] = [[O carniceiro se aproximou lentamente, coaxou e liberou uma nuvem de esporos da mandíbula na direção do vegeta e só se aproximou depois disso.<new_line>Ele pisou no fio, houve um barulho e uma nuvem de fumaça branca. No entanto, parece que a fumaça não afetou o monitor depois de se misturar com a nuvem de esporos.<new_line>Quando a fumaça e os esporos se dissiparam, o carniceiro não estava em lugar algum.]],
				['2'] = [["Estou observando a movimentação dos biomutantes. Estou tentando achar um padrão nas migrações deles. Quero entender se há espécies conscientes entre eles.<new_line>Preciso de um parceiro, assim eu não perco nada."]],
				['3'] = [["Excelente, vamos. Tá vendo aquele bando ali? Os maiores deles são os gramaníacos. Duvido que sejam conscientes, já que os corpos deles estão muito consumidos pelo micélio. Olha, eles se separaram do grupo.<new_line>Siga-os. Se atacarem, lembre-se de que quando um gramaníaco morre, ele libera uma nuvem de esporos que afeta todos por perto. Por isso, mantenha a distância durante a luta."]],
				['11'] = [["Sim, os carniceiros são os mais interessantes deles. O micélio transformou animais simples, deixando-os mais fortes e mais inteligentes e conferiu a muitos deles habilidades incomuns.<new_line>Na verdade... vamos atrair um até aqui. Quero estudar a dieta deles. Coisas como os vegetais que eles comem. Procure esse tipo de coisa, vou esperar aqui."]],
				['1'] = [["Ei, vem cá. eu vi o jeito que você caça biomutantes. Estou pensando em te levar uma missão."]],
				['6'] = [["Eles são os mais inteligentes de todos. Não foram tão infectados pelo micélio. Provavelmente, eles conseguiram suportar a influência do micélio por mais tempo. Mas ainda são monstros.<new_line>Para piorar, eles são os mais ousados e perigosos. Não se esqueceram de como usar armas humanas. Pelo menos eles não te infetam, já que não cospem esporos."]],
				['7'] = [["E, agora, vamos observar esses parasitas.<new_line>O comportamento deles é bem complexo e precisamos ter o máximo de cuidado possível. Vou acompanhar estes dois, e você tente manter o outro na mira do fuzil."]],
				['4'] = [[Os gramaníacos estão mais lentos por enquanto. Posso segui-los sempre, ou resolver minhas próprias questões sem perdê-los de vista.]],
				['5'] = [[Segui os gramaníacos, gravando o que faziam quando paravam. No final, eles voltaram para o grupo principal.<new_line><new_line>”Hum, eles voltaram. Eles foram explorar? Ou estavam procurando comida? Enfim, vou analisar isso depois. E aquilo são parasitas, viu?”]],
				['12'] = [["Ótimo. Certo, vamos deixar isso aqui, nos afastarmos e observar o que esse monitor fará. Instalei uma armadilha de fio aqui."]],
				['10'] = [["Você registrou tudo? Ótimo. Aqueles dois parasitas trouxeram vários carniceiros para o bando.<new_line>São muito espertos..."]],
				['8'] = [[A Caçadora sorriu com ceticismo.<new_line>”Espero que essa sua confiança não te transforme na refeição deles algum dia. Vamos acompanhar os parasitas.<new_line>O comportamento deles é bem complexo e precisamos do máximo de atenção. Eu vou cuidar desses dois, e você tente manter os outros na mira do fuzil.”]],
				['9'] = [[Eu me concentrei em observar o terceiro parasita. Ele não está com pressa nenhuma, então terei tempo para me preparar.”]]
			},
			['choice'] = {
				['2'] = [[Seguir os gramaníacos]],
				['3'] = [[Ir atrás dos gramaníacos]],
				['1'] = [["Qual é o trabalho?"]],
				['6'] = [["Entendi."]],
				['7'] = [[Levar os registros de observação]],
				['4'] = [["O que você sabe sobre eles?"]],
				['5'] = [["Sim, eu sei muito sobre eles."]],
				['8'] = [["Vamos observar os carniceiros agora?"]],
				['9'] = [[Afastar-se e observar]]
			}
		},
		['new_year_boss_5_4'] = {
			['episode'] = {
				['6'] = [["Como se houvesse outra opção. Certo, garoto, cabeça erguida. Acho que você tem uma pequena chance. Decore a árvore uma última vez enquanto eu preparo tudo."]],
				['4'] = [["Ah, que seja. Vou ficar preso aqui com vocês de qualquer jeito... Posso sugerir um plano idiota também.<new_line>E se redirecionarmos o seu portal direto para o esconderijo do Frosty?"]],
				['5'] = [["É, aquele onde você esteve esse tempo todo, gênio. Não temos energia para mais do que isso.<new_line>E aí, quando você estiver no esconderijo de Frosty, você só terá a si mesmo em quem confiar."]],
				['2'] = [["Nós gastamos quase toda a nossa energia naquele vácuo! Cair direto na armadilha de Frosty não é apenas loucura — é suicídio."]],
				['3'] = [["Que outra escolha temos? Nossas contas já estão com saldo negativo!"]],
				['1'] = [["Sinto o traço da magia que foi roubada de nós... Frosty se escondeu em algum lugar próximo e está a nossa espera para contra-atacar."]]
			},
			['choice'] = {
				['2'] = [["Você está sugerindo desistir?"]],
				['3'] = [["Meu portal?"]],
				['4'] = [["Posso fazer isso sozinho?"]],
				['1'] = [["Então vamos atacar!"]]
			}
		},
		['new_year_boss_4_2'] = {
			['episode'] = {
				['2'] = [["Nossa, obrigado. Então, o que houve?"]],
				['3'] = [["Ah, é por isso que vocês estão aqui. A maldita corporação está tirando os presentes das pessoas comuns! Então, eu me conectei às plataformas de uns mercenários enquanto festejavam. Vamos nessa — temos que quebrar o gelo deles."]],
				['4'] = [["Por que é que o detonado aqui sou eu e é você que está demorando a entender?! Quer ou não quer informações sobre a superarma? Eu sei onde encontrar. Vamos para lá."]],
				['1'] = [["Uhh ah, ugh ooh"...<new_line>Noel das Cavernas dá um tapinha nas costas de Cybernoel com simpatia enquanto ele se inclina sobre uma lata de lixo.]]
			},
			['choice'] = {
				['1'] = [[Dê água para ele]]
			}
		},
		['mirror_lady'] = {
			['episode'] = {
				['2'] = [[Após a luta, vasculhei a casa em busca de vestígios de Igor. A marionete de cerâmica na cômoda me pareceu suspeita. Sentia como se estivesse me observando. Quando me aproximei para ver mais de perto, o boneco me acertou no rosto e, movendo rapidamente seus pezinhos, desapareceu rumo ao desconhecido.]],
				['1'] = [[A casa estava cheia de espelhos. Conforme a instrução do garçom, acendi uma vela na frente de um espelho e comecei a invocar Maria Sangrenta. No começo, senti apenas um perfume nauseante, mas então ouvi passos e uma voz baixa... Ora, ora, ora, o que temos aqui?]]
			}
		},
		['boatman_camp_chat'] = {
			['episode'] = {
				['2'] = [["Sopa?", o barqueiro sorriu. "Não precisa fazer tanto alarde. Ouvi falar de um instituto científico onde deviam estar trabalhando naquele vírus. Mas é perigoso. Radiação, mutantes... pessoas. Você não deveria pisar nessas partes."]],
				['3'] = [["Você não para! Eu já disse, é perigoso e você não está pronto! Fique aqui mais um pouco e aprimore suas habilidades primeiro. Ajude os moradores e realize algumas tarefas no bar. Venda uns produtos, converse com o capataz. Depois, quando estiver pronto, volte e eu conto tudo."]],
				['1'] = [[O barqueiro estava fazendo sopa de peixe numa cuba grande. Quando me viu, ele acenou com a cabeça.<new_line>"E então, encontrou o que procurava em Belomorsk?"]]
			},
			['choice'] = {
				['2'] = [["Onde fica esse instituto?"]],
				['3'] = [["Você me convenceu. Até a próxima."]],
				['1'] = [["Não, eu quero saber o que houve. Quem começou a guerra, o que está por trás da epidemia..."]]
			}
		},
		['embacity_house5'] = {
			['episode'] = {
				['2'] = [[O fungo se espalhou por todo o prédio, em alguns lugares chegando a devorar roupas e livros. Algumas criaturas se alimentam da substância mofada, mas é perigoso se aproximar sem proteção especial.]],
				['3'] = [[Raízes grossas se projetam das janelas, enquanto biomutantes seguem mascando o mofo crescido. Uma raiz brotou num guarda-roupa, agora virado na varanda. Preciso me lembrar de usar proteção especial.]],
				['4'] = [[Das ruínas da casa destruída pelo mofo, é possível ouvir gritos humanos. Não sei se os antigos moradores sofreram mutações ou se são recém-chegados, mas vou precisar de um traje de proteção pra entrar em confronto com eles.]],
				['1'] = [[A julgar pelos sons vindos da casa em ruínas, esse lugar está cheio de criaturas. Se for lutar contra elas, vou precisar de um traje de proteção.]]
			}
		},
		['gravedigger'] = {
			['episode'] = {
				['2'] = [[O coveiro não queria falar. Encontrei uma nota estranha no manto dele, parecia uma lista de compras para partes corpo: braços, pernas, cabeças. No final, havia uma assinatura: ]],
				['1'] = [[Não foi difícil encontrar o coveiro; o homem de aparência estranha me encontrou no portão do cemitério. Na mão direita, ele carregava uma pá, e na esquerda — uma perna podre. Ele me abriu um sorriso medonho, jogou o pé numa carroça próxima cheia de outras partes de corpos, colocou a pá por cima do ombro e correu cemitério adentro. É hora de cuidar dele!]]
			}
		},
		['titanium_mine'] = {
			['episode'] = {
				['6'] = [[Depois de cuidar dos mutantes, prossegui pelos túneis até que finalmente encontrei um carrinho com minério de titânio.]],
				['7'] = [[Continuei correndo ao longo das catacumbas enquanto procurava pela saída. Então, um grupo de mutantes me atacou de repente.]],
				['4'] = [[Não estamos minerando titânio para nos divertir. É um recurso valioso. Se quiser o seu, consiga uma licença em Magnitogorsk. Sem permissão, não entra.']],
				['5'] = [[Seguindo em frente, localizei um elevador. Eu desci cada vez mais pelos túneis confusos e intricados, até que me deparei com um carrinho de minério de titânio.<new_line>]],
				['2'] = [[Um homem de armadura pesada me parou:<new_line>'Você tem uma licença?']],
				['3'] = [[Seguindo em frente, localizei um elevador. Eu desci cada vez mais pelas catacumbas desnorteadoras, até que me deparei com mutantes.]],
				['8'] = [[Correndo pelos corredores das catacumbas, finalmente avistei um elevador e consegui retornar ao ponto de controle.]],
				['1'] = [[Um ponto de controle fortificado havia sido construído na entrada da mina de titânio. Metralhadoras apontavam para as profundezas da mina. Pelo visto, a ameaça não vinha do lado de fora.]]
			},
			['choice'] = {
				['2'] = [[Entregar a licença e seguir andando]],
				['3'] = [[Que tipo de licença?']],
				['1'] = [[Subir até a entrada]]
			}
		},
		['emba_commander_medium'] = {
			['episode'] = {
				['6'] = [["Agradeço, camarada! Derrotamos todos. Chegou a hora do experimento. Vou mencionar você no relatório, sua ajuda foi crucial. Deixe o rádio ali no toco de árvore, a gente pega depois."]],
				['4'] = [["Olha! Que tal coordenar a gente? Temos rádios. Você pode corrigir as nossas ações. Se fizer isso, iremos lá até nessas roupas surradas."]],
				['5'] = [["Sim, estamos prontos para iniciar o ataque e aguardamos suas ordens."]],
				['2'] = [["Agora sim, agradeço! Eu e meu parceiro costumávamos visitar a cidade infectada. Queríamos confirmar uma teoria que tínhamos. Foi assim que arruinamos nossos casacos de proteção... A visibilidade e a mobilidade estão péssimas.<new_line>Ainda estamos com o projeto de biomutantes."]],
				['3'] = [["O líder da minha pesquisa está encarregado disso. É o dr. Isaev, um grande cientista. Meu trabalho agora é testar uma nova tecnologia de extração de micélio dos biomutantes derrotados. Mas não sei como vamos chegar perto deles com roupas assim."]],
				['1'] = [["Oi! Espera, estamos sem energia. Acabamos de chegar. Syoma, você tem uma bebida bioenergética? Não? Droga, o que... <new_line>Ah! Você pode me trazer algo refrescante?"]]
			},
			['choice'] = {
				['2'] = [["Você tem alguma ideia do que fazer?"]],
