			['episode'] = {
				['2'] = [[Apparemment, ma présence l'importune. Poussant un hurlement sauvage, il s'est précipité sur moi en brandissant une lance !]],
				['1'] = [[J'ai trouvé un homme à l'intérieur de la maison ! Ses vêtements sont en loques et son visage, émacié. Ses yeux hagards ont balayé nerveusement la pièce. Quand ils se sont posés sur moi, l'inconnu a laissé échapper un cri à peine audible, avant de se cacher derrière une table cassée.]],
				['3'] = [[J'ai trouvé un homme à l'intérieur de la maison ! Les vêtements en loques, le visage émacié. Ses yeux hagards ont balayé nerveusement la pièce. En me voyant, l'inconnu a laissé échapper un cri à peine audible, avant de se cacher derrière une table cassée.]]
			}
		},
		['blood_trail'] = {
			['choice'] = {
				['2'] = [[Il est condamné. Fouillez-le et prenez ses affaires.]],
				['1'] = [[Suivre le chemin]],
				['4'] = [[Attendez qu'il reprenne connaissance.]],
				['3'] = [[Utilisez des médicaments pour l'aider.]],
				['5'] = [[Laissez-lui un peu d'eau potable et partez.]]
			},
			['episode'] = {
				['2'] = [[Comme je le craignais, deux loups sont entrés et dévorent le cadavre d'un inconnu. Ils ne m'ont pas remarqué.]],
				['1'] = [[Sur le palier, j'ai remarqué une empreinte de pas ensanglantée se dirigeant dans la maison. Je ne sais pas ce qui m'attend à l'intérieur.]],
				['4'] = [[J'ai traité et pansé ses blessures, puis nettoyé la mare de sang, du mieux que je pouvais. Au moins, il a une chance de s'en tirer.]],
				['3'] = [[À l'intérieur, je trouve quelqu'un par terre. Un jeune homme étendu dans une mare de sang. Il a sûrement essayé de panser ses blessures, mais a perdu conscience à cause de l'hémorragie. Il a besoin d'aide.]],
				['5'] = [[À peine une heure plus tard, l'homme a commencé à reprendre connaissance. Lorsque je lui ai expliqué ce qui s'était passé, il m'a remercié en m'indiquant où trouver sa cachette.]]
			}
		},
		['halloween_boss_2022_1'] = {
			['choice'] = {
				['9'] = [[Voyons ce que vous m'avez préparé.']],
				['6'] = [[Aller au milieu et attendre]],
				['5'] = [[Soudoyer]],
				['8'] = [[Et à quoi ça me servira ?']],
				['7'] = [[Quel artéfact ?']],
				['2'] = [[Pénétrer à l'intérieur]],
				['1'] = [[Fabriquer une robe]],
				['4'] = [[Donner une bougie]],
				['3'] = [[Donner de la vodka]],
				['11'] = [[Écouter]]
			},
			['episode'] = {
				['9'] = [[Il te donnera les pouvoirs de ce Démon. L'œil de Balthazar, enchaîné à l'intérieur, te laissera dévorer les âmes de tes ennemis pour renforcer ta puissance.']],
				['6'] = [[Au milieu se trouvait un pentagramme brillant avec la silhouette d'un Démon. Quand les portes se sont refermées, le chef des cultistes a pris la parole.]],
				['5'] = [[D'autres cultistes se pressaient déjà dans la grange. En me faufilant parmi eux, j'ai senti quelqu'un me taper sur l'épaule avant de murmurer : 'Jolie robe, mon frère, respect.']],
				['8'] = [[Tu as bien combattu, mortel. On peut fabriquer un artéfact puissant à partir du Démon que tu as vaincu.']],
				['7'] = [[Le réveil de notre seigneur nécessite de verser le sang. Selon la coutume, nous devons sacrifier le serviteur le plus zélé d'entre nous—celui qui porte la robe la plus réussie. Mes biens chers frères et sœurs, montrez-la-moi !' À ces mots, tout le monde s'est tourné vers moi.]],
				['2'] = [[Ça ne ressemble pas trop à une robe, mais ça fera l'affaire—pas le temps de chipoter. Avec un peu de chance, personne ne me remarquera si je frôle les murs.]],
				['1'] = [[Cette vieille grange est le lieu de réunion des cultistes. Plusieurs d'entre eux sont en train de fumer à l'entrée, et tous portent une sorte de robe. Il m'en faut une, si je veux entrer en me faisant passer pour l'un des leurs. Avec quelques bouts de tissu, je crois pouvoir en fabriquer une en vitesse.]],
				['4'] = [[Si seulement tous les acolytes étaient aussi courageux et loyaux que toi, mon frère. Viens, entre.']],
				['3'] = [[Le gardien posté à l'entrée n'a pas voulu me laisser passer : 'Halte-là ! T'as une bien belle robe, mon frère, mais il va falloir laisser une offrande avant de pouvoir entrer. De la vodka ou une bougie.']],
				['11'] = [[Oh, j'ai failli oublier. J'ai quelque chose à vendre. Regarde—ça pourrait t'être utile.']],
				['10'] = [[Tu devras affronter des adversaires redoutables. Il faudra donc améliorer ton niveau démoniaque au fur et à mesure. D'ailleurs, j'ai déjà un deuxième contrat à te proposer.']]
			}
		},
		['emba_craft_easy'] = {
			['episode'] = {
				['2'] = [[Merci ! Vous n'êtes peut-être pas impliqué dans nos recherches, mais vous contribuez à faire avancer la science. Et ce n'est pas rien !']],
				['1'] = [[Bonjour, camarade ! On a parcouru les friches de long en large avec nos véhicules tout-terrain et les ingénieurs commencent à manquer de pièces de rechange pour les réparer.<new_line>Pouvez-vous nous aider à nous ravitailler ?']]
			}
		},
		['rival'] = {
			['choice'] = {
				['1'] = [[Le menacer]]
			},
			['episode'] = {
				['2'] = [[L'étranger a jeté un coup d'œil à mon équipement. Réalisant son erreur, il a rapidement fait marche arrière.]],
				['1'] = [[Avant de pouvoir entrer, je suis tombé nez à nez avec un homme armé. L'étranger a dit qu'il avait trouvé cet endroit en premier et que donc, c'était à moi de partir.]]
			}
		},
		['halloween_def_easy'] = {
			['episode'] = {
				['2'] = [[Merci, je n'oublierai pas ton geste. Il faut se préparer. Je peux faire rouler des barils de carburant pour que tu les fasses exploser. Sinon, je peux poser des pièges, mais attention où tu mettras les pieds.']],
				['1'] = [[Aimable étranger, tu viens vraiment à mon secours ? Ces démons sont une plaie. Ils ont dévoré toute la récolte. Mes voisins ne croient pas à ces créatures surnaturelles. Ils racontent que je suis un vieux fou, victime d'hallucinations, et que j'ai épuisé mes réserves. M'aideras-tu ? Ça en vaudra la peine.']],
				['3'] = [[Oh ! Tu entends ça ? Les monstres approchent. On va leur mettre le feu !']]
			},
			['choice'] = {
				['2'] = [[Je n'ai pas le temps.' (Partir)]],
				['1'] = [[Je vous aiderai, bien sûr.']],
				['4'] = [[Utilisez les barils.']],
				['3'] = [[Posez les pièges.']]
			}
		},
		['mummy'] = {
			['choice'] = {
				['2'] = [[Avancer dans le couloir]],
				['1'] = [[Suivre l'homme masqué]],
				['4'] = [[Aller à gauche]],
				['3'] = [[Aller tout droit]],
				['6'] = [[Continuer à regarder]],
				['5'] = [[Aller à droite]],
				['8'] = [[Rebrousser chemin]],
				['7'] = [[Ouvrir le coffre]]
			},
			['episode'] = {
				['9'] = [[J'arrive presque immédiatement dans une petite salle dont le plafond est voûté. Au centre de celle-ci, je remarque un coffre.]],
				['6'] = [[Je m'approche légèrement pour mieux observer l'étrange spectacle, mais la momie remarque ma présence. Lorsqu'elle tourne la tête dans ma direction, son cou décharné produit un craquement sinistre. D'une voix rauque et dans une langue qui m'est inconnue, la créature vocifère des syllabes cabalistiques, sans nul doute quelque malédiction égyptienne ancestrale.]],
				['5'] = [[Sur le socle en pierre gît une momie enveloppée de bandages. L'individu que j'ai suivi se tient dans un coin de la salle. Il murmure des paroles inintelligibles tout en remuant une décoction visqueuse et verdâtre dans un chaudron rouillé d'où émane une odeur d'ortie et de plantain.]],
				['8'] = [[Je suis revenu à l'embranchement. Que vais-je faire à présent ? Je dois prendre une décision.]],
				['7'] = [[De l'autre côté du passage voûté, un escalier descend vers une petite salle truffée de cages où sont enfermés des rats. D'innombrables yeux rouges se posent sur moi. Réalisant ma situation, je fais volte-face, mais dans ma précipitation, mon pied bute contre une dalle en pierre. Une seconde plus tard, j'entends le clic des cages qui s'ouvrent, suivi des hurlements de rats voraces.]],
				['2'] = [[L'homme balance sans ménagement le rat dans une cage avant de se diriger solennellement vers une maison dans laquelle il entre. Quelques instants plus tard, j'y pénètre à mon tour. Une fois à l'intérieur, je me retrouve devant un gigantesque couloir bordé de murs en pierre, avec pour seul éclairage, la lueur blafarde de torches moribondes. Frappé de stupeur, je scrute les ténèbres insondables de ce passage cyclopéen. Parmi les effluves putrides qui assaillent mes sens, je perçois l'arôme agréable d'herbes fraîches.]],
				['1'] = [[Approchant de ma destination, je tends l'oreille. À dix mètres devant moi, se dresse une personne vêtue d'une longue robe et d'un masque insolite en forme de bec. Des couinements désespérés provenant de sa direction semblent indiquer que l'inconnu vient d'attraper un rat.]],
				['4'] = [[Le passage du milieu débouche sur un dédale de couloirs. Me fiant à l'odeur des herbes pour m'orienter, je me fraye un chemin à travers ce labyrinthe. Mon odorat ne m'a pas trahi : après quelques minutes, j'arrive dans une grande salle, au milieu de laquelle se dresse un bloc de pierre.]],
				['3'] = [[Je continue d'avancer pendant quelques minutes avant d'atteindre trois petits passages voûtés, tous plongés dans l'obscurité. Devrais-je aller tout droit, à gauche ou à droite ?]],
				['12'] = [[„Alors, allez-vous-en et ne revenez pas“, dit le coffre, d'un ton menaçant avant de se refermer.]],
				['11'] = [[„Très bien, apportez-le ici“, dit le coffre en ouvrant une gueule béante à la dentition intimidante. Sans oser hasarder un regard à l'intérieur de cette monstruosité buccale, j'y balance la carcasse du rat. Le coffre referme aussitôt la gueule et mâche bruyamment avant de me cracher dans la main quelques sacs de friandises. „Maintenant, du balai !„]],
				['10'] = [[Dès que j'atteins le coffre, son couvercle s'entrouvre, exposant une rangée de crocs acérés. „Bas les pattes“, vocifère une voix bourrue et caverneuse provenant de l'intérieur de l'étrange caisse ouvragée et ornementée. Surpris, je sursaute et recule. „Votre première fois ? Vous m'apportez un rat ?“, questionne le coffre.]]
			}
		},
		['comrade_in_trouble_fire'] = {
			['episode'] = {
				['1'] = [[Le feu est presque éteint, mais du bois sec devrait s'enflammer rapidement au contact des braises.]]
