		['doll'] = {
			['episode'] = {
				['2'] = [[« Espèce de sot ! » Les paroles du marionnettiste étaient ponctuées par un rire rauque. « Igor n'est pas sur la terre ferme. Il apportera le livre et le Comte nous libérera tous ! » Le marionnettiste a alors poussé un dernier ricanement nerveux, puis s'est tu.]],
				['1'] = [[Assis sur une chaise, il était absorbé par sa menue besogne. Les marionnettes lui apportaient des outils et tenaient des bougies pour l'éclairer. Lorsqu'il m'a aperçu, le marionnettiste a souri, « Bonjour, prototype. Merci d'être venu. Maintenant, mes marionnettes n'auront pas besoin de partir à ta recherche. Tuez-le ! »]]
			}
		},
		['emba_search_easy'] = {
			['episode'] = {
				['2'] = [[Encore mieux ! Les gars font la queue comme si c'était le magasin du village quand la livraison arrive. J'arrive jamais à me procurer ce qu'il me faut pour ma concoction.']],
				['1'] = [[Écoutez jeune home, j'ai un marché à vous proposer. Je prépare une concoction au mycélium ici et il me faut des matières premières. Vous en auriez pas ?']],
				['3'] = [[En général, la vie n'est pas sans risques. C'est pas grave, essayez ma concoction et vous changerez tout de suite d'idéologie.<new_line>Enfin, on ne va pas y passer la journée. Vous allez goûter ?']],
				['6'] = [[Oh, je vois que c'est bien passé ! Reprenez-en un.']],
				['5'] = [[Alors ça, ça me botte — de travailler ensemble ! Je mets la machine en route ! Vous en voulez... un dernier pour la route, comme on dit ?']]
			},
			['choice'] = {
				['4'] = [[Non, merci.']],
				['3'] = [[Vous comprenez que c'est dangereux ?']],
				['6'] = [[C'est une sorte de gnôle ?']],
				['5'] = [[D'accord, je vais vous aider.']],
				['7'] = [[Bon, vous m'avez convaincu, je vais tester.']]
			}
		},
		['place_stash_quest'] = {
			['episode'] = {
				['9'] = [[Dans les arbustes, j'ai vu un groupe d'individus peu recommandables. Ils avaient l'intention de trouver ma planque.]],
				['6'] = [[J'ai réglé le compte des ennemis. Maintenant, je dois vite partir avant de tomber sur quelqu'un d'autre.]],
				['10'] = [[Dans les arbustes, je suis tombé sur le jeune paumé du bar.<new_line>'Les temps sont rudes, vous savez... On m'a demandé de vous surveiller de près.'<new_line>Il m'a tendu les bras. Une main tenait un paquet de cigarettes, l'autre, une bouteille de vodka.<new_line>'Tenez. C'est moi qui régale.']],
				['7'] = [[La grenade est tombée aux pieds du pilleur. L'explosion a été telle qu'il n'avait aucune chance de s'en sortir. Le chien s'est enfui la queue entre les jambes.]],
				['2'] = [[J'ai enterré la caisse et recouvert la cachette de feuilles mortes.]],
				['1'] = [[J'ai trouvé le lieu indiqué sur la carte. Ça fera clairement une bonne planque.]],
				['4'] = [[J'ai entendu une branche craquer et aperçu les prédateurs.]],
				['3'] = [[J'ai entendu un bruit suspect du côté des arbustes. J'ai fait mon devoir, mais je parie qu'ils me paieront plus, au camp, si je découvre qui a mis la main sur la planque.]],
				['12'] = [[Le jeune homme a hoché la tête, rangeant la bouteille de vodka, avant de repartir.]],
				['11'] = [[Le jeune homme a hoché la tête, glissant les cigarettes dans sa poche, avant de repartir.]],
				['5'] = [[J'ai vu quelqu'un sortir des arbustes avec un chien en laisse.]]
			},
			['choice'] = {
				['5'] = [[Inspecter les arbustes]],
				['8'] = [[Lancer une grenade en restant caché]],
				['7'] = [[Bondir du couvert et attaquer]],
				['2'] = [[Creuser avec les mains]],
				['1'] = [[Creuser avec la pelle]],
				['4'] = [[Se cacher]],
				['3'] = [[S'échapper]],
				['12'] = [[Prendre les cigarettes]],
				['11'] = [[Prendre la vodka]]
			}
		},
		['new_year_boss_3_2'] = {
			['choice'] = {
				['2'] = [[Cette fois, je vais vous massacrer !']],
				['1'] = [[Qui c'est, celui-là ?']],
				['4'] = [[Ils voulaient l'essayer.']],
				['3'] = [[Ils ont parlé d'une arme, non ?']],
				['6'] = [[Mais de qui parlez-vous ?!']],
				['5'] = [[Qui ?']],
				['7'] = [[Soit.']]
			},
			['episode'] = {
				['9'] = [[Oh, non. Pas cet énergumène arrogant ! On ne peut pas faire sans lui ?']],
				['6'] = [[Pendant le combat, deux de ces vauriens ont emporté tous les cadeaux ! Je n'ai rien pu faire.']],
				['5'] = [[Ha, encore toi ? Hé, les mecs, occupez-vous de ces mauviettes ! Brûlez et dévorez la moitié des cadeaux. Jetez le reste dans le bunker. Je reviendrai vérifier !'<new_line><new_line>Père Fouettard posa l'étrange bonhomme de neige sur sa moto, sauta dessus et appuya sur l'accélérateur. Toute sa bande fondit sur moi.]],
				['8'] = [[Oui, j'ai fait une belle gaffe. Désolé, camarades. Cette révélation d'une arme secrète fait froid dans le dos. Il faut découvrir de quoi ils parlent. Nous avons besoin d'un espion.']],
				['7'] = [[C'est vrai ! Nounours, ça va ? Tu as entendu ? S'ils venaient en plus à mettre la main sur une arme secrète... les fêtes seraient finies, et nous aussi.']],
				['2'] = [[Bougre d'âne ! Tu veux parler de redistribution ? Mes gars ont besoin de plus de cadeaux ! De tous les cadeaux, de toute leur énergie ! Noël brûlera pour nous donner plus de pouvoir !']],
				['1'] = [[Père Fouettard, je sais que tu as travaillé sans salaire et qu'on t'a privé des fruits de ton labeur. Nous proposons une distribution plus équitable des...']],
				['4'] = [[Père Nounours leva la main et fit un pas vers Fouettard.<new_line>'Attendez, attendez ! Ce n'est pas ce que nous avons convenu. Discutons de la manière...'<new_line><new_line>Un lourd craquement retentit. Père Nounours fut frappé à la tête et il tomba au sol.]],
				['3'] = [[Ouah, Fouettard. Ils ont apporté des tas de trucs. Y a même de quoi essayer notre arme secrète.']],
				['10'] = [[Nous n'avons même pas pu sauver les cadeaux ! Non, impossible de se passer de lui.<new_line>Ne pose pas de question, camarade. Le plus simple est encore de le rencontrer. Il faudra encore une fois décorer le sapin pour faire venir un autre visiteur.']]
			}
		},
		['celebrate_new_year'] = {
			['choice'] = {
				['2'] = [[Ded Moroz !]],
				['1'] = [[Ded Moroz !]]
			},
			['episode'] = {
				['2'] = [[Snegurochka réunit tout le monde dans un cercle autour de l'arbre que j'ai décoré. «Et maintenant, appelons tous Ded Moroz !», s'écrie-t-elle.]],
				['1'] = [[À mon grand étonnement, je trouve à côté de l'arbre une table prête pour un festin, avec d'innombrables convives. «Vous voyez ? Tout est prêt, et ces gens sont tous ceux que vous avez aidés cette année», dit Snegurochka en me prenant par la main. «Venez, il y a encore du pain sur la planche.»]],
				['4'] = [[Cette fois, tout le monde crie à l'unisson. Les gens commencent à sourire. «Une fois de plus !», crie Snegurochka avec enthousiasme.]],
				['3'] = [[Tout le monde lui emboîte le pas, mais sans trop de conviction... et de manière désorganisée. «Recommençons !», crie Snegurochka !]],
				['6'] = [[La fête a duré toute la nuit. Ded Moroz a souhaité à tout le monde une Bonne Année et nous a offert des cadeaux. Plus tard, accompagné de Snegurochka, il est venu me remercier.]],
				['5'] = [[Je vous entends ! Je vous entends !, répond une voix grave provenant d'arbres non loin, d'où émerge soudain Ded Moroz, comme si de rien n'était. Il est vêtu d'un lourd manteau rouge, tient un bâton flambant neuf dans les mains et porte une hotte regorgeant de cadeaux ! Snegurochka court vers lui pour le prendre dans les bras. Il rigole joyeusement et lui tapote le dos.]],
				['7'] = [[La Reine de l'hiver est toujours dans la glace. Je l'en extirperai lorsqu'elle apprendra à faire de nouveau confiance à autrui, dit Moroz en se tournant vers moi. «Quant à vous, je vous serai éternellement reconnaissant de votre aide. J'espère que l'année prochaine, nos chemins se croiseront une fois de plus, mais pour le moment, prenez cela en guise de cadeau d'adieu ! Bonne Année !»]]
			}
		},
		['halloween_fight_easy'] = {
			['episode'] = {
				['2'] = [[Je suis très proche de la meute de bêtes. Je peux attaquer.]],
				['1'] = [[Les hurlements sanglants des bêtes contre nature qui saccagent la région s'entendent à des kilomètres à la ronde. Elles constituent sûrement une menace pour les gens d'ici. J'aurai une forte récompense si je parviens à les neutraliser.]]
			}
		},
		['help_hunter2'] = {
			['episode'] = {
				['1'] = [[J'ai trouvé le chasseur pris au piège dans une toile d'araignée. Il était encerclé de dépouilles d'arachnides... Mais dès que j'ai libéré le captif, ces insectes géants se sont rués sur nous de toutes parts.]]
			}
		},
		['camp_supply_quest_pt2'] = {
			['episode'] = {
				['2'] = [[Le garde avait l'air inquiet.<new_line>'Je n'ai aucune nouvelle de mes hommes. Que se passe-t-il là-bas ?']],
				['1'] = [[Le chef des gardes m'attendait à la base.<new_line>'Salut, messager.']],
				['4'] = [[Ah, j'avais senti le vent tourner... Laissez la marchandise, voici votre récompense.']],
				['3'] = [[Le chef des gardes me donna une tape sur l'épaule.<new_line>'Mes hommes m'ont tout raconté. Ils ne tarissent pas d'éloges à votre sujet. Voici votre récompense.']]
			},
			['choice'] = {
				['2'] = [[Euh, bonjour à vous aussi']],
				['4'] = [[Rendre compte de l'attaque et des victimes]],
				['3'] = [[Hocher de la tête]]
			}
		},
		['new_year_commander_easy_2'] = {
			['choice'] = {
				['2'] = [[Se concentrer sur les barbes et traîneaux]],
				['1'] = [[Se concentrer sur la distribution de cadeaux]]
			},
			['episode'] = {
				['2'] = [[Oh, oh... J'ai l'impression que le sol s'ouvre sous mes pieds...]],
				['1'] = [[J'ai l'impression que quelqu'un pense un peu trop à moi. L'un des Pères a besoin d'aide !<new_line>Je dois me bouger et venir à la rescousse, tout comme ils l'ont fait pour moi. Il faut que je me concentre.]],
				['4'] = [[Dans un éclair, j'ai été soudainement transporté dans un nouveau monde. Et mes pensées sont si confu-arrrrgh ! Grrrruh ! Hmpffr !]],
				['3'] = [[Dans un éclair, j'ai été soudainement transporté dans un nouveau monde. Que se passe-t-il ? J'ai un bras qui s'est transformé en prothèse, et j'entends de la musique hard rock dans ma tête !<new_line>Je suis dans la peau... de Cyber Noël ?! Quoi qu'il en soit, l'heure n'est pas aux questionnements. Les ennemis se préparent à attaquer ! Réservons-leur un accueil rock'n'roll !]],
				['6'] = [[Dans un éclair, j'ai été soudainement transporté dans un nouveau monde. Était-ce encore une de ces grèves du travail ? De nos jours, plus personne ne respecte les petits propriétaires d'usines, fussent-ils économes...<new_line>Une minute, pourquoi je porte un monocle ? Et puis, où... hé ! Qui touche mes machines, là ? Vous comptez vous syndicaliser ? Ça suffit !]],
				['5'] = [[Dans un éclair, j'ai été soudainement transporté dans un nouveau monde. Et j'ai ressenti des sensations étranges...<new_line>Tant de puissance dans mes pattes ! Ma fourrure épaisse est une carapace contre ce monde froid et capitaliste, et mes crocs tranchants sont prêts à dévorer la main invisible du marché !]],
				['8'] = [[Je commence à reprendre mes esprits... Eh bien, quel gâchis. Je ferais mieux de pratiquer un peu cette magie de Noël, la prochaine fois, si je veux voyager physiquement dans l'espace.<new_line>Enfin, après tout, j'ai quand même réussi à aider mon collègue. Pas si mal !]],
				['7'] = [[Dans un éclair, j'ai été soudainement transporté dans un nouveau monde. Quelle lumière aveuglante. D'où sort-elle ?!<new_line>De mon temps, ce genre de flashs lumineux étaient plaisants et bien plus convenables. Que reste-t-il de cette époque ?<new_line>Je suis vieux. Mais les jeunes gens, de nos jours, feraient mieux de prêter l'oreille à des vieillards comme moi. Bref, qu'avons-nous là ? Encore un combat ? Comme d'habitude, c'est à moi de faire tout le travail !]]
			}
		},
		['infected_shop'] = {
			['episode'] = {
				['1'] = [[Vu l'aspect extérieur du bâtiment, des insectes mutants s'étaient visiblement installés dans le vieux magasin. Il devait y avoir des choses utiles à l'intérieur, mais le boulot s'avérait dangereux.]]
			}
		},
		['gigahive_ufa'] = {
			['episode'] = {
				['2'] = [[Je me suis trouvé dans un long couloir presque sans fin. Quelques minutes plus tard, j'ai vu des cloques se former sur ma peau.<new_line>]],
				['1'] = [[Ironie du sort, les abeilles mutantes ont érigé la plus grande ruche que j'aie jamais vue dans l'ancien institut apicole.<new_line>Le sol tout autour a l'air de vibrer à cause de l'extraordinaire bourdonnement ambiant.]],
				['4'] = [[En quelques pas, je me suis trouvé dans un long couloir presque sans fin. Dans mon exploration, je suis tombé sur une pièce remplie d'abeilles mutantes.]],
				['3'] = [[L'exploration de la ruche n'allait pas être une mince affaire. On m'a parlé de gens qui chassaient les abeilles mutantes à Izhevsk. Espérons qu'ils soient de bon conseil.]],
				['6'] = [[Quand la reine des abeilles est morte, j'ai ressenti une vague de terreur et de détresse. L'horrible bourdonnement était insoutenable : effrayées, les abeilles fuyaient la ruche.<new_line>Je dois vite fouiller la zone et partir d'ici.]],
				['5'] = [[Après avoir vaincu mes ennemis, j'ai décidé de presser le pas. En courant dans les couloirs, je suis arrivé dans une autre pièce recouverte d'alvéoles et de larves. Avant même de pouvoir réfléchir, j'ai vu une abeille mère furieuse s'envoler vers moi.]]
			},
			['choice'] = {
				['2'] = [[Utiliser l'antidote et entrer]],
				['1'] = [[S'enfuir]]
			}
		},
		['intelligence'] = {
			['episode'] = {
				['2'] = [[Je pourrais traverser le terrain d'exercice, car je vois beaucoup d'abris pour l'entraînement au tir. L'autre option est d'entrer par l'entrepôt des matériaux inflammables. Quel que soit mon choix, je devrais me battre pour avancer.]],
				['1'] = [[La situation est au plus mal : tous les humains de la base militaire se sont transformés en biomutants. Mais il y a une bonne nouvelle : le plan de la base que Progress m'a montré s'est avéré correct. Il y a deux moyens d'entrer dans le département des archives.]],
				['4'] = [[Finalement, ce n'était pas si simple, des biomutants gardaient l'endroit. Apparemment, le dernier ordre qui leur a été donné s'est gravé dans leur esprit. Même leur mutation n'a pas pu les forcer à quitter leur poste.]],
				['3'] = [[Je suis sur les lieux. Si l'on en croit les cartes que Progress m'a fournies, les archives contenant les documents qu'il me faut devraient être dans la salle suivante.]],
				['5'] = [[J'ai fouillé la pièce et recueilli tous les plans et cartes avec l'inscription 'EMBA'. Je dois retourner au camp avec.]]
			},
			['choice'] = {
				['2'] = [[Traverser l'entrepôt]],
				['1'] = [[Traverser le terrain d'exercice]],
				['3'] = [[Ouvrir la porte]]
			}
		},
		['bandit_camp_quest'] = {
			['episode'] = {
				['2'] = [[J'ai marché sur une branche morte et le grincement a attiré l'attention des bandits vers l'arbuste. Je suis repéré, ça ne fait aucun doute.]],
				['1'] = [[J'ai trouvé un petit camp dans la forêt. Ces bandits ont du culot de l'avoir monté aussi près de la base.]],
				['4'] = [[C'était facile, en fait. Je dois m'emparer de tout ce qui pourrait être utile et repartir.]],
				['3'] = [[J'ai marché sur une branche morte et le grincement a attiré l'attention d'un bandit, qui s'est approché vers moi pour tenter de me débusquer.<new_line>'Qui va là ? Que voulez-vous ?']],
				['5'] = [[Le chef des bandits a levé les mains en l'air, en signe de paix.<new_line>'On ne veut pas se battre. On repart dans quelques jours.']]
			},
			['choice'] = {
				['5'] = [[OK, ça marche']],
				['1'] = [[S'approcher furtivement du camp]],
				['4'] = [[Sortez de là tant que vous le pouvez encore']],
				['3'] = [[Fouiller le camp et partir]]
			}
		},
		['letter_of_hope'] = {
			['choice'] = {
				['2'] = [[Faire exploser la boîte aux lettres]],
				['1'] = [[Forcer la boîte aux lettres]],
				['4'] = [[Faire exploser la glace]],
				['3'] = [[Briser la glace]],
				['6'] = [[Lui donner une cartouche]],
				['5'] = [[Nourrir l'oiseau]],
				['8'] = [[Prenez une lettre]],
				['7'] = [[Lui faire un compliment]]
			},
			['episode'] = {
				['2'] = [[Il s'avère que la lettre est dans une bouteille au fond d'un fossé recouvert d'une épaisse couche de glace. Comment la lettre est arrivée là, je n'en ai pas la moindre idée, mais la récupérer va être coton.]],
				['1'] = [[Ironiquement, la lettre est dans une boîte aux lettres. Seul hic : il n'y a plus de maison. De tout l'édifice, il ne reste plus qu'un mur, avec la boîte aux lettres qui y est accrochée. Je vais devoir trouver quelque chose pour l'ouvrir.]],
				['4'] = [[Le corbeau n'est pas intéressé.]],
				['3'] = [[Perché dans un arbre, le corbeau me fixe tout en agitant sa tête noire, la lettre dans un bec tout aussi noir. Un troc semble être la meilleure solution.]],
				['5'] = [[Raven croassa haut et fort, a laissé tomber la lettre et agita ses ailes.]]
			}
		},
		['emba_time_easy'] = {
			['episode'] = {
				['9'] = [[Pendant que je les observais, l'un des biomutants s'est tout à coup effondré en plein mouvement, il semblait mort. Un peu plus tard, son corps était entrelacé par les racines noires et traîné jusqu'au centre de la boule végétale.]],
				['6'] = [[Certaines des créatures ne bougeaient pas comme les autres. Leurs mouvements étaient plus abrupts et saccadés. C'est peut-être l'effet des substances chimiques vaporisées.]],
				['5'] = [[Pendant cette période, les biomutants n'ont rien fait de spécial. En renvanche, quand une sorte d'animal, peut-être un loup, est apparu à l'horizon, plusieurs des créatures se sont lancées à sa poursuite.]],
				['8'] = [[C'est peut-être normal pour les biomutants, mais ils ont eu un comportement inhabituel plusieurs fois, restant immobilisés sur place pendant environ dix minutes.]],
				['7'] = [[Les créatures ont commencé à se battre deux fois. Elles ont traîné leurs congénères déchiquetés à l'intérieur de la boule verte.<new_line>Je n'avais jamais remarqué ce comportement dans mes précédentes observations.]],
				['2'] = [[Nous conduisons des expériences sur le comportement des biomutants sous l'emprise de certains composés chimiques.<new_line>Écrivez ce qu'il se produit dans cette assemblée biologique, nous reviendrons dans quelques heures. Ne les attaquez surtout pas, ça interromprait l'expérience.']],
				['1'] = [[Bien le bonjour, camarade ! Vous voyez cette boule de plantes enchevêtrées ? Il y a des biomutants à l'intérieur. Nous avons discrètement vaporisé un produit spécial dessus.']],
				['4'] = [[J'ai remarqué quelque chose d'inhabituel : l'un des biomutants a commencé à bouger avec apathie avant de tomber sur les racines noires et de ne plus bouger.<new_line>Assez rapidement, les racines l'ont attrapé et traîné vers l'intérieur de la boule verte.]],
				['3'] = [[Les biomutants flânent, mangent la moisissure qui pousse sur les racines enchevêtrées.]],
				['11'] = [[Les biomutants ne m'ont jamais repéré. En tout cas, personne ne m'a attaqué.<new_line>Cependant, l'un des biomutants m'a fixé du regard pendant quelques minutes ! Je ne sais pas si c'était un accident ou s'il m'a vraiment vu, mais c'était à glacer le sang.]],
				['14'] = [[J'ai entendu des pas discrets derrière moi. C'était l'un des scientifiques. Il a murmuré :<new_line>'Avez-vous tout noté ? Voyons voir. Bien, oui... Merveilleux... Oui, ce sont d'excellentes observations. Vous avez fait du très bon travail, merci.']],
				['10'] = [[J'ai remarqué quelque chose d'inhabituel : l'un des biomutants, qui était de toute évidence humain avant, a hurlé ou crié quelque chose.<new_line>Puis les autres créatures lui ont obéi ! Ils ont commencé à le suivre quelque part, dans un semblant d'ordre.]]
			},
			['choice'] = {
				['2'] = [[Commencer les observations]],
				['1'] = [[Pour quoi ?']],
				['4'] = [[Enregistrer les observations]],
				['3'] = [[Continuer les observations]]
			}
		},
		['embacity_biolab_2'] = {
			['episode'] = {
				['1'] = [[Les lampes qui avaient survécu éclairaient faiblement le dôme. En son centre, il y avait une porte solide, fermée hermétiquement. Impossible de passer par des moyens habituels.]]
			}
		},
		['construction_team_mutants'] = {
			['episode'] = {
				['1'] = [[Voici l'endroit que le contremaître a marqué sur la carte. Inutile de fouiller leur repaire. Les mutants m'ont remarqué dès mon arrivée, ils se préparent à attaquer.]]
			}
		},
		['frankenstein'] = {
			['episode'] = {
				['2'] = [[Les vestiges de la porte d'entrée étant éparpillés par terre, l'édifice est ouvert à tous les vents. À l'intérieur règne une forte odeur d'ozone. Le sol est jonché de câbles enchevêtrés qui mènent dans une salle où ont eu lieu des actes contre nature. Au centre, le monstre gît sur une table en métal autour de laquelle s'affaire un étrange bossu. Lorsque celui-ci m'aperçoit, il pousse un hurlement de terreur et s'empresse d'actionner un interrupteur sur le mur. S'ensuivent une détonation assourdissante et un éclair d'électricité qui illumine toute la pièce. Quand ce maelström de son et de lumière s'apaise, le monstre ouvre les yeux.]],
				['1'] = [[La bobine s'est complètement déroulée. L'endroit où se trouve le monstre ne pourrait pas être plus évident : non loin, j'aperçois un édifice au sommet duquel des paratonnerres grotesquement difformes attirent la foudre qu'engendre un nuage noir menaçant.]]
			}
		},
		['embacity_radio2'] = {
			['episode'] = {
				['2'] = [[Le magasin était couvert de moisissures, et de gros bourgeons écarlates poussaient au pied des racines vertes. Alors que je les regardais, j'ai failli ne pas remarquer les biomutants. Je dois m'assurer que ma combinaison de protection est fonctionnelle.]],
				['1'] = [[Le magasin est complètement envahi par la végétation, mais il est loin d'être délabré. Il y a peu de traces de mutants, mais quelque chose m'attend à l'intérieur, c'est certain. Avant d'entrer, je dois vérifier si mes défenses sont fonctionnelles.]],
				['4'] = [[Dans les quelques vitrines qui restent du magasin, on aperçoit parfois les silhouettes de biomutants errants. Mieux vaut éviter de les approcher sans combinaison de protection.]],
				['3'] = [[La moisissure a pratiquement détruit le bâtiment, transformant le béton et le fer en un terrain fertile pour biomutants. J'aurai besoin d'une combinaison de protection pour m'occuper d'eux.]]
			}
		},
		['camp_defence_quest'] = {
			['episode'] = {
				['9'] = [[Au final, c'est plutôt positif. Ils s'en sortiront, c'est juste une question de temps.]],
				['6'] = [[Les bandits arrivent. Ils sont nombreux...<new_line>Mais un comité d'accueil est prêt à les recevoir !]],
				['5'] = [[J'ai vu l'endroit idéal pour un piège à loup près du camp et j'ai pu en creuser un rapidement.]],
				['8'] = [[Nous avons tenu bon, mais certains de nos combattants ont été grièvement blessés.]],
				['7'] = [[La situation est sous contrôle. Nous avons tenu bon. Plus personne ne nous ennuiera pendant longtemps.]],
				['2'] = [[En approchant de l'avant-poste, j'ai entendu des coups de feu. Aucun doute : les bandits étaient déjà en train d'attaquer le camp.]],
				['1'] = [[Un homme armé se trouve à l'avant-poste :<new_line>'Vous venez aider ? Bon, mettez-vous en position. Ils sont déjà en chemin.']],
				['4'] = [[J'ai réussi à accrocher le rondin avec les pieux.]],
				['3'] = [[Au point de rendez-vous, je ne vois qu'un cadavre gisant au sol.]],
				['12'] = [[Je n'ai pas pu aider ces gens, mais je les vengerai.]],
				['11'] = [[Les bandits sont enfin partis. J'ai fouillé le camp, il ne restait que les cadavres de nos alliés.]],
				['10'] = [[J'ai entendu bruit et je me suis caché. Des étrangers rôdaient près du camp.<new_line>C'était trop tard. Les bandits avaient déjà pris le contrôle.]],
				['13'] = [[Si j'en crois la carte, le camp n'est plus très loin.]]
			},
			['choice'] = {
				['16'] = [[Examiner le cadavre]],
				['9'] = [[Se mettre en position et attendre]],
				['17'] = [[Venger les camarades tués]],
				['18'] = [[Attendre le départ des bandits]],
				['14'] = [[Rejoindre le combat]],
				['10'] = [[Défendre notre camp]],
				['8'] = [[Voir ce qu'il y a à faire]],
				['7'] = [[Creuser un piège à loup]]
			}
		},
		['santa_monopolist'] = {
			['choice'] = {
				['2'] = [[Que se passe-t-il ?']],
				['1'] = [[Je ne sais pas. Qui êtes-vous ?']],
				['3'] = [[D'accord, allons-y.']]
			},
			['episode'] = {
				['2'] = [[Je travaille... ailleurs. Je ressemble au Père Noël, mais je suis différent de lui. Nous en discuterons plus tard. Pour l'heure, je dois savoir où est passé mon collègue.']],
				['1'] = [[Ho-ho-ho, jeune homme. Que viens-tu faire ici ? Où est ton Père Noël ?']],
				['4'] = [[Parfait. Ton Père Noël devrait avoir des éléments de réponse. Je propose d'aller là où il reçoit habituellement ses partenaires commerciaux.']],
				['3'] = [[Argh, le temps, c'est de l'argent... Bon, en gros, dans mon monde, je fabrique des produits de Noël. Je fournis l'esprit des fêtes à tous ceux qui souhaitent faire affaire. Dernièrement, cela dit, tous ceux que j'envoie en livraison dans votre monde disparaissent. Et si l'on découvrait ensemble ce qui se passe ?']]
			}
		},
		['polar_star'] = {
			['episode'] = {
				['2'] = [[Je m'appelle Umka, et je sais pourquoi vous m'avez invoqué. Je vous aiderai, mais la Reine de l'hiver recèle une puissance incommensurable. Vous devez prouver que vous êtes suffisamment fort pour l'affronter, dit l'ours. «Venez me voir lorsque vous serez prêt.»]],
				['1'] = [[Lorsque j'ai levé la boule vers le ciel, elle a brillé de manière encore plus intense, se remplissant de l'éclat éblouissant des étoiles. Lorsque mes yeux se sont de nouveau accoutumés à l'obscurité, un gigantesque ours polaire se tenait juste devant moi, la boule enchâssée dans son front.]],
				['3'] = [[Vous êtes fort, mais vous aurez besoin de protection contre la sorcellerie glaciale de la Reine de l'hiver. Dénichez une plume de l'Oiseau de feu et invoquez-moi à nouveau lorsque vous serez prêt, dit l'ours avant de disparaître. La boule est alors tombée doucement par terre.]]
			}
		},
		['icebreaker_short'] = {
			['episode'] = {
				['2'] = [[Toutes les portes du navire avaient été enfoncées, et des douilles jonchaient le sol. Des cadavres étaient vautrés dans tous les coins. Les étagères étaient remplies de bric-à-brac du navire, mais j'ai trouvé des médicaments, intacts. L'équipage n'avait même pas tenté de quitter le navire. Les blessés n'avaient pas été soignés. C'était si étrange.]],
				['1'] = [[En fait, on dirait que le fou furieux de Monchegorsk a servi à bord de ce navire.]],
				['3'] = [[Dans la cabine du capitaine, j'ai trouvé le livre de bord. Il était détrempé, mais j'ai réussi à lire la date de la dernière entrée : le 22 septembre 1984.<new_line>Parmi les tas de sacs dans le placard, un objet brillant a attiré mon regard. Une bombe aérosol, avec une étiquette 'Day R'. J'ai continué de fouiller les débris, et j'en ai trouvé une bonne dizaine, toutes pareilles.<new_line>À quoi servaient ces aérosols ? Qu'était-il arrivé à l'équipage ?<new_line>C'était comme si tous les matelots avaient perdu la tête et s'étaient entretués. Je dois partir de ce lieu sordide.]]
			},
			['choice'] = {
				['2'] = [[Trouver la cabine du capitaine]],
				['1'] = [[Monter sur le navire]]
			}
		},
		['comrade_in_trouble_bar'] = {
			['episode'] = {
				['2'] = [[Le chef des gardes a hoché de la tête en écoutant mon rapport.<new_line>'Message reçu, nous lui envoyons de l'aide. Merci de votre aide !']],
				['1'] = [[Le chef des gardes a hoché de la tête en écoutant mon rapport.<new_line>'C'était donc ça. Merci du renseignement, vous avez bien travaillé.']],
				['3'] = [[Le chef des gardes a hoché de la tête en écoutant mon rapport.<new_line>'Beau travail ! Vous savez vous rendre utile. Voici un petit supplément pour compenser.']]
			}
		},
		['forest_pithouse'] = {
			['choice'] = {
				['9'] = [[Sortir]],
				['6'] = [[Je ne fais que passer]],
				['5'] = [[Sortir des buissons et dire bonjour]],
				['8'] = [[Dire au revoir et partir]],
				['7'] = [[Ça fait un bail que je n'ai pas parlé à qui que ce soit ! Ça vous dit de bavarder.]],
				['2'] = [[Entrer]],
				['1'] = [[Ne pas se faire voir et attendre]],
				['4'] = [[Installer un piège à grenade]],
				['3'] = [[Fouiller partout]],
				['12'] = [[Fouiller de fond en comble]],
				['11'] = [[C'est bon, je m'en vais]],
				['10'] = [[Oh, doucement ! Je veux juste parler !]]
			},
			['episode'] = {
				['9'] = [[J'ai trouvé de nombreuses provisions à l'intérieur de l'abri : vivres, médicaments, outils et munitions pour n'en citer que quelques-unes. Je devais juste sortir avant le retour du maître des lieux.]],
				['6'] = [[Lorsque l'homme a réalisé qu'il ne risquait rien, son visage s'est décrispé. Nous avons bu, mangé et bavardé, notamment du passé, et de nos idées pour l'avenir.]],
				['5'] = [[Il m'a jeté un regard méfiant avant de se détendre. <new_line>]],
				['8'] = [[La porte était juste fermée par un petit loquet, manifestement pour empêcher les animaux d'entrer. Il faisait plutôt chaud à l'intérieur. Des sacs et des caisses remplis de bric et de broc étaient entassés dans les coins.]],
				['7'] = [[Quand le moment est venu de se dire au revoir, il m'a demandé si je voulais échanger du sel, des cigarettes et des munitions.]],
				['2'] = [[Il n'a pas fallu attendre très longtemps pour que le propriétaire des lieux revienne. C'était un homme trapu tenant d'une main son fusil, et de l'autre, un panier rempli de champignons. Il a jeté un regard inquisiteur autour de lui avant d'alimenter le feu.]],
				['1'] = [[Dans les bois, je suis tombé sur un abri creusé à même la terre. Les charbons dans le foyer sont encore chauds, quelqu'un vit donc ici et ne doit pas être bien loin. Je n'entends aucun bruit à l'intérieur.]],
				['4'] = [[Tu ferais bien de dégager]],
				['3'] = [[L'homme s'est redressé brutalement et a pointé son fusil dans ma direction.<new_line>]],
				['12'] = [[J'ai installé le piège à grenade avant de me cacher dans des buissons. Quelques instants après, le propriétaire des lieux est arrivé. À peine est-il entré dans son logis de fortune que la grenade a explosé. L'inconnu était déjà mort quand je suis arrivé. Je peux fouiller la zone sans encombre.]],
				['11'] = [[À côté de l'abri, j'ai trouvé quelques baies, de la viande, ainsi que des peaux qui séchaient et un panier de charbon.]],
				['10'] = [[Dès que je suis sorti avec mon sac plein à craquer, j'ai aperçu un homme, probablement le propriétaire. L'air résolu, il pointait son fusil dans ma direction. J'ai alors entendu un coup de feu et senti une douleur fulgurante dans l'épaule droite.]],
				['13'] = [[Alors que je m'apprêtais à partir avec mon butin, une meute de loups a surgi des ténèbres de la forêt. Il semblerait que la détonation de l'explosion et mon intrusion les aient attirés. Je dois me défendre.]]
			}
		},
		['frozen'] = {
			['episode'] = {
				['1'] = [[Alors que les brigands chargeaient les immenses blocs de glace sur leurs traîneaux, j'ai pu m'approcher. À travers la glace brumeuse, j'ai aperçu les silhouettes des captifs. Il faut vite les libérer.]]
			}
		},
		['flying_dutchman'] = {
			['episode'] = {
				['2'] = [[Lorsque le capitaine en a terminé, Igor avait disparu, englouti par les flots. Jetant un coup d'œil par-dessus le garde-corps, j'ai entraperçu le Cavalier sans tête en lévitation au-dessus de l'eau. Dans une main, il tenait le livre que je recherchais et de l'autre, sa tête. Sans perdre un instant, le Cavalier s'est lancé dans les ténèbres à bride abattue avant d'y disparaître. Impuissant, je décidais de continuer à fouiller le navire.]],
				['1'] = [[La cloche a retenti avec force. Des profondeurs abyssales de l'océan a surgi un vieux bateau recouvert d'algues et de coquillages. Faisant fi de mes doutes et de mes craintes, je suis monté à bord. L'embarcation m'a alors emmené jusqu'à un navire enveloppé d'une brume fantomatique. Ses marins, atrocement défigurés, ne m'ont prêté aucune attention lorsque je me suis hissé sur le pont détrempé et glissant. À ce moment précis, Igor est sorti des quartiers du capitaine. Décontenancé, mais reprenant rapidement ses esprits, il m'a montré du doigt en hurlant, « Il est venu me chercher ! Tuez le chasseur ! »]]
			}
		},
		['help_hunter1'] = {
			['episode'] = {
				['1'] = [[Une horde de zombies a piégé le chasseur. Sans aide, il n'avait aucune chance d'en réchapper.]]
			}
		},
		['reactor_guard'] = {
			['choice'] = {
				['1'] = [[Entrer]]
			},
			['episode'] = {
				['1'] = [[Trois ours gigantesques ont transformé la salle du réacteur en tanière. Je dois me débarrasser d'eux pour obtenir ce dont j'ai besoin.]]
			}
		},
		['newyearEvent_time_medium'] = {
			['episode'] = {
				['9'] = [[Malheureusement, l'ami, il va falloir démonter tout ça. Les vauriens ont glissé ce plan à Nounours, et vous avez construit leur repaire.']],
				['10'] = [[Je dois retirer les pièges. Cette tâche me prendra 8 heures.]],
				['6'] = [[Je ne souhaite pas discuter de mon travail avec des exploiteurs !']],
				['5'] = [[Avant de pouvoir prononcer un mot, j'ai entendu le bruit d'un moteur derrière moi. C'était une petite voiture rouge, à l'ancienne. Elle s'est arrêtée, et Père Négoce est sorti du véhicule, l'air visiblement agacé.<new_line>'Qu'avez-vous fait, bande de farxistes ?! Vous vous rendez compte qui est le commanditaire de ce chantier, au moins ?']],
				['8'] = [[Grooh graraaaaavaaaaah...'<new_line>Le coup asséné, Nounours et Nomade se comprenaient tous les deux. Le premier semblait reprocher avec animation quelque chose au second. Perdus dans leurs échanges, ils quittèrent le village.<new_line><new_line>Je me suis retrouvé seul avec Négoce.]],
				['7'] = [[Négoce posa les doigts sur sa tête. Quelques instants plus tard, miracle : un portail était apparu, et un Père Nomade tout aussi agacé en sortit.<new_line>'Graah groovaaah ?!'<new_line>Nomade semblait jurer comme un charretier, et il frappa Nounours avec sa massue.<new_line>]],
				['2'] = [[C'est assez simple. On m'a donné un plan. Je dois partir de cette base pour mener un projet de construction !'<new_line>]],
				['1'] = [[Salut, camarade ! Le travail, c'est la santé. Pas vrai ? En fait, ça me manque de me crever au boulot ! Tu veux bien m'aider ?']],
				['4'] = [[Nounours travaillait à une vitesse hallucinante. Le temps de bâtir un cabanon, il avait construit tout le reste. Le chantier terminé, le résultat de notre labeur nous semblait étrange.<new_line>Nounours avait formé des blocs de glace, posé des pièges et creusé un foyer pour des rituels.<new_line>'Comme on dit : ouvriers du monde, restez au chaud !']],
				['3'] = [[Repose-toi le temps qu'il faut, on se mettra au travail après. Ça devrait prendre une dizaine d'heures.'<new_line>]],
				['12'] = [[Maintenant, je dois démonter les fortifications. Ça va m'occuper pendant 12 heures.]],
				['11'] = [[J'aimerais beaucoup trouver une utilité à tout ça, mais on n'a pas le choix. Les habitants du coin sont partis, ou alors ils ont été capturés. Personne n'est là pour occuper ce repaire... à l'exception des vauriens, naturellement. Il faut démonter tout ça, d'accord ?']],
				['14'] = [[Bon, je file. Ton monde a besoin de révolutions ! À plus tard !']],
				['13'] = [[Une fois le travail terminé, quelqu'un me donna une tape sur l'épaule.<new_line>'Salut, camarade ! Je suis venu m'excuser, tu as fait ça tout seul... mais je suis incapable de casser quoi que ce soit. Et puis, je viens à peine de me remettre du coup de massue du Père Nomade. Laisse-moi t'offrir un bon dîner.']]
			},
			['choice'] = {
				['9'] = [[Manger de la tarte]],
				['6'] = [[Que faire maintenant ?']],
				['5'] = [[Que diable avez-vous...']],
				['8'] = [[Se mettre au travail (12 heures)]],
				['7'] = [[On ne peut pas s'en servir ?']],
				['2'] = [[Je n'ai pas le temps.' (Partir)]],
				['1'] = [[Qu'allons-nous faire ?']],
				['4'] = [[Se mettre au travail (10 heures)]],
				['3'] = [[Ça me paraît simple et limpide.']],
				['12'] = [[Se mettre au travail (14 heures)]],
				['11'] = [[Au revoir.']],
				['10'] = [[Avaler une boisson énergétique]],
				['13'] = [[Se mettre au travail (16 heures)]]
			}
		},
		['help_hunter3'] = {
			['episode'] = {
				['1'] = [[Tout en poussant des ricanements sinistres, la sorcière enchaîne de redoutables sortilèges pour déloger le chasseur de sa cachette. Contraint de se cacher in extremis, il a laissé son arme non loin de son refuge. Je dois attaquer la sorcière avant qu'elle ne parvienne à faire passer sa proie de vie à trépas.]]
			}
		},
		['emba_commander_easy'] = {
			['episode'] = {
				['2'] = [[Excellent ! Non, vous n'allez pas servir de chair à canon. Vous devrez juste vous tenir assez loin pour ne pas courir de risque et envoyer des signaux aux tourelles pendant que nous observons.<new_line>Vous avez de l'expérience, vous comprendrez vite comment ça fonctionne. Allons-y !']],
				['1'] = [[Bonjour, camarade ! Vous voyez ces biomutants ? Nous les avons couverts d'une solution spéciale qui devrait les faire agir différemment en combat.<new_line>Il nous faut quelqu'un pour contenir leurs attaques pendant qu'on les observe.']],
				['3'] = [[Très bien, très bien. Nous devons changer les radicaux libres... et augmenter la diffusion autour des biomutants...<new_line>Ah, vous êtes encore là ! Merci du coup de main. Si vous revenez, on testera une nouvelle formule.']]
			},
			['choice'] = {
				['2'] = [[Je ne suis pas prêt.']],
				['1'] = [[Je suis prêt à vous aider.']]
			}
		},
		['emba_energy_hard'] = {
			['episode'] = {
				['9'] = [[Vous v-voyez, avez la bonne pop... prop... portion, je peux d-découper dix de ces meuh... muh... mutants. Allons dans la cour, je vais vous m-montrer ! Mais d'abord, un au... t-tre...']],
				['6'] = [[La concoction du vieux bonhomme a beaucoup de propriétés non explorées... C'est l'appel de la science. On commence l'expérience ?']],
				['5'] = [[C'est une concoction au mycélium, camarade. Les scientifiques s'en contrefichent, mais ce truc à un de ces effets sur le corps ! C'est vraiment quelque chose !']],
				['8'] = [[Regardez ce que fait ce myc-cé... lium. Et on est sérieux. T-t-terminons l'exp..érience. Cul sec, pour la science !']],
				['7'] = [[Ce vieil homme a vraiment trouvé une super formule. Ru...ré... actifs purs. Écrivez vos ob... serrr... va-ti-ons dans ce journal, on le montrera aux g-gars jaunes plus tard. On remet ça !']],
				['2'] = [[Vous savez, moi aussi, je suis une sorte de scientifique. Mais il me faut des matériaux pour étudier. J'ai passé un accord avec un vieil homme. Il vous suffit d'aller le voir pour récupérer ce dont j'ai besoin.']],
				['1'] = [[Hé, je me souviens de vous. Vous voulez faire une expérience ? Je crois qu'on en a déjà fait plusieurs.']],
				['4'] = [[Le vieil homme m'a donné un paquet que j'ai rapporté au fermier.<new_line><new_line>Il l'a déballé avec enthousiasme. Il en a sorti une bouteille de liquide vert et a commencé à prendre des notes dans son journal :<new_line>'Bien. Nous réalisons l'expérience numéro... euh... je ne sais plus. Matériaux... un litre de la substance à étudier. Bien, vous êtes prêt ?']],
				['3'] = [[C'est le fermier qui vous envoie ? Je lui ai déjà dit que sans nourriture, pas de matériaux. Vous pouvez m'en apporter vous-même si ce paysan est trop fainéant pour le faire.']],
				['12'] = [[Ce qu'il s'est passé ensuite est un trou de mémoire. Je me suis réveillé dans la cour où tout était à l'envers avec un couteau tordu coincé dans la clôture.<new_line>Dans la cabane du fermier, les rideaux étaient tirés et les portes fermées à clé. Il semble que l'expérience n'ait pas fonctionné.<new_line>Je me sens très mal. J'ai dû mal compter la dose — cette concoction au mycélium n'est pas à prendre à la légère.]],
				['11'] = [[C'est comme si mes souvenirs de ce qui s'est passé ensuite avaient été complètement effacés. Je me suis réveillé dans la cour, tout était à l'envers et un couteau tordu était coincé dans la clôture.<new_line>Dans la cabane du fermier, les rideaux étaient tirés et les portes fermées à clé. Il semble que l'expérience n'ait pas fonctionné.<new_line>Mais je me sens mieux que ce que j'aurais pu penser — cette concoction au mycélium est peut-être prometteuse, après tout.]],
				['10'] = [[Ayant bu la substance jusqu'à la dernière goutte, le fermier s'est levé avec difficulté, a fait deux pas vers la porte et s'est effondré par terre en se mettant immédiatement à ronfler. J'ai pris ses notes sur la table et je suis sorti en chancelant. Je vais apporter ça aux scientifiques pour qu'ils puissent évaluer ses recherches.]]
			},
			['choice'] = {
				['2'] = [[Aller voir le vieil homme]],
				['1'] = [[Vous vous êtes mis à la science ?']],
				['4'] = [[C'est quoi ?']],
				['3'] = [[Versez-le.']],
				['5'] = [[Sacré scientifique...']]
			}
		},
		['embacity_biolab_reservoir_2'] = {
			['episode'] = {
				['2'] = [[Un énorme réservoir occupe une grande partie de la biostation. Il est maintenant vide.<new_line>Une station de pompage y est attachée ; des machines similaires se trouvent sur les petites citernes en périphérie de la cité.]],
				['1'] = [[Un énorme réservoir occupe une grande partie de la biostation. Il est rempli à ras bord de liquide vert.<new_line>Une station de pompage y est attachée ; des machines similaires se trouvent sur les petites citernes en périphérie de la cité.]],
				['3'] = [[Un énorme réservoir occupe une grande partie de la biostation. Il est rempli à moitié de liquide vert.<new_line>Une station de pompage y est attachée ; des machines similaires se trouvent sur les petites citernes en périphérie de la cité.]]
			}
		},
		['witch_delivery'] = {
			['choice'] = {
				['1'] = [[Donner les ingrédients]]
			},
			['episode'] = {
				['2'] = [[Après avoir appris que je venais du Monde au-dessus, la sorcière m'a invité à m'asseoir, puis m'a nourri et questionné au sujet des événements par-delà le portail. Elle m'a alors donné les potions pour la taverne avant d'aller vaquer à ses occupations. Le moment est venu pour moi de partir.]],
				['1'] = [[À mon arrivée, une femme d'âge moyen m'a accueilli. Elle ne ressemblait en rien à une sorcière. « Eh bien, que m'apportez-vous ? Qu'est-ce qu'il y a dans la boîte ? Posez-la donc sur la table, ne soyez pas timide.»]]
			}
		},
		['mad_man_base'] = {
			['episode'] = {
				['1'] = [[La clôture de la maison délabrée était maculée de sang. La signature des fous furieux. Ils adorent entasser du bric-à-brac, je devrais trouver des choses intéressantes à l'intérieur. Mais je doute qu'ils partagent facilement leurs petits trésors.]]
			}
		},
		['lair1'] = {
			['episode'] = {
				['1'] = [[Tout était recouvert de toiles d'araignées. Me frayant difficilement un chemin à travers ce linceul purulent, j'ai enfin atteint le cœur de la tanière, où m'attendait un combat farouche contre de redoutables créatures arachnéennes.]]
			}
		},
		['emba_isaev_surgut'] = {
			['episode'] = {
				['9'] = [[Isaev, vous êtes un homme intelligent. Donnez-moi la valise, et repartez.']],
				['6'] = [[Oui. La boîte noire a enregistré le processus de formation de l'objet que vous avez rencontré. Le mycélium dans son entièreté serait, selon toute vraisemblance, un organisme unique. Un peu comme un réseau téléphonique, si vous voulez.<new_line>L'absence d'hôte fixe ne l'entrave pas du tout. Pour nous, c'est un grave problème !']],
				['5'] = [[Quoi ?! Cette chose parlait ?! C'est impossible, c'est du grand n'importe quoi... Vous dites que le nom de Gerin a été mentionné ?<new_line>Nom d'un chien, qu'est-ce que ça veut dire ? Nous mettons fin immédiatement à tout développement plus avancé de cette technologie !']],
				['8'] = [[Nous allons préparer l'arrêt total des recherches, et il y aura encore quelques missions pour vous. Revenez d'ici quelques heures.']],
				['7'] = [[S'il est impossible d'avoir le contrôle total du mycélium, alors il ne faut pas l'utiliser, même partiellement. Nous pouvons travailler avec un fragment de cet organisme mais, tôt ou tard, une autre partie de celui-ci commencera à muter.<new_line>Surviendra un Émissaire comme celui-ci, ou un Cultivateur. Alors, il ne sera absolument plus question d'un quelconque contrôle, même sur notre fragment de mycélium.']],
				['2'] = [[Nous sommes déjà en pourparlers avec les principales factions. Nous avons prévu de partager cette technologie en l'échange de leur aide pour développer un système de sécurité.<new_line>Si ce problème de sécurité devait persister, nous devrions condamner tout le programme et éliminer toute trace de la technologie. Vous voyez donc la gravité des enjeux. Nous ne pouvons prendre aucun risque.']],
				['1'] = [[Nous travaillons d'arrache-pied sur la technologie de végétalisation. Et grâce à l'analyse de l'œuvre scientifique de Gerin, les choses ont beaucoup avancé !<new_line>Il nous reste encore à développer un système de protection contre les activations non autorisées. En fait, c'est là que nous espérons avoir des réponses pour trouver le moyen de résoudre ce problème.']],
				['4'] = [[Il ne reste plus qu'à découvrir ce que cache cette cité des sciences. Veuillez récupérer la boîte noire de la biostation.'<new_line>(Tend un bout de papier avec le code)]],
				['3'] = [[Malheureusement, il n'y a aucune mention de développement dans les documents. C'est un autre laboratoire d'analyse de données.<new_line>Mais ce qui est étonnant, c'est que nous recevons des signaux radio de cette biostation ! Nous n'avons pas pu les déchiffrer, mais il pourrait y avoir quelqu'un là-bas qui continue de travailler.']],
				['12'] = [[Très bien. Tenez, prenez-la et laissez-nous partir.']],
				['11'] = [[Comme l'ont prouvé les scientifiques britanniques, l'argent n'a pas d'odeur, doc. Ne faites pas l'imbécile, je n'ai aucune envie de vous tuer.']],
				['10'] = [[Vous ne comprenez pas ! Cette technologie est instable et dangereuse !']],
				['13'] = [[(chuchote) 'Voilà, l'ami. Il faut partir d'ici, tout de suite. J'ai contacté mes collègues de S.O.V.A., nous allons dans leur ville.<new_line>Nous avons vraiment besoin de votre aide, veuillez vous y rendre dès que possible.']]
			},
			['choice'] = {
				['1'] = [[Quelle sera l'application de cette technologie ?']],
				['4'] = [[Que voulez-vous dire ?']],
				['3'] = [[Pourquoi ? Vous avez de nouvelles données ?']],
				['6'] = [[D'accord. Bonne chance, docteur.']],
				['5'] = [[Aller voir Isaev]]
			}
		},
		['snowmen'] = {
			['episode'] = {
				['1'] = [[Un groupe de bonshommes de neige passent à tabac un pauvre type et lui balance des boules de neige. Le moment est venu de mettre un terme à ce petit jeu cruel.]]
			}
		},
		['guard_leningrad'] = {
			['episode'] = {
				['2'] = [[Qu'est-ce que vous cherchez là-bas ? On aurait besoin d'aide, ici.']],
				['1'] = [[Vous cherchez quelque chose ?']],
				['4'] = [[De nos jours, les survivants se font rares. C'est essentiel de s'entraider. Et vous m'êtes plus utile vivant que mort.']],
				['3'] = [[Écoutez, c'est dangereux du côté de Moscou. Tver y compris. Cette zone était une cible majeure pour l'ennemi, elle a été frappée de plein fouet par les ogives nucléaires. Elle est saturée de radiations !<new_line><new_line>Je viens de penser. Aidez-moi un peu. Je vous aiderai à vous préparer pour ce périple. Je vous donnerai même de l'équipement pour vous remercier. Comme ça, vous ne mourrez pas en posant les pieds là-bas.']],
				['6'] = [[Dans ce cas, partez vers le sud. Il y a un camp de survivants vers le port de Tver. En fait, on a vécu là-bas. D'ailleurs, je connais Svetlana. Elle a sauvé des vies. Elle doit être encore sur place.<new_line><new_line>Prenez votre équipement, utilisez-le judicieusement, et surveillez votre compteur Geiger. La friche radioactive ne fait pas de cadeau.']],
				['5'] = [[Alors, vous êtes mieux préparé ?']]
			},
			['choice'] = {
				['2'] = [[J'ai une chose importante à faire. Je recherche quelqu'un.']],
				['1'] = [[Je voudrais savoir s'il reste un camp de survivants à Tver.']],
				['4'] = [[D'accord. À la prochaine.']],
				['3'] = [[Pourquoi cette question ?']],
				['6'] = [[Oui.']],
				['5'] = [[Pas maintenant.']],
				['7'] = [[Merci.']]
			}
		},
		['bandit_camp_quest_pt2'] = {
			['episode'] = {
				['2'] = [[Quelques jours plus tard, je suis retourné au camp. Les bandits étaient toujours là, avec leurs propres affaires.]],
				['1'] = [[Quelques jours plus tard, je suis retourné au camp des bandits. Ils avaient tenu parole, en laissant simplement un mot :<new_line>'Merci d'avoir coopéré de façon pacifique.']],
				['4'] = [[Devrais-je vérifier ce qu'il se passe dans le camp des bandits ?]],
				['3'] = [[J'étais plus qu'heureux de m'occuper du cas de ces vauriens.]],
				['6'] = [[Du calme. Nous avons pris du retard, mais c'est l'heure du départ. Merci d'avoir coopéré de façon pacifique. Tenez, voici un cadeau en signe de gratitude.'<new_line>Les bandits ont fini de préparer leurs affaires avant de lever rapidement le camp.]],
				['5'] = [[J'ai changé d'avis', m'a lancé le chef des bandits.]]
			},
			['choice'] = {
				['2'] = [[Attaquer]],
				['4'] = [[Partir maintenant]],
				['3'] = [[Attendre le moment convenu]],
				['6'] = [[Ce n'est pas ce qui était convenu']],
				['5'] = [[Ramasser le mot et partir]]
			}
		},
		['newyearEvent_search_easy'] = {
			['episode'] = {
				['2'] = [[Groooooooooh !'<new_line>Nomade sauta de joie, m'arracha les herbes des mains et partit.]],
				['1'] = [[Avec son raffinement habituel, Père Nomade essayait de m'expliquer qu'il avait besoin d'herbes pour une recette de Noël. C'est le Père Négoce qui m'en avait parlé, ayant lui-même tenté d'aider Nomade avant de recevoir un coup sur la tête.<new_line>'Scrunch-scrunch... Aah, groovaah.']],
				['4'] = [[Nomade approuva d'un signe de la tête.]],
				['3'] = [[Après avoir couru un peu, j'ai découvert un énorme bol de pierre, chauffé de tous côtés par une flamme magique. Des gens se tenaient autour. Nomade cuisinait avec ferveur, ajoutant constamment des ingrédients. Finalement, il termina sur un résonnant :<new_line>'Miam-miam !']],
				['6'] = [[Je me suis immédiatement senti mal, et Nomade commença à devenir intelligible dans ses propos.<new_line>'Ah, on dirait que vous êtes trop éloigné de l'énergie verte : vos chakras pancréatiques ne digèrent pas très bien les smoothies hautement spirituels. Merci d'avoir fait ce grand sacrifice pour les autres.']],
				['5'] = [[Nomade plongea sa tête dans la cuve pour boire une généreuse portion de son contenu.<new_line>'Miam-miam !'<new_line>C'est bien beau, mais son estomac de pierre doit être capable de digérer tout et n'importe quoi. L'estomac des humains ordinaires, en revanche, n'a forcément pas le même degré de tolérance. Je suppose qu'il va falloir y goûter moi-même.]],
				['7'] = [[La soupe était finalement un délice de gourmet. Quand les gens virent ma satisfaction, ils se servirent à leur tour. Père Nomade profita du spectacle d'un repas au calme, un sourire béat sur le visage.]]
			},
			['choice'] = {
				['2'] = [[Non, je n'ai rien. Je vais chercher.' (Secouer la tête et partir)]],
				['1'] = [[Tenez.' (Donner les herbes)]],
				['4'] = [[Vous voulez faire manger ça aux gens ?']],
				['3'] = [[Rattraper Nomade]],
				['6'] = [[Et si on goûtait, d'abord ? Ils risquent l'intoxication.']],
				['5'] = [[Goûter la soupe]]
			}
		},
		['newyearEvent_energy_medium'] = {
			['episode'] = {
				['9'] = [[Les décorations ramassées, nous avons jeté les sacs sur nos épaules puis nous sommes dirigés vers la sortie. Cependant, une bande de vauriens nous bloquait le chemin. Une fois à l'écart au coin de la pièce, Négoce chuchota :<new_line>'On peut trouver une autre issue, mais ce sera compliqué : les sacs sont lourds. Mais tu crois peut-être qu'on devrait neutraliser ces types et foncer tout droit ?']],
				['6'] = [[Après la séance de casse de véhicules, nous avons continué à explorer le bloc de glace. La pièce suivante était l'entrepôt de carburant.<new_line>'Excellent, on peut poser des mines ici ! Prends de quoi tout faire péter.'<new_line>Négoce me lança une boîte de feux d'artifice.]],
				['5'] = [[En réalité, un certain nombre d'accès étaient dissimulés dans le bloc de glace, et nous avons emprunté l'un d'eux pour débouler dans un garage avec une variété de véhicules.<new_line>'Prends une masse et casse tout ce que tu trouves !']],
				['8'] = [[Ne te fais pas de bile, l'ami. Ça fait des années que je répands la bonté et les miracles dans tous les foyers. Je sais faire la distinction entre ce qui est bien ou mal. Prends un sac, et jette les décorations dedans.']],
				['7'] = [[Une fois les mines posées, nous avons avancé un peu plus pour arriver dans une salle pleine de décorations de Noël.<new_line>'Oh, quel beau cadeau ! Quelle chance de tomber là-dessus. Mets tout ça dans des sacs.'<new_line>Après quoi, Négoce sortit deux grands sacs rouges.]],
				['2'] = [[Des vauriens. Notre usine exploite Noël et les miracles pour faire de la publicité, et ils n'aiment pas ça. Ce sont nos concurrents directs, il faut donc s'occuper de leur cas. Tu es prêt à restaurer un semblant de justice ?']],
				['1'] = [[Ho-ho-ho, bonjour l'ami ! Notre usine fait face à une quantité de problèmes, et j'ai enfin compris ce qui n'allait pas. C'est la foutue concurrence qui nous met des bâtons dans les roues ! Pourrais-tu m'aider à saboter leurs usines, juste ce qu'il faut ?']],
				['4'] = [[Il fallut marcher pendant des kilomètres avant d'arriver devant un gros bloc de glace. Négoce me souffla quelque chose à l'oreille.<new_line>'Suis-moi. Oh, et prends ce sort de portail avec toi. Si tu estimes que tu vas y rester, lis l'incantation, elle te fera sortir d'ici.']],
				['3'] = [[J'en suis convaincu à 100% ! La concurrence est le bien suprême. Notre civilisation repose sur certains piliers. Et comment savoir qui est le pilier et qui est le parvenu sans se livrer une concurrence féroce ? Soit ils gagnent et prouvent qu'ils ont raison, soit c'est nous ! Alors, tu es prêt ?']],
				['11'] = [[J'ai sorti le rouleau pour lire l'incantation. En me voyant, Négoce a rapidement suivi mon exemple. À la fin du sort, j'ai vu un éclair bleu aveuglant et, quelques secondes plus tard, je me suis retrouvé dans une friche.]],
				['10'] = [[À bonne distance du bloc de glace, en lieu sûr, Négoce sortit le détonateur et le déclencha. Une grande explosion eut lieu.<new_line>'Voilà ! Beau travail, on a vraiment faire une bonne action ! En éliminant nos concurrents directs, on vient d'assurer la prospérité de l'usine !']]
			},
			['choice'] = {
				['9'] = [[Ce n'est pas beau de voler, je crois ?']],
				['6'] = [[S'échapper à travers un portail]],
				['5'] = [[Tout casser avec une masse]],
				['8'] = [[Mettre les décorations dans le sac]],
				['7'] = [[Poser des mines dans la pièce]],
				['2'] = [[Je suis prêt.']],
				['1'] = [[Quelle concurrence ? D'où ils sortent ?']],
				['4'] = [[Suivre Père Négoce]],
				['3'] = [[Ce sont des salauds... Mais c'est sûr qu'ils ont causé les problèmes à l'usine ?']],
				['10'] = [[Trouver une autre issue]]
			}
		},
		['newyearEvent_craft_easy'] = {
			['episode'] = {
				['9'] = [[Il y a eu une légère méprise avec le cadeau, mais j'apprends encore à connaître votre monde, et mes fidèles elfes me manquent vraiment. La prochaine fois, je réfléchirai à mes erreurs pour te faire une meilleure suggestion de cadeau. Mais tu as quand même fait une très bonne action !']],
				['6'] = [[Oh, c'est pour moi ? Qu'est-ce que c'est ?']],
				['5'] = [[Je veux vous présenter le nouveau Père Noël de la région. Il vous apporte un petit cadeau. C'est lui qui s'occupera de la magie et des miracles à venir.']],
				['8'] = [[La femme déchira rapidement le papier cadeau pour découvrir le contenu. 'Oh, c'est pour moi ? Vraiment ? C'est gentil, merci. Je me suis trompée à votre sujet. Je vais réfléchir à votre offre d'emploi.']],
				['7'] = [[La femme déchira rapidement le papier cadeau pour découvrir le contenu.<new_line>'Oh, vous plaisantez ? Merci, hein, mais vous feriez mieux d'y aller avant que mon chien ne se réveille.']],
				['2'] = [[Oh, fascinant ! Que c'est joli... Et si l'on partait ensemble livrer ce cadeau ? Je crois que le destinataire sera ravi de rencontrer l'artisan qui a réalisé cette belle pièce.']],
				['1'] = [[Ho-ho-ho ! Bonjour, l'ami ! J'ai besoin de ton aide. En fait, les gamins ne sont pas les seuls à mériter d'avoir des cadeaux, il y a aussi des adultes. Mais mon usine ne peut pas fabriquer de quoi satisfaire leurs besoins. Je n'ai ni les plans, ni les matériaux. Pourrais-tu fabriquer quelque chose ?']],
				['4'] = [[Une femme ouvrit la porte.<new_line>'Qui est-ce ? Oh, mais... Je vous ai déjà dit cent fois que je n'étais pas intéressée par votre usine !']],
				['3'] = [[J'ai pris le cadeau, et nous avons marché jusqu'à l'une des maisons du village. Négoce frappa à la porte.<new_line>'Ho-ho-ho !']]
			},
			['choice'] = {
				['2'] = [[Je vais voir ce que je peux faire.' (Partir)]],
				['1'] = [[Tenez, prenez ça.']],
				['4'] = [[Non, merci. Je dois partir.' (Partir)]],
				['3'] = [[Très bien, c'est parti.']],
				['6'] = [[Sortir de la maison]],
				['5'] = [[Donner le cadeau]]
			}
		},
		['embacity_biolab_5'] = {
			['episode'] = {
				['2'] = [[Oui... C'est bien ce que je pensais. Vous n'avez même pas tenté de saisir la nouvelle réalité. Vous n'avez tout simplement pas entendu la voix. Pas même Kourganov, et il était pourtant disposé à faire beaucoup.']],
				['1'] = [[Tiens, tiens, mais qu'avons-nous là ? Un autre sujet de recherche est arrivé. Excellent. Rejoignez mes collègues. J'imagine que vous êtes doué d'une stupidité sans borne, vous aussi, et que vous êtes incapables de voir l'évidence même.']],
				['4'] = [[En faisant le tour de la pièce, j'ai trouvé une porte discrète avec une serrure à code. Voilà donc l'utilité du code.]],
				['3'] = [[Une armée ? Non, ces absurdités militaires appartiennent au passé. Vous ne semblez tout simplement pas capable de comprendre l'avenir. Qu'importe. Un peu de patience, je vais vous arranger... Tâchez de ne pas résister, si possible.']]
			},
			['choice'] = {
				['2'] = [[Vous levez une armée, vous aussi ?']],
				['1'] = [[Qu'avez-vous fait ?!']]
			}
		},
		['scientist_rescue_bar'] = {
			['episode'] = {
				['2'] = [['Sain et sauf ? Excellent ! Sachez que vous avez sauvé la vie d'un homme bon. Et merci aussi pour le dosimètre, c'est un modèle rare. Voici un petit plus pour ce travail de qualité.']],
				['1'] = [['Sain et sauf ? Excellent ! Sachez que vous avez sauvé la vie d'un homme bon. Voici ce qu'on vous doit.']],
				['3'] = [['Sain et sauf ? Excellent ! Sachez que vous avez sauvé la vie d'un homme bon. Et on m'a déjà fait part de vos services sur le plan médical, alors voici un petit supplément. Ici, on apprécie ceux qui prennent soin de nous.']]
			}
		},
		['construction_team_pests'] = {
			['episode'] = {
				['1'] = [[Voici l'endroit que le contremaître a marqué sur la carte. Inutile de fouiller les terriers, les vermines viennent déjà ramper à ma rencontre.]]
			}
		},
		['destroy_lair_quest'] = {
			['episode'] = {
				['15'] = [[J'ai exterminé le nid d'insectes mutants.]],
				['16'] = [[J'ai massacré les mutants du coin. Ils ne causeront plus de problèmes.]],
				['5'] = [[En m'écartant de quelques pas du lieu indiqué, je suis tombé sur un nid d'abeilles mutantes.]],
				['9'] = [[Les coordonnées m'ont guidé dans les profondeurs de la forêt. Avant de pouvoir inspecter la zone, j'ai été attaqué par des créatures mutantes.]],
				['2'] = [[Je suis arrivé au lieu indiqué. Au bout de quelques minutes, une meute de loups m'a attaqué.]],
				['1'] = [[Dès mon arrivée sur place, je suis tombé sur l'antre d'un ours.]],
				['4'] = [[En arrivant à l'endroit indiqué, j'ai aperçu des scorpions.]],
				['3'] = [[J'ai croisé un nid de sangliers dans les bois. Vraisemblablement, j'étais au bon endroit.]],
				['12'] = [[Cette meute de loups ne causera plus d'ennuis.]],
				['11'] = [[J'ai neutralisé les ours, ils ne blesseront personne.]],
				['14'] = [[J'ai supprimé le nid de scorpions.]],
				['13'] = [[Le nid est détruit. Je peux retourner à la base.]]
			},
			['choice'] = {
				['3'] = [[Se défendre]]
			}
		},
		['newyearEvent_def_medium'] = {
			['episode'] = {
				['9'] = [[Tu n'as pas fait assez d'efforts. Si tu avais vraiment tout essayé, tu serais en train de me jeter un sort en ce moment, et non l'inverse. Bon, laisse-moi te montrer un autre truc. Si tu suis mon exemple en travaillant dur, tu prendras peut-être le coup de main.<new_line>Je peux téléporter nos ennemis avec nous, où je veux, pour les combattre plus facilement. Mais tu choisis la destination.']],
				['6'] = [[Tu en restes bouche bée, hein ? Attention à ne pas avaler une mouche. Choisis plutôt le sort que tu veux que je lance sur toi.']],
				['5'] = [[Tu as bien de la chance, aujourd'hui, imbécile. Et tu peux remercier Cyber Noël, crois-moi. Tu as la tête ailleurs ? Tu n'as pas vu l'ennemi arriver ?'<new_line><new_line>En regardant au loin, j'ai pu constater que c'était le cas.]],
				['8'] = [[Tu n'es pas irrécupérable, finalement. C'est vrai, ce qu'on dit : Noël est faiseur de miracles.']],
				['7'] = [[Abracadabra, et voilà. J'espère au moins que tu auras appris quelque chose et que ma magie ne sera pas gaspillée en faisant n'importe quoi.'<new_line><new_line>C'est à ce moment que les vauriens sont entrés dans le périmètre pour semer la zizanie.]],
				['2'] = [[Parfait ! Alors dirige-toi vers le périmètre. Ton partenaire t'attend là-bas.']],
				['1'] = [[Ça va, choom ? Je vais jouer à un gros concert, mais on m'a filé le tuyau que des indésirables allaient se taper l'incruste.<new_line>Il faut leur tomber dessus, si jamais ils pointent le bout de leur pif. Tu veux bien m'aider à sécuriser l'événement ?']],
				['4'] = [[J'ai regardé autour de moi, et toujours aucune trace de mon partenaire. Mais au moment de tourner les talons pour rejoindre Cyber Noël, Père Mago est apparu derrière moi.]],
				['3'] = [[Il y a une foule énorme au concert, les gens sont serrés comme des sardines. J'ai réussi à me frayer un passage vers le périmètre, mais je ne vois mon partenaire nulle part.]],
				['12'] = [[Les chooms ! Le concert était top ! Merci d'avoir géré ces cyberpsychopathes.']],
				['11'] = [[Comme d'habitude, Père Mago me jeta un sort. Quelques minutes après, l'ennemi surgit à l'horizon, et Mago commença à réciter une incantation complexe.<new_line>'Valar morghulis, ommm... alarmis.'<new_line><new_line>Après quoi, il y eut un éclair blanc qui nous transporta tout à fait ailleurs, avec nos ennemis.]],
				['10'] = [[Je suis tellement fort qu'il me reste encore un peu de magie pour te faire bénéficier d'un sort. Choisis celui que tu veux.']],
				['13'] = [[En augmentant son propre niveau de maîtrise, plus besoin de demander la protection d'autrui ! Il faut travailler sur soi-même, au lieu de faire des concerts.']]
			},
			['choice'] = {
				['9'] = [[Vers les marais impénétrables.]],
				['6'] = [[J'ai essayé.']],
				['5'] = [[Je veux voler comme un oiseau.']],
				['8'] = [[Vers l'avant-poste sécurisé.]],
				['7'] = [[Vers le dépôt de carburant.]],
				['2'] = [[Aller vers le périmètre]],
				['1'] = [[Pas de problème.']],
				['4'] = [[Je vois.']],
				['3'] = [[C'est vous, mon partenaire ?']],
				['12'] = [[De rien.']],
				['11'] = [[Attaquer.]],
				['10'] = [[Vers la forêt touffue.]]
			}
		},
		['set_up_devices_bar'] = {
			['episode'] = {
				['1'] = [[C'est fait ? Excellent ! Ne soyez pas étonné, de nombreuses recherches scientifiques avancent grâce à ce genre de petites études.']]
			}
		},
		['secret_documents'] = {
			['episode'] = {
				['9'] = [[Je suis arrivé au bâtiment correspondant aux coordonnées que les archivistes m'ont données. La porte est verrouillée. On dirait qu'il va falloir des explosifs pour y remédier.]],
				['6'] = [[Ces gens n'étaient pas d'humeur à discuter. Je suis ressorti en courant pour rappeler notre accord à leur chef, mais il n'y avait plus personne.<new_line>Les personnes du laboratoire me suivent déjà dehors.]],
				['5'] = [[Ces gens n'étaient pas d'humeur à discuter. Ce n'est pas agréable de vivre une fusillade dans un laboratoire, alors je suis sorti en courant.<new_line>Les maraudeurs m'ont suivi.]],
				['8'] = [[J'ai récupéré les documents secrets et je dois les livrer à la base de Progress afin d'aider le Biologiste dans ses recherches.]],
				['7'] = [[La voie est libre. J'ai continué à fouiller le laboratoire et j'ai fini par trouver un coffre portant le sigle EMBA.]],
				['2'] = [[Alors, qu'allons-nous faire ? Nous étions là en premier. Si vous nous payez la même chose, nous vous laisserons ces ruines. Sinon, on peut se montrer convaincants, mais ce sera moins agréable.']],
				['1'] = [[J'ai découvert un groupe de personnes à l'entrée du bâtiment. Leur chef m'a écouté et a secoué la tête.<new_line>'Vous ne comprenez pas, nous ne sommes pas venus pour nous promener, mais pour trouver des informations rares que des personnes importantes demandent.']],
				['4'] = [[Le laboratoire n'occupait qu'une petite partie du bâtiment. Il servait sûrement à stocker les documents d'archives.<new_line>Alors que j'étais en train d'explorer un autre recoin, j'ai entendu des voix humaines.]],
				['3'] = [[La voie est libre, je peux fouiller le laboratoire.]],
				['11'] = [[J'ai attiré les insectes vers la surface, mais ils ne voulaient pas quitter leur nid et petit à petit, ils s'y sont réinstallés.]],
				['10'] = [[Le laboratoire n'occupait qu'une petite partie du bâtiment. Il servait sûrement à stocker les documents d'archives.<new_line>Alors que j'étais en train d'explorer un autre recoin, je suis tombé sur un nid d'insectes.]]
			},
			['choice'] = {
				['2'] = [[Fouiller le laboratoire]],
				['1'] = [[Moi aussi']],
				['4'] = [[Retourner à la base de Progress]],
				['3'] = [[Utiliser un outil pour ouvrir]],
				['6'] = [[Attirer les insectes à la surface]],
				['5'] = [[Placer les explosifs]]
			}
		},
		['science_polygon'] = {
			['episode'] = {
				['2'] = [[J'ai une idée. Nous avons plusieurs barils de carburant stockés ici. Si vous les faites rouler sur le champ de test et que vous verser un sac de substrat de mycélium autour, les biomutants sont sûrs de mordre à l'hameçon.<new_line>Ça les attire comme des aimants. Et c'est à ce moment-là que vous les faites griller.']],
				['1'] = [[Un homme de la cabane blindée est venu à ma rencontre.<new_line>'Bonjour, camarade ! Je suis ravi que vous ayez pris le temps de venir. Satanés biomutants. Mais vous pouvez leur régler leur compte, pas vrai ?']],
				['4'] = [[Vous nous avez vraiment dépannés, camarade ! Voici votre récompense.']],
				['3'] = [[J'ai fait rouler les barils et j'ai versé la substance verdâtre à l'odeur de moisissure entre eux.<new_line><new_line>Il n'aura fallu que cinq minutes pour que les biomutants apparaissent et commencent à manger la biomasse.]]
			},
			['choice'] = {
				['1'] = [[Sortir les barils]]
			}
		},
		['provision_bar'] = {
			['episode'] = {
				['1'] = [[Super, cette expédition nous donne beaucoup d'espoir. Vos contributions nous aideront à obtenir d'importantes informations sur le projet EMBA. Merci.']]
			}
		},
		['intelligence_bar'] = {
			['episode'] = {
				['1'] = [[Vous avez les données de reconnaissance ? Excellent, elles contiennent des informations d'une grande importance sur le projet EMBA.']]
			}
		},
		['emba_various_hard'] = {
			['episode'] = {
				['19'] = [[Voilà ! J'ai aussi rendu visite à quelques personnes, ça devrait suffire pour le rapport. Merci beaucoup, je ne voulais pas avoir la honte devant le Dr Isaev.']],
				['5'] = [[Il y a un réalisateur qui fait des films sur les mutants. Il m'a même interviewé, vous imaginez ? En gros, il a recueilli plein d'observations utiles, demandez-lui de vous en parler.']],
				['16'] = [[J'ai payé les gars pour les remercier de leur aide.<new_line>Je dois maintenant retourner voir le Biologiste et lui demander si je dois interroger d'autres personnes.']],
				['15'] = [[Vous voulez que je reparle au bigleux ? Non, j'ai un raid à faire.<new_line>Enfin... si vous combattez à la place d'un de mes soldats, je peux l'envoyer discuter avec les scientifiques.']],
				['9'] = [[Vous quoi ? Vous parler du mycélium ? Vous voulez peut-être aussi les clés de mon appartement où j'ai laissé mon argent ?<new_line>Bien, apportez-moi des ingrédients à distiller et je vous dirai tout.']],
				['17'] = [[Oui, c'est arrivé. Nous avons abandonné la serre assez vite, parce que ça nous aurait coûté cher de tenter quoi que ce soit. Je peux vous faire visiter, mais je n'irai pas sans protection. Il nous faut une sorte de filtre pour respirer.']],
				['18'] = [[J'ai pris des notes sur tout ce que j'ai vu dans la serre qui me semblait important, et j'ai même fait des croquis du mycélium.<new_line>Je dois maintenant retourner voir le Biologiste et lui demander si je dois interroger d'autres personnes.]],
				['6'] = [[Il y a un docteur pas loin d'ici qui a organisé un hôpital de campagne. Il soigne les personnes qui ont été infectées par les spores de mycélium. Il devrait avoir une tonne d'informations utiles sur la progression de la maladie.<new_line>L'argent ne l'intéresse pas, donc il devrait nous aider.']],
				['10'] = [[Tenez, prenez ce carnet, j'y ai noté toutes les étapes pour le cultiver ici. Et dites au blanc-bec de me rapporter le carnet quand il aura fini. Sinon, je lui ferai... une injection dans le derrière !']],
				['8'] = [[Regardez, il y a une ferme derrière ce champ. J'ai entendu dire que du mycélium avait recouvert les cultures. Vous devriez interroger les gens là-bas, ils vous montreront peut-être même les plantes infectées.']],
				['7'] = [[J'ai parlé au chef des combattants près du camp voisin. Je ne sais pas s'il acceptera de répéter ce qu'il m'a dit, il n'était pas très bavard. Mais essayez quand même.']],
				['2'] = [[Eh bien, j'ai dû aller interroger toutes sortes de personnes et visiter différents endroits... Ça m'a pris un temps fou. Je ne peux pas refaire tout ça tout seul.<new_line>Aidez-moi d'abord à obtenir des corps de biomutants pour une autopsie. Il me faut des infos de première main, si je puis dire.']],
				['1'] = [[Bonjour ! Comment ça va ? Je suis dans le pétrin. J'ai perdu mes rapports de recherche. Le Dr Isaev m'a chargé de recueillir des informations sur les biomutants et le mycélium de sources externes.']],
				['4'] = [[Mes collègues et moi avons rendu visite à un vieil homme... pour les affaires. Il était très intelligent ! Il cultivait presque du mycélium dans son cellier.<new_line>En gros, il nous a raconté plein de trucs. Je me souviens de presque tout, mais il était réticent à parler du mycélium. Vous devriez essayer de lui parler.']],
				['3'] = [[Je vais m'occuper des autopsies pendant que vous interrogez les gens, d'accord ?']],
				['12'] = [[Luttant pour rester éveillé, j'ai pris des notes sur ce qui me semblait le plus important.<new_line>Je dois maintenant retourner voir le Biologiste et lui demander si je dois interroger d'autres personnes.]],
				['11'] = [[Vous en parler ? Vous croyez que je suis conférencier ou quoi ? Tout est dans mes films ! Asseyez-vous, je vais vous montrer les deux derniers épisodes.']],
				['14'] = [[J'ai fini de transporter les blessés et j'ai pris la boîte des dossiers de patients.<new_line>Je dois maintenant retourner voir le Biologiste et lui demander si je dois interroger d'autres personnes.]],
				['13'] = [[Oh, vous voulez dire Alexander ? Oui, je le connais. Je peux vous donner les dossiers médicaux de mes anciens patients. Mais j'ai aussi besoin d'aide. Le camp a été attaqué et les blessés doivent être transportés à mon hôpital. Pouvez-vous vous en charger ?']]
			},
			['choice'] = {
				['9'] = [[Regarder le film]],
				['6'] = [[Aller voir le chef]],
				['5'] = [[Aller voir le docteur]],
				['8'] = [[Retourner voir le biologiste]],
				['7'] = [[Aller voir les fermiers]],
				['2'] = [[Bien. Je vais aller tirer sur ceux qui sont là-bas.' (combattre)]],
				['1'] = [[Quelles sont ces sources ?']],
				['4'] = [[Aller voir le réalisateur]],
				['3'] = [[Où dois-je aller ?']],
				['11'] = [[Accepter (combattre)]],
				['10'] = [[Transporter les blessés]]
			}
		},
		['emba_time_medium2'] = {
			['episode'] = {
				['9'] = [[Je me concentre sur le troisième parasite. Il n'a pas l'air pressé d'aller ailleurs, j'ai donc le temps de me préparer.]],
				['10'] = [[Vous avez tout noté ? Bien. Ces deux parasites ont amené plusieurs charognards à leur groupe.<new_line>Ils sont vraiment malins...']],
				['6'] = [[Ce sont les plus intelligents de tous. Ils ne sont pas tant infectés par le mycélium. C'est sûrement eux qui ont réussi à résister le plus longtemps à son influence. Enfin, ça reste des monstres.<new_line>En plus, ce sont les plus rusés et dangereux. Ils n'ont pas oublié comment utiliser les armes humaines. Au moins, ils ne vous infecteront pas, vu qu'ils ne crachent pas de spores.']],
				['5'] = [[J'ai suivi les broussailleux et j'ai noté ce qu'ils faisaient quand ils se sont arrêtés. Ils ont fini par rejoindre le groupe.<new_line><new_line>'Hmm, ils sont revenus. Ils sont partis explorer la zone ? Ou chercher à manger ? Bref, j'analyserai ça plus tard. Ceux-là sont des parasites, vous voyez ?']],
				['8'] = [[La chasseuse a émis un petit rire de doute.<new_line>'J'espère que votre assurance ne vous transformera pas en repas pour eux. Nous allons traquer les parasites.<new_line>Leur comportement est assez complexe et nous devons rester vigilants. Je vais suivre ces deux-là. Essayez de ne pas perdre l'autre avec le fusil d'assaut.']],
				['7'] = [[Et maintenant, observons ces parasites.<new_line>Leur comportement est complexe et nous devons être très prudents. Je vais suivre ces deux-là. Essayez de ne pas perdre l'autre avec le fusil d'assaut.']],
				['2'] = [[J'observe les déplacements des biomutants. J'essaie de comprendre la logique de leurs migrations. Je veux juste comprendre s'il y a des espèces sensibles parmi eux.<new_line>Il me faut un partenaire pour ne rien rater.']],
				['1'] = [[Hé, venez ici. Je vous ai vu poursuivre les biomutants. Je me dis que je pourrais vous emmener en mission.']],
				['4'] = [[Les broussailleux se déplacent lentement pour l'instant. Je peux les suivre ou vaquer à mes occupations sans risquer de perdre leur trace.]],
				['3'] = [[Excellent, allons-y. Vous voyez ce groupe là-bas ? Les plus en friche sont les broussailleux. Ça m'étonnerait que ce soient des êtres sensibles, leurs corps sont trop consommés par le mycélium. Regardez, ils se sont séparés du groupe.<new_line>Suivez-les. S'ils attaquent, n'oubliez pas qu'en mourant, ils libèrent un nuage de spores qui affecte tout le monde. Alors, gardez vos distances en combat.']],
				['12'] = [[Super. Bien, laissons ça là maintenant, éloignez-vous et observez ce que fera ce varan. J'ai mis un fil-piège là-bas.']],
				['11'] = [[Oui, les charognards sont les plus intéressants. Le mycélium les a transformés en simples animaux. Ils sont plus forts et plus intelligents, beaucoup ont même des capacités hors du commun.<new_line>D'ailleurs... appâtons-en un. Je veux étudier leur alimentation. Je veux savoir quels légumes ils mangent. Allez en trouver, je vous attends ici.']],
				['14'] = [[Merde, il est parti ! Tant pis, un jour... un autre jour. Et merci du coup de main. Vous êtes libre de partir. Nos observations m'ont donné à réfléchir.']],
				['13'] = [[Le charognard s'est approché doucement, a coassé avant de lâcher un nuage de spores de sa gueule en direction du légume. Il s'en est ensuite approché.<new_line>Il a déclenché le fil-piège, il y a eu un bruit sec puis un nuage de fumée blanche. Cependant, il semble que cette fumée n'ait aucun effet sur le varan quand elle est mélangée aux spores.<new_line>Une fois la fumée et les spores dissipées, le charognard avait disparu.]]
			},
			['choice'] = {
				['9'] = [[Me déplacer et observer]],
				['6'] = [[Compris.']],
				['5'] = [[Oui, je sais pas mal de choses.']],
				['8'] = [[On regarde les charognards maintenant ?']],
				['7'] = [[Sortir les carnets d'observation]],
				['2'] = [[Suivre les broussailleux]],
				['1'] = [[En quoi consiste le boulot ?']],
				['4'] = [[Qu'est-ce que vous savez sur eux ?']],
				['3'] = [[Poursuivre les broussailleux]]
			}
		},
		['emba_time_medium'] = {
			['episode'] = {
				['2'] = [[Pas une 'sorte de film', mais un super film. Regardez un peu l'époque qu'on vit ! Vous pensez que les gens n'ont besoin que de vaccins et d'injecteurs ? Vous les sous-estimez ! C'est important pour eux de comprendre ce qu'il se passe, et pas en lisant un article rasoir, mais grâce au travail d'un artiste. Comme moi. Et vous, si vous m'aidez.']],
				['1'] = [[Bien le bonjour, camarade ! Voulez-vous contribuer à l'éducation scientifique de la nation ? Vous devrez travailler comme cadreur pendant que je filme des scènes panoramiques.']],
				['4'] = [[Coupez ! Maintenant, allez là-bas et filmez la route. Vous voyez ce qu'ils font ? Ils ont trouvé une carcasse de vache et ils la mangent. Montrons aux gens les trucs intéressants. Action !']],
				['3'] = [[Voilà, tenez-la comme ça et appuyez ici. Regardez, il y a des biomutants dans la clairière. Filmez ce qu'ils font. Mais ne vous en approchez pas. Allez, j'ai besoin d'images.']],
				['6'] = [[Vous me remercierez de vous avoir mis dans une scène ! On commence ?']],
				['5'] = [[Coupez ! Il nous faut maintenant le plus important. La scène de combat ! Je vous ai même trouvé des extras, mais attention, mon budget est limité.']],
				['7'] = [[Coupez ! Bravo, tout le monde ! Encore une douzaine de scènes comme ça et on aura quelque chose de super. J'ai des tonnes d'idées, il se passe tellement de trucs autour de nous !<new_line>Vous avez vraiment fait du bon travail. Je vous appellerai pour filmer le prochain épisode. Vous rentrerez dans l'histoire du monde du cinéma, s'il reste un monde.']]
			},
			['choice'] = {
				['2'] = [[D'accord, montrez-moi où appuyer.']],
				['1'] = [[Une sorte de film ?']],
				['4'] = [[De combat ? C'est quoi votre film ?']],
				['3'] = [[Commencer à filmer]],
				['5'] = [[D'accord, je suis prêt.']]
			}
		},
		['easter_buyer'] = {
			['episode'] = {
				['2'] = [[Je ne sais pas de quoi vous parlez. Est-ce que vous voulez vendre quelque chose ?']],
				['1'] = [[Bien le bonjour ! Besoin d'aide ?']],
				['4'] = [[Du calme, du calme, je plaisantais ! Il ne faut pas s'énerver comme ça ! Tiens ! La voilà, ta récompense.']],
				['3'] = [[C'est la première fois de ma vie que je vous vois ! Si vous n'avez rien à vendre, passez votre chemin.']]
			},
			['choice'] = {
				['2'] = [[On avait conclu un marché... Besoin que je te rafraîchisse la mémoire ?' (Poser votre main sur votre arme.)]],
				['1'] = [[Je suis là pour la récompense.']],
				['3'] = [[On avait conclu un marché...']]
			}
		},
		['march_skeletons'] = {
			['choice'] = {
				['9'] = [[Dans le sens horaire]],
				['10'] = [[Dans le sens antihoraire]],
				['6'] = [[Sel]],
				['5'] = [[Cendre]],
				['8'] = [[De l'ouest vers l'est]],
				['7'] = [[De l'est vers l'ouest]],
				['2'] = [[Les jambes vers l'intérieur]],
				['1'] = [[Les jambes vers l'extérieur]],
				['4'] = [[Terre]],
				['3'] = [[De la tête aux pieds]],
				['12'] = [[Tetridanok]],
				['11'] = [[Tetriandok]],
				['14'] = [[Envoyer sur le chemin]],
				['13'] = [[Teriantrok]]
			},
			['episode'] = {
				['2'] = [[Une fois les squelettes positionnés en cercle, je dois saupoudrer les crânes de...]],
				['1'] = [[Eh bien, j'ai les squelettes et le parchemin. Je suis au bon endroit. J'imagine que je peux commencer.<new_line><new_line>Je dois positionner les squelettes en cercle.]],
				['4'] = [[Pour terminer le rituel, je dois invoquer l'esprit. Insuffle la vie à ces os, ô Grand...]],
				['3'] = [[Pendant que je saupoudre, je devrais faire le tour du cercle trois fois...]],
				['6'] = [[Plusieurs squelettes ont commencé à bouger et m'ont attaqué. Apparemment, j'ai commis des erreurs.]],
				['5'] = [[Dès que j'ai terminé, un vent glacial s'est levé et les squelettes se sont dressés dans d'atroces claquements d'os, formant un cercle autour de moi. Les voilà prêts à recevoir mes ordres.]]
			}
		},
		['mirror_lady'] = {
			['episode'] = {
				['2'] = [[Après le combat, j'ai commencé à fouiller la maison, espérant y trouver des indices sur l'endroit où Igor aurait pu aller. La marionnette en céramique assise sur le buffet ne me disait rien qui vaille. J'avais la désagréable sensation qu'elle observait mes moindres faits et gestes. Lorsque je m'en suis approché pour l'examiner de plus près, la marionnette m'a frappé en plein visage. Elle a alors bondi par terre et, portée par ses jambes et ses pieds lilliputiens, elle a pris la fuite vers une destination inconnue.]],
				['1'] = [[La maison entière regorgeait de miroirs. Comme le tavernier l'avait indiqué, j'ai allumé une bougie devant le miroir et murmuré le nom de Marie la Sanglante. Au début, des relents nauséabonds ont empli la pièce, puis j'ai entendu des bruits de pas et une voix douce... - Bien, bien, bien, qu'avons-nous là ?]]
			}
		},
		['pumpkin_king'] = {
			['choice'] = {
				['1'] = [[Continuer à éventrer des citrouilles]]
			},
			['episode'] = {
				['2'] = [[Les sbires du roi gisent sur le sol, sans vie. Quant au monarque, il m'observe du coin de l'œil. J'ai recommencé à éventrer des citrouilles, et l'effet escompté ne s'est pas fait attendre très longtemps : de nouveaux disciples orangés surgissent de la brume.]],
				['1'] = [[J'ai suivi les instructions à la lettre, je me suis placé au centre du cercle. J'ai ensuite commencé à éventrer les citrouilles une par une, déversant leurs entrailles sur le sol fangeux. Après quelques secondes, un bruit sourd, presque imperceptible, résonne dans l'épaisse brume. Soudain, je remarque que s'élèvent dans les airs quelques-unes des citrouilles d'Halloween que j'ai posées sur le cercle. Au-dessous de ces courges grimaçantes se matérialisent des corps difformes.]],
				['4'] = [[Après avoir étouffé les flammes orange qui brûlaient farouchement dans la tête de citrouille du dernier sbire, je me débarrasse de ses viscères végétaux dont mon visage est maculé. Le sol, mon arme et mes vêtements sont recouverts des tripes, du jus malodorant et des graines des suppôts du monarque.]],
				['3'] = [[Après avoir remporté la victoire, j'ai entendu un hurlement mâtiné de surprise et de colère. Il semble que j'aie attiré l'attention de Jack. Je dois continuer sur ma lancée !]],
				['5'] = [[J'ai déchiqueté quelques citrouilles de plus. Deux grandes flammes illuminant le cercle sont apparues. Il m'est impossible de détacher le regard de ces flammes qui dansent frénétiquement dans les yeux du grand et majestueux roi-citrouille. Je brandis mon arme, prêt à me battre.]]
			}
		},
		['ambush_quest'] = {
			['episode'] = {
				['22'] = [[Je m'approche de l'endroit que le barman a indiqué sur la carte.]],
				['20'] = [[Pendant que je fouillais les corps, les autres ont sorti la marchandise des voitures.]],
				['16'] = [[Nous avons réussi à les repousser, malgré quelques blessés graves parmi nos hommes.]],
				['15'] = [[Je vois. Très bien, prenez la marchandise.'<new_line>L'homme a rendu la marchandise et sa bande est repartie.]],
				['9'] = [[Deux voitures se sont arrêtées près de l'arbre mort. Des types sont sortis dégager le passage. En voyant que c'était une embuscade, ils ont dégainé leurs armes.<new_line>'Vous voulez quoi ?']],
				['17'] = [[Grâce à ma trousse de secours, nous pourrons soigner les blessés en rentrant à la base.]],
				['18'] = [[Vous avez du cran, hein ?'<new_line>Tous les autres bandits sont sortis des voitures. Ils étaient plus nombreux que prévu.<new_line>]],
				['6'] = [[J'ai attaché des pieux au rondin.]],
				['5'] = [[J'ai réussi à chasser les bêtes. Mon travail ici est terminé.]],
				['8'] = [[J'ai trouvé l'endroit idéal pour creuser un piège à loup, vers l'arbre mort.]],
				['19'] = [[Vous vous en sortez bien ! Maintenant, du balai. Je ne veux plus vous voir par ici.']],
				['2'] = [[Je suis arrivé sur place et j'ai trouvé des cadavres.]],
				['1'] = [[En arrivant à destination, j'ai vu un groupe d'individus près d'un arbre mort.<new_line>'Vous venez nous aider ? Ne restez pas planté là. Au travail !']],
				['4'] = [[Attirées par l'odeur, des bêtes se sont déjà ruées vers les cadavres.]],
				['3'] = [[Je n'ai rien trouvé d'intéressant.]],
				['12'] = [[Posez vos armes ou vous allez le regretter.']],
				['11'] = [[L'homme m'a gardé en joue.<new_line>'Nous sommes de simples marchands. Nous voulons éviter les ennuis.']],
				['14'] = [[L'homme s'est mis à sourire alors que d'autres sbires armés sont descendus des véhicules.<new_line>'On ne s'en ira pas si gentiment.']],
				['13'] = [[C'est une menace ?'<new_line>Des hommes de main sont sortis des voitures.]]
			},
			['choice'] = {
				['17'] = [[Cette marchandise a plus de valeur que votre vie ?']],
				['6'] = [[Essayer autre chose]],
				['5'] = [[Accrocher un rondin avec des pieux]],
				['32'] = [[Baisser votre arme]],
				['7'] = [[Accrocher un rondin]],
				['2'] = [[Partir]],
				['1'] = [[Fouiller les cadavres]],
				['4'] = [[Creuser un piège à loup]],
				['3'] = [[Rejoindre le combat]],
				['12'] = [[Rentrer à la base]],
				['11'] = [[Ce serait dommage de mourir pour de la marchandise']],
				['30'] = [[Continuer d'avancer]],
				['10'] = [[Donnez la marchandise et personne ne sera blessé']]
			}
		},
		['emba_search_easy3'] = {
			['episode'] = {
				['2'] = [[Je n'ai pas réussi à arracher les pousses : même si elles étaient encore jeunes, leurs racines étaient déjà trop profondément en terre.<new_line>Tout ce que je peux faire pour l'instant, c'est brûler cette pousse.]],
				['1'] = [[J'ai remarqué de jeunes pousses étranges par terre. C'était cette nouvelle plante dont se nourrissent les biomutants. Comme si on n'avait pas déjà assez de soucis...]],
				['3'] = [[Bon, au moins ça ralentira sa croissance et ça réduira la source d'alimentation des biomutants.<new_line>Je vais devoir parler de cet endroit aux scientifiques, ils voudront sûrement étudier le développement de ces plantes après un tel stress.]]
			},
			['choice'] = {
				['5'] = [[La brûler]],
				['1'] = [[Essayer de l'arracher]]
			}
		},
		['emba_search_easy2'] = {
			['episode'] = {
				['2'] = [[Avez-vous cueilli les échantillons de l'herbier ? Nous sommes impatients de commencer l'expérience !'<new_line>]],
				['1'] = [[Bonjour ! Voulez-vous aider la science ? Nous étudions l'interaction du mycélium avec les plantes.<new_line>Pouvez-vous nous rapporter des échantillons de la flore locale ?']],
				['3'] = [[Le scientifique a pris les plantes avant de grimper dans le véhicule tout-terrain. Il est revenu après quelque temps.<new_line><new_line>'C'est prometteur, camarade ! Le mycélium a commencé une interaction active dès qu'il a été mis en contact avec des tissus étrangers.<new_line>Bon, je retourne à mes observations ! J'espère retravailler avec vous !']]
			}
		},
		['emba_fight_hard'] = {
			['episode'] = {
				['9'] = [[Ce n'était qu'une partie de la horde. Je vais orienter ceux qui ne se sont pas battus vers le lieu que la chasseuse a indiqué.]],
				['6'] = [[Vous êtes aussi intelligent, quelle trouvaille ! Bien, professeur, je vais sur les lieux du piège en accédant par l'autre côté. Avancez vers le groupe de biomutants dans cinq à dix minutes.']],
				['5'] = [[C'est plus facile avec celui-là. C'est une sorte de réservoir organique avec une réserve de biomasse. Ça aide les mutants à absorber les spores.<new_line>Au fait, si vous êtes à côté quand il explose, ne vous en faites pas. Même si sa substance visqueuse n'est pas super agréable au toucher.<new_line>Mais si vous en êtes recouvert, ça absorbera quand même l'effet des spores. C'est positif pour vous, parce que ça les aspire ou un truc du genre.']],
				['8'] = [[J'ai assez attendu, je peux commencer à attaquer la horde de biomutants.]],
				['7'] = [[Je l'espère vraiment. Je vais sur les lieux du piège en accédant par l'autre côté. Avancez vers le groupe de biomutants dans cinq à dix minutes.']],
				['2'] = [[C'est facile, même pour un amateur. Progress a besoin de mutants en vie. Vous devez les guider vers un piège que j'ai posé. Ça ne devrait pas être trop difficile.<new_line>Quand on sera en place, on finira le reste de la horde.']],
				['1'] = [[Hé, attendez un peu. Vous vous débrouillez bien avec les armes. J'ai un boulot pour vous.']],
				['4'] = [[Ce sont des organismes très intéressants. En fait, ce n'est pas vraiment clair s'ils sont vivants ou non. Un Nid est une sorte de colonie de mycélium. Des broussailleux en sortent de temps à autre.<new_line>Pour le moment, j'essaie de savoir s'ils attendent en état d'inertie ou s'ils se régénèrent. C'est aussi possible que la colonie leur donne naissance.']],
				['3'] = [[Si je ne réfléchissais pas à tous les cas de figure, je serais morte et j'aurais perdu les deux yeux.<new_line>D'ailleurs, en parlant de vigilance, une meute pourrait traîner autour d'objets de ce type... Sans utiliser les noms latins, vous avez peut-être entendu parler du 'Nid' et de la 'Cloque' ?<new_line>Je vous conseille de vous occuper d'eux d'abord en combat.']],
				['11'] = [[Pas mal. Ça s'est avéré plus compliqué que je ne le pensais, mais j'aime bien chasser avec vous. Je vais voir qui est dans le piège là-bas. Au revoir.']],
				['10'] = [[Une partie de la horde en fuite a disparu quelque part ; il semblerait que le piège ait fonctionné. Plusieurs biomutants de plus se sont précipités en avant et des coups de feu ont retenti de cette direction quand la chasseuse a rejoint le combat.<new_line>Je peux continuer à poursuivre la horde et attaquer par-derrière. Je peux aussi les contourner par les flancs et voir si je trouve une meilleure position.]]
			},
			['choice'] = {
				['9'] = [[Les contourner par le flanc]],
				['6'] = [[Attendre et attaquer]],
				['5'] = [[D'accord, et la Cloque ?']],
				['8'] = [[Attaquer par-derrière]],
				['7'] = [[Avancer]],
				['2'] = [[Je vois que vous avez pensé à tout.']],
				['1'] = [[Vous pouvez me donner des détails ?']],
				['4'] = [[Qu'est-ce que c'est ?']],
				['3'] = [[Je comprends. Je suis prêt.']]
			}
		},
		['emba_fight_easy3'] = {
			['episode'] = {
				['2'] = [[Voici donc ce qu'il se passait ici : les biomutants se battaient contre une meute de dangereux prédateurs. Les ciseaux ont trouvé leur pierre !<new_line>Ce sont des conditions optimales pour détruire ces deux hordes au milieu de la confusion.]],
				['1'] = [[J'ai remarqué les biomutants de loin. Ils se comportent de manière inhabituelle.]]
			}
		},
		['emba_fight_easy2'] = {
			['episode'] = {
				['2'] = [[Il n'y a aucun humain nulle part. S'ils ont survécu et décident de revenir, ils tomberont directement dans les griffes des biomutants.<new_line>Je sauverai sûrement des vies en éliminant ces monstres tout de suite.]],
				['1'] = [[Même si la ferme n'a pas l'air ancienne ni délabrée, elle est complètement envahie par cette nouvelle végétation mutante. Et les biomutants sont là aussi, à flâner et à s'alimenter de la moisissure qui pousse.]],
				['4'] = [[Une trappe s'est ouverte dans le sol près de la maison. Le fermier en est sorti, paralysé de peur.<new_line>Il a regardé autour de lui et s'est approché de moi. Il m'a donné un sac de ce qui semblait être de la nourriture et a fini par marmonner quelques mots, une sorte de remerciement.]],
				['3'] = [[Le danger a été éliminé. J'ai trouvé de la nourriture que la moisissure n'a pas encore touchée.<new_line>Si les fermiers reviennent, je pense qu'ils ne m'en voudront pas d'avoir pris ce petit tribut en échange de leur sécurité.]]
			}
		},
		['emba_fight_easy'] = {
			['episode'] = {
				['2'] = [[Les biomutants mangeaient la moisissure qui poussait sur les racines noires sortant du sol. Ces créatures dangereuses vont maintenant reprendre de la force et attaquer tout ce qui vit dans les friches.]],
				['1'] = [[J'ai vu un groupe de biomutants regroupés autour de quelque chose.]]
			},
			['choice'] = {
				['1'] = [[Approcher prudemment]]
			}
		},
		['emba_isaev_chelyabinsk'] = {
			['episode'] = {
				['9'] = [[Gerin n'a pas créé le mycélium. Je m'en doutais depuis longtemps, mais tout cela vient confirmer mes soupçons. Le mycélium est une espèce biologique à part entière, et Gerin essayait de l'adapter à un usage agricole.']],
				['6'] = [[C'est terrible. Tout le monde comptait vraiment sur Gerin... Et il y a pire encore. L'ordre de lancement non programmé du projet de végétalisation des friches ne provient pas de cette biostation.']],
				['5'] = [[Oui, j'ai déjà envoyé des médecins auprès des scientifiques. Mais attendez, ce n'est pas possible ! Êtes-vous certain que c'était lui qui contrôlait les scientifiques ? Le Professeur Gerin, il... il a donné sa vie pour que vous et moi puissions avoir un avenir !']],
				['8'] = [[Si c'est arrivé une fois, cela peut se reproduire. Nous devons simplement identifier les causes de la tournure des événements. Nous n'interromprons pas les recherches, mais la menace doit être éliminée.']],
				['7'] = [[Le signal est venu d'ailleurs. Non seulement cela a activé le projet, mais en apportant au préalable des modifications dans les mutations du mycélium, au niveau de toutes les biostations.<new_line>Bien sûr, cela n'excuse pas Gerin mais, quoi qu'il arrive, ces circonstances sont une grave menace pour nos recherches.']],
				['2'] = [[La situation commence à devenir plus claire. Les scientifiques sous la direction de Gerin travaillaient dans cette biostation sur un projet de revégétalisation du monde après une guerre nucléaire.<new_line>Mais le travail n'a pas été mené jusqu'au bout, le projet n'a donc pas pu être lancé.']],
				['1'] = [[Gerin doit se trouver à la biostation la plus proche. Nous avons besoin du code d'accès pour y entrer. Encore une fois, je dois vous demander de terminer nos missions de repérage afin de récupérer les documents secrets du projet E.M.B.A.']],
				['4'] = [[En mettant la main sur la boîte noire de ce laboratoire, nous pourrions faire une véritable percée dans nos recherches. Après tout, on dirait que notre travail a déjà été entrepris avant nous, même si le temps a manqué pour l'achever.<new_line>Nous avons déchiffré le code d'accès pour le compartiment secret du labo. Tenez, je l'ai noté ici.'<new_line>(Tend un bout de papier avec le code)]],
				['3'] = [[Oui, comme vous l'avez deviné, le projet a reçu le feu vert. C'était peut-être un échec, et Gerin tente d'y remédier.']],
				['10'] = [[Tout en travaillant sur le mécanisme de végétalisation, il faut continuer à enquêter sur le désastre de la cité des sciences. Nous avons trouvé des preuves d'activité suspecte dans une autre biostation.<new_line>En revanche, selon les rapports, les scientifiques là-bas n'ont mené aucune expérience sur les mutations. Nous partirons dès que vous serez prêt.']]
			},
			['choice'] = {
				['2'] = [[Mais les biomutants sont apparus à cause du lancement du projet.']],
				['4'] = [[En quoi est-ce une menace ?']],
				['3'] = [[D'où ça ?']],
				['6'] = [[Je pense qu'il a aussi transmis la technologie à Kourganov.']],
				['5'] = [[Qu'avez-vous appris de nouveau ?']]
			}
		},
		['emba_isaev_rostov'] = {
			['episode'] = {
				['9'] = [[Mais il n'est pas à l'hôpital avec les autres blessés. Ce qui veut dire... qu'il est mort dans l'exercice de ses fonctions. Quelle nouvelle navrante ; nous avons perdu un homme bon et un employé très précieux. Toutefois, il faut poursuivre notre travail afin qu'il ne soit pas mort en vain.']],
				['6'] = [[Juste ciel, vous les avez sauvés ! Mes collègues sont vivants, je le sais déjà. J'ai dépêché sur place nos meilleurs médecins. Mais que leur est-il arrivé ?']],
				['5'] = [[Je suis d'accord. Ainsi, je vous conseille d'aller auprès du navigateur sans plus tarder. Parlez-lui et commencez à chercher la boîte noire. Nous avons déchiffré le code d'accès pour le compartiment secret du labo. Tenez, je l'ai noté ici.'<new_line>(Tend un bout de papier avec le code)]],
				['8'] = [[Absolument. Si vous voulez mon avis, le général a forcé Gerin à poursuivre les développements militaires. Nous devons sauver Gerin, cela nous faciliterait grandement la tâche. Je n'ai reçu aucun rapport du navigateur, savez-vous s'il y a un problème ?']],
				['7'] = [[Il s'est servi d'eux pour des expériences ?! C'est inimaginable ! Il a aussi dû prendre Gerin pour un cobaye, il faut le retrouver tout de suite.']],
				['2'] = [[Je suppose que ce sont les gens qui ont travaillé sur le projet avant la guerre. Selon les archives, un homme est entré par effraction dans la biostation précédente à l'aide du code d'accès du Colonel Kourganov. Nous tâcherons d'en savoir plus à son sujet.']],
				['1'] = [[La boîte noire que vous avez rapportée de la dernière cité des sciences a mieux survécu que la précédente. Nos hypothèses sont confirmées. Des gens travaillent encore dans certaines biostations.']],
				['4'] = [[Rien de précieux, d'un point de vue pratique, pour le moment. Mais nous avons appris que le navigateur devait bien connaître Kourganov, l'homme dont le code d'accès a été utilisé pour pénétrer dans la biostation précédente.']],
				['3'] = [[Encore une fois, je dois vous demander de terminer nos missions de repérage afin de récupérer les documents secrets du projet E.M.B.A. C'est nécessaire en premier lieu pour déchiffrer le code d'accès du laboratoire.']],
				['12'] = [[Il faut sauver Gerin. D'après les archives, il se trouverait assez loin d'ici. En chemin, nous visiterons une autre cité des sciences. Là-bas, il y a un objet au sujet duquel Kourganov a recueilli des informations. Je pense qu'il y a une raison à cela, et nous ne pouvons pas l'ignorer. Nous partirons dès que vous serez prêt.']],
				['11'] = [[Cela reste à découvrir, mais c'est très probablement la symbiose de la biologie et de la cybernétique qui est au cœur de toute cette histoire. Peut-être, cependant, que d'autres technologies secrètes ont joué un rôle là-dedans.']],
				['10'] = [[Nous avons découvert que les scientifiques utilisaient une technologie moderne, entre autres, pour stabiliser le mycélium.<new_line>Clairement, des machines informatiques dotées de certains algorithmes ont permis d'endiguer les mutations incontrôlables du virus. Maintenant, nous travaillons activement dans ce sens.']]
			},
			['choice'] = {
				['2'] = [[Que dois-je faire ?']],
				['1'] = [[Quoi ?! Qui ça peut bien être ?']],
				['4'] = [[Vous pensez que tout est la faute de Kourganov ?']],
				['3'] = [[C'est Kourganov qui les contrôlait.']],
				['6'] = [[Mais comment Kourganov a pu contrôler l'équipe d'expédition ?']],
				['5'] = [[Parler de l'explosion]],
				['7'] = [[Il faut le lui demander !']]
			}
		},
		['halloween_boss_2022_6'] = {
			['choice'] = {
				['2'] = [[Lancer une grenade]],
				['1'] = [[Lancer une corde]],
				['4'] = [[Suivre l'Âne]],
				['3'] = [[Sauter et l'attraper]]
			},
			['episode'] = {
				['2'] = [[J'ai lancé une corde autour de son cou, mais l'Âne a réussi à la couper en la mordant ! Au même moment, j'ai vu quelque chose tomber de sa bouche.<new_line>Il a fui en appelant bruyamment à l'aide.]],
				['1'] = [[J'ai avancé vers une hutte dans le marais. L'Âne parlant s'y trouvait ! Il fouillait les buissons en maugréant :<new_line>'Fleurs bleues, épines rouges...'<new_line>Je suis assez proche de lui pour tenter ma chance.]],
				['4'] = [[L'Âne m'a entendu sauter et a esquivé d'un pas sur le côté.<new_line>Il a fui en appelant bruyamment à l'aide.]],
				['3'] = [[L'Âne a réussi à capter la grenade entre ses dents et à la jeter dans ma direction. Au même moment, j'ai vu quelque chose tomber de sa bouche.<new_line>Il a fui en appelant bruyamment à l'aide.]],
				['5'] = [[J'ai entendu un fracas et un rugissement provenant de cabinets en bois que je n'avais même pas remarqués auparavant. La bête des marais !<new_line>La Faucheuse est alors apparue à côté des cabinets pour bloquer la porte avec sa faux.<new_line>'Eh bien, mortel, c'est la dernière fois que je t'aide ! Cours après l'Âne parlant, il s'enfuit ! Je vais rester et tenir la porte.']]
			}
		},
		['quest_order'] = {
			['episode'] = {
				['1'] = [[Le barman m'a appelé de derrière le comptoir au moment où je venais d'entrer.<new_line>'Alors, vous avez tout ?']]
			},
			['choice'] = {
				['2'] = [[Donner la commande et accepter la récompense]],
				['1'] = [[Pas maintenant']]
			}
		},
		['newyearEvent_fight_hard'] = {
			['choice'] = {
				['2'] = [[Explorer la forteresse (12 heures)]],
				['1'] = [[Utiliser la clé]],
				['4'] = [[Élaborer un plan (8 heures)]],
				['3'] = [[Descendre]],
				['6'] = [[Placer les explosifs]],
				['5'] = [[Disposer le bois sec]],
				['8'] = [[Prendre les provisions et partir]],
				['7'] = [[Mettre le feu au bois]]
			},
			['episode'] = {
				['9'] = [[Le bois sec a pris feu de manière spectaculaire. Les flammes ont rapidement déclenché les explosifs et démoli la forteresse.<new_line>Au bout d'un moment, le feu s'est calmé et j'ai pu descendre jusqu'à l'entrée secrète. Comme je m'y attendais, la porte avait été balayée : la voie vers l'entrepôt des vauriens était libre.]],
				['6'] = [[Cette forteresse est un bloc de glace. Le meilleur moyen d'en venir à bout, c'est d'allumer un grand feu. Je vais couvrir les étages supérieurs de bois sec et placer des explosifs au niveau du sous-sol.]],
				['5'] = [[Une fois les gardes neutralisés, j'ai exploré la pièce et trouvé une autre porte secrète. Elle est dépourvue de serrure et fermée hermétiquement.<new_line>Mieux vaudrait faire sauter la porte... et, au passage, tous les étages au-dessus. D'abord, je dois trouver le moyen d'atteindre cet objectif.]],
				['8'] = [[Enfin, tout est prêt pour la démolition. Il ne me reste plus qu'à mettre le feu au bois.]],
				['7'] = [[Des moteurs grondent au-delà des murs de la forteresse. Apparemment, quelqu'un est au courant de mon entrée par effraction. Je crois bien qu'un féroce combat m'attend.]],
				['2'] = [[Dans un grondement sourd, les portes s'ouvrent et la forteresse m'accueille dans un silence glacial. Maintenant, je dois l'explorer pour trouver le meilleur moyen de la détruire.]],
				['1'] = [[Une imposante forteresse de vauriens se trouve dans les étendues des friches couvertes de neige. Un énorme cadenas glacé pend à ses portes. On dirait que les habitants de cette forteresse ne sont pas chez eux en ce moment.<new_line>Ça vaudrait la peine de raser cet endroit pour que les vauriens ne reviennent pas l'an prochain. Cependant, se faufiler à l'intérieur s'annonce difficile : il vous faudra une clé.]],
				['4'] = [[Au sous-sol, j'ai finalement rencontré les habitants de la forteresse. Ils semblent garder quelque chose de précieux.]],
				['3'] = [[Alors que j'explorais chaque recoin de la forteresse, j'ai découvert une porte dérobée. Derrière, un long escalier plongeait sous terre.]]
			}
		},
		['new_year_prologue'] = {
			['choice'] = {
				['2'] = [[Monter sur la plate-forme]],
				['1'] = [[Prendre la décoration]]
			},
			['episode'] = {
				['2'] = [[Relevant la tête, j'ai vu une plate-forme lumineuse juste devant moi. Bizarre, elle n'était pas là juste avant.]],
				['1'] = [[Quelque chose qui scintillait dans la neige de manière anormale attira mon attention. En me penchant pour jeter un coup d'œil, je me suis rendu compte que c'était une décoration de Noël.]]
			}
		},
		['lost_soul'] = {
			['choice'] = {
				['2'] = [[Suivre le cri]],
				['1'] = [[Suivre les empreintes]],
				['4'] = [[Courir dans la forêt]],
				['3'] = [[Morceau de tissu]],
				['6'] = [[Courir jusqu'à la colline]],
				['5'] = [[Courir jusqu'au lac]],
				['8'] = [[Courir à son aide]],
				['7'] = [[Lui envoyer une corde]]
			},
			['episode'] = {
				['9'] = [[Il n'y a rien ni personne au lac. Le craquement était en fait la glace sur la berge. Je ferais mieux de retourner dans la clairière.]],
				['6'] = [[En grimpant la colline, j'aperçois l'homme de l'autre côté. Il tire sur un ours qui s'approche de lui en zigzaguant entre les arbres. Je ferais mieux d'intervenir !]],
				['10'] = [[En me frayant un chemin à travers les buissons et arbustes, j'ai, moi aussi, arraché mes vêtements... avant de me retrouver de nouveau au camp.]],
				['8'] = [[Les hurlements étaient en fait juste le vent. Je ferais mieux de revenir vite dans la clairière.]],
				['7'] = [[Une fois que j'ai secouru l'homme, nous retournons au camp pour nous réchauffer au coin du feu. L'homme me remercie et nous partons chacun de notre côté.]],
				['2'] = [[Les empreintes s'enfoncent dans les bois et reviennent à la tente.]],
				['1'] = [[J'ai trouvé le camp. La tente gisait sur la neige et le feu s'était éteint, mais les cendres étaient encore chaudes. A priori, l'occupant des lieux venait tout juste de partir. Soudain, j'ai entendu un cri provenant de la gauche, mais les empreintes quittant la droite se dirigent vers la droite. Quelle direction dois-je prendre ?]],
				['4'] = [[Le sentier mène à une clairière, où plusieurs bruits proviennent simultanément de différents endroits. Un craquement de glace au niveau du lac, un hurlement ressemblant à celui d'un loup dont l'origine semble être le fin fond de la forêt et (apparemment) un coup de feu provenant de la colline. Où aller ?]],
				['3'] = [[Mon intuition me dit que je touche au but. Le chemin en face de moi se scinde encore en deux. Les empreintes se dirigent dans un sens, mais dans l'autre, il y a un morceau de tissu accroché à un buisson truffé d'épines. Par où est passé l'homme qui a disparu ?]],
				['12'] = [[Arrivé au sommet de la colline, je ne trouve rien. Le bruit devait sûrement être une branche qui a craqué.]],
				['11'] = [[Au centre du lac glacé, j'aperçois un énorme trou dans lequel l'homme est tombé. Je dois l'aider !]],
				['5'] = [[La source du cri s'est avérée être un oiseau perché sur un arbre. Il me jette un coup d'œil et recommence à crier. Je dois retourner vite au camp.]],
				['13'] = [[Je trouve l'homme au sommet de l'arbre, au pied duquel plusieurs loups font les cent pas. Les animaux tournent la tête dans ma direction.]]
			}
		},
		['emba_time_easy2'] = {
			['episode'] = {
				['2'] = [[J'ai tout nettoyé autour du puits, mais j'ai remarqué que tout était en fleur à l'intérieur. La moisissure flottait également à la surface de l'eau tout au fond.]],
				['1'] = [[Il y avait un vieux puits au milieu des plantes enchevêtrées. Apparemment, le mycélium s'y était fixé pour avoir une source d'eau et avait poussé rapidement.<new_line>Les endroits comme ceux-ci attirent toujours les biomutants. Cette source potentielle de danger doit être détruite.]],
				['4'] = [[Tout est enfin fini. Maintenant, on a une zone de trouble en moins dans cette région.]],
				['3'] = [[Excellent, le puits a été nettoyé. Cependant, toute cette dangereuse végétation doit maintenant être entassée et brûlée. Sinon, les biomutants se rassembleront ici pour se nourrir et formeront une horde menaçante.]]
			},
			['choice'] = {
				['2'] = [[Dégager le puits]],
				['1'] = [[Casser toutes les branches et racines]],
				['3'] = [[Tout entasser et y mettre le feu]]
			}
		},
		['stash_polar_fox'] = {
			['episode'] = {
				['2'] = [[Le renard polaire m'a guidé vers la planque.]],
				['1'] = [[Le renard polaire m'a guidé vers le camp des bandits.]]
			}
		},
		['emba_isaev_intro'] = {
			['episode'] = {
				['19'] = [[Faites voir. Hum, ils devraient beaucoup nous aider à avancer. Apportez-les immédiatement au biologiste du camp, et aidez-le dans ses recherches.']],
				['5'] = [[Les membres de ProgrèS ont hâte de découvrir vos aspirations et vos futures contributions à l'expédition. Nous avons un plan pour résoudre le problème, et vous pouvez nous aider.']],
				['16'] = [[Exact. D'après nos renseignements, cette cité des sciences est complètement envahie par la moisissure et habitée par des formes de vie hostiles. Vous en avez rencontré certaines, récemment.']],
				['15'] = [[Pour le moment, il nous est impossible de les contacter. Nous avons ainsi fait appel au navigateur de Vecteur pour nous aider. En marge du projet E.M.B.A., je m'intéresse également au sort de mes compagnons d'armes.']],
				['9'] = [[Première phase : restaurer la biosphère. Le but de notre expédition est de trouver une technologie de terraformation. À la veille de la catastrophe planétaire, les scientifiques avaient réussi à la créer. Il s'agit du projet E.M.B.A.']],
				['17'] = [[Nous ignorons tout de leur origine. J'espère que vous pourrez nous aider à la découvrir.']],
				['18'] = [[C'est une association de spécialistes issus de domaines scientifiques de pointe. Sans doute la dernière dans son genre en activité... C'est pourquoi on nous appelle parfois les archivistes, avec notre manie de recueillir et de conserver les miettes de tout ce qui a survécu.<new_line>Mais bientôt, nos connaissances et nos ressources seront suffisantes pour donner au monde une deuxième chance !']],
				['6'] = [[J'ai marqué l'endroit sur la carte. Le navigateur de l'expédition vous attendra là-bas. Il vous fera part de tous les renseignements.']],
				['10'] = [[La souche de mycélium génétiquement modifiée est une moisissure capable d'absorber toute substance novice du sol. Sous son effet, une friche dévastée par les retombées radioactives pourrait devenir une terre fertile.']],
				['8'] = [[Le docteur soupira et retira ses lunettes pour les essuyer, comme s'il se préparait à une longue conversation.<new_line>'Évidemment, vous le savez, notre monde est en ruine. ProgrèS a préparé un ensemble de mesures pour surmonter la crise.']],
				['7'] = [[La menace a été éliminée. Le chef des gardes a donné le feu vert pour que le laboratoire soit installé dans le camp. Nous avons un peu de temps devant nous, je peux répondre à vos questions.']],
				['2'] = [[Il est impensable d'installer un laboratoire en plein air, c'est pourquoi nous voulons nous installer dans ce camp. Mais les habitants se méfient des scientifiques.']],
				['1'] = [[Je crois, camarade, que vous êtes notre agent potentiel. Je suis le Dr Victor Isaev. Enchanté de faire votre connaissance. Malheureusement, je ne peux vous donner tous les détails tant que nous n'aurons pas réglé nos principaux problèmes.']],
				['4'] = [[Non, mais je peux comprendre ces gens simples. Là, nous venons précisément arranger les choses. Mais sans ravitaillement, cette mission est vouée à l'échec.']],
				['3'] = [[Cela est lié aux recherches qui ont été menées dans les cités des sciences, des complexes urbains scientifiques. Il y en a un à proximité. Ces développements ont fait surgir des menaces biologiques ainsi que de nouveaux types de mutants. Les gens nous tiennent pour responsables, nous autres scientifiques.']],
				['12'] = [[Ce ne sera pas facile à trouver. Les recherches ont été menées dans des biostations, sur le territoire fermé des cités des sciences, dans le secret le plus absolu. Nous ne connaissons donc même pas l'emplacement de toutes les cités des sciences.']],
				['11'] = [[Non, le chef du projet E.M.B.A. était l'un des meilleurs scientifiques de notre ère, le professeur Mark Gerin. Grâce à ses travaux, il a été possible de réaliser cette percée dans le domaine de la géoingénierie.']],
				['14'] = [[Il n'y a pas si longtemps, ProgrèS a envoyé une expédition dans cette région, en quête d'une autre technologie perdue. Mais ils ont découvert fortuitement l'une de ces cités des sciences.']],
				['13'] = [[Après la catastrophe planétaire, le projet E.M.B.A. est tombé en décrépitude, et notre expédition a pour vocation de rectifier le tir.']]
			},
			['choice'] = {
				['15'] = [[Alors, où est la première expédition ?']],
				['9'] = [[Ont-ils raison ?']],
				['17'] = [[Ouais. C'est quoi, ces créatures ?']],
				['16'] = [[Dans ce cas, mieux vaut commencer par cette cité des sciences.']],
				['6'] = [[Et qu'est-ce que l'organisation ProgrèS ?']],
				['5'] = [[Qu'allez-vous faire ?']],
				['8'] = [[Pourquoi ?']],
				['7'] = [[J'ai trouvé des documents dans le bunker.']],
				['10'] = [[Dites-moi votre plan.']],
				['1'] = [[Que s'est-il passé ?']],
				['4'] = [[Quel est le principal objectif de l'expédition ?']],
				['3'] = [[Comment puis-je vous aider ?']],
				['12'] = [[C'est vous qui avez lancé le projet ?']],
				['11'] = [[Quelle est sa finalité ?']],
				['14'] = [[Et où en est le projet ?']],
				['13'] = [[Alors, nous avons besoin de Gerin ?']]
			}
		},
		['comrade_in_trouble_rad'] = {
			['episode'] = {
				['1'] = [[L'éclaireur gît au sol. Son triste sort est peut-être dû à sa propre stupidité ; reste qu'il a connu une fin horrible.]]
			}
		},
		['brick_fall'] = {
			['choice'] = {
				['2'] = [[Prendre la brique comme souvenir]],
				['1'] = [[Examiner les débris]]
			},
			['episode'] = {
				['2'] = [[Après m'être débarrassé des rats, j'ai trouvé quelques objets utiles dans les débris du bâtiment.]],
				['1'] = [[Alors que je traversais les rues désertes de la ville, le mur en briques de l'immeuble bancal s'est effondré. Manque de chance, j'étais juste à côté et j'ai reçu une brique en plein sur le crâne.]]
			}
		},
		['boatman_olonec'] = {
			['episode'] = {
				['2'] = [[Salut. J'imagine que vous n'êtes pas encore prêt ?']],
				['1'] = [[Absorbé par sa tâche, le batelier bricole le moteur du vieux canot.]],
				['4'] = [[Il se trouve dans la partie sud de Léningrad. Vous devez déjà savoir à quel point c'est dangereux, par là-bas.']],
				['3'] = [[Salut. On ne parle que vous dans le village. Merci d'avoir aidé les gens du coin.']],
				['5'] = [[Au fait, pendant que vous preniez vos aises, j'ai réparé mon vieux canot à moteur pour votre mission. Il faudra vous débrouiller pour le carburant, un marchand doit en vendre dans le village.<new_line><new_line>Quand vous serez sur le lac, faites cap vers l'ouest. J'espère vous revoir. Soyez prudent.']]
			},
			['choice'] = {
				['2'] = [[Pas maintenant. À la prochaine.']],
				['1'] = [[Salut']],
				['4'] = [[Pas besoin.']],
				['3'] = [[Je vous en prie. C'est quoi, cette histoire d'institut ?']],
				['5'] = [[Merci, batelier.']]
			}
		},
		['halloween_search_medium'] = {
			['episode'] = {
				['2'] = [[Oh, gamin. Il manque encore une herbe. J'avais complètement oublié.']],
				['1'] = [[Bonjour, gamin. Je concocte une potion particulièrement coriace, aujourd'hui. Tu as peut-être des ingrédients ?']],
				['3'] = [[Apporte-moi encore une herbe, et le marché sera conclu.']]
			},
			['choice'] = {
				['2'] = [[Non, mais je vais chercher.' (Partir)]],
				['1'] = [[Et voilà.']],
				['4'] = [[Bon...' (Partir)]],
				['3'] = [[Mais notre marché...']]
			}
		},
		['embacity_biolab_1'] = {
			['episode'] = {
				['2'] = [[Le combat a été intense, mais le biomutant est mort.]],
				['1'] = [[Alors que j'approchais du dôme, j'ai entendu du bruit. Jetant un coup d'œil prudent, j'ai vu un cadavre monstrueux tout fracasser sur son passage.]],
				['4'] = [[L'escalier m'a conduit à une grande salle. Apparemment, le mutant vivait là et se nourrissait de la biomasse qui coulait dans les tuyaux du réservoir.<new_line> En faisant le tour de la pièce, j'ai trouvé une porte discrète avec une serrure à code.]],
				['3'] = [[J'ai fouillé le dôme et j'ai trouvé une grande ouverture. À l'intérieur, il y avait un escalier menant à la partie souterraine du laboratoire.]],
				['6'] = [[Une bande de biomutants venait de surgir dans le laboratoire. Heureusement, le plus dangereux d'entre eux ne m'avait pas encore repéré. Je pouvais en profiter pour m'échapper.]],
				['5'] = [[Après avoir fouillé la pièce, j'ai trouvé un conteneur. Au-dessus, un panneau indiquait « Enregistreur d'urgence ». C'était la boîte noire que je cherchais.]]
			},
			['choice'] = {
				['2'] = [[Descendre les escaliers]],
				['1'] = [[Fouiller les environs]]
			}
		},
		['search_killer2'] = {
			['episode'] = {
				['1'] = [[Une nouvelle victime. Les empreintes sont fraîches. Je pense que je gagne du terrain sur le tueur.]]
			}
		},
		['monstrous_bag'] = {
			['episode'] = {
				['1'] = [[Pendant plusieurs heures, j'ai écouté le sac qui gronde faim derrière. Il semble sa patience a manqué. Je me suis senti dents pointues dans mon épaule! J'ai laissé tomber le sac. Mais il a senti le sang et a obtenu complètement fou. Je dois montrer qui est le patron ici.]]
			}
		},
		['order_delivery_quest_pt2'] = {
			['episode'] = {
				['2'] = [[Pourquoi cette tête d'enterrement ? Un problème avec la marchandise ?']],
				['1'] = [[Alors, comment ça s'est passé ?']],
				['4'] = [[Le barman a poussé un grand soupir.<new_line>'Foutus marchands ! Bon, ce n'est pas votre faute. Tenez, je vais vous rembourser.']],
				['3'] = [[Le barman a secoué la tête.<new_line>'Désolé, mais c'est trop cher pour moi. Gardez tout.']],
				['5'] = [[La caisse était remplie de provisions. Rien qui vaille l'argent dépensé, mais c'était toujours mieux que de repartir les mains vides.]]
			},
			['choice'] = {
				['2'] = [[Lui dire combien ça m'a coûté]],
				['1'] = [[Hocher de la tête et donner la marchandise]],
				['4'] = [[Ouvrir la caisse pour voir le contenu]],
				['3'] = [[Donner la marchandise et prendre l'argent]],
				['5'] = [[Prendre les provisions]]
			}
		},
		['gift_delivery'] = {
			['choice'] = {
				['1'] = [[Placer le cadeau]]
			},
			['episode'] = {
				['2'] = [[À mon arrivée, je n'ai rien vu d'inhabituel. Je me demandais où laisser le cadeau lorsque j'ai entendu un fusil qu'on rechargeait et une voix rauque dire : «Pose la caisse sur le sol et dégage. Ne te retourne pas.»]],
				['1'] = [[C'est un camp de petite taille, avec un appentis confectionné à partir de branches d'épicéas recouvertes de neige. Il y a un feu, et de l'eau en train de bouillir dans une marmite. On dirait que l'homme vient tout juste de partir. Il ne va pas en croire ses yeux quand à son retour, il trouvera un cadeau.]],
				['3'] = [[À ma grande surprise, j'ai trouvé un sapin du Nouvel An avec des décorations improvisées. Quelqu'un est venu ici et a dormi, emmitouflé dans plusieurs couvertures. J'aurais tout intérêt à laisser un cadeau sous le sapin et à m'en aller.]]
			}
		},
		['order_delivery_quest'] = {
			['episode'] = {
				['2'] = [[J'ai trouvé le commerçant. Il n'avait pas l'air ravi. Il m'a montré la marchandise avant de demander :<new_line>'Vous croyez qu'on vous file tout ça gratis ?']],
				['1'] = [[J'ai trouvé le commerçant et, sans tambour ni trompettes, m'a monté la marchandise.]]
			},
			['choice'] = {
				['1'] = [[Prendre la marchandise et partir]],
				['4'] = [[Voilà l'argent. Satisfait ?']],
				['3'] = [[Refuser de payer et partir]]
			}
		},
		['halloween_boss_2022_6_2'] = {
			['choice'] = {
				['9'] = [[Quoi, c'est tout ?']],
				['6'] = [[C'est ça que vous cherchez ?' (Sortir la page de la liste des victimes du Monstre de Frankenstein)]],
				['10'] = [[Continuer d'avancer]],
				['8'] = [[Dégainer l'arme]],
				['7'] = [[Contemple la puissance des paperasses et de la bureaucratie !' (Déchirer la page)]],
				['2'] = [[Où sommes-nous ? Et où est passé l'Âne ?']],
				['1'] = [[Traverser à gué]],
				['4'] = [[Je vous enquiquine, et je rentre à ma maison.']],
				['3'] = [[De quoi parlez-vous ? J'ai rempli le contrat, au final ?']],
				['5'] = [[Je suis imbattable !']]
			},
			['episode'] = {
				['15'] = [[Non, tu as également droit à une compensation—tiens. Au fait, j'ai d'autres affaires à régler dans ton monde, alors n'hésite pas à passer voir ma marchandise si tu as besoin de quelque chose.']],
				['9'] = [[J'en doute beaucoup, tu sais. Tu as traversé la Rivière de l'oubli. Et maintenant, nos amis communs vont venir ici et t'anéantir.'<new_line>D'un claquement de doigts de la Faucheuse, des portails sont apparus tout autour en projetant des étincelles électriques.]],
				['10'] = [[En effet, tes forces ont décuplé depuis le premier contrat que je t'ai donné. Mais il y a un léger imprévu.<new_line>J'ai commencé à comploter contre toi quand j'ai vu ton nom listé dans le Livre de la mort. En d'autres termes... Nul n'échappe à son destin, tu te rappelles ?'<new_line>(La Faucheuse sort le Livre et commence à le feuilleter)]],
				['6'] = [[Cette berge était sombre et déserte, en réalité, comme si elle venait d'un autre monde. Un brouillard épais est tombé sur la rivière, d'un seul coup. Cerise sur le gâteau, il s'est aussi mis à pleuvoir. Un éclair a déchiré le ciel, et un orage a éclaté.<new_line>L'Âne demeurait introuvable. La Faucheuse, elle, se tenait devant moi.]],
				['5'] = [[J'ai vu encore plus de monstres sortir du bois ! Contre une telle meute, je n'avais aucune chance de m'en sortir. J'ai dû traverser la rivière pour gagner la rive opposée.]],
				['8'] = [[Oublie le contrat—j'ai d'autres objectifs en tête. Tes pouvoirs démoniaques sont devenus colossaux. La plupart des Faucheuses ne peuvent que rêver de moissonner une âme aussi puissante.']],
				['7'] = [[Ça, mortel, c'est mon monde. Bienvenue ! Et ne t'inquiète pas pour l'Âne, il est exactement là où il devrait être.<new_line>Vois-tu, toutes ces bêtes et tous ces monstres sont de gros poissons, bien sûr... mais personnellement, je veux attraper un vrai requin.']],
				['2'] = [[La forêt devenait plus sombre à chacun de mes pas et les yeux des crânes brillaient, rouges. D'un coup, le sentier fit un virage et je manquai de percuter une bande hétéroclite de monstres.]],
				['1'] = [[Étant à mi-chemin de ma vie, je me trouvai dans une forêt obscure. Au bord du sentier se trouvaient des crânes empalés sur des piquets, et des voix effrayantes hurlaient derrière les arbres.]],
				['4'] = [[J'ai rejoint la rivière. Bizarrement, je n'ai pas le souvenir de l'avoir vue sur une carte.<new_line>La Faucheuse, qui se tenait sur l'autre rive, m'a fait signe :<new_line>'Il y a un gué ici. Traverse, dépêche-toi, l'Âne s'en va !'<new_line>Sur ces mots, j'ai entendu des branches craquer juste derrière moi.]],
				['3'] = [[Tous ces ennuis pour un Âne galeux...<new_line>Mais j'entends ses braiments, pas très loin. Cette fois, il ne m'échappera pas !]],
				['12'] = [[Comme pour souligner l'instant tragique, des éclairs ont retenti dans un grondement terrible. Et l'un des portails a été frappé par la foudre ! Dans un flash aveuglant, il a disparu. Pas mal ! Ainsi donc, mon Gigawattor peut détruire des portails...]],
				['11'] = [[Que diable... Comment ?! Au dos de la même feuille ?! Mais c'est moi qui te l'ai donnée...']],
				['14'] = [[Ne t'inquiète pas, l'ami. Je suis une autre Faucheuse. On m'a envoyée ici pour remplacer mon collègue véreux. Cette histoire de fraude, de contrats et de sous-traitance... quelle honte pour la profession.<new_line>Au nom du monde des morts, je m'excuse. Je te renvoie immédiatement dans ton monde natal.']],
				['13'] = [[En me voyant tendre la main vers mon Gigawattor, la Faucheuse s'est mise à siffler avec colère : 'Crapule ! Qu'importe, tu ne fermeras pas tous mes portails. Simple mortel, tu n'es pas assez fort !']]
			}
		},
		['undertaker'] = {
			['episode'] = {
				['1'] = [[Au cimetière, un chasseur fatigué m'a rencontré avec une pelle dans ses mains. Il hocha la tête à un endroit près des tombes ouvertes où je peux mettre les cercueils.]]
			},
			['choice'] = {
				['1'] = [[Donnez les cercueils]]
			}
		},
		['snowmen_fortress'] = {
			['episode'] = {
				['1'] = [[La forteresse des bonshommes de neige s'est avérée être une gigantesque structure de blocs de glace. De dessous leurs seaux, les sentinelles en poste sur les tours me regardaient déjà d'un mauvais œil, se balançant des morceaux de neige d'une main à l'autre.]]
			}
		},
		['newyearEvent_commander_easy'] = {
			['episode'] = {
				['9'] = [[Nounours est arrivé quelques minutes après la bataille. Et il ne semblait pas avoir perdu confiance.<new_line>'J'ai du pain sur la planche, dans ce bas monde. Nous avons tous été humains, autrefois ! Merci pour le soutien.']],
				['6'] = [[Père Nounours avança dans l'antre avec les villageois. Il y eut un silence radio pendant un court instant, puis le talkie-walkie se mit bientôt à grésiller.<new_line>'Fshhh... Hé, qui va par... fshhh... Nous venons en paix... fshhh... Hé, Glagla, ce compère est venu de son plein gré. On l'emmène voir Fouettard ou... fshhh...']],
				['5'] = [[Très bien, camarade. Mais je te connais, alors tu ne viens pas avec nous. Prends le talkie-walkie. S'il arrive quelque chose, on sera en contact.']],
				['8'] = [[Fshhh... Bien reçu, le compère, c'est nous. Il faut nous dire... fshhh...']],
				['7'] = [[Fshhh... On se replie. Tu n'y arriveras pas. Dis-nous plutôt quoi faire... Courir vers la sortie ou revenir au gara... fshhh...']],
				['2'] = [[Euh... Comment tu es arrivé là ?']],
				['1'] = [[Père Nounours était aux côtés d'un couple de survivants, près du camp des vauriens.<new_line><new_line>'Je vous assure, nous sommes tous des prolétaires ! Il n'y a pas de différence entre nous, la lutte des classes nous a été imposée par les capitalistes ! Mais nous pouvons trouver un terrain d'entente, comme des êtres civilisés et sensés, selon les préceptes de Karl Farx.]],
				['4'] = [[Vauriens du Grand Nord, pumas sud-américains ou pandas asiatiques : est-ce que ça compte vraiment ? J'ai l'intention de me lancer dans des négociations pacifiques.']],
				['3'] = [[Vous n'y comprenez ! Inutile de neutraliser qui que ce soit. C'est possible de trouver un terrain d'entente, en distribuant les ressources de manière juste et équitable.']]
			},
			['choice'] = {
				['9'] = [[Attaquez en premier ! Ils ne s'y attendront pas !']],
				['6'] = [[Nounours, entrez ! Ne restez pas là. J'arrive !']],
				['5'] = [[Prendre le talkie-walkie]],
				['8'] = [[Retournez au garage !']],
				['7'] = [[Courez vers la sortie !']],
				['2'] = [[Je voulais neutraliser les vauriens.']],
				['1'] = [[Bonjour !']],
				['4'] = [[Je sais, mais au moins, laissez-moi surveiller vos arrières.']],
				['3'] = [[Mais ce sont des vauriens.']]
			}
		},
		['new_year_boss_5_3'] = {
			['choice'] = {
				['2'] = [[Traquer Glagla]],
				['1'] = [[Encore lui ?!']],
				['4'] = [[Qu'allons-nous faire ?']],
				['3'] = [[Se relever et traquer Glagla]]
			},
			['episode'] = {
				['2'] = [[Oh, mais qui vient nous rendre visite ! Vous m'apportez des cadeaux ? Ha ha ! Non ? Pas de souci ! Je voulais justement tester mon arme d'aspiration magique sur des créatures vivantes !']],
				['1'] = [[Nous avons atteint un escalier de glace menant sous terre. Partout, le sol est recouvert de décorations de Noël calcinées et de paquets cadeaux éventrés.]],
				['4'] = [[Tu me reverras. Dans tes cauchemars !'<new_line><new_line>Glagla jeta Fouettard sur son épaule, tira une pièce scintillante parmi les fragments de l'arme, puis courut dans les escaliers pour rejoindre la sortie.]],
				['3'] = [[Bande de bêtas bornés ! Pères divers et Fouettard ! La machine... vous l'avez détruite ! Bah, qu'importe. C'est pas fini. Voyons ce que vous avez dans le ventre, sans votre magie !']],
				['6'] = [[Une fois encore, un mur de neige me repoussa aussi sec !<new_line><new_line>'C'est inutile. Glagla est rusé. Et maintenant, il détient la pièce de l'arme secrète qui a aspiré notre magie.']],
				['5'] = [[Un tourbillon de neige me renversa. Il semblait sortir de nulle part.]],
				['7'] = [[Sortir de là avant d'être ensevelis sous la neige ! Voilà ma récompense pour avoir traîné avec vous...']]
			}
		},
		['easter_rabbit'] = {
			['episode'] = {
				['30'] = [[Même les mouches ne volent pas gratuitement. Si tu m'apportes quelque chose, je me montrerai charitable et je l'offrirai aux gens. Comme ça, toi aussi, tu feras une bonne action.']],
				['29'] = [[Je suis un type comme les autres maintenant, ce qui s'est passé avant n'a plus la moindre importance. Si tu as d'autres paniers, apporte-les-moi... Les festivités ne sont pas encore terminées. Et puis, j'aimerais quelque chose dans une de mes boutiques.']],
				['28'] = [[Eh bien, on dirait que tu as apporté assez de paniers et il y a de moins en moins de clients. Tu as donc mérité ta récompense ! Je vais même t'offrir un petit quelque chose en prime. Quant à moi, je vais m'offrir une retraite bien méritée.']],
				['27'] = [[J'ai fait de la prison une fois. J'ai été marié deux fois. Ensuite, on m'a enrôlé dans l'armée et j'ai dû passer deux ans et demi au fin fond du Kazakhstan sans aucune raison.<new_line>Je me suis fracturé le visage, on m'a enlevé un rein et j'ai un éclat d'os dans la cheville qui ne guérira jamais. J'ai vu toutes sortes de choses dans cette vie...']],
				['26'] = [[D'où vient le lait des vaches ? Arrête un peu de poser des questions stupides et tu n'auras plus à entendre des réponses idiotes.']],
				['25'] = [[Va dans n'importe quelle ville abandonnée et fouille partout. Tu finiras bien par trouver des terriers de lapin et il te suffira de creuser pour mettre la main sur de la peinture. Pense à prendre une pelle.’]],
				['24'] = [[Tu peux aussi en acheter à un commerçant au triple du prix, mais seulement en dernier recours.']],
				['23'] = [[Explore les friches, radioactives ou non, on tombe souvent sur des poulets dans ces coins-là. Et qui dit poulet, dit œuf.']],
				['22'] = [[J'ai entendu dire que des gens disparaissaient dans les fermes abandonnées dans les friches. Apparemment, les coupables seraient des poulets mutants. Tu devrais leur rendre une petite visite... Qui dit poulet, dit œuf.']],
				['21'] = [[Eh bien...']],
				['20'] = [[Oh, excellent ! Voilà ta part. Tu en as d'autres ?']],
				['19'] = [[Bien joué, ça fait plaisir ! Voilà ta part. Tu en as d'autres ?']],
				['5'] = [[Pourquoi les pingouins ont-ils besoin de faire des crottes ?... Ils m'ont viré du ministère des lapins, pardi !!! Je veux regagner leur confiance et redevenir adorable et tout doux.']],
				['16'] = [[Qu'est-ce que tu as d'autre ?']],
				['15'] = [[Il n'y a pas de conditions particulières. J'achèterai juste à un prix intéressant, meilleur que celui proposé par le psychopathe. Si tu m'en vends assez, je t'offrirai une récompense. Le prix est intéressant de toute façon, alors penses-y.']],
				['9'] = [[Je te donnerai quand même le costume, mais à condition que tu parviennes à m'apporter assez de paniers pour répondre à la demande. J'ai mis un temps fou à fabriquer ce costume.']],
				['17'] = [[C'est bien. Montre-moi ce que tu as.']],
				['18'] = [[Tu aurais pu rapporter un peu de ferraille avec toi, aussi... Voilà ta part. Tu en as d'autres ?']],
				['6'] = [[Tu vas me rendre dingue avec tes « Sérieusement ?! » ! Utilise tes cellules grises ! Pâques arrive à grands pas, et les gens offrent des œufs pour Pâques. J'ai fabriqué un costume de lapin et j'ai l'intention de me lancer dans les affaires.<new_line>Je vais faire la tournée des familles fortunées, leur vendre des paniers cadeaux et faire un max de profit. Ton travail consiste à peindre des œufs pour faire des paniers cadeaux.']],
				['10'] = [[Voici une pelle. Va te balader dans les villes, cherche des trous de lapin dans les bâtiments abandonnés et creuse. C'est comme ça que tu trouveras de la peinture.']],
				['8'] = [[Je ne sais pas... C'est trop rare, on n'en trouvera pas d'autres dans ces terres désolées. En tout cas, on dirait que tes cellules grises ont commencé à s'activer dans ta petite tête... Alors, pourquoi ne pas partager une partie des bénéfices des paniers entre nous ?<new_line>En plus, j'ai d'autres raretés que je pourrais aussi partager avec toi.']],
				['7'] = [[Je te donnerai mon costume de lapin.']],
				['2'] = [[Comment ça, « Qui es-tu ? » ?! Nettoie les toiles d'araignée que tu as dans les yeux ! Lapin Rose, à ton service.']],
				['1'] = [[Hé, mon garçon. J'ai besoin d'aide ! Vite, par ici !']],
				['4'] = [[C'est sérieux à ce point ?']],
				['3'] = [[Tu es du genre lent, non ? De quoi peut bien avoir besoin un lapin rose ? D'œufs peints, bien évidemment.']],
				['12'] = [[Alors, ne reste pas les bras ballants comme ça ! Remplis la commande, les clients sont déjà en train de faire la queue.']],
				['11'] = [[Tu ne voudrais pas que je te donne la main, aussi ? Tu peux en acheter, mais la demande a explosé et les prix ont flambé. Sinon, tu peux aussi en trouver ailleurs...']],
				['14'] = [[Mais enfin ! Pourquoi diable travailler pour ce taré, hein ? Tu ferais mieux de me les vendre à moi, les œufs peints ! Je te garantis un prix correct. La demande est bonne en ce moment. Et les gens ne laisseront jamais ce psychopathe entrer chez eux.']],
				['13'] = [[Psst, par ici ! Ça ne sera pas long, promis !']]
			},
			['choice'] = {
				['26'] = [[Peut-être que tu pourrais me le donner gratuitement ?']],
				['25'] = [[Peut-être que le moment est venu de lever le voile ? Qui es-tu ?']],
				['24'] = [[Pourquoi y aurait-il de la peinture dans les trous ? D'ailleurs, c'est quoi ces trous ?']],
				['23'] = [[J'aimerais demander autre chose.']],
				['22'] = [[Je n'ai pas d'autres questions.']],
				['21'] = [[Parle-moi un peu de toi.']],
				['20'] = [[Rappelle-moi où trouver la peinture.']],
				['19'] = [[Je ne trouve pas d'œuf.']],
				['5'] = [[Sérieusement ?!']],
				['16'] = [[Je passais juste dans le coin.' (Partir)]],
				['15'] = [[J'ai des questions !']],
				['9'] = [[D'accord. Mais où est-ce que je peux trouver les œufs et la peinture ?']],
				['17'] = [[Donner les paniers]],
				['18'] = [[C'est tout pour le moment.']],
				['6'] = [[En quoi est-ce mon problème ?']],
				['10'] = [[Et les œufs ?']],
				['8'] = [[Voilà qui est mieux.']],
				['7'] = [[Et pourquoi est-ce que ça m’intéresserait ?']],
				['2'] = [[Alors, que me veux-tu, petit lapin rose ?']],
				['1'] = [[D'ailleurs, tu es qui au juste ?']],
				['4'] = [[Pourquoi des œufs peints, au fait ?']],
				['3'] = [[Vraiment ?!']],
				['12'] = [[Qu'en est-il des conditions ?']],
				['11'] = [[Entendu.']],
				['14'] = [[J'ai des paniers !']],
				['13'] = [[D'accord.' (Partir)]]
			}
		},
		['search_killer1'] = {
			['episode'] = {
				['1'] = [[J'ai trouvé un cadavre non loin du portail. Je dois déterminer l'endroit où le tueur s'est échappé.]]
			}
		},
		['forest_camp'] = {
			['choice'] = {
				['9'] = [[Tout prendre et partir]],
				['6'] = [[Prendre ce que vous pouvez et partir]],
				['5'] = [[Entrer dans la tente du centre]],
				['8'] = [[Égorger le bandit]],
				['7'] = [[Entrer dans la tente de droite]],
				['2'] = [[Se camoufler et s'introduire sans bruit dans les tentes]],
				['1'] = [[S'approcher]],
				['4'] = [[Fouiller le camp]],
				['3'] = [[Entrer dans la tente de gauche]]
			},
			['episode'] = {
				['2'] = [[Lorsque la sentinelle m'a vu, elle a immédiatement brandi son fusil et m'a tiré dessus ! La balle m'a touché en pleine poitrine, mais je suis encore vivant. Je vais devoir me battre.]],
				['1'] = [[Alors que je traversais la forêt, j'ai perçu l'odeur caractéristique de la fumée.<new_line>J'en ai rapidement déterminé l'origine : des bandits ont établi un camp non loin et y ont allumé un feu. L'un de ces malfrats monte la garde, s'approcher pourrait être dangereux.]],
				['4'] = [[Après avoir coupé la toile et être entré dans la tente, je suis tombé sur un bandit. Très surpris, il a instinctivement pointé son pistolet dans ma direction. J'imagine qu'un combat est inévitable.]],
				['3'] = [[J'ai réussi à me cacher dans les hautes herbes et à m'approcher des tentes. Je pourrais utiliser mon couteau pour découper la toile de l'une d'elles et m'y introduire.]],
				['6'] = [[Quel horrible bazar ! J'espère que parmi ce foutoir, je réussirai à dénicher quelque chose d'utile.]],
				['5'] = [[Maintenant que tous les bandits sont morts, je peux fouiller leur camp tranquillement.]],
				['8'] = [[Un mouvement rapide a suffi à envoyer le bandit ad patres. Il n'a même pas su ce qui lui arrivait. Maintenant, je peux m'emparer de ses possessions.]],
				['7'] = [[À l'intérieur de la tente, je suis tombé sur un bandit qui ronflait bruyamment. À en juger par la bouteille vide à côté de lui, il n'est pas près de se réveiller.]]
			}
		},
		['new_year_boss_4'] = {
			['choice'] = {
				['2'] = [[De quoi il parle ?']],
				['1'] = [[S'approcher de la scène]]
			},
			['episode'] = {
				['2'] = [[Nous sommes arrivés juste à la fin de la chanson. Cyber Noël a sauté dans nos bras directement depuis la scène.<new_line>'Vous êtes venus... Samouraï, on a une ville à brûler...']],
				['1'] = [[Père Nomade nous a guidés le long du chemin, mais au bout d'un moment, nous avons simplement commencé à suivre le grondement de la musique pour trouver la scène.<new_line>Cyber Noël saute dessus. Étonnamment, parmi la foule en délire se trouve un membre de la bande de Fouettard.]],
				['3'] = [[Il faut le faire décuver. Je vais... Camarade, apporte-lui de l'eau potable.']]
			}
		},
		['stock_bandit'] = {
			['choice'] = {
				['2'] = [[Sortir et se cacher derrière la caisse]],
				['1'] = [[Les attaquer]],
				['4'] = [[Ne pas se faire voir et attendre]],
				['3'] = [[Pousser la caisse sur le pilleur]],
