				['4'] = [[Continuer d'avancer]],
				['3'] = [[Fouiller le squelette]],
				['12'] = [[Se défendre contre la créature]],
				['16'] = [[Examiner le mot de près]],
				['14'] = [[Inspecter le carnet]],
				['13'] = [[Ravitailler le générateur]]
			},
			['name'] = [[Bunker militaire]],
			['episode'] = {
				['9'] = [[Sans réfléchir, j'ai couru vers une autre pièce en fermant la porte derrière moi.<new_line>Le mille-pattes a commencé à se jeter contre le lambris rouillé. La porte n'allait pas tenir bien longtemps.]],
				['17'] = [[Le panneau était alimenté par un générateur portable qui manquait d'essence.<new_line>J'ai vu un pistolet par terre. Je me suis baissé pour le ramasser, quand j'ai entendu un léger bruit au-dessus de ma tête.]],
				['18'] = [[Une voix d'homme s'est élevée des haut-parleurs :<new_line>'fsssh... Vasily. Vous me recevez ?... fsssh... Vasya, je suis vers Kandalakcha... fsssh... Ces foutus fous furieux ont envahi la ville... fsssh...']],
				['6'] = [[Un grand panneau de commande radio se trouvait dans la salle de contrôle... malheureusement hors tension.]],
				['19'] = [[Il n'y a rien d'autre ici. C'est le moment de partir.]],
				['8'] = [[Au-dessus de moi, j'ai vu un mille-pattes géant. Son corps recouvrait presque tout le plafond.<new_line>La lumière de la torche semblait avoir réveillé cette énorme bestiole.]],
				['7'] = [[Un journal se trouve le panneau de commande, mais il fait trop sombre pour lire.]],
				['2'] = [[En descendant, je suis arrivé dans une chambre d'aération où se trouvait un squelette avec un uniforme d'officier.]],
				['1'] = [[Derrière le QG, j'ai trouvé un conduit d'aération ouvert.<new_line>On dirait un bunker militaire. Il doit forcément renfermer des choses utiles.]],
				['4'] = [[Dans le couloir, j'ai trouvé une porte fermée sur laquelle était écrit 'Salle de contrôle'.]],
				['3'] = [[La cage thoracique du squelette était défoncée, et il manquait la jambe droit. Qui a bien pu faire une chose pareille ? Restons prudent.]],
				['12'] = [[En proie aux flammes, la créature n'a pas tardé à rendre son dernier spasme.<new_line>Épuisé, j'ai pris un bidon d'essence avant de revenir à la salle de contrôle.]],
				['11'] = [[Je me trouvais dans une réserve remplie de barils d'essence.<new_line><new_line>J'avais maintenant une chance de repousser la créature.]],
				['14'] = [[...fsssh... Vasya. Vous me recevez ?... fssssh...'<new_line><new_line>Le vieux générateur a commencé à gronder, avant de lâcher.<new_line>Le panneau est devenu noir.]],
				['13'] = [[Le générateur a crépité en revenant à la vie. Allumé, le panneau de commande semblait chercher des fréquences pour capter un signal radio.<new_line>Au bout de quelques minutes, quelque chose comme l'écho d'un voix humaine s'est fait entendre.]]
			}
		},
		['gigahive_bar'] = {
			['episode'] = {
				['2'] = [[C'est leur manière de se protéger des étrangers. On a même développé un antidote pour ça.<new_line>Vous voulez vraiment aller là-dedans ? Ma foi, si vous trouvez un moyen de tuer la reine, apportez-moi sa tête. Vous serez grandement récompensé.'<new_line>]],
				['1'] = [[C'est un endroit extrêmement dangereux : la demeure de la reine des abeilles. Beaucoup ont tenté de la tuer, mais tous ont péri dans la ruche. Qu'ils reposent en paix.']],
				['4'] = [[Incroyable, vous avez réussi à tuer la reine des abeilles ! Vous êtes un héros pour nous tous. Je vais accrocher ce trophée dans le bar ! Je vous avais promis une belle récompense. Eh bien, la voici.']],
				['3'] = [[Il n'y a plus de stock pour l'instant, mais si vous apportez de la cire à notre spécialiste, il vous en préparera une dose. Vous en trouverez dans les nids d'abeilles mères, partout autour d'Oufa.']],
				['5'] = [[Ça alors, vous empestez les abeilles ! Vous avez vraiment abattu la reine ? Je ne vous crois pas. Apportez-moi sa tête pour le prouver.']]
			},
			['choice'] = {
				['2'] = [[Mais où trouver l'antidote ?']],
				['1'] = [[Parler des cloques]],
				['3'] = [[Hocher de la tête et partir]]
			}
		},
		['halloween_boss_2022_2'] = {
			['choice'] = {
				['2'] = [[Mais alors, pourquoi les zombies ont-ils réussi à sortir ?']],
				['1'] = [[D'où viennent-ils ?']]
			},
			['episode'] = {
				['2'] = [[Du vieux cimetière, évidemment. On surveille de près cet endroit—après tout, on sait très bien ce qui arrive quand le Médecin de la peste marche parmi les tombes...']],
				['1'] = [[Bonjour, chasseur ! Jusqu'à maintenant, on parvient à repousser les zombies au prix de lourds efforts. Mais ils sont de plus en plus nombreux.']],
				['3'] = [[Eh bien, le Médecin de la peste a versé un somnifère dans les boissons des gardes pour entrer dans le cimetière. Maintenant, impossible de l'en sortir—il se cache derrière une brume toxique et une foule de morts-vivants. Allez, donnez-lui une bonne leçon !']]
			}
		},
		['halloween_boss_2022_2_2'] = {
			['choice'] = {
				['2'] = [[Un baume pour bec puant.']],
				['1'] = [[Avancer vers les éclairs]],
				['4'] = [[Un poison de putréfaction.']],
				['3'] = [[Un sérum de vérité.']],
				['6'] = [[Un remède aux radiations.']],
				['5'] = [[Un somnifère.']],
				['7'] = [[Soit.']]
			},
			['episode'] = {
				['9'] = [[Et moi qui pensais qu'il allait te poser une colle ! Pas mal, pas mal... Tiens, voici ton prochain contrat.']],
				['6'] = [[Faux. Vous l'ignorez. Eh bien, il faudra revenir une prochaine fois... et voilà ce que je réserve aux imbéciles qui répondent au hasard.'<new_line><new_line>Le Médecin de la peste jeta quelque chose dans son chaudron, créant un nuage de vapeur encore plus épais que la brume environnante. Une fois la vapeur retombée, le Médecin avait disparu et des zombies m'encerclaient.]],
				['10'] = [[Ah, vous revoici. Bien. Allons-nous reprendre notre petite conversation ?']],
				['8'] = [[C'est très drôle de suivre tes aventures, mortel ! Fais marcher ta cervelle, et retente ta chance—le Médecin ne risque pas de quitter la région...']],
				['7'] = [[Correct. C'est la recette d'une potion d'endormissement si puissante qu'on l'appelle Sirop du Mort vivant. Vous n'êtes pas aussi bête que je le pensais... J'ai hâte de disséquer votre cerveau—et c'est précisément ce que je compte faire !']],
				['2'] = [[Le Médecin de la peste s'est placé entre deux tombes ouvertes, et il verse quelque chose dans un chaudron bouillonnant. Il faut mettre un terme à tout ça.]],
				['1'] = [[Dans le coin le plus éloigné du cimetière, les pierres tombales sont éclairées par des éclairs verts. C'est sans doute là que le Médecin de la peste mène ses viles expériences.]],
				['4'] = [[Le Médecin secoua la tête avec déception alors que j'attrapais mon arme. Il fit un geste de la main et disparut dans le brouillard vert qui l'enveloppait. Je vais devoir retenter le coup quand il reviendra au cimetière.]],
				['3'] = [[Eh bien, qu'avons-nous là ? Voilà un bien curieux spécimen... Parlons un peu, avant que je m'occupe de votre cas.']],
				['5'] = [[J'ai épuisé ma réserve de potions, j'en concocte d'autres. Dites-moi, qu'obtiendrais-je en mélangeant de la racine d'asphodèle en poudre dans une infusion d'armoise ?']]
			}
		},
		['halloween_boss_2022_3'] = {
			['choice'] = {
				['1'] = [[Montre-moi le chemin.']]
			},
			['episode'] = {
				['1'] = [[Enfin, quelqu'un vole à mon secours ! Des vampires nous ont attaqués, et je suis la seule à avoir survécu ! Par pitié, aidez-moi à rentrer chez moi. Vite, avant qu'ils reviennent finir le travail !']]
			}
		},
		['boatman_camp_chat'] = {
			['episode'] = {
				['2'] = [[De la soupe ?' Le batelier m'a souri. 'Pas besoin de faire autant d'histoires. J'ai entendu parler d'un institut scientifique qui devait sans doute travailler sur ce virus. Mais c'est un endroit dangereux. Les radiations, les mutants... les gens. Vous feriez mieux de ne pas vous en approcher.']],
				['1'] = [[Le batelier préparait de la soupe de poisson dans une grande cuve. Opinant du chef :<new_line>'Alors, c'est fait. Vous avez trouvé ce que vous cherchiez à Belomorsk ?']],
				['3'] = [[Vous n'allez jamais lâcher l'affaire ! Je vous l'ai dit, c'est dangereux et vous n'êtes pas prêt ! Restez encore un peu, commencez par affiner vos compétences. Aidez les gens du coin, acceptez des missions au bar. Vendez de la marchandise, discutez avec le contremaître. Et puis, quand vous serez prêt, revenez me voir. Je vous dirai tout.']]
			},
			['choice'] = {
				['2'] = [[Où est l'institut ?']],
				['1'] = [[Non, je veux savoir ce qui est arrivé. Qui a provoqué la guerre, ce que cache l'épidémie...']],
				['3'] = [[C'était convaincant. À la prochaine.']]
			}
		},
		['halloween_search_medium_2'] = {
			['episode'] = {
				['2'] = [[Bon, une pincée de ceci... Un zeste de cela... Abracadabra, peine et malheur... Feu, brûle, et chaudron, bouillonne ! Je pense que c'est prêt. Goûte un peu, gamin.']],
				['1'] = [[Alors, on a trouvé les herbes ?']],
				['3'] = [[Sans moi, tu ne verras pas l'ombre d'une récompense, gamin. Elle est bien cachée. Allez, bois.']]
			},
			['choice'] = {
				['2'] = [[Pas question ! Par ici la récompense, sinon...']],
				['1'] = [[Pas maintenant.' (Partir)]],
				['3'] = [[Boire la potion]]
			}
		},
		['toy_story'] = {
			['name'] = [[Terem]],
			['episode'] = {
				['2'] = [[Arrêtez !, a hurlé Snegurochka, arrivant précipitamment dans le terem. J'étais au beau milieu de carcasses de jouets et le Casse-Noisettes s'est alors immobilisé, son sabre prêt à frapper.]],
				['1'] = [[La porte du terem magnifiquement ornementé était démolie et à l'intérieur, régnait un chaos indescriptible. Je n'avais pas eu le temps de prendre mes repères quand j'ai entendu une voix étrange s'élever depuis une table retournée. «Ils sont revenus ! Vite, chargez le canon ! Prêt ! Visez ! Feu !» Après quoi, une détonation et un projectile de petite taille m'ont frôlé l'oreille. Qu'est-ce qui se passe ici ?]],
				['3'] = [[Une heure plus tard, après avoir fait un peu de ménage, nous avons pu déterminer sans l'ombre d'un doute que le terem avait été la cible de brigands des glaces. Ils ont tout saccagé, mais n'ont rien volé. À mon arrivée, les jouets pensaient que j'étais l'un des brigands. Snegurochka m'a promis de découvrir où ces vauriens avaient élu domicile.]]
			}
		},
		['ice_brigands'] = {
			['episode'] = {
				['1'] = [[Les brigands ont tendu une embuscade ! Ils ont surgi des congères et me sont tombés dessus. Leur visage blême était dénué de toute humanité.]]
			}
		},
		['halloween_boss_2022_5'] = {
			['choice'] = {
				['2'] = [[Continuer]],
				['1'] = [[Se retourner]]
			},
			['episode'] = {
				['2'] = [[J'ai couru vers l'homme, mais il était trop tard—il avait déjà perdu trop de sang et succombé à ses blessures.<new_line>Derrière moi, un rire retentit.]],
				['1'] = [[Dès mon arrivée, j'ai rencontré une bande de monstres encerclant quelqu'un. Je n'avais pas le choix—ils avaient déjà remarqué ma présence.]],
				['3'] = [[Tu n'as pas compris, mortel ? Si un nom figure sur cette page, c'est qu'il n'y a plus d'espoir pour lui. Ne perds pas de temps—Igor prélève les organes des cadavres frais. Continue de chercher.']]
			}
		},
		['halloween_boss_2022_5_2'] = {
			['choice'] = {
				['2'] = [[Retirer l'obstacle avec une pelle]],
				['1'] = [[Retirer l'obstacle avec les mains]],
				['4'] = [[S'approcher encore plus...]],
				['3'] = [[S'approcher en rampant]],
				['6'] = [[Hein ?']],
				['5'] = [[Attaquer depuis les fourrés]],
				['7'] = [[Ne bougez plus !']]
			},
			['episode'] = {
				['9'] = [[Après m'être occupé du monstre, j'ai voulu chercher l'homme qui s'était enfui. Je l'ai trouvé non loin : apparemment, dans sa course éperdue, il était tombé dans un ravin. Son cou était brisé.]],
				['6'] = [[J'ai vu un petit bossu et un énorme monstre humanoïde. Les deux se sont tournés vers moi. Surpris, le monstre a lâché un homme contusionné qui a pris la fuite sans demander son reste. Après quoi, le visage du monstre s'est fendu d'un sourire menaçant :<new_line>'Igooooor ! V'là une toute autre histoire. Je veux SON foie à lui !']],
				['5'] = [[J'étais presque à côté d'eux. Le zézaiement a repris de plus belle :<new_line>'Quel public, ze te le demande ? Et pour aller où ? Tu es un monztre, et ze te propose zuste d'avoir un foie !']],
				['8'] = [[Le bossu se précipita vers la grosse électrode et plusieurs autres créatures, visiblement d'autres expériences du laboratoire de Victor Frankenstein, rejoignirent le monstre.]],
				['7'] = [[Le bossu zézaya :<new_line>'Un beau zpézimen, il est vrai. Ze vais te montrer quelque soze, l'ami—vise un peu cette zolie électrode là...']],
				['2'] = [[À l'intérieur se trouvait un corps sans vie. Il semblait encore intact, mais mon dosimètre commença à s'agiter furieusement. Cette personne devait être enfermée là-dedans, elle est morte de faim ou n'a pas supporté les radiations. Je ferais mieux de partir pour éviter de connaître le même sort.]],
				['1'] = [[Quelqu'un doit mourir dans ce bâtiment. Or, je ne vois personne... Il y a bien une pièce dont l'entrée est bloquée, peut-être qu'elle se trouve derrière ?]],
				['4'] = [[Au zézaiement a répondu une voix tonitruante, comme un grondement :<new_line>'Igoooor, ça vient d'un ivroooogne. Je n'en veux paaaas. J'aurais honte de sortir en public avec un foie comme çaaaa !']],
				['3'] = [[Ayant entendu une voix qui zozote, j'ai décidé de me frayer un chemin à travers les fourrés pour ne faire peur à personne.<new_line><new_line>'Z'ai l'imprezzion que tu t'en fous ! C'est un FOIE, grand dézénéré !']],
				['10'] = [[Tu vois, nul n'échappe à son destin. Surtout quand il est écrit dans le Livre de la mort. Viens me retrouver dès que tu auras le temps, le dernier contrat t'attend.']]
			}
		},
		['emba_isaev_tula'] = {
			['episode'] = {
				['9'] = [[Nous avons découvert que le caractère unique du mycélium n'est pas seulement dû à son impact sur l'environnement ; ses gènes ont une facilité de modification déconcertante.<new_line>Cependant, une fois le génome modifié, des mutations incontrôlables se déclenchent. Elles sont très difficiles à prévoir.']],
				['6'] = [[Oui, cette biostation a des tourelles de combat automatiques. Elles vous seront utiles au combat si vous parvenez à les utiliser. Le navigateur s'en chargera. Allez-y et commencez à chercher la boîte noire.']],
				['5'] = [[De nombreuses informations scientifiques. Cela nous aidera certainement dans nos recherches. De la partie appliquée, nous avons réussi à déterminer l'emplacement de la cité des sciences et le fait qu'un spécimen de combat se trouve à la biostation.<new_line>Il est marqué comme « Objet - Briseur d'os », avec l'indication de la présence d'une souche de mycélium intégrée dans le système nerveux.']],
				['8'] = [[Alors il y a une chance qu'ils soient encore en vie... Pendant le redéploiement, j'essaierai de les recontacter. Maintenant, je peux répondre à vos questions.']],
				['7'] = [[Un biomutant élevé artificiellement dans le laboratoire ! C'est fou.<new_line>Oh, et avez-vous vu là-bas les corps de mes collègues de la première expédition ProgrèS ?']],
				['2'] = [[Nous recherchons sans cesse des documents, mais c'est une tâche longue et fastidieuse : on ne sait jamais à l'avance quel type de renseignements mèneront aux bonnes archives, ou s'ils indiquent où les chercher.<new_line> Par conséquent, je vous demande maintenant d'assister mon personnel dans leurs missions. Vous les trouverez dans le bar local.']],
				['1'] = [[Parfait, vous voilà. Prêt à vous mettre au travail ?']],
				['4'] = [[Félicitations, vous avez terminé avec succès la phase de recherche. Il faut maintenant commencer à chercher la boîte noire à la biostation de la cité des sciences.']],
				['3'] = [[Nos éclaireurs vous aideront à localiser les documents secrets. Allez au bar et suivez leurs instructions.<new_line>Vous les avez déjà trouvés ? Si oui, donnez-les au biologiste pour l'aider dans ses recherches.']],
				['12'] = [[Bon, vous êtes prêt à poursuivre l'expédition ?']],
				['10'] = [[Malheureusement, cette boîte noire est sérieusement endommagée, et nous devons la reconstituer. En attendant, nous avons pu obtenir des précisions sur certains itinéraires logistiques.<new_line>Maintenant, il faut aller à Kiev. Nous partirons dès que vous serez prêt.']],
				['14'] = [[D'abord, nous aurons besoin des documents secrets. Ils renferment des informations sur les recherches du projet E.M.B.A. ainsi que le code d'accès décrypté au laboratoire de la cité des sciences locale.']],
				['13'] = [[J'ai failli oublier ! Nous avons déchiffré le code d'accès pour le compartiment secret du labo. Tenez, je l'ai noté ici.'<new_line>(Tend un bout de papier avec le code)]]
			},
			['choice'] = {
				['2'] = [[D'accord. Qu'avez-vous appris des documents ?']],
				['4'] = [[Faire un rapport]],
				['3'] = [[Des informations pour savoir comment s'occuper de cet « objet » ?']],
				['6'] = [[Comment obtenir ces documents ?']],
				['5'] = [[Non, il n'y avait aucun corps.']],
				['7'] = [[Qu'avez-vous appris d'autre ?']]
			}
		},
		['new_year_boss_2'] = {
			['choice'] = {
				['5'] = [[En d'autres termes...']],
				['16'] = [[Euh, d'accord...' (S'allonger près du trou)]],
				['15'] = [[Que feraient-ils par ici ?']],
				['9'] = [[Il suffit juste d'attendre ?']],
				['17'] = [[Faire le mort]],
				['18'] = [[Suivre le fuyard]],
				['6'] = [[Cacher le trou avec de l'herbe]],
				['10'] = [[Une crise cardiaque ? Hein ?']],
				['8'] = [[Il faut faire profil bas et tendre une embuscade ?']],
				['7'] = [[Euh, c'est l'heure de la sieste ?']],
				['2'] = [[Il faut creuser un trou à cet endroit ?']],
				['1'] = [[Il faut affronter quelqu'un ici ?']],
				['4'] = [[Creuser un trou]],
				['3'] = [[Vous n'êtes pas un peu fou ?']],
				['12'] = [[Notre mission... creuser un trou ?']],
				['11'] = [[Vous... savez parler ?']],
				['14'] = [[Bien, mettons-nous au travail.']],
				['13'] = [[Plaît-il ?']]
			},
			['episode'] = {
				['20'] = [[Pendant le combat, un membre de la bande s'est extirpé pour prendre la fuite !]],
				['19'] = [[C'était à peine croyable ! À peine allongé près du trou, j'ai entendu venir le fracas et le rugissement de moteurs.]],
				['16'] = [[Ayez confiance en moi. Je n'ai guère le temps de vous enseigner l'art de percevoir l'autosimilarité fractale de l'unité de l'être.']],
				['15'] = [[Alors, nous aurons besoin d'un leurre. Allongez-vous près du trou pour attirer les ravisseurs.']],
				['9'] = [[Greeeuhh uhh...'<new_line>Père Nomade se lève, chancèle et tombe abruptement sur le dos.]],
				['17'] = [[Nul ne mordra à l'hameçon si vous restez simplement allongé là ! Pour incarner un animal blessé, il faut devenir soi-même un animal blessé.']],
				['5'] = [[Père Nomade inspecta minutieusement le trou creusé.<new_line>'Ooh. Gruh vah, gruh vah !'<new_line><new_line>En nous regardant, il arracha plusieurs longues tiges d'herbe et les disposa au-dessus du trou.]],
				['6'] = [[Oh, c'est limpide ! Il veut recouvrir le trou avec quelque chose. Pour le dissimuler !'<new_line>Père Nomade hocha la tête en souriant.]],
				['10'] = [[Père Nomade se lève lentement, prend sa massue, et... me frappe à la tête !]],
				['8'] = [[Sans se lever, Père Nomade secoue sa tête.<new_line><new_line>'Non, non. C'est autre chose... Il faut essayer de réfléchir comme si tu étais un gros... euh, je veux dire, de faire une autre suggestion.']],
				['7'] = [[Voilà que le Père Nomade s'allonge à côté du trou en écartant les bras et les jambes.]],
				['2'] = [[Il secoue la tête et essaie de gratter la couche de terre en surface avec le manche de sa massue.]],
				['1'] = [[Groh-ooh-ooh ! Ahhh buh.'<new_line>Père Nomade frappe le sol de sa massue. Il semble vouloir nous dire quelque chose...]],
				['4'] = [[Tu as compris, hein ? Il a l'intention de creuser un trou pour faire un piège ! La nature est une chose étonnante, mon vieux.']],
				['3'] = [[Uiiii !'<new_line>Le Père Nomade hoche la tête avec un grand sourire. On dirait qu'il doit creuser un trou ici.]],
				['12'] = [[En effet. Voyez-vous, un individu ne peut acquérir suffisamment de connaissances pour transcender son propre cadre mental quand il se situe dans un environnement étranger.<new_line>Enfin, revenons à notre mission. Le temps presse.']],
				['11'] = [[Père Nomade pose une sorte de chiffon sur ma tête.<new_line>'Pardonnez-moi, l'ami. Au bout d'un moment, il est devenu évident que nos efforts de communication étaient dans une impasse.']],
				['14'] = [[Quand vous parlez à Père Négoce, il ne perçoit désormais plus que les baragouinages que vous entendiez en m'écoutant, juste avant...'<new_line><new_line>Le Père Négoce nous regarda tous deux pendant quelques instants, fit un signe de la main et s'en alla.]],
				['13'] = [[Grruuhhh, ahh, ho-ho-ho ! Grah eeuuh groogah, euh.']]
			}
		},
		['halloween_def_easy2'] = {
			['episode'] = {
				['9'] = [[... Trois !']],
				['6'] = [[On dirait que tout est prêt. On saute à trois. Prêt ?']],
				['10'] = [[Oh, merci beaucoup, mon garçon !']],
				['8'] = [[Deux...']],
				['7'] = [[Un...']],
				['2'] = [[Pendant que la sorcière détournait l'attention des monstres, j'ai couru droit vers la hutte. Une fois le seuil franchi, la maison s'est dressée sur ses pattes.<new_line><new_line>'Voilà qui est mieux ! Tu viens bien aider une vieillarde ? Ensemble, on les repoussera plus vite.']],
				['1'] = [[Une foule se précipita autour de la cabane comme devant la scène d'un concert de rock.<new_line>La Vieille sorcière sortit sa tête par la fenêtre :<new_line>'Entre vite, gamin ! Je vais les distraire !']],
				['4'] = [[Comme tu veux, gamin. Une petite potion avant le combat ? Par contre, je n'ai pas encore eu l'occasion de l'essayer.']],
				['3'] = [[J'ai concocté en vitesse une potion spéciale qui a dû leur plaire un peu trop. Vois-tu, j'aime recevoir des gens, mais seulement sur invitation. J'ai un piège intéressant, fabriqué à partir de champignons, qui pourrait nous servir. Je le pose, ou tu crois qu'ils ne feront pas le poids ?']],
				['11'] = [[C'est mon dos. Je ne suis plus toute jeune, tu sais. Tu ne m'en veux pas, hein, gamin ? Tu t'en es bien sorti ! Prends ta récompense, et pars en paix.']],
				['5'] = [[Brave gamin ! On dirait que tout est prêt. On saute à trois. Prêt ?']]
			},
			['choice'] = {
				['9'] = [[Pourquoi ne pas avoir sauté ?']],
				['6'] = [[Non, merci.']],
				['5'] = [[Boire la potion]],
				['8'] = [[Sauter]],
				['7'] = [[....]],
				['2'] = [[D'où sortent ces bestioles ?']],
				['1'] = [[Courir vers la cabane]],
				['4'] = [[Ils ne feront pas le poids.']],
				['3'] = [[Posez le piège.']]
			}
		},
		['halloween_energy_hard'] = {
			['episode'] = {
				['9'] = [[Ouais ! Euh... émigrer ! Après, je pourrais dépenser l'argent dans un autre monde. Buvons à ça !'<new_line>L'agriculteur remplit les deux verres à liqueur.]],
				['15'] = [[J'sais pas... hips. Tant qu'à faire, autant siffler la bouteille du magicien.'<new_line>L'agriculteur remplit les deux verres à liqueur.]],
				['16'] = [[Tu sais... t'es un brave type. J'devrais peut-être... te donner... tout ce que j'ai...'<new_line>Sur ce, l'agriculteur s'évanouit.]],
				['17'] = [[Je me suis réveillé dans un lieu étrange et vide. J'ai mal au crâne. Qu'est-ce que je fais là ? Que s'est-il passé hier ?]],
				['10'] = [[J'ai un ami... magicien ! Il m'a donné une note. Il me doit une fière chandelle... Le texte est assez brouillon, par contre... Si tu arrives à lire l'incantation, il se montrera...']],
				['6'] = [[Qui en trouverait l'utilité ? J'ai vérifié... Le métal ne vaut rien. Personne ne s'y intéressera. Encore quelques verres, pour faire tourner nos cellules grises.'<new_line>L'agriculteur remplit les deux verres à liqueur.]],
				['5'] = [[J'essaie de décider... ce que je vais faire de mes richesses...']],
				['8'] = [[Hein ?']],
				['7'] = [[Pfiou... c'est de la bonne. Ces... euh, ces bouffons m'ont dit... que l'argent valait quelque chose. De quoi acheter une ferme. Mais comment ?']],
				['2'] = [[L'agriculteur posa un autre verre à liqueur sur la table et le remplit de gnôle.<new_line>'Allez... c-cul sec !']],
				['1'] = [[Ah, c'est toi. Je veux dire, celui dont... Bah, qu'importe... Tu importes peu... Non, bon sang... J'ai touché le jackpot, et plus jamais je ne serai agriculteur ! Sauf que la monnaie est étrangère. Je ne vois pas du tout ce que je peux en faire, j'essaie de trouver une solution. Réfléchissons à ça... ensemble.']],
				['4'] = [[Allez... C'est un truc qui fait pousser les poils du torse... On trinque, à l'amitié !'<new_line>L'agriculteur remplit les deux verres à liqueur.]],
				['3'] = [[J'ai sifflé le verre d'une traite. La gnôle était extrêmement forte, elle me brûla les entrailles.<new_line>'Alors... T'en d-dis quoi... de ma... hips... de ma gnôle ?']],
				['12'] = [[C'est beau de rêver. La politique d'immigration des autres mondes est très stricte. On peut partir en vacances avec son argent, mais pour ça, il faut signer un tas de paperasses. Maintenant, hors de ma vue !']],
				['11'] = [[J'ai lu la note, et un magicien est apparu dans la pièce.<new_line><new_line>'C'est pour quoi, manant ?']],
				['14'] = [[J'ai l'air de tenir une taverne, moi ?! Bah, soit. Abracadabra. Voilà. J'ai jeté un sortilège de feu d'enfer sur ta gnôle. Si tu me rappelles, tu subiras le même sort.'<new_line>Le magicien disparut aussi vite qu'il était apparu.]],
				['13'] = [[Mince... Ce serait possible, au moins, de faire apparaître une boisson assez forte ? J'ai de quoi payer...'<new_line>L'agriculteur donna des pièces au magicien.]]
			},
			['choice'] = {
				['9'] = [[L'agriculteur veut... euh... émigrer...']],
				['6'] = [[Euh...']],
				['10'] = [[Qu'allez-vous faire maintenant ?']],
				['8'] = [[Lire la note]],
				['7'] = [[Euh... émigrer.']],
				['2'] = [[Pas le temps.']],
				['1'] = [[Continuez.']],
				['4'] = [[Ça tabasse les boyaux.']],
				['3'] = [[Boire]],
				['12'] = [[Se lever et partir]],
				['11'] = [[Prendre les richesses]],
				['5'] = [[On pourrait... le mettre en gage ?']],
				['13'] = [[Abandonner]]
			}
		},
		['construction_team'] = {
			['episode'] = {
				['26'] = [[Dans la zone déjà déboisée, on ne peut pas faire un pas sans trébucher sur une souche ou tomber dans un trou. La brigade veut installer des cabanes, on attend de moi que j'aide à nettoyer la parcelle.]],
				['24'] = [['Vous nous sauvez la mise !']],
				['32'] = [['Bientôt, le chantier sera fini et des combattants seront postés ici. Il faut d'abord repérer ce qui se passe dans la région. Nos gars ont remarqué des activités suspectes, ici et là. Allez voir ce qui se passe. Mais si vous n'avez pas le temps, aidez-nous d'abord à finir le travail. Ensuite, je trouverai quelqu'un d'autre à envoyer.']],
				['22'] = [[L'exploitation forestière n'avance pas assez vite. Les ouvriers sont fatigués, ils ont besoin de mon aide.]],
				['20'] = [['Rien de grave—il n'y a plus de cigarettes. Mais je ne suis pas efficace quand je n'ai rien à fumer. Si vous en aviez en rab, ça me sauverait la mise.']],
				['10'] = [[Le contremaître fit l'inspection des travaux finis et m'appela :<new_line><new_line>'Si seulement tous ceux qui venaient ici étaient de votre trempe ! Reposez-vous. J'aimerais discuter d'un autre travail avec vous, en aparté — si vous avez le temps.']],
				['30'] = [['Nous avons déjà essayé de planter des choses à titre expérimental. Et puis, les vermines sont venues tout gâcher. Vous êtes doué pour le combat, alors vous devriez résoudre le problème. Mais si vous n'avez pas le temps, aidez-nous d'abord à finir le travail. Ensuite, on s'occupera de leur cas nous-mêmes.']],
				['12'] = [[Nous avons fini de déblayer à l'arrivée d'un convoi de tracteurs équipés de remorques. Le contremaître m'a tapé sur l'épaule :<new_line><new_line>'Bon, grâce à vous, on peut à nouveau tenir les délais. Voici ce qu'on vous doit pour votre travail. Et un petit supplément pour avoir contribué à résoudre le problème.']],
				['14'] = [[Un camion est apparu juste devant moi, aux portes du futur avant-poste. Un type a sauté des marches :<new_line>'Ah, des renforts ! Parfait. C'est moi qui m'occupe de l'équipe de chantier. Il y a du boulot pour tout le monde, vous êtes prêt à commencer ?']],
				['33'] = [[L'équipe fait une pause cigarette. Mais personne n'a de quoi fumer.]],
				['28'] = [['Les bandits du coin volent nos outils et s'en prennent à nos ouvriers. Vous êtes doué pour le combat, alors vous devriez résoudre le problème. Mais si vous n'avez pas le temps, aidez-nous d'abord à finir le travail. Ensuite, on s'occupera de leur cas nous-mêmes.']],
				['16'] = [[Le contremaître fit l'inspection des travaux finis et m'appela :<new_line><new_line>'Excellent, ça fera l'affaire. Reposez-vous un peu. J'aimerais discuter d'un autre travail avec vous, en aparté — si vous avez le temps.']],
				['9'] = [['Il faut d'abord dégager toute cette zone, là-bas. Vous voyez ? Hé, Vasilitch ! Donne ta tronçonneuse à ce type, il prend le relais.']],
				['17'] = [[Le contremaître nous montre des feuilles couvertes de lignes :<new_line>'Voilà, ce sont les plans qu'ils ont envoyés. Nous allons bâtir une tour, et nos gars devront la défendre. Il faudra tous qu'on fasse un gros effort.']],
				['18'] = [[Nous avons érigé une tour de guet, petite mais solide. Le contremaître fit lui-même l'inspection des travaux, puis se tourna vers moi :<new_line><new_line>'Merci pour vos efforts. Passez le bonjour aux gars du camp. Voici ce qu'on vous doit pour votre travail. Et un petit supplément pour avoir contribué à résoudre le problème.']],
				['6'] = [[La future ferme collective est très bien protégée. Le contremaître inspecte les résultats :<new_line><new_line>'Bon, les petits gars savent bosser quand quelqu'un d'expérimenté est là pour les surveiller ! Voici ce qu'on vous doit pour votre travail. Et un petit supplément pour avoir contribué à résoudre le problème.']],
				['7'] = [[La future ferme collective est très bien protégée. Le contremaître inspecte les résultats :<new_line><new_line>'Bon, les petits gars savent bosser quand quelqu'un d'expérimenté est là pour les surveiller ! Voici ce qu'on vous doit pour votre travail.']],
				['23'] = [[Le camion est là, chargé à plein. Sans mon aide, les ouvriers n'ont jamais trouvé le temps de s'en occuper.]],
				['25'] = [[Ils ont choisi de travailler une terre vierge dans un lieu qui ne manque pas de dangers. Je dois les aider à construire une vraie barrière de protection.]],
				['21'] = [[Les ouvriers s'affairent toujours sur le toit de l'entrepôt. Il est temps de rejoindre la cause commune.]],
				['31'] = [['Des mutants sont apparus dans la forêt. Il y en a un groupe tout près, et les sentinelles n'aident pas. Vous êtes doué pour le combat, alors vous devriez résoudre le problème. Mais si vous n'avez pas le temps, aidez-nous d'abord à finir le travail. Ensuite, on s'occupera de leur cas nous-mêmes.']],
				['29'] = [['On devait recevoir plus de matériel, mais le camion n'est pas encore arrivé. Il est peut-être coincé quelque part, en bord de route. Allez récupérer notre chargement, ce n'est pas si énorme que ça.']],
				['5'] = [[Le contremaître examine les bords du champ défriché :<new_line>'La terre est bonne ici, mais c'est un environnement sauvage. Il faut placer la clôture intelligemment. Vous feriez mieux d'aider les jeunes à ce niveau-là—ils n'ont jamais rencontré de vrais mutants.']],
				['8'] = [[Dans l'exploitation forestière, j'ai été accueilli par un type en combinaison :<new_line>'Salut, je suis le contremaître ! Vous tombez à pic. Nous prenons du retard sur une zone difficile. Prêt à vous mettre au travail ?']],
				['19'] = [[Nous avons érigé une tour de guet, petite mais solide. Le contremaître fit lui-même l'inspection des travaux, puis se tourna vers moi :<new_line><new_line>'Merci pour vos efforts. Passez le bonjour aux gars du camp. Voici ce qu'on vous doit pour votre travail.']],
				['2'] = [['Nous avons lancé la construction d'un entrepôt. Aidez nos gars, il faut qu'on termine la toiture.']],
				['1'] = [[Un homme s'avança vers moi derrière un tas de matériaux de construction :<new_line><new_line>'Bonjour, je suis le contremaître. Ici se trouve la future ferme collective—une petite parcelle de terre non contaminée que nous avons dénichée. Vous venez donner un coup de main ?']],
				['4'] = [[Le contremaître fit l'inspection des travaux finis et m'appela :<new_line><new_line>'Merci. Sans vous, ça aurait pris beaucoup plus de temps. Bon, c'est l'heure de la pause cigarette. Venez que je vous montre la suite de notre chantier de construction.']],
				['3'] = [[Le contremaître fit l'inspection des travaux finis et m'appela :<new_line><new_line>'Merci. Sans vous, ça aurait pris beaucoup plus de temps. Bon, c'est l'heure de la pause cigarette. J'aimerais discuter d'un autre travail avec vous, en aparté — si vous avez le temps.']],
				['15'] = [['Vous voyez où ils ont posé ces tôles en acier et ces planches ? Aidez-les à tout décharger, nous allons bâtir une tour.']],
				['11'] = [['Voilà, ça fera un peu de place pour des cabanes et une grange. Nous parcourons 10 km par jour en ce moment, entre ici et l'ancien chantier.<new_line><new_line>Aidez-nous à niveler le terrain, les tracteurs pour les cabanes ne devraient pas tarder.']],
				['27'] = [[Les ouvriers ont déjà commencé à bâtir la tour, et ils attendent tous que je vienne les rejoindre.]],
				['13'] = [[Nous avons fini de déblayer à l'arrivée d'un convoi de tracteurs équipés de remorques. Le contremaître m'a tapé sur l'épaule :<new_line><new_line>'Bon, grâce à vous, on peut à nouveau tenir les délais. Voici ce qu'on vous doit pour votre travail.']]
			},
			['choice'] = {
				['2'] = [[Chercher des cigarettes]],
				['1'] = [['Ah, quel genre de travail ?']],
				['4'] = [[Donner les cigarettes]],
				['3'] = [['En quoi consiste le boulot ?']],
				['6'] = [[Répondre à la demande du contremaître]],
				['5'] = [[Se mettre au travail]]
			}
		},
		['halloween_energy_medium'] = {
			['episode'] = {
				['9'] = [[En prenant une autre caisse, quelque chose me poussa violemment dans le dos. Ma charge tomba sur le sol avec fracas.<new_line>'Nom d'un balai, qu'as-tu fait ?! Mais qu'as-tu fait ?'<new_line>]],
				['5'] = [[Nous sommes arrivés dans un entrepôt bien entretenu. La Vieille sorcière murmura : <new_line>'Prends cette caisse et jette-la dans le portail. Mais ne fais pas de bruit.'<new_line>]],
				['6'] = [[J'ai jeté la caisse dans le portail. La Vieille sorcière fit de même avec une agilité déconcertante.<new_line>'Maintenant, celle-là.']],
				['10'] = [[Les portes de l'entrepôt s'ouvrirent et un garde apparut à la porte :<new_line>'Hé ben mince alors, qu'avons-nous là ?! Des voleurs ? Je vais vous montrer ce qui leur arrive, aux voleurs !'<new_line>]],
				['8'] = [[J'ai jeté une autre caisse dans le portail.<new_line>'Très bien, on peut y aller.'<new_line>]],
				['7'] = [[Parfait, celle-ci aussi.'<new_line>La sorcière pointa du doigt une autre caisse.<new_line>]],
				['2'] = [[Alors, n'es-tu pas un brave petit gars ? Accorde-moi juste une seconde.'<new_line><new_line>La Vieille sorcière retira le couvercle d'un grand chaudron et se dépêcha d'y jeter une poignée d'herbes.]],
				['1'] = [[Ah, tu tombes à pic, gamin ! Donne-moi un coup de main, hein ? J'ai vraiment besoin d'un costaud pour m'aider à déplacer une partie de mes affaires. Impossible d'y arriver seule.']],
				['4'] = [[Et alors, on a la frousse ? Pas besoin. Je suis une gentille sorcière. Je ne te veux aucun mal.'<new_line>La Vieille sorcière fit un bond agile dans le chaudron et disparut.]],
				['3'] = [[Voilà, gamin, j'ai fini. C'est un portail qui nous évitera d'avoir à faire toute une trotte pour aller chercher mes affaires. Saute dedans. Je te suis.']],
				['12'] = [[Merci beaucoup, gamin. Tu es un brave garçon.']],
				['11'] = [[Pendant que je combattais le garde, la sorcière continuait à trimballer des caisses avec entrain.<new_line><new_line>'C'est bon, on peut partir maintenant.'<new_line>]],
				['14'] = [[J'ai sauté dans le portail, qui m'a ramené dans la cabane de la sorcière. Je crois qu'elle m'en veut. Je ferais mieux de vider les lieux rapidement.]],
				['13'] = [[Chuuut... On récupère mes affaires, gamin. Je t'ai dit. Si tu fais le moindre bruit, je vais devoir sévir.']]
			},
			['choice'] = {
				['2'] = [[D'accord, je vous aide.']],
				['1'] = [[Il y a quelque chose de louche. Que fait-on dans un entrepôt ?']],
				['4'] = [[Sauter dans le chaudron]],
				['3'] = [[Attendre]],
				['6'] = [[Prendre la caisse et la placer dans le portail]],
				['5'] = [[Non, vous d'abord.']],
				['8'] = [[Sauter dans le portail]],
				['7'] = [[Je n'ai rien fait ! Vous m'avez poussé !']]
			}
		},
		['newyearEvent_time_easy'] = {
			['episode'] = {
				['9'] = [[Ho-ho-ho ! Joli ! Les mioches vont adorer. Merci d'avoir fait une bonne action !']],
				['6'] = [[Alooors, qu'avons-nous là ? Magnifique ! On jurerait que tu es un artisan de métier. Tu ne cacherais pas des oreilles pointues sous tes cheveux, dis-moi ?']],
				['5'] = [[Voici les matériaux. À mon retour, je compte voir le superbe fruit de ton travail étalé sur la table. Si tu es prêt, dans ce cas, ne perds pas une seconde : au boulot.']],
				['8'] = [[Voici les matériaux. Si tu es fatigué, fais une pause... une courte pause. Et remets-toi au travail direct !']],
				['7'] = [[Bon, eh bien, au tour des jeunes garçons. Les petits mecs aussi ont droit à des cadeaux. J'ai trouvé de quoi faire des soldats de plomb, mais les matériaux sont un peu radioactifs. Tu préfères les nettoyer, ou tu ne crains pas trop de tomber malade ?']],
				['2'] = [[Cet argent doit financer nos opérations, mais les habitants de ce monde demandent bien plus que le coût de main-d'œuvre moyen. Si je commence à les payer autant qu'ils le demandent, l'établissement fera faillite et les enfants devront se passer de la magie de Noël.']],
				['1'] = [[Je suis passé devant un petit bâtiment avec une pancarte indiquant Usine TLtoys.<new_line>'Te voilà enfin, l'ami. J'ai besoin d'aide, c'est urgent. Il n'y a pas d'ouvriers à l'usine ! J'ai vraiment besoin de toi pour faire les trois-huit. Faute de quoi, les enfants seront privés de jouets...']],
				['4'] = [[Tu as du pain sur la planche ! D'abord, il faut fabriquer des cadeaux pour les jeunes filles. Choisis toi-même ce que tu voudrais offrir à ces gamines.']],
				['3'] = [[Ho-ho-ho, voilà ! C'est ça qu'on veut ! Direction le lieu de travail, vite !']]
			},
			['choice'] = {
				['9'] = [[Je vais tout nettoyer' (14 heures)]],
				['6'] = [[Je veux fabriquer des poupées' (8 heures)]],
				['5'] = [[Je veux préparer des gâteaux' (8 heures)]],
				['8'] = [[Finissons-en plus vite' (6 heures) (+radiations)]],
				['7'] = [[Se préparer]],
				['2'] = [[Vous êtes plein aux as. Il n'y a vraiment personne d'autre qui veuille un salaire ?']],
				['1'] = [[D'accord, je veux bien travailler quelques temps.']],
				['4'] = [[Aller sur le lieu de travail]],
				['3'] = [[Je n'ai pas le temps.' (Partir)]],
				['10'] = [[Se mettre au travail (8 heures)]]
			}
		},
		['halloween_energy_easy'] = {
			['episode'] = {
				['9'] = [[D'abord, il faut creuser un jardin. C'est quand vous voulez pour commencer.']],
				['6'] = [[Ce sont les affaires, et les affaires passent avant tout. Bien, voilà. Au travail, maintenant.']],
				['10'] = [[Maintenant, il faut planter les graines. C'est quand vous voulez pour commencer.']],
				['8'] = [[Non... Nous avons peut-être fait une erreur. Il faudra réessayer. Merci beaucoup de votre aide.']],
				['7'] = [[Ô Empereur au visage solaire, reviens vers nous. Pardonne-nous de t'avoir abandonné. Nous avons besoin de toi. Reviens, et nous te serons fidèles !'<new_line><new_line>Le rituel a duré plusieurs minutes, après quoi les têtes-de-citrouilles se sont dispersées, l'air abattu.<new_line>]],
				['2'] = [[Merci beaucoup ! D'abord, il faut creuser un jardin. C'est quand vous voulez pour commencer.']],
				['1'] = [[Bonjour, étranger ! Vous pouvez nous aider ? Notre commandant nous manque vraiment. Sans lui, tout est difficile. Nous voulons en faire pousser un autre. Nous avons déjà trouvé des graines et un jardin, mais notre maladresse nous empêche d'y arriver.']],
				['4'] = [[Beau travail. Il ne reste plus qu'à fertiliser le sol. Vous pouvez demander de l'engrais à l'agriculteur ? Il devrait être chez lui.']],
				['3'] = [[Parfait ! Maintenant, il faut planter les graines. C'est quand vous voulez pour commencer.']],
				['11'] = [[Tout est prêt, ou presque. Il faut fertiliser le sol.']],
				['5'] = [[N'oubliez pas de bien fermer. Cet engrais vaut son pesant d'or pour les têtes-de-citrouilles. J'y ai travaillé jour et nuit.']]
			},
			['choice'] = {
				['2'] = [[Pas le temps.' (Partir)]],
				['1'] = [[De la maladresse, hein ? Bien, je vous aiderai.']],
				['4'] = [[D'accord.' (Aller voir l'agriculteur)]],
				['3'] = [[Se lancer]],
				['6'] = [[Est-ce qu'on a retrouvé le commandant ?']],
				['5'] = [[Pourquoi leur venir en aide ?']]
			}
		},
		['new_year_boss_2_2'] = {
			['choice'] = {
				['2'] = [[Hé, ça suffit !']],
				['1'] = [[Qu'est-ce que...']],
				['4'] = [[Laissez-le tranquille !']],
				['3'] = [[Demander à l'ancien ce qui se passe]],
				['6'] = [[Que lui avez-vous fait ?!']],
				['5'] = [[Sauter à travers la fumée]],
				['8'] = [[Tout ça est l'œuvre de ce vieillard !']],
				['7'] = [[Est-elle toxique ?']]
			},
			['episode'] = {
				['9'] = [[Malédiction ! Je n'ai pas pu vous aider à cause de cette horrible fumée.']],
				['6'] = [[Tout un tas de décorations de Noël alimentèrent les flammes, qui déversèrent un flot de fumée âcre encore plus épais que le premier. Le vieil homme tira à lui le pauvre ancien du village.]],
				['5'] = [[Tout d'un coup, le vieil homme se tourna vers moi et se mit à rire.<new_line>'Ah, tu veux parler à l'ancien ? Hé, les mecs, jetez tout ce bric-à-brac au feu !']],
				['8'] = [[En réponse, le vieil homme éclata de rire derechef. Toujours en riant, il courut jusqu'à sa moto et partit de la place en trombe.<new_line>Les voyous, menés par l'ancien défiguré, s'approchèrent lentement vers moi.]],
				['7'] = [[La fumée se dissipa. À côté du vieil homme, là où se trouvait l'ancien, se tenait désormais une sorte de monstre.<new_line>'Les mecs, la bande du Père Fouettard a une nouvelle recrue ! Le bleu, ta première mission sera d'éliminer ce pauvre type !']],
				['2'] = [[Sur la place du village, les vandales empilent les décorations de Noël près d'un grand feu. Des riverains se blottissent contre les murs des maisons. Ils sont menés par un homme maussade portant des cadeaux dans les mains.]],
				['1'] = [[Le fuyard me conduisit jusqu'à un village décoré pour les fêtes. Sous mes yeux, des voyous retiraient des guirlandes lumineuses des poteaux, arrachaient des décorations sur les arbres, et disparaissaient derrière des maisons.]],
				['4'] = [[C'était comme si personne ne m'avait entendu. Le cadeau finit dans le feu et des flammes azurées éclatèrent, laissant échapper des volutes de fumée bleue. Le vieil homme et sa bande, les yeux révulsés, se tenaient autour du feu pour inhaler les vapeurs.]],
				['3'] = [[Hé, l'ancien ! T'as récupéré les cadeaux de tout le monde ? Aboule !'<new_line>Le vieillard au masque effrayant arracha des mains de l'homme un paquet emballé, qu'il jeta au feu.]],
				['11'] = [[Oh, vous ne l'avez pas reconnu ? Ce vieil homme, c'était le Père Noël ! J'ignore comment il a pu si mal tourner...<new_line>Je suis incapable même d'imaginer une telle abomination. Peut-être le Père Négoce saura-t-il quoi faire. Nous devons le retrouver.']],
				['10'] = [[Là n'est pas la question. Leur façon de l'ingérer... tout en détruisant tout ce qui a trait à Noël. Votre monde est-il entièrement corrompu ?']]
			}
		},
		['take_stash_quest'] = {
			['episode'] = {
				['9'] = [[J'ai réussi à extirper la caisse des mains du cadavre. Soudain, j'ai un entendu un lourd craquement derrière moi. J'ai fait volte-face. C'était un ours. Il s'est léché les babines, comme s'il avait trouvé son déjeuner.]],
				['6'] = [[C'était compliqué, mais j'ai réussi à vaincre les bandits.]],
				['5'] = [[Les traces m'ont guidé loin dans la forêt jusqu'à un cadavre. Le corps gisait au sol et tenait encore fermement une caisse couverte de terre entre ses bras.]],
				['8'] = [[Alors que j'examinais le corps, j'ai un entendu un lourd craquement derrière moi. J'ai fait volte-face. C'était un ours.]],
				['7'] = [[J'ai la caisse. Mon travail ici est terminé.]],
				['2'] = [[En arrivant à l'endroit indiqué, j'ai vu un trou fraîchement creusé et des traces au sol.]],
				['1'] = [[J'ai trouvé la cachette sans problème.]],
				['4'] = [[Les traces m'ont guidé à travers les bois jusqu'au camp des bandits. Ce sont eux qui avaient pillé les planques, c'est certain. Je devrais retourner à la base et le signaler. En même temps, si je récupère moi-même les marchandises des bandits, je parie qu'ils me paieront plus.]],
				['3'] = [[Celui qui a laissé cette planque a manqué de temps, le trou n'est pas profond. J'ai rapidement trouvé la caisse.]],
				['11'] = [[Ma diversion a fonctionné. L'ours est distrait et ne se soucie plus de moi.]],
				['10'] = [[Et c'est qui le roi de la forêt, maintenant ?']]
			},
			['choice'] = {
				['6'] = [[Fouiller le cadavre]],
				['5'] = [[Suivre les traces de pas au sol]],
				['8'] = [[Prendre la caisse]],
				['2'] = [[Déterrer la planque avec les mains]],
				['1'] = [[Déterrer la planque avec la pelle]],
				['4'] = [[S'assurer d'être prêt au cas où avant de poursuivre]],
				['3'] = [[Prendre la caisse et partir]],
				['12'] = [[Briser le pot de miel sur la pierre]],
				['11'] = [[Lancer le poisson à la face de l'ours]]
			}
		},
		['winter_queen'] = {
			['choice'] = {
				['2'] = [[Pénétrer à l'intérieur]],
				['1'] = [[Partir pour le palais de glace]],
				['4'] = [[Et maintenant ?]],
				['3'] = [[Entrer dans la salle]],
				['5'] = [[Retour]]
			},
			['episode'] = {
				['9'] = [[L'ours nous ramène rapidement. Snegurochka reste sur son dos. «Finissez de décorer le sapin du Nouvel An. En attendant, je vais terminer les préparatifs», me dit-elle avant de s'envoler dans le lointain.]],
				['6'] = [[Soudain, Ded Moroz apparaît à côté de la Reine de l'hiver. Il a dû s'échapper lorsque la glace s'est fissurée. S'emparant du bâton qu'elle tient d'une main léthargique, le vieillard le brise en deux ! À ce moment précis, une explosion retentit.]],
				['5'] = [[Affaiblie par la bataille, la Reine de l'hiver nous lance un regard fielleux. «Qu'il en soit ainsi, mais je vous emporterai avec moi !» En prononçant ces paroles, elle brandit son bâton et en frappe le sol une dernière fois. Un craquement assourdissant retentit et de gigantesques fragments de glace s'écroulent du plafond. Des fissures apparaissent et se répandent d'un mur à l'autre. Le palais commence à s'effondrer.]],
				['8'] = [[Snegurochka se tourne dans ma direction. Des larmes, désormais glacées, sur les joues. «Je sais ce qu'il faut faire», dit-elle. «Nous devons rebrousser chemin le plus vite possible», dit-elle en se précipitant vers la sortie.]],
				['7'] = [[Snegurochka et moi, nous nous retrouvons face à un gigantesque bloc de glace qui retient le plafond, derniers vestiges de la salle du trône. Au loin, j'aperçois les silhouettes gelées de Ded Moroz et de la Reine de l'hiver.]],
				['2'] = [[Acquiesçant de la tête, l'ours prend de l'élan et s'élance dans les airs. Snegurochka et moi-même avons juste le temps de nous accrocher à son dos. Nous sommes au cours d'une aurore boréale sublimée par les étincelles d'un millier d'étoiles. Quelques secondes plus tard, nous nous trouvons aux portes du palais de glace, à l'extrême nord du monde.]],
				['1'] = [[Comme auparavant, j'ai levé l'Étoile polaire en direction du ciel, et Umka est apparu en face de moi. J'ai installé Snegurochka sur son dos, avant d'y grimper à mon tour. Le moment est venu de partir.]],
				['4'] = [[La Reine de l'hiver est assise sur son trône, avec Moroz enchaîné à côté d'elle, exactement comme je les ai vus tous deux dans le miroir. «Donc, vous êtes venus malgré tout», dit la Reine de l'hiver, remuant à peine les lèvres. «Qu'importe, vous ferez partie de ce palais.» En prononçant ces mots, elle agite son bâton et envoie une rafale de vent glacial dans notre direction.]],
				['3'] = [[Umka reste devant la porte, pendant que Snegurochka et moi-même partons à la recherche de Ded Moroz. À l'intérieur, le palais ressemble à une gigantesque grotte de glace. Nous allons bientôt arriver à la salle du trône.]]
			}
		},
		['embacity_gastronome2'] = {
			['episode'] = {
				['2'] = [[La végétation n'avait pas complètement envahi le bâtiment, il n'y avait donc pas grand-chose pour nourrir une grand meute de créatures. Je dois vérifier l'état de mes défenses avant d'entrer.]],
				['1'] = [[Du mildiou recouvre le bâtiment, avec des fleurs rouges poussant ici et là. Elles ont attiré un groupe de biomutants. Je ne dois pas oublier ma protection spéciale.]],
				['3'] = [[Un cadavre couvert de racines était appuyé contre la porte de la structure. Une bande de biomutants semble attendre que la moisissure le ronge suffisamment. Je ne peux pas me lancer dans un combat sans protection spéciale.]]
			}
		},
		['new_year_search_fight'] = {
			['choice'] = {
				['2'] = [[Donner les décorations]],
				['1'] = [[Comment aurais-je pu le faire ?']]
			},
			['episode'] = {
				['2'] = [[Cherche pas à m'embrouiller ! On restera là tant que t'auras pas filé notre part des décorations. Et toi, pareil !']],
				['1'] = [[Je suis tombé sur une bande de vauriens dans une des pièces. Ils semblaient chercher quelque chose, eux aussi. L'un d'eux m'invectiva :<new_line>'Hé, mec, elles sont où les décos de Noël ? T'as déjà tout ramassé ?!']],
				['3'] = [[Aaaah, voilà ! Des cadeaauuux ! Qui a une allumette ? C'est parti !'<new_line><new_line>Les vauriens m'oublièrent complètement et repartirent.]]
			}
		},
		['santa_wizard'] = {
			['choice'] = {
				['2'] = [[C'est sûr ?']],
				['1'] = [[Si on l'attaque, on perd notre magie.']],
				['3'] = [[Une sécurité en cas de surcharge ?']]
			},
			['episode'] = {
				['2'] = [[Cette arme est un foutu aspirateur à magie ! Plus j'en sais là-dessus, moins je sais quoi faire.']],
				['1'] = [[Que se passe-t-il ici ? On n'arrive pas à s'en sortir sans moi, hein ?']],
				['4'] = [[Évidemment qu'il est sûr ! C'est un magicien, nom d'un choom !<new_line>Autre chose, pour que tu sois tranquille dans ta tête. J'ai appris que Fouettard avait des barrières d'énergie. Du coup, j'ai capté à quoi ça servait.']],
				['3'] = [[C'est vrai. Ceci dit, une attaque directe est ce à quoi l'ennemi s'attend le moins. En unissant nos forces, notre puissance sera telle que l'arme secrète ne pourra pas tout absorber et explosera, tout simplement.']],
				['5'] = [[Exactement ! Si on commence par les détruire, alors on aura la certitude que l'arme ne survivra pas à une attaque directe. C'est bon de pouvoir compter sur au moins un autre individu compétent que moi, parfois. Cyber, guide-nous vers les barrières d'énergie.']]
			}
		},
		['comrade_in_trouble_gas'] = {
			['episode'] = {
				['1'] = [[Un camp a été établi à côté du véhicule. Un homme s'affaire à charger ou décharger toutes sortes de marchandises.]]
			}
		},
		['traveler_salvation'] = {
			['episode'] = {
				['1'] = [[Je l'ai retrouvé recroquevillé sous un arbre. À première vue, il était à bout de force et s'est endormi... mais un soupçon de vie brûle encore en lui. Si j'étais arrivé un peu plus tard, il serait mort de froid.]]
			}
		},
		['comrade_in_trouble_loot'] = {
			['episode'] = {
				['1'] = [[Une lourde caisse est posée par terre dans le camp désert. L'éclaireur m'a demandé de l'apporter au camp.]]
			}
		},
		['impostor3'] = {
			['episode'] = {
				['1'] = [[Les gens étaient heureux de me voir revenir avec leurs effets personnels.]]
			},
			['choice'] = {
				['1'] = [[Faire des dons]]
			}
		},
		['new_year_boss_6'] = {
			['choice'] = {
				['9'] = [[Vous êtes venus !']],
				['6'] = [[Tu mens comme tu respires, et je vais te tuer !']],
				['5'] = [[Tu es un monstre !']],
				['8'] = [[Voilà !']],
				['7'] = [[Fouettard, vous étiez le Père Noël jadis, souvenez-vous !']],
				['2'] = [[Celle déjà faite au Père Noël ?']],
				['1'] = [[Sortir de derrière la pile]],
				['4'] = [[Le Père Fouettard les avait prises ?']],
				['3'] = [[Pas question.']],
				['10'] = [[Vous vous êtes transformé en...']]
			},
			['episode'] = {
				['9'] = [[J'ai détruit presque Noël tout entier, de mes propres mains. Mon cerveau est empoisonné, le mal est devenu irréparable... mais seules les causes désespérées méritent qu'on se batte !'<new_line><new_line>Le Père Fouettard agite son bâton et frappe la pièce de l'arme brisée sortie par Glagla.]],
				['10'] = [[Comment oses-tu ! Vieux sac d'os ! La magie de Noël est à moi ! Je vais régner sur l'hiver nucléaire, et vous allez tous crever ! Tous !']],
				['6'] = [[Ouaip ! C'était ce vieux fou. À l'époque, il se faisait encore passer pour le Père Noël. Il voulait gaspiller la puissance des décorations pour des clowneries : la protection contre les radiations, la création d'une source d'énergie alternative... Pfft.<new_line>Mais il a eu la chance de tomber sur un génie de ma trempe, juste à temps pour aller dans la bonne direction : le pouvoir.']],
				['5'] = [[Écoute ! Tout ce que je veux, c'est les décos de ton beau sapin. C'est essentiel pour fabriquer mon arme secrète. À ton avis, pourquoi le sapin n'était pas décoré quand tu l'as trouvé, à la base ?']],
				['8'] = [[Hmm. Finalement, la première impression est toujours la bonne. Tu es VRAIMENT stupide. Fouettard, arrête de faire joujou avec la batterie magique et occupe-toi de cet imbécile heureux !']],
				['7'] = [[Personne n'y gagnera à se battre ! Unis plutôt tes forces aux miennes.<new_line>J'écouterai tes propositions pour agir plus... humainement. J'ai même deux-trois idées ! Alors, tope là ?!']],
				['2'] = [[Dans un sous-sol s'entassent des bouts de décorations ainsi que toutes sortes de boulons et d'outils.<new_line><new_line>De l'autre côté de la pile, j'entends la voix de Glagla.<new_line>'Non, c'est moi qui commande maintenant ! À cause de toi, on doit repartir de zéro dans un sous-sol moisi !']],
				['1'] = [[Voilà, c'est prêt. On lance la téléportation, ou je dois patienter sagement dans le froid ?']],
				['4'] = [[Le Père Fouettard frissonna et Glagla laissa échapper un rire répugnant.<new_line>'Ha, j'avais vu juste : t'es loin d'être stupide. Ce gros sac n'a même pas pu t'éliminer alors qu'il avait toutes les cartes en main.<new_line>Alors, imagine les pouvoirs qu'on pourrait atteindre en unissant nos forces, toi et moi !']],
				['3'] = [[Hé ! Comment t'as fait pour passer mes... On dirait que t'es pas aussi bête que t'en as l'air. J'ai une proposition à te faire.']],
				['12'] = [[Notre magie a été libérée, je devais m'assurer que tu ne ficherais pas tout en l'air ! Enfin, je veux dire... Merci d'avoir restauré notre magie, quoi.']],
				['11'] = [[Greeeuuuh ! Aagaaah !']],
				['14'] = [[On discutera plus tard, cher collègue ! Après cette grosse décharge magique, cet endroit commence déjà à s'effondrer ! Emmenons Fouettard et sortons d'ici !']],
				['13'] = [[Hein ? Mais que se passe-t-il ?']]
			}
		},
		['newyearEvent_def_easy'] = {
			['episode'] = {
				['2'] = [[Après une concentration intense, j'ai souhaité de toutes mes forces à un miracle de Noël. Rien ne s'est passé. Peut-être parce que le miracle de Noël, c'est moi. Après tout, je suis une sorte de Père Noël, moi aussi. Je dois penser à quelque chose de précis.]],
				['1'] = [[En approchant des festivités, j'aperçois un épais nuage de fumée. Des vauriens sont venus gâcher la fête. Je dois protéger les villageois, mais en le faisant moi-même, je risque de les exposer au danger. J'ai besoin d'aide.]],
				['4'] = [[Un portail se matérialisa à mes côtés, et Nomade en sortit.<new_line>'Greeuh aaaah !', cria-t-il. Sur ces belles paroles, il fonça dans la mêlée. Il fallait bien que je le suive.]],
				['3'] = [[Derrière moi, j'ai entendu le bruit d'un moteur. C'était une petite voiture rouge, à l'ancienne. Elle s'est arrêtée, et Négoce est sorti du véhicule.<new_line>'Ho-ho-ho ! Encore toi, cher ami ? Viens, les gens ont besoin d'aide. Il n'y a pas de temps à perdre.']],
				['6'] = [[Un portail se matérialisa à mes côtés, et Cyber Noël en sortit.<new_line>'Hé, choom, ça gaze ! C'est quoi, cette affaire ? Encore un cas de cyberpsychose ? Flanquons-leur une bonne raclée !']],
				['5'] = [[Un portail se matérialisa à mes côtés, et Nounours en sortit.<new_line>'Les gens ont besoin d'aide. Avec moi, camarade ! Pour la solidarité du prolétariat !']],
				['7'] = [[Un portail se matérialisa à mes côtés, et Mago en sortit.<new_line>'Encore cet imbécile ? Il faut vraiment que je me coltine tous tes problèmes insignifiants ? Bah, autant donner un coup de main.']]
			},
			['choice'] = {
				['2'] = [[Père Négoce]],
				['1'] = [[Appeler à l'aide]],
				['4'] = [[Père Nounours]],
				['3'] = [[Père Nomade]],
				['5'] = [[Cyber Noël]]
			}
		},
		['frost_tamb_event'] = {
			['choice'] = {
				['2'] = [[Danse d'accalmie]],
				['1'] = [[Danse du blizzard]],
				['4'] = [[Déséquiper le tambourin]],
				['3'] = [[Danse de pluie fertile]]
			},
			['episode'] = {
				['2'] = [[La température a chuté d'un coup, de gros nuages ont tapissé le ciel, et la neige a étalé son manteau blanc. Je sens déjà la rosée glacée craquer sous mes pieds.]],
				['1'] = [[Je peux reproduire la danse chamanique de nos ancêtres avec un tambourin pour influencer la météo. Ça demandera beaucoup d'énergie.]],
				['4'] = [[Des nuages légers et rapides se sont réunis puis ont commencé à inonder le sol d'une pluie chaude. Avec des intempéries aussi propices à la moisson, les récoltes seront vraiment bonnes.]],
				['3'] = [[En un instant, une brise chaude et fraîche est venue chasser tous les nuages et dissiper les vapeurs radioactives, laissant place au silence et au calme.]]
			}
		},
		['newyearEvent_energy_easy'] = {
			['episode'] = {
				['9'] = [[Oohgravahahaaa !']],
				['10'] = [[Aaaahaharrraharagaaaaaaaaaaaaaaaaaaah !']],
				['6'] = [[Cyber Noël posa sa main métallique sur les cordes et commença à hurler frénétiquement.<new_line>'Aaaaah grrrakhah aaaaaaaaaaah, graaaym haaaaaaaaa !']],
				['5'] = [[Cyber Noël s'empara du microphone et annonça :<new_line>'Aujourd'hui, j'ai fait venir un invité très spécial à notre petite sauterie. Dites bonjour à ce bonhomme, le seul, l'unique, la star du coin, LE PÈRE NOËL !!!'<new_line><new_line>Le public se mit à l'acclamer.]],
				['8'] = [[Les vivats devenaient assourdissants.<new_line><new_line>'Agravaaaaaaaaaaaaaaah ahmragaaaaahhhhhhhhha !']],
				['7'] = [[Devant une foule en délire, Cyber Noël continua sa prestation.<new_line>'Agravaaaaaaaaaaaaaaah haaaaaaarrrrrrrrrrr !']],
				['2'] = [[Du calme, j'ai juste besoin d'un batteur pour mon groupe. C'est rude de trouver des zicos dans ton monde, j'ai l'impression.']],
				['1'] = [[Ça boume, choom ? T'es prêt à m'aider pour atomiser ce bas monde ?']],
				['4'] = [[Savoir jouer, c'est pas important. Tout est une question de coolitude. Tape comme un sourd sur la batterie et donne-toi à fond !'<new_line><new_line>Cyber Noël s'avança vers le microphone.]],
				['3'] = [[Nan, choom. Pas le temps d'aller en chercher un. Pose tes miches sur ce tabouret et prends les baguettes.']],
				['12'] = [[J'ai frappé la batterie de toutes mes forces, crevant les peaux et fêlant les cymbales. Après les dernières ovations de la foule, le rideau tomba sur la scène. Cyber Noël vint à ma rencontre.<new_line><new_line>'Tu vois, choom ? Fallait pas t'inquiéter !']],
				['11'] = [[Le reste du groupe s'arrêta presque de jouer. J'ai senti le groove me pénétrer et j'ai commencé à jouer un rythme violent et lourd. À la fin, le public hurla avec une énergie inédite, et Cyber Noël lui accorda un rappel.<new_line><new_line>'Agraaaaaaaaaaaaaaaaaaaaaaaaaaah !']],
				['14'] = [[Ma clarté mentale me revint progressivement, jusqu'à distinguer la silhouette de Cyber Noël.<new_line>'Debout, samouraï. T'as été un poil trop rock'n'roll, je crois bien. Mais ça va : on est tous passés par là, avec un concert aussi génial. Tu vas tout déchirer la prochaine fois.']],
				['13'] = [[C'est un miracle de Noël. Merci d'avoir fait cette bonne action. On a changé les idées de ces gens, et demain leur semblera une journée de survie moins triste que d'habitude.']]
			},
			['choice'] = {
				['9'] = [[Frapper fort et rapidement]],
				['10'] = [[Faire un solo]],
				['6'] = [[Jouer de la batterie]],
				['5'] = [[Accueillir la foule]],
				['8'] = [[Jouer doucement]],
				['7'] = [[Faire un roulement]],
				['2'] = [[Il faut que j'en trouve un ?']],
				['1'] = [[Je ne sais pas trop.']],
				['4'] = [[S'asseoir à la batterie]],
				['3'] = [[Mais je ne suis pas musicien !']],
				['12'] = [[C'était quoi, ça ? Je ne sais pas jouer.']],
				['11'] = [[Casser la batterie]],
				['14'] = [[S'évanouir]],
				['13'] = [[Heureux d'avoir pu me rendre utile.' (Partir)]]
			}
		},
		['embacity_restaurant2'] = {
			['episode'] = {
				['2'] = [[Un groupe de mutants s'était réuni devant le bâtiment envahi par la végétation, se disputant la carcasse moisie de la bête. J'avais besoin d'une combinaison de protection pour m'occuper d'eux.]],
				['1'] = [[Depuis les ruines couvertes de moisissures, on peut entendre le bruit de ses nombreux habitants. Je dois me rappeler d'enfiler une combinaison de protection pour éliminer les biomutants.]],
				['3'] = [[Au milieu des excroissances moisies, j'aperçois de magnifiques fleurs rouges. Mais je doute que cette bande de mutants soit venue ici pour les admirer, je ferais donc mieux de penser à mettre une protection spéciale.]]
			}
		},
		['newyearEvent_fight_easy'] = {
			['episode'] = {
				['2'] = [[Hmm, il est possible d'attaquer le bloc de glace sous plusieurs angles. Je dois décider quelle est la meilleure option.]],
				['1'] = [[À l'écart de toute trace de civilisation se trouve un bloc de glace servant de base temporaire aux vauriens. Toutes leurs attaques sont lancées d'ici, et tous les objets qu'ils volent passent par ici.<new_line>Si j'élimine ceux qui se trouvent à l'intérieur, la vie dans les camps environnants deviendra sans doute plus facile et les gens pourront fêter Noël en paix.]]
			},
			['choice'] = {
				['2'] = [[Porte principale]],
				['1'] = [[Approcher du bloc de glace]],
				['4'] = [[Accès dérobé]],
				['3'] = [[Garage]]
			}
		},
		['emba_isaev_krasnoyarsk'] = {
			['episode'] = {
				['23'] = [[En toute franchise, nous n'avons pas eu le cœur de l'anéantir complètement. Nous l'avons placé dans la grotte où il se trouvait depuis des millénaires. Un retour à son milieu naturel, sans ordinateur.<new_line>Le biologiste et certains de mes collègues resteront ici quelques temps, pour retirer toutes les technologies des biostations. Certaines seront détruire, et nous en emporterons d'autres avec nous.']],
				['22'] = [[Non, car je crois que les gens pourraient tirer une leçon de toutes ces catastrophes. En attendant, nous devenons des archivistes dans le vrai sens du terme.<new_line>Nous stockerons des informations afin d'éviter que les pires erreurs soient commises par ceux qui suivront le chemin du savoir.']],
				['21'] = [[Nous avons décodé la boîte noire. Le signal a été généré, il a déjà été émis vers le prototype de mycélium. L'appareil s'autodétruira, et les pétroliers n'auront plus rien.<new_line>Nous avons besoin de temps pour faire de nouvelles vérifications, après quoi, nous partirons d'ici. Revenez tout à l'heure pour faire vos adieux.']],
				['20'] = [[C'est difficile à prévoir. Tout ce que nous savons, d'après les documents, c'est qu'il s'agit d'une très grande cité des sciences. Et en son centre, sous la biostation, se cache le vestige de mycélium, connecté à un supercalculateur.<new_line>Nul ne peut prédire comment les mutations et l'ordinateur peuvent affecter son activité vitale.']],
				['19'] = [[Les pétroliers vont exploiter le mécanisme de végétalisation à des fins commerciales.<new_line>Ils ne se soucient guère de comprendre que le vestige de mycélium prendra le contrôle immédiatement et qu'une nouvelle catastrophe se produira.<new_line>En d'autres termes, notre objectif est d'infiltrer la cité des sciences et d'envoyer un signal d'autodestruction au prototype de mycélium utilisé dans notre mécanisme.']],
				['5'] = [[Au départ, nous pensions que les mutations du mycélium étaient spontanées, et c'était ce caractère aléatoire qui posait le plus problème.<new_line>Toutefois, d'après les informations que vous avez obtenues, on dirait que son fonctionnement est différent. Non manipulé, le mycélium s'avère moins changeant.<new_line>Ce vestige de mycélium existait depuis des milliers d'années sous forme stable, dans des grottes isolées.']],
				['16'] = [[Et nous sommes à la croisée des chemins.']],
				['15'] = [[Bonjour, cher camarade. C'est confirmé : le prototype a bel et bien été détruit.']],
				['9'] = [[Étonnant ! Incroyable ! Extraordinaire !']],
				['17'] = [["Nous avons été obligés de mettre à l'arrêt toutes les recherches et de changer la direction du développement de notre organisation. Toute cette histoire avec le vestige de mycélium...<new_line>Vous l'avez bien vu : les gens sont ambitieux, cupides et sont le plus souvent incapables de faire un choix, même si c'est pour la bonne cause.']],
				['18'] = [[Hélas, mon ami, hélas. Pas à ce prix. Mais vous avez apporté une contribution inestimable à la défense de ce monde chancelant. Maintenant, qui sait, avons-nous une toute petite chance de survivre ? Bonne chance à vous, cher camarade. Et adieu !']],
				['6'] = [[Et toutes ses mutations ont eu lieu suite à des stimuli externes. L'erreur fatale du projet E.M.B.A. a été de connecter le vestige de mycélium à un supercalculateur.<new_line>Ce changement a produit des réactions radicales. Apparemment, l'émergence de biomutants et d'individus autonomes comme l'Émissaire est la conséquence de l'accès du mycélium aux circuits logiques d'ordinateurs très puissants.']],
				['10'] = [[Ce vestige de mycélium semble être doué de conscience. Pas d'une seule, comme la nôtre, mais plutôt d'une conscience partagée. C'est sans doute la raison pour laquelle il a communiqué si facilement avec le supercalculateur.']],
				['8'] = [[Eh bien, bonne chance.'<new_line>(Tend un bout de papier avec le code)]],
				['7'] = [[Vous devrez naviguer selon les circonstances. Essayez au moins de vous emparer de la boîte noire sur place.<new_line>Avec l'aide du biologiste, vous nous avez fournis un excellent laboratoire mobile, et nous pouvons potentiellement émettre un signal direct à partir de celui-ci en nous basant sur les codes de cette boîte noire. Alors, camarade, prêt pour une dernière mission ?']],
				['2'] = [[Bien entendu, un conflit militaire n'est pas la solution. Cela dit, nous avons un plan. Je n'entrerai pas dans les détails pour l'instant.']],
				['1'] = [[Enfin, vous voilà. Nous avons de gros ennuis. Le Syndicat Pétrolier s'est emparé de notre prototype de mécanisme de végétalisation. Il faut les empêcher de l'utiliser, faute de quoi, toutes les friches vont devenir un véritable enfer vert.']],
				['4'] = [[Bonjour, collègue. Grande nouvelle ! D'après les documents, il y a un vestige de mycélium à la biostation, connecté à un supercalculateur. C'est là que se trouve le centre de décision.']],
				['3'] = [[Maintenant que le prototype est entre les mains des pétroliers, cela n'a aucun sens de condamner notre projet.<new_line>Nous devons essayer par tous les moyens de sécuriser la technologie de végétalisation. Encore une fois, je dois vous demander de trouver les documents. C'est nécessaire pour notre plan et pour l'étude dans sa globalité.']],
				['12'] = [[Oui ! Tout cela est logique. Rien n'empêcherait cette chose de répandre des spores dans le monde entier et de tisser son réseau sur toutes les villes. D'autant plus qu'elle semble friande de fortes radiations.<new_line>Mais tout ce qu'elle attend de nous, c'est la paix. Apparemment, elle ne fait pas la distinction entre les individus et considère l'humanité comme un unique organisme. Pour elle, chaque être humain est l'humanité.']],
				['11'] = [[Le vestige de mycélium a été extrait des entrailles de la terre et soumis à une sélection intense. Tous les événements survenus jusqu'ici ne sont dus à rien d'autre qu'à une créature qui tente de se protéger à nouveau de l'humanité.<new_line>La symbiose avec l'ordinateur lui a offert des opportunités sans précédent, certes, mais elle a aussi été motivée, si je puis dire, par nos méthodes de travail.']],
				['14'] = [[Plus tard, l'ami, plus tard.']],
				['13'] = [[C'est vrai. Je suis plutôt d'accord avec notre camarade fongique. L'ambition mêlée aux meilleures intentions de l'humanité lui a donné tous les outils nécessaires. À qui incombe la plus grande part de responsabilité ?<new_line>La réponse n'a pas l'air de pencher en notre faveur. C'est pourquoi l'Émissaire nous ressemble tant, sans parler du sort de Gerin et Kourganov...']]
			},
			['choice'] = {
				['22'] = [[C'est plutôt pessimiste.']],
				['15'] = [['Je suis prêt'.]],
				['9'] = [[Alors, toutes ces biostations...']],
				['17'] = [[Que va-t-on faire du vestige de mycélium ?']],
				['18'] = [[Donc, pas de végétalisation des friches...']],
				['20'] = [[Alors, il suffit d'aller à la biostation et de pénétrer dans la salle du supercalculateur ?']],
				['5'] = [[Mais du coup, les mutations ?...']],
				['16'] = [[Et la végétalisation des friches ?']],
				['7'] = [[Qu'y a-t-il, docteur ?']],
				['2'] = [[Et les technologies, là-dedans ? Notre but était de sauver le monde.']],
				['1'] = [[Comment les arrêter ? Il y a toute une armée.']],
				['4'] = [[Qu'est-ce que c'est, le vestige de mycélium ?']],
				['19'] = [[Au revoir, Dr Isaev.']],
				['12'] = [[Qu'est-ce que vous allez faire maintenant ?']],
				['11'] = [[Et le prototype des pétroliers ?']],
				['14'] = [[À quoi dois-je m'attendre à la biostation ?']],
				['21'] = [[Et nous avons été perçus comme une menace ?']]
			}
		},
		['new_year_boss_4_2'] = {
			['choice'] = {
				['1'] = [[Donner de l'eau]]
			},
			['episode'] = {
				['2'] = [[Pfiou, merci, choom. Alors, quoi de neuf ?']],
				['1'] = [[Gruuh ahh, ugh grooh...'<new_line>Père Nomade donne une tape compatissante dans le dos de Cyber Noël, alors penché au-dessus d'une poubelle.]],
				['4'] = [[Choom, c'est moi qui suis épuisé, alors pourquoi c'est toi qui galères à capter ?! Tu veux en savoir plus sur cette arme secrète, non ? Je sais où piocher les infos. C'est parti.']],
				['3'] = [[Ah, c'est pour ça que vous êtes là. De foutus corpos volent les cadeaux des honnêtes gens ! Bon, j'ai tapé dans le matos de deux mercenaires pendant la fiesta. On y go. Va falloir pirater la glace.']]
			}
		},
		['new_year_boss_4_4'] = {
			['choice'] = {
				['1'] = [[Hé, ça suffit ! Comment faire pour l'arrêter ?']]
			},
			['episode'] = {
				['2'] = [[C'est horrible ! Tout ce qu'on a fabriqué va servir à augmenter la puissance de Fouettard !']],
				['1'] = [[Ils ont fabriqué une arme qui capte la magie de Noël. Fouettard n'a plus besoin de faire brûler des cadeaux en grande pompe.<new_line>Il suffit juste de pointer l'arme dessus, et bim : ça t'aspire toute la magie des fêtes ! Il peut le faire à l'échelle industrielle. Voire à l'échelle mondiale !']],
				['4'] = [[L'arme carbure à la magie. Si on pouvait la pirater, y aurait pas de problème.<new_line>Après, pour ce qui est de quoi faire avec la magie de Noël, j'en sais rien... mais je connais un magicien. Ouaip !']],
				['3'] = [[Tu sais où tu peux te la mettre, ton industrie toute entière ?']],
				['5'] = [[J'ai appris plein de trucs grâce à lui, quand j'étais jeune. S'il pouvait rappliquer, on aurait une chance !<new_line>Bon, t'es prêt à faire tes trucs vaudous et à décorer le sapin ?']]
			}
		},
		['impostor2'] = {
			['episode'] = {
				['2'] = [[Armes prêtes, je suis allé aux bandits. L'imposteur a crié: « Hé, mec, attendez que je ne veux me battre pour ce fatras mais pas seulement pour vous donner ce que vous savez nous apporter trois bouteilles de vodka et ce sac est à vous DEAL!.?.? »]],
				['1'] = [[Bandits en costume étaient assis à l'arrêt et en comptant les choses volées. « Quelle poubelle! » imposteur Moroz mis en sourdine. « Je vous ai dit que l'idée était stupide! »]]
			},
			['choice'] = {
				['1'] = [[Rapproche toi]]
			}
		},
		['new_year_search_medium'] = {
			['episode'] = {
				['2'] = [[Bon, il me faut autre chose. Je vais en faire une vitrine. Avec une relique comme celle-là, ces ploucs vont se pisser dessus.<new_line>Pourquoi tu me regardes ? Quand on devient vieux comme moi, la vie, c'est pas toujours rose. Je dois arrondir les fins de mois dans une boutique de magie.']],
				['1'] = [[Arrête ce que tu en train de faire. J'ai une mission pour toi.<new_line>Apporte-moi du bric-à-brac. Avec un brin de magie, je peux te fabriquer l'artefact le plus puissant sur le marché.']],
				['3'] = [[C'est pas trop tôt. La retraite arrive plus vite que toi. D'accord, pour te remercier, je vais te dire un secret de magicien.<new_line>Tout rebut peut être vendu comme artefact en écrivant le mot « maudit » sur l'étiquette de prix. Que ce soit un os, une baie, une pièce : ça vaut pour tout.']]
			}
		},
		['emba_time_hard'] = {
			['episode'] = {
				['9'] = [[Bravo ! Qu'est-ce que j'aimerais une équipe de tournage qui fasse une journée pareille tous les jours. J'ai maintenant une réserve infinie d'images.<new_line>Attendez, j'ai autre chose pour vous. Je n'ai presque plus d'agent de développement, mais j'ai appris à en fabriquer moi-même.<new_line>Il me faut juste quelques produits chimiques. Pouvez-vous me les obtenir ? Sinon tout mon... enfin, votre travail n'aura servi à rien.]],
				['6'] = [[Hé, vous filmez ici ou quoi ?']],
				['5'] = [[J'ai filmé assez de plans larges, je peux maintenant faire des gros plans. Les scientifiques m'ont autorisé à filmer la centrifugeuse et le vivarium biologique en action.]],
				['8'] = [[Je pense avoir recueilli suffisamment d'images pour dix épisodes. Il est temps de donner la bande au réalisateur.]],
				['7'] = [[Ça suffit avec l'équipement du cinéaste moustachu. Je préfère vous dire ce que mon directeur de recherche a inventé ! En gros, j'ai perdu les capuchons du chromatographe, alors on s'est mis en route pour...'<new_line><new_line>Je me préparais à filmer un autre récit des tribulations d'un scientifique de Progress.]],
				['2'] = [[J'ai décidé d'utiliser mes talents pour créer une série entre la science et le cinéma très artistique. J'ai eu une soudaine révélation : c'est exactement ce dont les gens ont besoin en ce moment.']],
				['1'] = [[Camarade, vous avez un instant ? Je filme une série sur la routine quotidienne des scientifiques de Progress. J'ai besoin de beaucoup de contenu, et mon fainéant de cadreur est malade. J'ai besoin que quelqu'un le remplace.']],
				['4'] = [[J'ai déjà arrangé quelque chose avec deux scientifiques. Leur véhicule tout-terrain est là-bas. Appuyez ici sur la caméra et n'oubliez pas cet obturateur. N'utilisez pas trop de bande, on ne pourra utiliser qu'un dixième des images de toute façon.']],
				['3'] = [[Vous ne connaissez pas bien les gens ni le cinéma. Ils éviteront à tout prix les inventions étranges et les explications incompréhensibles.<new_line>On est à une époque où tout s'effondre, c'est dans ces moments-là que les artistes ont le plus de travail. Je dois combler l'écart entre la science et le peuple.']],
				['11'] = [[Merci. Bon, je vais au labo photo. Revenez plus tard, j'ai déjà trouvé autre chose à filmer pour les prochains épisodes.']],
				['10'] = [[Alors, vous avez les produits chimiques ? Je dois passer la nuit à travailler dans le laboratoire photo. Les gars de Progress pourraient au moins me filer un coup de main, je travaille assez dur pour eux.']]
			},
			['choice'] = {
				['9'] = [[Commencer la recherche]],
				['6'] = [[Oui, et alors ?']],
				['5'] = [[Filmer l'équipement de travail]],
				['8'] = [[Retourner voir le réalisateur]],
				['7'] = [[Filmer une interview]],
				['2'] = [[Je peux vous aider.']],
				['1'] = [[C'est quoi cette série ?']],
				['4'] = [[Commencer à filmer les scientifiques]],
				['3'] = [[Je croyais qu'ils avaient besoin de scientifiques.']]
			}
		},
		['halloween_search_medium_1'] = {
			['episode'] = {
				['1'] = [[Bonjour, gamin. Je concocte une potion particulièrement coriace, aujourd'hui. Tu as peut-être des ingrédients ?']]
			}
		},
		['scientist_rescue_quest'] = {
			['episode'] = {
				['9'] = [[Quelqu'un s'est assuré que l'appareil soit fermement riveté au toit. En plus, les fixations sont un peu rouillées. Ça promet d'être laborieux.]],
				['6'] = [['Désolé, mais la médecine de combat... je préfère éviter. J'ai des instruments stérilisés ici, tout est plus ou moins prêt. Et je peux toujours attendre qu'on rejoigne notre centre médical. C'est juste une égratignure, vraiment.']],
				['5'] = [['Je suis prêt, bien sûr, mais il y a une chose. Des bandits sont passés par ici. Je les ai accueillis comme il se doit, mais pour faire court, j'ai une petite blessure. Je ne peux pas trop me plaindre.']],
				['8'] = [['Je suis plus ou moins prêt. J'ai pris tous les instruments, ou presque. L'un d'eux m'a posé problème, sur le toit. J'ai essayé de le dévisser mais ça n'a fait qu'empirer les choses.<new_line><new_line>Si vous avez le temps, vous pourriez tenter de le démonter ? L'essentiel, c'est qu'on ramène le reste du matériel au camp, alors je me fie entièrement à votre expérience.]],
				['7'] = [[Le scientifique attend patiemment avec son sac à dos rempli de matériel, à l'exception de la trousse de secours.]],
				['2'] = [['D'accord, laissez-moi juste mettre les échantillons de sol dans mon sac à dos, et je serai à votre service. Je peux garder mon arme attachée sur le côté ?']],
				['1'] = [[J'ai frappé à la porte du poste de garde. Le scientifique a ouvert le verrou d'un coup avant de pointer immédiatement un fusil de chasse sur moi.<new_line><new_line>Il a fallu du temps pour le convaincre que je venais du camp.]],
				['4'] = [['Pas tant que ça, je pense. Laissez-moi vous montrer où se trouve leur camp sur la carte, et vous déciderez de la marche à suivre : se rendre sur place ou rentrer.']],
				['3'] = [['Voilà le topo. Un gang a déjà réussi à me voler un appareil. On peut rentrer directement au camp, mais ce serait nous apporter une aide précieuse de récupérer cet équipement avant.']]
			},
			['choice'] = {
				['2'] = [['Tout est prêt, on décolle.']],
				['1'] = [['Prêt à rentrer ?']],
				['4'] = [['On rafistolera ça en cours de route.']],
				['3'] = [['Il y a beaucoup de bandits, là-bas ?']],
				['6'] = [[Dévisser avec des outils]],
				['5'] = [[Se rendre au camp]]
			}
		},
		['rabbit_house'] = {
			['episode'] = {
				['20'] = [[J'ai ouvert la boîte et j'ai vu la précieuse peinture qu'elle contenait.]],
				['19'] = [[J'ai déposé mon offrande dans la boîte et je l'ai refermée. Ma foi, voyons si ça a donné quelque chose.]],
				['5'] = [[Le mur au bout de l'arène s'est levé, et j'ai entendu un rugissement venant de la même direction.]],
				['16'] = [[À mon retour dans l'immense salle de l'entrée, les deux chaînes de la porte du milieu avaient disparu.]],
				['15'] = [[Toutes les ampoules se sont éteintes quand j'ai lancé le poids.]],
				['9'] = [[La première ampoule s'est allumée.]],
				['17'] = [[Une plate-forme se trouvait au centre de la pièce, avec une boîte décorée dessus.]],
				['18'] = [[La boîte est vide. Fort de mon expérience en matière d'énigmes, je parie qu'il faut mettre quelque chose dedans.]],
				['6'] = [[À mon retour dans l'immense salle de l'entrée, une des chaînes de la porte du milieu avait disparu.]],
				['10'] = [[La troisième ampoule s'est allumée.]],
				['8'] = [[La deuxième ampoule s'est allumée.]],
				['7'] = [[La pièce de droite était petite et austère. Six ampoules éteintes étaient suspendues au plafond, et il y avait un poids au centre de la pièce.]],
				['2'] = [[J'ai tenté d'ouvrir la porte, sans succès. Même constat après avoir essayé de la défoncer. Il fallait trouver la clé pour entrer.]],
				['1'] = [[Une petite maison rose au milieu d'une friche sans vie. Cette vue dégage quelque chose d'inquiétant, c'est le moins qu'on puisse dire. Il y a toutes sortes de curiosités à voir ici. C'est peut-être ce dont j'ai besoin. Il pourrait y avoir de la peinture et des œufs à l'intérieur.]],
				['4'] = [[La porte de gauche s'ouvrait sur une imposante arène ronde. Fallait-il se battre contre un adversaire là-dedans ?<new_line><new_line>Un gros levier se trouvait au centre.]],
				['3'] = [[L'intérieur de la maison s'est avéré beaucoup plus grand qu'il n'y paraissait vu de l'extérieur. Au bout d'un énorme couloir d'entrée se trouvaient trois portes. Celle du milieu était bloquée par deux énormes chaînes.]],
				['12'] = [[La cinquième ampoule s'est allumée.]],
				['11'] = [[La quatrième ampoule s'est allumée.]],
				['14'] = [[Toutes les ampoules se sont allumées, et j'ai posé le poids.]],
				['13'] = [[La sixième ampoule s'est allumée.]]
			},
			['choice'] = {
				['9'] = [[Poser le poids]],
				['10'] = [[Retourner dans l'entrée]],
				['6'] = [[Soulever le poids]],
				['5'] = [[Se battre !]],
				['8'] = [[Lancer le poids]],
				['7'] = [[Soulever le poids à nouveau]],
				['2'] = [[Passer la porte de droite]],
				['1'] = [[Utiliser la clé]],
				['4'] = [[Activer le levier]],
				['3'] = [[Passer la porte de gauche]],
				['12'] = [[Ouvrir la boîte]],
				['11'] = [[Passer la porte du milieu]],
				['14'] = [[La prendre et partir]],
				['13'] = [[Déposer quelque chose dans la boîte]]
			}
		},
		['emba_intro'] = {
			['episode'] = {
				['1'] = [[Votre attention, je vous prie !<new_line>Ici le Dr Victor Isaev. Je dirige une expédition de recherche avec d'autres membres d'une organisation scientifique, baptisée ProgrèS. Notre but est de redonner vie au monde grâce aux avancées scientifiques des hautes technologies. <new_line><new_line>Actuellement, notre expédition est en quête du projet secret E.M.B.A. grâce auquel les radiations, les maladies, la famine et le déclin de la civilisation humaine ne deviendraient plus qu'un mauvais souvenir. Ce serait le début d'une nouvelle ère de prospérité. <new_line>Nous avons besoin des services d'un combattant expérimenté pour travailler dans des zones à haut risque. Vous aurez pour mission de neutraliser un ennemi potentiel et de recueillir des données sur les recherches menées dans les biostations E.M.B.A.<new_line><new_line>ProgrèS vous fournira des informations plus détaillées lors de votre arrivée aux coordonnées spécifiées. C'est ensemble, et non autrement, que nous pourrons assurer à notre monde un avenir plus radieux. <new_line>Terminé.]]
			}
		},
		['embacity_exogort'] = {
			['episode'] = {
				['2'] = [[Les dents sur les murs de la fosse s'agitèrent, et un grondement se fit entendre depuis les entrailles de la terre. Un peu plus tard, un objet vola hors du trou !]],
				['1'] = [[Le chemin est bloqué par une fosse qui ressemble à la bouche dentue d'un prédateur monstrueux. Je ressens l'envie irrésistible de jeter quelque chose dans ce gouffre sans fond.]],
				['3'] = [[J'ai entendu un grondement satisfait provenant de la fosse. En jetant un regard en bas, j'ai vu que la bouche pleine de dents était fermée. Visiblement, elle n'avait plus besoin de se nourrir pour quelques temps.]]
			},
			['choice'] = {
				['1'] = [[Jeter la carcasse dans la fosse]]
			}
		},
		['emba_intro_bunker'] = {
			['episode'] = {
				['2'] = [[J'ai neutralisé les biomutants du labo souterrain et j'ai trouvé un dossier marqué « Classifié ». Je dois retourner voir le navigateur.]],
				['1'] = [[La vieille porte du bunker est entrouverte. Un grincement et un cliquetis désagréables viennent d'en bas. On dirait qu'il y a quelque chose de dangereux là-dedans.]]
			},
			['choice'] = {
				['1'] = [[Retourner voir le navigateur]]
			}
		},
		['emba_energy_easy3'] = {
			['episode'] = {
				['2'] = [[Ça ne nous ferait pas de mal d'avoir une paire de mains en plus. En gros, on a trouvé cette ferme par hasard hier et elle était recouverte de ces racines bizarres et de moisissure.<new_line>On a à peine tout nettoyé depuis dix heures qu'on y travaille. On a du mal à tenir debout et toute cette saloperie doit être ramassée et entassée pour qu'on la brûle.']],
				['1'] = [[Bonjour. Si vous avez besoin de quoi que ce soit, vous devrez patienter, désolé. On est occupés et pour être franc, tout le monde est crevé.']],
				['4'] = [[Ça n'a pas été facile. Des morceaux de tiges épaisses et des racines coupées jonchaient le sol. Les bouts de moisissures étaient bien plus lourds que je ne le pensais.<new_line><new_line>Ces gens ont vraiment travaillé dur, mais moi aussi, j'ai fait du beau boulot. C'est un gros tas de détritus maintenant.]],
				['3'] = [[J'ai entendu parler de nouveaux mutants verts qui se réunissent dans des endroits remplis de moisissure. Ils la mangent.<new_line>Alors, notre cour est un vrai buffet pour eux. C'est risqué de laisser tout ça comme ça, mais tout le monde est lessivé.]],
				['5'] = [[Les fermiers ont versé de l'essence sur le tas et on l'a regardé brûler avec une certaine satisfaction.<new_line>Ensuite, j'ai fait mes adieux, ils m'ont remercié et m'ont souhaité bonne chance.]]
			},
			['choice'] = {
				['2'] = [[C'est quoi l'urgence ?']],
				['1'] = [[Vous avez besoin d'aide ?']],
				['3'] = [[Appeler les fermiers]]
			}
		},
		['santa_cave'] = {
			['choice'] = {
				['2'] = [[Je vous en prie.']],
				['1'] = [[Hein ? Je n'ai pas compris.']]
			},
			['episode'] = {
				['2'] = [[Ho-ho-ho, on va avoir du mal avec ce spécimen. Mais quand la nature prive un individu de... certaines capacités, elle lui donne d'autres talents. Bref, c'est qu'il est un traqueur fantastique ! Laisse-moi essayer.']],
				['1'] = [[Grrruuuh ? Grooooh !']],
				['4'] = [[Euh... greuuh, ahhh buh groooh !'<new_line><new_line>On dirait que Père Nomade a compris quelque chose. Il nous fait signe de le suivre.]],
				['3'] = [[Père Négoce pointe du doigt l'arbre à moitié décoré, puis dans ma direction, et écarte les bras. Le nouveau venu se gratte la tête avec sa massue.]]
			}
		},
		['cover_the_fighter'] = {
			['episode'] = {
				['9'] = [[L'éclaireur est occupé à examiner tous les casiers et les recoins sombres des lieux. Le coffre-fort est resté là, intact.]],
				['6'] = [[Mon compagnon baisse son arme et, respirant bruyamment, inspecte les lieux :<new_line><new_line>'Ouf, heureusement que je t'ai attendu. Sinon, je ne serais pas sorti d'ici vivant. Maintenant, il faut tout fouiller avant que ces monstres reviennent au pas de course. Merci de ton aide, et bonne chance à toi !']],
				['5'] = [[Mon compagnon baisse son arme et, respirant bruyamment, inspecte les lieux :<new_line><new_line>'Ouf, heureusement que je t'ai attendu. Sinon, je ne serais pas sorti d'ici vivant. Maintenant, il faut tout fouiller avant que ces monstres reviennent au pas de course.']],
				['8'] = [[Au prix d'un grand effort, j'ai réussi à ouvrir le coffre-fort. À l'intérieur se trouvaient des appareils électroniques en excellent état et des boîtes de munitions. L'éclaireur siffla d'étonnement avant de me donner une tape sur l'épaule.]],
				['7'] = [['Mince, vise un peu ce coffre-fort ! Avec une serrure comme celle-là, le contenu doit valoir son pesant de cacahuètes. Dommage que le temps presse. On a fait le gros du boulot, mais si tu pouvais ouvrir le coffre, nos gars te paieraient un joli supplément.']],
				['2'] = [[L'éclaireur fume, assis à l'entrée d'un hangar rouillé. En me voyant, il se lève d'un bond :<new_line>'Des renforts. Enfin une bonne nouvelle, mec ! J'ai déjà crocheté la serrure. On s'y met, d'accord ?']],
				['1'] = [[L'éclaireur fume, assis à l'entrée d'un long garage. En me voyant, il se lève d'un bond :<new_line>'Des renforts. Enfin une bonne nouvelle, mec ! J'ai déjà crocheté la serrure. On s'y met, d'accord ?']],
				['4'] = [[Un bruissement et un raclement se font entendre dans l'obscurité. Nous sortons nos lampes de poche juste à temps : de viles créatures ont élu domicile ici, elles s'apprêtent à nous attaquer.]],
				['3'] = [[L'éclaireur fume, assis à l'entrée d'un abri anti-aérien. En me voyant, il se lève d'un bond :<new_line>'Des renforts. Enfin une bonne nouvelle, mec ! J'ai déjà crocheté la serrure. On s'y met, d'accord ?']],
				['12'] = [[L'éclaireur est mort. J'ai échoué dans ma mission de protection.]],
				['11'] = [['Le butin n'est pas dingue mais, au moins, il est à nous. Il y a une autre porte, là-bas. Peut-être qu'on trouvera quelque chose derrière pour nous maintenir en vie. Mais d'après ce que j'entends, ça promet du grabuge. On se risque au combat, ou on rentre au bercail ?']],
				['10'] = [[Il n'y avait personne à l'intérieur. L'éclaireur jeta un coup d'œil aux alentours avant de revenir dans ma direction.]]
			},
			['choice'] = {
				['5'] = [[Passer la porte]],
				['1'] = [[Entrer dans le bâtiment]],
				['4'] = [[Exploser la serrure]],
				['3'] = [[Crocheter la serrure]]
			}
		},
		['emba_navigator'] = {
			['episode'] = {
				['28'] = [[Au fait, l'accès aux cités des sciences n'est pas toujours facile à cause des problèmes de spores. Et puis, il y a aussi beaucoup de biomutants dans les friches ordinaires. Allez vers l'un des points indiqués et récupérez quelques fragments de mycélium.<new_line>Je parie que vous avez hâte de recueillir de la biomasse et d'obtenir une arme stylée du biologiste. J'ai marqué votre position sur la carte, allez vous battre un peu, combattant.']],
				['26'] = [[Ma foi, si on a fait le tour de la question, partons.']],
				['25'] = [[Très bien. D'autres questions ?']],
				['24'] = [[Ma foi, si on a fait le tour de la question, allons-y. Rendez-vous à la biostation du centre.']],
				['23'] = [[Ça alors ! Je peux vous dire une chose avec certitude : il valorise l'amitié et le dévouement. Il a dû se faire des amis dans son nouveau lieu d'affectation.<new_line>Kourganov est un combattant très expérimenté, il aurait bien pu survivre et continuer à aider les autres dans le cadre du projet E.M.B.A.']],
				['22'] = [[« Colonel-général, vous voulez dire. J'ai servi dans la division qu'il commandait.<new_line>Un gars intelligent, et surtout, très fidèle à ses camarades. Et puis, je me rappelle, il a été promu. Quelques années plus tard, j'ai lu dans les journaux qu'il prenait part au projet E.M.B.A. pour l'armée.']],
				['21'] = [[Vous savez, allons-y quand même, à l'ancienne. Pompez la biomasse hors du réservoir central. Ça a déjà marché pour dégager le chemin, si ça se trouve, ça fera l'affaire ici aussi.<new_line>Et je vais jeter un autre coup d'œil à l'étage supérieur du labo. Je vais voir si je peux pirater leur panneau de contrôle pour voir ce qui nous attend en bas.']],
				['20'] = [[Non, à mon avis, ça ne marchera pas. Cette fois, la biostation n'est pas équipée de tourelle. De toute évidence, une petite surprise nous attend.']],
				['19'] = [[Tiens, vous revoilà, l'ami ! J'espère que votre cerveau n'a pas été mis en bouillie à force de résoudre les problèmes scientifiques du monde entier avec le docteur.']],
				['5'] = [[Salut à vous ! Prêt à partir à la recherche de la boîte noire ? Laissez-moi vous faire un briefing.']],
				['16'] = [[Justement. Tout le réseau de radiodiffusion est tombé en panne après les frappes nucléaires. Chez Vecteur, nous sommes en pleine réhabilitation du réseau dans les friches. Ce qui a pu générer un tel signal, et de quelle manière reste un mystère.']],
				['15'] = [[De mon point de vue, ce qui s'est passé dans toutes les biostations n'était pas accidentel. Croyez-en mon expérience en la matière : un tel pic d'activité synchrone n'est possible qu'en présence d'un signal radio puissant.']],
				['9'] = [[Beau travail ! Apportez la boîte noire au biologiste. Mais pour les recherches au labo, vous aurez sans doute besoin de ces fameux... sporiflores et génofruits. Vous pouvez en trouver dans divers bâtiments de la cité des sciences.<new_line>Ma foi, c'est tout. L'essentiel est de ne pas oublier de porter une combinaison spéciale AIDA, si vous comptez vous battre dans la cité des sciences. Et faites le plein d'herbicide, vous pouvez en obtenir auprès des scientifiques du service Consommables.']],
				['17'] = [[Vous voilà ! Nos petites escapades commençaient à me manquer.']],
				['18'] = [[Il n'y a qu'une seule tourelle ici, mais elle a plus d'un tour dans son sac. Ou plutôt, deux canons dans sa structure. Pour bénéficier d'un véritable avantage tactique, vous me direz comment tirer.<new_line>Mais d'abord, pompez la biomasse hors du réservoir central. Comme la dernière fois.']],
				['6'] = [[Bien, commençons. Bien des choses insolites et utiles se trouvent dans les cités des sciences. En plus du biolaboratoire au centre, il y a d'autres bâtiments.<new_line>Ceux couverts de fleurs rouges sont généralement habités par des biomutants. Nous avons justement aussi besoin de ces fleurs rouges, la sporiflore, pour les recherches au laboratoire de ProgrèS.<new_line>Vous aurez également besoin de ces fruits jaunes que les scientifiques appellent génofruits pour améliorer le laboratoire. Vous en trouverez dans d'autres bâtiments où les biomutants ne rôdent jamais. Mais il y a un autre problème.']],
				['10'] = [[Oh, c'est ce qui provient des biomutants ? Eh bien, vous pouvez extraire de la biomasse du mycélium. Vous en aurez besoin pour créer toutes sortes de choses utiles dans le laboratoire de ProgrèS.<new_line>On peut extraire de la biomasse des fragments de mycélium « à l'ancienne », avec un couteau. Mais c'est plus efficace d'utiliser ce bidule high-tech : l'extracteur. Avec ça, vous obtiendrez plus de biomasse.<new_line>L'extracteur, tout comme les kits AIDA et l'herbicide, peut aussi être obtenu en échange de disquettes de données avec les archivistes. N'oubliez pas d'aller faire un tour dans leur service Consommables.']],
				['8'] = [[Bien, passons aux choses sérieuses. Ce qui nous intéresse en ce moment, c'est la boîte noire. Elle devrait se trouver au centre de la cité des sciences, à la biostation, sous un grand dôme. Vous ne pouvez la récupérer qu'en disposant du plus haut niveau d'accès.<new_line>Les codes d'accès sont consignés dans les documents secrets du projet E.M.B.A.. Le code de ce laboratoire se trouvait dans les documents que vous avez trouvés dans ce bunker, vous vous souvenez ? Je l'ai déchiffré et je l'ai noté pour vous.<new_line>Oh, et n'oubliez pas d'enfiler votre kit AIDA dans les cités des sciences. Les spores volent de partout, et sans ça, vous ne ferez pas long feu au combat. Alors, pionnier, prêt à partir ?'<new_line>(Tend un bout de papier avec le code)]],
				['7'] = [[Ce n'est pas extrêmement dangereux, mais tout est recouvert de végétation. Vous aurez besoin d'herbicide pour vous faufiler à l'intérieur et récupérer les génofruits.<new_line>Vous pouvez en obtenir auprès des archivistes contre des disquettes de données. Passez au service des Consommables.']],
				['2'] = [[Les habitants du coin n'aiment pas les sommités scientifiques, il faut leur faire changer d'avis. Honnêtement, c'est un miracle que ce camp ait survécu : certes, il y a une cité des sciences à quelques centaines de kilomètres, mais j'ai trouvé un laboratoire de terrain abandonné juste à côté.<new_line>Les biomutants sortent de là comme des fourmis d'une fourmilière.']],
				['1'] = [[Je suis le navigateur de l'unité opérationnelle Vecteur. Nous aidons l'expédition ProgrèS à accomplir leur mission. Merci de vous joindre à nous.']],
				['4'] = [[Beau travail, combattant. Dites au Dr Isaev que tout s'est bien passé. Ensuite, revenez me voir. Nous organiserons une inspection de la cité des sciences, hors site. Vous aurez les détails plus tard, allez-y.']],
				['3'] = [[Ça a sans doute quelque chose à voir avec ce mystérieux projet recherché par le camarade Isaev. J'ai fait un petit repérage des lieux... j'ai failli y laisser ma peau.<new_line>Une archive de documents a survécu, si j'ai bien compris. Je parie qu'ils seront d'une grande utilité. Bien évidemment, éliminez tous les mutants. Ensuite, la colonie nous accueillera en héros.<new_line>L'entrée du laboratoire est bien camouflée, mais je vais vous la marquer sur la carte. Et voilà, à vous de jouer !']],
				['12'] = [[D'après mes données, il se trouve au centre de la cité des sciences. À côté du dôme de la biostation, il y a un énorme réservoir rempli de biomasse. Le briseur d'os s'est installé sous la biostation et se gave dans les réserves.']],
				['11'] = [[Salut ! Pendant que vous écoutiez la conférence du médecin, j'étais en repérage. Et je n'ai pas enfilé des perles : j'ai piraté le système de contrôle des tourelles, pour vous apporter de la puissance de feu en appui.']],
				['14'] = [[Ce n'est pas si simple. Je n'ai pas pu entrer dans la partie souterraine de la biostation. Et on dirait bien que le briseur d'os se trouve là, avec la boîte noire. Il faut attirer cette créature au dernier étage du labo et la faire passer devant les tourelles, de manière à ce que la voie soit libre pour accéder à la boîte noire.']],
				['13'] = [[J'ai pensé à tout... ma ne me sert pas qu'à manger des flocons d'avoine. Il y a une petite citerne, à la périphérie de la ville, avec des pompes. Rendez-vous là-bas et activez les pompes.<new_line>Elles aspireront la biomasse du réservoir central de la biostation jusqu'à ce petit réservoir. Privé de nourriture, le briseur d'os rampera à l'étage pour s'inviter à notre table.<new_line>N'oubliez pas notre tactique : il faut attirer le briseur d'os dans la ligne de tir des tourelles !']]
			},
			['choice'] = {
				['16'] = [[Ils ont un problème ?']],
				['15'] = [[C'est un labo de quel genre ?']],
				['9'] = [[Ça marche. Rendez-vous au labo.']],
				['17'] = [[Encore une question : pourquoi avons-nous besoin de fragments du mycélium ?']],
				['18'] = [[Entendu.']],
				['6'] = [[Je suis prêt.']],
				['5'] = [[Que pensez-vous du projet E.M.B.A. ?']],
				['8'] = [[Moi aussi. Quel est le plan d'action ?']],
				['7'] = [[Mais il n'y a pas de réseau.']],
				['2'] = [[Où se trouve exactement le briseur d'os ?']],
				['10'] = [[Pas encore. Est-ce qu'on suit le plan d'origine ?']],
				['4'] = [[Pourquoi sortirait-il ?']],
				['3'] = [[Il faut aller là-dedans et l'éliminer ?']],
				['12'] = [[Le docteur a mentionné le Colonel Kourganov...']],
				['11'] = [[Comment faire pour avancer ?']],
				['14'] = [[Bien, je suis prêt à continuer.']],
				['13'] = [[Son code d'accès a récemment été utilisé pour entrer dans le labo.']]
			}
		},
		['emba_isaev_tver'] = {
			['episode'] = {
				['10'] = [[J'espère que non. En théorie, au moment de la catastrophe planétaire, le Professeur Gerin et d'autres scientifiques du projet E.M.B.A. ont dû être évacués comme nous.<new_line>Mais nous avons perdu le contact avec eux, ainsi qu'avec tous les autres... Au fil du temps, nous sommes devenus des « archivistes ». Mais ne parlons pas de choses tristes.']],
				['9'] = [[Nous ignorions totalement que le mycélium pouvait avoir de telles conséquences à l'échelle d'une ville entière ! Mais ne vous en faites pas pour cela, l'essentiel est de restaurer cette technologie et d'offrir un avenir meilleur à ce monde.']],
				['17'] = [[Oui, c'est ça ! Un groupe expérimental et méthodologique de biostations qui élèvent... des spécimens de combat ?! Il y a les coordonnées de l'endroit où de telles expériences ont été menées.<new_line>La première expédition est en danger, nous devons nous rendre d'urgence à cette cité des sciences ! Notre prochaine destination sera Tula. Nous partirons dès que vous serez prêt.']],
				['18'] = [[Bon, vous êtes prêt à poursuivre l'expédition ?']],
				['6'] = [[Bien, excellent. Les informations sur le projet de recherche E.M.B.A. sont stockées dans une boîte noire. Elle se trouve dans la biostation au cœur de la cité des sciences. Le navigateur se chargera de vous, et il vous aidera à la recherche. Si vous voulez bien m'excuser, je dois me remettre au travail.']],
				['5'] = [[Vous avez mis la main sur un kit AIDA ?']],
				['8'] = [[Bienvenue ! Je vois que vous avez accompli votre mission avec succès, camarade. Eh bien, qu'avez-vous là ?']],
				['7'] = [[Dépêchez-vous d'apporter la boîte noire au biologiste, et aidez-le à poursuivre ses recherches.']],
				['2'] = [[Eh bien, c'est parfait. Votre mission principale lors de la prochaine expédition sera d'aider à faire avancer la recherche, en recueillant des biomatériaux et en localisant des documents et des boîtes noires.']],
				['1'] = [[Vous avez déjà donné les documents au biologiste pour l'aider dans ses recherches ?']],
				['4'] = [[Notre personnel est disposé à échanger un kit AIDA et d'autres biens consommables contre des disquettes contenant des données.<new_line>Aidez mes collègues à étudier le mycélium et enregistrez vos données sur des disquettes. Quand vous en aurez recueilli suffisamment, passez au service des Consommables pour prendre possession d'une combinaison spéciale AIDA.']],
				['3'] = [[Rendez-vous à la cité des sciences la plus proche pour chercher la boîte noire. Il n'y a pas de radiations là-bas, mais le risque de contracter des spores de mycélium est extrêmement élevé.<new_line>Ainsi, avant d'aller plus loin, procurez-vous un kit de protection contre les spores AIDA.']],
				['12'] = [[Croissance agressive du mycélium... propagation hors de la biostation... activation des spores indépendante de notre volonté... accompagnée d'une perturbation du système nerveux central. Je crois qu'il était trop tard pour les sauver.<new_line>Mais si la première expédition ProgrèS a avancé, dans ce cas...']],
				['11'] = [[Cela prendra du temps de tout décrypter, mais nous avons déjà analysé le premier bloc de données.<new_line>Tiens, tiens... Quelque chose s'est passé dans toutes les cités des sciences au même moment. C'est ce que confirment les signaux S.O.S. des biostations les plus proches.']],
				['14'] = [[Vous avez assemblé un kit AIDA ?']],
				['13'] = [[Le biologiste vous aidera. Mais il faut de la biomasse pour fabriquer la combinaison.<new_line>Je vais vous en donner, pour cette fois. Mais à partir de maintenant, vous devrez l'extraire du mycélium avec un extracteur. Vous pouvez en demander un au biologiste.']]
			},
			['choice'] = {
				['9'] = [[Les scientifiques de la cité des sciences sont devenus des mutants ?']],
				['6'] = [[Je suis prêt.']],
				['5'] = [[Qu'avez-vous appris de la boîte noire ?']],
				['8'] = [[Oui.']],
				['7'] = [[Je ne suis pas encore prêt.']],
				['2'] = [[Entendu.']],
				['1'] = [[Pas maintenant.']],
				['4'] = [[Faire un rapport]],
				['3'] = [[Où puis-je l'obtenir ?']],
				['11'] = [[Ils ont trouvé quelque chose de neuf ?']],
				['10'] = [[Que s'est-il passé ?']]
			}
		},
		['firebird_feather'] = {
			['choice'] = {
				['1'] = [[S'approcher sans faire de bruit]]
			},
			['name'] = [[oiseau de feu]],
			['episode'] = {
				['2'] = [[Après un certain temps, l'oiseau est finalement apparu. Il s'agit d'une créature relativement imposante, à l'image d'un paon, d'où émanent chaleur et lumière. Ce merveilleux volatile a atterri non loin des pommes et a commencé à les examiner.]],
				['1'] = [[J'ai posé les pommes enchantées dans la neige et versé du miel dessus, avant de me cacher dans les buissons non loin. Difficile à croire qu'un oiseau de conte de fées est censé surgir d'un moment à l'autre... mais je dois continuer à espérer.]],
				['4'] = [[Épuisé par la bataille, l'Oiseau de feu a décidé de battre en retraite. Il s'est envolé, s'estompant dans le lointain. Notre affrontement a toutefois porté ses fruits : une plume gît dans la neige fondue.]],
				['3'] = [[Je m'approche furtivement de l'Oiseau de feu et l'attrape par la queue, mais une vive sensation de chaleur intente me pousse à le relâcher immédiatement ! L'oiseau pousse un cri et s'envole, avant de faire volte-face et de fondre sur moi. Je réussis toutefois à l'éviter in extremis. Tout porte à croire que je vais devoir me battre pour cette plume.]]
			}
		},
		['embacity_biolab_3'] = {
			['episode'] = {
				['16'] = [[D'après votre description, il s'agit de la première expédition perdue de ProgrèS. Ils sont sauvés, Isaev ne l'oubliera pas... Pour moi, c'est trop tard. Vous savez, c'était un honneur de vous connaître.']],
				['15'] = [[Vous avez conscience du pouvoir que j'ai entre les mains ? Je ferai renaître notre pays en ruine de ses cendres !<new_line>Et je répandrai un nouveau fléau à travers le monde. Mais vous ne serez pas là pour en témoigner. Ne pas respecter un ordre est passible de peine de mort.']],
				['9'] = [[Aaah !'<new_line><new_line>(Après un grondement, une déflagration m'a projeté contre le mur. Je me suis violemment cogné la tête, je ne vois plus rien.)]],
				['17'] = [[Vous n'imaginez pas à quel point on vous est tous redevables. Finissez le travail. J'ai eu grand plaisir à faire un bout de route avec vous, combattant.']],
				['5'] = [[Bon, allons-y.']],
				['6'] = [[Ils ont bâti tout un manoir souterrain ! Vous avez vu les tuyaux au plafond ? C'est par ici que la biomasse coule d'en haut. Mais pourquoi il n'y a pas âme qui vive...<new_line>Si le briseur d'os n'a pas dévoré la biomasse, alors qui ? Et cette porte n'a pas pu s'ouvrir toute seule, si ?']],
				['10'] = [[Qui vient infiltrer une installation militaire ? Un nouveau combattant pour mon armée ? En formation !']],
				['8'] = [[Il y a un compartiment avec la boîte noire, juste derrière. Entrez le code donné par les scientifiques, prenez la boîte, et sortons d'ici avant qu'il n'arrive quelque chose.<new_line>Je vais monter la garde et bidouiller le système électronique. Je peux essayer de comprendre ce qui se passe ici.']],
				['7'] = [[La suite, c'est cette salle. Ouah, regardez cette énorme racine qui pousse là-bas ! Je parie qu'elle est aussi rigide que notre ami Isaev.<new_line>Bon sang, elle sonne creux... fini, les blagues. Ça ne me plaît pas du tout, il faut agir vite. Là-bas, vous voyez la porte avec une serrure à code ?']],
				['2'] = [[Ah, mais c'est le plombier en chef de ProgrèS ! En pompant la biomasse du réservoir, sous cette lumière rouge là-bas, la porte s'est ouverte, vous voyez ?<new_line>Il y a un escalier derrière. Je ne sais pas où il mène. Peut-être qu'une ordure nous attend aussi derrière cette porte, et que vous lui avez piqué tous ses bonbons.']],
				['1'] = [[J'ai jeté un coup d'œil prudent dans le labo. La première chose qui a attiré mon attention, c'était la lumière rouge clignotante au-dessus de la porte, sur le mur, au fond de la pièce. <new_line> Cette porte, en réalité très massive, disposait d'un système de contrôle automatisé. Elle était légèrement entrouverte.]],
				['4'] = [[Au milieu de la salle, entourée de mutants humanoïdes, une silhouette sinistre se penchait sur une table du labo.<new_line>Dans l'obscurité de la cage d'escalier, j'ai pu voir les corps mutilés par le mycélium et entendre des grognements bestiaux. Personne ne mérite un tel sort.]],
				['3'] = [[L'escalier derrière la porte m'a conduit à une salle souterraine spacieuse. Au-dessus, des restes de biomasse verte coulaient des tuyaux dans des cuvettes en métal, et des racines noueuses s'étendaient du sol au plafond, se frayant un chemin jusqu'à la surface de la cité.]],
				['12'] = [[Je ne discuterai pas de mes affaires avec un simple soldat ! Vous le saurez quand vous aurez rejoint mon armée.<new_line>Votre ami fouineur a fait la connaissance de mes explosifs, il n'est plus bon à rien. Mais je vais prendre soin de vous. Au bloc opératoire.']],
				['11'] = [[Revenir à la raison, d'où ? Sous temps, nous allons tout réparer ici. À commencer par vous. Un de mes collègues scientifiques m'a appris une merveilleuse méthode...']],
				['14'] = [[Ça y est, combattant. Je crois que ma mission s'achève ici. Que s'est-il passé après l'explosion ?']],
				['13'] = [[Vous dites qu'il faisait allusion au professeur ? Apparemment, ils se sont rencontrés après la catastrophe. J'aimerais connaître le rôle de Gerin dans toute cette affaire...']]
			},
			['choice'] = {
				['9'] = [[Prendre la boîte noire et partir]],
				['6'] = [[Vous êtes cinglé, et je vais vous arrêter !']],
				['5'] = [[De Gerin ? Qu'avez-vous fait du professeur ?']],
				['8'] = [[Il faut vite aller chercher de l'aide pour ces gens et vous.']],
				['7'] = [[Mentionner Kourganov]],
				['2'] = [[Ouais, c'est bizarre. Et maintenant, on va où ?']],
				['1'] = [[Bon, on descend ?']],
				['4'] = [[Général Kourganov ? Que faites-vous ?! Reprenez-vous !']],
				['3'] = [[Je l'ai en ligne de mire.']],
				['10'] = [[Prendre la boîte noire et aller chercher de l'aide]]
			}
		},
		['reinforcement_quest'] = {
			['episode'] = {
				['2'] = [[Les hommes ont dégainé à temps. Ils ont capturé les bandits en route vers la base.]],
				['1'] = [[J'ai fait aussi vite que j'ai pu. C'était la crise : les bandits avaient déjà fait irruption dans le camp.]],
				['4'] = [[Ce n'était pas juste des bandits, mais des mutants. Mais les hommes ont fait du bon travail en les rattrapant avant d'atteindre le camp.]],
				['3'] = [[Ce n'était pas juste des bandits, mais des mutants. Ils avaient déjà fait irruption dans le camp.]],
				['6'] = [[La victoire est à nous. Les hommes m'ont remercié de mon aide.]],
				['5'] = [[Les bandits sont entrés dans le camp, mais les hommes ont dégainé à temps et les ont piégés dans le dépôt de carburant.]],
				['8'] = [[J'ai apporté les premiers soins. Maintenant, je dois filer.]],
				['7'] = [[Nous avons gagné, malgré quelques blessés. Il faut vite les soigner, ils risquent de mourir.]]
			},
			['choice'] = {
				['2'] = [[Aider à attaquer le camp]],
				['9'] = [[Se préparer]],
				['4'] = [[Aider à supprimer la tanière des mutants]],
				['3'] = [[Aider à défendre le camp]],
				['6'] = [[Fouiller les cadavres et partir]],
				['5'] = [[Aider à exterminer les bandits]],
				['8'] = [[Les laisser et rentrer à la base]],
				['7'] = [[Utiliser la trousse de soins]]
			}
		},
		['halloween_boss_2022_4_2'] = {
			['choice'] = {
				['2'] = [[Les récoltes de l'agriculteur ont disparu.']],
				['1'] = [[Poursuivre les fuyards]],
				['4'] = [[Écouter]],
				['3'] = [[Et maintenant ?']],
				['5'] = [[S'approcher]]
			},
			['episode'] = {
				['2'] = [[Chut toi-même ! Le mange-citrouilles est parti ? Je ne vois rien.']],
				['1'] = [[Je n'avais pas fait cinq pas en direction du bâtiment que j'ai entendu un murmure dans un coin. 'Chuuut ! Quelqu'un vient nous transformer en soupe. Chut, silence !']],
				['4'] = [[J'ai fait de mon mieux pour traverser le champ de maïs sans faire de bruit, mais quelque chose a craqué sous mon pied. J'ai immédiatement entendu : 'Vite, courez vers la forêt ! Par toutes les graines, ma plus grande peur est de finir dans une tarte à la citrouille !']],
				['3'] = [[Aaargh ! Courez ! Il va boire tout notre jus de citrouille !'<new_line>J'ai distingué quelques petits hommes à tête ronde, cachés derrière le mur. Je n'ai pas eu le temps de bien les voir, ils ont aussitôt disparu derrière des arbres fruitiers.]],
				['6'] = [[Je sais. J'ai donné vie à toutes les citrouilles afin qu'elles puissent lui échapper. Ce fermier élevait mes sujets au milieu d'une friche radioactive, sans protection. J'ai aussi repris la citrouille qu'il avait plantée dans les décombres de cette ville horriblement irradiée juste pour gagner un prix.']],
				['5'] = [[Arrivé à une clairière dans la forêt, je vis au milieu une grande silhouette avec une énorme citrouille à la place de la tête, coiffée d'une couronne. Une flamme s'agita derrière les fentes des yeux de la citrouille, et la créature se tourna vers moi : 'Pourquoi poursuis-tu mes sujets, misérable humain ?']],
				['8'] = [[Bon, la foire rurale va pouvoir suivre son cours habituel ! Passe me voir, au fait. J'ai des choses à te proposer en exclusivité depuis le monde des morts.']],
				['7'] = [[Je tenais à punir moi-même l'agriculteur en le faisant venir ici. Mais puisque tu es là, que ta mort lui serve d'ultime avertissement.']]
			}
		},
