		['camp_supply_quest'] = {
			['episode'] = {
				['9'] = [[Il n'y avait plus âme qui vive dans le camp. J'étais arrivé trop tard.]],
				['6'] = [[J'ai guéri la blessure. Il survivra.]],
				['5'] = [[L'homme a pris la caisse, reconnaissant.<new_line>Il y avait un blessé, dans un coin.]],
				['10'] = [[Je me suis vengé des bandits en défendant le camp. Mais à quoi bon, finalement ?]],
				['7'] = [[Tout était étrangement calme, près du camp.<new_line>En m'approchant, j'ai aperçu des bandits en train de monter la garde. Le sol était jonché des cadavres de nos alliés.]],
				['2'] = [[L'homme poussa un soupir de soulagement.<new_line>'Merci de votre aide.']],
				['1'] = [[Je suis parti en direction du point sur la carte, où m'attendait un homme maussade.]],
				['4'] = [[Nous avons pu les repousser, avec de la chance. Mon allié m'a regardé, surpris.<new_line>'Je dois vous remercier, bien sûr... mais qui êtes-vous ?']],
				['3'] = [[J'ai repéré les vieux bâtiments où j'étais censé rencontrer nos alliés. Soudain, des coups de feu ont résonné. C'était une attaque !]],
				['12'] = [[Je n'ai rien', m'a dit l'homme avec un sourire triste. 'Filez la marchandise, ou barrez-vous !']],
				['11'] = [[L'ordure... Regardez ! Tout a déjà été rendu.']],
				['14'] = [[Les alliés continuent de défendre leur camp avec succès.]],
