		['new_year_boss_2_2'] = {
			['choice'] = {
				['2'] = [["Ehi, smettetela!"]],
				['1'] = [["Che cosa..."]],
				['8'] = [["È tutta opera di quel vecchio!"]],
				['7'] = [["È tossico?"]],
