				['5'] = [[Salta fuori dal fumo]],
				['4'] = [["Lascialo in pace!"]],
				['3'] = [[Chiedi all'anziano cosa sta succedendo]]
			},
			['episode'] = {
				['2'] = [[Nella piazza del villaggio, i delinquenti gettano le decorazioni natalizie in un mucchio situato accanto a un falò. Alcuni abitanti del posto sono raggruppati contro i muri delle case. Sono guidati da un uomo austero con un paio di doni in mano.]],
				['1'] = [[Il fuggitivo mi ha condotto a un villaggio decorato per le festività. Davanti ai miei occhi, i delinquenti srotolavano le luci dai pali, toglievano gli ornamenti dagli alberi e sparivano dietro le case.]],
				['11'] = [["Ah, non l'hai riconosciuto? Quel vecchio è Babbo Natale! Non so come abbia fatto a diventare così...<new_line>Non riesco proprio a comprendere un tale abominio. Forse Babbo Naffare sa cosa fare. Dobbiamo trovarlo."]],
				['10'] = [["Non si tratta di questo. Il modo in cui lo inalano... mentre distruggono qualsiasi cosa che abbia a che fare con il Natale. Il tuo mondo è corrotto fino a tal punto?"]],
				['9'] = [["È terribile! A causa di quel terribile fumo, non sono riuscito a venire in tuo aiuto."]],
				['8'] = [[In risposta, il vecchio è di nuovo scoppiato a ridere. Sogghignando, è corso verso la sua motocicletta ed è fuggito dalla piazza.<new_line>I delinquenti, guidati dall'anziano sfigurato, si sono avvicinati verso di me.]],
				['7'] = [[Il fumo si è dissipato. Accanto al vecchio, dove prima c'era l'anziano del villaggio, è comparso una specie di mostro.<new_line>"Furfanti, la banda di Krampus ha una nuova recluta! Novellino, il tuo primo compito è sbarazzarti di questo teppistello!"]],
				['6'] = [[Un mucchio di decorazioni natalizie sono finite tra le fiamme, causando l'emissione di un fumo acre persino più denso del precedente. Il vecchio ha spinto a sé il povero anziano del villaggio.]],
				['5'] = [[D'un tratto, il vecchio si è girato verso di me e ha iniziato a ridere.<new_line>"Ah, vuoi parlare con l'anziano? Ehi, furfanti, gettate tutta questa spazzatura nel fuoco!"]],
				['4'] = [[Sembrava che nessuno mi avesse sentito. Appena il regalo è stato gettato nel fuoco, sono divampate delle fiamme blu che hanno dato origine a un fumo dello stesso colore. Il vecchio e la sua banda erano intorno al falò e hanno inalato il fumo. I loro occhi si sono rigirati nella testa.]],
				['3'] = [["Ehi, anziano! Hai raccolto tutti i regali? Portali qui!"<new_line>Il vecchio con una maschera spaventosa in volto ha preso una scatola con un fiocco festivo dalle mani dell'uomo e l'ha gettata tra le fiamme.]]
			}
		},
		['halloween_reaper'] = {
			['choice'] = {
				['2'] = [["Che cosa vuoi in cambio dei contratti?"]],
				['1'] = [["E tutto questo che cosa ha a che fare con me?"]],
				['7'] = [["Un asino parlante? Sul serio?"]],
				['6'] = [[Prendi la pagina e vattene]],
				['5'] = [["No."]],
				['4'] = [["Ho capito."]],
				['3'] = [["Dove posso trovare queste monete?"]]
			},
			['episode'] = {
				['2'] = [["Ti offro i miei contratti per questi ghoul. Se li affronterai uno a uno, potrai ottenere artefatti di grande valore."]],
				['1'] = [["Salve, mortale! Rilassati, non sto dando la caccia a te. Alcuni spiriti malvagi sono riusciti a fuggire, perciò devo recuperarli."]],
				['11'] = [["Che cosa posso dirti? Il mondo è bello perché è vario, mortale. Sta a te decidere, ma questo asino ha una taglia considerevole sulla testa! Varrebbe la pena morirci... A ogni modo, adesso smamma. Ho delle cose da fare."]],
				['10'] = [["Perché mi guardi in quel modo? È un bel contratto..."]],
				['9'] = [["È un registro in cui è scritto chi morirà, quando e dove. Questa è una lista di coloro che moriranno vicino all'ultima scena del crimine del Mostro di Frankenstein."<new_line>(Strappa e porge una pagina del libro)]],
				['8'] = [["È Igor, un tipo sfuggente. Catturarlo non sarà facile, perciò ti aiuterò. Hai mai sentito parlare del Libro dei morti?"]],
				['7'] = [["Ehi, senti questa. Ho scommesso con il mio amico 5$ che sarebbe affogato nel lago... È stata una vittoria agrodolce."]],
				['6'] = [["Salve, mortale, ho una barzelletta per te. Qual è il colmo per un condannato a morte? Avere un freddo boia."]],
				['5'] = [["Sai perché il Medico della peste si chiama così? Non voglio interferire, ma ti consiglierei di indossare la tua nuova armatura demoniaca quando andrai a fargli visita."]],
				['4'] = [["Oltre a numerose bestie, troverai anche molta gente disposta a pagarti per sbrigare delle commissioni."]],
				['3'] = [["I mietitori vengono pagati in monete. Ecco il tuo primo contratto. Leggilo, se ne sei capace."]]
			}
		},
		['search_killer3'] = {
			['episode'] = {
				['1'] = [[L'assassino mi stava aspettando. Ha atteso che mi avvicinassi per cogliermi di sorpresa. Fortunatamente, ero pronto.]]
			}
		},
		['new_year_boss_4_3'] = {
			['choice'] = {
				['2'] = [[Vai alla porta]],
				['1'] = [[Fai esplodere la porta]],
				['12'] = [[Chiama Cyberbabbo]],
				['11'] = [[Premi il pulsante E9]],
				['13'] = [["Cyberbabbo, la strada è libera."]],
				['14'] = [["Io non sono..."]],
				['15'] = [[Porta il cavo nella stanza]],
				['16'] = [[Collega il cavo]],
				['6'] = [[Mangia il dolcetto rosso]],
				['5'] = [[Mangia il dolcetto blu]],
				['4'] = [[Vai verso gli scaffali]],
				['3'] = [[Vai al tavolo]],
				['18'] = [[Segui Cyberbabbo]],
				['17'] = [[Rispondi al telefono]],
				['7'] = [[Guarda sotto al tavolo]],
				['8'] = [[Premi il pulsante 7A]],
				['9'] = [[Premi il pulsante 55]],
				['10'] = [[Premi il pulsante 1C]]
			},
			['episode'] = {
				['2'] = [[Prima che potessi piazzare l'esplosivo, la porta si è spalancata.<new_line>"Che razza di hacker entra dall'ingresso principale? Sono riuscito a violare la porta sul retro e sono entrato qualche minuto fa. Forza, abbiamo un sacco di lavoro da fare."]],
				['1'] = [[Sono sul posto. L'edificio è fatto di ghiaccio, e la porta è notevolmente liscia e resistente. Non c'è nemmeno una fessura tra la porta e lo stipite. Non riuscirò a entrare senza esplosivi.]],
				['12'] = [[Qualcosa nel muro ha fatto uno scatto melodioso, ma la porta ancora non si apre.]],
				['11'] = [[Dopo uno stridio spiacevole, i pulsanti sono ritornati alla loro posizione iniziale.]],
				['13'] = [[La serratura della porta ha fatto uno scatto. Ce l'ho fatta!]],
				['14'] = [["Perfetto! Ora prendi questo cavo, portalo nell'altra stanza e collegalo alla... scatola metallica.<new_line>Lì dentro c'è anche un telefono. Ti chiamerò appena sarà tutto pronto. Non devi far altro che rispondere."]],
				['15'] = [["Stammi a sentire, choomba, vorrei davvero chiacchierare, ma quelli della corporazione si faranno vivi da un momento all'altro e ci cancelleranno in un batter d'occhio. Ora va'!"]],
				['16'] = [[La stanza è piccola e scarsamente illuminata. C'è sicuramente una scatola di metallo sul tavolo, e da qualche parte nell'oscurità sopra una cassettiera c'è un telefono.]],
				['6'] = [[Gli scaffali sono vuoti.]],
				['5'] = [[È qui che la banda conserva l'arma! Bella scoperta.]],
				['4'] = [[Fatta eccezione per le pareti di ghiaccio, è una normale stanza con dei mobili e un'altra porta di fronte all'ingresso.<new_line>Cyberbabbo ha collegato una specie di scatola con dei pulsanti all'interruttore sul muro e ci sta ancora armeggiando.]],
				['3'] = [["Va' verso la porta per la sala di controllo mentre io preparo qualcosa per l'attacco hacker principale."]],
				['18'] = [["Benvenuto nel mondo reale!<new_line>Ho scaricato tutte le informazioni. Tagliamo la corda. Avanti, choomba, datti una mossa! Seguimi!"]],
				['17'] = [[Il telefono squilla.]],
				['7'] = [[Sul tavolo ci sono dei documenti con degli strani diagrammi e un barattolo. Dentro di esso ci sono gli ultimi due dolcetti: blu e rosso.]],
				['8'] = [[Sotto al tavolo c'è un codice scarabocchiato:<new_line>"E9, 7A, 1C, 55"]],
				['9'] = [[Accanto alla porta c'è un pannello con dei pulsanti. Sopra al pannello ci sono delle istruzioni.<new_line>"Inserire la sequenza corretta."]],
				['10'] = [["Non hai ancora violato questo pezzo di ferraglia? Guarda, questi gonk hanno scarabocchiato il codice sotto al tavolo."]]
			}
		},
		['emba_intro2'] = {
			['episode'] = {
				['2'] = [["Ciao, compagno! Stiamo studiando delle anomalie biologiche, ultimamente ce ne sono state molte.<new_line>Aiutaci con le ricerche e ti daremo accesso ai nostri più recenti sviluppi."]],
				['1'] = [[Ho sentito il rumore di un motore e ben presto ha fatto la sua comparsa un grosso ATV arancione. Il veicolo si è fermato accanto a me e ne è uscito un uomo con una tuta particolare. Mi ha fatto cenno: vuole parlare.]],
				['8'] = [["Splendido, non vediamo l'ora di ricevere notizie da te! Prendi nota di tutte le informazioni sulle nuove piante e i biomutanti, e consegnacele. Immagino che gli avanzamenti che siamo disposti a darti in cambio possano interessarti.<new_line>Quando sarà tutto pronto, vai a Tver e parla con il dottor Isaev su ricerche più generali."<new_line><new_line>(È richiesto il livello 25 per incontrare i capi della spedizione a Tver)]],
				['7'] = [["No. Per ora stiamo studiando solo le anomalie biologiche. Al momento sono il nostro principale obiettivo scientifico. E i veri scienziati non dovrebbero fare supposizioni senza informazioni.<new_line>Però è probabile che tutto sia collegato. Magari potresti parlarne con il dottor Isaev, il capo della spedizione. Ma per farlo dovrai raggiungerlo nei pressi di Tver."]],
				['6'] = [["Stiamo raccogliendo dati per scoprirlo. Per ora possiamo solo ipotizzare che siano dovute alla catastrofe globale. Noi, per così dire, lavoriamo sul campo, mentre i nostri colleghi vicino a Tver si occupano della parte teorica.<new_line>Potrai parlare con loro quando arriverai lì. Nel frattempo, vorremmo che ci aiutassi con la raccolta dei dati."]],
				['5'] = [["Altre domande?"]],
				['4'] = [["Siamo la spedizione dell'organizzazione Progresso. Ora i nostri capi si trovano a Tver. Potrai incontrarli non appena sarai pronto.<new_line>Nel frattempo, ti chiederemmo un po' di tempo per aiutarci a raccogliere dei dati. Sono sicuro che ti interessano le tecnologie più avanzate, che siamo disposti a scambiare per dei dischetti con i dati."<new_line><new_line>(È richiesto il livello 25 per incontrare i capi della spedizione a Tver)]],
				['3'] = [["Siamo interessati a tutto ciò che ha a che fare con fauna e flora mutate di recente. Salva i dati su dei dischetti e portaceli. Ecco un dispositivo di archiviazione.<new_line>Inoltre, gli abitanti del posto faticano a occuparsi delle nuove minacce biologiche. Aiutali: se facendolo dovessi ottenere delle informazioni sulle nuove anomalie, sarà doppiamente utile."]]
			},
			['choice'] = {
				['2'] = [["Come posso essere d'aiuto?"]],
				['1'] = [[Avvicinati]],
				['7'] = [["No, è tutto chiaro."]],
				['6'] = [["Avete idea di cosa sia successo al mondo?"]],
				['5'] = [["Queste anomalie... Da dove provengono?"]],
				['4'] = [["Va bene."]],
				['3'] = [["Comunque, chi sareste?"]]
			}
		},
		['boatman_2'] = {
			['episode'] = {
				['2'] = [[Non appena sono entrato, sono stato colpito da un odore terribile. I muri erano sporchi di sangue e la stanza era stata messa a soqquadro. Un cadavere sfigurato giaceva in un angolo, con accanto un quaderno.]],
				['1'] = [[È successo qualcosa qui. La porta è rotta e la stanza è ricoperta di bossoli e sangue secco.]],
				['4'] = [[Vasily stava aspettando il segnale per dirigersi al fiume quando i pazzi sono entrati nella casa. A giudicare dalle tracce e dalla confusione, lo hanno ucciso e hanno rubato l'antenna radio.<new_line>Devo dirlo al barcaiolo.]],
				['3'] = [[Nella tasca del cadavere c'erano un accendino e un vecchio passaporto sovietico. Era Vasily, l'amico del barcaiolo.]]
			},
			['choice'] = {
				['2'] = [[Prendi il quaderno]],
				['1'] = [[Entra nella casa]]
			}
		},
		['icebreaker_short'] = {
			['episode'] = {
				['2'] = [[Ogni porta della nave era stata spalancata e il pavimento era ricoperto di bossoli. C'erano cadaveri ovunque e gli scaffali erano pieni di cianfrusaglie della nave. Ma le medicine che ho trovato erano intatte. L'equipaggio non aveva nemmeno provato a scappare dalla nave. Nessuno aveva curato i feriti. Era tutto così strano.]],
				['1'] = [[Sembra che il pazzo di Monchegorsk abbia davvero prestato servizio su questa nave.]],
				['3'] = [[Nella cabina del capitano ho trovato il diario di bordo. Era fradicio, ma sono riuscito a distinguere la data dell'ultima annotazione: 22 settembre 1984.<new_line>La mia attenzione è stata attirata da qualcosa che brillava tra i mucchi di borsoni nell'armadio: una familiare bomboletta spray con l'etichetta 'Day R'. Continuando a scavare tra i detriti, ne ho trovate altre decine, tutte uguali.<new_line>Cosa sono queste bombolette spray? Cos'è successo all'equipaggio?<new_line>È come se tutti a bordo avessero perso la testa e si fossero uccisi a vicenda. Devo andarmene da questo posto orrendo.]]
			},
			['choice'] = {
				['2'] = [[Trova la cabina del capitano]],
				['1'] = [[Sali sulla barca]]
			}
		},
		['comrade_in_trouble_animal'] = {
			['episode'] = {
				['1'] = [[I predatori hanno circondato un traliccio arrugginito e lo sventurato esploratore è seduto là in cima.]]
			}
		},
		['emba_time_easy'] = {
			['episode'] = {
				['2'] = [["Effettuiamo esperimenti sul comportamento dei biomutanti sotto l'influenza di alcuni composti chimici.<new_line>Annota ciò che accade in questo raduno biologico, noi torneremo tra poche ore. Cerca di non attaccarli, altrimenti l'esperimento verrà interrotto."]],
				['1'] = [["Auguri amico mio! Vedi quella sfera di piante intrecciate? Contiene biomutanti. Ci abbiamo spruzzato discretamente qualcosa di speciale."]],
				['11'] = [[I biomutanti non mi hanno mai scoperto e comunque nessuno mi ha attaccato.<new_line>Tuttavia, uno di loro mi ha fissato per un paio di minuti! Non so se sia stato accidentale o se mi abbia davvero visto, ma è stato piuttosto spaventoso.]],
				['14'] = [[Ho sentito passi silenziosi dietro di me. Era uno degli scienziati. Mi ha sussurrato,<new_line>"Hai annotato tutto? Vediamo. Bene, sì... Meraviglioso... Sì, queste sono osservazioni eccellenti. Hai fatto un ottimo lavoro, grazie."]],
				['10'] = [[Ho notato qualcosa di insolito: uno dei biomutanti, che una volta era stato ovviamente un essere umano, ululava o gridava qualcosa.<new_line>Poi le altre bestie gli hanno obbedito! Si sono messe in marcia per seguirlo chissà dove, in una parvenza di ordine.]],
				['9'] = [[Mentre li osservavo, uno dei biomutanti è crollato improvvisamente a terra mentre era in movimento e sembrava essere morto. Qualche tempo dopo, il suo corpo è stato avvolto dalle radici nere e trascinato al centro della sfera di piante.]],
				['8'] = [[Questo potrebbe essere normale per i biomutanti, ma si sono comportati in modo insolito diverse volte, rimanendo immobili per circa dieci minuti.]],
				['7'] = [[Le creature hanno iniziato a litigare tra loro un paio di volte. Hanno trascinato i fratelli che hanno fatto a pezzi nel profondo della sfera verde.<new_line>Non ho mai notato questo tipo di comportamento nei nostri precedenti incontri.]],
				['6'] = [[Alcune delle bestie non si muovevano allo stesso modo delle altre. I loro movimenti erano più bruschi e a scatti. Questo potrebbe essere stato l'effetto delle sostanze chimiche spruzzate.]],
				['5'] = [[Non è successo niente di speciale nel comportamento dei biomutanti in questo periodo. Tuttavia, quando un animale, che potrebbe essere stato un lupo, è apparso all'orizzonte, molte delle creature si sono precipitate a inseguirlo.]],
				['4'] = [[Una delle cose insolite che ho notato è stata quando uno dei biomutanti ha iniziato a muoversi più svogliatamente e lentamente e alla fine è caduto sulle radici nere e ha smesso di muoversi.<new_line>Piuttosto rapidamente, le radici si sono intrecciate e lo hanno trascinato in profondità, all'interno della sfera verde.]],
				['3'] = [[I biomutanti vagano, mangiando il terriccio che cresce sulle radici intrecciate.]]
			},
			['choice'] = {
				['2'] = [[Inizia le osservazioni]],
				['1'] = [["Per che cosa?"]],
				['4'] = [[Registra le osservazioni]],
				['3'] = [[Continua le osservazioni]]
			}
		},
		['new_year_boss_5_3'] = {
			['choice'] = {
				['2'] = [[Insegui Frosty]],
				['1'] = [["Ancora tu?!"]],
				['4'] = [["Che cosa facciamo adesso?"]],
				['3'] = [[Alzati e insegui Frosty]]
			},
			['episode'] = {
				['2'] = [["Oh, guardate chi è venuto a farmi visita! Avete portato dei regali? Ah ah! No? Non c'è problema! Avevo comunque intenzione di vedere il modo in cui la mia arma risucchia la magia dagli esseri viventi!"]],
				['1'] = [[Abbiamo raggiunto una scalinata di ghiaccio che conduce sottoterra. Il posto è interamente disseminato di ornamenti natalizi carbonizzati e scatole lacerate.]],
				['7'] = [["Andiamocene prima di essere risucchiati nella neve! Sto pagando il prezzo di essermi fatto coinvolgere..."]],
				['6'] = [[Ancora una volta, un muro di neve mi ha fatto cadere a terra!<new_line><new_line>"È inutile, Frosty è sveglio. Come se non bastasse, ha il componente dell'arma che ha risucchiato la nostra magia."]],
				['5'] = [[Un vortice di neve mi ha fatto cadere a terra. Sembrava che provenisse dal nulla.]],
				['4'] = [["Mi incontrerai ancora. Nei tuoi incubi!"<new_line><new_line>Frosty si è lanciato Krampus sulla spalla, ha estratto una specie di componente luminoso dai frammenti dell'arma ed è corso di fuori usando le scale.]],
				['3'] = [["Stupidi Babbi! Stupido Krampus! Avete distrutto la macchina! Non importa, non finisce qui. Vediamo cosa siete in grado di fare ora senza la vostra magia!"]]
			}
		},
		['firebird_feather'] = {
			['episode'] = {
				['2'] = [[Qualche istante più tardi, l'uccello appare per davvero. È una creatura dalle dimensioni di un pavone che emana calore e luce. Il volatile atterra vicino alle mele per ispezionarle.]],
				['1'] = [[Poso le mele incantate sulla neve e le cospargo di miele, prima di nascondermi tra i cespugli vicini. È difficile credere che un uccello delle fiabe stia per volare sin qui, ma non posso far altro che continuare a sperare.]],
				['4'] = [[Esausto dalla battaglia, l'uccello di fuoco decide di ritirarsi. Lo vedo volare lontano, fino a quando non è solo un puntino fiammeggiante nel cielo. Tutto ciò che rimane di lui è una singola piuma adagiata su una pozza di neve sciolta.]],
				['3'] = [[Mi avvicino di soppiatto all'uccello di fuoco e lo afferro per la coda, ma mi scotta e il dolore mi costringe a lasciare la presa. Il volatile schiamazza e prende il volo, poi scende in picchiata verso di me. Riesco a levarmi di mezzo in tempo e capisco che non avrò alcuna piuma senza combattere.]]
			},
			['name'] = [[Firebird]],
			['choice'] = {
				['1'] = [[Avvicinati]]
			}
		},
		['construction_team_mutants'] = {
			['episode'] = {
				['1'] = [[Questo è il posto che il capo ha segnato sulla mappa. Non c'è bisogno di esplorare la loro tana, i mutanti mi hanno già notato e si preparano ad attaccare.]]
			}
		},
		['ambush_quest'] = {
			['episode'] = {
				['2'] = [[Arrivato a destinazione, ho trovato dei cadaveri.]],
				['1'] = [[Arrivato a destinazione, ho visto un gruppo di persone vicino a un albero caduto.<new_line>'Sei qui per aiutarci? Non stare lì impalato, mettiti al lavoro!']],
				['12'] = [[Mettete giù le armi o qualcuno si farà male.']],
				['11'] = [[L'uomo continuava a puntarmi contro l'arma.<new_line>'Siamo solo commercianti. Non vogliamo problemi.']],
				['13'] = [[È una minaccia?'<new_line>Alcuni dei suoi uomini sono scesi dalle auto.]],
				['14'] = [[L'uomo ha sorriso, vedendo che altri suoi uomini erano usciti dalle auto completamente armati.<new_line>'Non ci arrenderemo facilmente.']],
				['18'] = [[Siete coraggiosi, eh?'<new_line>Il resto dei banditi è sceso dalle auto. Ce n'erano più di quanti ci aspettassimo.<new_line>]],
				['17'] = [[Grazie al mio kit di pronto soccorso, potremo portare i combattenti feriti alla base.]],
				['6'] = [[Ho legato dei paletti al tronco.]],
				['5'] = [[Ho allontanato gli animali. Non c'è altro da fare qui.]],
				['4'] = [[Gli animali si erano già nutriti dei cadaveri, attratti dall'odore.]],
				['3'] = [[Non ho trovato nulla di interessante.]],
				['22'] = [[Mi sto avvicinando al luogo segnato sulla mappa dall'oste.]],
				['20'] = [[Mentre esaminavo i corpi, gli altri hanno preso il carico dalle auto.]],
				['19'] = [[Ben per voi! Ora sparite. Non voglio vedervi di nuovo qui in giro.']],
				['16'] = [[Siamo riusciti a mandarli via, ma alcuni dei nostri uomini sono stati feriti gravemente.]],
				['8'] = [[Ho notato il posto perfetto per una trappola per lupi accanto all'albero caduto.]],
				['9'] = [[Due auto hanno accostato all'albero caduto e ne sono scesi degli uomini per liberare il sentiero. Quando si sono accorti dell'imboscata, hanno estratto le armi.<new_line>'Che cosa vuoi?']],
				['15'] = [[Va bene. Bene, prendi il carico.'<new_line>L'uomo ha passato il carico e la banda se ne è andata.]]
			},
			['choice'] = {
				['2'] = [[Vattene]],
				['1'] = [[Esamina i cadaveri]],
				['12'] = [[Torna alla base]],
				['11'] = [[Non vale la pena morire per questa roba']],
				['32'] = [[Metti giù l'arma]],
				['30'] = [[Continua a muoverti]],
				['17'] = [[Questa roba è più importante della tua vita?']],
				['10'] = [[Dammi il carico e nessuno si farà male!']],
				['7'] = [[Appendi un tronco]],
				['6'] = [[Prova qualcos'altro]],
				['5'] = [[Appendi il tronco con dei paletti]],
				['4'] = [[Scava una trappola per lupi]],
				['3'] = [[Unisciti al combattimento]]
			}
		},
		['new_year_craft_medium'] = {
			['choice'] = {
				['1'] = [["Togli le mani dalla clava!"]]
			},
			['episode'] = {
				['2'] = [[L'uomo sta migliorando, ma trema ancora per il freddo.<new_line>Babbo Naclava strofina i suoi bastoncini, i quali non producono neanche una scintilla.<new_line>"Ugh agh ooh?"]],
				['1'] = [["Salve, compagno! Abbiamo trovato una vittima degli sporchi trucchetti dei furfanti.<new_line>Questo poveretto è quasi morto congelato, ma non abbiamo il necessario per curarlo. Puoi aiutarci?"]],
				['4'] = [["Arghh, om-nom-nom!"<new_line>Babbo Naclava mi ha dato una pacca sulla spalle in segno di approvazione e, un cucchiaio alla volta, ha iniziato a nutrire l'uomo con la sostanza riscaldante.<new_line><new_line>Sta migliorando proprio davanti ai nostri occhi! Chi avrebbe mai detto che le medicine popolari avrebbero avuto un effetto simile? Accidenti! Ottimo lavoro!]],
				['3'] = [["Eh, la sua clava mi ha già colpito. Sono tempi strani, compagno... Ma Babbo Naclava ha detto che esiste soltanto un prodotto in grado di riscaldare quest'uomo. Ti andrebbe di dare una mano?"]]
			}
		},
		['new_year_search_medium'] = {
			['episode'] = {
				['2'] = [["Ok, mi serve un'altra cosa. La metterò in una vetrina. Una reliquia del genere farà andare in estasi gli abitanti del villaggio.<new_line>Che cos'hai da guardare? Quando diventerai vecchio come me, ti accorgerai che non sarà tutto rose e fiori. Devo fare un secondo lavoro in nero in un negozio di magia."]],
				['1'] = [["Smettila di fare qualsiasi cosa tu stia facendo. Ho un lavoro per te.<new_line>Portami della spazzatura. Con un po' di magia, posso trasformarla nell'artefatto più potente presente sul mercato."]],
				['3'] = [["Finalmente. Gli assegni della mia pensione arrivano più velocemente di te. E va bene, in segno di ringraziamento, ti rivelerò un segreto da stregone.<new_line>Puoi trasformare qualsiasi pezzo di spazzatura in un artefatto di grande valore se scrivi "maledetto" sul cartellino. Osso maledetto, bacca maledetta, moneta maledetta. Decidi tu."]]
			}
		},
		['new_year_boss_2_3'] = {
			['choice'] = {
				['2'] = [[Bevila tutta]],
				['1'] = [["Sta ancora parlando in un linguaggio incomprensibile..."]],
				['5'] = [["Chi?"]],
				['4'] = [[Digli di Krampus]],
				['3'] = [["Potresti smettere di farlo?!"]]
			},
			['episode'] = {
				['2'] = [["Hai ragione. È meglio che tu capisca chiunque piuttosto che soltanto me. Ecco, bevi questa pozione. Ti aiuterà."<new_line><new_line>Babbo Naclava mi ha dato una tazza di legno. Ho fatto un sorso con cautela. Niente male!]],
				['1'] = [["Ooh ah. Agh ooh!"]],
				['6'] = [["Non lo sento da un po'... Ha avuto da fare con i problemi governativi del suo mondo.<new_line>Ma ha senza dubbio esperienza in combattimento. È deciso! Decora di nuovo l'albero per aprire un portale per lui."]],
				['5'] = [["Non posso crederci. Cosa può essere successo a Babbo Natale per farlo diventare così... Ad ogni modo, ci serviranno dei rinforzi per affrontare una banda così pericolosa. Conosco qualcuno che fa al caso nostro."]],
				['4'] = [["Finalmente parli in un modo normale! Che cos'è successo?"]],
				['3'] = [[Ci sono cascato di nuovo! Appena ho piegato all'indietro la testa per bere la pozione, Babbo Naclava mi ha colpito ancora con la clava!]]
			}
		},
		['snowman_giant'] = {
			['episode'] = {
				['2'] = [[Nonno Gelo non è il mio nonno biologico e io non sono sempre stata una fanciulla di neve. Un'epidemia mi lasciò orfana, a congelare nel bosco. Fu lui a salvarmi e a offrirmi di diventare la sua assistente.]],
				['1'] = [[La fanciulla di neve mi dà appuntamento al centro delle terre desolate innevate. In lontananza, scorgo l'enorme ombra di un pupazzo di neve che avanza lentamente. «La donna che hai visto si fa chiamare regina d'inverno. È stata una fanciulla di neve... prima di me» dice.]],
				['5'] = [[Tutto ciò che rimane del gigante sono un cumulo di neve e una strana sfera luminosa. Deve trattarsi di qualcosa di importante. La fanciulla di neve potrebbe saperne di più.]],
				['4'] = [[Sembra che abbia deciso di mettere fine alla violenza congelando tutti e ha rapito Nonno Gelo in modo che lui non potesse fermarla. Devo trovare un modo per salvarlo. dice la fanciulla di neve, voltandosi verso di me. «Nel frattempo, ferma quel pupazzo di neve.»]],
				['3'] = [[Nonno Gelo preferisce non parlarne. So che era un'orfana, proprio come me. Rimase con lui per molti anni. Poi vennero l'epidemia e le sue conseguenze, e la sua mente subì un duro colpo. Alcuni anni dopo, rubò il bastone magico e abbandonò Nonno Gelo.]]
			},
			['name'] = [[Pupazzo di neve gigante]],
			['choice'] = {
				['2'] = [[E la regina d'inverno?]],
				['1'] = [[Come può essere?]]
			}
		},
		['santa_cave'] = {
			['choice'] = {
				['2'] = [["Fai pure."]],
				['1'] = [["Che cosa? Non ti seguo."]]
			},
			['episode'] = {
				['2'] = [["Oh oh oh, con questo avremo una bella gatta da pelare. Però, insomma, quando la natura priva qualcuno... di certe abilità, in cambio gli dona... Quello che voglio dire è che è un fantastico segugio! Lasciami provare."]],
				['1'] = [["Uhhh? Oooh!"]],
				['4'] = [["Uh... Eh, aah buh oooh!"<new_line><new_line>Sembra che Babbo Naclava abbia capito qualcosa. Agita la mano e vuole che lo seguiamo.]],
				['3'] = [[Babbo Naffare punta il dito verso l'albero decorato a metà, poi lo rivolge verso di me e alza le mani. Il Babbo Natale appena arrivato si gratta la testa con la sua clava.]]
			}
		},
		['construction_team_fight'] = {
			['episode'] = {
				['1'] = [[Vicino al loro accampamento, ho visto una catasta disordinata di oggetti del cantiere. Quando mi hanno notato, i banditi sono scoppiati a ridere:<new_line>"Oh, ora gli operai vengono a consegnarci gli attrezzi di persona. Beh, sei pronto a sgobbare e a combattere?"]]
			}
		},
		['newyearEvent_commander_hard'] = {
			['episode'] = {
				['2'] = [["Per farla breve, queste sono le regole: ognuno utilizza un'attrezzatura preautorizzata, quindi non si può fare affidamento sui cyberdeck. Dovresti controllare anche la lista dei trucchetti proibiti... E niente animali domestici!"]],
				['1'] = [["Choomba! Iniziavo a pensare che non saresti venuto! Il torneo inizierà a momenti, perciò sbrigati ad iscriverti! Dovrai guadagnarti la tua reputazione da Babbo preem."]],
				['12'] = [["Nella battaglia di apertura delle semifinali vedremo scontrarsi l'amatissimo Babbo Natale e il non proprio amatissimo Babbo Naffare! Vedremo chi riuscirà a guadagnarsi un posto per il prossimo round."]],
				['11'] = [[Mentre riposavo, si è svolta qualche battaglia. Dopodiché c'è stata una piccola pausa, e poi, finalmente, tutti si sono riuniti di nuovo.<new_line><new_line>"È con estremo piacere che vi do il benvenuto alle semifinali! L'Elfo galeotto, la banda dei pupazzi di neve, lo Yeti e la banda di furfanti ci hanno lasciati."]],
				['13'] = [["Nella battaglia di apertura delle semifinali vedremo scontrarsi l'amatissimo Babbo Natale e l'affascinante Babbo Naclava! Vedremo chi riuscirà a guadagnarsi un posto per il prossimo round."]],
				['14'] = [["È stata una battaglia feroce, proprio come piace a noi! Ora lasceremo riposare il nostro combattente preferito. Guardiamo la prossima battaglia!"]],
				['15'] = [["Siamo finalmente tornati! Adesso non ci attende un evento comune, ma il combattimento per il titolo di campione dove vedremo combattere il meglio del meglio!<new_line>Il solo e unico, il magnifico e amato dai fan Babbo Natale si scontrerà con il grande e saggio Babbo Stregone! Si prospetta essere la battaglia del secolo, perciò non perdiamo altro tempo. Diamo inizio alle danze!"]],
				['16'] = [["Siamo finalmente tornati! Adesso non ci attende un evento comune, ma il combattimento per il titolo di campione dove vedremo combattere il meglio del meglio!<new_line>Il solo e unico, il magnifico e amato dai fan Babbo Natale si scontrerà con l'allegro e duro come la pietra Babbo Naclava! Si prospetta essere la battaglia del secolo, perciò non perdiamo altro tempo. Diamo inizio alle danze!"]],
				['6'] = [["Nella battaglia di apertura dei quarti di finale, vedremo scontrarsi l'Elfo galeotto e l'inimitabile Babbo Natale!"]],
				['5'] = [["Ora vi presento i combattenti di oggi. L'avaro e terribile schiavo della corporazione, Babbo Naffare! Un visitatore dall'Età della pietra, Babbo Naclava! Il grande coltivatore di magia, Babbo Stregone! Una banda di furfanti freddi come la pietra!<new_line>Freddi come il mio cuore, la banda dei pupazzi di neve! Il misterioso Yeti! Il fuorilegge Elfo galeotto! E per finire, il solo e unico, il campione della vostra città... Babbo Natale!!!"]],
				['4'] = [[Il numero di persone intorno alla cupola non fa che aumentare. Alcune di loro si stanno addirittura arrampicando. Dopo un po' di tempo, Cyberbabbo appare sul palco accanto alla cupola.<new_line>"Come butta, choomba! Vi do un elettrizzante benvenuto al torneo di Figaggine Natalizia!"]],
				['3'] = [["Beh, il microfono mi attende. Aspetta all'ingresso della cupola."]],
				['18'] = [["Sei un fico, choomba! Se mai i dubbi sulla tua figaggine sovraccaricassero la mia CPU, mi ricorderò per sempre di questo momento. Hai portato alla gente la gioia e un po' di fiducia in più sul fatto che avranno un incredibile Babbo Natale."]],
				['17'] = [["Siamo finalmente tornati! Adesso non ci attende un evento comune, ma il combattimento per il titolo di campione dove vedremo combattere il meglio del meglio!<new_line>Il solo e unico, il magnifico e amato dai fan Babbo Natale si scontrerà con... Ehm... Deve esserci un errore... No? Si scontrerà con Babbo Naffare. Sono sicuro che per il nostro combattente migliore sarà un gioco da ragazzi."]],
				['7'] = [["Nella battaglia di apertura dei quarti di finale, vedremo scontrarsi lo Yeti e l'inimitabile Babbo Natale!"]],
				['8'] = [["Nella battaglia di apertura dei quarti di finale, vedremo scontrarsi la banda dei pupazzi di neve e l'inimitabile Babbo Natale!"]],
				['9'] = [["Nella battaglia di apertura dei quarti di finale, vedremo scontrarsi la banda di furfanti e l'inimitabile Babbo Natale!"]],
				['10'] = [[La folla è in visibilio e Cyberbabbo riappare dal vivo.<new_line>"Che battaglia! Auguriamo un felice viaggio verso casa al perdente e accogliamo Babbo Natale nelle semifinali!"]]
			},
			['choice'] = {
				['2'] = [[Leggi il regolamento]],
				['1'] = [["Che genere di torneo?"]],
				['6'] = [[Riposati]],
				['5'] = [[Vai all'arena]],
				['4'] = [[Applaudi]],
				['3'] = [["..."]]
			}
