		['halloween_boss_2022_5_2'] = {
			['choice'] = {
				['2'] = [[Libera il passaggio con una pala]],
				['1'] = [[Libera il passaggio a mani nude]],
				['7'] = [["Fermo!"]],
				['6'] = [["Che cosa?"]],
				['5'] = [[Sferra un attacco dai cespugli]],
				['4'] = [[Avvicinati di più...]],
				['3'] = [[Avvicinati strisciando]]
			},
			['episode'] = {
				['2'] = [[Ho trovato un cadavere all'interno. Sembrava non avesse un graffio, ma poi il dosimetro ha iniziato a impazzire. Probabilmente è stato murato qui dentro ed è morto di fame. O magari a causa delle radiazioni. È meglio che me ne vada, se voglio evitare di fare la stessa fine.]],
				['1'] = [[Qualcuno morirà in questo edificio. Qui però non c'è nessuno... anche se la porta di una delle stanze è bloccata. Forse sono qui dentro?]],
				['10'] = [["Beh, non si sfugge al destino. Specialmente se è scritto nel Libro dei morti. Passa a trovarmi non appena sei pronto. Ti attende il tuo ultimo contratto."]],
				['9'] = [[Dopo essermi occupato del mostro, partii alla ricerca dell'uomo che era fuggito e lo trovai nelle vicinanze. A quanto pare, mentre stava scappando era caduto in un burrone. Il suo collo era spezzato.]],
				['8'] = [[Il gobbo corse verso il grande elettrodo, e numerose altre creature si unirono al mostro. Erano chiaramente altri esperimenti dal laboratorio di Victor Frankenstein.]],
				['7'] = [[Il gobbo rispose:<new_line>"È un esssemplare davvero affassscinante. Lassscia che ti mossstri qualcosssa, amico mio. Guarda quell'elettrodo laggiù..."]],
				['6'] = [[Vidi un gobbo basso e un gigantesco mostro umanoide. Si voltarono verso di me, e sorprendentemente il mostro lasciò cadere un uomo ferito che trasportava. L'uomo se la diede subito a gambe, e sul volto del mostro apparve un sorriso minaccioso:<new_line>"Iiiigor! Questo non ha niente a che vedere con quell'altro. Voglio il SUO fegato!"]],
				['5'] = [[Gli ero quasi vicino. A quel punto, sentii di nuovo la zeppola:<new_line>"In giro? Dove pensssi di andare? Sei un mossstro! E quello è un fegato!"]],
				['4'] = [[La zeppola venne sovrastata da una voce tonante e ruggente:<new_line>"Igor, è il fegato di un viscido ubriacone. Non lo voglio. Mi vergognerei a farmi vedere in giro con un fegato del genere!"]],
				['3'] = [[Sentendo qualcuno parlare con la zeppola, decisi di dirigermi verso i cespugli per non spaventare nessuno.<new_line><new_line>"È come ssse non ti importassse! È un fegato, ssstupido!"]]
			}
		},
		['impostor2'] = {
			['episode'] = {
				['2'] = [[Armi pronte, sono andato ai banditi. L'impostore gridò: ]],
				['1'] = [[Banditi in abiti erano seduti a una battuta d'arresto e contando le cose rubate. «Che spazzatura!» impostore Moroz disattivato. «Ti ho detto che l'idea era stupido!»]]
			},
			['choice'] = {
				['1'] = [[Avvicinati]]
			}
		},
		['emba_def_easy'] = {
			['episode'] = {
				['2'] = [[I contadini mi hanno ringraziato per l'aiuto e mi hanno persino offerto da bere per il viaggio.]],
				['1'] = [[La fattoria è vuota, come se gli esseri umani l'avessero appena abbandonata. Non ho avuto il tempo di sorprendermi perché ho sentito degli spari e un ruggito disumano provenire da dietro il fienile.<new_line>Sono gli agricoltori che respingono un attacco di biomutanti! Ce la faranno senza il mio aiuto? Devo decidere.]]
			},
			['choice'] = {
				['2'] = [[Rifiuta e vattene]],
				['1'] = [[Bevi e vattene]]
			}
		},
		['camp_supply_quest_pt2'] = {
			['episode'] = {
				['2'] = [[La guardia sembrava preoccupata.<new_line>'Non ho notizie dai miei uomini. Che è successo laggiù?']],
				['1'] = [[Il capo delle guardie mi ha incontrato alla base.<new_line>'Ciao, messaggero.']],
				['4'] = [[Ah, sapevo che qualcosa non andava... Lascia qui il carico e prendi la tua ricompensa.']],
				['3'] = [[Il capo delle guardie mi ha dato una pacca sulla spalla.<new_line>'I miei uomini mi hanno parlato bene di te. Continua così. Ecco la tua ricompensa.']]
			},
			['choice'] = {
				['2'] = [[Beh, ciao anche a te']],
				['4'] = [[Fai rapporto sull'attacco e i morti]],
				['3'] = [[Annuisci]]
