		},
		['order_delivery_quest'] = {
			['episode'] = {
				['2'] = [[Ho trovato il magazziniere. Non sembrava molto felice. Mi ha mostrato il carico e mi ha chiesto:<new_line>'Pensi che ti daremo tutta questa roba gratis?']],
				['1'] = [[Ho trovato il magazziniere che, senza troppe cerimonie, mi ha mostrato il carico.]]
			},
			['choice'] = {
				['1'] = [[Prendi il carico e vattene]],
				['4'] = [[Ecco i soldi. Contento?']],
				['3'] = [[Rifiuta di pagare e vattene]]
			}
		},
		['gravedigger'] = {
			['episode'] = {
				['2'] = [[Il beccamorti non voleva saperne di parlare, ma frugando nel suo mantello trovo uno strano messaggio: una specie di lista della spesa piena di parti umane (braccia, gambe e teste) con in calce la firma ]],
				['1'] = [[È stato facile trovare il beccamorti: un uomo dall'aria inquietante, fermo accanto al cancello del cimitero. Nella sua mano destra regge una pala, nella sinistra... una gamba in putrefazione. Scrutandomi con un ghigno beffardo, scaglia l'arto in un vicino carretto traboccante di membra, si getta in spalla la pala e corre nelle profondità del cimitero. È ora di porre fine a questo scempio!]]
			}
		},
		['new_year_boss_5_4'] = {
			['choice'] = {
				['2'] = [["Stai dicendo che dovremmo arrenderci?"]],
				['1'] = [["Allora attacchiamo!"]],
				['4'] = [["Posso farcela?"]],
				['3'] = [["Il mio portale?"]]
			},
			['episode'] = {
				['2'] = [["Abbiamo usato quasi tutta la nostra energia con l'aspirapolvere! Finire dritti nella trappola di Frosty non è soltanto folle, è un suicidio."]],
				['1'] = [["Riesco a percepire una traccia della magia che ci è stata rubata... Frosty si è rintanato da qualche parte qui vicino e sta aspettando la nostra prossima mossa."]],
				['6'] = [["Come se ci fosse un'altra opzione. Coraggio, ragazzino. Penso tu abbia una minima possibilità. Decora l'albero un'ultima volta mentre preparo tutto il resto."]],
				['5'] = [["Sì, quello su cui sei rimasto per tutto questo tempo, genio. Non ci è rimasta molta energia.<new_line>Perciò, una volta che sarai nel nascondiglio di Frosty, potrai fare affidamento solo sulle tue forze."]],
				['4'] = [["Oh, e va bene. Visto che sono comunque bloccato qui con voi... Posso suggerire un piano del tutto folle.<new_line>Immagino di poter reindirizzare il tuo portale verso il nascondiglio di Frosty."]],
				['3'] = [["Che altra scelta abbiamo? Abbiamo già fatto tutto ciò che potevamo!"]]
			}
		},
		['halloween_boss_2022_6_2'] = {
			['choice'] = {
				['10'] = [[Continua a muoverti]],
				['1'] = [[Attraversa il guado del fiume]],
				['2'] = [["Dove siamo? E dov'è l'asino?"]],
				['9'] = [["Che cosa? Tutto qui?"]],
				['8'] = [[Estrai l'arma]],
				['7'] = [["Assisti al potere supremo delle scartoffie e della burocrazia!" (Strappa la pagina su cui è scritto il tuo nome)]],
				['6'] = [["Cerchi questa?" (Tira fuori la pagina con la lista delle vittime del Mostro di Frankenstein)]],
				['5'] = [["Non puoi sconfiggermi!"]],
				['4'] = [["Al diavolo, me ne vado a casa."]],
				['3'] = [["Che stai dicendo? Ho portato a termine il contratto o no?"]]
			},
			['episode'] = {
				['2'] = [[A ogni mio passo, il bosco diventava sempre più tetro, mentre gli occhi dei teschi brillavano di un rosso acceso. A un certo punto, arrivai a una deviazione, e per poco non mi imbattei in un variegato gruppo di mostri stanziati attorno alla curva.]],
				['1'] = [[Nel mezzo del cammin, mi ritrovai per una selva oscura. Ai fianchi del sentiero vi erano dei teschi impalati, e delle voci inquietanti provenivano da dietro gli alberi.]],
				['12'] = [[A conferma di ciò che avevo appena detto, un fulmine si schiantò su di noi con un ruggito colpendo uno dei portali! Poi scomparve con un bagliore accecante. Niente male! Questo significa che il mio Gigawattore può distruggere i portali...]],
				['11'] = [["Ma cosa... Come?! Dall'altro lato della stessa pagina?! Ma sono stato io stesso a consegnartela..."]],
				['15'] = [["No, vorremmo anche darti qualcosa come compenso. Tieni. A proposito, ho ancora degli affari da sbrigare nel tuo mondo. Perciò, potresti passare da noi se ti servisse qualcosa."]],
				['14'] = [["Non temere, amico mio. Sono un nuovo Mietitore. Sono stato inviato qui per prendere il posto del mio disonesto collega. Tutti quegli imbrogli con contratti e subcontratti... un vero peccato...<new_line>Porgo le mie scuse a nome di tutto il mondo dei morti. Ti rispedisco subito al mondo da cui provieni."]],
				['10'] = [["La tua forza è senza dubbio aumentata da quando ti ho dato il primo contratto. Ma ci tengo a precisare una cosa.<new_line>Ho iniziato ad avere in mente dei piani per te da quando ho visto il tuo nome nel Libro dei morti. Ciò significa... che non si sfugge al destino, ricordi?"<new_line>(Il Mietitore estrae il Libro dei morti e inizia a sfogliarlo)]],
				['13'] = [[Il Mietitore vide che stavo per estrarre il Gigawattore ed esclamò furiosamente: "Oh, maledetto! Non importa, non puoi chiudere tutti i portali. Non sei abbastanza forte, sciocco mortale!"]],
				['9'] = [["Dubito fortemente che tu ci riesca, poiché hai attraversato il Fiume dell'Oblio. E adesso i nostri amici in comune verranno qui a farti a pezzi."<new_line>Appena il Mietitore schioccò le dita, apparvero ovunque dei portali che scintillavano di elettricità.]],
				['8'] = [["Lascia perdere il contratto, ho altri obiettivi in mente. Hai sviluppato un grande potere demoniaco. Molti mietitori possono soltanto sognare di ottenere un'anima così potente."]],
				['7'] = [["Questo, mortale, è il mio mondo. Ti do il benvenuto! Non preoccuparti dell'asino, poiché è esattamente dove dovrebbe essere.<new_line>Vedi, tutte queste bestie e mostri sono dei pesci grossi, eccome... Ma io voglio catturare un vero squalo."]],
				['6'] = [[L'altra sponda del fiume si rivelò essere oscura e desolata, come se appartenesse a un altro mondo. Poi il fiume venne completamente inghiottito dalla nebbia. Come se non bastasse, iniziò anche a piovere, e con il bagliore di un fulmine si scatenò un temporale.<new_line>Non c'era traccia dell'asino, ma il Mietitore era lì.]],
				['5'] = [[Vidi ancora più mostri sbucare dal bosco! Non avevo alcuna possibilità contro un'orda del genere. Non avevo altra scelta che attraversare il guado e arrivare all'altra sponda.]],
				['4'] = [[Sbucai accanto al fiume. Era piuttosto strano, poiché non ricordavo di averlo visto sulle mappe.<new_line>Il Mietitore si trovava dall'altra sponda e mi faceva un cenno con la mano:<new_line>"Qui c'è un guado. Presto, attraversalo, l'asino sta scappando!"<new_line>Appena pronunciò quelle parole, sentii dei rami spezzarsi alle mie spalle.]],
				['3'] = [[Tutto questo casino per colpa di quello spregevole asino...<new_line>Ma posso sentirlo ragliare, non deve essere molto lontano. Questa volta non lo lascerò scappare!]]
			}
		},
