		['emba_energy_medium'] = {
			['episode'] = {
				['2'] = [["È un documentario in varie serie. Il nostro pubblico deve vedere con i propri occhi cosa sono i biomutanti e perché sono pericolosi.<new_line>Ne ho catturati un paio. Ho bisogno di un attore tosto che riesca a scappare da loro mentre filmo. Ma dove posso trovarne uno al giorno d'oggi? Tu mi sembri la persona perfetta per questo ruolo."]],
				['1'] = [["Ciao! Guarda cosa sta succedendo! Riesco a malapena a metterlo sul nastro per i posteri! Ho bisogno di filmare una scena di inseguimento adesso. Vuoi partecipare?"]],
				['12'] = [["Oh cielo, avresti potuto impegnarti di più nel nome dell'arte. Adesso mi toccherà catturare dei nuovi biomutanti.<new_line>Comunque, bevi questo e rimettiti in sesto, ho molto da fare."]],
				['11'] = [["Oh cielo, avresti dovuto impegnarti di più, nel nome dell'arte. Adesso mi toccherà catturare dei nuovi biomutanti. Comunque, bevi questo e rimettiti in sesto, ho molto da fare."]],
				['10'] = [[Sono inciampato e sono caduto a terra. Ho sentito il rumore degli spari e gli ululati dei biomutanti.<new_line>Sentivo il Regista che imprecava nel suo megafono.]],
				['9'] = [[Il mio trucco ha funzionato e sono tornato dal Regista senza farmi vedere.<new_line><new_line>"Non è una mossa sbagliata, considerando che non sei un professionista, nel film sarà bello. Ti inviterò alla prima quando sarà pronto."]],
				['8'] = [[Ho raggiunto l'albero e mi sono arrampicato. Sembra che i biomutanti mi abbiano perso di vista, ma si aggirano nelle vicinanze.<new_line>Posso saltare giù e correre verso la troupe cinematografica, o distrarre i biomutanti con qualcosa di commestibile e svignarmela senza farmi notare.]],
				['7'] = [[Li avevo quasi raggiunti. Mancavano solo dieci metri... quando ho sentito lo schiocco delle armi da fuoco e i biomutanti sono caduti a terra, tutti morti.<new_line><new_line>"Non avresti dovuto mettere in pericolo la troupe televisiva, anche se il tuo ritorno è stato sensazionale. Ti inviterò a una proiezione quando il film sarà pronto."]],
				['6'] = [[Ho cambiato la traiettoria e ora sto correndo verso la troupe cinematografica. I biomutanti che mi inseguono diventeranno presto un loro problema.]],
				['5'] = [[Ho staccato i miei inseguitori, ma inizio a sentire la stanchezza. Posso iniziare a tornare indietro per unirmi di nuovo alla troupe cinematografica. Oppure potrei provare ad arrampicarmi su un albero e trovare qualcosa mentre sono lassù.]],
				['4'] = [[Avevo già iniziato a correre quando mi sono voltato e ho visto un intero sciame di feroci biomutanti correre verso di me. Devo accelerare.]],
				['3'] = [["Eccellente! Sei pronto? Grisha, apri la gabbia! Azione! Bene, vai, vai, vai!"]]
			},
			['choice'] = {
				['2'] = [["Va bene, partecipo."]],
				['1'] = [["Cos'è la scena d'inseguimento che stai filmando?"]],
				['11'] = [[Prova ad alzarti]],
				['10'] = [[Lascia cadere il cibo e svignatela]],
				['9'] = [[Salta giù e continua a correre]],
				['8'] = [[Corri dalla troupe cinematografica]],
				['7'] = [[Corri verso l'albero]],
				['6'] = [[Torna indietro]],
				['5'] = [[Incline alla caduta]],
				['4'] = [[Scappa]],
				['3'] = [[Guardati intorno]]
			}
		},
		['baba_yaga_1'] = {
			['choice'] = {
				['10'] = [[Ricomincia]],
				['1'] = [[Disegna un cerchio con il sale]],
				['12'] = [[Capanna! Ehi, capanna! Devi girati verso di me, non verso la foresta!]],
				['11'] = [[Termina il rituale]],
				['2'] = [[Posiziona le ossa in modo da formare delle zampe di gallina]],
				['9'] = [[Guarda oltre la tua spalla sinistra]],
				['13'] = [[Mi ha mandato il cacciatore. Devo trovare il mostro di...]],
				['14'] = [[Raccogli la lista]],
				['8'] = [[Guarda oltre la tua spalla destra]],
				['7'] = [[Batti tre volte le mani]],
				['6'] = [[Batti due volte le mani]],
				['5'] = [[Batti le mani]],
				['4'] = [[Gira attorno al cerchio da sinistra verso destra]],
				['3'] = [[Gira attorno al cerchio da destra verso sinistra]]
			},
			['episode'] = {
				['2'] = [[Temo di aver sbagliato qualcosa. Devo ricominciare daccapo.]],
				['1'] = [[Questo luogo mi sembra adatto a invocare Baba Yaga. Devo assicurarmi di non sbagliare il rituale.]],
				['6'] = [[Raccolgo la lista e torno a voltarmi verso la strega, ma sia lei che il cerchio sono spariti.]],
				['5'] = [[„Il gomitolo di lana, naturalmente. È sempre la stessa storia con voi“ borbotta la vecchia, sparendo all'interno della sua dimora. Un minuto più tardi, vedo schizzare dalla finestra un foglietto di carta, seguito dalla voce di Baba Yaga: „Invocami di nuovo quando avrai trovato tutti gli oggetti della lista e ti darò il gomitolo“.]],
				['4'] = [[Controvoglia, la capanna si accinge a girarsi, ma si ferma di scatto. Una vecchia signora si affaccia seccata dalla finestrella che mi sta di fronte e inizia a sputare una valanga di domande: „Chi sei, ragazzo? Perché diamine mi hai invocata? Che vuoi da me?“.]],
				['3'] = [[Mi volto e vedo che una capanna con delle zampe di gallina è apparsa all'interno del cerchio, come se fosse sempre stata lì. La mia posizione mi permette di vedere solo il retro della dimora.]]
			}
		},
		['emba_isaev_izhevsk'] = {
			['episode'] = {
				['2'] = [["Purtroppo, non abbiamo scoperto niente. L'oggetto non viene menzionato, è come se lì non fosse mai stato creato nulla. L'unica informazione che posso darti è che in questa città della scienza ci sono due serbatoi per il pompaggio della biomassa.<new_line>Ricordalo quando lo pomperai fuori dal serbatoio della stazione biologica. Purtroppo, dovrai scoprire il resto sul posto. Abbiamo decifrato il codice di accesso dello scomparto segreto del laboratorio. Eccolo, l'ho scritto qui."<new_line>(Ti porge un pezzo di carta con il codice)]],
				['1'] = [["Purtroppo, non sappiamo quasi nulla di ciò che stai per incontrare. Però interessava molto a Kurganov e dobbiamo scoprire perché.<new_line>Abbiamo già perduto il Navigatore e non posso mandarti alla città della scienza alla cieca. Cerca dei documenti segreti per permetterci di ottenere altre informazioni."]],
				['6'] = [["Dobbiamo raggiungere la stazione biologica dove dovrebbe trovarsi il professor Gerin. Spero che sia ancora vivo. Partiremo non appena sarai pronto."]],
				['5'] = [["Prima della guerra, la stazione biologica era un ordinario centro analitico che non conduceva progetti propri. Poche persone analizzavano il DNA del micelio mutato. Forse queste cose sono collegate... ma non sappiamo ancora esattamente come."]],
				['4'] = [["I mutanti delle terre desolate hanno ereditato i loro processi riproduttivi dagli animali. Hanno un'evoluzione perversa. Abbiamo a che fare con una sorta di mutazione consapevole. Questo organismo ha cambiato sé stesso nel modo in cui aveva bisogno di cambiare."]],
				['3'] = [["Ho dei campioni dalla creatura che hai incontrato. A giudicare dai documenti, le mutazioni sono avvenute autonomamente, o se non altro non sono state apportate direttamente dagli scienziati.<new_line>L'unica menzione che trovo nei documenti segreti o nella scatola nera è il nome: l'Ibridatore. È molto allarmante."]]
			},
			['choice'] = {
				['2'] = [["Cos'ha che non va? Ci sono molti mutanti nelle terre desolate."]]
			}
		},
		['celebrate_new_year'] = {
			['choice'] = {
				['2'] = [[Nonno Gelo!]],
				['1'] = [[Nonno Gelo!]]
			},
			['episode'] = {
				['2'] = [[La fanciulla di neve raduna tutti in cerchio attorno all'albero che ho addobbato. «Ora dobbiamo chiamare Nonno Gelo!» scandisce con voce sonora.]],
				['1'] = [[Con mia sorpresa, accanto all'albero trovo una folla attorno a una tavola imbandita. «Vedi? È tutto pronto, e queste sono le persone che hai aiutato quest'anno» mi spiega la fanciulla di neve prendendomi per mano. «Avanti, ormai non manca molto.»]],
				['7'] = [[La regina d'inverno è ancora intrappolata nel ghiaccio. La libereremo quando tornerà a credere nella bontà delle persone afferma Nonno Gelo spostando il suo sguardo su di me, «Ti sono eternamente riconoscente per il tuo aiuto. Spero che ci rivedremo l'anno prossimo. Nel frattempo, accetta questo regalo d'addio. Buon Anno Nuovo!»]],
				['6'] = [[La festa si protrae per tutta la notte. Nonno Gelo augura buon capodanno a tutti e inizia a distribuire i regali. Infine, lui e la fanciulla di neve vengono a ringraziarmi.]],
				['5'] = [[Vi sento! Vi sento! sbotta un vocione potente da un albero vicino, da cui, come se nulla fosse successo, fa capolino Nonno Gelo con il suo cappotto rosso, un bastone nuovo di zecca e un sacco pieno di regali! La fanciulla di neve corre ad abbracciarlo e l'uomo scoppia in una risata affettuosa, consolandola con dei colpetti sulla schiena.]],
				['4'] = [[Questa volta viene meglio, e le labbra della gente attorno a me si increspano in un sorriso. «Ancora una volta!» esclama con entusiasmo la fanciulla di neve.]],
				['3'] = [[Tutti si uniscono al coro, ma senza troppa convinzione né coordinazione. «Di nuovo!» sbotta la fanciulla di neve.]]
			}
		},
		['cafe_wolves'] = {
			['choice'] = {
