		['new_year_boss_1'] = {
			['choice'] = {
				['2'] = [["Di certo i sospettati non mancano."]],
				['1'] = [["Cos'è successo qui?"]],
				['9'] = [["Quindi devo fare delle buone azioni..."]],
				['8'] = [["Ok, hai qualche decorazione?"]],
				['7'] = [["Che cosa c'entra l'albero con questo?"]],
				['6'] = [["Che ne dici di cercare..."]],
				['5'] = [["A me? Come?"]],
				['4'] = [["Ehi, non mettermi alla prova. Chi siete?"]],
				['3'] = [["Dov'è Babbo Natale?"]]
			},
			['episode'] = {
				['2'] = [["Tremo al solo pensiero. Spero solo che Babbo Natale sia riuscito a scappare. Che razza di delinquente farebbe una cosa simile nel tuo mondo?"]],
				['1'] = [[Siamo arrivati in una residenza di legno. Le pareti sono carbonizzate, le vetrate colorate sono rotte e la porta ornata è stata sfondata.]],
				['12'] = [["Ah, la magia di questo albero di Natale funziona solo con le decorazioni speciali che la gente comune conserva in casa per tutto l'anno con amore e affetto. Non si possono costruire in una fabbrica... purtroppo."]],
				['11'] = [["Mmh, la tecnologia non è il mio forte... Tutto ciò che so è che l'albero di Natale deve essere decorato per far funzionare i portali."]],
				['10'] = [["Ah, hai ragione! La scomparsa di Babbo Natale ha chiaramente qualcosa a che fare con la banda guidata da... Krampus? Beh, conosco un grande segugio. Ma per portarlo qui, dovrai decorare di nuovo l'albero di Natale."]],
				['13'] = [["E riceverai queste decorazioni come doni di ringraziamento. Presto, devi aprire il portale per il segugio. Mettiti all'opera!"]],
				['9'] = [["Nel frattempo, inizierò a vendere la mia merce qui. Non c'è traccia di Babbo Natale, ma le festività sono comunque alle porte. Passa a dare un'occhiata al mio negozio. Forse troverai qualcosa di utile."]],
				['8'] = [["Oh oh oh, eccellente. Fai delle buone azioni e in cambio ricevi doni di ringraziamento. È così che si diventa un maestro Babbo Natale."]],
				['7'] = [["Ah, il tuo Babbo Natale ha ordinato dei costumi speciali da me. Sono la chiave di tutta la faccenda. Uno di essi costerà soltanto... No, lascia perdere. È chiaro che il tuo mondo sia in pericolo. Puoi avere il costume gratuitamente. È il minimo che possa fare."]],
				['6'] = [["È un oltraggio. Che furfanti! Ma una cosa è chiara: il Natale è in pericolo nel tuo mondo. Spero che il mio collega Babbo Natale sia ancora vivo. Finché non lo troviamo, sembra che spetterà a te farti carico delle sue responsabilità."]],
				['5'] = [["Sei stupido o cosa? Ho detto di smammare. Anzi, no! Krampus ama la gente stupida. Perciò, voi cazzoni presto lo incontrerete! Forza, prendeteli!"]],
				['4'] = [["Il vostro Babbo Natale è andato. Smammate, prima che diamo fuoco anche alle vostre barbe da nonnetti."<new_line>Dalle spalle dell'uomo è volata una bottiglia che mi ha colpito alla gamba.]],
				['3'] = [[In quel momento, abbiamo sentito il rombo dei motori, e delle persone in motocicletta sono uscite dalla residenza bruciata.<new_line><new_line>Uno di loro ci ha urlato contro.<new_line>"Che volete? Adesso qui comandiamo noi!"]]
			}
		},
		['halloween_energy_hard'] = {
			['episode'] = {
				['10'] = [["Ho quest'amico... è uno stregone! E mi ha dato un appunto. Mi deve un favore... Il testo è un po' sbiadito, però... Se lo leggi, lui apparirà..."]],
				['1'] = [["Oh, sei tu. Insomma, quello... Beh, al diavolo... Ecco, con te... No, accidenti... Ho vinto alla lotteria e non farò più il contadino! Ma il montepremi è in valuta straniera. Non ho idea di cosa potrei fare, perciò sto cercando una soluzione. Potremmo... pensarci su insieme."]],
				['12'] = [["Sogni troppo in grande. La politica di immigrazione negli altri mondi può essere molto rigida. Potresti andare in vacanza con i suoi soldi, ma persino questo richiederebbe un mucchio di scartoffie. Ora fuori dai piedi!"]],
				['11'] = [[Dopo aver letto l'appunto, apparve lo stregone.<new_line><new_line>"Che cosa volete, zoticoni?"]],
				['13'] = [["Accidenti... Puoi almeno far apparire una bevanda forte? Posso pagare..."<new_line>Il contadino offrì delle monete allo stregone.]],
				['14'] = [["Ti sembro per caso un oste?! E va bene. Abracadabra. Ecco, ho incantato il tuo distillato clandestino col fuoco infernale. Se osi evocarmi ancora, farò lo stesso con te."<new_line>Lo stregone scomparve così com'era apparso.]],
				['8'] = [["Che cosa?"]],
				['7'] = [["Fiu... bella roba... Quei... Quei, ehm, babbuini mi hanno detto... che i soldi vanno bene. Potrei comprare una fattoria. Ma come?"]],
				['6'] = [["A chi servono? Ho controllato... Il metallo non vale niente, perciò nessuno lo accetterà. Dobbiamo bere ancora un po' per far lavorare i nostri cervelli."<new_line>Il contadino riempì entrambi i bicchieri.]],
				['5'] = [["Sto cercando di decidere... Cosa fare con le mie ricchezze..."]],
				['4'] = [["Avanti... Questa roba ti farà screscere i peli sul petto... Un brindisi all'amicizia!"<new_line>Il contadino riempì entrambi i bicchieri.]],
				['3'] = [[Mandai giù il distillato in un solo sorso. Era incredibilmente forte e mi bruciava in gola.<new_line>"A... allora... Ti pia... hic... piace il mio... distillato clandestino?"]],
				['9'] = [["Esatto! Em... Emigrare! Così potrei spendere i soldi in un altro mondo. Beviamoci su!"<new_line>Il contadino riempì entrambi i bicchieri.]],
				['16'] = [["Sai... sei un tipo a posto... Forse dovrei... darti... tutte le mie ricchezze..."<new_line>Detto questo, il contadino perse conoscenza.]],
				['15'] = [["Non lo so... hic... Direi che dovremmo provare la sbobba che ha fatto lo stregone."<new_line>Il contadino riempì entrambi i bicchieri.]],
				['17'] = [[Mi risvegliai in un luogo vuoto e sconosciuto. Ho un mal di testa martellante. Come sono finito qui? Cos'è successo ieri?]],
				['2'] = [[Il contadino mise un altro bicchiere sul tavolo e lo riempì con il distillato clandestino.<new_line>"B... bel lavoro..."]]
			},
			['choice'] = {
				['10'] = [["Che cosa farete adesso?"]],
				['1'] = [["Forza."]],
				['12'] = [[Alzati e vattene]],
				['11'] = [[Prendi le ricchezze]],
				['2'] = [["Non c'è tempo."]],
				['13'] = [[Arrenditi]],
				['9'] = [["Il contadino vuole em... emigrare... verso..."]],
				['8'] = [[Leggi l'appunto]],
				['7'] = [["Em... emigrare."]],
				['6'] = [["Em..."]],
				['5'] = [["Forse possiamo... darlo in pegno?"]],
				['4'] = [["È davvero forte."]],
				['3'] = [[Bevi]]
			}
		},
		['take_stash_quest'] = {
			['episode'] = {
				['2'] = [[Quando ho raggiunto il posto, ho visto una buca fresca e delle impronte.]],
				['1'] = [[Ho trovato facilmente il nascondiglio.]],
				['11'] = [[Il mio stratagemma ha funzionato. L'orso si è distratto e ha smesso di interessarsi a me.]],
				['10'] = [[Beh, chi è il re del bosco ora?']],
				['9'] = [[In qualche modo sono riuscito a rimuovere la cassa dal cadavere. Ma improvvisamente ho sentito un forte fruscio alle mie spalle. Mi sono girato e ho visto un orso. L'orso mi guardava come se avesse trovato il suo pranzo.]],
				['8'] = [[Mentre esaminavo il corpo, ho sentito un fruscio dietro di me. Mi sono voltato di scatto e ho visto un orso.]],
				['7'] = [[Ho la cassa. Non c'è altro da fare qui.]],
				['6'] = [[Non è stato facile, ma ho sconfitto i banditi.]],
				['5'] = [[Le impronte mi hanno condotto a un cadavere. Il corpo giaceva a terra e stringeva tra le braccia una cassa sporca di terra.]],
				['4'] = [[Le impronte mi hanno condotto direttamente nell'accampamento dei banditi. Chiaramente, hanno rubato loro il tesoro. Devo tornare alla base e fare rapporto. Tuttavia scommetto che se riconquistassi la refurtiva da solo, mi pagherebbero di più.]],
				['3'] = [[Chiunque abbia lasciato quel tesoro era di fretta e non ha scavato molto. Sono riuscito a raggiungere velocemente la cassa.]]
			},
			['choice'] = {
				['2'] = [[Dissotterra il tesoro con le mani]],
				['1'] = [[Dissotterra il tesoro con la pala]],
				['12'] = [[Rompi il barattolo di miele sulla pietra]],
				['11'] = [[Lancia il pesce in faccia all'orso]],
				['8'] = [[Prendi la cassa]],
				['6'] = [[Esamina il cadavere]],
				['5'] = [[Segui i passi sul terreno]],
				['4'] = [[Assicurati di essere preparato prima di prendere qualcosa]],
				['3'] = [[Prendi la cassa e vattene]]
			}
		},
		['emba_def_easy2'] = {
			['episode'] = {
				['2'] = [["Qui potrebbe esserci un laboratorio da campo, dove qualcuno stava studiando tutta la nuova attività biologica. Molto prima di noi, capisci?<new_line>Ma non riusciamo ancora a entrare. Ci sono molti biomutanti lì."]],
				['1'] = [[Qualcosa sta preoccupando gli scienziati. In risposta alle mie domande, mi hanno portato in un edificio a un piano, circondato da un recinto di filo spinato.]],
				['5'] = [["E dov'è il nostro combattente, è ferito? Oh, che brutta notizia. Scusa, ma devo prendermi cura di lui, non possiamo perdere uno specialista!"]],
				['4'] = [["Ottimo lavoro, compagno! È un peccato che non tutti i nostri team di ricerca abbiano combattenti come te."]],
				['3'] = [["Veramente? È fantastico. Un paio dei nostri colleghi sono già sul posto. Sono soldati di un'unità delle forze speciali... ma persino a te potrebbe servire un po' di fuoco di supporto.<new_line>Hanno già fatto irruzione nell'edificio, ma si continua a combattere nel parcheggio sul retro. Da dove pensi di entrare?"]]
			},
			['choice'] = {
				['2'] = [[Nell'edificio]],
				['1'] = [["Che cos'è questo?"]],
				['3'] = [[Parcheggio dietro l'edificio]]
			}
		},
		['leprechaun_debts'] = {
			['episode'] = {
				['1'] = [[Ho a malapena il tempo di spiegare la ragione della mia visita, prima che i volti dei leprecani si contorcano dalla rabbia, pronti ad attaccare.]]
			}
		},
		['embacity_biolab_0'] = {
			['episode'] = {
				['2'] = [[Dietro la porta c'è una scala. Mi ha condotto in un ampia sala che sembra completamente abbandonata. Dopo averla esaminata, ho trovato una porta poco appariscente chiusa da una serratura a codice.]],
				['1'] = [[La prima cosa che mi ha colpito quando sono entrato nel laboratorio è stata la luce rossa lampeggiante sopra la porta sulla parete di fondo della stanza. Quella porta si è rivelata essere estremamente pesante e controllata automaticamente. Ma al momento è leggermente socchiusa.]],
				['3'] = [[Un odore di muffa si diffondeva dallo scomparto aperto. Guardando più da vicino, ho visto un contenitore e, sopra di esso, un cartello con la scritta "Registratore di emergenza". È la scatola nera che stavo cercando.]]
			},
			['choice'] = {
				['2'] = [[Inserisci il codice]],
				['1'] = [[Varca la soglia]]
			}
		},
		['emba_craft_easy'] = {
			['episode'] = {
				['2'] = [["Grazie! Anche se non vuoi farti coinvolgere troppo nella nostra ricerca, stai aiutando la scienza a fare progressi! È importante!"]],
				['1'] = [["Ciao! Abbiamo girato per le terre desolate con i nostri ATV e gli ingegneri stanno iniziando a esaurire i pezzi di ricambio per ripararli.<new_line>Puoi aiutarci con le provviste?"]]
			}
		},
		['emba_commander_easy'] = {
			['episode'] = {
				['2'] = [["Eccellente! Non ti useremo come carne da macello, ovviamente. Devi solo mantenerti a una distanza di sicurezza e inviare segnali alle torrette mentre facciamo le nostre osservazioni.<new_line>Hai esperienza, quindi capirai come funziona. Andiamo!"]],
				['1'] = [["Ciao! Vedi quei biomutanti? Li abbiamo cosparsi con una soluzione speciale e dovrebbero comportarsi diversamente in battaglia.<new_line>Abbiamo bisogno di qualcuno che contenga i loro attacchi mentre effettuiamo le nostre osservazioni."]],
				['3'] = [["Eccellente, eccellente. Dobbiamo cambiare i radicali liberi... e aumentare la diffusione intorno ai biomutanti...<new_line>Ah, sei ancora qui! Grazie per l'aiuto. La prossima volta che vieni, proveremo una nuova formula."]]
			},
			['choice'] = {
				['2'] = [["Non posso farlo."]],
				['1'] = [["Posso aiutarti."]]
			}
		},
		['new_year_boss_6_2'] = {
			['choice'] = {
				['2'] = [["Abbiamo molti Babbi."]],
				['1'] = [["Ciò che conta è che tu sia tornato."]],
				['7'] = [["Urrà!"]],
				['6'] = [["Buon Natale!"]],
				['5'] = [["Puoi dirlo forte."]],
				['4'] = [["Ma soltanto per un anno!"]],
				['3'] = [["Che cosa vorresti dire?"]]
			},
			['episode'] = {
				['2'] = [["Va sempre così, choomba."]],
				['1'] = [["Amici, non so che cosa dire in mia difesa. All'inizio, volevo fare ciò che era più giusto. Ma poi... Per quanto riguarda ciò che è successo dopo, non ho nessuna scusa."]],
				['8'] = [["Ee eeee!"]],
				['7'] = [["Va bene, festeggeremo, ma poi ci rimetteremo al lavoro! Tutti amate festeggiare, ma come sempre sono io quello che deve mettere a posto le cose. Comunque, buon Natale!"]],
				['6'] = [["Ti aiuteremo, socio. Non riesco a credere a ciò che sto per dire, ma ne ho abbastanza degli affari!<new_line>Ci meritiamo una festa tutta nostra. Dobbiamo festeggiare!"]],
				['5'] = [["Per tutto questo tempo, ti sei fatto carico delle mie responsabilità in maniera egregia. Molto di più di quanto abbia fatto io... Orso ha ragione: ormai sono utile solo se vado in pensione."]],
				['4'] = [["Esatto! Dobbiamo ricostruire molto se vogliamo che la gente festeggi il Natale. Ma non credo che Babbo Natale sia nelle condizioni di fare una cosa del genere."]],
				['3'] = [["La gente non fa che inventare scuse! Ma chi metterà a posto le cose?! Il Natale arriverà da solo anche senza Babbo Natale?"]]
			}
		},
		['mirror_lady'] = {
			['episode'] = {
				['2'] = [[Dopo lo scontro, inizio a perlustrare la casa in cerca delle tracce di Igor. La marionetta di ceramica sul comò attira la mia attenzione e ho la sensazione che mi stia fissando. Quando mi avvicino per osservarla meglio, la marionetta mi colpisce al volto e, scalciando furiosamente i suoi piedini, si divincola e scompare alla mia vista.]],
				['1'] = [[L'intera casa è tappezzata di specchi. Seguendo le istruzioni dell'oste, accendo una candela di fronte a uno di essi e invoco Bloody Mary. Inizialmente, percepisco solo un profumo stucchevole, ma poi sento dei passi e una voce pacata... 'Bene, bene, bene... Chi abbiamo qui?'.]]
			}
		},
		['embacity_biolab_helecopter'] = {
			['episode'] = {
				['1'] = [[Diversi biomutanti si aggirano intorno all'elicottero. È strano: non ci sono molti pollini e sporafiori che amano. A quanto pare, è il centro della città ad attrarli.]]
			}
		},
		['lair_beemother'] = {
			['episode'] = {
				['1'] = [[Sembra che l'ape madre stia usando questo alveare per crescere la sue prole mutante. E probabilmente sarà decisa a proteggerlo.]]
			}
		},
		['emba_isaev_krasnoyarsk'] = {
			['episode'] = {
				['2'] = [[Naturalmente, un conflitto militare non sarebbe la soluzione. Però abbiamo un piano. Ti spiegherò i dettagli in seguito."]],
				['1'] = [["Eccoti qui, finalmente. Siamo nei guai. Il Sindacato petrolifero ha preso il nostro prototipo del meccanismo per il rinverdimento. Dobbiamo impedire loro di utilizzarlo, altrimenti le terre desolate si trasformeranno in un inferno verde."]],
				['12'] = [["Sì! Torna tutto. Nulla impedirebbe a questa cosa di diffondere spore in tutto il mondo e di collegare tra loro tutte le città, soprattutto perché sembra nutrirsi di radiazioni elevate.<new_line>Ma tutto ciò che vuole da noi è essere lasciata in pace. A quanto pare, non riesce a distinguere una persona dall'altra e vede l'umanità come un unico organismo. Ogni essere umano è l'umanità per questa creatura."]],
				['11'] = [["Il micelio reliquia è stato estratto dalle viscere della terra e sottoposto a un'intensa selezione. Tutti questi eventi non sono altro che il tentativo di questa creatura di proteggersi ancora una volta dall'umanità.<new_line>La simbiosi con il computer non solo le ha offerto opportunità senza precedenti, ma è stata anche sollecitata, per così dire, dai nostri metodi di lavoro."]],
				['13'] = [["Ha ragione. Sono d'accordo con il nostro amico micelio. L'ambizione mista alle migliori intenzioni dell'umanità gli ha fornito tutti gli strumenti. Di chi è la responsabilità maggiore?<new_line>La risposta non ci mette certo in buona luce. Ecco perché l'Emissario è così simile a noi, per non parlare del destino di Gerin e Kurganov..."]],
				['14'] = [["A presto, amico mio. A presto."]],
				['15'] = [["Ciao, caro compagno. È tutto confermato: il prototipo è stato effettivamente distrutto."]],
				['16'] = [["E ora le nostre strade si dividono."]],
				['6'] = [["Inoltre, tutte le sue mutazioni sono una reazione agli stimoli esterni. L'errore fatale del progetto E.M.B.A. è stato quello di collegare il micelio reliquia a un supercomputer.<new_line>Il micelio ha reagito radicalmente a questo cambiamento. A quanto pare, la comparsa di biomutanti e di individui autonomi come l'Emissario è dovuto al fatto che il micelio ha ottenuto accesso ai circuiti logici del computer più potente al mondo."]],
				['5'] = [["Inizialmente avevamo pensato che il micelio mutasse spontaneamente e che il problema principale fosse questa casualità.<new_line>Tuttavia, secondo le informazioni che hai ottenuto, le cose funzionano diversamente. Il micelio è più stabile se viene lasciato in pace.<new_line>Questo micelio reliquia esiste da migliaia di anni in forma stabile in caverne isolate."]],
				['4'] = [["Ciao, collega. Grandi notizie! Secondo i documenti, nella stazione biologica c'è un micelio reliquia collegato a un supercomputer. È lì che si trova il centro decisionale."]],
				['3'] = [["Ora che il prototipo è nelle mani dei petrolieri, non ha senso che chiudiamo il progetto.<new_line>Dobbiamo cercare in tutti i modi di mettere al sicuro la tecnologia di rinverdimento. Ancora una volta, devo chiederti di trovare i documenti. Ci servono per il nostro piano e per l'intero studio."]],
				['23'] = [["Certo, non abbiamo avuto il coraggio di distruggerlo completamente. L'abbiamo messo nella caverna dove è rimasto negli ultimi millenni. Senza computer. Nel suo ambiente naturale.<new_line>Il Biologo e alcuni miei colleghi resteranno qui per un po', per rimuovere tutta la tecnologia dalle stazioni biologiche. Alcune le distruggeremo, altre le porteremo con noi."]],
				['22'] = [["No, perché credo che la gente possa imparare una lezione da tutti questi disastri. Nel frattempo, diventeremo Archivisti nel pieno senso della parola.<new_line>Immagazzineremo le informazioni e preverremo i peggiori errori della gente lungo il cammino della conoscenza."]],
				['21'] = [["Abbiamo decodificato la scatola nera. Il segnale è stato generato ed è già stato inviato al prototipo di micelio. Il dispositivo si autodistruggerà e ai petrolieri non resterà nulla.<new_line>Abbiamo bisogno di un po' di tempo per ricontrollare tutto, dopodiché ce ne andremo da questo posto. Torna tra poco per dirci addio."]],
				['20'] = [["È difficile prevedere l'esito di ciò. Tutto quello che ci dicono i documenti è che si tratta di una città della scienza molto grande. E che al suo centro, sotto la stazione biologica, è nascosto questo micelio reliquia, collegato a un supercomputer.<new_line>Il modo in cui le mutazioni e il computer potrebbero influenzare la sua attività vitale è imprevedibile."]],
				['19'] = [["I petrolieri hanno intenzione di utilizzare il meccanismo di rinverdimento catturato per scopi commerciali.<new_line>Non sono interessati a capire che il micelio reliquia prenderà immediatamente il sopravvento e si verificherà un altro disastro.<new_line>Quindi questo significa che il nostro obiettivo è infiltrarci nella città della scienza e inviare un segnale di autodistruzione al prototipo di micelio che abbiamo usato nel nostro meccanismo."]],
				['18'] = [["Ahimè, amico mio. Non a questo prezzo. Ma hai dato un contributo inestimabile alla difesa di questo mondo instabile. Ora, forse, hai una piccola possibilità di sopravvivere. Buona fortuna a te, caro amico, e addio!"]],
				['17'] = [["Siamo stati costretti ad accantonare tutti gli sviluppi e a cambiare la direzione dello sviluppo della nostra organizzazione. Tutta questa storia del micelio reliquia...<new_line>L'hai visto anche tu: le persone sono ambiziose, avide e spesso non sanno fare una scelta, anche se per una buona causa."]],
				['7'] = [["Dovrai agire in base alle circostanze. Cerca almeno di prendere la scatola nera da lì.<new_line>Tu e il Biologo ci avete dotato di un eccellente laboratorio mobile, e abbiamo la possibilità di inviare un segnale direttamente da lì, facendo affidamento sui codici cifrati di questa scatola nera. Allora, compagno, sei pronto per un'ultima missione?"]],
				['8'] = [["Beh, buona fortuna."<new_line>(porge un pezzo di carta con il codice)]],
				['9'] = [["Meraviglioso! Incredibile! Inimmaginabile!"]],
				['10'] = [["Questo micelio reliquia somiglia in tutto e per tutto a una mente. Non singola, come la nostra, ma una mente condivisa. Probabilmente è per questo che ha comunicato così facilmente con il supercomputer."]]
			},
			['choice'] = {
				['2'] = [["Che ne sarà degli sviluppi? Volevamo salvare il mondo."]],
				['1'] = [["Come possiamo fermarli? Hanno un intero esercito."]],
				['12'] = [["Quindi cosa farai ora?"]],
				['11'] = [["E il prototipo dei petrolieri?"]],
				['19'] = [["Arrivederci, dottor Isaev."]],
				['18'] = [["Quindi non ci sarà alcun rinverdimento delle terre desolate..."]],
				['17'] = [["Che succede al micelio reliquia?"]],
				['5'] = [["E le mutazioni?"]],
				['4'] = [["Cos'è il micelio reliquia?"]],
				['22'] = [["Mi sembra pessimista."]],
				['14'] = [["Cosa troverò ad attendermi alla stazione biologica?"]],
				['16'] = [["E il rinverdimento delle terre desolate?"]],
				['7'] = [["Cosa c'è che non va, dottore?"]],
				['15'] = [["Sono pronto".]],
				['9'] = [["E così, tutte quelle stazioni biologiche..."]],
				['20'] = [["Quindi basta raggiungere la stazione biologica ed entrare nella stanza del supercomputer?"]],
				['21'] = [["E ci ha percepito come una minaccia?"]]
			}
		},
		['wounded_bandit'] = {
			['choice'] = {
				['2'] = [[Riparati dietro il muro]],
				['1'] = [[Sparagli]],
				['7'] = [[Ispeziona il bandito]],
				['6'] = [[Levagli la pistola di mano e colpiscilo con l'ascia]],
				['5'] = [[Levagli la pistola di mano e spara]],
				['4'] = [[Aiutalo]],
				['3'] = [[Offri aiuto]]
			},
			['episode'] = {
				['2'] = [[Quando il bandito ha capito cosa stavo per fare, ha sparato, ma ha sbagliato mira e ha colpito la mia gamba sinistra. Non ha avuto una seconda chance: il mio proiettile ben piazzato l'ha spedito al creatore.]],
				['1'] = [[Mi sono imbattuto in un bandito in un edificio. Era sul pavimento, appoggiato alla parete, e stava tentando di fasciarsi le ferite. Quando mi ha visto, ha alzato la pistola e mi ha minacciato con il filo di voce che gli era rimasto.]],
				['6'] = [[Quando mi sono avvicinato, ho visto che il bandito era totalmente indifeso. Adesso è morto, e posso ispezionare il suo corpo.]],
				['5'] = [[Gli ho dato antidolorifici, ho ripulito le sue ferite e l'ho aiutato a fasciarle. Il bandito era molto sorpreso, e mi ha disegnato la mappa per arrivare a una delle sue scorte segrete, in segno di gratitudine.]],
				['4'] = [[Quando ho detto che volevo solo aiutare, mi ha guardato con fare sospetto, ma quando ho lentamente tirato fuori le mie scorte mediche e gliele ho mostrate, ha abbassato la pistola e si è fatto avvicinare.]],
				['3'] = [[Ero dietro al muro quando un proiettile ha colpito la mia spalla sinistra. Dovrò affrontare il bandito se voglio uscire vivo di qui.]]
			}
		},
