			['episode'] = {
				['2'] = [["Oh, giovanotto, mi serve ancora un'erba. Me ne ero completamente dimenticata."]],
				['1'] = [["Salve, giovanotto. Oggi sto preparando una pozione molto complicata. Per caso hai qualcuno degli ingredienti?"]],
				['3'] = [["Portami un'altra erba e faremo un patto."]]
			},
			['choice'] = {
				['2'] = [["No, ma darò un'occhiata in giro." (Vattene)]],
				['1'] = [["Ecco qua."]],
				['4'] = [["E va bene..." (Vattene)]],
				['3'] = [["Ma i patti prevedevano..."]]
			}
		},
		['newyearEvent_search_easy'] = {
			['episode'] = {
				['2'] = [["Oooooooooh!"<new_line>Naclava ha fatto salti di gioia, mi ha strappato le erbe dalle mani ed è corso via.]],
				['1'] = [[Come suo solito, Babbo Naclava cercava di spiegarmi che gli servono le erbe per una ricetta natalizia. Ne ho sentito parlare da Babbo Naffare, che ha cercato di aiutare Naclava per poi farsi colpire in testa.<new_line>"Gnam gnam... Ah oovaaah."]],
				['7'] = [[La zuppa si è rivelata essere deliziosa. Quando la gente ha notato la mia soddisfazione, hanno iniziato a dare il loro contributo. Babbo Naclava teneva d'occhio il pasto con calma e con il sorriso.]],
				['6'] = [[Mi sono subito sentito male, mentre Naclava iniziava a parlare in modo comprensibile.<new_line>"Oh, sembra che ti sia isolato fin troppo dall'energia verde, quindi i tuoi chakra pancreatici non digeriscono molto bene i frappè ad alto contenuto spirituale. Ti ringrazio per aver fatto un tale sacrificio per la gente."]],
				['5'] = [[Naclava ha infilato la testa nella tinozza e ha bevuto un generoso quantitativo del suo contenuto.<new_line>"Gnam gnam!"<new_line>Fin qui tutto bene, ma il suo stomaco di pietra potrebbe digerire qualsiasi cosa. I comuni umani, tuttavia, non necessariamente sarebbero capaci di farlo. Immagino che dovrò provarci io stesso.]],
				['4'] = [[Naclava ha annuito in segno di approvazione.]],
				['3'] = [[Dopo aver corso per un po', mi sono imbattuto in un'enorme ciotola di pietra interamente riscaldata da un fuoco magico. La gente vi era riunita intorno, mentre Naclava cucinava con grande concentrazione e aggiungeva costantemente ingredienti. Terminata la preparazione, ha annunciato:<new_line>"Gnam gnam!"]]
			},
			['choice'] = {
				['2'] = [["No, non ne ho. Darò un'occhiata in giro." (Scuoti la testa e vattene)]],
				['1'] = [["Ecco." (Offri le erbe)]],
				['6'] = [["Forse prima dovresti assaggiarla? Altrimenti si avveleneranno da soli."]],
				['5'] = [[Assaggia la zuppa]],
				['4'] = [["Vuoi dare da mangiare questa roba alla gente?"]],
				['3'] = [[Raggiungi Naclava]]
			}
		},
		['emba_isaev_surgut'] = {
			['episode'] = {
				['2'] = [["Siamo già in trattative con le principali fazioni. Abbiamo intenzione di condividere la tecnologia con loro, se ci aiuteranno a sviluppare un sistema di sicurezza<new_line>Ma se non riusciamo a risolvere il problema con la sicurezza, dovremo chiudere l'intero programma e sbarazzarci della tecnologia. Immagino tu capisca quanto sia seria la situazione. Non possiamo correre alcun rischio."]],
				['1'] = [["Stiamo lavorando attivamente sulla tecnologia di rinverdimento. E grazie allo studio del lavoro di Gerin, abbiamo fatto passi da gigante!<new_line>Dobbiamo ancora sviluppare un sistema di protezione contro l'attivazione non autorizzata. In effetti, speriamo di ottenere risposte su come risolvere questo problema."]],
				['12'] = [["Va bene. Ecco, prendi e lasciaci andare."]],
				['11'] = [["Come hanno dimostrato gli scienziati britannici, i soldi non puzzano, dottore. Non fare lo spilorcio, non voglio ucciderti."]],
				['10'] = [["Non capisci! Questa tecnologia è instabile e pericolosa!"]],
				['13'] = [[(sussurra) "Eccoti qui, amico mio. Dobbiamo andarcene subito da qui. Ho contattato i miei colleghi dell'A.S.S.A., andremo nella loro città.<new_line>Abbiamo davvero bisogno del tuo aiuto, ti prego di raggiungerci lì il prima possibile."]],
				['9'] = [["Isaev, sei un uomo intelligente: dammi la valigetta e vai per la tua strada."]],
				['8'] = [["Inizieremo subito a preparare la cessazione dello sviluppo, ma ci sarà ancora qualche incarico per te. Torna tra qualche ora."]],
				['7'] = [["Se non controlliamo completamente il micelio, non possiamo usarlo nemmeno parzialmente. Possiamo lavorare con un frammento di questo organismo, ma prima o poi un'altra parte di esso comincerà a mutare.<new_line>E nascerà un Emissario come questo. O un Ibridatore. E non potremo esercitare più alcun controllo, nemmeno sulla nostra parte di micelio."]],
				['6'] = [["Sì. La scatola nera ha registrato il procedimento di formazione dell'oggetto che hai trovato. A quanto pare, l'intero micelio è un unico organismo. Possiamo paragonarlo a una rete telefonica.<new_line>L'assenza di un ospite fisso non lo ostacola in alcun modo. Ma per noi questo è un problema critico!"]],
				['5'] = [["Cosa?! Stava parlando?! Non può essere, è un'assurdità... Stai dicendo che ha parlato di Gerin?<new_line>Buon Dio, con cosa abbiamo a che fare? Interromperemo immediatamente lo sviluppo del prodotto: non possiamo sviluppare ulteriormente questa tecnologia!"]],
				['4'] = [["Non ci resta che scoprire cosa nasconde questa città della scienza. Per favore, recupera la scatola nera dalla stazione biologica."<new_line>(Ti porge un pezzo di carta con il codice)]],
				['3'] = [["Sfortunatamente, i documenti non parlano di alcuno sviluppo. Si tratta di un altro laboratorio di elaborazione dati.<new_line>Ma ecco la cosa più sorprendente: riceviamo segnali radio da questa stazione biologica! Non siamo ancora stati in grado di decifrarli, ma potrebbe esserci ancora qualcuno lì."]]
			},
			['choice'] = {
				['1'] = [["Come applicherai la tecnologia?"]],
				['6'] = [["Va bene. Buona fortuna, dottore."]],
				['5'] = [[Avvicinati a Isaev]],
				['4'] = [["Che cosa intendi?"]],
				['3'] = [["Perché? Hai ottenuto nuovi dati?"]]
			}
		},
		['embacity_reservoir_empty'] = {
			['episode'] = {
				['2'] = [[Davanti a me c'è una stazione di pompaggio, con tubi che conducono a una grande cisterna e si infilano nel sottosuolo. Accanto alla leva della pompa c'è un pannello con dei pulsanti su cui sono disegnati dei numeri. A quanto pare, è necessario un codice di accesso.<new_line>Io non ne ho uno, quindi ho premuto una combinazione casuale di pulsanti e ho tirato la leva. Si è sentito un forte e sgradevole "bip" e la leva è tornata con uno scatto nella posizione precedente.]],
				['1'] = [[Davanti a me c'è una stazione di pompaggio, con tubi che conducono a una grande cisterna e si infilano nel sottosuolo. Ora la cisterna è vuota. Le stesse tubature si vedono nel centro della città, vicino al laboratorio biologico.]]
			}
		},
		['getting_wolf'] = {
			['episode'] = {
				['2'] = [[Appena ho sistemato i topi, mi sono avvicinato al cucciolo e ho capito che in realtà era un incrocio tra un cane e un lupo. Quando mi ha visto, ha provato a indietreggiare, ma è stramazzato al suolo dalla stanchezza. Sembra essere ferito e molto affamato.]],
				['1'] = [[Ho notato una calca di topi intorno a una palla di pelo che guaiva. Si trattava di un cucciolo che i topi probabilmente volevano divorare, ma la povera bestiolina non aveva energie per difendersi. Non posso far finta di niente: devo aiutarlo.]]
			},
			['choice'] = {
				['1'] = [[Prendi il lupacchiotto]]
			}
		},
		['help_hunter5'] = {
			['episode'] = {
				['1'] = [[Il vampiro sta per affondare i suoi canini nel collo del cacciatore, quando lo interrompo con un urlo. Accortosi della nuova minaccia, il mostro abbandona la sua vittima e si lancia verso di me.]]
