				['3'] = [[Apri la porta]]
			}
		},
		['help_hunter2'] = {
			['episode'] = {
				['1'] = [[Trovo il cacciatore imprigionato in una ragnatela, circondato da numerosi ragni morti. Non appena finisco di liberarlo, una moltitudine di creature a otto zampe si lancia verso di noi.]]
			}
		},
		['new_year_boss_3'] = {
			['choice'] = {
				['2'] = [["Sei stato tu a dirmi di "occuparmi" del parco."]],
				['1'] = [["No, è tutto a posto."]],
				['5'] = [[Varca la soglia]],
				['4'] = [["Che cosa facciamo?"]],
				['3'] = [["Ehm..."]]
			},
			['episode'] = {
				['2'] = [[Ho sfondato la porta che conduce al settore interno del parco. Spero che il magazzino dei regali sia qui.]],
				['1'] = [[Il parco non è aperto. Il cancello è chiuso e nessuno ha risposto dopo che ho bussato. Non ho altra scelta, devo irrompere nel parco.]],
				['8'] = [["Ok... Un problema alla volta. Naffare ha stretto un accordo con il gestore, il quale condividerà i suoi regali. Speriamo di riuscire a insabbiare tutto questo vandalismo più tardi.<new_line>Andiamo a negoziare con la banda."]],
				['7'] = [["È il lato oscuro del mio passato. Ora supporto la lotta di classe."]],
				['6'] = [["Orso, significa che anche io ti ho frainteso. Dopotutto, nel tuo mondo hai condotto una rivoluzione dopo l'altra!"]],
				['5'] = [[Orso Natale si è messo gli artigli sulla testa.<new_line>"Intendevo dire che avresti dovuto proteggere il parco nel caso in cui Krampus si fosse fatto vivo! Chi si impadronisce della proprietà con la forza?!"]],
				['4'] = [["Allora che cos'è successo? Oh no... Sei stato tu a distruggere questo posto?!"]],
				['3'] = [[Quando gli ultimi colpi si sono attenuati, mi sono guardato attorno e ho visto i miei compagni in piedi davanti alla porta.<new_line>"Siamo arrivati troppo tardi, compagno? La banda di Krampus ha già razziato il parco?"]]
			}
		},
		['emba_intro'] = {
			['episode'] = {
				['1'] = [[Attenzione, per favore!<new_line>Sono il dottor Victor Isaev, il capo della spedizione di ricerca. Siamo Progresso: un'organizzazione scientifica che ha l'obiettivo di ripristinare il mondo utilizzando progressi scientifici all'avanguardia. <new_line><new_line>Al momento, la nostra spedizione sta cercando gli sviluppi segreti del progetto E.M.B.A. Se dovessimo riuscirci, inizierebbe una nuova era di prosperità: radiazioni, malattie, carestie e il declino generale della civiltà umana sarebbero solo ricordi del passato. <new_line>Abbiamo bisogno di un combattente esperto che lavori in aree a rischio elevato. Avrai il compito di neutralizzare un possibile nemico e ottenere dati sulle ricerche condotte presso le stazioni biologiche E.M.B.A.<new_line><new_line>Progresso ti fornirà maggiori informazioni all'arrivo alle coordinate indicate. Solo insieme potremo condurre il mondo a un futuro radioso. <new_line>Passo e chiudo.]]
			}
		},
		['scientist_rescue_quest'] = {
			['episode'] = {
				['2'] = [["Ok, metto i campioni di terreno nello zaino e poi sono a tua disposizione. Va bene se tengo il fucile a tracolla?"]],
				['1'] = [[Ho bussato alla porta della guardiola. Lo scienziato ha aperto di scatto il chiavistello e mi ha puntato subito un fucile da caccia.<new_line><new_line>Ci è voluto del tempo per convincerlo che venivo dall'insediamento.]],
				['9'] = [[Tempo fa, qualcuno si è assicurato che il dispositivo fosse ben saldo. Inoltre, i ganci sono un po' arrugginiti. Non sarà una passeggiata.]],
				['8'] = [["Diciamo che ci sono. Sto prendendo tutti gli strumenti, ma ho avuto un problema con uno strumento sul tetto. Ho cercato di svitarlo, ma ho solo peggiorato le cose.<new_line><new_line>Se hai tempo, puoi provare a smontarlo? L'importante è portare il resto dell'attrezzatura a casa, perciò mi affido completamente alla tua esperienza.]],
				['7'] = [[Lo scienziato ha preparato lo zaino e attende paziente. Ha lasciato fuori il kit di pronto soccorso.]],
				['6'] = [["Mi spiace, ma la medicina da campo, sai... preferisco evitarla. Ho degli strumenti sterilizzati, è tutto praticamente pronto. Comunque, posso sempre aspettare fino a quando non raggiungeremo il centro medico. È solo un graffietto, sul serio."]],
				['5'] = [["Certo che sono pronto. Ma c'è un problema: prima sono entrati dei banditi. Li ho scacciati, ma diciamo che ho rimediato una piccola ferita. Niente di grave, comunque."]],
				['4'] = [["Non molti, credo. Ti mostro dove si trova il loro accampamento sulla mappa, poi starà a te decidere se andarci o tornare dritto a casa."]],
				['3'] = [["Vedi, il problema è che una banda è già riuscita a rubarmi un dispositivo. Possiamo tornare diretti a casa, ma faresti un gran favore all'insediamento se riuscissi a recuperare anche l'attrezzatura."]]
			},
			['choice'] = {
				['2'] = [["Tutto pronto, muoviamoci."]],
				['1'] = [["Pronto a tornare?"]],
				['6'] = [[Svita con gli attrezzi]],
				['5'] = [[Vai all'insediamento]],
				['4'] = [["Medichiamola lungo il cammino."]],
				['3'] = [["Là ci sono un sacco di banditi?"]]
			}
		},
		['lair3'] = {
