		['emba_energy_easy'] = {
			['episode'] = {
				['2'] = [["Ho fatto una stupidaggine! Il mio vicino mi aveva raccontato storie su questo micelio che fertilizza il terreno per ottenere raccolti migliori. L'ho piantato nelle serre ed è cresciuto così bene che ora non riesco nemmeno a tagliarlo. Per favore aiutami!"]],
				['1'] = [["Ehi aspetta! Ho bisogno di aiuto. Queste maledette radici hanno ricoperto l'intera fattoria. I mutanti verdi hanno già iniziato a venire qui, come se fossimo la loro mensa personale."]],
				['7'] = [["Oh, al diavolo! Non ha senso, ricrescerà di nuovo e ricomincerà tutto da capo. Comunque, grazie per averci provato, ovviamente, ma devi riconoscere i tuoi limiti..."<new_line><new_line>Il contadino si dirige verso la sua capanna sconsolato, prendendo a calci una radice che sta crescendo nel terreno proprio davanti ai tuoi occhi.]],
				['6'] = [["Uff, abbiamo finito! Questa spazzatura brucerà tutta e io potrò occuparmi di nuovo della fattoria.<new_line>Grazie mille, sei stato fantastico. Ti compenserò per il lavoro come posso."]],
				['5'] = [[È come se il micelio stesse cercando di avviluppare il fienile in una sfera di rami e radici, ma ce l'abbiamo fatta.<new_line>Non resta che raccogliere tutta questa vegetazione mutante in un mucchio e bruciarla. Il contadino è andato a prendere la benzina e mi ha detto di raccogliere le radici.]],
				['4'] = [[Abbiamo fatto la prima parte e ripulito i campi, ma non è tutto, bisogna passare alla stalla.]],
				['3'] = [["Amico, mi hai salvato!<new_line>Guarda. Devi tagliare tutti questi rami in una volta sola, ammucchiarli e dargli fuoco. Non c'è tempo per riposare, altrimenti questa robaccia attecchirà di nuovo e spargerà le sue spore dappertutto. Cominciamo dai campi."]]
			},
			['choice'] = {
				['2'] = [[Taglia le radici]],
				['1'] = [["Come è successo?"]],
				['3'] = [[Accumula le radici]]
			}
		},
		['scientist_rescue_bar'] = {
			['episode'] = {
				['2'] = [["Vivi e vegeti? Ottimo! Sappi che hai salvato la vita di un brav'uomo. Grazie anche per il dosimetro, è un modello raro. Un lavoro coi fiocchi, meriti un piccolo extra."]],
				['1'] = [["Vivi e vegeti? Ottimo! Sappi che hai salvato la vita di un brav'uomo. Ecco la tua ricompensa."]],
				['3'] = [["Vivi e vegeti? Ottimo! Sappi che hai salvato la vita di un brav'uomo. Ho sentito parlare dei tuoi servizi medici, perciò eccoti un piccolo extra. Apprezziamo chi si prende cura della nostra gente."]]
			}
		},
		['forest_camp'] = {
			['choice'] = {
				['2'] = [[Indossa vestiti mimetici ed entra nelle tende]],
				['1'] = [[Avvicinati]],
				['9'] = [[Prendi tutto e scappa]],
				['8'] = [[Taglia la gola al bandito]],
				['7'] = [[Entra nella tenda a destra]],
				['6'] = [[Prendi quel che puoi e scappa]],
				['5'] = [[Entra nella tenda centrale]],
				['4'] = [[Perlustra l'accampamento]],
				['3'] = [[Entra nella tenda a sinistra]]
			},
			['episode'] = {
				['2'] = [[Quando la sentinella mi ha visto, ha immediatamente alzato il fucile e mi ha sparato! Il proiettile mi ha colpito nel petto, ma sono ancora vivo. Dovrò combattere.]],
				['1'] = [[Stavo camminando nella foresta quando ho sentito odore di fumo.<new_line>Poco dopo ho scoperto da dove proveniva: un piccolo accampamento di banditi con tende e un fuoco. Uno dei banditi è a guardia del perimetro: avvicinarsi sarà pericoloso.]],
				['8'] = [[Un movimento rapido e il bandito ha perso la vita. Non ha avuto tempo di capire cosa stesse succedendo. Ora posso prendere tutte le sue cose.]],
				['7'] = [[Nella tenda ho trovato un bandito che dormiva e russava beatamente. A giudicare dalla bottiglia vuota lì accanto, non credo si sveglierà tra molto poco.]],
				['6'] = [[Qui dentro c'è il caos. Spero di poter trovare qualcosa di utile.]],
				['5'] = [[Ora che tutti i banditi sono morti, posso ispezionare l'accampamento in pace.]],
				['4'] = [[Una volta entrato nella tenda, ho trovato un bandito a dir poco sorpreso che puntava una pistola verso di me. Non ho scelta: dovrò affrontarlo.]],
				['3'] = [[Sono riuscito a nascondermi nella vegetazione e a raggiungere le tende. Ne ho tagliata una con il coltello e sono entrato.]]
			}
		},
		['embacity_restaurant2'] = {
			['episode'] = {
				['2'] = [[Un gruppo di mutanti si è radunato davanti all'edificio invaso dalla vegetazione. I biomutanti si stanno scontrando tra loro per la carcassa ricoperta di muffa. Avrò bisogno di una tuta protettiva per affrontarli.]],
				['1'] = [[Dalle rovine ricoperte di muffa proviene il rumore dei numerosi abitanti dell'edificio. Devo ricordarmi di indossare una tuta protettiva per eliminare i biomutanti.]],
				['3'] = [[Tra le escrescenze ammuffite vedo dei bellissimi fiori rossi. Ma non credo che questo gruppo di mutanti sia venuto qui per ammirarli, quindi devo pensare a una protezione speciale.]]
			}
		},
		['cover_the_fighter'] = {
			['episode'] = {
				['2'] = [[L'esploratore è seduto a fumare all'ingresso di un hangar arrugginito. Non appena mi vede, salta in piedi:<new_line>"Rinforzi. Che bello vederti, amico! Ho già scassinato la serratura. Che ne dici, cominciamo?"]],
				['1'] = [[L'esploratore è seduto a fumare all'ingresso di un lungo garage. Non appena mi vede, salta in piedi:<new_line>"Rinforzi. Che bello vederti, amico! Ho già scassinato la serratura. Che ne dici, cominciamo?"]],
				['12'] = [[L'esploratore è morto. Dovevo proteggerlo, ma ho fallito la missione.]],
				['11'] = [["Un bottino povero, ma almeno è tutto nostro. Laggiù c'è un'altra porta. Magari troveremo qualcosa di valore. A giudicare dai rumori, dovremo combattere. Rischiamo o torniamo a casa?"]],
				['10'] = [[All'interno non c'era nessuno. L'esploratore ha dato un'occhiata alla zona ed è tornato da me.]],
				['9'] = [[L'esploratore è occupato a esaminare tutti gli armadietti e ogni angolo buio della struttura. La cassaforte non è stata toccata.]],
				['8'] = [[Con un bel po' di fatica, ho aperto la cassaforte. Dentro abbiamo trovato un paio di dispositivi elettronici in buone condizioni e scatole di munizioni. L'esploratore ha fischiato per la sorpresa e mi ha dato una pacca sulla spalla.]],
				['7'] = [["Wow, guarda quella cassaforte! Dev'esserci per forza qualcosa di prezioso. Che peccato, non c'è tempo per trattenersi. Abbiamo fatto il nostro dovere, ma se riesci ad aprire anche quella, i nostri ragazzi ti pagheranno un extra."]],
				['6'] = [[Il mio compagno abbassa l'arma e, con il respiro pesante, esamina la struttura.<new_line><new_line>"Fiù, meno male che ti ho aspettato, altrimenti non ne sarei uscito vivo. Devo perlustrare il posto prima che quei mostri ritornino alla carica. Grazie dell'aiuto e buona fortuna!"]],
				['5'] = [[Il mio compagno abbassa l'arma e, con il respiro pesante, esamina la struttura.<new_line><new_line>"Fiù, meno male che ti ho aspettato, altrimenti non ne sarei uscito vivo. Devo perlustrare il posto prima che quei mostri ritornino alla carica."]],
				['4'] = [[Sentiamo frusciare e stridere nell'oscurità. Prendiamo le torce giusto in tempo: siamo nella tana di creature orribili che stanno per aggredirci.]],
				['3'] = [[L'esploratore è seduto a fumare all'ingresso di un rifugio antiaereo. Non appena mi vede, salta in piedi:<new_line>"Rinforzi. Che bello vederti, amico! Ho già scassinato la serratura. Che ne dici, cominciamo?"]]
			},
			['choice'] = {
				['5'] = [[Varca la soglia]],
				['1'] = [[Entra nell'edificio]],
				['4'] = [[Fai saltare la serratura]],
				['3'] = [[Scassina la serratura]]
			}
		},
		['flying_dutchman'] = {
			['episode'] = {
				['2'] = [[Vedendo il capitano stramazzare al suolo, Igor si tuffa in mare. Scrutando da dietro il corrimano, scorgo la figura del cavaliere senza testa fluttuare sul pelo dell'acqua. In una mano tiene il libro che sto cercando, nell'altra regge la sua stessa testa. Igor, invece, sembra essere sprofondato negli abissi. Senza attendere oltre, il cavaliere lancia il suo destriero al galoppo e scompare oltre l'orizzonte. Non posso far altro che finire di perlustrare il vascello.]],
				['1'] = [[La campanella risuona chiara, mentre dall'acqua si erge una vecchia imbarcazione costellata di conchiglie. Ignorando i miei dubbi, mi isso al suo interno e lascio che mi conduca a un vascello immerso nella nebbia. Gli orripilanti marinai mi ignorano mentre mi arrampico sul ponte scivoloso. Improvvisamente, Igor esce dagli alloggi del capitano. Preso alla sprovvista per un istante, mi indica e inizia a urlare: ]]
			}
		},
		['gift_delivery'] = {
			['choice'] = {
				['1'] = [[Posiziona il regalo]]
			},
			['episode'] = {
				['2'] = [[Al mio arrivo, non noto niente di insolito. Sto riflettendo su dove lasciare il regalo, quando, alle mie spalle, sento il rumore di un fucile che viene caricato. Una voce sconosciuta mi intima: «Lascia il pacco a terra e vattene senza voltarti.»]],
				['1'] = [[È un accampamento piccolo con un annesso di rami di abete coperti di neve. Il fuoco è acceso e sul pentolone bolle dell'acqua. L'uomo sembra essersi appena allontanato. Rimarrà sorpreso quando, al suo ritorno, troverà un regalo tutto per lui.]],
				['3'] = [[Con mia sorpresa, trovo un piccolo albero di capodanno con degli addobbi improvvisati. Qualcuno è stato qui e ha dormito avvolto da svariate coperte. Sarà meglio lasciare il regalo sotto l'albero e andarmene alla svelta.]]
			}
		},
		['secret_documents'] = {
			['episode'] = {
				['2'] = [["Quindi cosa facciamo? Siamo venuti qui per primi. Se ci paghi lo stesso, ti lasceremo queste rovine. In caso contrario, possiamo spiegarti la situazione in modo molto più sgradevole."]],
				['1'] = [[Ho scoperto un gruppo di persone all'ingresso dell'edificio. Il loro capo mi ha ascoltato e ha scosso la testa.<new_line>"Devi capire, non siamo venuti qui per fare una passeggiata, ma per raccogliere informazioni rare ordinate da persone importanti."]],
				['11'] = [[Ho attirato gli insetti in superficie, ma non volevano abbandonare la tana e gradualmente hanno iniziato a strisciare di nuovo dentro.]],
				['10'] = [[Il laboratorio occupava una piccolissima parte dell'edificio. Probabilmente era stato utilizzato come deposito di documenti d'archivio.<new_line>Mentre esploravo un altro angoletto, mi sono imbattuto in una tana di insetti.]],
				['9'] = [[Sono arrivato alle coordinate dell'edificio fornitemi dagli Archivisti. La porta è sigillata. Sarà necessario usare gli esplosivi.]],
				['8'] = [[Ho i documenti segreti e devo consegnarli alla base del Progresso e aiutare il Biologo con le sue ricerche.]],
				['7'] = [[Il percorso è libero. Ho continuato la ricerca del laboratorio e finalmente ho trovato una cassaforte con i marchi EMBA.]],
				['6'] = [[Queste persone non erano dell'umore giusto per parlare. Sono corso fuori per ricordare al loro capo il nostro accordo, ma non c'era più nessuno.<new_line>Quelli del laboratorio mi stanno già inseguendo.]],
				['5'] = [[Queste persone non erano dell'umore giusto per parlare. Una sparatoria in un laboratorio non è una buona idea, quindi sono corso fuori.<new_line>I saccheggiatori mi hanno seguito.]],
				['4'] = [[Il laboratorio occupava una piccolissima parte dell'edificio. Probabilmente era stato utilizzato come deposito per i documenti d'archivio.<new_line>Mentre esploravo un altro dei suoi angoli, ho sentito delle voci umane.]],
				['3'] = [[Il percorso è libero, quindi posso perlustrare il laboratorio ora.]]
