				['1'] = [[Apri il tombino con un piede di porco]],
				['9'] = [[Trova un'uscita]],
				['8'] = [[Prendi cose e scappa]],
				['7'] = [[Cerca il tesoro]],
				['6'] = [[Fai il giro intorno]],
				['5'] = [[Prova a disarmarla]],
