				['1'] = [["Salute, cacciatore! Per il momento riusciamo a tenere testa agli zombi, ma ne arrivano sempre di più."]],
				['3'] = [["Beh, quella canaglia deve aver messo qualcosa nei bicchieri delle guardie e si sono addormentate. È riuscito a entrare nel cimitero e ora non riusciamo a catturarlo. Si nasconde dietro una nebbia velenosa e un'orda di morti. Avanti, dagli una bella lezione!"]]
			}
		},
		['embacity_biolab_reservoir_2'] = {
			['episode'] = {
				['2'] = [[Un'enorme cisterna occupa gran parte della stazione biologica. Al momento è vuota.<new_line>Ad essa è collegata una stazione di pompaggio: macchine simili sono presenti nelle cisterne più piccole alla periferia della città.]],
				['1'] = [[Un'enorme cisterna occupa gran parte della stazione biologica, piena fino all'orlo di un liquido verde.<new_line>Ad essa è collegata una stazione di pompaggio: macchine simili si trovano nelle cisterne più piccole alla periferia della città.]],
				['3'] = [[Un'enorme cisterna occupa gran parte della stazione biologica: è piena per metà di un liquido verde.<new_line>Ad essa è collegata una stazione di pompaggio: macchine simili sono presenti nelle cisterne più piccole alla periferia della città.]]
			}
		},
		['catch_thief_quest'] = {
			['episode'] = {
				['10'] = [[Il capo del magazzino mi ha ringraziato e ha aggiunto:<new_line><new_line>'Sospetto non lavorasse da solo, ma ora non possiamo più scoprirlo.']],
				['1'] = [[Stando al capo del magazzino, qualcuno ha preso l'abitudine di prendere dal magazzino qualsiasi cosa non sia inchiodata al terreno. Dobbiamo prendere il ladro.]],
				['12'] = [[Ho sentito il tonfo di qualcuno che posizionava la scala dall'altro lato della recinzione. Quando il ladro è arrivato dall'altra parte, ha trovato me ad aspettarlo.]],
				['20'] = [[Il lupo mi ha condotto a un accampamento di banditi.]],
				['5'] = [[La scala sembra normale ma l'ho resa instabile. Ho appena sentito un fruscio nei paraggi. Sta arrivando qualcuno, devo nascondermi!]],
				['40'] = [[Il capo del magazzino sa essere davvero convincente. Il ladro ha ceduto e ha cantato i nomi dei complici. Il capo mi ha detto:<new_line>"Beh, ottime notizie. Devo chiedere aiuto alla base. O forse vuoi occuparti tu della banda? I tuoi sforzi saranno ripagati."]],
				['41'] = [["Beh, grazie. Ora è tutto a posto. Quei ladri mi stavano prosciugando! Tieni, te lo devo."]],
				['38'] = [[Il criminale non ha ammesso nulla. Forse è solo un ladro di passaggio e il grosso colpo al magazzino lo ha organizzato qualcun altro.]],
				['13'] = [[Mentre legavo il ladro, ho notato che si era ferito contro le sbarre di ferro e ora perdeva sangue.]],
				['19'] = [[Il mattino dopo, ho raccontato tutto al capo del magazzino, che ha scosso la testa, amareggiato.<new_line>'Scusami, ma non hai catturato il ladro, quindi niente ricompensa.']],
				['18'] = [[Non sono riuscito a catturare il ladro, che è scomparso nella notte.]],
				['17'] = [[Dopo aver finito il lavoro, il ladro è tornato e ha iniziato a salire sulla scala. Per fortuna, i pioli che avevo indebolito si sono rotti sotto al suo peso, facendolo cadere a terra.]],
				['6'] = [[Il filo è pronto e ben nascosto.]],
				['28'] = [[I banditi hanno provato ad attaccarmi, ma quando gli ho mostrato la chiave del magazzino, il loro umore è cambiato. Abbiamo ripulito velocemente il magazzino.]],
				['4'] = [[Ho visto delle impronte dirette verso il bosco vicino alla staccionata e ci sono anche tracce di trascinamento.<new_line>Ho trovato una scala artigianale nel cespuglio.]],
				['39'] = [[Il capo del magazzino sa essere davvero convincente. Il ladro ha ceduto e ci ha detto dove ha nascosto la refurtiva. Il capo mi ha chiesto:<new_line>"Puoi andare tu a recuperare la refurtiva della canaglia? I tuoi sforzi saranno ripagati."]],
				['35'] = [[Dopo una specie di corsa a ostacoli, il ladro non ha più scampo. Però ho appena sentito un fruscio nei paraggi. Arriva qualcuno, devo nascondermi!]],
				['34'] = [[Ho visto il ladro sistemare una scala al di là della recinzione e iniziare a scenderla. Sui pioli finali, è caduto con un tonfo. Come minimo si è slogato la caviglia, ma almeno non andrà da nessuna parte!]],
				['32'] = [[È stata una mossa astuta. Ottimo lavoro! All'inizio non avevo capito. Ti meriti una ricompensa straordinaria.']],
				['31'] = [[Dopo un po', il capo è entrato nel magazzino, agitato.<new_line><new_line>'Cosa sta succedendo qui? Perché i banditi sono entrati nel magazzino?']],
				['30'] = [[Ho sconfitto i banditi, ma ora devo capire cosa fare adesso.]],
				['29'] = [[Il capo della banda mi ha dato la mia parte di merce rubata.]],
				['25'] = [[Beh, sono riuscito a raggiungere il ladro. Però lui mi ha portato dai suoi alleati, che non erano molto felici di vedermi.]],
				['24'] = [[Quel mascalzone conosceva meglio i boschi di me. Dopo qualche minuto è scomparso dalla mia vista.]],
				['23'] = [[Il ladro mi ha visto. Ha lasciato cadere il suo bottino ed è fuggito nel bosco.]],
				['22'] = [[Il capo del magazzino è stato soddisfatto del risultato.<new_line><new_line>'Sapevo di potermi fidare di te. Ecco la tua ricompensa, te la sei meritata.']],
				['15'] = [[Il capo del magazzino ha lanciato un'occhiata al ladro.<new_line><new_line>'È vivo? Bene, così possiamo interrogarlo.']],
				['16'] = [[Ho sentito un rumore provenire dal cespuglio e ho visto il profilo scuro di un uomo con qualcosa tra le mani, ma non sono riuscito a capire cosa.]],
				['7'] = [[È notte. Dovrei capire dove appostarmi.]],
				['8'] = [[Ho sentito uno schianto provenire dal bosco: la mia trappola ha funzionato.<new_line>Dovrei dirlo al capo del magazzino.]],
				['14'] = [[Il ladro ha lanciato la scala dal nostro lato della recinzione. Mentre scendeva, sono uscito dal mio nascondiglio. L'uomo ha sobbalzato dallo stupore e la scala si è rotta sotto di lui, facendolo cadere a terra.]],
				['21'] = [[Dopo averli sconfitti, mi sono guardato attorno: non avevo abbastanza forze per riportare indietro tutto quello che avevano rubato, ma ho preso quel che ho potuto.]]
			},
			['choice'] = {
				['2'] = [[Guardati intorno]],
				['1'] = [[Aspetta la notte e l'arrivo del ladro]],
				['11'] = [[Nasconditi tra la staccionata e il muro del magazzino]],
				['40'] = [[Spiega il tuo piano]],
				['42'] = [[Non c'è speranza. Non ho possibilità contro a così tanti nemici. Meglio andare via.]],
				['24'] = [[Arrenditi!']],
				['19'] = [[Cura la ferita]],
				['18'] = [[Aspetta fino al mattino]],
				['17'] = [[Fermo!']],
				['27'] = [[Tendi un'imboscata: 'Non muoverti!']],
				['28'] = [[Lascia andare il ladro e manda il lupo all'inseguimento]],
				['4'] = [[Torna dal capo del magazzino]],
				['48'] = [[Vai a prendere la refurtiva]],
				['49'] = [[Vai dai banditi]],
				['47'] = [[Torna al riparo]],
				['46'] = [[Scava una fossa]],
				['44'] = [[Aspetta il capo del magazzino]],
				['43'] = [[Prendi qualcosa di utile e scappa]],
				['36'] = [[Dov'è la mia parte?']],
				['34'] = [[Ehi, ragazzi! Che ne dite di collaborare?']],
				['32'] = [[Insegui]],
				['31'] = [[Prendi la ricompensa]],
				['29'] = [[Attacca]],
				['9'] = [[Nasconditi vicino alla staccionata]],
				['6'] = [[Metti un innesco a filo vicino alla scala]],
				['7'] = [[Distruggi i pioli più bassi della scala]],
				['14'] = [[Torna al magazzino e aspetta il mattino]],
				['21'] = [[Tendi un'imboscata]],
				['22'] = [[Aspetta che il ladro torni]]
			}
		},
		['emba_fight_hard'] = {
			['episode'] = {
				['2'] = [["È facile anche per chi non è un professionista. Il Progresso ha bisogno di biomutanti viventi e io ho teso una trappola. Devi guidarceli dentro, dovresti saperlo fare bene.<new_line>Quando saremo entrambi in posizione, finiremo il resto del branco."]],
				['1'] = [["Ehi, aspetta un attimo. Te la cavi bene con le armi. Ho un lavoro per te."]],
				['11'] = [["Non è un brutto lavoro. È diventato più difficile di quanto pensassi, ma è bello cacciare con te. Andrò a vedere chi c'è nella trappola laggiù. Ci vediamo in giro."]],
				['10'] = [[Parte del branco in fuga era scomparsa chissà dove: sembrava che la trappola avesse funzionato. Molti altri biomutanti si erano precipitati in avanti e da quella direzione sono risuonati degli spari, mentre la Cacciatrice si univa al combattimento.<new_line>Ora posso continuare a inseguire il branco e attaccarli da dietro. Potrei anche fiancheggiarli e vedere se lì c'è una posizione migliore.]],
				['9'] = [[Questa era solo una parte del branco. Ho guidato quelli che erano rimasti e non si erano uniti alla lotta verso il luogo indicato dalla Cacciatrice.]],
				['8'] = [[Ho aspettato abbastanza, posso iniziare ad attaccare il branco di biomutanti.]],
				['7'] = [["Lo spero davvero. Girerò intorno al luogo dell'imboscata dalla parte opposta e tu dovresti spostarti verso il gruppo di biomutanti tra circa cinque o dieci minuti."]],
				['6'] = [["Sei anche intelligente, che novità! Va bene, professore, girerò intorno al luogo dell'imboscata dal lato opposto, e tu dovresti spostarti verso il gruppo di biomutanti tra circa cinque o dieci minuti."]],
				['5'] = [["È più facile con quello. È un po' come un serbatoio organico con una riserva di biomassa. Aiuta i mutanti ad assorbire le spore.<new_line>A proposito, se ti trovi vicino quando esplode, non aver paura. Il tocco della sua melma non dà una bella sensazione.<new_line>Ma se ne sei coperto, assorbirà comunque l'effetto delle spore. È l'ideale per te, poiché li risucchia, o qualcosa del genere."]],
				['4'] = [["Sono organismi davvero interessanti. In realtà non è del tutto chiaro se siano vivi o meno. Un Nido è una sorta di colonia di micelio. Di tanto in tanto ne escono degli erbosi.<new_line>In questo momento, sto cercando di capire se aspettano lì fermi o se si rigenerano. Potrebbero anche essere effettivamente nati nella colonia."]],
				['3'] = [["Se non studiassi tutti i dettagli, sarei steso morto e senza il secondo occhio.<new_line>A proposito di stare attenti. Un branco potrebbe aggirarsi intorno a oggetti come questi... Senza usare nomi difficili, hai mai sentito parlare del Nido e della Bolla?<new_line>Ti consiglio di affrontare prima loro in battaglia."]]
			},
			['choice'] = {
				['2'] = [["Vedo che l'hai studiato bene."]],
				['1'] = [["Mi spieghi i dettagli?"]],
				['9'] = [[Fiancheggia]],
				['8'] = [[Attacca da dietro]],
				['7'] = [[Procedi]],
				['6'] = [[Aspetta e attacca]],
				['5'] = [["E per quanto riguarda la Bolla?"]],
				['4'] = [["Cos'è?"]],
				['3'] = [["Capisco, sono pronto."]]
			}
		},
		['emba_energy_easy2'] = {
			['episode'] = {
				['2'] = [[Dentro è vuoto ed è tutto allagato. Più vado avanti, più diventa profondo. Prima o poi dovrò immergermi fino al collo per andare avanti.<new_line>Ma là c'è la stanza che ha qualcosa sugli scaffali ancora asciutti!]],
				['1'] = [[La porta del bunker è rotta. Probabilmente qualcuno è già stato qui.]],
				['6'] = [[Con grande difficoltà, sono uscito dall'acqua e ho ripreso fiato. Ne ho abbastanza di tutti questi misteriosi bunker e ricerche.<new_line>Soprattutto perché ho lasciato cadere la cassa ed è finita sott'acqua.]],
				['5'] = [[Finalmente sono fuori! Scommetto che i miei amici scienziati vorranno darci un'occhiata. Gliela mostrerò quando li vedrò.]],
				['4'] = [[La cassa è pesante, è difficile trasportarla con l'acqua fino al collo e io mi trovo nel mezzo della stanza.]],
				['3'] = [[È una cassa di plastica semitrasparente. Dentro si può distinguere qualcosa che assomiglia a quelle strane piante.<new_line>Qualcuno le stava davvero studiando qui?]]
			},
			['choice'] = {
				['2'] = [[Torna indietro con la cassa]],
				['1'] = [[Entra in acqua e cammina in avanti]],
				['4'] = [[Continua a camminare con la cassa]],
				['3'] = [[Lascia cadere la cassa e vattene]]
			}
		},
		['embacity_biolab_7'] = {
			['episode'] = {
				['2'] = [[Il ronzio dei computer e dell'armadio di controllo al centro è cessato. Il grumo di micelio non mostra segni di vita. Chissà cos'era... Ma lasciamo che se ne occupino gli scienziati. Ho fatto tutto il possibile, ora devo solo portare a loro la scatola nera.]],
				['1'] = [[Il piano sotterraneo di questa stazione biologica un tempo fungeva da sala di controllo. Ma ora il pavimento è bucherellato, le radici si estendono oltre le antenne dei computer e sui tavoli sono ammassati mucchi di biomassa.<new_line>Al centro della stanza, sull'armadio di controllo, ho notato un movimento. I computer hanno immediatamente iniziato a ronzare e dei biomutanti sono emersi dall'ombra della parete.]]
			}
		},
		['emba_search_medium'] = {
			['episode'] = {
				['2'] = [["Eccellente. Vieni con me, è qui vicino.<new_line>Guarda, qui è dove metterò il cibo e cercherò di attirarli più lontano, mentre tu andrai in quella grotta, i sacchi di micelio dovrebbero essere lì."]],
				['1'] = [["Ciao! È successo qualcosa di spiacevole, puoi dare una mano?<new_line>I sacchi di micelio rubati dai biomutanti devono essere restituiti. Ho trovato la tana dove hanno prelevato i campioni sperimentali, ma ci sono troppi mutanti lì.<new_line>Suggerisco di attirarli lontano dalla tana. Hai bisogno di qualche esca fresca... ne hai?"]],
				['8'] = [[Il problema è risolto, ora nessuno interferirà quando mi prendo il micelio.]],
				['7'] = [[Diversi biomutanti sono ancora nella grotta! Potrei attaccarli e provare a ripetere il trucco di distrarli con il cibo e scappare velocemente con i sacchi.]],
				['6'] = [["Evviva, ce l'abbiamo fatta! Grazie, mi hai tolto un peso enorme dalle spalle. Bene, devo sbrigarmi a fare delle ricerche prima che accada qualcos'altro. Abbi cura di te!"]],
				['5'] = [[Non c'era nessuno nella caverna. La situazione sta andando alla grande, dovrei prendere i sacchi e andarmene.]],
				['4'] = [[Ci sono molte tracce all'imboccatura della caverna, ma non riesco a vedere i biomutanti, devo sbrigarmi a prendere i sacchi.]],
				['3'] = [["Ti avevo procurato un paio di sacchi di micelio dai miei capi, ma sono stato distratto da un altro piccolo esperimento e le borse sono state rubate dai biomutanti. Sarò nei guai se non risolvo la situazione ora. Allora, andrai alla grotta?"]]
			},
			['choice'] = {
				['2'] = [["Come è potuto succedere?"]],
				['1'] = [[Vai alla grotta]],
				['6'] = [[Usa il pesce per distrarre e prendi il micelio]],
				['5'] = [[Usa la carne per distrarre e prendi il micelio]],
				['4'] = [[Raccogli i sacchi e vattene]],
				['3'] = [[Entra nella grotta]]
			}
		},
		['impostor1'] = {
			['episode'] = {
				['1'] = [[Le persone ingannate dal impostore me incontrati e mi hanno detto in quale direzione ha lasciato con il bottino. Mi hanno chiesto di restituire i loro effetti personali e ha avvertito che non lavora da solo. Devo fretta prima che i banditi prima che i banditi fuggiranno.]]
			}
		},
		['embacity_radio2'] = {
			['episode'] = {
				['2'] = [[Il negozio è ricoperto da corde di muffa e grandi boccioli scarlatti spuntano dai germogli verdi delle radici. Mentre li guardavo, quasi non ho fatto caso ai biomutanti. Devo controllare se la mia tuta protettiva è a posto.]],
				['1'] = [[Il negozio è completamente invaso dalla vegetazione, ma non è in cattive condizioni. Non ci sono molte tracce di mutanti in giro, ma sicuramente c'è qualcosa all'interno. Devo controllare che le mie difese siano a posto prima di entrare.]],
				['4'] = [[Nelle restanti vetrine dei negozi, di tanto in tanto si scorgono le sagome di biomutanti vaganti. Non è una buona idea avvicinarsi ad essi senza una tuta protettiva.]],
				['3'] = [[La muffa ha praticamente distrutto l'edificio, trasformando il cemento e il ferro in un terreno fertile per i biomutanti. Avrò bisogno di una tuta protettiva per affrontarli.]]
			}
		},
		['halloween_def_easy2'] = {
			['episode'] = {
				['10'] = [["Oh, ti ringrazio molto, ragazzo mio!"]],
				['1'] = [[Attorno alla capanna si era riunita una folla, come fossero dei fan a un concerto rock.<new_line>La Vecchia Strega si affacciò dalla finestra:<new_line>"Entra qui dentro, giovanotto! Io li distraggo!"]],
				['11'] = [["La mia schiena è bloccata. Non sono più giovane ormai. Nessun rancore, giusto, giovanotto? Sei stato fantastico! Prendi la ricompensa e va' per la tua strada."]],
				['2'] = [[Mentre la strega distraeva i mostri, corsi verso la capanna. Appena entrai all'interno, la casa si alzò in piedi.<new_line><new_line>"Decisamente meglio! Potresti aiutare un'anziana signora? Li scacceremo più in fretta insieme."]],
				['9'] = [["Tre!"]],
				['8'] = [["Due..."]],
				['7'] = [["Uno..."]],
				['6'] = [["Sembra che ci siamo. Al mio tre, saltiamo. Pronto?"]],
				['5'] = [["Che giovanotto sveglio! Sembra che ci siamo. Al mio tre, saltiamo. Pronto?"]],
				['4'] = [["La scelta è tua, giovanotto. Che ne dici di una pozione prima di combattere? A proposito, non ho ancora avuto modo di provarla."]],
				['3'] = [["Ho creato una pozione speciale che penso gli sia piaciuta fin troppo. Sai, sono felice di avere degli ospiti, ma solo quando sono invitati. Ho una trappola interessante ricavata dai funghi da usare contro di loro. Vuoi che la prepari o pensi che possiamo riuscire a gestirli senza?"]]
			},
			['choice'] = {
				['2'] = [["Come sono arrivate qui queste bestie?"]],
				['1'] = [[Corri verso la capanna]],
				['9'] = [["Perché non hai saltato?"]],
				['8'] = [[Salta]],
				['7'] = [[....]],
				['6'] = [["No, grazie."]],
				['5'] = [[Bevi la pozione]],
				['4'] = [["Possiamo tenerli a bada."]],
				['3'] = [["Posiziona la trappola."]]
			}
		},
		['halloween_def_hard'] = {
			['episode'] = {
				['10'] = [[Lo stregone mi diede un colpetto col suo bastone e disse:<new_line>"Abracadabra. Fatto."<new_line><new_line>Le bestie si avvicinano ancora.]],
				['1'] = [["Sei qui per dare una mano o solo per guardare?"]],
				['12'] = [["Abracadabra... Quasi. Può bastare. Le bestie si avvicinano. Preparati."]],
				['11'] = [["Sei stato utile solo al 5%. Perché dovrei sprecare la mia magia con te? Potresti almeno impegnarti un po' di più? Se vuoi essere utile, pensa bene a quale incantesimo potrei usare su di te."]],
				['13'] = [["Abracadabra... Quasi. Può bastare. Le bestie si avvicinano. Preparati."]],
				['14'] = [[Lo stregone sbadigliò rumorosamente.<new_line>"Fiu, me la sono cavata bene. Gli altri dovrebbero prendere spunto... Mio malgrado, secondo quanto riportato, il Codice degli stregoni stabilisce che chiunque mi assista debba ricevere un appunto. Leggendolo, si ottiene la capacità di evocarmi nell'ora del bisogno e bla, bla, bla...<new_line>Ma ricorda che se non si tratta della vera ora del bisogno, te ne pentirai. Fine della storia."]],
				['9'] = [["Hai fatto del tuo meglio per aiutarmi, ma non è servito a molto. Forse potresti farti venire in mente un piano leggermente più intelligente? Scegli l'incantesimo che vuoi stavolta."]],
				['7'] = [[Lo stregone agitò pigramente il suo bastone.<new_line>Abracarabraboom. Ecco fatto. Spero di non aver sprecato tutto questo potere per niente."<new_line><new_line>L'ululato delle bestie sembra molto vicino.]],
				['2'] = [["Non startene lì impalato! Pensi che possa sconfiggere le orde di mostri e difendere il villaggio da solo? Potresti alzare anche solo un dito per aiutarmi. Così, per dire."]],
				['5'] = [[Lo stregone agitò pigramente il suo bastone.<new_line>Abracarabraboom. Ecco fatto. Spero di non aver sprecato tutto questo potere per niente."<new_line><new_line>L'ululato delle bestie sembra molto vicino.]],
				['4'] = [["Ho combattuto per giorni e giorni senza riposare, e adesso pretendi che mi inventi un piano?! Mio Dio, quanto sei inutile. Bene, credo che i mostri si avvicinino ancora. Che ne dici se facessi un incantesimo su di te per renderti un po' più utile? Dimmi quale incantesimo vorresti."]],
				['3'] = [["Muoviti, non startene lì impalato."]]
			},
			['choice'] = {
				['2'] = [["Sono solo di passaggio."]],
				['1'] = [["Sono qui per dare una mano."]],
				['9'] = [["Grazie." (Vattene)]],
				['8'] = [[Attacca]],
				['7'] = [["Voglio correre come un lupo."]],
				['6'] = [["Voglio avere la vista di un'aquila."]],
				['5'] = [["Voglio essere forte come Ercole."]],
				['4'] = [["Voglio volare come un uccello."]],
				['3'] = [["Qual è il piano? Che cosa facciamo?"]]
			}
		},
		['halloween_boss_2022_6'] = {
			['choice'] = {
				['2'] = [[Lancia una granata verso l'asino]],
				['1'] = [[Lancia una corda verso l'asino]],
				['4'] = [[Segui l'asino]],
				['3'] = [[Salta e afferra l'asino]]
			},
			['episode'] = {
				['2'] = [[Lanciai una corda intorno al suo collo, ma l'asino riuscì a morderla! Subito dopo, notai che qualcosa era caduto dalla sua bocca.<new_line>L'asino scappò ragliando aiuto ad alta voce.]],
				['1'] = [[Riuscii a raggiungere una capanna nella palude ed è lì che trovai l'asino! Non faceva che muoversi con foga tra i cespugli e borbottare tra sé e sé:<new_line>"Fiori blu, spine rosse..."<new_line>Mi avvicinai abbastanza da fare la mia mossa.]],
				['5'] = [[Sentii un frastuono e un ruggito provenire da una piccola baracca di legno che non avevo mai notato prima. La bestia della palude!<new_line>Ma poi il Mietitore apparve accanto alla baracca e chiuse la porta con la sua falce.<new_line>"Bene, mortale, questa è l'ultima volta che ti aiuto! Corri, l'asino sta scappando! Io resto qui a trattenere la porta."]],
				['4'] = [[L'asino mi sentì saltare e riuscì a schivare di lato.<new_line>Scappò di nuovo ragliando aiuto ad alta voce.]],
				['3'] = [[Riuscì ad afferrare la granata con i denti e a rilanciarla verso di me. Subito dopo, notai che qualcosa era caduto dalla sua bocca.<new_line>L'asino scappò ragliando aiuto ad alta voce.]]
			}
		},
		['newyearEvent_time_easy'] = {
			['episode'] = {
				['2'] = [["Questo denaro serve a espandere le nostre operazioni, ma in questo mondo gli abitanti del posto ne vogliono molto di più rispetto al costo di lavoro medio. Se iniziassi a pagarli con la stessa quantità di denaro che chiedono, andremmo in bancarotta in men che non si dica, e i bambini verrebbero privati dello spirito delle festività."]],
				['1'] = [[Sono sceso vicino a un piccolo edificio con un'insegna con su scritto TLtoys Factory.<new_line>"Finalmente sei qui, mio caro amico. Avevo un immediato bisogno di aiuto. Nella fabbrica non ci sono lavoratori! Ho assolutamente bisogno che lavori per un paio di turni, altrimenti i bambini non riceveranno alcun giocattolo..."]],
				['9'] = [["Ho oh oh, che belli! I bambini saranno sicuramente entusiasti. Ti ringrazio per aver fatto una buona azione!"]],
				['8'] = [["Ecco qui i materiali. Se sei stanco, prenditi pure una pausa. Ma fa' che sia breve, e poi rimettiti subito all'opera!"]],
				['7'] = [["Bene, dobbiamo realizzare i giocattoli anche per i giovani boy scout. Ho trovato delle parti per creare dei soldatini giocattolo. Tuttavia, i materiali sono leggermente radioattivi. Potresti dargli una ripulita, oppure non ti spaventa contrarre una piccola malattia?"]],
				['6'] = [["Dunque, che cosa abbiamo qui? Che meraviglia! Sembra quasi che tu faccia questo lavoro da una vita. Di' un po', sotto quei capelli non nascondi delle orecchie a punta, vero?"]],
				['5'] = [["Ecco qui i materiali. Quando sarò di ritorno, i risultati del tuo lavoro dovranno abbellire il tavolo. Se sei pronto, non indugiare. Inizia pure."]],
				['4'] = [["Ti ho assegnato un lavoro fatto apposta per te! Prima dovrai realizzare i regali per le giovani componenti del gentil sesso. Scegli pure ciò che ritieni più opportuno realizzare."]],
				['3'] = [["Oh oh oh! Così si fa! Presto, raggiungi la tua postazione di lavoro!"]]
			},
			['choice'] = {
				['2'] = [["Hai un sacco di soldi. Non c'è proprio nessuno che abbia voglia di scommettere?"]],
				['1'] = [["Ok, lavorerò per un po'."]],
				['10'] = [[Mettiti all'opera (8 ore)]],
				['9'] = [["Li ripulirò." (14 ore)]],
				['8'] = [["Facciamo in fretta." (6 ore) (+ radiazioni)]],
				['7'] = [[Preparati]],
				['6'] = [["Voglio fare le bambole." (8 ore)]],
				['5'] = [["Voglio preparare le torte." (8 ore)]],
				['4'] = [[Vai alla postazione di lavoro]],
				['3'] = [["Non ho tempo." (Vattene)]]
			}
		},
		['comrade_in_trouble_rad'] = {
			['episode'] = {
				['1'] = [[L'esploratore giace a terra. Se la sarà cercata, ma è un modo orribile per andarsene.]]
			}
		},
		['emba_intro_bunker'] = {
			['episode'] = {
				['2'] = [[Ho eliminato i biomutanti dal laboratorio sotterraneo e ho trovato documenti con scritto "Riservato". Devo tornare dal Navigatore.]],
				['1'] = [[La vecchia porta del bunker è socchiusa e da sotto senti provenire uno sgradevole scricchiolio e un rumore metallico. Sembra che lì dentro ci sia qualcosa di pericoloso.]]
			},
			['choice'] = {
				['1'] = [[Torna dal Navigatore]]
			}
		},
		['traveler_salvation'] = {
			['episode'] = {
				['1'] = [[Lo trovo rannicchiato sotto un albero. Sembra aver esaurito le energie ed essersi addormentato, ma in lui arde ancora una scintilla di vita. Un minuto più tardi e sarebbe stato troppo tardi.]]
			}
		},
		['sled_repair'] = {
			['choice'] = {
				['1'] = [[Esamina la slitta]]
			},
			['episode'] = {
				['1'] = [[Non ho impiegato molto a trovare la slitta. È abbandonata su una strada di campagna in uno stato pietoso. Vediamo cosa posso fare.]]
			}
		},
		['emba_commander_medium'] = {
			['episode'] = {
				['2'] = [["Questa è tutt'altra cosa, grazie! Io e il mio compagno andavamo spesso alla città infetta. Volevamo verificare una nostra teoria. È così che siamo riusciti a rovinare le nostre tute protettive... La visibilità e la mobilità sono terribili ora.<new_line>Inoltre, non abbiamo ancora terminato il progetto sui biomutanti."]],
				['1'] = [["Ciao! Aspetta, siamo a corto di energia. Siamo appena arrivati. Syoma, hai una bevanda bioenergetica? No? Dannazione, cosa... <new_line>Ah! Forse tu puoi portarmi qualcosa di rinfrescante?"]],
				['6'] = [["Grazie! Li abbiamo abbattuti tutti, quindi è il momento dell'esperimento. Ti citerò nel rapporto, ci hai davvero aiutato. Lascia la radio su quel ceppo, la riprenderemo più tardi."]],
				['5'] = [["Sì, siamo pronti per iniziare l'attacco e stiamo aspettando i tuoi ordini."]],
				['4'] = [["Senti, ti andrebbe di coordinarci? Abbiamo le radio. Potresti apportare correzioni alle nostre azioni. Se lo fai, andremo lì nonostante le tute a brandelli."]],
				['3'] = [["Se ne occupa il mio capo della ricerca. È il dottor Isaev, un grande scienziato. Il mio compito ora è testare una nuova tecnologia per estrarre il micelio dai biomutanti abbattuti. Ma non so come riusciremo ad avvicinarci a loro con queste tute."]]
			},
			['choice'] = {
				['2'] = [["Sai cosa fare?"]],
				['1'] = [["Che tipo di progetto?"]],
				['4'] = [[Inizia la caccia]],
				['3'] = [["Va bene, dammi la radio e portami dai biomutanti."]]
			}
		},
		['embacity_biolab_3'] = {
			['episode'] = {
				['2'] = [["Oh, ecco che arriva il capo idraulico del Progresso! Quando hai pompato tutta la biomassa fuori dal serbatoio, sotto quella luce rossa laggiù, la porta si è aperta, hai visto?<new_line>C'è una scala dietro di essa. Non so come sia collegata. Forse anche dietro quella porta c'è un mostro affamato e tu l'hai lasciato senza cibo."]],
				['1'] = [[Ho sbirciato con cautela all'interno del laboratorio. La prima cosa che mi ha colpito è stata la luce rossa lampeggiante sopra la porta sulla parete di fondo della stanza.<new_line>Quella porta si è rivelata essere estremamente pesante e controllata automaticamente. Ma al momento è leggermente socchiusa.]],
				['12'] = [["Non parlerò dei miei affari con un soldato semplice! Lo scoprirai quando ti unirai al mio esercito.<new_line>Il tuo amico curioso ha già avuto modo di incontrare i miei esplosivi e ora non può più fare nulla. Mi occuperò io di te, però. Nella sala operatoria."]],
				['11'] = [["Rinsavire? "Tornare in sé da cosa? Solo un altro po' e sistemeremo tutto. E cominceremo con te. Ho imparato un metodo meraviglioso da uno dei miei colleghi scienziati..."]],
				['13'] = [["Ha detto che stava alludendo al professore? Pare che si siano incontrati dopo la catastrofe. Vorrei sapere qual è il ruolo di Gerin in tutto questo..."]],
				['14'] = [["Bene, combattente. Sembra che la mia missione finisca qui. Cosa è successo dopo l'esplosione?"]],
				['15'] = [["Ti rendi conto del potere che ho nelle mie mani ora? Ricostruirò il nostro Paese in rovina dalle ceneri!<new_line>E poi diffonderò una nuova piaga nel mondo. Ma tu non sarai qui per assistervi. Disubbidire agli ordini è punibile con la morte."]],
				['16'] = [["A giudicare dalla tua descrizione, questa è la prima spedizione Progresso scomparsa. Sono stati salvati, Isaev non lo dimenticherà... Per me è troppo tardi. È un bene che ci siamo incontrati."]],
				['6'] = [["Hanno costruito un intero palazzo sottoterra! Hai visto i tubi nel soffitto? È così che la biomassa arriva qui dall'alto. Ma perché non c'è nessuno qui...?<new_line>Se non è stato lo Spaccaossa a mangiare la biomassa, allora chi è stato? E quella porta si è aperta da sola?"]],
				['5'] = [["Forza, andiamo."]],
				['4'] = [[Al centro della sala, circondata da mutanti umanoidi, una figura spettrale era china su un tavolo da laboratorio.<new_line>Dall'oscurità della tromba delle scale, ho osservato i corpi mutilati dal micelio e sentito i loro grugniti animaleschi. Nessuno merita un simile destino.]],
				['3'] = [[Le scale dietro la porta mi hanno condotto in una spaziosa stanza sotterranea. Sopra, resti di biomassa verde scorrevano da tubi in fossati metallici e radici nodose si estendevano dal pavimento al soffitto, dirigendosi verso la superficie della città.]],
				['17'] = [["Non hai idea di quanto ti dobbiamo tutti. Finisci il lavoro. È stato un piacere percorrere questa strada con te, soldato."]],
				['7'] = [["E poi c'è questa sala. Wow, guarda la radice che cresce laggiù! Scommetto che è spessa come la testa del nostro amico Isaev.<new_line>Maledizione, è anche vuota... Basta scherzare. Non mi piace affatto, dobbiamo agire in fretta. Vedi la porta con la serratura a codice, laggiù?"]],
				['8'] = [["C'è uno scomparto che contiene la scatola nera. Inserisci il codice che ci hanno dato gli scienziati, prendi la scatola e andiamocene prima che succeda qualcosa.<new_line>Io farò la guardia qui e mi occuperò del sistema elettronico. Forse riuscirò a capire cosa sta succedendo."]],
				['9'] = [["Aaah!"<new_line><new_line>(Ci fu un boato e un'esplosione mi scaraventò contro il muro. Sbattei dolorosamente la testa e non vidi più nulla.)]],
				['10'] = [["Chi è che si infiltra in una struttura militare? Un nuovo combattente per il mio esercito? Formazione!"]]
			},
			['choice'] = {
				['2'] = [["Sì, è strano. Dove andiamo adesso?"]],
				['1'] = [["Allora, scendiamo?"]],
				['10'] = [[Prendi la scatola nera e vai a cercare aiuto]],
				['9'] = [[Prendi la scatola nera e vattene]],
				['8'] = [["Dobbiamo sbrigarci a trovare aiuto per te e per quelle persone."]],
				['7'] = [[Menziona Kurganov]],
				['6'] = [["Sei pazzo e io ti fermerò!"]],
				['5'] = [["Da Gerin? Che hai fatto al professore?"]],
				['4'] = [["Generale Kurganov? Cosa stai facendo, rinsavisci!"]],
				['3'] = [["Lo vedo."]]
			}
		},
		['snowmen'] = {
			['episode'] = {
				['1'] = [[Una banda di pupazzi di neve sta tirando palle di neve a un povero disgraziato. È ora di porre fine ai loro divertimenti.]]
			}
		},
		['doll'] = {
			['episode'] = {
				['2'] = [[Sei pazzo, esplode il burattinaio con una risata roca. ]],
				['1'] = [[È seduto su una sedia, intento ad armeggiare con qualcosa. Alcune marionette gli passano degli strumenti, mentre altre gli reggono delle candele. Quando si accorge di me, il burattinaio sorride: ]]
			}
		},
		['search_killer1'] = {
			['episode'] = {
				['1'] = [[Ho trovato un cadavere vicino al portale. Devo scoprire dove si trova l'assassino.]]
			}
		},
		['set_up_devices'] = {
			['episode'] = {
				['2'] = [[Gli Archivisti volevano collocare l'attrezzatura proprio qui. E capisco perché: ho visto biomutanti in lontananza.]],
				['1'] = [[Sono in posizione. Ora devo piazzare e mimetizzare attentamente l'attrezzatura.]],
				['3'] = [[Ho terminato l'installazione e ho cercato di mimetizzare l'attrezzatura. I biomutanti hanno iniziato ad avvicinarsi gradualmente. L'attrezzatura probabilmente li influenza in qualche modo e c'è il rischio che possano danneggiarla.<new_line>Gli Archivisti apprezzeranno il fatto che mi sia preso cura della loro attrezzatura. D'altra parte, ho già completato il mio incarico principale.]]
			}
		},
		['provision_bar'] = {
			['episode'] = {
				['1'] = [["Fantastico, abbiamo grandi speranze per questa impresa. Il tuo contributo ci aiuterà a ottenere informazioni importanti sul progetto EMBA. Grazie."]]
			}
		},
		['order_delivery_quest_pt2'] = {
			['episode'] = {
				['2'] = [[Perché quella faccia lunga? Qualcosa non va con il carico?']],
				['1'] = [[Allora, com'è andata?']],
				['5'] = [[Nella cassa c'erano alcune scorte. Non valeva di sicuro tutti quei soldi ma era meglio che non avere niente in mano.]],
				['4'] = [[L'oste ha emesso un lungo sospiro.<new_line>'Quei maledetti mercanti! Beh, non è colpa tua. Ti ripagherò. Ecco qui.']],
				['3'] = [[L'oste ha scosso la testa.<new_line>'Scusa, è troppo costoso per me. Puoi tenerlo.']]
			},
			['choice'] = {
				['2'] = [[Chiedigli quanto vuole]],
				['1'] = [[Annuisci e dagli il carico]],
				['5'] = [[Prendi le scorte]],
				['4'] = [[Apri la cassa e scopri cosa contiene]],
				['3'] = [[Dai il carico e prendi i soldi]]
			}
		},
		['intelligence_bar'] = {
			['episode'] = {
				['1'] = [["Hai i dati di ricognizione? Eccellente, contengono informazioni estremamente preziose sul progetto EMBA."]]
			}
		},
		['emba_various_hard'] = {
			['episode'] = {
				['2'] = [["Beh, sono dovuto andare in giro e interrogare un sacco di gente, visitando tanti posti diversi... Ho impiegato molto tempo a fare tutto. Non posso rifarlo in tempo da solo.<new_line>Aiutami a ottenere cadaveri biomutanti per le autopsie. Ho bisogno di informazioni dirette."]],
				['1'] = [["Ehi! Come va la vita? Sono nei guai. Ho perso i miei rapporti di ricerca in giro. Il dottor Isaev mi aveva incaricato di raccogliere informazioni su biomutanti e micelio da fonti esterne."]],
				['12'] = [[Lottando per rimanere sveglio, ho annotato attentamente le parti che ritenevo importanti.<new_line>Ora devo tornare dal Biologo e chiedere se devo interrogare qualcun altro.]],
				['11'] = [["Vuoi che te ne parli? Pensi che io sia un conferenziere o qualcosa del genere? È tutto nei miei film! Siediti, ti mostrerò le ultime due serie."]],
				['13'] = [["Ah, vuoi dire Alexander? Sì, lo conosco. Posso darti le cartelle cliniche dei miei vecchi pazienti. Ma ho anche bisogno di aiuto. Il campo è stato attaccato e i feriti devono essere portati al mio ospedale. Puoi farlo?"]],
				['14'] = [[Ho finito con i feriti e ho preso la scatola delle cartelle dei pazienti.<new_line>Ora devo tornare dal Biologo e scoprire se devo interrogare qualcun altro.]],
				['15'] = [["Vuoi che parli di nuovo con quattrocchi? No, ho un'incursione in arrivo.<new_line>Però... se sostituisci uno dei miei combattenti durante l'assalto, posso mandarlo a chiacchierare con gli scienziati."]],
				['16'] = [[Ho pagato i ragazzi per il loro aiuto.<new_line>Ora devo tornare dal Biologo e chiedere se devo fare domande a qualcun altro.]],
				['6'] = [["Non lontano da qui c'è un medico che ha organizzato un ospedale da campo. Sta curando coloro che sono stati colpiti dalle spore del micelio. Deve avere molte informazioni utili sulla progressione della malattia.<new_line>Ovviamente non è interessato al profitto, quindi dovrebbe aiutarci."]],
				['5'] = [["C'è un regista che fa molti film sui mutanti. Mi ha anche intervistato, ci pensi? Comunque, ha raccolto molte osservazioni utili, quindi lascia che te ne parli."]],
				['4'] = [["Io e i miei colleghi una volta abbiamo fatto visita a un uomo anziano... per lavoro. Era davvero intelligente! Stava quasi coltivando il micelio nella sua cantina.<new_line>Ci ha detto un sacco di cose. Ricordo quasi tutto, ma era reticente a parlare della coltivazione del micelio. Dovresti provare a fargli qualche domanda."]],
				['3'] = [["Per ora mi occuperò io delle autopsie, mentre tu cominci ad andare in giro a fare qualche domanda alla gente?"]],
				['19'] = [["Questo è tutto! Ho anche visitato un paio di persone e questo dovrebbe bastare per la relazione. Grazie mille, non volevo davvero essere imbarazzato di fronte al dottor Isaev."]],
				['18'] = [[Ho annotato tutte le cose importanti che ho visto nella serra e ho persino fatto un paio di schizzi del micelio.<new_line>Ora devo tornare dal Biologo e chiedergli se devo parlare con qualcun altro.]],
				['17'] = [["Sì, è successo. Abbiamo abbandonato quella serra abbastanza in fretta, perché ci sarebbe costato caro occuparcene. Posso farti fare un giro, ma non ci andrò senza protezione. Dobbiamo trovare una sorta di filtro respiratorio."]],
				['7'] = [["Ho parlato con il capo dei combattenti del campo vicino. Non so se accetterà di raccontare tutto di nuovo, il ragazzo non era un gran chiacchierone. Ma dovresti provare."]],
				['8'] = [["Guarda, c'è una fattoria dietro quel campo. Ho sentito che il micelio aveva ricoperto i loro raccolti. Dovresti fare alcune domande, potrebbero persino mostrarti le piante infette."]],
				['9'] = [["Tu cosa? Vuoi che ti parli del micelio? Forse vuoi le chiavi dell'appartamento dove ho i miei soldi?!<new_line>Bene, portami del materiale per la distillazione e poi te ne parlerò."]],
				['10'] = [["Ecco, prendi questo taccuino, ho scritto tutti i passaggi per coltivarlo qui. E di' a quel furfante di riportarmi il taccuino più tardi o gli faccio... un'iniezione dove non batte il sole!"]]
			},
			['choice'] = {
				['2'] = [["Bene. Andrò a sparare a quelli laggiù." (Combatti)]],
				['1'] = [["Quali sono queste fonti?"]],
				['11'] = [[Accetta (combatti)]],
				['10'] = [[Trasporta i feriti]],
				['9'] = [[Guarda il film]],
				['8'] = [[Torna dal Biologo]],
				['7'] = [[Vai dai contadini]],
				['6'] = [[Vai dal Leader]],
				['5'] = [[Vai dal Dottore]],
				['4'] = [[Vai dal Regista]],
				['3'] = [["Dove devo andare?"]]
			}
		},
		['emba_time_medium2'] = {
			['episode'] = {
				['2'] = [["Sto osservando i movimenti dei biomutanti. Sto cercando di trovare la logica nelle loro migrazioni. Voglio solo capire se ci sono specie senzienti tra di loro.<new_line>Ho bisogno di un partner per non perdermi nulla."]],
				['1'] = [["Ehi, vieni qui. Ho visto come dai la caccia ai biomutanti. Forse potrei portarti con me in missione."]],
				['12'] = [["Grande. Bene, lasciamolo qui adesso, andiamocene e osserviamolo con questo monitor. Ho messo lì una trappola a innesco."]],
				['11'] = [["Sì, i mangiacadaveri sono i più interessanti. Il micelio ha cambiato gli animali semplici, rendendoli più forti e più intelligenti, conferendo a molti di loro delle abilità insolite.<new_line>Senti... attiriamone uno qui. Voglio comunque studiare la loro dieta. Scoprire quali ortaggi mangiano. Vai a trovarne un po', io aspetterò qui."]],
				['14'] = [["Dannazione, se n'è andato! Non importa, prima o poi... E grazie per il tuo aiuto, ora puoi andare. Adesso ho altre cose a cui pensare."]],
				['10'] = [["Hai registrato tutto? Bene. Quei due parassiti hanno portato diversi mangiacadaveri nel loro branco.<new_line>Sono davvero intelligenti..."]],
				['13'] = [[Il mangiacadaveri si era avvicinato lentamente, aveva gracchiato e rilasciato una nuvola di spore dalle sue fauci verso l'ortaggio e solo dopo ci si è avvicinato.<new_line>Così facendo, ha fatto scattare il filo, c'è stato uno schiocco e una nuvola di fumo bianco. Tuttavia, sembra che il fumo non abbia avuto alcun effetto sul monitor dopo essersi mescolato con la nuvola di spore.<new_line>Quando il fumo e le spore si sono diradate, il mangiacadaveri si era già dileguato.]],
				['9'] = [[Mi sono concentrato sull'osservazione del terzo parassita. Non ho fretta di andare da nessuna parte, quindi ho tempo per prepararmi.]],
				['8'] = [[La Cacciatrice ridacchiava scettica.<new_line>"Spero che un giorno la tua presunzione non ti trasformi nella loro cena. Rintracceremo i parassiti.<new_line>Il loro comportamento è piuttosto complesso e dobbiamo stare il più attenti possibile. Prenderò questi due e tu dovresti cercare di non perdere l'altro con il fucile d'assalto."]],
				['7'] = [["E ora, osserviamo questi parassiti.<new_line>Il loro comportamento è piuttosto complesso e dobbiamo stare il più attenti possibile. Prenderò questi due e tu dovresti cercare di non perdere l'altro con il fucile d'assalto."]],
				['6'] = [["Sono i più intelligenti di tutti. Non sono così infettati dal micelio. Probabilmente sono riusciti a resistere più a lungo alla sua influenza. Comunque, sono dei mostri.<new_line>Inoltre, sono i più astuti e pericolosi. Non hanno dimenticato come usare le armi umane. Però almeno non ti infettano, perché non sputano spore."]],
				['5'] = [[Ho seguito gli erbosi, registrando quello che hanno fatto quando si sono fermati. Alla fine, sono tornati al gruppo principale.<new_line><new_line>"Uhm, sono tornati. Sono andati in ricognizione? O stavano cercando cibo? Ci rifletterò più tardi. E quelle creature sono parassiti, capisci?"]],
				['4'] = [[Gli erbosi si stanno muovendo lentamente per ora. Posso seguirli continuamente oppure occuparmi dei miei affari senza il rischio di perderne le tracce.]],
				['3'] = [["Eccellente, andiamo. Vedi quel branco laggiù? I più grandi sono gli erbosi. Dubito che siano senzienti, poiché i loro corpi sono troppo consumati dal micelio. Guarda, si sono separati dal gruppo.<new_line>Seguili. Se attaccano, ricorda che quando un erboso cade, rilascia una nuvola di spore che colpisce chiunque si trovi nelle vicinanze. Quindi tieniti a distanza da loro in battaglia."]]
			},
			['choice'] = {
				['2'] = [[Segui gli erbosi]],
				['1'] = [["Di che lavoro si tratta?"]],
				['9'] = [[Allontanati e osserva]],
				['8'] = [["Ora osserviamo i mangiacadaveri?"]],
				['7'] = [[Tira fuori i registri delle osservazioni]],
				['6'] = [["Inteso."]],
				['5'] = [["Sì, so molto su di loro."]],
				['4'] = [["Cosa sai di loro?"]],
				['3'] = [[Insegui gli erbosi]]
			}
		},
		['emba_time_hard'] = {
			['episode'] = {
				['2'] = [["Ho deciso di applicare il mio talento alla creazione di un prodotto a cavallo tra scienza e cinema, altamente artistico. Ho avuto un'illuminazione: questo è ciò di cui le persone hanno più bisogno in questo momento."]],
				['1'] = [["Compagno, puoi dedicarmi un minuto? Sto filmando una serie sulla routine quotidiana degli scienziati del Progresso. Devo raccogliere molto materiale e il mio pigro cameraman si è ammalato. Ho bisogno di qualcuno che lo sostituisca."]],
				['11'] = [["Grazie. Bene, vado al laboratorio fotografico. Torna più tardi, ho già trovato qualcosa da filmare per la serie successiva."]],
				['10'] = [["Allora hai portato i prodotti chimici? Adesso devo passare la notte a lavorare nel laboratorio fotografico. Alcuni dei ragazzi del Progresso potrebbero almeno dare una mano, sto facendo tutto questo solo per loro."]],
				['9'] = [["Ben fatto! Mi piacerebbe una troupe cinematografica che facesse un turno così ogni giorno. C'è un mare infinito di materiale adesso.<new_line>Aspetta, ho un altro lavoro per te. Sto esaurendo lo sviluppatore liquido, ma ho imparato a prepararlo da solo.<new_line>Però mi servono alcuni prodotti chimici. Me li puoi procurare? Altrimenti tutte le mie... voglio dire, le tue fatiche saranno state vane..."]],
				['8'] = [[Penso di aver raccolto abbastanza materiale per riempire dieci serie ed è ora di consegnare il film al Regista.]],
				['7'] = [["È parecchia attrezzatura per il regista baffuto. Sapessi dove mi ha portato la mia ricerca! In pratica, avevo perso i tappi per il cromatografo e siamo partiti per..."<new_line><new_line>Mi sono preparato a filmare un'altra storia delle prove e tribolazioni di uno scienziato del Progresso.]],
				['6'] = [["Ehi, cosa stai facendo? Stai registrando?"]],
				['5'] = [[Ho registrato parecchie panoramiche, quindi ora posso filmare alcuni oggetti da vicino. Gli scienziati mi hanno permesso di filmare da vicino la centrifuga e il vivaio biologico in azione.]],
				['4'] = [["Ho già fatto un accordo con un paio di scienziati. Il loro ATV è laggiù. Premi questo pulsante e non dimenticarti di rimuovere l'otturatore. Non esagerare con le registrazioni, tanto soltanto un decimo al massimo del materiale sarà utilizzabile."]],
				['3'] = [["Non capisci bene le persone e il cinema. Scapperanno se proponi strane invenzioni e spiegazioni incomprensibili.<new_line>Quando tutto crolla, il lavoro dell'artista aumenta. Devo colmare il divario tra la scienza e le persone."]]
			},
			['choice'] = {
				['2'] = [["Posso aiutare."]],
				['1'] = [["Cos'è questa serie?"]],
				['9'] = [[Parti alla ricerca]],
				['8'] = [[Torna dal Regista]],
				['7'] = [[Filma un'intervista]],
				['6'] = [["Sì, che ne dici?"]],
				['5'] = [[Filma l'attrezzatura di lavoro]],
				['4'] = [[Inizia a filmare gli scienziati]],
				['3'] = [["Pensavo che avessero bisogno di scienziati."]]
			}
		},
		['emba_time_easy2'] = {
			['episode'] = {
				['2'] = [[Ho ripulito intorno al pozzo, ma ho notato che qualcosa fioriva all'interno. Anche il terriccio galleggiava sopra l'acqua nelle sue profondità.]],
				['1'] = [[C'era un vecchio pozzo al centro delle piante intrecciate. Sembra che il micelio vi si sia attaccato come fonte d'acqua e sia cresciuto rapidamente.<new_line>Luoghi come questo attirano sempre biomutanti, quindi questa potenziale fonte di pericolo deve essere distrutta.]],
				['4'] = [[Finalmente ho fatto tutto. Almeno ora c'è una zona calda in meno in questo distretto.]],
				['3'] = [[Ottimo, il pozzo è stato pulito. Però, tutta questa vegetazione pericolosa ora deve essere ammucchiata e bruciata. Altrimenti, i biomutanti si riuniranno comunque qui, per nutrirsi e diventare un branco pericoloso.]]
			},
			['choice'] = {
				['2'] = [[Pulisci il pozzo]],
				['1'] = [[Rompi tutti i rami e le radici]],
				['3'] = [[Ammucchia tutto e dai fuoco]]
			}
		},
		['emba_search_easy2'] = {
			['episode'] = {
				['2'] = [["Allora, hai raccolto l'erbario? Non vediamo l'ora di iniziare l'esperimento!"<new_line>]],
				['1'] = [["Ciao amico! Vuoi aiutare la scienza? Stiamo studiando il modo in cui il micelio interagisce con le piante.<new_line>Puoi consegnarci campioni di flora tipica locale?"]],
				['3'] = [[Lo scienziato ha raccolto le piante ed è salito sull'ATV. Dopo qualche tempo, è tornato.<new_line><new_line>"Un risultato promettente, collega! Il micelio ha iniziato a interagire attivamente con il tessuto estraneo fin dai primi secondi della loro combinazione.<new_line>Bene, vado a continuare le mie osservazioni! Spero che continueremo a lavorare insieme!"]]
			}
		},
		['forest_pithouse'] = {
			['choice'] = {
				['2'] = [[Entra]],
				['1'] = [[Nasconditi e aspetta]],
				['12'] = [[Ispeziona dentro e fuori]],
				['11'] = [[Ok, me ne vado.]],
				['10'] = [[Cosa ti prende? Volevo solo parlare un po'!]],
				['9'] = [[Esci]],
				['8'] = [[Saluta e vai via]],
				['7'] = [[È da tanto che non vedo gente. Mi piacerebbe chiacchierare un po'.]],
				['6'] = [[Passavo da queste parti]],
				['5'] = [[Esci dai cespugli e saluta]],
				['4'] = [[Metti una granata con filo]],
				['3'] = [[Ispeziona ovunque]]
			},
			['episode'] = {
				['2'] = [[Prima il padrone del rifugio è tornato. Era un uomo basso con un fucile e una cesta piena di funghi. Si è guardato in giro cautamente e poi ha attizzato le braci del fuoco.]],
				['1'] = [[Nella foresta ho trovato un rifugio scavato. Le braci del fuoco erano ancora calde, quindi qualcuno deve vivere qui e non può trovarsi molto lontano. Dall'interno non proviene alcun rumore.]],
				['12'] = [[Ho sistemato la granata e mi sono nascosto dietro ai cespugli. Poco dopo è apparso il padrone del rifugio. Appena è entrato, la granata è esplosa. Quando mi sono avvicinato era già morto. Ora posso ispezionare bene il posto.]],
				['11'] = [[Vicino al rifugio ho trovato bacche, carne, pelli lasciate ad asciugare e un cesto di carbone.]],
				['10'] = [[Non appena sono uscito con il sacco pieno di roba, ho visto il padrone del rifugio: un uomo che puntava un fucile verso di me. Dopo un attimo ho sentito uno sparo e un dolore lancinante alla spalla destra.]],
				['13'] = [[Proprio quando stavo per andare via con tutto il bottino, un intero branco di lupi è apparso da dietro gli alberi. Dev'essere stato il rumore dell'esplosione e del mio rovistare ad attrarre la loro attenzione. Dovrò difendermi.]],
				['9'] = [[Dentro il rifugio ho trovato un sacco di scorte: cibo, medicine, attrezzi, munizioni e altro. Sono dovuto scappare prima che tornasse il padrone.]],
				['8'] = [[La porta era chiusa con un semplice chiavistello, probabilmente per tenere alla larga gli animali. Dentro faceva piuttosto caldo. Negli angoli si trovavano sacchi e casse piene di vari oggetti.]],
				['7'] = [[Quando ho detto di voler andare, mi ha proposto di scambiare sale, sigarette o munizioni.]],
				['6'] = [[Quando l'uomo ha capito che non ero pericoloso, si è calmato. Abbiamo chiacchierato amabilmente, mangiato e bevuto. Abbiamo parlato a lungo del passato e condiviso i nostri piani per il futuro.]],
				['5'] = [[Mi ha guardato con sospetto, ma poi si è rilassato. <new_line>]],
				['4'] = [[Ti consiglio di continuare a camminare]],
				['3'] = [[Di punto in bianco, l'uomo si è rizzato in piedi e ha puntato il fucile verso di me.<new_line>Mi ha chiesto: ]]
			}
		},
		['emba_fight_easy3'] = {
			['episode'] = {
				['2'] = [[Quindi, ecco qual era il problema: i biomutanti avevano litigato con un branco di pericolosi predatori. La forbice ha trovato il sasso!<new_line>Queste sono le condizioni ideali per distruggere entrambi nella confusione.]],
				['1'] = [[Ho notato i biomutanti da lontano. Si stanno comportando in modo piuttosto insolito.]]
			}
		},
		['emba_commander_medium2'] = {
			['episode'] = {
				['2'] = [["Aspetta. Sto lavorando a un progettino e potrei aver bisogno di un po' di aiuto. Mi serve qualcuno che sappia distinguere un fucile d'assalto da una vanga.<new_line>Ma prima c'è qualche altra faccenda da risolvere. Mi serve una corda, nel caso ne dovessi catturare qualcuno vivo. Ne hai una? L'ATV è troppo lontano."]],
				['1'] = [["Aspetta. Sto lavorando a un progettino e potrei aver bisogno di un po' di aiuto. Mi serve qualcuno che sappia distinguere un fucile d'assalto da una vanga.<new_line>Ma prima c'è qualche altra faccenda da risolvere. Sto esaurendo la vernice mimetica. Puoi portarmi la base per una nuova porzione, mentre io diluisco la vernice?"]],
				['11'] = [["Non hai abbassato la guardia, posso contare su di te. Sarebbe stato bello catturare qualcosa di vivo, ovviamente... per curiosità.<new_line>Beh, pazienza. La prossima volta. Grazie per l'aiuto."]],
				['10'] = [[Ci siamo tenuti impegnati per un po'. Quando ha iniziato a fare buio, la Cacciatrice si è alzata, mi ha dato la radio e mi ha detto di iniziare.]],
				['9'] = [["Sapevi che lucertole e varani sono mangiatori di carogne e superano facilmente qualsiasi ostacolo? Almeno non volano.<new_line>I rettili mi sono sempre interessati. E questi hanno tutte quelle capacità... Spero di catturarne uno stavolta.<new_line>Uhm, comunque, basta parlare. È ora di andarsene. Ecco la radio. Ci muoviamo?"]],
				['8'] = [["Eccellente. Mentre aspettiamo che faccia buio, posso dirti qualcosa sui biomutanti.<new_line>Hai pulito la tua arma di recente? Mi sembra sporca e non mi sorprenderebbe se si bloccasse a ogni secondo colpo."]],
				['7'] = [["Dovrò agire rapidamente, quindi non posso appesantirmi, dovrò nascondermi senza attrezzature extra.<new_line>Ecco perché voglio che tu prenda un mirino per la visione notturna, ti metta in posizione nelle vicinanze con una radio e mi riferisca la situazione sul campo di battaglia. Agirò in base ai tuoi rapporti."]],
				['6'] = [["Hai fretta di andare da qualche parte? Allora, il punto è che voglio attaccare i biomutanti al buio e vedere come fanno a orientarsi senza luce. Un altro esperimento."]],
				['5'] = [["Sì. Allora, che ne dici di una piccola avventura? Non hai paura del buio, vero?"]],
				['4'] = [["Pochi l'hanno fatto, ecco perché voglio sapere come agiscono queste creature nell'oscurità.<new_line>Potrei anche catturarne qualcuna! Sarebbe una svolta per il mio progetto.<new_line>Ma questo è un lavoro per due cacciatrici."]],
				['3'] = [["Bene, mettiamoci al lavoro. L'operazione è complicata e il Progresso non riesce a trovarmi un partner esperto.<new_line>Quindi dovrai farlo tu... Hai mai dato la caccia ai biomutanti di notte?"]]
			},
			['choice'] = {
				['2'] = [["Per noi due?"]],
				['1'] = [["Cosa vuoi che faccia?"]],
				['9'] = [[Avvia l'operazione]],
				['8'] = [[Lavora sulle mie armi]],
				['7'] = [[Trova informazioni sui biomutanti]],
				['6'] = [["Non sono ancora pronto."]],
				['5'] = [["Posso provare."]],
				['4'] = [["Cosa ti serve che io faccia?"]],
				['3'] = [["Non avrò paura, cosa bisogna fare?"]]
			}
		},
		['halloween_boss_2022_5'] = {
			['choice'] = {
				['2'] = [[Procedi]],
				['1'] = [[Girati]]
			},
			['episode'] = {
				['2'] = [[Sono corso verso quella persona, ma ormai era troppo tardi. Era già morta dissanguata a causa delle ferite.<new_line>A quel punto, ho sentito un sogghigno provenire da dietro di me.]],
				['1'] = [[Dopo il mio arrivo, mi sono imbattuto in un gruppo di mostri accalcati attorno a qualcuno. Non avevo scelta, si erano già accorti di me.]],
				['3'] = [["Non sai leggere, mortale? Se il nome di qualcuno è scritto sulla pagina, non c'è alcuna speranza che possa salvarsi. Non sprecare il tuo tempo. Igor raccoglie solo gli organi dei cadaveri più freschi. Continua a cercare."]]
			}
		},
		['toy_story'] = {
			['name'] = [[Terem]],
			['episode'] = {
				['2'] = [[Fermi! sbraita la fanciulla di neve, precipitandosi all'interno del terem. Sono circondato da giocattoli rotti. Uno schiaccianoci si immobilizza con la sciabola sguainata.]],
				['1'] = [[La porta del terem, così minuziosamente decorato, è sfondata. All'interno regna il caos. Ancor prima di avere il tempo di guardarmi attorno, sento una strana vocetta provenire da dietro il tavolo rovesciato: «Sono tornati! Presto, caricate il cannone! Pronti, puntare, fuoco!». Dopodiché, sento un scoppio e proiettile minuscolo mi sfreccia vicino a un orecchio. Che sta succedendo?]],
				['3'] = [[Un'ora più tardi, dopo aver dato una ripulita, scopriamo che i briganti di ghiaccio hanno attaccato il terem. Hanno messo tutto a soqquadro, ma non hanno rubato nulla. Vedendomi, i giocattoli avevano creduto che fossi uno di loro. La fanciulla di neve ha promesso di aiutarli a scoprire da dove sono venuti i briganti.]]
			}
		},
		['emba_isaev_chelyabinsk'] = {
			['episode'] = {
				['2'] = [["La situazione sta iniziando a farsi più chiara. In questa stazione biologica gli scienziati agli ordini di Gerin stavano lavorando a un progetto in grado di rinverdire il mondo dopo una guerra nucleare.<new_line>Ma non avevano completato il lavoro, quindi non hanno potuto lanciare il progetto."]],
				['1'] = [["Gerin deve trovarsi nella stazione biologica più vicina. Dobbiamo ottenere il suo codice di accesso. Devo chiederti di nuovo di completare i nostri incarichi di ricognizione per recuperare i documenti del progetto segreto E.M.B.A."]],
				['10'] = [["Stiamo lavorando al meccanismo di rinverdimento, dobbiamo continuare a indagare sul disastro alla città della scienza. Abbiamo trovato prove di attività sospette presso un'altra stazione biologica.<new_line>Però, stando ai rapporti, lì gli scienziati non stavano eseguendo esperimenti sulle mutazioni. Partiremo non appena sarà tutto pronto."]],
				['9'] = [["Gerin non ha creato il micelio. Lo sospettavo da molto, ma ora la mia ipotesi è stata confermata. Il micelio è una specie biologica a sé stante e Gerin stava cercando di modificarlo per adattarlo all'uso agricolo."]],
				['8'] = [["Se è accaduto una volta, può accadere di nuovo. Dobbiamo scoprire le cause di quello che è successo. Non interromperemo le ricerche, ma questa minaccia va eliminata."]],
				['7'] = [["Il segnale è arrivato da un altro posto. Non si è limitato a lanciare il progetto, ma prima ha anche apportato delle mutazioni al micelio in tutte le stazioni biologiche.<new_line>Naturalmente, ciò non assolve Gerin, ma in ogni caso è una grave minaccia per le nostre ricerche."]],
				['6'] = [["È orribile. Facevamo molto affidamento su Gerin... Però ci sono notizie ancora peggiori. L'ordine per il lancio non programmato del progetto di rinverdimento delle terre desolate non è arrivato da questa stazione biologica."]],
				['5'] = [["Sì, ho già mandato dei dottori agli scienziati. Ma aspetta, non è possibile! Hai la certezza che fosse lui a controllare gli scienziati? Il professor Gerin... ha dato la vita affinché noi potessimo avere un futuro!"]],
				['4'] = [["Se riuscissimo a mettere le mani sulla scatola nera di questo laboratorio, potremmo fare grandi progressi nelle nostre ricerche. In fondo, sembra che il nostro lavoro sia già stato fatto, anche se non abbiamo avuto il tempo di finirlo.<new_line>Abbiamo decifrato il codice di accesso dello scomparto segreto del laboratorio. Eccolo, l'ho scritto qui."<new_line>(Ti porge un pezzo di carta con il codice)]],
				['3'] = [["Sì, come hai detto, il progetto è stato lanciato. Potrebbe essersi trattato di un errore a cui Gerin sta cercando di rimediare."]]
			},
			['choice'] = {
				['2'] = [["Ma i biomutanti sono apparsi a causa del lancio del progetto."]],
				['6'] = [["Penso che abbia trasmesso la tecnologia anche a Kurganov."]],
				['5'] = [["Cosa hai scoperto?"]],
				['4'] = [["Perché si tratta di una minaccia?"]],
				['3'] = [["Da dove?"]]
			}
		},
		['lair_beehive'] = {
			['episode'] = {
				['1'] = [[Un tempo, questo doveva essere stato un normale edificio di cinque piani, ma le api mutanti lo hanno trasformato nella loro casa. Dall'interno proviene un ronzio. Scommetto che all'interno c'è qualcosa di utile, ma le api non mi faranno entrare tanto facilmente nel loro alveare.<new_line>]]
			}
		},
		['emba_energy_hard'] = {
			['episode'] = {
				['2'] = [["Sai, anch'io sono una specie di scienziato. Ma ho bisogno di materiali per studiare. C'è un vecchio con cui ho fatto un patto, devi solo andare a trovarlo e ritirarli."]],
				['1'] = [["Ehi, mi ricordo di te. Vuoi fare un esperimento? Penso che abbiamo già fatto alcuni esperimenti insieme."]],
				['12'] = [[Ho un buco di memoria e non ricordo cosa è successo dopo. Mi sono svegliato in cortile, dove tutto era sottosopra e c'era un coltello ricurvo conficcato nel recinto.<new_line>La casa del contadino aveva le tende tirate su tutte le finestre e le porte erano chiuse. Sembra che l'esperimento sia fallito.<new_line>Mi sento terribile. Probabilmente ho calcolato male la dose: quell'infuso di micelio non è uno scherzo.]],
				['11'] = [[È come se il mio ricordo di ciò che è accaduto dopo, sia stato completamente cancellato. Mi sono svegliato in cortile, dove tutto era sottosopra e un coltello piegato era conficcato nel recinto.<new_line>La capanna del contadino aveva le tende tirate su tutte le finestre e le porte chiuse. Sembra che l'esperimento sia fallito.<new_line>Ma non mi sento così male come previsto: potrebbe essere l'effetto dell'infuso di micelio.]],
				['10'] = [[Bevuto l'ultimo bicchierino, il contadino si è alzato con qualche difficoltà, ha fatto due passi verso la porta ed è caduto a terra, cominciando subito a russare. Ho preso i suoi registri dal tavolo e mi sono avviato tremante verso l'uscita. Lo porterò agli scienziati in modo che possano valutare la sua ricerca.]],
				['9'] = [["Vedi, s-se prendo la p-p-porzione giusta posso tagliarne dieci di quei meh... muh... mutanti. Usciamo in cortile e ti faccio vedere! Però ce ne serve... anc-cora..."]],
				['8'] = [["Guarda cosa sta facendo questo mic-celio. E noi siamo persone serie. Completiamo l'esperimento. Forza, per la scienza!"]],
				['7'] = [["Quel vecchio ha davvero una formula speciale. Puro re-re-agente. Scrivi le tue os-serrvazioni in questo diario, lo porteremo a quei tizi g-gialli più tardi. Prendiamone ancora!"]],
				['6'] = [["L'infuso del vecchio ha molte proprietà inesplorate. Se la scienza chiama, bisogna rispondere. Iniziamo l'esperimento?"]],
				['5'] = [["Questo è infuso di micelio, fratello. Agli scienziati non interessa, ma questa roba ha un vero effetto fisico! È davvero speciale!"]],
				['4'] = [[Il vecchio mi ha dato un fagotto che ho riportato al contadino.<new_line><new_line>L'ha aperto con allegria e ha tirato fuori una bottiglia di liquido verde, poi ha iniziato ad annotare qualcosa nel suo diario:<new_line>"Dunque. Stiamo effettuando l'esperimento numero... ehm... non me lo ricordo. Materiali... un litro di sostanza da studiare. Allora, procediamo?"]],
				['3'] = [["Ti ha mandato il contadino? Gliel'ho già detto: niente cibo, niente merci. Puoi portare tu stesso del cibo da scambiare con me, se quel bifolco è troppo pigro."]]
			},
			['choice'] = {
				['2'] = [[Vai dal vecchio]],
				['1'] = [["Ti interessa la scienza?"]],
				['5'] = [["Sei proprio uno scienziato..."]],
				['4'] = [["Che cos'è questo?"]],
				['3'] = [["Versalo."]]
			}
		},
		['emba_energy_easy3'] = {
			['episode'] = {
				['2'] = [["Un po' di aiuto ci sarebbe utile. Giusto ieri ci siamo imbattuti casualmente in questa fattoria ed era interamente ricoperta da queste strane radici e terriccio.<new_line>Abbiamo lavorato per circa dieci ore, per cercare di rimuovere tutto. Non ci reggiamo più in piedi e tutta questa spazzatura deve essere raccolta e ammucchiata per bruciarla."]],
				['1'] = [["Oh ciao. Se hai bisogno di qualcosa, dovrai aspettare, mi dispiace. Siamo impegnati ora e, a essere sinceri, siamo tutti molto stanchi."]],
				['5'] = [[I contadini hanno versato benzina sul mucchio e quando abbiamo visto il modo in cui bruciava, ci siamo sentiti soddisfatti.<new_line>Poi, ho detto addio, dopo che mi hanno ringraziato e mi hanno augurato buona fortuna.]],
				['4'] = [[Non è stato facile. C'erano gambi spessi e radici tritate sparsi dappertutto. Persino i pezzi di terriccio si sono rivelati molto più pesanti di quanto pensassi.<new_line><new_line>Questa gente ha lavorato sodo, ma anch'io ho fatto un buon lavoro. Ora è tutto ammucchiato.]],
				['3'] = [["Ho sentito che i nuovi mutanti verdi si radunano in luoghi con molto terriccio e lo mangiano.<new_line>Quindi adesso il nostro cortile è come una mensa aperta per loro. È pericoloso lasciarlo così, ma siamo tutti stremati."]]
			},
			['choice'] = {
				['2'] = [["Che fretta c'è?"]],
				['1'] = [["Hai bisogno di aiuto?"]],
				['3'] = [[Chiama i contadini]]
			}
		},
		['construction_team_pests'] = {
			['episode'] = {
				['1'] = [[Questo è il posto che il capo ha segnato sulla mappa. Non devo cercare le loro tane, gli insetti sono già usciti fuori e si dirigono verso di me.]]
			}
		},
		['polar_star'] = {
			['episode'] = {
				['2'] = [[Mi chiamo Umka e so perché mi hai evocato. Ti aiuterò, ma sappi che la regina d'inverno è molto potente. Devi dimostrarmi di avere la forza necessaria per portela affrontare sentenzia l'orso. «Fammi sapere quando sei pronto.»]],
				['1'] = [[Alzo la sfera al cielo e questa inizia a brillare ancora più intensamente, riempiendosi della luce delle stelle. Poi esplode in un bagliore accecante. Quando i miei occhi tornano ad abituarsi all'oscurità, dinanzi a me c'è un grosso orso polare con la sfera tra le zampe.]],
				['3'] = [[Sei forte, ma avrai comunque bisogno di protezione contro le magie di ghiaccio della regina d'inverno. Procurati la piuma di un uccello di fuoco ed evocami di nuovo quando sarai pronto a combattere proclama l'orso prima di svanire. La sfera cade sulla neve con un tonfo silenzioso.]]
			}
		},
		['marauder_garage'] = {
			['episode'] = {
				['2'] = [[I predoni hanno preso la vodka e sono usciti dal garage di buon umore.]],
				['1'] = [[Le grida ubriache dei predoni risuonano dall'interno del garage della cooperativa. Questo è ovviamente il luogo in cui conservano gli oggetti di valore rubati. Non posso prendere nulla senza combattere.]]
			},
			['choice'] = {
				['2'] = [[Esamina i garage]],
				['1'] = [[Offri della vodka]]
			}
		},
		['lair_chiken'] = {
			['episode'] = {
				['2'] = [[Ho raggiunto la fattoria, ma ancora non c'era nessun segno di vita.]],
				['1'] = [[Sopravvissuta in parte al bombardamento, la fattoria da lontano appare piuttosto tranquilla. Se non fosse per quei bozzoli rosso sangue aggrovigliati intorno all'intera fattoria, non sarebbe un brutto posto in cui riposare.<new_line>A quanto pare, gli abitanti del posto hanno alzato i tacchi dopo aver percepito la presenza di una nuova vittima.]],
				['8'] = [[La mia distrazione ha funzionato e le creature hanno esitato. È la mia possibilità di scappare prima che il gallo canti ancora.]],
				['7'] = [[Nonostante il canto del gallo stesse continuando, ho deciso di ritornare in me e di togliere le mani dalle orecchie.<new_line>Non è cambiato niente: lo strillo sembrava provenire dalla mia testa. In preda alla confusione, ho voltato lo sguardo, solo per rendermi conto che un'orda di creature inferocite si stava dirigendo verso di me.]],
				['6'] = [[Mentre mi allontanavo dalla fattoria, ho sentito un assordante canto del gallo. Ho coperto le orecchie, ma non è servito a niente. L'urlo stava per perforarmi i timpani e sembrava non volersi fermare.]],
				['5'] = [[Cercando di non perdere di vista la fattoria, ho iniziato a fare dei passi indietro con cautela. Nessuno è uscito allo scoperto.]],
				['4'] = [[Dopo il combattimento, ho esplorato la fattoria da cima a fondo. I bozzoli di sangue sembravano essere il prodotto degli abitanti della fattoria, i quali attiravano gli sventurati viaggiatori per poi divorarli.<new_line>Forse queste creature non sono agili, ma sono estremamente scaltre. Perché, tra tutti i mutanti esistenti, proprio i polli abbiano un tale istinto da cacciatori rimane un mistero.]],
				['3'] = [[Subito dopo aver messo piede nel territorio della fattoria, sono sbucati da ogni angolo dei polli mutanti. Sarebbe stato anche divertente, se solo non fossero così aggressivi. Ormai, però, era troppo tardi per tornare indietro.]]
			},
			['choice'] = {
				['2'] = [[Togliti le mani dalle orecchie]],
				['1'] = [[Avvicinati]],
				['4'] = [[Fuggi]],
				['3'] = [[Spaventa]]
			}
		},
		['rabbit_teleport'] = {
			['episode'] = {
				['2'] = [[La voce si è acquietata per qualche secondo, per poi riprendere: <new_line>"C'è qualche città a cui possiamo accedere al momento. Quale sceglierai?"]],
				['1'] = [[Mentre mi facevo strada verso la tana, ho sentito una voce nella mia testa:<new_line>"Salute a te, avventuriero. Vedo che sei in viaggio."]],
				['4'] = [["Adesso salta."]],
				['3'] = [[La voce nella mia testa ha proseguito: <new_line>"Ottima scelta. Getta una delizia nella tana e poi saltaci dentro."]]
			},
			['choice'] = {
				['2'] = [["No. Forse più tardi." (Vattene)]],
				['1'] = [["Ok, dove posso andare?"]],
				['7'] = [[Salta nella tana]],
				['6'] = [["Non ne ho abbastanza." (Vattene)]],
				['5'] = [["Voglio scegliere un posto diverso."]],
				['4'] = [[Getta una delizia]],
				['3'] = [["Nessuna. Forse più tardi." (Vattene)]]
			}
		},
		['comrade_in_trouble_gas'] = {
			['episode'] = {
				['1'] = [[È stato allestito un accampamento accanto al veicolo. C'è un uomo che si aggira caricando e scaricando roba di ogni tipo.]]
			}
		},
		['boatman_camp_chat'] = {
			['episode'] = {
				['2'] = [["Zuppa?" Il barcaiolo ha sorriso. "Non c'è bisogno di fare tanto chiasso. Ho sentito di un istituto scientifico in cui, probabilmente, stavano lavorando a quel virus. Ma quell'area è pericolosa. Radiazioni, mutanti... persone. Non dovresti andare lì."]],
				['1'] = [[Il barcaiolo stava preparando della zuppa in un grande pentolone. Quando mi ha visto, mi ha chiesto:<new_line>"Ce l'hai fatta allora. Hai trovato ciò che stavi cercando a Belomorsk?"]],
				['3'] = [["Non vuoi proprio darmi tregua, eh! Te l'ho detto, è pericoloso e tu non sei pronto! Dovresti rimanere qui per un po' e affinare le tue abilità. Aiuta i locali e accetta qualche incarico al bar. Vendi qualcosa, parla con il capo. Poi, quando avrai finito, torna da me e ti dirò tutto."]]
			},
			['choice'] = {
				['2'] = [["Dov'è questo istituto?"]],
				['1'] = [["No, voglio sapere che è successo. Chi ha iniziato la guerra, chi c'è dietro a questa epidemia..."]],
				['3'] = [["Mi hai convinto. Alla prossima."]]
			}
		},
		['prologue_new_year'] = {
			['choice'] = {
				['2'] = [[È sufficiente addobbare un albero?]],
				['1'] = [[Cosa devo fare?]],
				['3'] = [[Da dove dovrei iniziare?]]
			},
			['episode'] = {
				['2'] = [[Qualcuno ha rapito Nonno Gelo. Sto cercando di salvarlo, ma non posso farcela da sola. Aiutami, ti prego! mi supplica la ragazza. Non credo alle sue parole, ma decido di stare al gioco.]],
				['1'] = [[In questi giorni, senza alcun preavviso, la temperatura è scesa repentinamente e la neve ha iniziato a ricoprire ogni cosa. La situazione ha preso una svolta ancora più inaspettata quando, per strada, mi sono imbattuto in una ragazza vestita da fanciulla di neve.]],
				['5'] = [[Ti ho stilato un lista di compiti. Portali a termine e vedrai che è tutto vero. Dopo che avrai ripristinato parte della magia, mi occuperò della fase successiva. Ti auguro buona fortuna. Grazie in anticipo per l'aiuto!]],
				['4'] = [[Non proprio. Occorrono addobbi speciali... magici. Appariranno ogni volta che commetterai una buona azione. Dovrai farne molte se vuoi addobbare tutto l'albero.]],
				['3'] = [[La scomparsa di Nonno Gelo ha fatto svanire tutta la magia festiva. Per ripristinarla, dobbiamo addobbare l'albero di capodanno.]]
			}
		},
		['embacity_biolab_kpp'] = {
			['episode'] = {
				['1'] = [[Nonostante il fatto che su queste radici ci siano pochi sporafiori amati dai biomutanti, vari di essi si aggirano intorno ai cancelli del checkpoint. È come se stessero facendo la guardia inconsciamente e istintivamente all'edificio del laboratorio centrale.]]
			}
		},
		['headless_horseman'] = {
			['episode'] = {
				['10'] = [[Il cavaliere non impiega molto a superarmi. „Che delusione“ borbotta prima di accelerare verso l'oscurità.]],
				['1'] = [[Le ombre si radunano. Alle mie spalle, sento il rombo di un motore e il nitrito di un cavallo: il cavaliere mi ha trovato.]],
				['11'] = [[Premo l'acceleratore nel tentativo di superare il cavaliere, ma questo lancia una bomba sotto le mie ruote e perdo il controllo. Sono costretto a fermarmi e a togliermi dalla strada. „Basta giochetti. Devi morire!“ urla lo spettro, direzionando la sua motocicletta verso di me.]],
				['2'] = [[Eccolo lì: nello specchietto retrovisore, scorgo una motocicletta avvolta da una strana luce verdastra e il teschio di un cavallo sulla parte frontale. Un attimo più tardi, un casco luminescente schizza di fianco a me e una voce metallica esclama in tono beffardo: „Vediamo di cosa sei capace!“, prima di tornare nel suo corpo.]],
				['9'] = [[Rallento e svolto con cautela, mentre il cavaliere mi raggiunge senza fatica e si mette al pari con me.]],
				['8'] = [[Realizzo che accelerare in curva è stata una pessima idea nel medesimo istante in cui perdo il controllo e finisco in un fossato. Alla vista della mia sfortuna, il cavaliere ride e svanisce nella notte. Temo che dovrò riprovare.]],
				['7'] = [[Quando il cavaliere si prepara a girare, decido di andargli addosso e sbalzarlo fuori dalla strada. Con mia sorpresa, lo vedo finire in un fossato. Mi avvicino al margine della strada per controllare, ma il cavaliere è già tornato in sella alla sua motocicletta. „Pagherai con la vita!“ urla furioso.]],
				['6'] = [[Di fronte a noi c'è un tornante.]],
				['5'] = [[Rallento fino a trovarmi fianco a fianco al cavaliere. „Batti già la fiacca?“ chiede una voce spettrale con una risata di scherno.]],
				['4'] = [[Premo l'acceleratore e mi lascio alle spalle il cavaliere.]],
				['3'] = [[La strada è rettilinea e non sembra avere irregolarità.]]
			},
			['choice'] = {
				['2'] = [[Osserva la strada]],
				['1'] = [[Guarda indietro]],
				['6'] = [[Torna in strada]],
				['5'] = [[Vai addosso al cavaliere]],
				['4'] = [[Rallenta]],
				['3'] = [[Accelera]]
			}
		},
		['stock_bandit'] = {
			['choice'] = {
				['2'] = [[Esci e nasconditi dietro a una cassa]],
				['1'] = [[Salutali con una rissa]],
				['7'] = [[Scappa dalla porta laterale]],
				['6'] = [[Aspetta]],
				['5'] = [[Butta di sotto il predone e affronta gli altri]],
				['4'] = [[Nasconditi e aspetta]],
				['3'] = [[Spingi la cassa addosso al predone]]
			},
			['episode'] = {
				['2'] = [[Sono entrati tre uomini armati. Uno si è seduto proprio sotto il mio nascondiglio e ha ordinato agli altri di perlustrare il deposito.]],
				['1'] = [[Stavo ispezionando il deposito, quando ho sentito delle voci avvicinarsi dall'esterno. Credo che avrò presto compagnia: devono essere predoni.]],
				['6'] = [[I predoni non hanno avuto tempo di reagire alla granata caduta dall'alto. Due sono morti sul colpo, e il terzo è scappato.]],
				['5'] = [[Poco dopo i predoni se ne sono andati, portando con sé qualsiasi cosa avesse valore.]],
				['4'] = [[Uno di loro è salito e ha cominciato a ispezionare la cassa, ma per ora non mi ha visto.]],
				['3'] = [[È bastata una spinta a far cadere giù la cassa. È atterrata sul predone, che è morto sul colpo, e si è frantumata in mille pezzi dall'urto. Ora devo occuparmi degli altri.]]
			}
		},
		['emba_isaev_intro'] = {
			['episode'] = {
				['2'] = [["Non possiamo approntare un laboratorio nel bel mezzo del nulla, quindi vogliamo stabilirci in questo insediamento. Ma la gente del posto non si fida degli scienziati."]],
				['1'] = [["Credo, compagno, che tu sia il nostro potenziale agente. Piacere di conoscerti, sono il dottor Victor Isaev. Purtroppo, non potrò darti tutti i dettagli finché non ci saremo occupati dei problemi principali."]],
				['12'] = [["Non sarà una ricerca semplice. Queste ricerche venivano svolte con il massimo riserbo all'intero delle stazioni biologiche nel territorio di città della scienza chiuse. Pertanto, nemmeno noi siamo a conoscenza dell'ubicazione di tutte le città della scienza."]],
				['11'] = [["No, il capo del progetto E.M.B.A. era uno dei più grandi scienziati della nostra epoca, il professor Mark Gerin. Grazie al suo lavoro, è stato possibile raggiungere questo avanzamento nella geoingegneria."]],
				['13'] = [["Dopo la catastrofe globale, il progetto E.M.B.A. è andato in rovina. La nostra spedizione intende rimediare."]],
				['14'] = [["Qualche tempo fa, Progresso ha inviato una spedizione in quest'area alla ricerca di un'altra tecnologia perduta. I suoi membri hanno accidentalmente scoperto una di queste città della scienza."]],
				['15'] = [["Al momento non siamo in grado di entrare in contatto con loro, quindi abbiamo chiesto aiuto al Navigatore di Vettore. Non sono interessato solo al progetto E.M.B.A., ma anche alla sorte dei miei colleghi."]],
				['16'] = [["Esatto. Secondo le nostre informazioni, questa città della scienza è stata completamente sopraffatta dalla muffa ed è abitata da forme di vita ostili. Ne hai già incontrate alcune di recente."]],
				['6'] = [["Ho segnato il luogo sulla tua mappa; il Navigatore della spedizione ti aspetterà lì. Ti aggiornerà lui."]],
				['5'] = [["Noi di Progresso siamo entusiasti delle tue aspirazioni e dei contributi futuri alla spedizione. Abbiamo un piano per risolvere il problema e tu potrai aiutarci."]],
				['4'] = [["No, ma posso comprendere la gente comune. Ora siamo qui appositamente per sistemare le cose. Ma, senza scorte, questa missione è destinata a fallire."]],
				['3'] = [["Ciò è dovuto alle ricerche condotte nelle città della scienza, tipici centri scientifici. Ce n'è una nelle vicinanze. Questi sviluppi hanno dato origine alle minacce biologiche e all'apparizione di nuovi tipi di mutanti. La gente dà la colpa a noi, gli scienziati."]],
				['19'] = [["Fammeli vedere. Uhm, ci aiuteranno molto nelle nostre ricerche. Portali subito al Biologo all'insediamento e aiutalo a condurre le sue ricerche."]],
				['18'] = [["È un'associazione di specialisti provenienti da campi scientifici all'avanguardia. Probabilmente l'ultima del suo genere ancora esistente... È per questo che a volte veniamo chiamati "archivisti": stiamo cercando di raccogliere e conservare frammenti di ciò che è rimasto.<new_line>Ma presto le nostre conoscenze e risorse saranno sufficienti per dare un'altra possibilità al mondo!"]],
				['17'] = [["Non sappiamo da dove vengano. Speriamo che tu possa aiutarci a scoprirlo."]],
				['7'] = [["La minaccia è stata eliminata. Il capo delle guardie ha acconsentito a ospitare il laboratorio nell'insediamento. Abbiamo un po' di tempo, posso rispondere alle tue domande."]],
				['8'] = [[Il dottore ha sospirato e si è tolto gli occhiali per pulirli, come se si stesse preparando a una lunga conversazione.<new_line>"Come probabilmente saprai, il mondo è in rovina. Progresso ha preparato una serie di misure per superare la crisi."]],
				['9'] = [["La prima fase consiste nel ripristino della biosfera. L'obiettivo della nostra spedizione è trovare una tecnologia per la terraformazione. All'alba della catastrofe globale, degli scienziati sono riusciti a crearla: si tratta del progetto E.M.B.A."]],
				['10'] = [["Il ceppo geneticamente modificato di micelio è una muffa in grado di assorbire gli effetti negativi dal terreno. Sotto la sua influenza, le terre desolate nucleari potrebbero diventare terreni fertili."]]
			},
			['choice'] = {
				['10'] = [["Dimmi il tuo piano."]],
				['1'] = [["Cos'è successo?"]],
				['12'] = [["Hai guidato tu il progetto?"]],
				['11'] = [["A che serve?"]],
				['17'] = [["Già. Cosa sono quelle creature?"]],
				['16'] = [["Allora vale la pena di iniziare da questa città della scienza."]],
				['15'] = [["Che fine ha fatto la prima spedizione?"]],
				['14'] = [["Cos'è successo al progetto?"]],
				['13'] = [["Quindi abbiamo bisogno di Gerin?"]],
				['9'] = [["Hanno ragione?"]],
				['8'] = [["Perché?"]],
				['7'] = [["Ho trovato dei documenti nel bunker."]],
				['6'] = [["Cos'è l'organizzazione Progresso?"]],
				['5'] = [["Cosa farete?"]],
				['4'] = [["Qual è lo scopo principale della spedizione?"]],
				['3'] = [["Come posso essere d'aiuto?"]]
			}
		},
		['emba_fight_easy2'] = {
			['episode'] = {
				['2'] = [[Non ci sono esseri umani da nessuna parte. Se sono sopravvissuti e intendono tornare, cadranno immediatamente nelle fauci dei biomutanti.<new_line>Probabilmente salverò delle vite se faccio subito a pezzi le bestie.]],
				['1'] = [[Anche se la fattoria non sembra vecchia e in rovina, è completamente ricoperta da questa nuova vegetazione mutata. E i biomutanti sono proprio qui, vagano e si nutrono del terriccio che vi cresce.]],
				['4'] = [[Un portello si è aperto nel terreno vicino alla casa. Il contadino è emerso, raggelato dalla paura.<new_line>Si è guardato intorno scioccato e si è avvicinato a me. Mi ha dato un sacchetto contenente qualcosa che probabilmente era cibo e alla fine è riuscito a mormorare una specie di grazie.]],
				['3'] = [[Pericolo rimosso. Ho trovato del cibo qui che il terriccio non ha ancora raggiunto.<new_line>Se i contadini torneranno davvero, non credo che gli dispiacerà se prendo questo piccolo compenso per la loro sicurezza.]]
			}
		},
		['emba_isaev_kiev'] = {
			['episode'] = {
				['2'] = [["Probabilmente alcuni degli scienziati superstiti hanno continuato a lavorare per qualche tempo. Per inerzia, diciamo. Questa non è l'unica ipotesi, quindi devi fare attenzione."]],
				['1'] = [["Abbiamo ottenuto nuovi dati dalla scatola nera. Di tanto in tanto è entrata in funzione anche dopo la grande guerra. Non può essersi accesa da sola, quindi qualcuno stava utilizzando le attrezzature del laboratorio."]],
				['9'] = [["I dati decodificati conducono al laboratorio dove sono state sviluppate le varietà "da combattimento" del micelio. Siamo diretti lì. Partiremo non appena sarai pronto."]],
				['8'] = [["Non ancora, purtroppo. Dobbiamo continuare le ricerche. Hai già visto le conseguenze dell'uso indiscriminato della tecnologia. Potrebbe peggiorare ulteriormente le cose."]],
				['7'] = [["Grazie all'aiuto che hai fornito nel migliorare il laboratorio del Biologo, stiamo facendo dei progressi significativi per quanto riguarda le mutazioni controllate del micelio!<new_line>A questo punto, abbiamo ottenuto una varietà simile a quella sviluppata per scopi militari."]],
				['6'] = [["Beh, a quanto pare l'operazione è andata bene, complimenti!"]],
				['5'] = [["Sono state sviluppate per scopi militari. Non ricordi? L'esercito ha collaborato con il progetto E.M.B.A. Era scritto su tutti i giornali."]],
				['4'] = [["Il Navigatore ti aiuterà di nuovo con le torrette del personale. Abbiamo decifrato il codice di accesso dello scomparto segreto del laboratorio. Eccolo, l'ho scritto qui."<new_line>(Ti porge un pezzo di carta con il codice)]],
				['3'] = [["Stiamo ancora ricevendo i dati scientifici, ma ci sono informazioni sulla città della scienza. Probabilmente incontrerai qualcosa di simile a quello dell'ultima volta."]]
			},
			['choice'] = {
				['2'] = [["Che cosa facciamo adesso?"]],
				['1'] = [["Chi può essere stato?"]],
				['8'] = [["Un altro Spaccaossa? Posso occuparmene io."]],
				['7'] = [["Quindi il micelio può aiutarci?"]],
				['6'] = [["Grazie. Come vanno le ricerche?"]],
				['5'] = [["Me ne sono dimenticato. Sono pronto a procedere."]],
				['4'] = [["Sono pronto a procedere."]],
				['3'] = [["Comunque, da dove vengono quelle creature?"]]
			}
		},
		['letter_of_hope'] = {
			['choice'] = {
				['2'] = [[Fai esplodere la cassetta]],
				['1'] = [[Forza la cassetta]],
				['8'] = [[Prendere una lettera]],
				['7'] = [[Fagli un complimento]],
				['6'] = [[Dagli una cartuccia]],
				['5'] = [[Nutri l'uccello]],
				['4'] = [[Fai esplodere il ghiaccio]],
				['3'] = [[Rompi il ghiaccio]]
			},
			['episode'] = {
				['2'] = [[A quanto pare, la lettera si trova in una bottiglia sul fondo di un fosso, sotto uno spesso strato di ghiaccio. Non ho idea di come sia finita laggiù, ma ci vorrà del tempo per recuperarla.]],
				['1'] = [[Per ironia della sorte, la lettera si trova in una cassetta postale. Il problema è che non esiste più alcuna casa. Di tutto l'edificio, è rimasta solo una parete, dalla quale spunta la cassetta. Devo trovare un modo per aprirla.]],
				['5'] = [[Raven gracchiò ad alta voce, lasciò cadere la lettera e sbatteva le ali.]],
				['4'] = [[Il corvo non sembra interessato.]],
				['3'] = [[Il corvo è appollaiato su un albero. Quando la sua testolina nera si volta per fissarmi, noto la lettera nel suo becco. Sono a corto di idee: non posso far altro che proporre uno scambio.]]
			}
		},
		['dracula2020'] = {
			['episode'] = {
				['2'] = [[Dracula viene polverizzato, mentre i colossi si fondono in un'unica massa informe di tentacoli e viticci. Sembra che l'Antico sia stato sconfitto e abbia deciso di arrendersi. Nel cielo si apre un varco gigantesco che inghiotte entrambi prima di implodere su sé stesso. Lo straordinario spettacolo si conclude quando un adorabile seme di zucca atterra sul palmo della mia mano aperta. Penso che lo riporterò all'oste.]],
				['1'] = [[Sono testimone di uno scontro tra titani. L'Antico e il dio delle zucche si lanciano in una battaglia all'ultimo sangue, senza alcuna intenzione di arrendersi. Un paio di ali nere mi distrae dalla scena e il Conte Dracula atterra di fronte a me: ]]
			}
		},
		['wood_delivery'] = {
			['choice'] = {
				['1'] = [[Posiziona della legna da ardere]]
			},
			['episode'] = {
				['2'] = [[Le persone accolgono la legna con gioia. Mi fanno posto vicino al fuoco e mi offrono da bere.]],
				['1'] = [[Svariate persone se ne stanno attorno a un barile d'olio arrugginito al cui interno scoppietta un fuoco. Sembra che abbiano quasi esaurito la legna. Potrei offrirmi di aiutarli.]]
			}
		},
		['frankenstein'] = {
			['episode'] = {
				['2'] = [[Non vedo alcuna porta... ah, no, eccola: è a terra, di fronte all'ingresso. L'interno della struttura odora fortemente di ozono e vi sono cavi attorcigliati ovunque. Li seguo, addentrandomi sempre più nell'edificio. I cavi mi conducono a una stanza: il mostro è sdraiato su un tavolino di metallo e un inquietante ometto gobbo si muove freneticamente attorno a lui. Non appena mi vede, lancia un urlo di terrore e aziona un interruttore sulla parete. Una scarica di elettricità colpisce il lettino e il mostro si sveglia.]],
				['1'] = [[Il gomitolo si arresta di colpo e capisco che il mostro è vicino. Una nube nera copre un edificio poco lontano e scarica fulmini su un parafulmine piegato sul tetto.]]
			}
		},
		['frozen'] = {
			['episode'] = {
				['1'] = [[Riesco ad avvicinarmi ai briganti mentre loro sono indaffarati a caricare blocchi di ghiaccio sulle loro slitte. Sotto le lastre gelate, riconosco le sagome dei prigionieri. Devo liberarli al più presto.]]
			}
		},
		['embacity_exogort'] = {
			['episode'] = {
				['2'] = [[I denti sulle pareti della fossa si agitano e un suono rimbombante emerge dal sottosuolo. Un attimo dopo, un oggetto vola fuori dalla buca!]],
				['1'] = [[Il percorso è bloccato da una fossa che somiglia alla bocca dentata di un mostruoso predatore. Sento un impulso irresistibile a gettare qualcosa in questo abisso senza fondo.]],
				['3'] = [[Ho sentito un brontolio soddisfatto provenire dalla fossa. Abbassando lo sguardo, ho visto che la bocca dentata era chiusa. A quanto pare, per il momento non ha bisogno di nutrirsi ulteriormente.]]
			},
			['choice'] = {
				['1'] = [[Lancia una carcassa nella fossa]]
			}
		},
		['portal'] = {
			['choice'] = {
				['2'] = [[Torna in superficie]],
				['1'] = [[Entra]],
				['4'] = [[Parla con il cacciatore]],
				['3'] = [[Prosegui verso le Tenebre]]
			},
			['episode'] = {
				['2'] = [[Non appena entro nella grotta, la nebbia si infittisce alle mie spalle, separandomi dal mondo esterno. Cosa dovrei fare?]],
				['1'] = [[Di fronte a me, si apre una grotta alquanto singolare. L'entrata è incorniciata da enormi blocchi di pietra e avvolta da una fitta nebbia.]],
				['3'] = [[Il cacciatore è seduto con gli occhi chiusi. Ha un'espressione accigliata e una sfera di cristallo tra le mani. Penso sarebbe meglio non disturbarlo.]]
			}
		},
		['newyearEvent_energy_hard'] = {
			['episode'] = {
				['2'] = [["Sì, ho un problema. A causa della scarsità di lavoro, la fabbrica sta passando un periodo difficile. Non riesco a trovare nessuno disposto a lavorare per il bene dei miracoli nel tuo mondo. I figli non hanno il permesso dei genitori, e nemmeno loro vogliono lavorare. Dicono di avere già molto a cui pensare con l'apocalisse alle porte."]],
				['1'] = [["Oh oh... Salve, mio caro amico... Ho bisogno di un certo oggetto. Per caso ce l'hai?"]],
				['12'] = [["Questi imbroglioni... questi imbr... oglioni... Non abbiamo bisogno di loro!"<new_line>Il contadino ha sbattuto il suo bicchiere sul tavolo.]],
				['11'] = [["Miracoli? Non sa nemmeno lanciare un incantesimo... Vuole solo che la gente lavori per lui gratuitamente."<new_line>Il contadino ha nuovamente riempito entrambi i bicchieri.]],
				['15'] = [["E va bene... Hai vinto... Domani... Andrò alla fabbrica."<new_line>Dopo aver detto queste parole, il contadino è finito nel mondo dei sogni, mentre io mi sono guadagnato un'altra vittoria.]],
				['14'] = [["Eh... Puoi dirlo forte, barbuto! Forza, un brindisi alle buone azioni!"<new_line>Il contadino si è asciugato una lacrima e ha riempito entrambi i bicchieri.]],
				['10'] = [["I metodi all'antica dello sporco capital... capitalismo. Dicono che questo sia il suo modus operandi, ed è per questo che nessuno vuole lavorare per lui."]],
				['13'] = [["Che genere di persona? Una persona buona? Beh... Direi che mi piacerebbe pensare di essere buono."]],
				['9'] = [["Sì, dimenticavo. Ho sentito che Babbo Naffare ha proposto una pessima offerta ai suoi impiegati, perciò nessuno vuole lavorare per lui."<new_line>Il contadino ha nuovamente riempito entrambi i bicchieri.]],
				['8'] = [["Allora, che cosa vuoi da me, barbuti?"]],
				['7'] = [[Il contadino ha riempito entrambi i bicchieri.<new_line>"Bevi."]],
				['6'] = [[Sulla soglia ha fatto la sua comparsa un volto familiare.<new_line>"Finalmente sei qui! Aspettavo questo momento! Mi hai umiliato e fatto sentire debole! Ma ora riuscirò a pareggiare i conti. Mi sono preparato per molto tempo, e adesso finalmente posso affrontarti!"<new_line>Il contadino ha preso il regalo, ha tirato fuori il contenuto e l'ha appoggiato sul tavolo.]],
				['5'] = [[Abbiamo raggiunto una fattoria isolata nella periferia del villaggio. Ricordavo a malapena questo posto, ma l'ultima volta era in condizioni nettamente migliori. Naffare mi ha augurato buona fortuna e se n'è andato.]],
				['4'] = [[Gli occhi di Babbo Naffare si sono riempiti di gioia.<new_line>"Oh oh oh! Così si fa! Forza, ti ci porto io."]],
				['3'] = [["È così e basta. Ma forse l'oggetto che hai potrà essere d'aiuto. È un piccolo regalo, ma devi essere tu a darlo. Puoi cercare di convincere la persona a cui farai visita a lavorare per me. Potrebbe essere la salvezza della fabbrica."]]
			},
			['choice'] = {
				['2'] = [["No, ma proverò a dare un'occhiata in giro." (Vattene)]],
				['1'] = [["Ecco, tieni. Qualcosa non va?"]],
				['11'] = [["Ecco! Tu... tu puoi essere buono... Non conta chi offre qualcosa, ma COSA viene offerto!"]],
				['10'] = [["Non ho nulla da ridire... Ma pensi... pensi di essere una persona buona?"]],
				['9'] = [["Ma sta portando i miracoli in tutto il mondo..."]],
				['8'] = [["Sei un contadino, ma ora è inverno. Vogliamo offrirti un lavoro."]],
				['7'] = [[Siediti]],
				['6'] = [[Bussa alla porta]],
				['5'] = [["Andiamo."]],
				['4'] = [["Ok, ci proverò."]],
				['3'] = [["Ha senso."]]
			}
		},
		['titanium_mine'] = {
			['episode'] = {
				['2'] = [[Sono stato fermato da un uomo con un'armatura pesante:<new_line>'Hai un permesso?']],
				['1'] = [[Vicino all'entrata della miniera di titanio c'era un punto di controllo fortificato. Dei mitragliatori erano puntati verso l'interno della miniera. A quanto pareva, la minaccia non veniva da un intruso esterno.]],
				['8'] = [[Correndo lungo le gallerie delle catacombe, ho trovato un ascensore e sono tornato al punto di controllo.]],
				['7'] = [[Ho continuato a correre lungo quelle catacombe alla ricerca di un'uscita. Poi, all'improvviso, un gruppo di mutanti mi ha attaccato.]],
				['6'] = [[Dopo aver eliminato i mutanti, ho continuato a correre lungo le gallerie finché non ho trovato un carro pieno di titanio.]],
				['5'] = [[Proseguendo, ho individuato un ascensore. Mentre scendevo ancora più in profondità e cercavo di orientarmi in quelle catacombe, mi sono imbattuto in un carro pieno di titanio.<new_line>]],
				['4'] = [[Non stiamo estraendo titanio solo per divertimento. È una risorsa preziosa. Se vuoi il tuo, compra un permesso a Magnitogorsk. Niente permesso, niente ingresso.']],
				['3'] = [[Proseguendo, ho individuato un ascensore. Mentre scendevo ancora più in profondità e cercavo di orientarmi in quelle catacombe, mi sono imbattuto in alcuni mutanti.]]
			},
			['choice'] = {
				['2'] = [[Dai il permesso e prosegui]],
				['1'] = [[Raggiungi l'ingresso]],
				['3'] = [[Che tipo di permesso?']]
			}
		},
		['newyearEvent_energy_easy'] = {
			['episode'] = {
				['2'] = [["Rilassati, ho solo bisogno di un batterista per la mia band. Per qualche ragione, è difficile trovare dei musicisti nel tuo mondo."]],
				['1'] = [["Come butta, choomba! Allora, sei pronto ad aiutarmi a ridurre in atomi questo mondo?"]],
				['12'] = [[Con tutta la forza che avevo in corpo, ho colpito la batteria facendola cadere a pezzi. Dopo che la folla ci ha ringraziati per l'ultima volta con un applauso, sul palco è sceso il sipario. Cyberbabbo si è avvicinato verso di me.<new_line><new_line>"Hai visto, choomba? E pensare che ti eri preoccupato!"]],
				['11'] = [[Gli altri componenti della band hanno smesso di suonare senza dare nell'occhio, mentre io mi sono lasciato coinvolgere dal ritmo e ho iniziato a suonare pieno di furia ed energia. Una volta finito, la folla ha esultato con un'energia mai vista prima, e Cyberbabbo ha concesso il bis.<new_line><new_line>"Agraaaaaaaaaaaaaaaaaaaaaaaaaaaaah!"]],
				['14'] = [[A poco a poco ho iniziato a riacquisire la mia lucidità mentale e a distinguere la sagoma di Cyberbabbo.<new_line>"In piedi, samurai. Sembra che tu ci abbia dato dentro fin troppo. Ma va bene così, ci siamo passati tutti quanti dopo uno spettacolo da paura. La prossima volta sarai il padrone della scena."]],
				['10'] = [["Aaaahaharrraharagaaaaaaaaaaaaaaaaaaah!"]],
				['13'] = [["È un miracolo di Natale. Grazie per aver compiuto questa buona azione. Abbiamo risollevato lo spirito delle persone, e ora per loro non sarà poi così miserabile dover vivere un altro giorno."]],
				['9'] = [["Oohgravahahaaa!"]],
				['8'] = [[La folla ruggiva ancora più forte.<new_line><new_line>"Agravaaaaaaaaaaaaaaah ahmragaaaaahhhhhhhhha!"]],
				['7'] = [[La folla ha iniziato a esultare, mentre Cyberbabbo continuava.<new_line>"Agravaaaaaaaaaaaaaaah haaaaaaarrrrrrrrrrr!"]],
				['6'] = [[Con la sua mano metallica, Cyberbabbo ha iniziato a suonare la chitarra e a emettere freneticamente dei lamenti.<new_line>"Aaaaah grrrakhah aaaaaaaaaaah, graaaym haaaaaaaaa!"]],
				['5'] = [[Cyberbabbo si è diretto verso il microfono per fare un annuncio:<new_line>"Oggi vorrei presentarvi un ospite speciale che farà baldoria insieme a noi. Fatemi un saluto al solo e unico, il preferito della vostra città... BABBO NATALE!!!"<new_line><new_line>La folla era in visibilio.]],
				['4'] = [["Non importa sapere come si suona. Ciò che importa è quanto sei fico mentre suoni. Colpisci quella batteria e dacci dentro!<new_line><new_line>Cyberbabbo si è avvicinato al microfono.]],
				['3'] = [["No, choomba, non abbiamo tempo per cercarne uno. Metti le chiappe su quello sgabello e prendi le bacchette."]]
			},
			['choice'] = {
				['2'] = [["Vuoi che ne trovi uno?"]],
				['1'] = [["Non ne sono sicuro."]],
				['12'] = [["Che cos'era? Non so suonare."]],
				['11'] = [[Rompi la batteria]],
				['14'] = [[Svieni]],
				['10'] = [[Assolo]],
				['13'] = [["Felice di aiutare." (Vattene)]],
				['9'] = [[Battito a raffica]],
				['8'] = [[Battito]],
				['7'] = [[Rimbalzo]],
				['6'] = [[Inizia a suonare la batteria]],
				['5'] = [[Saluta la folla]],
				['4'] = [[Siediti dietro la batteria]],
				['3'] = [["Ma non sono un musicista."]]
			}
		},
		['people_bonfire'] = {
			['choice'] = {
				['2'] = [[Indossa vestiti mimetici, avvicinati e origlia]],
				['1'] = [[Avvicinati]],
				['3'] = [[Offri sigarette]]
			},
			['episode'] = {
				['2'] = [[Gli estranei erano sorpresi di vedermi, ma hanno presto capito che non ero pericoloso, quindi mi hanno invitato ad unirmi a loro.]],
				['1'] = [[Stavo camminando per le terre desolate, quando ho visto del fumo sollevarsi nell'aria. Poco dopo ho avvistato un falò. Intorno al fuoco c'erano delle persone che stavano chiacchierando.]],
				['5'] = [[Mi sono mimetizzato e sono riuscito ad avvicinarmi e a origliare la loro conversazione. Stavano parlando del loro tesoro. È qui vicino e credo di poterlo trovare.]],
				['4'] = [[Gli estranei si sono accesi le sigarette e mi hanno raccontato delle storie divertenti dei loro viaggi. Ho imparato alcune cose nuove. Un paio d'ore più tardi, li ho salutati e ho proseguito per la mia strada.]],
				['3'] = [[Ci siamo scambiati notizie, ma dopo un po' non avevamo altro da dirci, quindi ho deciso di ripartire e li ho salutati.]]
			}
		},
		['newyearEvent_def_easy'] = {
			['episode'] = {
				['2'] = [[Dopo essermi concentrato, ho pensato a un miracolo di Natale con tutto me stesso, ma non è successo niente. Forse è perché io stesso sono il miracolo di Natale, dato che sono anche una specie di Babbo Natale. Devo pensare a qualcosa di più specifico.]],
				['1'] = [[Mentre mi avvicino ai festeggiamenti, vedo un denso pennacchio di fumo. I furfanti stanno rovinando la festa. Devo proteggere gli abitanti del villaggio. Tuttavia, se lo facessi da solo, li metterei in serio pericolo. Ho bisogno di aiuto.]],
				['7'] = [[Davanti ai miei occhi si è materializzato un portale da cui è uscito Babbo Stregone.<new_line>"Ancora tu? Inutile idiota. Non riesci a gestire da solo nemmeno questa faccenda? E va bene, ti aiuterò, ma facciamo in fretta."]],
				['6'] = [[Davanti ai miei occhi si è materializzato un portale da cui è uscito Cyberbabbo.<new_line>"Come butta, choomba! Che cosa abbiamo qui? Un altro caso di cyberpsicosi? Facciamogli vedere i sorci verdi!"]],
				['5'] = [[Davanti ai miei occhi si è materializzato un portale da cui è uscito Orso.<new_line>"La gente ha bisogno di aiuto. Forza, compagno! In onore della solidarietà del proletariato!"]],
				['4'] = [[Davanti ai miei occhi si è materializzato un portale da cui è saltato fuori Naclava.<new_line>"Eh aaah!" ha urlato, e con queste parole si è lanciato nella battaglia. Ho capito di dover stare al suo passo.]],
				['3'] = [[Dietro di me, ho sentito il rombo di un motore. A quanto pare si trattava di un'auto d'epoca piccola e rossa. Dopo essersi fermata, ho visto scendere Naffare.<new_line>"Oh oh oh! Ancora tu, mio caro amico? Forza, alla gente serve aiuto. Non c'è tempo da perdere."]]
			},
			['choice'] = {
				['2'] = [[Babbo Naffare]],
				['1'] = [[Chiama aiuto]],
				['5'] = [[Cyberbabbo]],
				['4'] = [[Orso Natale]],
				['3'] = [[Babbo Naclava]]
			}
		},
		['impostor3'] = {
			['episode'] = {
				['1'] = [[La gente era felice di vedermi ritornare con i loro effetti personali.]]
			},
			['choice'] = {
				['1'] = [[Dare via le cose]]
			}
		},
		['forest_feeder'] = {
			['choice'] = {
				['1'] = [[Riempi la mangiatoia]]
			},
			['episode'] = {
				['1'] = [[Appesa a un albero c'è una mangiatoia di legno a forma di casetta. Diversi scoiattoli e uccelli mi fissano con interesse e attendono di poter placare la loro fame.]]
			}
		},
		['camp_defence_quest'] = {
			['episode'] = {
				['2'] = [[Mentre mi avvicinavo all'avamposto, ho sentito il rumore di alcuni spari. Non c'era alcun dubbio: i banditi avevano già attaccato l'accampamento.]],
				['1'] = [[Nell'avamposto c'è un uomo armato:<new_line>'Sei qui per aiutare? Ok, mettiti in posizione. Stanno già arrivando.']],
				['12'] = [[Non ho potuto aiutarli, ma li vendicherò.]],
				['11'] = [[I banditi se ne sono andati. Ispezionando l'accampamento ho trovato solo i cadaveri dei nostri alleati.]],
				['10'] = [[Ho sentito un rumore e mi sono nascosto. Degli sconosciuti stavano vagando per l'accampamento.<new_line>Era troppo tardi. I banditi lo avevano già conquistato.]],
				['13'] = [[Stando alla mappa, mi sto avvicinando all'accampamento.]],
				['9'] = [[Tutto sommato, non è andata così male. Si rimetteranno presto, è solo questione di tempo.]],
				['8'] = [[Abbiamo mantenuto la posizione, ma alcuni dei nostri sono stati feriti gravemente.]],
				['7'] = [[Abbiamo tutto sotto controllo. Abbiamo mantenuto la posizione. Nessuno ci darà ancora fastidio.]],
				['6'] = [[Ci sono dei banditi. Molti banditi...<new_line>Ma anche noi siamo in tanti. E siamo pronti!]],
				['5'] = [[Ho notato il posto perfetto per una trappola per lupi accanto all'accampamento e ne ho scavata una rapidamente.]],
				['4'] = [[Ho appeso con successo un tronco con dei paletti.]],
				['3'] = [[Al punto di incontro, ho trovato solo un cadavere.]]
			},
			['choice'] = {
				['10'] = [[Difendi l'accampamento]],
				['9'] = [[Mettiti in posizione e aspetta]],
				['8'] = [[Scopri che altro c'è da fare]],
				['7'] = [[Scava una trappola per lupi]],
				['18'] = [[Aspetta che i banditi se ne vadano]],
				['17'] = [[Vendica i compagni caduti]],
				['16'] = [[Esamina il cadavere]],
				['14'] = [[Unisciti al combattimento]]
			}
		},
		['construction_team'] = {
			['episode'] = {
				['32'] = [["Presto la costruzione sarà completata e vi apposteranno dei soldati. Dobbiamo scoprire cosa sta succedendo nei dintorni. In questi due luoghi i nostri ragazzi hanno notato delle attività sospette. Vai a perlustrarli. Se non hai tempo, aiutaci qui con il lavoro. Troverò qualcun altro da mandare."]],
				['22'] = [[Il disboscamento procede a rilento. Gli operai sono stanchi e hanno bisogno del mio aiuto.]],
				['12'] = [[Avevamo appena sgomberato la zona quando è arrivato un convoglio di trattori con rimorchio. Il capo mi ha dato una pacca sulla spalla:<new_line><new_line>"Bene, siamo di nuovo in linea coi tempi, grazie dell'aiuto. Ecco la ricompensa per il tuo lavoro... e un piccolo extra per averci aiutato a risolvere il problema."]],
				['20'] = [["Niente di grave: siamo a corto di sigarette. Io però non posso stare senza qualcosa da fumare. Se ne hai qualcuna da offrire, mi salverai la vita."]],
				['24'] = [["Ci hai salvato la vita!"]],
				['14'] = [[Ai cancelli del futuro avamposto, mi è comparso davanti un camion da cui è saltato fuori un uomo:<new_line>"Wow, rinforzi, grandioso! Sono il responsabile della squadra di costruzione. C'è un sacco di lavoro per tutti, sei pronto a cominciare?"]],
				['26'] = [[Nell'area già disboscata, non si può fare un passo senza inciampare su un ceppo o cadere in una buca. La squadra vuole installare delle capanne residenziali e stanno aspettando il mio aiuto per sgomberare la zona.]],
				['16'] = [[Il capo ha ispezionato il lavoro e mi ha chiamato:<new_line><new_line>"Eccellente, andrà benone. Prenditi una pausa. Vorrei parlarti di un nuovo lavoro in un'altra zona... Se hai tempo, naturalmente."]],
				['28'] = [["I banditi locali stanno rubando i nostri attrezzi e aggredendo gli operai. Sei un uomo d'azione, vai a risolvere il problema. Se non hai tempo, aiutaci qui con il lavoro. Poi risolveremo la questione tutti insieme."]],
				['2'] = [["Abbiamo iniziato a costruire un magazzino. Dai una mano ai ragazzi, dobbiamo finire il tetto."]],
				['1'] = [[Un uomo mi si è avvicinato da dietro un mucchio di materiali da costruzione:<new_line><new_line>"Salve, io sono il capo. Qui costruiremo la fattoria comune: abbiamo trovato un piccolo appezzamento di terra incontaminata. Sei qui per darci una mano?"]],
				['11'] = [["Ora abbiamo un po' di spazio per posizionare delle capanne e un fienile. Per adesso stiamo facendo 10 km al giorno avanti e indietro dal vecchio cantiere.<new_line><new_line>Aiutaci a livellare la zona, a breve i trattori porteranno le capanne."]],
				['13'] = [[Avevamo appena sgomberato la zona quando è arrivato un convoglio di trattori con rimorchio. Il capo mi ha dato una pacca sulla spalla:<new_line><new_line>"Bene, siamo di nuovo in linea coi tempi, grazie dell'aiuto. Ecco la ricompensa per il tuo lavoro."]],
				['23'] = [[Il camion è fermo con un carico pieno. Senza il mio aiuto, gli operai non troverebbero mai il tempo di occuparsene.]],
				['15'] = [["Hai visto dove hanno portato le lamine e le tavole di acciaio? Aiutaci a scaricarle, ci costruiremo una torre."]],
				['25'] = [[Hanno deciso di occupare il terreno vergine di un luogo non privo di pericoli. Devo aiutarli a costruire un'adeguata recinzione protettiva.]],
				['6'] = [[La futura fattoria comune è ben protetta. Il capo ispeziona i risultati:<new_line><new_line>"E così i ragazzi sanno lavorare quando c'è una persona esperta che li tiene d'occhio! Ecco la ricompensa per il tuo lavoro, e un piccolo extra per averci aiutato a risolvere il problema."]],
				['5'] = [[Il capo ispeziona i confini del campo ripulito:<new_line>"Il terreno è buono, ma è un luogo selvaggio. Dobbiamo costruire il recinto con buonsenso. Meglio che tu dia una mano ai ragazzi... Hanno sentito parlare di mutanti solo nei racconti."]],
				['29'] = [["Dovevano portarci altre scorte, ma il camion ancora non si vede. Forse è rimasto bloccato da qualche parte fuori strada. Vai a recuperare quel carico, non è troppo grosso."]],
				['3'] = [[Il capo ha ispezionato il lavoro e mi ha chiamato:<new_line><new_line>"Grazie, ci avremmo messo una vita senza di te. È ora di una pausa sigaretta. Mi piacerebbe parlarti di un nuovo lavoro in un'altra zona... Se hai tempo, naturalmente."]],
				['33'] = [[La squadra sta facendo una pausa sigaretta, ma non hanno niente da fumare.]],
				['10'] = [[Il capo ha ispezionato il lavoro e mi ha chiamato:<new_line><new_line>"Se solo mi mandassero sempre gente come te! Prenditi una pausa. Vorrei parlarti di un nuovo lavoro in un'altra zona... Se hai tempo, naturalmente."]],
				['9'] = [["Per prima cosa, dobbiamo disboscare quella zona laggiù. Hai capito quale? Ehi, Vasilich! Dai la tua motosega a questo ragazzo, ti darà il cambio."]],
				['8'] = [[Nella zona di disboscamento, ho incontrato un uomo in tuta da lavoro:<new_line>"Ciao, sono il capo della zona! Ti hanno mandato giusto in tempo. Ci siamo imbattuti in un'aria complicata e siamo indietro con il lavoro. Sei pronto a metterti all'opera?"]],
				['7'] = [[La futura fattoria comune è ben protetta. Il capo ispeziona i risultati:<new_line><new_line>"E così i ragazzi sanno lavorare quando c'è una persona esperta che li tiene d'occhio! Ecco la ricompensa per il tuo lavoro."]],
				['17'] = [[Il capo ci mostra dei fogli ricoperti di linee:<new_line>"Bene, questi sono i progetti che ci hanno mandato. Costruiremo una torre. I nostri uomini dovranno difenderla, perciò vediamo di dare il massimo."]],
				['18'] = [[Abbiamo costruito una torre di vedetta, piccola ma robusta. Il capo l'ha ispezionata di persona, poi si è voltato verso di me:<new_line><new_line>"Grazie del lavoro, e salutami i ragazzi dell'insediamento. Ecco, questa è la tua ricompensa... e un piccolo extra per averci aiutato a risolvere il problema."]],
				['19'] = [[Abbiamo costruito una torre di vedetta, piccola ma robusta. Il capo l'ha ispezionata di persona, poi si è voltato verso di me:<new_line><new_line>"Grazie del lavoro, e salutami i ragazzi dell'insediamento. Ecco, questa è la tua ricompensa."]],
				['21'] = [[Gli operai stanno ancora trafficando sul tetto del magazzino. È ora di unirsi alla causa comune.]],
				['27'] = [[Gli operai hanno già iniziato a costruire la torre e stanno tutti aspettando che io mi unisca a loro.]],
				['4'] = [[Il capo ha ispezionato il lavoro e mi ha chiamato:<new_line><new_line>"Grazie, ci avremmo messo una vita senza di te. È ora di una pausa sigaretta. Vieni, ti mostro il prossimo progetto del piano di costruzione."]],
				['30'] = [["Abbiamo già testato il terreno con alcune coltivazioni, ma gli insetti sono saltati fuori rovinando tutto. Sei un uomo d'azione, vai a risolvere il problema. Se non hai tempo, aiutaci qui con il lavoro. Poi risolveremo la questione tutti insieme."]],
				['31'] = [["Nella foresta sono comparsi alcuni mutanti. Ne abbiamo un branco addosso, ma le sentinelle non riescono a tenerlo a bada. Sei un uomo d'azione, vai a risolvere il problema. Se non hai tempo, aiutaci qui con il lavoro. Poi risolveremo la questione tutti insieme."]]
			},
			['choice'] = {
				['2'] = [[Cerca le sigarette]],
				['1'] = [["Davvero, che tipo di lavoro?"]],
				['6'] = [[Rispondi alla richiesta del capo]],
				['5'] = [[Mettiti all'opera]],
				['4'] = [[Consegna le sigarette]],
				['3'] = [["Di che lavoro si tratta?"]]
			}
		},
		['newyearEvent_fight_easy'] = {
			['episode'] = {
				['2'] = [[Mmh, il blocco di ghiaccio può essere attaccato da molte direzioni. Devo decidere quale sia l'opzione migliore.]],
				['1'] = [[A debita distanza dal resto della civiltà c'è un blocco di ghiaccio che funge da base temporanea dei furfanti. Questo è il luogo di origine delle loro incursioni, e anche gli oggetti che rubano passano da qui.<new_line>Se riuscissi a scacciare gli abitanti del blocco di ghiaccio, di certo diventerebbe più sicuro vivere negli insediamenti circostanti. Inoltre, la gente potrebbe finalmente festeggiare il Natale in pace.]]
			},
			['choice'] = {
				['2'] = [[Cancello principale]],
				['1'] = [[Avvicinati al blocco di ghiaccio]],
				['4'] = [[Ingresso sul retro]],
				['3'] = [[Garage]]
			}
		},
		['gigahive_bar'] = {
			['episode'] = {
				['2'] = [[È così che si proteggono dagli estranei. Abbiamo anche creato un antidoto per questa roba.<new_line>Vuoi davvero andarci? Beh, se riesci a uccidere la regina, portami la sua testa così potrò ricompensarti come si deve.'<new_line>]],
				['1'] = [[Questo è un posto estremamente pericoloso: è la casa dell'ape regina. Molti hanno cercato di ucciderla, ma nessuno è uscito vivo dall'alveare. Che possano riposare in pace.']],
				['5'] = [["Amico mio, puzzi di api! Hai davvero abbattuto la regina in persona?! Non ci crederei mai. Vieni quaggiù."]],
				['4'] = [[Non riesco a credere che tu abbia ucciso l'ape regina! Ora sei un eroe locale! Appenderò questo trofeo proprio qui nel bar! Ti avevo promesso una ricompensa fantastica, no? Ecco, prendi questo.']],
				['3'] = [[Al momento abbiamo esaurito le scorte, ma se ci porti un po' di cera per il nostro specialista, potrà preparartene una dose. Ci sono nidi di api madre nelle vicinanze di Ufa, puoi prendere lì la cera.']]
			},
			['choice'] = {
				['2'] = [[Ma dove posso prendere l'antidoto?']],
				['1'] = [[Parla delle pustole]],
				['3'] = [[Annuisci e vai via]]
			}
		},
		['dracula'] = {
			['episode'] = {
				['2'] = [[Esco dal portale e mi ritrovo nel bel mezzo di un campo di battaglia. Esseri umani, cacciatori e altre creature serpeggiano tra le tende illuminate dalle torce. Il cacciatore che ho seguito è vicino a me. Una persona con indosso il suo stesso completo è appena corsa da lui per discutere di qualcosa.]],
				['1'] = [[Il cacciatore annuisce e si dirige verso l'uscita del portale. Bisbiglia qualcosa e getta della strana polvere nella nebbia, che diventa rosso sangue e inizia ad agitarsi freneticamente. Il cacciatore sembra soddisfatto. Mi invita a seguirlo con un cenno della mano, prima di inoltrarsi nella nebbia.]],
				['8'] = [[„La caccia si è conclusa... per quest'anno, almeno. Mi auguro di vederti anche il prossimo“ mi dice il cacciatore. Alle sue spalle, i suoi compagni stanno calando il corpo del conte in una bara d'argento. „In segno di gratitudine, voglio donarti la mia balestra. Spero che tu ne faccia buon uso“ aggiunge, consegnandomi la sua arma. „Ti rimando nel tuo mondo, ma ricorda: ben presto i portali si richiuderanno e torneranno ad apparire solo l'anno prossimo. Buona fortuna in superficie!“.]],
				['7'] = [[Entriamo nella sala e vediamo il conte in piedi di fronte al trono; il suo volto non lascia trasparire alcuna emozione. Senza proferire parola, il cacciatore solleva la balestra e spara alla testa del vampiro. Con un scatto fulmineo, Dracula afferra la freccia a pochi centimetri dal suo naso. „Che scortesia“ dichiara con un sorriso, „Voi gentiluomini dovreste ripassare le buone maniere“. Senza produrre il minimo rumore, il vampiro si lancia verso di noi.]],
				['6'] = [[Nel castello incontriamo parecchia resistenza, ma riusciamo ad avere la meglio sui mostri che occupano ciascun piano. L'ultimo ostacolo è un'orda di creature che sta di guardia di fronte alla sala del trono.]],
				['5'] = [[Il castello è in uno stato pietoso e ha più l'aspetto di un cumulo di rovine che di un palazzo leggendario. Ho idea che la situazione fosse già disperata ben prima dell'esplosione che ha distrutto portone e barricate. Senza attendere oltre, l'esercito irrompe attraverso la breccia nel muro per lottare contro i mostri. Io e il cacciatore seguiamo a ruota.]],
				['4'] = [[„È molto semplice: io e te ci faremo strada verso i piani superiori per stanare il vampiro, mentre gli altri si occuperanno dei mostri che occupano il castello. Seguimi, stiamo per iniziare“ dice il cacciatore, voltandosi verso l'edificio che torreggia innanzi a noi.]],
				['3'] = [[„Maestro Helsing! Abbiamo cessato il fuoco, come ha ordinato. È tutto pronto per l'assalto al castello“ lo informa il cacciatore sconosciuto. „Splendido! Fate saltare il portone e le barricate. Faremo irruzione come pianificato“ risponde il mio amico, mentre realizzo che la sua posizione tra i cacciatori deve essere alquanto elevata.]]
			},
			['choice'] = {
				['2'] = [[Tendi le orecchie]],
				['1'] = [[Seguilo]],
				['5'] = [[Prendi la balestra]],
				['4'] = [[Sali le scale]],
				['3'] = [[Pianificato, hai detto? Qual è il piano?]]
			}
		},
		['cold_footprints'] = {
			['choice'] = {
				['1'] = [[Che sta succedendo?]]
			},
			['episode'] = {
				['2'] = [[Quando penso di essermi disfato dei briganti di ghiaccio, dalla tenda centrale ne emerge un altro. È alto e brandisce una grossa ascia: il loro leader, suppongo. È ora di attaccare.]],
				['1'] = [[Una grossa tenda rivestita di pelli di animali ghiacciate e altre tende, più piccole, sparse qua e là, circondate da un'alta fortificazione di neve. Così si presenta l'accampamento dei briganti di ghiaccio. Quei farabutti non impiegano molto a notare la mia presenza, e mi preparo a combattere.]],
				['5'] = [[Li hai uccisi tutti, non è così? chiede la donna, lanciandomi un'occhiata gelida attraverso lo specchio. «Voi umani siete bravi in questo. Ma non importa. Presto arriverà un inverno senza fine che placherà ogni sofferenza». A queste parole, colpisce il pavimento con il suo bastone e lo specchio si rompe, interrompendo l'immagine.]],
				['4'] = [[Allo specchio appare l'immagine di una donna alta e candida come la neve, con in mano un bastone. È seduta su un trono di ghiaccio e, accanto a lei, vedo un vecchio con la barba canuta in catene. È Nonno Gelo!]],
				['3'] = [[Entrando nella tenda del leader, la mia attenzione viene immediatamente catturata da un grosso specchio di ghiaccio piazzato al centro. Come in trance, mi avvicino e passo una mano sulla sua superficie liscia.]]
			}
		},
		['embacity_biolab_5'] = {
			['episode'] = {
				['2'] = [["Sì... Proprio come pensavo. Non hai nemmeno provato a comprendere la nuova realtà. Non hai sentito la voce. Nemmeno Kurganov, e lui era disposto a fare molto."]],
				['1'] = [["Bene, bene, bene, cosa abbiamo qui? Fantastico, è arrivato un altro soggetto di ricerca. Unisciti ai miei colleghi, sono sicuro che sei altrettanto incredibilmente stupido e non riesci a vedere l'ovvio."]],
				['4'] = [[Mi sono aggirato nella stanza e ho trovato una porta poco appariscente chiusa da una serratura a codice. Immagino che il codice serva per aprirla.]],
				['3'] = [["Un esercito? No, queste sciocchezze militari rimarranno nel passato. Sembra che tu non sia in grado di comprendere il futuro. Va bene così, sii paziente adesso e ti sistemerò. Cerca di non opporre resistenza, se ci riesci."]]
			},
			['choice'] = {
				['2'] = [["Anche tu stai radunando un esercito?"]],
				['1'] = [["Che cos'hai fatto?!"]]
			}
		},
		['mummy'] = {
			['choice'] = {
				['2'] = [[Percorri il corridoio]],
				['1'] = [[Seguilo]],
				['8'] = [[Torna indietro]],
				['7'] = [[Apri il baule]],
				['6'] = [[Continua a osservare]],
				['5'] = [[Svolta a destra]],
				['4'] = [[Svolta a sinistra]],
				['3'] = [[Vai dritto]]
			},
			['episode'] = {
				['10'] = [[Sono a un passo dal baule, quando il coperchio si apre quel tanto che basta per mettere in mostra una fila di denti aguzzi: „Giù le mani!“ esclama una voce roca dall'interno del baule. Io sobbalzo, tirando indietro la mano con uno scatto deciso. „È la tua prima volta? Mi porti un ratto?“ chiede la voce.]],
				['1'] = [[Raggiungo la tana con le orecchie ben tese. A una decina di metri da me, vedo una figura con indosso una tunica nera e una maschera con un becco adunco. Gli squittii disperati che riecheggiano tra le pareti della grotta mi suggeriscono che quel tale ha appena catturato un ratto.]],
				['12'] = [[„Allora vattene e non tornare indietro“ borbotta il baule prima di chiudersi.]],
				['11'] = [[„Bene, portalo qui“ dice il baule, aprendo la sua morsa letale. Senza nemmeno guardare dentro, getto la carcassa del ratto all'interno. Il coperchio si chiude di colpo e osservo il baule masticare rumorosamente, prima di sputare alcuni sacchetti di dolciumi verso di me. „Ora sparisci“.]],
				['2'] = [[La figura chiude il ratto in una gabbietta e lo conduce senza fretta in una casa vicina. Attendo un minuto prima di entrare a mia volta e la scena che mi attende mi lascia senza fiato: di fronte a me si estende un lungo corridoio illuminato da torce appese alle pareti di pietra. Un forte olezzo di muffa invade le mie narici, ma percepisco distintamente anche un piacevole aroma di erbe appena colte.]],
				['9'] = [[Un istante più tardi, raggiungo una stanza piccola con il tetto a volta e un baule al centro.]],
				['8'] = [[Torno al bivio e decido dove altro andare.]],
				['7'] = [[Dall'altra parte dell'arcata, una gradinata scende verso una stanza minuscola con file e file di ratti in gabbia. Decine di occhietti rossi sono puntati su di me. Capendo di non aver altra via d'uscita, mi volto, ma calpesto una placca di pietra: un secondo più tardi, sento le gabbiette aprirsi di scatto e lo squittio di centinaia di roditori affamati.]],
				['6'] = [[Mi avvicino piano per osservare meglio, ma la mummia si accorge della mia presenza. Il suo collo produce un rumore secco nel momento in cui scatta verso di me e la sento mormorare un'antica maledizione egizia.]],
				['5'] = [[Sul blocco di pietra è adagiata una mummia avvolta nelle bende. Il medico di prima se ne sta in un angolo a borbottare, mentre è intento a mescolare un liquido verde e viscoso in un calderone arrugginito. L'aria è impregnata di un distinto odore di ortica e piantaggine.]],
				['4'] = [[L'arcata centrale conduce a un labirinto di corridoi; sono costretto a utilizzare l'aroma delle erbe per orientarmi. L'olfatto non mi delude: dopo qualche minuto, raggiungo una stanza ampia con, al centro, un blocco di pietra.]],
				['3'] = [[Proseguo a camminare per alcuni minuti, fino a quando mi trovo di fronte a un bivio. Vi sono tre arcate e conducono tutte verso l'oscurità. Vado dritto, svolto a sinistra o svolto a destra?]]
			}
		},
		['supply_quest'] = {
			['episode'] = {
				['2'] = [[L'uomo mi ha guardato con sospetto e poi ha dato una lunga occhiata nella scatola.<new_line>'Non ti pagherò l'intera somma per questo.']],
				['1'] = [[L'uomo ha lanciato un'occhiata nella scatola.<new_line>'È un piacere fare affari con te.']],
				['5'] = [[Hai preso tutto ciò che ti serviva?']],
				['4'] = [[L'uomo mi ha congedato con una smorfia sdegnata:<new_line>'Tieniti quella spazzatura e vattene da qui.']],
				['3'] = [[Un vero spilorcio, eh! Sei fortunato che oggi mi sento generoso.']]
			},
			['choice'] = {
				['2'] = [[Prendi la ricompensa parziale]],
				['5'] = [[Tieni l'ordine e vattene]],
				['6'] = [[Dai la cassa]],
				['3'] = [[Chiedi l'intera ricompensa]]
			}
		},
		['undertaker'] = {
			['episode'] = {
				['1'] = [[Al cimitero, un cacciatore stanco mi ha incontrato con una pala in mano. Fece un cenno in un posto vicino alle tombe aperte dove posso mettere le bare.]]
			},
			['choice'] = {
				['1'] = [[Dare le bare]]
			}
		},
		['lost_soul'] = {
			['choice'] = {
				['2'] = [[Segui il rumore]],
				['1'] = [[Segui le tracce]],
				['8'] = [[Corri ad aiutarlo]],
				['7'] = [[Gettagli una fune]],
				['6'] = [[Corri verso la collina]],
				['5'] = [[Corri verso il lago]],
				['4'] = [[Corri nel bosco]],
				['3'] = [[Lembo di tessuto]]
			},
			['episode'] = {
				['2'] = [[Le tracce fanno il giro degli alberi e mi riportano all'accampamento.]],
				['1'] = [[Ho trovato l'accampamento. La tenda si trova sulla neve e il fuoco è spento, ma le ceneri sono ancora calde. Si direbbe che l'uomo se ne sia andato da poco. Improvvisamente, odo starnazzare alla mia sinistra, ma le tracce dalla tenda conducono a destra. Devo decidere da che parte andare.]],
				['12'] = [[Raggiungo la sommità della collina, ma non vedo nulla di sospetto. Forse non si è trattato di uno sparo, ma del rumore di un ramo che si spezza.]],
				['11'] = [[Al centro del lago vedo l'uomo che cerco finire in una grossa spaccatura nel ghiaccio. Devo aiutarlo!]],
				['10'] = [[Mi faccio strada tra gli arbusti mentre i miei vestiti continuano a impigliarsi alle spine, strappandomi alcuni lembi di tessuto. Superati i cespugli, mi ritrovo di nuovo nell'accampamento.]],
				['13'] = [[Avvisto l'uomo scomparso in cima a un albero, circondato da un branco di lupi famelici. Un attimo dopo, gli animali fiutano la mia presenza.]],
				['9'] = [[Nel lago non c'è nulla. Il rumore è stato provocato dalla lastra di ghiaccio che spinge contro le sponde. Sarà meglio che mi sbrighi a tornare alla radura.]],
				['8'] = [[L'ululato che mi è parso di sentire era soltanto il vento. Sarà meglio che mi sbrighi a tornare alla radura.]],
				['7'] = [[Dopo averlo salvato, torniamo all'accampamento per scaldarci attorno al fuoco. L'uomo mi ringrazia, poi ognuno va per la propria strada.]],
				['6'] = [[Raggiungo la sommità della collina e scorgo l'uomo sparare a un orso che zigzaga tra gli alberi cercando di raggiungerlo. Devo fare qualcosa!]],
				['5'] = [[La fonte del rumore sembra essere un uccello appollaiato su un albero. Mi lancia un'occhiata e torna a starnazzare. Devo tornare all'accampamento.]],
				['4'] = [[Il sentiero conduce a una radura, dove sento tre distinti rumori attorno a me. Il rumore secco del ghiaccio che si spacca dalla parte del lago, un ululato lupesco dalle profondità del bosco e una sorta di sparo dalla collina. Dove dovrei andare?]],
				['3'] = [[Il mio intuito mi suggerisce di essere vicino al mio obiettivo. Il sentiero dinanzi a me si divide di nuovo: le tracce conducono da una parte, ma dall'altra c'è un lembo di tessuto impigliato a un arbusto irto di spine. Dove sarà andato l'uomo scomparso?]]
			}
		},
		['santa_monopolist'] = {
			['choice'] = {
				['2'] = [["Che sta succedendo?"]],
				['1'] = [["Non lo so. Chi sei?"]],
				['3'] = [["Va bene, mettiamoci al lavoro."]]
			},
			['episode'] = {
				['2'] = [["Io lavoro... altrove. Sono come il tuo Babbo Natale, ma diverso. Riparliamone più tardi, però. Adesso devo sapere che fine ha fatto il mio collega."]],
				['1'] = [["Oh oh oh, giovanotto, che cosa ci fai qui? Dov'è il tuo Babbo Natale?"]],
				['4'] = [["Eccellente. Il tuo Babbo Natale dovrebbe avere le risposte. Andiamo dove di solito riceve i suoi soci in affari."]],
				['3'] = [["Uff, il tempo è denaro... E va bene. Per farla breve, nel mio mondo mi occupo della produzione di merce natalizia. Fornisco lo spirito delle festività a chiunque abbia voglia di fare affari. Ultimamente, però, chiunque io mandi a consegnare prodotti nel tuo mondo scompare. Perché non cerchiamo di scoprire insieme cosa sta succedendo?"]]
			}
		},
		['winter_queen'] = {
			['choice'] = {
				['2'] = [[Entra]],
				['1'] = [[Vai al palazzo di ghiaccio]],
				['5'] = [[Torna]],
				['4'] = [[E adesso?]],
				['3'] = [[Entra nella stanza]]
			},
			['episode'] = {
				['2'] = [[L'orso annuisce, poi inizia a correre e spicca un salto in aria. Io e la fanciulla di neve ci stringiamo più forte a lui. Siamo circondati dalle luci del nord e dallo scintillio di migliaia di stelle. Pochi secondi dopo, ci troviamo di fronte alle porte del palazzo di ghiaccio, nel lontano nord.]],
				['1'] = [[Come avevo già fatto, sollevo la stella polare e Umka appare dinanzi a noi. Aiuto la fanciulla di neve a issarsi sulla sua schiena e faccio altrettanto. È ora di andare.]],
				['9'] = [[L'orso ci riporta indietro alla svelta, ma la fanciulla di neve rimane aggrappata alla sua schiena. «Finisci di addobbare l'albero di capodanno, io mi occuperò del resto!» proclama prima di sparire in lontananza.]],
				['8'] = [[La fanciulla di neve si volta a guardarmi, il viso rigato da lacrime di ghiaccio. «So cosa va fatto» mormora con voce spezzata. Poi sembra riprendersi: «Presto! Prima torniamo e meglio è!» esclama correndo verso l'uscita.]],
				['7'] = [[Io e la fanciulla di neve restiamo a fissare un grosso blocco di ghiaccio che sorregge il soffitto di quella che era la stanza del trono. A pochi passi da noi, scorgiamo le figure immobili di Nonno Gelo e della regina d'inverno.]],
				['6'] = [[Improvvisamente, Nonno Gelo appare a fianco della regina d'inverno. Immagino che si sia liberato dalle catene quando il ghiaccio ha iniziato a spaccarsi. L'uomo le strappa il bastone dalle mani inerti e lo spezza a metà! Un'esplosione rimbomba nella stanza.]],
				['5'] = [[Indebolita dallo scontro, la regina d'inverno ci fissa con uno sguardo carico d'odio. «Benissimo, ma voi verrete con me!» sibila la donna, colpendo nuovamente il pavimento con il suo bastone. Un rumore secco erompe nella stanza e dal soffitto iniziano a precipitare blocchi di ghiaccio. Le crepe si propagano sulle pareti. Il palazzo ci sta crollando addosso.]],
				['4'] = [[La regina d'inverno è seduta sul suo trono e Nonno Gelo le è accanto, esattamente come li ho visti nello specchio. «Allora siete venuti» scandisce la regina a labbra strette, «Non importa. Ora diverrete parte di questo palazzo». Così dicendo, fa ondeggiare il bastone e spara un getto di vento gelido contro di noi.]],
				['3'] = [[Umka rimane fuori, mentre io e la fanciulla di neve andiamo a cercare Nonno Gelo. All'interno, il palazzo ha tutta l'aria di una vasta grotta di ghiaccio. In men che non si dica, arriviamo alla sala del trono.]]
			}
		},
		['emba_isaev_rostov'] = {
			['episode'] = {
				['2'] = [["Presumo che siano le stesse persone che lavoravano al progetto prima della guerra. Secondo i registri, la precedente stazione biologica è stata violata da un uomo che ha usato il codice di accesso del colonnello Kurganov. Cercheremo di saperne di più su di lui."]],
				['1'] = [["La scatola nera che hai riportato dall'ultima città della scienza era in condizioni migliori della precedente. Le nostre ipotesi sono state confermate. Qualcuno sta ancora lavorando in alcune stazioni biologiche."]],
				['12'] = [["Dobbiamo salvare Gerin. Secondo i registri, potrebbe essere molto lontano da qui. Lungo la strada, visiteremo un'altra città della scienza: lì c'è un certo oggetto su cui Kurganov sta raccogliendo informazioni. Credo che ci sia un motivo per il suo interesse e non possiamo ignorarlo. Partiremo non appena sarai pronto."]],
				['11'] = [["Dobbiamo ancora scoprirlo, ma molto probabilmente il centro della questione è la simbiosi tra biologia e cibernetica. Forse, però, sono coinvolte altre tecnologie segrete."]],
				['10'] = [["Abbiamo scoperto che gli scienziati hanno usato la tecnologia moderna, tra le altre cose, per stabilizzare il micelio".<new_line>Le macchine di calcolo con determinati algoritmi hanno chiaramente aiutato a frenare i cambiamenti incontrollabili del virus. Ora stiamo lavorando attivamente in questa direzione."]],
				['9'] = [["Ma non è in ospedale tra i feriti. Questo significa che... è morto in servizio. È una notizia molto deprimente; abbiamo perso un brav'uomo e un dipendente molto prezioso. Tuttavia, dobbiamo continuare a lavorare affinché la sua morte non sia stata vana."]],
				['8'] = [["Assolutamente. Credo che il Generale abbia costretto Gerin a continuare lo sviluppo militare. Se salviamo Gerin, il nostro compito sarà molto più facile. Non ho ricevuto alcun rapporto dal Navigatore, hai idea di quale sia il problema?"]],
				['7'] = [["Li ha usati per gli esperimenti?! È inconcepibile! Deve aver sfruttato anche Gerin, dobbiamo trovarlo immediatamente."]],
				['6'] = [["Santo cielo, li hai salvati! I miei colleghi sono vivi, ma lo sapevo già. Ho mandato da loro i nostri migliori medici. Che cosa gli è successo, comunque?"]],
				['5'] = [["Sono d'accordo. Quindi ti suggerisco di andare immediatamente dal Navigatore. Parla con lui e inizia a cercare la scatola nera. Abbiamo decifrato il codice di accesso dello scomparto segreto del laboratorio. Eccolo, l'ho scritto qui."<new_line>(Ti porge un pezzo di carta con il codice)]],
				['4'] = [["Nulla di utile dal punto di vista pratico, per ora. Ma abbiamo scoperto che il Navigatore deve sapere di Kurganov, l'uomo il cui codice di accesso è stato usato per entrare nella precedente stazione biologica."]],
				['3'] = [["Devo chiederti di nuovo di completare i nostri incarichi di ricognizione per recuperare i documenti del progetto segreto E.M.B.A. Questo è necessario innanzitutto per decifrare il codice di accesso al laboratorio."]]
			},
			['choice'] = {
				['2'] = [["Che cosa devo fare ora?"]],
				['1'] = [["Cosa?! Chi potrebbe essere?"]],
				['7'] = [["Dobbiamo chiederglielo!"]],
				['6'] = [["E come ha fatto Kurganov a controllare il team di spedizione?"]],
				['5'] = [[Parla dell'esplosione]],
				['4'] = [["Pensi che sia tutta colpa di Kurganov?"]],
				['3'] = [["Erano controllate da Kurganov."]]
			}
		},
		['pharmacy_freak'] = {
			['choice'] = {
				['2'] = [[Ispeziona la farmacia]],
				['1'] = [[Ispeziona la persona]],
				['4'] = [[Scappa dalla farmacia]],
				['3'] = [[Spara al corpo]]
			},
			['episode'] = {
				['2'] = [[Ho fatto per toccare la persona, quando ha aperto gli occhi e mi ha morso la mano! Poi è saltato in piedi e ha estratto un coltello.]],
				['1'] = [[La farmacia era stata completamente saccheggiata. Sul pavimento, in mezzo a tutto quel caos, c'era una persona immobile con accanto delle siringhe vuote. Pare che qualcuno abbia già ripulito tutto.]],
				['5'] = [[Ho deciso che, per non rischiare, sarebbe stato meglio sparargli al cranio. A giudicare da come si è contorto il corpo, prima era ancora vivo.]],
				['4'] = [[Stavo per andarmene dalla farmacia, quando ho visto che il corpo non c'era più. Prima che potessi riprendermi dalla sorpresa, l'uomo che credevo scomparso mi ha attaccato alle spalle con un coltello!]],
				['3'] = [[Come sospettavo, non era rimasto niente, nemmeno bende o carbone attivo.]]
			}
		},
		['emba_search_easy'] = {
			['episode'] = {
				['2'] = [["Meglio! Il giorno della consegna, i ragazzi fanno la fila come se fossero al negozio di paese. Non riesco nemmeno a procurarmi in tempo tutto ciò di cui ho bisogno per la fermentazione."]],
				['1'] = [["Ascolta, giovanotto, ho una proposta da farti. Sto preparando l'infuso di micelio qui e ho bisogno di materie prime. Ne hai un po'?"]],
				['6'] = [["Oh, vedo che scende bene! Ancora."]],
				['5'] = [["Ecco un concetto che capisco: lavorare insieme! Sto avviando la macchina! Ne vuoi... un po' per darti energia?"]],
				['3'] = [["In generale, la vita è pericolosa. Comunque, prova il mio infuso e cambierai subito idea.<new_line>Si sta facendo tardi. Lo proverai?"]]
			},
			['choice'] = {
				['7'] = [["Va bene, mi hai convinto, ci proverò."]],
				['6'] = [["È una specie di alcol?"]],
				['5'] = [["Va bene, mettiamoci al lavoro."]],
				['4'] = [["No grazie."]],
				['3'] = [["Capisci che è pericoloso?"]]
			}
		},
		['fire_event'] = {
			['choice'] = {
				['2'] = [[Prova a estinguere il fuoco]],
				['1'] = [[Salva cose]],
				['3'] = [[Scappa]]
			},
			['episode'] = {
				['2'] = [[Sono corso immediatamente a salvare le cose che erano rimaste dentro. Sono riuscito a prenderne alcune prima che le fiamme inghiottissero l'edificio, ma ne sono uscito con qualche ustione.]],
				['1'] = [[Purtroppo mi sono distratto, e il fuoco che ho acceso nell'edificio è diventato pericoloso. Tra poco prenderà fuoco tutto quanto!]],
				['3'] = [[Sono riuscito a estinguere il fuoco che avevo acceso, ma mi sono ustionato e ho consumato tutta l'acqua che avevo.]]
			}
		},
		['embacity_institute'] = {
			['episode'] = {
				['2'] = [[La cupola dell'istituto è ricoperta da potenti steli carichi di fiori rossi. Forse sono questi che attirano le creature mutanti. Devo controllare se la mia tuta protettiva è a posto.]],
				['1'] = [[Una solida massa di radici e muffa ha deturpato l'intero edificio. Un piccolo esercito di mutanti si è radunato nei dintorni e dovrò indossare una protezione speciale per affrontarli.]],
				['4'] = [[L'istituto è gravemente danneggiato, e diverse creature umanoidi mutate si intravedono dalle finestre. Non è una buona idea avere a che fare con loro senza una tuta protettiva.]],
				['3'] = [[Una grande orda di biomutanti si aggira tra le radici, le cui gigantesche propaggini stanno quasi distruggendo l'ossatura dell'edificio. Senza una protezione speciale, non posso affrontarli.]]
			}
		},
		['new_year_search_fight'] = {
			['choice'] = {
				['2'] = [[Consegna gli ornamenti]],
				['1'] = [["Come avrei fatto a prenderli?"]]
			},
			['episode'] = {
				['2'] = [["Non provare a fregarmi! Non ci muoveremo da qui finché non ci avrai dato la nostra parte degli ornamenti, e neanche tu! !"]],
				['1'] = [[In una delle stanze, mi sono imbattuto in un gruppo di furfanti. Credo che anche loro stiano cercando qualcosa. Uno di loro si è rivolto verso di me esclamando:<new_line>"Ehi, dove sono finiti gli ornamenti natalizi? Non li avrai già presi tutti quanti?!"]],
				['3'] = [["Oooh, sì! Regaaaaali! Qualcuno ha un fiammifero? Andiamo!"<new_line><new_line>I furfanti si sono completamente dimenticati di me e hanno tagliato la corda.]]
			}
		},
		['lair4'] = {
			['episode'] = {
				['1'] = [[Mi dirigo verso la tana, percependo occhi famelici puntati su di me e un mesto ringhio che riecheggia tutto intorno. Quando mi abbasso per passare sotto un albero caduto, il lupo mannaro coglie al volo l'opportunità e mi attacca. ]]
			}
		},
		['gigahive_ufa'] = {
			['episode'] = {
				['2'] = [[Mi sono trovato in un corridoio buio, che sembrava infinito. Dopo qualche minuto ho iniziato a notare delle pustole sulla mia pelle.<new_line>]],
				['1'] = [[Ironicamente, le api mutanti hanno creato l'alveare più grande che abbia mai visto proprio all'interno dell'ex istituto di apicoltura.<new_line>Il ronzio è fortissimo, tanto da far tremare il terreno intorno all'alveare.]],
				['6'] = [[Dopo aver ucciso l'ape regina, sono stato sopraffatto da un'agghiacciante ondata di paura e disperazione. Il terribile ronzio è diventato più forte: le api hanno iniziato a fuggire dall'alveare in preda al terrore.<new_line>Devo sbrigarmi a perlustrare l'area, poi potrò andarmene da qui.]],
				['5'] = [[Dopo averle sconfitte, ho deciso di affrettarmi. Correndo lungo i corridoi, mi sono ritrovato in un'altra stanza ricoperta di larve e nidi d'ape. Prima che potessi pensare a un piano, ho visto un'ape madre furiosa caricare verso di me.]],
				['4'] = [[Sono entrato e mi sono ritrovato in un corridoio buio che sembrava infinito. A un certo punto mi sono imbattuto in una stanza piena di api.]],
				['3'] = [[Esplorare l'alveare non sarebbe stato semplice. Ho sentito che ci sono persone che cacciano le api mutanti a Izhevsk. Forse potranno darmi dei consigli.]]
			},
			['choice'] = {
				['2'] = [[Usa l'antidoto ed entra]],
				['1'] = [[Scappa]]
			}
		},
		['camp_supply_quest'] = {
			['episode'] = {
				['2'] = [[L'uomo ha sospirato sollevato.<new_line>'Grazie, sei stato di grande aiuto.']],
				['1'] = [[Mi sono diretto verso la posizione sulla mappa. Lì ho incontrato un uomo tetro.]],
				['12'] = [[Non ho nulla,' ha detto l'uomo con un sorriso triste. 'Dammi il carico o vattene.']],
				['11'] = [[Bastardo... Guarda! Ti ho già dato tutto quello che ho!']],
				['14'] = [[Gli alleati riescono ancora a difendere l'accampamento.]],
				['13'] = [[L'uomo ferito è ancora steso all'angolo.]],
				['9'] = [[Non c'era più nessuno in vita nell'accampamento. Era troppo tardi.]],
				['10'] = [[Mi sono vendicato e ho difeso l'accampamento. Ma che senso ha avuto?]],
				['7'] = [[Era tutto stranamente silenzioso vicino all'accampamento.<new_line>Mi sono avvicinato e ho notato dei banditi di guardia. I cadaveri dei nostri alleati erano sparsi sul terreno.]],
				['6'] = [[Ho curato la sua ferita. Vivrà.]],
				['5'] = [[L'uomo ha preso il pacchetto.<new_line>Ho notato un uomo ferito in un angolo.]],
				['4'] = [[In qualche modo li abbiamo sconfitti. Il mio alleato mi ha guardato sorpreso.<new_line>'Devo ovviamente ringraziarti per l'aiuto, ma... chi saresti tu?']],
				['3'] = [[Ho individuato i vecchi edifici dove avrei dovuto incontrare i nostri alleati. All'improvviso, ho sentito il rumore di spari. Erano sotto attacco!]]
			},
			['choice'] = {
				['1'] = [[Qualcuno ha ordinato dei rifornimenti?']],
				['12'] = [[C'è un pacchetto per te, ma dovrai pagare']],
				['11'] = [[Riprendi il pacchetto]],
				['13'] = [[Dai il pacchetto]],
				['9'] = [[Aspetta che i banditi se ne vadano]],
				['8'] = [[Vendicati]],
				['5'] = [[Aiuta]],
				['4'] = [[Degli amici mandano rifornimenti']],
				['14'] = [[Cerca aiuto]]
			}
		},
		['halloween_boss_2022_1'] = {
			['choice'] = {
				['2'] = [[Entra]],
				['1'] = [[Crea una veste]],
				['11'] = [[Ascolta]],
				['9'] = [["Vediamo che cosa hai per me."]],
				['8'] = [["E che cosa dovrei farmene?"]],
				['7'] = [["Quale artefatto?"]],
				['6'] = [[Vai al centro e aspetta]],
				['5'] = [[Corrompi]],
				['4'] = [[Dai una candela]],
				['3'] = [[Dai della vodka]]
			},
			['episode'] = {
				['2'] = [[Non la chiamerei esattamente "veste", ma dovrà bastare. Non c'è tempo per aggiustarla. Se resterò nell'ombra, forse nessuno mi noterà.]],
				['1'] = [[Questo è il fienile in cui si riuniscono i seguaci della setta. Molti di loro fumano davanti all'ingresso, e indossano tutti le stesse vesti. Me ne servirà una uguale per farmi riconoscere come membro ed entrare. Penso di riuscire a ricavare qualcosa, ma mi serviranno dei pezzi di tessuto.]],
				['11'] = [["Oh, quasi dimenticavo. Ho qualcosa in vendita. Dai un'occhiata, potresti trovare qualcosa di utile."]],
				['10'] = [["Ti ritroverai faccia a faccia con avversari spaventosi, perciò andando avanti dovrai aumentare il tuo livello demoniaco. A proposito, ho già un secondo contratto che fa al caso tuo."]],
				['9'] = [["Ti donerà i poteri del Demone. L'occhio di Balthazar incatenato al suo interno ti permetterà di consumare le anime dei tuoi nemici per diventare più forte."]],
				['8'] = [["Una degna battaglia, mortale. Possiamo ricavare un potente artefatto dal Demone che hai sconfitto."]],
				['7'] = [["Per risvegliare il nostro signore, avremo bisogno di un sacrificio di sangue. Secondo la tradizione, dovremo sacrificare il nostro servo più fedele, colui che si è presentato con la miglior veste. Fratelli e sorelle, mostratemelo!" Dopo aver finito di pronunciare queste parole, tutti si voltarono verso di me.]],
				['6'] = [[Al centro c'era un pentagramma splendente con all'interno la sagoma del Demone. Appena arrivarono tutti quanti, il capo iniziò il suo discorso.]],
				['5'] = [[Altri seguaci della setta si erano già riuniti all'interno del fienile. Mentre mi facevo strada tra di loro, qualcuno mi diede una pacca sulla spalla sussurrandomi: "Bella veste, fratello. Hai il mio rispetto."]],
				['4'] = [["Se solo noi accoliti fossimo tutti coraggiosi e fedeli come te, fratello. Entra pure."]],
				['3'] = [[Il buttafuori a guardia dell'ingresso non mi ha fatto entrare: "Fermo! Hai una veste davvero bella, fratello, ma prima di entrare dovrai lasciare una donazione. Accetterò della vodka o una candela."]]
			}
		},
		['scientist_rescue_bandit'] = {
			['episode'] = {
				['2'] = [[Lo scienziato si è catapultato al centro dell'accampamento con un grido di gioia ed è tornato con una scatoletta metallica. Mi ha ringraziato di cuore e ha nascosto la scatoletta nello zaino.]],
				['1'] = [[Ci avviciniamo a un accampamento misero e uno dei banditi esce per parlarci:<new_line>"Siete qui per il congegno, eh? Sentite, non viviamo nel lusso, non possiamo restituirvelo e basta. Offrite qualche soldo a un pover'uomo e vi lascerò andare in pace."]],
				['4'] = [[Lo scienziato è morto. La mia missione principale era tenerlo in vita, e ho fallito.]],
				['3'] = [[Il bandito ci ha portato una scatoletta metallica. Lo scienziato l'ha nascosta subito nello zaino e mi ha ringraziato.]]
			}
		},
		['guard_leningrad'] = {
			['episode'] = {
				['2'] = [["Di cosa hai bisogno? Ci farebbe comodo il tuo aiuto qui."]],
				['1'] = [["Cerchi qualcosa?"]],
				['6'] = [["Molto bene, allora dirigiti a sud. C'è un insediamento di sopravvissuti vicino al porto di Tver. Vivevamo lì una volta, sai? Conosco anche Svetlana. Ha salvato molte vite. Penso che sia ancora lì.<new_line><new_line>Prendi la tua attrezzatura, usala saggiamente e tieni d'occhio il contatore Geiger. È fin troppo semplice finire in mezzo a una terra desolata radioattiva."]],
				['5'] = [["Quindi ora sei meglio preparato?"]],
				['4'] = [["Non siamo rimasti in molti. Dobbiamo aiutarci a vicenda. E tu mi sei molto più utile da vivo."]],
				['3'] = [["Senti, l'area attorno a Mosca è pericolosa. Tver inclusa. Tutta la zona è stata uno dei principali bersagli del nemico ed è stata colpita duramente dal nucleare. Ora è piena di radiazioni!<new_line><new_line>Mi è appena venuta un'idea. Dammi una mano: potrai prepararti per questo viaggio pericoloso che hai intenzione di fare e, per ringraziarti, ti darò anche dell'attrezzatura. In questo modo, non cadrai a terra morto non appena arriverai a destinazione."]]
			},
			['choice'] = {
				['2'] = [["Ho cose importanti da fare. Sto cercando una persona."]],
				['1'] = [["Voglio sapere se c'è un insediamento di sopravvissuti a Tver."]],
				['7'] = [["Grazie."]],
				['6'] = [["Sì."]],
				['5'] = [["Non ancora."]],
				['4'] = [["Va bene. Alla prossima."]],
				['3'] = [["Perché ti interessa?"]]
			}
		},
		['help_hunter1'] = {
			['episode'] = {
				['1'] = [[Un'orda di zombi ha messo il cacciatore con le spalle al muro. Non ha alcuna possibilità di farcela da solo.]]
			}
		},
		['embacity_biolab_4'] = {
			['episode'] = {
				['1'] = [[Ho notato solo una figura in questa sala. Da questa distanza, sembra abbastanza umana, ma c'è qualcosa di spaventosamente innaturale nei suoi movimenti.<new_line>La figura si aggira nella sala, controllando le autoclavi presenti. Notandomi, si prepara ad attaccare.]]
			}
		},
		['search_killer2'] = {
			['episode'] = {
				['1'] = [[Una nuova vittima. Le tracce sono ancora piuttosto fresche. Penso di essere vicino all'assassino.]]
			}
		},
		['halloween_boss_2022_2_2'] = {
			['choice'] = {
				['2'] = [["Un balsamo per becchi puzzolenti."]],
				['1'] = [[Dirigiti verso i bagliori]],
				['7'] = [["Accomodati pure."]],
				['6'] = [["Una medicina conto le radiazioni."]],
				['5'] = [["Una pozione soporifera."]],
				['4'] = [["Un veleno putrefacente."]],
				['3'] = [["Un siero della verità."]]
			},
			['episode'] = {
				['2'] = [[Il Medico della peste si nasconde tra due tombe aperte e sta rovesciando qualcosa in un calderone ribollente. È tempo di porre fine a tutto questo una volta per tutte.]],
				['1'] = [[Nell'angolo più remoto del cimitero, le lapidi sono illuminate da dei bagliori verdi. Deve essere questo il posto in cui il Medico della peste conduce i suoi vili esperimenti.]],
				['10'] = [["Oh, di nuovo tu. Molto bene, possiamo riprendere da dove avevamo interrotto?"]],
				['9'] = [["E io che pensavo ci avresti rimesso l'osso del collo! Davvero niente male... Ho il tuo prossimo contratto proprio qui."]],
				['8'] = [["Osservarti è davvero divertente, mortale! Prova a usare il cervello e torna più tardi. Il medico ha intenzione di restare qui per molto tempo..."]],
				['7'] = [["Esatto. Si ottiene una pozione soporifera così potente da essere conosciuta come 'Sorso del morto vivente'. Sei più intelligente di quanto pensassi... Non vedo l'ora di dissezionarti il cervello, ed è esattamente ciò che faremo adesso!"]],
				['6'] = [["No, non lo sai. Beh, ci riproveremo un'altra volta. E adesso vedrai che succede quando mi dai delle risposte stupide."<new_line><new_line>Il Medico della peste lanciò qualcosa nel calderone e il vapore che ne fuoriusciva divenne ancora più denso. Una volta che il vapore si era dissipato, il medico era scomparso nel nulla, e mi ritrovai circondato da zombi.]],
				['5'] = [["Ho usato alcune delle mie pozioni, perciò devo prepararne delle altre. Dimmi, che cosa ottengo mischiando radice di asfodelo tritata e infuso di assenzio?"]],
				['4'] = [[Mentre stavo per estrarre l'arma, il medico scuoteva la testa come se fosse deluso. Ha agitato la mano ed è scomparso nella nebbia verde che lo avvolgeva. Dovrò riprovarci quando tornerà al cimitero.]],
				['3'] = [["Ma guarda un po', chi abbiamo qui? Devi essere un esemplare davvero curioso... Che ne dici di parlare un po', prima che mi occupi di te?"]]
			}
		},
		['halloween_fight_hard'] = {
			['episode'] = {
				['2'] = [[Mentre mi avvicinavo cautamente alla fonte del rumore, quest'ultimo iniziava ad attenuarsi a poco a poco. Appena raggiunsi la fonte, il rumore era completamente svanito.]],
				['1'] = [[Il rauco baccano delle bestie si sente da molto vicino. A quanto pare, ce ne sono parecchie. È meglio fare attenzione.]],
				['7'] = [[Preparai il campo di battaglia in caso di attacco e piazzai delle semplici trappole. Meglio di niente, quantomeno. In seguito, feci un po' di rumore, e le bestie rimanenti corsero verso di me. Purtroppo, erano estremamente pericolose.]],
				['6'] = [[Ho perlustrato l'area con attenzione e ho scoperto che ci sono altre bestie nelle vicinanze. Adesso ho l'elemento sorpresa, anche se questi mostri sembrano più pericolosi degli altri.]],
				['5'] = [[Non sento più nessuna bestia. Non ho idea di come siano riuscite ad attirarmi in una trappola. Devo assicurarmi che non ce ne siano altre nascoste qui intorno.]],
				['4'] = [[Mi sono sbarazzato dei mostri, ma dubito fortemente che siano finiti qui. Gli ululati delle bestie continuavano ad aumentare intorno a me.]],
				['3'] = [[Calpestai un ramo e le bestie intorno a me iniziarono a ululare. Era una trappola!]]
			},
			['choice'] = {
				['2'] = [[Controlla la zona]],
				['1'] = [[Avvicinati]],
				['3'] = [[Attira l'attenzione su di te]]
			}
		},
		['reinforcement_quest_pt2'] = {
			['episode'] = {
				['2'] = [[Non ho mai dubitato di te. Tieni, sono in debito.'<new_line>Il capo delle guardie mi ha ricompensato per l'aiuto.]],
				['1'] = [[Dimmi, com'è andata?']],
				['3'] = [[Il capo delle guardie poteva leggermelo in volto.<new_line>'Hai fatto ciò che potevi.'<new_line>Mi ha consegnato la ricompensa.]]
			},
			['choice'] = {
				['2'] = [[Abbiamo sconfitto i nemici, ma...']],
				['1'] = [[Li abbiamo distrutti']]
			}
		},
		['construction_team_scout'] = {
			['episode'] = {
				['2'] = [[Sul terreno ci sono i segni neri di un'esplosione, ma non ci sono tracce fresche di persone. Molto probabilmente è troppo tardi per capire chi o cosa abbia causato l'esplosione.]],
				['1'] = [[A giudicare dalle tracce sul terreno, le bestie vengono qui per dissetarsi. Non sono necessariamente pericolose, ma è meglio evitare altre situazioni simili in futuro.]],
