		['halloween_energy_medium'] = {
			['episode'] = {
				['10'] = [[Le porte del magazzino si spalancarono e a quel punto apparve una guardia:<new_line>"Ehi, che cosa abbiamo qui?! Ladri? Vi faccio vedere io che cosa succede ai ladri!"<new_line>]],
