				['2'] = [[Oh oh... Sembra che il terreno si stia muovendo sotto i miei piedi...]],
				['1'] = [[Ho la sensazione che qualcuno mi stia pensando un po' troppo. Uno dei Babbi sta chiedendo aiuto!<new_line>Devo andare a salvarlo il prima possibile, proprio come loro hanno salvato me. Devo concentrarmi.]],
				['8'] = [[A poco a poco, inizio a tornare in me... Cavolo, che casino. Dovrò assolutamente fare pratica con questa magia natalizia se la prossima volta vorrò viaggiare fisicamente attraverso lo spazio.<new_line>Anche se, nonostante ciò, sono comunque riuscito ad aiutare il mio collega. Niente male!]],
				['7'] = [[C'è stato un lampo, e improvvisamente sono stato teletrasportato verso un nuovo mondo. Devo ammettere che era fin troppo luminoso. Chi farebbe mai una cosa del genere?!<new_line>Ai miei tempi, i lampi erano godibili e decisamente più decorosi. Che fine hanno fatto quei giorni?<new_line>Sono invecchiato, ma i giovani d'oggi farebbero bene ad ascoltare i consigli di un vecchio saggio come me. Comunque, che cosa abbiamo qui? Un'altra battaglia? Come al solito, tocca a me occuparmi della questione!]],
				['6'] = [[C'è stato un lampo, e improvvisamente sono stato teletrasportato verso un nuovo mondo. Per caso c'è stato un altro di quegli scioperi? Al giorno d'oggi, non c'è il minimo rispetto per i proprietari di fabbriche per bene e parsimoniosi...<new_line>Un attimo, perché indosso un monocolo? E dove... Ehi! Chi osa toccare le mie macchine? Non starai cercando di fondare un sindacato? Fermati!]],
				['5'] = [[C'è stato un lampo, e improvvisamente sono stato teletrasportato verso un nuovo mondo. Riesco a percepire delle strane sensazioni...<new_line>Le mie zampe sono potentissime! La mia pelle è abbastanza spessa da permettermi di resistere a questo mondo freddo e capitalistico, e le mie zanne affilate sono pronte a masticare la mano invisibile del mercato!]],
				['4'] = [[C'è stato un lampo, e improvvisamente sono stato teletrasportato verso un nuovo mondo. I miei pensieri sono così confus-argh! Grrrr! Hmphh!]],
				['3'] = [[C'è stato un lampo, e improvvisamente sono stato teletrasportato verso un nuovo mondo. Che sta succedendo? Le mie braccia sono diventate delle protesi, e della musica rock a tutto volume mi risuona in testa!<new_line>Sono diventato... Cyberbabbo?! Comunque, non c'è tempo di preoccuparsi di questo ora. I nemici si preparano ad attaccare! Prepariamoci al divertimento!]]
			}
		},
		['santa_wizard'] = {
			['choice'] = {
				['2'] = [["Ne sei sicuro?"]],
				['1'] = [["Se la attaccassimo, perderemmo la nostra magia."]],
				['3'] = [["Sono dei piani B nel caso ci fosse un sovraccarico?"]]
			},
			['episode'] = {
				['2'] = [["Quest'arma è uno stupido aspirapolvere magico! Più cose scopro, meno capisco che cosa dobbiamo fare."]],
				['1'] = [["Che cosa è successo? Eravate disperati senza di me, eh?"]],
				['5'] = [["Esatto! Se le distruggessimo, l'arma non potrebbe resistere a un attacco diretto. Qualche volta è bello poter lavorare con qualcuno così competente. Cyber, portaci alle chiuse energetiche."]],
				['4'] = [["Certo che lo è! È uno stregone, accidenti!<new_line>Ancora una cosa, choomba, per la tua tranquillità. Ho scoperto che Krampus ha delle chiuse energetiche. Ora capisco a che cosa servono."]],
				['3'] = [["Esattamente. Però è altrettanto vero che il nemico non si aspetterebbe mai un attacco diretto. Se riuscissimo ad attaccarla tutti insieme, questa super-arma non sarebbe in grado di gestire e assorbire il potere, quindi esploderebbe."]]
			}
		},
		['new_year_boss_5'] = {
			['choice'] = {
				['2'] = [["Grazie, ma rimane ancora una chiusa energetica."]]
			},
			['episode'] = {
				['2'] = [["Ok, il secondo chi è?"]],
				['1'] = [["Questa volta non faremo a modo tuo, ma seguiremo un piano ben studiato. Alcuni di noi resteranno indietro a perlustrare il perimetro per assicurarsi che nessuno scappi per avvertire Krampus del nostro sabotaggio.<new_line>Puoi portare con te due aiutanti per la battaglia. Chi scegli?"]],
				['4'] = [["Ottimo combattimento, compagno! Ah, guardandoti mi sono ricordato di quando ero un cucciolo..."]],
				['3'] = [["Molto bene, si comincia!"]]
			}
		},
		['new_year_boss_6'] = {
			['choice'] = {
				['2'] = [["La stessa che hai fatto a Babbo Natale?"]],
				['1'] = [[Esci allo scoperto dal mucchio]],
				['10'] = [["Ti sei trasformato in..."]],
				['9'] = [["Siete arrivati!"]],
				['8'] = [["Sì!"]],
				['7'] = [["Krampus, una volta eri Babbo Natale. Avanti!"]],
				['6'] = [["Sei solo un bugiardo, e io ti ucciderò!"]],
				['5'] = [["Sei un mostro!"]],
				['4'] = [["Krampus li ha presi?"]],
				['3'] = [["Nemmeno per sogno."]]
			},
			['episode'] = {
				['2'] = [[In un seminterrato sono accatastati pezzi di ornamenti e ogni genere di bulloni e attrezzi.<new_line><new_line>Riesco a sentire la voce di Frosty provenire dall'altra parte del mucchio.<new_line>"No, adesso comando io! A causa tua, dobbiamo ricominciare tutto da capo in un seminterrato stantio!"]],
				['1'] = [["Ok, sei pronto a teletrasportarti oppure devo aspettarti qui al freddo?"]],
				['12'] = [["Abbiamo recuperato la nostra magia, e dovevo assicurarmi che non mandassi tutto all'aria! Ehm, volevo dire... Grazie per aver ripristinato la nostra magia o qualcosa del genere."]],
				['11'] = [["Eee! Aagaaah!"]],
				['14'] = [["Parleremo più tardi, socio! Dopo quella grande scarica di magia, l'intero posto sta iniziando a crollare! Prendiamo Krampus e andiamocene da qui!"]],
				['10'] = [["Come osi! Inutile vecchio! La magia dei Babbi è mia! Governerò su tutto l'inverno nucleare, mentre tu morirai! Tutti voi morirete!"]],
				['13'] = [["Eh? Che sta succedendo?"]],
				['9'] = [["Ho distrutto quasi tutto il Natale con le mie stesse mani. Il mio cervello è avvelenato, e ormai è tardi per rimediare... ma non rinuncerò alla speranza!"<new_line><new_line>Krampus agita il suo bastone e colpisce il componente che Frosty aveva estratto dall'arma distrutta.]],
				['8'] = [["Mmh, è proprio vero che le prime impressioni sono quelle che contano. Sei DAVVERO stupido. Krampus, smettila di giocherellare con la batteria magica e occupati di questo imbecille!"]],
				['7'] = [["Non otterremmo niente se combattessimo! Piuttosto, uniamo le forze.<new_line>Ascolterò i tuoi suggerimenti per agire nel modo più umano possibile. Ho già in mente un paio di idee! Allora, affare fatto?!"]],
				['6'] = [["Sì! È stata tutta opera di questo idiota. Al tempo ancora si chiamava Babbo Natale e voleva sprecare il potere di questi ornamenti per fare delle cose del tutto inutili: una protezione dalle radiazioni, una fonte di energia alternativa. Pff.<new_line>La fortuna ha voluto che incontrasse un genio come me al momento giusto e che andava nella giusta direzione: verso il potere."]],
				['5'] = [["Stammi a sentire! Tutto ciò di cui ho bisogno sono gli ornamenti del tuo albero. Sono i componenti della super-arma. Secondo te perché non c'era nessun ornamento sull'albero quando l'hai visto per la prima volta?"]],
				['4'] = [[Krampus ha sussultato, mentre Frosty è scoppiato a ridere in modo ripugnante.<new_line>"Ah, avevo ragione: non sei stupido. Questo rifiuto non è riuscito a tenerti testa nemmeno quando tutto era a suo vantaggio.<new_line>Immagina il potere che io e te potremmo ottenere se unissimo le forze!"]],
				['3'] = [["Ehi! Ma come hai fatto a passare... Sembra che tu non sia così stupido come pensavo. Ho un'offerta per te."]]
			}
		},
		['infected_shop'] = {
			['episode'] = {
				['1'] = [[A giudicare dall'esterno dell'edificio, gli insetti mutanti si erano comodamente installati nel vecchio negozio. Probabilmente all'interno c'era qualcosa di valore, ma sarebbe stato un lavoro pericoloso.]]
			}
		},
		['lair2'] = {
			['episode'] = {
				['1'] = [[Non appena varco la porta, l'odore pungente di oli e altri ingredienti mi fa arricciare il naso. La stanza è avvolta nell'oscurità. Improvvisamente, quella che credevo essere una statua si lancia verso di me e io riesco a evitarla per miracolo. È il medico della peste.]]
			}
		},
		['embacity_biolab_reservoir_empty'] = {
			['episode'] = {
				['1'] = [[Un'enorme cisterna occupa gran parte della stazione biologica. Al momento è vuota.]]
			}
		},
		['new_year_boss_5_2'] = {
			['choice'] = {
				['2'] = [["Siamo tutti pronti?"]],
				['1'] = [["Lo spero. Qual è il prossimo obiettivo?"]],
				['3'] = [["Allora diamoci dentro."]]
			},
			['episode'] = {
				['2'] = [["Oh oh oh, ottimo lavoro! Adesso abbiamo minimizzato i rischi?"]],
				['1'] = [["Rimane la seconda chiusa energetica. Seguiamo lo stesso piano d'azione. Scegli i tuoi aiutanti, ma lascia riposare quelli che hanno già combattuto in modo che possano fare da guardia."]],
				['4'] = [["Eee aaaooo!"]],
				['3'] = [["È tempo di dare all'aspirapolvere entropico di Krampus più energia di quanta riesca ad assorbire!"]]
			}
		},
		['emba_isaev_tver'] = {
			['episode'] = {
				['2'] = [["Bene, ottimo. Il tuo compito principale nella prossima spedizione sarà quello di aiutare nella ricerca: dovrai raccogliere materiali biologici e cercare documenti e scatole nere."]],
				['1'] = [["Hai già consegnato i documenti al Biologo e lo hai aiutato con la sua ricerca?"]],
				['12'] = [["Crescita aggressiva del micelio... diffusione all'esterno della stazione biologica... attivazione incontrollata delle spore... accompagnata da un'alterazione del sistema nervoso centrale. Credo che fosse troppo tardi per salvarli.<new_line>Ma se la prima spedizione Progresso è andata avanti, allora..."]],
				['11'] = [["Per decifrarlo completamente ci vorrà del tempo, ma abbiamo già la prima parte di dati.<new_line>Bene, bene, bene... È successo qualcosa in tutte le città della scienza nello stesso momento. I segnali S.O.S. delle stazioni biologiche più vicine lo confermano."]],
				['18'] = [["Bene, allora vuoi a continuare la spedizione?"]],
				['17'] = [["Sì, ci siamo! Un gruppo sperimentale e metodologico di stazioni biologiche che allevano... esemplari da combattimento?! Ci sono le coordinate del luogo in cui sono stati condotti tali esperimenti.<new_line>La prima spedizione è in pericolo, dobbiamo raggiungere subito quella città della scienza! La nostra prossima destinazione è Tula. Partiremo non appena sarai pronto."]],
				['14'] = [["Hai già assemblato un kit AIDA?"]],
				['10'] = [["Spero di no. In teoria, durante la catastrofe globale, il professor Gerin e gli altri scienziati del progetto E.M.B.A. avrebbero dovuto essere evacuati come noi.<new_line>Ma abbiamo perso i contatti con loro, così come con tutti gli altri... e col tempo ci siamo trasformati in "archivisti". Ma non parliamo di cose tristi."]],
				['13'] = [["Il Biologo ti aiuterà. Ma per fare la tuta ti serve biomassa.<new_line>Per questa volta te la darò io, ma d'ora in poi dovrai estrarla dal micelio con un estrattore. Puoi ottenerne uno anche dal Biologo."]],
				['9'] = [["Non avevamo idea che il micelio potesse fare questo a un'intera città! Ma non devi preoccuparti di questo: l'importante è ripristinare la tecnologia e dare un futuro a questo mondo."]],
				['8'] = [["Bentornato! Vedo che hai portato a termine con successo la tua missione, compagno. Ebbene, cos'hai lì?"]],
				['7'] = [["Presto, porta la scatola nera al Biologo e aiutalo a continuare le sue ricerche."]],
				['6'] = [["Beh, è fantastico. Le informazioni sulla ricerca per il progetto E.M.B.A. sono conservate in una scatola nera. Si trova nella stazione biologica al centro della città della scienza. Il tuo supervisore sarà il tuo Navigatore e ti aiuterà nella ricerca. Se vuoi scusarmi, ora devo tornare al lavoro."]],
				['5'] = [["Hai già ricevuto il kit AIDA?"]],
				['4'] = [["Il nostro staff è disposto a scambiare un kit AIDA e altri consumabili in cambio di dischetti contenenti dati.<new_line>Aiuta i miei colleghi a studiare il micelio e salva i dati sui floppy disk. Quando ne avrai raccolti abbastanza, passa al reparto Consumabili e acquista una tuta speciale AIDA."]],
				['3'] = [["Vai alla città della scienza più vicina per cercare la scatola nera. Lì non ci sono radiazioni, ma la probabilità di contrarre le spore del micelio è estremamente alta.<new_line>Quindi, prima di procedere, procurati un kit di protezione dalle spore, AIDA."]]
			},
			['choice'] = {
				['2'] = [["Ho capito."]],
				['1'] = [["Non ancora."]],
				['11'] = [["Hanno trovato qualcosa di nuovo?"]],
				['10'] = [["Cos'è successo?"]],
				['9'] = [["Gli scienziati della città della scienza sono diventati mutanti?"]],
				['8'] = [["Sì."]],
				['7'] = [["Non sono ancora pronto."]],
				['6'] = [["Sono pronto."]],
				['5'] = [["Cos'hai scoperto dalla scatola nera?"]],
				['4'] = [[Segnala gli avvistamenti]],
				['3'] = [["Dove posso ottenerlo?"]]
			}
		},
		['newyearEvent_commander_medium'] = {
			['episode'] = {
				['2'] = [[Alla mia vista, Naclava salta di gioia e scuote la clava sulla mia testa.<new_line>"Oooooooooooooh."]],
				['1'] = [[Circondato dagli abitanti del villaggio, Babbo Naclava cerca di spiegare loro qualcosa come è solito fare.<new_line>"Oooorgh", dice gesticolando con la clava.<new_line>"Brrrrr!", insiste indicando la foresta.]],
				['8'] = [[Durante la battaglia, il mio quoziente intellettivo ha iniziato a tornare alla normalità un po' alla volta.<new_line>Dopo la battaglia, uno dei miliziani si è avvicinato verso di me.<new_line>"Grazie a te, ora saremo in grado di proteggerci nell'ora del bisogno. Non esiste regalo più grande!"]],
				['7'] = [["Hanno svolto un ottimo lavoro, ma all'orizzonte vedo un altro gruppo di furfanti. Dovresti impartirgli un'altra lezione veloce. Forse alcuni degli abitanti la impareranno e la useranno a loro vantaggio in battaglia."]],
				['6'] = [[Anche gli altri miliziani hanno capito la lezione e hanno iniziato a fare pratica. Dopo un po', Babbo Naclava è venuto verso di me.<new_line>"I furfanti si avvicinano. Gli abitanti del villaggio dovrebbero affrontarli per dare prova dei tuoi insegnamenti. Mostragli come si combatte."]],
				['5'] = [[Due dei quattro miliziani hanno capito perfettamente la mia lezione e hanno iniziato a fare pratica. Gli altri due, invece, sembra che non abbiano imparato niente. Uno di loro ha chiesto:<new_line>"Ah ooomaroov?"]],
				['4'] = [[La milizia non è molto grande, sono solo quattro persone. Uno di loro viene verso di me e grugnisce una domanda.<new_line>"Ooogrooooom ah ooom?"]],
				['3'] = [[Il mio quoziente intellettivo è diminuito, perciò inizio a capire i discorsi di Naclava.<new_line>"Grazie agli dei puoi capirmi! Voglio insegnare agli abitanti del villaggio come si affrontano i nemici. Sono persino riuscito a formare una milizia. A essere sincero, non sono un granché come insegnante. Aiutami."]]
			},
			['choice'] = {
				['2'] = [[Lascia che ti colpisca in testa]],
				['1'] = [["Che sta succedendo qui?"]],
				['12'] = [[Mostra come si schiva]],
				['11'] = [[Mostra come si contrattacca]],
				['10'] = [[Mettiti in una posa difensiva]],
				['13'] = [["Felice di aiutare." (Vattene)]],
				['9'] = [[Urla il grido di battaglia per attaccare]],
				['8'] = [[Mostra come si usa un fucile a canne mozze]],
				['7'] = [[Mostra come si usa una lancia]],
				['6'] = [[Mostra come si usa una granata]],
				['5'] = [[Mostra come si usa un fucile]],
				['4'] = [["Va bene."]],
				['3'] = [["No, c'è un altro modo!" (Bevi la vodka)]]
			}
		},
		['comrade_in_trouble'] = {
			['episode'] = {
				['2'] = [[Nell'accampamento, c'è un uomo seduto su una sedia pieghevole davanti a un cavalletto. Ha sobbalzato alla mia vista, ma poi ha sorriso con aria colpevole distendendo le braccia.]],
				['1'] = [[Un uomo è uscito dall'accampamento per vedermi:<new_line>"Sei uno dei nostri? Non fare rumore o verranno tutti di corsa. Comunica alla base che resterò qui per un altro giorno o due: il lavoro si sta rivelando complesso."]],
				['12'] = [[È stato allestito un accampamento nei pressi delle rovine di una casa. Non si vede nessuno, ma l'ingresso è bloccato da un mucchio di macerie e mattoni. Dall'altro lato si sentono spari e imprecazioni.]],
				['11'] = [[Ci sono casse e scatole sparse in tutto l'accampamento. L'esploratore sta cercando di fissare il tutto a una vecchia moto.<new_line>"Guarda, ho un carico da favola ma non posso trasportarlo tutto. Ah, a casa sentono già la mia mancanza? Ok, mi metto in viaggio.<new_line>Però quella cassa enorme non entrerà mai. Fammi un favore, portala tu."]],
				['13'] = [[Alcuni tizi si sono appostati dall'altro lato della casa. Hanno le armi puntate verso l'interno. Si direbbe che l'esploratore si sia barricato all'interno.]],
				['14'] = [[I banditi hanno fatto un bel balzo, ma uno di loro si è ripreso subito:<new_line>"Ehi tu, laggiù! Calma! È un tuo amico quello barricato tra le rovine?<new_line>Non facciamo sciocchezze: lanciaci un po' di grana e vi lasceremo in pace."]],
				['15'] = [[Un uomo esce dalla finestra con aria contenta.<new_line>"Wow, grazie, mi hai salvato la vita! Pensavo di essere spacciato, avrei esaurito i proiettili e tanti saluti."]],
				['16'] = [[Il bandito fischietta, prende il denaro e va via con i suoi uomini al seguito. Un uomo esce dalla finestra con aria contenta.<new_line>"Wow, grazie, mi hai salvato la vita! Pensavo di essere spacciato, avrei esaurito i proiettili e tanti saluti."]],
				['6'] = [[L'oggetto è danneggiato, probabilmente per via di un'esplosione. Ehi, è il pezzo di un missile militare! Il mio dosimetro sta andando in tilt: quel dannato affare è radioattivo!]],
				['5'] = [[L'accampamento è vuoto, ma non ci sono tracce di combattimenti né di fughe improvvise. Accanto al fuoco, c'è un oggetto metallico deformato.]],
				['4'] = [[Il nostro eroico esploratore non morirà più di freddo.]],
				['3'] = [[Mentre mi avvicinavo all'accampamento, ho sentito un sonoro russare. Accanto alle braci di un fuoco da campo ho trovato un uomo che dormiva abbracciando affettuosamente una bottiglia vuota.<new_line>Non è stato difficile capire la causa del ritardo. Forse dovrei gettare un po' di legna nel fuoco? La notte è piuttosto fredda, dopotutto.]],
				['25'] = [[Meno male che sono arrivato in tempo per aiutarlo. Avrebbe potuto perdere molto sangue, ma la sua vita non è più in pericolo.]],
				['24'] = [[Ho offerto all'uomo un po' del mio carburante. Dovrebbe essere più che sufficiente per tornare all'insediamento.]],
				['23'] = [[Ho dato all'uomo un medicinale per le radiazioni. Presto starà meglio.]],
				['22'] = [[Sono stato inviato troppo tardi. L'esploratore è morto. A giudicare dalle ferite sul suo corpo, è stato aggredito da dei grossi predatori.]],
				['21'] = [[Accanto al fuoco dell'accampamento, un uomo sta mescolando l'acqua in un pentolino. Mi sorride:<new_line>"Aaah, a casa sono preoccupati perché non torno? Niente paura, digli che tornerò presto. La spedizione è stata un successo e mi sono trattenuto qui."]],
				['20'] = [[C'è un uomo seduto accanto al fuoco. Non ha una bella cera: sul petto ha degli squarci profondi e i suoi pantaloni sono insanguinati.]],
				['19'] = [[Un uomo è strisciato fuori verso la libertà con aria sollevata, spingendo un sacco davanti a sé.<new_line>"Stavo provando a far saltare in aria una cassaforte d'acciaio," ha detto l'esploratore alzando le spalle. "E così ero rimasto bloccato là dentro. Grazie!"]],
				['18'] = [[Dall'altro lato della casa ho trovato una finestra sbarrata. Non appena mi sono avvicinato, ho sentito un urlo dall'interno:<new_line>"Ehi, ehi! Sono barricato qui dentro! Tirami fuori da qui e gli abitanti dell'insediamento ti ricompenseranno."]],
				['17'] = [[È stato allestito un accampamento nei pressi delle rovine di una casa. Non si vede nessuno, ma l'ingresso è bloccato da un mucchio di macerie e mattoni.]],
				['7'] = [[Allontanandomi di qualche metro, ho sentito un debole grido. Un uomo in mimetica stava strisciando verso l'accampamento. Quando mi ha visto, ha sussurrato:<new_line>"Ho sentito un rumore nell'accampamento... Hai toccato il congegno? Ronza da matti! Sono stato un idiota ad aver lasciato il mio dosimetro nello zaino. Il campo si è illuminato e sono stato colpito dalle radiazioni. Respiro a malapena."]],
				['8'] = [[Dai dintorni dell'accampamento provenivano strani rumori. Mentre li seguivo, ho visto dei predatori che circondavano un traliccio arrugginito. Lo sventurato esploratore era seduto là in cima.]],
				['9'] = [[L'esploratore scende e mi offre una stretta di mano:<new_line>"Grazie! Credevo proprio che per me fosse finita. Mi avevano colto disarmato. L'unica soluzione era salire sempre più in alto."]],
				['10'] = [[È stato allestito un accampamento accanto al veicolo. C'è un uomo che si aggira caricando e scaricando roba di ogni tipo.<new_line>"Ah, a casa sentono già la mia mancanza? Sto portando un mucchio di scorte, ma mi sono dimenticato della benzina: sono rimasto a secco a metà strada."]]
			},
			['choice'] = {
				['2'] = [[Getta la legna nel fuoco]],
				['1'] = [[Riporta le notizie all'insediamento]],
				['12'] = [[Sega le sbarre della finestra]],
				['11'] = [[Prendi di mira i banditi e grida]],
				['16'] = [[Prendi la cassa]],
				['15'] = [[Riporta il corpo all'insediamento]],
				['14'] = [[Presta il primo soccorso]],
				['10'] = [[Perlustra la casa]],
				['13'] = [[Sposta le macerie dall'ingresso]],
				['9'] = [[Condividi la benzina]],
				['8'] = [[Combatti!]],
				['7'] = [[Cerca aiuto]],
				['6'] = [[Dai il medicinale per le radiazioni]],
				['5'] = [[Allontanati di più dal proiettile]],
				['4'] = [[Cerca l'esploratore nei paraggi]],
				['3'] = [[Esamina l'oggetto]]
			}
		},
		['base_restaurant'] = {
