		['pumpkin_seed'] = {
			['episode'] = {
				['2'] = [[Sembra che in questo mondo sia finalmente apparso un degno avversario. Il nostro scontro sarà leggendario! Addio, umano, tuona la zucca gigante. Ignorandomi totalmente, la vedo marciare in lontananza, facendo tremare il terreno a ogni passo. Decido di tornare alla taverna.]],
				['1'] = [[Pianto il seme nella terra e lo annaffio. Qualche istante dopo, appare un germoglio, che in un attimo si trasforma in un grosso viticcio da cui cresce una piccola zucca. Con mio grande stupore, le appaiono occhi, denti e zampette. Un minuto dopo, la zucca si stacca dal viticcio e mi afferra uno stivale. Non sono sicuro di cosa stia accadendo, ma è ora di far vedere a questo ortaggio chi comanda.]]
			}
		},
		['madhouse_kandalaksha'] = {
			['episode'] = {
				['2'] = [[I pazzi si sono stabiliti nella caserma distrutta.<new_line>Lì vicino ho trovato anche il barcaiolo: era nascosto e mi aspettava con un paio di fucili.]],
				['1'] = [[Delle urla risuonano dall'interno della casa. I pazzi.<new_line>Ce ne sono troppi. Entrare sarebbe un suicidio...]],
				['4'] = [[Il barcaiolo ha raccolto i pezzi rimanenti dell'attrezzatura radio e ha raggiunto la porta.<new_line>'Suggerisco di dare un'occhiata qui in giro. Non si sa mai, magari qualche cosa potrebbe tornarci utile. Ti aspetto alla barca.'<new_line><new_line>]],
				['3'] = [[Dopo aver assemblato le cartucce, il barcaiolo ha esclamato:<new_line>'Entriamo lì dentro e distruggiamoli.']]
			},
			['choice'] = {
				['2'] = [[Aspetta qui. Torno presto']],
