		['rabbit_house'] = {
			['episode'] = {
				['2'] = [[Ho cercato di aprire la porta, ma è stato inutile. Anche i miei tentativi di sfondarla non hanno avuto successo. Avevo decisamente bisogno di una chiave.]],
				['1'] = [[Una casetta rosa nel bel mezzo delle terre desolate è a dir poco inquietante. Qui mi trovo in bella vista, ma forse è ciò che mi serve. Al suo interno potrei trovare della pittura e qualche uovo.]],
				['12'] = [[La quinta lampadina si è accesa.]],
				['11'] = [[La quarta lampadina si è accesa.]],
				['13'] = [[La sesta lampadina si è accesa.]],
				['14'] = [[Dopo aver acceso tutte le lampadine, ho posizionato il peso.]],
				['15'] = [[Dopo aver lanciato il peso, tutte le lampadine si sono spente.]],
				['16'] = [[Dopo aver raggiunto l'ingresso, ho notato che entrambe le catene sulla porta centrale erano scomparse.]],
				['6'] = [[Dopo aver raggiunto l'ingresso, ho notato che una delle catene sulla porta centrale era scomparsa.]],
				['5'] = [[Il muro alla fine dell'arena si è sollevato e dalla stessa direzione ho sentito provenire un ruggito.]],
				['4'] = [[Oltre la porta sulla sinistra c'era un'arena rotonda e maestosa. Avrei davvero dovuto combattere contro qualcuno lì dentro?<new_line><new_line>Al centro si trovava una grande leva.]],
				['3'] = [[L'interno della casa era molto più grande di quanto apparisse dall'esterno. Alla fine del gigantesco ingresso c'erano tre porte. Quella centrale era bloccata da due enormi catene.]],
				['20'] = [[Ho riaperto la scatola e messo gli occhi sulla tanto desiderata pittura.]],
				['19'] = [[Ho messo la mia offerta nella scatola e l'ho richiusa. Vediamo se ha funzionato oppure no.]],
				['18'] = [[La scatola era vuota. Il mio istinto da asso dei rompicapi mi suggerisce di mettere qualcosa all'interno della scatola.]],
				['17'] = [[Al centro della stanza c'era una piattaforma sopra la quale era esposta una scatola decorata.]],
				['7'] = [[La stanza sulla destra si è rivelata essere piccola e austera. Dal soffitto pendevano sei lampadine spente e al centro della stanza c'era un peso.]],
				['8'] = [[La seconda lampadina si è accesa.]],
				['9'] = [[La prima lampadina si è accesa.]],
				['10'] = [[La terza lampadina si è accesa.]]
			},
