		['emba_craft_easy3'] = {
			['episode'] = {
				['2'] = [[Il condotto di ventilazione è troppo stretto per infilarcisi. Quindi, l’origine di queste piante rimarrà un mistero. Per ora, posso solo distruggere l'imbocco del condotto in superficie.<new_line>Potrei lanciare dentro qualcosa di infiammabile o esplosivo, non avendo altre opzioni.]],
				['1'] = [[L'apertura del condotto di ventilazione è al centro di una sfera di piante intrecciate. È piuttosto strano che stiano crescendo così e siano emerse in superficie qui.]],
				['3'] = [[C'è stato un rombo sottoterra, poi è crollato qualcosa. Sembrava che i gambi e le radici nere tremassero da soli e non per la scossa.<new_line><new_line>Per un paio di secondi c'è stato il silenzio assoluto. Tutto questo verde appassirà e morirà, ora che è separato dalle sue radici. Già adesso, le foglie e le escrescenze ammuffite sembrano essersi accartocciate. Ottimo lavoro.]]
			},
			['choice'] = {
				['5'] = [[Lancia gli esplosivi]]
			}
		},
		['emba_navigator_fight'] = {
			['episode'] = {
				['2'] = [["Ben fatto, ti ho osservato. Bene, vai pure dal Biologo, devi aiutarlo con la sua ricerca. Portagli la scatola nera. E già che ci sei, renditi utile nel suo laboratorio<new_line>Non dimenticare di estrarre la biomassa dai pezzi di micelio. Puoi farlo con un coltello, ma ti suggerisco un estrattore. Puoi ottenerne uno scambiandolo con dei dischetti dagli scienziati del reparto Consumabili.<new_line>Che altro...? Hai memorizzato tutto ciò che riguarda la città della scienza, o devo ripetertelo?"]],
				['1'] = [[Un branco di biomutanti cammina lungo la strada. Stanno cercando qualcosa da mangiare.]],
				['3'] = [["In breve: per migliorare il laboratorio, hai bisogno di sporafiori e genofrutti, che crescono nelle città della scienza piene di vegetazione.<new_line>Per entrarci ti serviranno una tuta AIDA e del diserbante, che puoi ottenere scambiandoli con dei dischetti dagli scienziati del reparto Consumabili.<new_line>Per far sì che il Biologo crei armi e ogni sorta di cose interessanti, hai bisogno di biomassa e la puoi estrarre dai biomutanti. Questo è tutto. Alla prossima!"]]
			},
			['choice'] = {
				['2'] = [["Ho memorizzato tutto. Alla prossima."]],
				['1'] = [["Ripetilo."]]
			}
		},
		['help_hunter3'] = {
			['episode'] = {
				['1'] = [[Ridendo sguaiatamente, la strega lancia incantesimo dopo incantesimo verso il nascondiglio del cacciatore. La sua arma si trova a poca distanza dal suo capanno. Devo attaccare la strega prima che lei riesca a raggiungerlo.]]
			}
		},
		['santa_cyber'] = {
			['choice'] = {
				['2'] = [["Ci servono informazioni su..."]],
				['1'] = [["Ciao, abbiamo bisogno..."]],
				['3'] = [["E adesso?"]]
			},
			['episode'] = {
				['2'] = [["Bada a come parli, Cyberbabbo! E voi, amici, tenete d'occhio le tasche per non farvi rubare niente."]],
				['1'] = [["Ehi, choomba, come va? Un attimo, chi ha invitato alla festa questo venduto della corporazione?"]],
				['5'] = [["Che cos'altro possiamo fare? Andremo al concerto e proveremo a parlargli di nuovo. Compagno Naffare, penso sia meglio che resti qui."]],
				['4'] = [["Uh aaah..."]],
				['3'] = [["Fottiti! Me ne vado da qui. Chiunque abbia voglia di suonare un po' di rock insieme a me sul palco, venga al concerto."]]
			}
		},
		['embacity_biolab_6'] = {
			['episode'] = {
				['2'] = [["Possiamo dire così, sì. Chiamami Emissario. Vedo che sei già al corrente di qualcosa e che hai fatto molto. Ma non avresti dovuto interferire con i miei piani, ho passato tanto tempo a lavorare sull'Ibridatore. Quindi ora tocca a te morire."]],
				['1'] = [["Una nuova seccatura... Che c'è, quell'idiota di Gerin non ce l'ha fatta? In ogni caso, sono felice quando vi uccidete."]],
				['3'] = [[Nella sala ho visto un uomo vestito con una tuta protettiva pesante che si aggira tra le autoclavi della sala.<new_line>Notandomi, si prepara ad attaccare.]]
			},
			['choice'] = {
				['1'] = [["È la tua voce quella di cui parlava Gerin?"]]
			}
		},
		['newyearEvent_energy_medium'] = {
			['episode'] = {
				['2'] = [["Furfanti. La nostra fabbrica sfrutta il Natale e i miracoli per fare pubblicità, e a loro non sta bene. Per questo motivo, sono i nostri diretti concorrenti e dobbiamo occuparcene. Siete pronti a ristabilire la giustizia?"]],
				['1'] = [["Oh oh salve, mio caro amico! Nella nostra fabbrica i problemi non mancano di certo, ma ho finalmente scoperto cosa c'è che non va. I nostri maledetti concorrenti stanno interferendo con la nostra operazione! Potresti aiutarmi a sabotare un po' le loro fabbriche?"]],
				['11'] = [[Ho estratto la pergamena e ho iniziato a leggerla. Quando Naffare mi ha visto, ha iniziato a fare lo stesso in fretta e furia. Dopo aver finito di leggerla, è apparso un lampo blu luminoso e qualche secondo dopo mi sono ritrovato in una terra desolata.]],
				['10'] = [[Dopo esserci allontanati a distanza di sicurezza dal blocco di ghiaccio, Naffare ha tirato fuori il detonatore e l'ha premuto. Si è sentita un'esplosione assordante.<new_line>"Ecco fatto! Abbiamo fatto un buon lavoro e una buona azione! La sconfitta dei nostri concorrenti farà prosperare la fabbrica!"]],
				['9'] = [[Dopo aver finito di raccogliere gli ornamenti, ci siamo messi i sacchi in spalla e siamo andati verso l'uscita. Tuttavia, una banda di furfanti ci bloccava la strada. Dopo esserci riparati dietro l'angolo, Naffare ha sussurrato:<new_line>"Possiamo trovare un'altra uscita, ma sarà difficile perché i sacchi sono pesanti. O magari è meglio occuparsi di questo gruppetto e procedere da questa parte?"]],
				['8'] = [["Non preoccuparti, mio caro amico. È da tantissimo tempo che dono gentilezza e miracoli alla gente. So perfettamente distinguere ciò che è giusto da ciò che è sbagliato. Prendi un sacco e riempilo di ornamenti."]],
				['7'] = [[Dopo aver piazzato le mine, abbiamo proseguito e siamo finiti in una stanza piena di decorazioni natalizie.<new_line>"Oh, che splendido regalo! Per fortuna siamo venuti qui. Getta tutto nei sacchi."<new_line>Dopo aver detto queste parole, Naffare ha tirato fuori due sacchi rossi.]],
				['6'] = [[Una volta finito di distruggere i veicoli, abbiamo continuato la nostra esplorazione. La stanza seguente era il deposito di carburante.<new_line>"Ottimo, qui possiamo piazzare delle mine! Prendi i fuochi d'artificio."<new_line>Naffare mi ha consegnato una scatola di fuochi d'artificio.]],
				['5'] = [[A quanto pare nel blocco di ghiaccio ci sono innumerevoli entrate nascoste, e noi ne abbiamo varcato una. Siamo finiti in quello che sembra essere un garage con una vasta gamma di veicoli.<new_line>"Prendi il martello e distruggi tutto ciò che vedi!"]],
				['4'] = [[Abbiamo dovuto camminare qualche miglio prima di arrivare a un enorme blocco di ghiaccio. Naffare mi ha sussurrato delle parole.<new_line>"Seguimi. Oh, tieni questa pergamena portale. Se ti rendi conto di non potercela fare, leggi la pergamena ed essa ti tirerà fuori da qui."]],
				['3'] = [["Ne sono sicuro al 100%! La competizione è il bene più prezioso. La nostra civiltà si fonda su dei pilastri. E come facciamo a sapere chi è il pilastro e chi l'ultimo arrivato se non facciamo del nostro meglio per competere l'uno con l'altro? O saranno loro a vincere e a dimostrare di avere ragione, oppure saremo noi! Allora, siete pronti?"]]
			},
			['choice'] = {
				['2'] = [["Sono pronto."]],
				['1'] = [["Quali concorrenti? Come sono arrivati fin qui?"]],
				['10'] = [[Trova un'altra uscita]],
				['9'] = [["Rubare non è sbagliato?"]],
				['8'] = [[Metti gli ornamenti nel sacco]],
				['7'] = [[Piazza le mine nella stanza]],
				['6'] = [[Fuggi attraverso un portale]],
				['5'] = [[Distruggi ogni cosa con un martello]],
				['4'] = [[Segui Babbo Naffare]],
				['3'] = [["Sono dei pochi di buono... Ma sei sicuro che ci siano loro dietro i problemi alla fabbrica?"]]
			}
		},
		['immortal_love'] = {
			['episode'] = {
				['2'] = [[La ragazza esce dal sarcofago e assale la mummia, prima di sparire nelle profondità della caverna. A quanto sembra, il rituale le ha conferito un potere speciale. È un peccato non aver avuto nemmeno il tempo di parlare: aveva un nonsoché di familiare. Spero riesca a tornare a casa. Quanto a me, farò meglio a uscire da questo postaccio.]],
				['1'] = [[Posiziono l'esca e attendo. Qualche minuto più tardi appaiono degli scorpioni, che mi accingo a seguire fino all'entrata di un covo colossale. A furia di schivare serpenti, ragni e altre creature striscianti, mi sembra di aver speso centinaia d'anni a vagare per i suoi corridoi sotterranei. Finalmente, scorgo il bagliore di una torcia e odo un roco cantilenare provenire dalla fine del lungo tunnel. La mummia si trascina attorno a un sarcofago al centro della caverna, proseguendo imperterrita il suo macabro rituale. È ora di porre fine a questa idiozia.]]
			}
		}
	},
