	['respected'] = [[Rispettato]],
	['polar_fox'] = [[Volpe artica]],
	['emba_suit3'] = {
		['description'] = [[Un nuovo prototipo sviluppato dal Progresso. Si usa per lavorare nelle zone più pericolose delle città invase dalla vegetazione.]],
		['name'] = [[AIDA 03]]
	},
	['dandelion'] = {
		['name'] = [[Dente di leone]],
		['description'] = [[Si trova nelle terre desolate e sulle spiagge.]]
	},
	['passengerPhrase2'] = [[Non è ciò che avevamo stabilito!]],
	['notOpenBaseNpc'] = [[Questo insediamento non è ancora aperto]],
	['militia'] = [[Miliziano]],
	['ignoreWallForShooting'] = [[I muri non bloccano i colpi di arma da fuoco]],
	['expNewYear'] = [[Esperienza delle buone azioni]],
	['gift_ammo'] = {
		['name'] = [[Regalo con munizioni]]
	},
	['svd_parts'] = {
		['name'] = [[Parti per SVD]]
	},
	['meat'] = [[Carne]],
	['book_blacksmith'] = {
		['name'] = [[Manuale da fabbro]]
	},
	['story'] = {
		['guest'] = {
			['episode'] = {
				['10'] = [[La casa ha un ingresso e tre stanze: una cucina, un soggiorno e una stanza da letto. Dall'ingresso, si può andare fuori o continuare per la cucina o il soggiorno. Bisogna passare dal soggiorno per andare nella stanza da letto.<new_line><new_line>Le finestre della cucina e della stanza da letto sono aperte.<new_line><new_line>La cucina ha una cantina.<new_line><new_line>Oltre alla casa principale, nella proprietà ci sono una dependance e un fienile.]],
				['68'] = [[Sono passati pochi minuti ed era tutto tranquillo.]],
				['30'] = [[Ho sceso le scale facendo molta attenzione. I gradini erano sbilenchi e il montante sinistro ha colpito la trave a ogni passo.<new_line><new_line>Ho acceso la torcia e ho dato un'occhiata in giro. Legno marcio e annerito coperto di ragnatele. Qua e là, c'erano contenitori di vetro e di latta che luccicavano al buio. Su alcuni era visibile della muffa. Dei pezzi di legno e di vetro galleggiavano su un liquido sconosciuto che si era riversato sul pavimento. La cassa di legno che Brufolo aveva aperto è accanto alle scale, vuota. Non c'era più niente da fare lì. Sono tornato su, mi sono fermato sulla soglia e ho pulito le suole delle scarpe con uno straccio.]],
				['20'] = [[Non appena ho guardato dentro dalla finestra, ho sentito un altro rumore dalla cantina, seguito da un urlo trionfante. Un minuto dopo, Brufolo è uscito sporco ma felice dalla cantina con in mano una bottiglia di vino da tre litri.<new_line><new_line>'Guarda qua! Perché diamine ha chiuso la cassa con i chiodi? Ho le mani piene di schegge! Se mai incontrerò il proprietario di questa casa, gli torcerò il collo.']],
				['50'] = [[È tutto come prima. Brufolo è nella cantina. Ogni tanto sento le assi marce del pavimento che scricchiolano.]],
				['40'] = [[Mi sono arrampicato nella stanza facendo meno rumore possibile, con il filo del ferro da stiro pronto. Alle spalle del bandito, non gli ho tolto gli occhi di dosso: non si muoveva. Un altro paio di passi e sarei riuscito ad attorcigliargli il filo al collo.<new_line><new_line>Ho schiacciato qualcosa col piede. Ho guardato subito a terra e ho visto la lente rotta di un paio di occhiali. Maledizione!<new_line><new_line>Ivan si è girato, mi ha visto e ha gridato.]],
				['70'] = [[Con voce frustrata, il leader ha detto: 'Sei ubriaco. Vai a dormire'.<new_line><new_line>'Tu... tu...', ha detto con voce tremolante lo sfigato. 'Mi hai rotto gli occhiali!'.<new_line><new_line>'Vai a dormire', ha ripetuto il cosacco alzando la voce. Dopo un paio di secondi, dalla fessura ho visto Ivan ritornare nella stanza da letto. 'Brufolo, pulisci quella brodaglia e vai a prendere da mangiare.'<new_line><new_line>I banditi hanno smesso di parlare.]],
				['60'] = [[È stata solo fortuna, incredibile e surreale fortuna se sono sopravvissuto alla lotta. Incredulo di avercela fatta, ho girato i corpi. Il cosacco e Tema erano morti.<new_line><new_line>Ne manca uno.]],
				['24'] = [[Sono nell'ingresso.<new_line><new_line>Non c'è molta luce. C'è della brace nel fornello. Sul lato opposto, ci sono un appendiabiti e un mobiletto antico con uno specchio.<new_line><new_line>La porta del soggiorno è chiusa. Dall'uscio senza porta della parete opposta, vedo la cucina e il portello aperto della cantina.]],
				['14'] = [[Sono nella stanza da letto.<new_line><new_line>A parte l'orribile carta da parati a pois rossi e il letto matrimoniale antico, non sembra esserci niente di particolare. Accanto alla porta c'è una toilette con flaconi di cosmetici di tutti i tipi e, nell'angolo opposto, c'è un grande armadio e un asse da stiro.<new_line><new_line>Non sento alcun rumore dal soggiorno, anche se la porta è leggermente socchiusa.]],
				['44'] = [[Ho estratto il coltello e ho sferrato un colpo preciso. Gli occhi di Ivan si sono spalancati sporgendo verso fuori. Si è premuto il collo emettendo un terribile suono rauco. Ha cercato di girarsi sulla schiena e sollevarsi, ma io l'ho spinto verso il letto con il mio peso. Dopo venti secondi, era tutto finito.<new_line><new_line>Ho ascoltato, ma non ho sentito alcun rumore dietro la porta.]],
				['34'] = [[Il cosacco è ancora seduto al tavolo. Sta strimpellando la chitarra e canticchia qualcosa. Brufolo striscia sul pavimento con un panno per pulire il vino versato e lo strizza in una bacinella. Tema è ancora seduto sul divano; pare che si sia appisolato. Accanto a lui c'è il fucile d'assalto assemblato e una manciata di cartucce.]],
				['38'] = [[Mi sono congelato il culo per buoni dieci minuti, ma in cucina non è venuto nessuno. Devo cambiare piano.]],
				['28'] = [[Nel periodo di permanenza nella dacia dell'ex ispettore, i banditi sono riusciti a consumare tutto il cibo. Non ho visto neppure una briciola commestibile da nessuna parte, tranne il latte condensato e le fette biscottate. Persino i barattoli di spezie costose sono stati svuotati.<new_line><new_line>Tutti i piatti, compresi i piatti del servizio buono, sporchi di grasso e appiccicosi, sono ammassati dentro e attorno al lavandino.]],
				['58'] = [[Tema era molto nervoso. Ha spinto la porta con così tanta forza, che per poco non staccava la ringhiera. Non appena se n'è andato dal portico, gli ho attorcigliato il filo attorno al collo.<new_line><new_line>Ma non è stato come con Ivan il mingherlino. Tema ha fatto una cosa che non mi aspettavo: ha opposto resistenza con le gambe e mi ha spinto verso dietro.]],
				['48'] = [[Tema, sull'uscio, ha chiamato Ivan che, ovviamente, non ha risposto. Il gigante si è avvicinato al letto e si è irrigidito. Ho seguito il suo sguardo. Di sicuro c'era una pozza di sangue vicino al letto.<new_line><new_line>'Cosacco, Ivan è stato ucciso', ha gridato il gigante.<new_line><new_line>Devo andarmene subito da qui.]],
				['2'] = [[Prima ho dovuto controllare tutta la casa senza farmi scoprire. Impugnando il coltello e la pistola, sono entrato nella proprietà.<new_line><new_line>Lungo il viottolo sono cresciuti dei grandi cespugli di uva spina, quindi ho deciso di appostarmi lì. Spostando i rami con le spine aguzze, sono strisciato al centro del cespuglio. Maledizione, le spine mi pungevano attraverso i vestiti!<new_line><new_line>Stavo per cercare un altro posto con una buona visuale, quando tre uomini sono usciti dalla casa.]],
				['1'] = [[La dacia dell'ispettore Gavrilov è l'unico edificio ancora in piedi nel villaggio alla periferia sud di Brjansk. L'estensione del terreno (più di mezzo acro) ha impedito che il fuoco raggiungesse la casa.<new_line><new_line>Il cancello del giardino si tiene in piedi grazie a un unico cardine e gran parte della staccionata è ormai a pezzi sul terreno. Il cancello dà su un viottolo calpestato che porta alla casa. Il viottolo è pieno di mozziconi di sigaretta recenti.<new_line><new_line>La casa è abitata, ma non necessariamente da Gavrilov e la sua famiglia.]],
				['69'] = [[Il cosacco gli ha preso la bottiglia dalle mani, ha tirato il tappo, ha odorato e ha fatto una smorfia.<new_line><new_line>'Brodaglia per maiali.'<new_line><new_line>'Brodaglia per maiali?', ha ripetuto Brufolo furioso. 'Questo è vino pregiato fatto in casa, non quel solvente che vendevano una volta nei negozi. Salute!<new_line><new_line>'Andiamo', ha ordinato il leader bruscamente. Ha restituito la bottiglia a Brufolo ed è andato nel soggiorno. Brufolo l'ha seguito zoppicando.]],
				['11'] = [[Ci sono due persone nel soggiorno: Tema il gigante e Ivan, un uomo calvo dall'aspetto un po' da sfigato.<new_line><new_line>Tema è seduto su un divano consumato. Ha smontato un fucile d'assalto e ora sta pulendo il pistone e il tubo del gas con un panno. Ha un AK è alle spalle, il che significa che sta pulendo l'arma di qualcun'altro.<new_line><new_line>Ivan sta facendo un solitario seduto al tavolo. Quasi tutta la superficie del tavolo è ricoperta di giornali, sopra i quali ci sono avanzi di cibo, bicchieri e barattoli vuoti, forchette e coltelli sporchi, un posacenere e un grande samovar a carbone.]],
				['61'] = [[La dependance è una baracca di legno sbilenca in cui un'asse con un foro circolare funge da bagno. Dopo essere corso dentro, riprendo fiato e poi aspetto.<new_line><new_line>Dopo neppure dieci secondi la porta si è aperta. Tema mi ha visto e ha spalancato la bocca per la sorpresa.]],
				['71'] = [[Ho sottovalutato la prontezza di riflessi del cosacco. Mentre correvo verso la staccionata, dei proiettili mi sono passati fischiando sopra la testa. Mi sono buttato a terra. Ho guardato attorno e ho visto un fucile nella finestra del soggiorno e il leader barbuto dei banditi che diceva di mirare.<new_line><new_line>Devo alzarmi o mi crivelleranno di proiettili.]],
				['55'] = [[Non sono riuscito a vedere lì vicino il lucchetto del portello, ma l'attizzatoio è entrato perfettamente nel chiavistello. Brufolo non uscirà da lì tanto presto. Quando avrò finito con i suoi amici, tornerò e gli sparerò con il suo stesso fucile.<new_line><new_line>L'attizzatoio non si nota neanche sul pavimento nero. Ora devo sbrigarmi, prima che il cosacco e Tema pensino di controllare se Brufolo si è addormentato nella cantina.]],
				['65'] = [[Sei bravo a cantare', ho commentato io entrando nel soggiorno.<new_line><new_line>Il cosacco ha suonato un ultimo accordo con la chitarra. Se lo avevo colto di sorpresa, non lo ha dimostrato]],
				['35'] = [[Ci vuole molto? Voglio mangiare', ha detto Tema sbadigliando.<new_line><new_line>'Questo coglione ha sprecato mezza bottiglia di vino pregiato', ha borbottato Brufolo. 'Si merita un bel calcio in culo.'<new_line><new_line>'Il tuo vino pregiato puzza di piscio di mucca. Ehi, cosacco, cantaci qualcosa. Quella che ci hai cantato fuori Belgorod, te la ricordi? 'Il cosacco è andato in terra straniera...' Mi ha colpito, sai?'<new_line><new_line>Il cosacco non ha risposto.]],
				['45'] = [[Lasciando cadere un'estremità del filo sotto il collo di Ivan, ho incrociato le estremità e ho stretto con tutta la mia forza. Gli occhi di Ivan si sono spalancati. Ha fatto pressione con la mano sul collo emettendo un terribile suono rauco. Ha cercato di girarsi sulla schiena e sollevarsi, ma io l'ho spinto verso il letto con il mio peso.<new_line><new_line>Dopo un minuto, è tutto finito. Ho ascoltato, ma non ho sentito alcun rumore dietro la porta.]],
				['15'] = [[L'armadio è pieno di vecchi abiti, ma una persona minuta riuscirebbe a entrarci. Potrei anche nascondermi sotto il letto: il paravento impedirebbe a quelli che entrano di vedermi.<new_line><new_line>Si potrebbe anche nascondere un corpo qui sotto.<new_line><new_line>Ho trovato un ferro da stiro rotto sulla mensola più alta dell'armadio. Il filo è praticamente staccato. Ho pensato che si potesse usare il filo come legatura. Ho tirato il filo dal ferro e tagliato la presa con il coltello.]],
				['25'] = [[Mentre avanzavo lentamente nell'ingresso, le assi del pavimento hanno scricchiolato a ogni passo. Per fortuna, la porta del soggiorno era chiusa e non mi hanno sentito.<new_line><new_line>Tanti cappotti pesanti e una giacca mimetica invernale sono ammucchiati sotto l'appendiabiti e accanto al mobiletto. Dall'aspetto, si direbbe che i cappotti siano rimasti una settimana in una palude.<new_line><new_line>Dopo aver rovistato dietro il fornello, ho trovato un attizzatoio, abbastanza robusto per fungere da arma, ma anche per essere utilizzato per altri scopi. L'ho preso.]],
				['49'] = [[Tema, sull'uscio, ha chiamato Ivan che, ovviamente, non ha risposto. Il gigante si è avvicinato al letto chiamandolo a voce più alta.<new_line><new_line>'È collassato', ha gridato il cosacco. 'Lo sveglio?'<new_line><new_line>'Lascialo dormire', ha risposto il leader. 'Vieni a mangiare.'<new_line><new_line>Tema se n'è andato e ha chiuso la porta. Mi sono lasciato andare contro la parete vicino alla finestra e ho provato a respirare di nuovo normalmente. C'è mancato poco... Ora devo occuparmi di Brufolo.]],
				['5'] = [[Ho aspettato un po' e poi ho percorso il perimetro della casa. Sono riuscito a carpire qualche informazione guardando attraverso le finestre.<new_line><new_line>Ci sono solo tre stanze nella casa: una stanza da letto, un soggiorno/sala da pranzo e una cucina. Bisogna passare dal soggiorno per andare nella stanza da letto, ma le finestre aperte della cucina e della stanza da letto sono aperte e sono grandi abbastanza per consentire a una persona di entrare. La finestra del soggiorno è chiusa dall'interno e la finestra dell'ingresso è piccola.<new_line><new_line>In mezzo alla cucina, ho visto un portello aperto di una cantina.]],
				['4'] = [[Dietro alla dependance pericolante c'è un piccolo fienile. È lì che il gigante ha portato la ragazza. Brufolo ha provato a intrufolarsi più tardi, ma l'omone l'ha afferrato dalla giacca e l'ha tirato.<new_line><new_line>'Dove pensi di andare? Il cosacco non ha dato il permesso.'<new_line><new_line>'Tema, e dai. Volevo solo fare due chiacchiere!', ha detto Brufolo lamentandosi.<new_line><new_line>'Il cosacco non ha dato il permesso', ha ripetuto il gigante chiudendo la porta. 'La vuole tutta per sé.']],
				['39'] = [[La porta del soggiorno è chiusa. Il letto è a malapena visibile dalla finestra e devo infilare leggermente la testa dentro.<new_line><new_line>Ivan è seduto sul bordo del letto, con la testa penzolante in avanti. Un grande livido si sta formando sul suo viso. Un revolver spunta dalla tasca della sua giacca. Gli occhiali di Ivan sono sulla toilette; la montatura è rotta.<new_line><new_line>Se è abbastanza ubriaco o se non vede bene senza gli occhiali, posso attaccarlo di sorpresa. Ma forse non è il caso di rischiare: posso aspettare che si addormenti.]],
				['32'] = [[Sono all'ingresso.<new_line><new_line>Non c'è molta luce. C'è della brace nel fornello. Sul lato opposto, ci sono un appendiabiti e un mobiletto antico con uno specchio.<new_line><new_line>La porta del soggiorno è chiusa e non sento alcun rumore provenire da quella stanza. Dall'uscio senza porta della parete opposta, vedo la cucina e il portello aperto della cantina.]],
				['22'] = [[Tutti e quattro i banditi sono nel soggiorno.<new_line><new_line>Tema è ancora seduto sul divano a pulire la canna del fucile d'assalto. Credo che si rilassi in quel modo.<new_line><new_line>Il cosacco, Ivan e Brufolo stanno giocando a carte seduti al tavolo. Il vino è già stato versato nei bicchieri, ma il cosacco non ne ha ancora bevuto neppure un goccio. Lo sfigato beve con moderazione, ma quello coi brufoli riempie in continuazione il bicchiere. Ogni tanto, ficca la mano sporca nel barattolo da cinque litri di crauti e ne mangia una manciata.]],
				['12'] = [[Sono nell'ingresso.<new_line><new_line>Non c'è molta luce. C'è della brace nel fornello. Sul lato opposto, ci sono un appendiabiti e un mobiletto antico con uno specchio.<new_line><new_line>La porta del soggiorno è chiusa e non sento alcun rumore provenire da quella stanza. La porta della cucina non c'è proprio, infatti da lì riesco a vedere la porta aperta della cantina e le spalle del cosacco. Se si gira da questa parte mi vedrà!]],
				['72'] = [[C'è stata una pausa. Improvvisamente il silenzio è stato interrotto dal lamento di Tema.<new_line><new_line>'Che problemi hai?'<new_line><new_line>'Figlio di... Lo stomaco si sta contorcendo... Santo cielo! Devo andare... devo andare subito...']],
				['62'] = [[Ho approfittato di questa confusione e ho infilzato il coltello nelle costole di Tema, nel cuore o lì vicino. Il gigante ha gridato e ha provato ad afferrarmi, ma io ho indietreggiato.<new_line><new_line>'Ti...', ha detto con voce stridula cadendo in avanti. Mi sono dovuto appoggiare alla parete per rimanere in piedi. Il corpo si è contorto un altro po' e poi ha smesso di muoversi.<new_line><new_line>Ne mancano due.]],
				['52'] = [[Spostare il frigorifero è stato più facile a dirsi che a farsi. Mi sono sforzato tantissimo, ma sono riuscito a spostarlo solo di poco dalla posizione originale. Il rumore dello strofinamento avrebbe svegliato pure i morti. Il frigo era appoggiato su una lastra di alluminio.<new_line><new_line>'Ehi, Tema, sei tu? Che ci fai quassù?', ha gridato Brufolo dalla cantina.]],
				['42'] = [[Mi sono arrampicato nella stanza facendo meno rumore possibile e mi sono avvicinato piano piano verso il letto. Non gli ho tolto gli occhi di dosso: non si muoveva. Un altro paio di passi e...<new_line><new_line>Ho schiacciato qualcosa col piede. Ho guardato subito giù e ho visto la lente rotta di un paio di occhiali. Per fortuna il rumore non ha svegliato Ivan.<new_line><new_line>Non si è sentito nessun suono dietro la porta chiusa. O questo posto è insonorizzato bene o i banditi nel soggiorno non stavano parlando. In ogni caso, ho dovuto sbrigarmi senza fare rumore e senza lasciare tracce.]],
				['46'] = [[Prima di uscire, ho sentito un suono raschiante dalla finestra della cucina. Mi sono avvicinato lentamente e ho guardato dentro. Tema il gigante era in piedi vicino alla cantina aperta con in mano dei barattoli di latte condensato e fette biscottate. L'orribile viso di quello coi brufoli faceva capolino dalla cantina.<new_line><new_line>'Ne ho abbastanza di questo schifoso latte condensato! Mangialo tu', ha detto Brufolo. 'Voglio mangiare cibo normale.']],
				['36'] = [[Sono in cucina.<new_line><new_line>La porta della cantina è aperta in mezzo alla stanza. Dei gradini di legno portano giù.<new_line><new_line>Sulla parte sinistra della cucina, c'è un lungo piano che copre la parete. Su di esso, ci sono sei confezioni aperte di latte condensato e due barattoli di vernice. In un barattolo ci sono dei pezzi di fette biscottate, nell'altro del latte condensato misto roba marcia del frigo.<new_line><new_line>A destra, c'è un frigorifero Minsk sporco, un lavandino con una pila di piatti dentro e un fornello a gas cosparso di una specie di chicchi.]],
				['26'] = [[Ehi, la smetti di mangiare quella schifezza? Mi stai disgustando! O giochiamo o me ne vado!', ha detto Ivan, credo.<new_line><new_line>'Eh? Ne vuoi un po'? Bastava dirlo. Si abbina molto bene al vino', ha risposto Brufolo cordialmente.<new_line><new_line>'Non ne voglio! E non voglio neppure il tuo vino!', ha detto Ivan infastidito.]],
				['16'] = [[Il piano avrebbe funzionato se nella stanza fosse entrata una persona sola, ma Tema e Ivan sono entrati insieme.<new_line><new_line>Tema ha un'arma in spalla, e l'impugnatura di un revolver fuoriesce dalla tasca della giaccia di Ivan.<new_line><new_line>Ho due opzioni: uscire dal guardaroba e ucciderli prima che possano chiamare rinforzi... o, come un codardo, aspettare nel guardaroba che vadano via. Non mi piace l'idea di essere un codardo, ma non credo che un attacco a sorpresa andrebbe a buon fine, soprattutto data la stazza di Tema.]],
				['95'] = [[Mi sono arrampicato nella stanza facendo meno rumore possibile e mi sono avvicinato piano piano verso il letto. Non gli ho tolto gli occhi di dosso. Non si è mosso. Un altro paio di passi e...<new_line><new_line>Non si sentiva nessun rumore dietro la porta chiusa. O questo posto è insonorizzato bene o i banditi nel soggiorno non stavano parlando. In ogni caso, dovevo sbrigarmi senza fare rumore e senza lasciare tracce.]],
				['94'] = [[In cucina c'era un uomo con una barba folta e un cappello da cosacco. Non aveva l'orecchio destro e aveva una vecchia cicatrice lungo tutto il collo. L'uomo parlava con qualcuno nella cantina. Molto probabilmente, Brufolo.]],
				['93'] = [[Ho ispezionato ogni centimetro della casa per trovare qualche indizio, ma non c'era niente. Dove può essere finito l'ispettore? Se la dacia fosse stata abbandonata, probabilmente avrei trovato qualcosa qui. Ma gli ospiti indesiderati sono riusciti a lasciare il segno.<new_line><new_line>Era il momento di liberare la prigioniera. Ho preso la chiave dal corpo di Tema e sono andato nel fienile.]],
				['92'] = [[Dovrò sbarazzarmi dei banditi, ma sono in quattro e non ho speranze di farcela in combattimento.<new_line><new_line>Ma chi lo dice che l'approccio diretto è l'unica soluzione? Ho qualche vantaggio: i miei avversari non sanno della mia presenza. Saranno anche più forti di me, ma ho la furtività e il fattore sorpresa dalla mia parte. Un po' di buona fortuna non guasterebbe di certo.]],
				['91'] = [[Nel soggiorno, di nuovo una vista familiare: il gigante stava oleando le componenti di un fucile. Di fronte a lui, c'era un altro, uno magro, calvo e con gli occhiali. Giocava al solitario e canticchiava a bassa voce. Quello dev'essere Ivan, quello che ha nominato Brufolo.]],
				['90'] = [[Ho sbattuto con la nuca sul gradino del portico e il corpo pesante di Tema mi è caduto addosso. Un secondo dopo, Tema ha afferrato il filo e ha strappato un'estremità dalla mia presa con incredibile forza. Si è rimesso in piedi barcollando, poi mi ha sollevato e mi ha spinto via con tutta la sua forza.<new_line><new_line>Sono caduto a terra e ho visto Tema e il cosacco correre entrambi verso di me. Il cosacco aveva in mano un fucile.]],
				['9'] = [[Sono fuori. Non c'è nessuno in giro. Nessuno può vedermi dalle finestre.]],
				['89'] = [[Il gigante ha iniziato a cadere in avanti per colpa della rincorsa. Il suo enorme corpo è caduto a terra con un tonfo ed è rimasto fermo lì senza muoversi.<new_line><new_line>Sembrava morto, ma non ho avuto il tempo di controllare. Ho sentito la porta del soggiorno spalancarsi. Il cosacco stava correndo verso di me.]],
				['88'] = [[Per poco un barattolo di marmellata non mi ha colpito in viso. Mi sono riparato la testa con il braccio all'ultimo secondo, ma il bandito si è scagliato contro di me e mi ha spinto contro il muro. Ho colpito una mensola di legno, sono caduto e ho sbattuto con la testa. So solo che dopo il mostro brufoloso mi ha morso la mano, quella in cui avevo il coltello. Ho gridato per il dolore.<new_line><new_line>'Fermo lì!', ha gridato Brufolo che poi è salito di corsa su per le scale fino alla cucina chiudendo il portello della cantina. 'Torneremo presto!']],
				['79'] = [[Beh, non si sa mai...'. Tema ha alzato le spalle.<new_line><new_line>'E ricorda i poliziotti: 'Ragazzi, avete fatto una bella passeggiata? È ora di tornare in prigione''.<new_line><new_line>'Va bene, è stato il vento', ha sospirato Tema. Sono tornati nel soggiorno.<new_line><new_line>Quando ho avuto la certezza che non sarebbero tornati, sono uscito dall'armadio e ho aperto di nuovo la finestra.]],
				['86'] = [[La porta del soggiorno si è aperta leggermente e Tema si è affacciato con la testa. Aveva delle feste biscottate in mano. Se si fosse affacciato uno o due secondi prima, avrebbe visto le mie gambe sulla finestra. Tema non ha visto niente di sospetto e ha chiuso di nuovo la porta.<new_line><new_line>Ho aspettato un altro po' e poi sono andato in cucina.]],
				['85'] = [[Ridendo Tema ha detto: 'Vai a guardare! Mentre tu controlli, noi ci mangiamo questo'.<new_line><new_line>'A me sta bene, ma quello che trovo è mio', ha risposto Brufolo che poi è scomparso nella cantina. 'Accomodati!'<new_line><new_line>Tema ha riso ed è uscito dalla cucina.]],
				['84'] = [[Ero quasi fuori, quando ho sentito il rumore di uno sparo e un dolore lancinante alla gamba. Quel bastardo non era così ubriaco come pensavo... Sotto la finestra, ho sentito le voci del cosacco, di Brufolo e di Tema.<new_line><new_line>Non arriverò lontano con una gamba ferita. Riusciranno a rintracciarmi seguendo le tracce di sangue.]],
				['76'] = [[Beh, ci hai provato!', ha detto quello brutto con un sorriso malizioso. 'Mi hai migliorato la giornata! Chi hai portato? Una pollastra che non solo è inutile, ma non sta neanche mai zitta!'<new_line><new_line>'Chiudi il becco, Brufolo, se ci tieni alla tua vita', ha ringhiato il gigante. 'Non c'era nessun altro. Se sei così intelligente, la prossima volta vieni pure tu!'<new_line><new_line>'Intelligente? Se fossi stato intelligente, avrei capito che tu e Ivan siete due coglioni il giorno in cui vi ho conosciuti!'. La risata di quello coi brufoli era ancora più brutta della sua bocca.]],
				['82'] = [[Avrei potuto nascondermi sotto il letto, ma la misteriosa scomparsa di Ivan avrebbe allarmato gli altri. Ho avuto un'idea migliore: ho alzato il colletto del bandito per nascondere il collo e l'ho girato sul fianco con le spalle verso la porta. Da questa angolatura, sembrava che dormisse.<new_line><new_line>Ne mancano tre.]],
				['81'] = [[Amico, non ti arrabbiare. Non hai perso molto', ha detto Tema. 'Bevi e ti sentirai meglio.'<new_line><new_line>Lo sfigato ha risposto con un patetico 'Vaffanculo'.<new_line><new_line>Hanno smesso di parlare.]],
				['80'] = [[Ho aperto il frigorifero e le mie narici si sono riempite di un tanfo così forte da provocarmi spasmi in tutta la gola. Sembrava l'odore di un intero frigo andato a male, ma c'era solo una confezione aperta di sardine. In realtà, non c'erano neppure tante sardine nella confezione, solo un liquido torbido giallo.<new_line><new_line>Mmh, la melma delle sardine aveva lo stesso colore del latte condensato...]],
				['8'] = [[(Consiglio:<new_line>i sopravvissuti avversari sono combattenti abili e forti. La tua unica chance di neutralizzarli è furtivamente, uno alla volta. Non farti scoprire!)]],
				['67'] = [[Ho spostato l'attizzatoio e ho aperto il portello della cantina. Brufolo era in fondo alle scale, accecato dalla luce. Senza perdere neanche un secondo, ho afferrato il suo fucile e l'ho fatto fuori.<new_line><new_line>Problema risolto.<new_line><new_line>Un girovago si è dimostrato più forte di quattro robusti uomini armati.]],
				['77'] = [[Sentirà dolore?', ha chiesto Brufolo arrabbiato.<new_line><new_line>'Forse. Cosa dovrei fare? Tagliarle le dita?'<new_line><new_line>'Sarebbe stata ora!', ha protestato Brufolo, ma anziché rispondere Tema lo ha trascinato in casa dal colletto.<new_line><new_line>Stavano entrambi in silenzio. Mentre chiudeva la porta alle sue spalle, l'omone ha gridato:<new_line><new_line>'Le ragazze non ci sono più, quindi niente zuppa! Di nuovo latte condensato e fette biscottate!']],
				['87'] = [[Tema, per l'ultima volta, apri la porta! Tema, porca miseria!', Brufolo si è lasciato andare a una serie di parolacce.<new_line><new_line>Poi è caduto il silenzio. Probabilmente il bandito si era seccato di sbattere sulla porta ed è sceso di nuovo.]],
				['78'] = [[Stavamo ancora lottando quando il cosacco e Brufolo sono entrati nella stanza armati di fucili d'assalto. Avranno sentito l'urlo di Ivan dalla finestra aperta della cucina.<new_line>]],
				['53'] = [[Con il coltello in mano, sono sceso dalle scale. Sfortunatamente, lo scricchiolio dei gradini mi ha tradito.<new_line><new_line>'Chi diavolo è stato?', ha sussurrato Brufolo con voce sinistra.<new_line><new_line>Non sono riuscito a capire dove fosse, perché la cantina era troppo buia e piena di ragnatele.]],
				['63'] = [[Ho pulito il coltello con della carta igienica e sono tornato nell'ingresso. La porta della cantina era ancora chiusa grazie all'attizzatoio, ma la porta del soggiorno era aperta. Ho sentito della musica indistinta provenire da dietro la porta: con la sua delicata voce da baritono, il cosacco stava cantando e suonando con la chitarra una canzone popolare, 'Cari fratelli, cari'.]],
				['73'] = [[Ho estratto il pugnale. Il cosacco ha barcollato e subito una macchia bordeaux si è espansa per tutto il petto.<new_line><new_line>'Il primo dormiva, il secondo l'ho preso alla sprovvista nella dependance. Ho rinchiuso Brufolo nella cantina.'<new_line><new_line>'Capisco', ha borbottato il bandito. 'Furbo... Sei stato bravo... Vorrei...'<new_line><new_line>Non è riuscito a finire la frase. Un brivido gli ha attraversato il corpo prima di accasciarsi a terra.]],
				['83'] = [[Avrei potuto nascondermi sotto il letto, ma la misteriosa scomparsa di Ivan avrebbe sicuramente allarmato gli altri. ho alzato il colletto del bandito per nascondere il collo e l'ho girato sul fianco con le spalle verso la porta. Da questa angolatura, sembrava che dormisse.<new_line><new_line>Il sangue che gli fuoriusciva dalla gola stava bagnando la coperta. Dalla porta, la macchia non si vedrà, ma quando il letto sarà saturo inizierà a gocciolare per terra...<new_line><new_line>In ogni caso, me ne mancano tre.]],
				['13'] = [[Dopo neppure tre passi le assi del pavimento hanno iniziato a scricchiolare. Ho guardato in cucina e ho visto la faccia tranquilla e alquanto indifferente del cosacco, che ha preso il revolver e ha sparato. Non mi ha preso per un soffio.<new_line><new_line>Meno di cinque secondi dopo lo sparo, Brufolo è salito di corsa dalla cantina e altri due sono usciti, sempre correndo, dal soggiorno.]],
				['23'] = [[Sono nella stanza da letto.<new_line><new_line>Non è cambiato niente.<new_line><new_line>Sento la voce stridula di quello coi brufoli nel soggiorno. Sta raccontando barzellette. Ogni tanto, i suoi discorsi incomprensibili vengono interrotti da una grassa risata di Tema, il gigante.]],
				['75'] = [[Un accenno di sorriso è spuntato sulle labbra esanimi del leader dei banditi.<new_line><new_line>'Furbo. Sei stato bravo.'<new_line><new_line>Il cosacco ha smesso di muoversi e ha osservato senza forze la pozza di sangue che si stava formando attorno a lui. Alla fine, la testa del bandito penzolava senza vita e il corpo si è accasciato su un lato.<new_line><new_line>Mi sono avvicinato.<new_line><new_line>Il cosacco non aveva niente in mano.]],
				['43'] = [[Ho assestato un bel colpo con abbastanza forza, ma l'attizzatoio mi ha tradito. È troppo sottile. Imprecando, il bandito si è svegliato. L'ho colpito di nuovo con l'attizzatoio, ma il bandito ha reagito e si è protetto con il braccio all'ultimo secondo. Son dovuto scappare.<new_line><new_line>Ero quasi fuori, quando ho sentito il rumore di uno sparo e un dolore lancinante alla gamba. Quel bastardo non era così ubriaco come pensassi... Sotto la finestra, ho sentito le voci del cosacco, di Brufolo e di Tema.<new_line><new_line>Non arriverò lontano con una gamba ferita. Riusciranno a rintracciarmi seguendo le tracce di sangue.]],
				['27'] = [[Sono in cucina.<new_line><new_line>La porta della cantina è aperta in mezzo alla stanza. Ho guardato dentro e ho visto solo mensole cadenti, grandi bombole scure e ragnatele, tante ragnatele. Dei gradini di legno portano giù.<new_line><new_line>Sulla parte sinistra della cucina, c'è un lungo piano che copre la parete. Su di esso, ci sono sei confezioni aperte di latte condensato e due barattoli di vernice. In un barattolo ci sono dei pezzi di fette biscottate, nell'altro del latte condensato misto a briciole di pane. Sulla parete destra, c'è un frigorifero Minsk sporco, un lavandino con una pila di piatti dentro e un fornello a gas cosparso di una specie di chicchi.]],
				['37'] = [[Scendendo in cantina, per poco non sono caduto dalle scale traballanti. Mi sono aggrappato al freddo muro di mattoni. Il legno marcio mi scricchiolava sotto i piedi.<new_line><new_line>La cantina è umida e molto fredda.]],
				['47'] = [[Il cosacco è ancora seduto al tavolo. Sta strimpellando la chitarra e canticchia qualcosa.<new_line><new_line>Tema è entrato nella stanza e ha appoggiato entrambe le ciotole di fronte al cosacco, che ha chiesto qualcosa, probabilmente su Brufolo. Il gigante ha risposto, ridendo tranquillamente. Il leader sembrava riflettere su qualcosa. Poi ha indicato a Tema la porta della stanza da letto e gli ha detto di chiamare Ivan. Tema ha annuito ed è andato nella stanza. Il cosacco si è grattato dove un tempo aveva l'orecchio destro e poi ha preso di nuovo la chitarra.]],
				['57'] = [[Tema era molto nervoso. Ha spinto la porta con così tanta forza, che per poco non staccava la ringhiera. Non appena se n'è andato dal portico, gli sono saltato davanti e ho affondato il coltello tra le costole. Se non ho preso il cuore, sarà stato solo per una questione di millimetri.<new_line><new_line>'Tu...', ha rantolato Tema. Ha gridato come un elefante ferito e poi è balzato verso di me.]],
				['74'] = [[Sono stati coraggiosi?'<new_line><new_line>Che strana domanda.<new_line><new_line>'Il primo dormiva, l'ho sorpreso nel bagno. Ho rinchiuso Brufolo nella cantina.'<new_line><new_line>Il cosacco ha annuito, ha posato la chitarra e ha preso il fucile che era sul divano.<new_line><new_line>'E come mai non hai preso alla sprovvista anche me, eroe?']],
				['7'] = [[Dove può essere andato Gavrilov?<new_line><new_line>Devo ispezionare la casa, ma non posso farlo finché ci sono quei banditi. Mi ricordo anche della ragazza malandata che il cosacco 'teneva per sé'. Vorrei aiutare anche lei, ma la chiave del fienile ce l'ha uno di quei criminali.]],
				['29'] = [[Ho versato attentamente il liquido dalla lattina al barattolo di latte condensato e l'ho mescolato fino ad amalgamarlo per bene. Il colore era lo stesso, non aveva alcun odore e ho sperato che il sapore non fosse cambiato di molto.<new_line><new_line>Oggi, dopo pranzo, i miei 'amici' staranno, come minimo, male di stomaco.]],
				['3'] = [[Un omone, alto almeno due metri, ha trascinato una ragazza sulla ventina sul portico. Il volto di lei è coperto di tagli e graffi, in bocca aveva una specie di pezza e aveva le mani legate con una corda.<new_line><new_line>Il gigante ha afferrato la corda e ha iniziato a trascinare la ragazza dentro. Un ragazzo con una faccia ricoperta di brufoli, nudo ma con una giacca larga addosso, li ha seguiti zoppicando.]],
				['31'] = [[Prima di riuscire ad avvicinare l'orecchio alla fessura, c'è stato un forte rumore di vetri rotti.<new_line><new_line>'Sei impazzito?!', ha gridato Brufolo. 'Che pensi di fare, stronzo?'<new_line><new_line>'Chi hai chiamato stronzo?', ha risposto Ivan respirando pesantemente. 'Cosacco, hai visto?! Ci sta fregando! Guarda! Il bastardo ha truccato il mazzo! Ah!'<new_line><new_line>Mi è sembrato che qualcuno avesse ricevuto un pugno.]],
				['17'] = [[Ho aspettato che entrambi si avvicinassero all'armadio, poi sono saltato direttamente davanti a Tema e gli ho tagliato la gola. Con il sangue che zampillava, il gigante ha gorgogliato ed è caduto in ginocchio. Ma a quel punto la mia fortuna è terminata.<new_line><new_line>Al posto della gola di Ivan, il mio coltello ha tagliato l'aria. Ivan ha urlato e ha estratto l'arma, ma sono riuscito a fargliela cadere dalle mani. Poi Ivan ha afferrato la mano con cui impugnavo il coltello.<new_line><new_line>Maledizione, sarà anche mingherlino, ma è forte come un bue!]],
				['18'] = [[Forse ho scelto la soluzione più codarda, ma era anche la cosa più intelligente da fare. I banditi hanno controllato la stanza, persino sotto il letto. Con disappunto, Ivan ha calciato i frammenti di vetro a terra e ha sprangato la finestra.<new_line><new_line>'Il vento. Cosa pensavi che fosse? Un fantasma?']],
				['19'] = [[In cucina, la porta della cantina è aperta. Non è chiaro cosa faccia Brufolo là sotto. Vicino al piano della cucina, il cosacco sta canticchiando qualcosa. Ha due revolver nella cintura.<new_line><new_line>Sul piano, ci sono sei confezioni aperte di latte condensato e due barattoli di vernice, uno dei quali pieno di pane secco.<new_line><new_line>Sulla parete opposta, c'è un frigorifero Minsk sporco, un lavandino con una pila di piatti dentro e un fornello a gas cosparso di una specie di chicchi.]],
				['51'] = [[Ho sentito due volte il rumore di vetri rotti dalla cantina. Sicuramente Brufolo che ha scaraventato di nuovo i barattoli a terra per dispetto perché non è riuscito a trovare niente di commestibile.<new_line><new_line>Ha lasciato il suo AK sul piano della cucina. Potrebbe essere l'occasione perfetta per sbarazzarmi di lui. Potrebbe non capitarmi più un'occasione altrettanto buona.]],
				['41'] = [[La porta del soggiorno è chiusa. Il letto è a malapena visibile dalla finestra e devo infilare leggermente la testa dentro.<new_line><new_line>Ivan è sdraiato a pancia in giù in diagonale sul letto. Un grande livido si sta formando sul suo viso. Un revolver spunta dalla tasca della sua giacca. Gli occhiali di Ivan sono sulla toilette; la montatura è rotta.<new_line><new_line>Ascolto attentamente. Il bandito sta russando.]],
				['54'] = [[Cercando di non fare rumore, ho chiuso piano piano il portello della cantina e mi sono fermato sopra. Sarà diventato ancora più buio là dentro, perché Brufolo ha iniziato a fare rumore. Un minuto dopo, ha tentato invano di aprire il portello.<new_line><new_line>'Ehi, Tema, smettila, idiota!', ha gridato il prigioniero, sbattendo sul portello, che però attutiva bene il rumore dei pugni e delle urla. Nessuno lo avrebbe mai sentito in fondo al corridoio nel soggiorno.]],
				['56'] = [[Ho sentito Tema dire: 'Hai detto che ti piacciono le more, ma i capelli di questa sono una specie di biondo e ha il naso all'insù'.<new_line><new_line>'Quindi ti piace?', ha chiesto il cosacco.<new_line><new_line>'No. È stupida, certo, ma la mia intenzione non è di farci una chiacchierata.' L'omone ha riso in modo volgare. 'Quindi la dividerai con noi? Visto che siamo stati io e Ivan a portarla? In cambio ti darò il latte condensato rimasto.'<new_line><new_line>'Non l'hai già consumato tutto?']],
				['59'] = [[Mi sono occupato del leader.<new_line><new_line>Sono andato dal corpo di Tema, che giaceva in una posiziona innaturale, e l'ho girato. Il suo viso coperto di terra aveva un'espressione di furia animalesca e gli occhi mi fissavano con odio irrazionale.<new_line><new_line>Ne manca uno.]],
				['6'] = [[Ho contato quattro persone. A giudicare dai tatuaggi elaborati, credo siano tutti e quattro ex detenuti. Probabilmente si sono conosciuti in prigione. I banditi sono armati di revolver e AK e sembrano abbastanza abili nella lotta. Feccia pericolosa.]],
				['64'] = [[La musica si è fermata e il cosacco è uscito nell'ingresso.<new_line><new_line>Con un rapido movimento, la lama si è infilzata nella scapola sinistra del leader dei banditi. Il cosacco ha respirato affannosamente, ha provato a girarsi, ma è caduto invece in ginocchio.<new_line><new_line>'Brufolo...', ha chiamato col fiato corto. 'Ivan...']],
				['66'] = [[Ferito mortalmente e coperto di sangue, il cosacco ha messo da parte il fucile con il caricatore vuoto.<new_line><new_line>Ha sussurrato: 'Sai combattere. Non ti lasci travolgere dalle emozioni. Sei freddo. Vieni qui, voglio dirti una cosa'.<new_line><new_line>'Mi avvicinerò solo dopo che mi farai vedere cos'hai in mano dietro la schiena.']]
			},
			['name'] = [[Ospiti indesiderati]],
			['choice'] = {
				['10'] = [[Sento un rumore assordante provenire dalla cucina. Cosa sta succedendo lì? (Guarda dalla finestra della cucina)]],
				['68'] = [[Guarda nel frigorifero]],
				['30'] = [[Scendi nella cantina]],
				['20'] = [[Esci dalla finestra]],
				['50'] = [[Spingi il frigorifero sul bandito]],
				['40'] = [[Scendi nella cantina e aspetta che uno dei banditi scenda per prendere del cibo]],
				['60'] = [[Ispeziona la casa]],
				['24'] = [[Scavalca ed esci]],
				['14'] = [[Vai verso la cucina]],
				['44'] = [[Colpisci Ivan in testa con l'attizzatoio]],
				['38'] = [[Allontanati dalla finestra]],
				['28'] = [[Esci]],
				['58'] = [[Vai nel soggiorno]],
				['48'] = [[Guarda nella cucina]],
				['2'] = [[Guarda]],
				['1'] = [[Raggiungi la dacia senza farti vedere]],
				['69'] = [[Nascondi il corpo]],
				['11'] = [[Vai nell'ingresso dalla porta principale]],
				['61'] = [[Entra nella stanza da letto dalla finestra]],
				['71'] = [[Corri alla finestra]],
				['55'] = [[Pugnala Tema]],
				['65'] = [[Aspetta Tema dietro il portone e strangolalo con il filo]],
				['35'] = [[Aspetta un po']],
				['19'] = [[Attira un bandito: butta a terra un flacone della toilette e nasconditi nell'armadio]],
				['15'] = [[Ispeziona l'ingresso]],
				['7'] = [[Osserva la casa dall'esterno]],
				['6'] = [[Mettiti all'opera]],
				['5'] = [[Continua...]],
				['4'] = [[Continua ad ascoltare]],
				['39'] = [[Chiudi la porta]],
				['32'] = [[Esci nell'ingresso]],
				['22'] = [[Resta nell'armadio]],
				['12'] = [[Entra nella stanza da letto dalla finestra aperta]],
				['72'] = [[Incastra l'attizzatoio nel chiavistello della cantina]],
				['62'] = [[Corri alla staccionata]],
				['52'] = [[Chiudi la porta della cantina]],
				['42'] = [[Esci dalla cantina]],
				['46'] = [[Taglia la gola a Ivan]],
				['36'] = [[Guarda dentro dalla finestra della stanza da letto]],
				['26'] = [[Qualcuno ha appena urlato nel soggiorno. (Ascolta)]],
				['16'] = [[Torna fuori]],
				['67'] = [[Aggiungi la carne marcia nel latte condensato e amalgama]],
				['77'] = [[Vai nel fienile]],
				['9'] = [[Guarda dentro dalla finestra della cucina]],
				['8'] = [[Guarda dentro dalla finestra del soggiorno]],
				['53'] = [[Vai nell'ingresso e ascolta la conversazione nel soggiorno]],
				['63'] = [[Esci e corri alla dependance]],
				['73'] = [[Preparati a combattere]],
				['76'] = [[Stringi forte il filo!]],
				['13'] = [[Completa la tua ricerca]],
				['75'] = [[Chinati sotto il suo braccio]],
				['74'] = [[Accendi la torcia]],
				['43'] = [[Scavalca ed entra nella stanza da letto]],
				['27'] = [[Vai in cucina]],
				['37'] = [[Entra nella cucina dalla finestra aperta]],
				['47'] = [[Esci. È troppo rischioso muoversi adesso]],
				['57'] = [[Fai un rumore e resta dietro la porta con un coltello]],
				['29'] = [[Ispeziona la cucina]],
				['3'] = [[Seguili]],
				['31'] = [[Esci dalla finestra]],
				['17'] = [[Combatti]],
				['18'] = [[Ispeziona attentamente la stanza da letto]],
				['21'] = [[Esci dall'armadio e pugnala i due banditi]],
				['45'] = [[Strangola Ivan con il filo del ferro da stiro]],
				['49'] = [[Scavalca ed entra in cucina]],
				['25'] = [[Apri un po' la porta e ascolta la conversazione nel soggiorno]],
				['51'] = [[Scendi furtivamente le scale della cantina e pugnala il bandito]],
				['41'] = [[Siediti sull'ultimo gradino e aspetta]],
				['56'] = [[Torna nell'ingresso]],
				['59'] = [[Non fare resistenza. I tuoi amici sono morti. Tutti e tre.']],
				['64'] = [[Aspetta Tema dietro il portone e pugnalalo]],
				['66'] = [[I tuoi amici sono morti.']],
				['54'] = [[Apri la cantina e spara a Brufolo]]
			}
		},
		['shewolf'] = {
			['episode'] = {
				['114'] = [[Sei un po' paranoico, Pavel, l'ho sempre detto', ha detto Yury ridendo. 'Ma davvero, perché dividiamo la squadra? Possiamo occuparci dei lupi addormentati in una volta e poi affrontare il gruppo da cinque. Non è così complicato. Le tue ingegnose strategie non servono qui.'<new_line><new_line>'Quindi, qual è la tua decisione?', ha detto l'ex ispettore rivolgendosi a me.]],
				['118'] = [[Sapete che me ne faccio delle vostre sentite condoglianze?', ha detto Nikita indispettito. 'Mi avete stufato, tutti quanti.'<new_line><new_line>Improvvisamente mi sono ricordato di Nikita a Kiev, che sparava ai lupi con le lacrime agli occhi.]],
				['141'] = [[Sconfortato, Yury ha detto: 'Pensateci. Nikita è il più giovane, ha tutta la vita davanti. Dovrebbe andare uno di noi...'<new_line><new_line>'Yury, smettila!', Nikita l'ha interrotto. 'La vita è mia, e decido io cosa farne. Giusto o sbagliato?'<new_line><new_line>'Giusto', ha detto Yury abbassando la testa.]],
				['64'] = [[Pare che abbia perso i sensi solo per poco tempo. Ho aperto gli occhi per il doloroso bagliore della torcia.<new_line><new_line>'Stenditi. Ti ha colpito bene.' Era la voce triste di Yury. Poi mi sono reso conto di non avere più il giubbotto addosso.<new_line><new_line>Ho capito tutto a quel punto.]],
				['84'] = [[Nikita ha sorriso.<new_line><new_line>'Sì, questa è la soluzione migliore. Per il mio patrigno, per Gavrilov...'<new_line><new_line>Ha iniziato a indossare il giubbotto.]],
				['74'] = [[Pare che abbia perso i sensi solo per poco tempo. Ho aperto gli occhi per il doloroso bagliore della torcia.<new_line><new_line>'Sei vivo?', era la voce triste di Grigory. Poi mi sono reso conto di non avere più il giubbotto addosso.<new_line><new_line>Ho capito tutto a quel punto.]],
				['109'] = [[Non c'era nient'altro da fare, quindi abbiamo fatto il giro del mattatoio dal lato opposto e siamo entrati uno dopo l'altro nel tunnel buio e sempre più stretto.<new_line><new_line>'Una volta lavoravo qui, all'uscita', ha sussurrato un cacciatore tristemente. 'Non avrei mai pensato che un giorno sarei finito io al macello.'<new_line><new_line>Mi è venuta la pelle d'oca, e credo che sia venuta anche ad altri.]],
				['14'] = [[Anche il mio patrigno. Quando tanto tempo fa, ai tempi della scuola, mia madre è morta, il mio patrigno mi ha intagliato una statuetta di mia madre che danzava. Era una ballerina e la ricorderò sempre ballare. Mi raccontò che quando era nell'esercito, fece delle incisioni dei suoi genitori per non sentirne troppo la mancanza. Ma la statuetta in sé non è importante quanto il sentimento che ci metti mentre la realizzi.'<new_line><new_line>Nikita si è girato la testa ghignante del lupo tra le mani. Non aveva gli occhi. Nikita ha riso:<new_line><new_line>'Puoi intagliare qualcosa con amore o con odio. Con quello che ti pare.']],
				['110'] = [[Procediamo, non abbiamo molto tempo', ho incitato ai cacciatori.<new_line><new_line>Eravamo circondati da buio e silenzio. Dei solitari raggi di luce penetravano da alcune finestrelle. Il mattatoio sembrava completamente deserto, ma quando siamo arrivati nella stanza con le tinozze che raccoglievano il sangue degli animali macellati, Nikita si è fermato:<new_line><new_line>'Ssh! Lo sentite?']],
				['34'] = [[Quando abbiamo raggiunto il camion, era già tutto finito. I quattro lupi erano morti, ma Yury e un altro cacciatore erano gli unici ancora vivi del gruppo. Yury correva in preda a una furia per tutto il piazzale insanguinato.<new_line><new_line>'Gliel'ho detto! Gliel'ho detto', ha gridato non appena ci ha visti. 'Non lasciateli feriti, uccideteli, uccideteli subito!']],
				['123'] = [[Hanno fatto tutto il possibile, Yury', ha detto uno dei cacciatori. 'Non biasimarli, non sono soldati.'<new_line><new_line>Yury ha serrato i denti.<new_line><new_line>La battaglia era finita. Avevamo perso cinque uomini e un altro, il sesto, era in punto di morte: Pavel Gavrilov.]],
				['107'] = [[Il mostro non ha ceduto alla morte fino all'ultimo. La lupa ha guaito, cercando di mordermi e rifiutandosi di morire. Solo quando ho trasformato il mio nemico in una massa sanguinante e informe le zampe hanno ceduto e questo incubo vivente, un mostro creato dalle radiazioni, ha esalato l'ultimo respiro.<new_line><new_line>Senza perdere altro tempo, sono andato nella cella frigorifera. Come speravo, l'intera cucciolata di lupi (più o meno una dozzina) era morta nell'esplosione.<new_line><new_line>La missione era compiuta.]],
				['122'] = [[Dopo aver confermato che i piani stavano andando come previsto, io, Gavrilov, Nikita e altri quattro cacciatori siamo andati a sud passando dalla bassa vegetazione. Finché non siamo stati pronti, ci siamo tenuti a debita distanza per non allertare i cinque lupi.<new_line><new_line>Poi abbiamo preso posizione, abbiamo mirato e, non appena abbiamo sentito il primo colpo da est, abbiamo aperto il fuoco.]],
				['124'] = [[Percependo una nuova minaccia, i lupi si sono fermati. Questo ha salvato ciò che rimaneva del secondo gruppo. Si sono ripresi dalla confusione e hanno aperto il fuoco, e noi ci siamo uniti a loro. I lupi, già gravemente feriti, sono stati abbattuti.<new_line><new_line>Anche se eravamo riusciti a sterminare il branco di lupi, non ce la sentivamo di festeggiare. Avevamo perso sei uomini e un altro, il settimo, era in punto di morte: Pavel Gavrilov.]],
				['1'] = [[Gli uomini della battuta di caccia si sono stabiliti temporaneamente vicino al magazzino incendiato lontano dall'autostrada.<new_line><new_line>Del fumo denso si alzava dai barili arrugginiti. I combattenti erano seduti attorno al fuoco e mangiavano voracemente delle patate arrostite e bevevano acqua da grandi tazze di latta.]],
				['151'] = [[Ho lasciato gli uomini nell'area di eviscerazione e ho illuminato l'altra parte della cella frigorifera con la torcia. Come speravo, l'intera cucciolata di lupi (più o meno una dozzina) era morta nell'esplosione.<new_line><new_line>A quel punto non ci restava che uscire da lì.]],
				['85'] = [[Ok, ci sto', Nikita ha ceduto dopo un secondo. 'È giusto così.'<new_line><new_line>Ha preso una scatola di fiammiferi dalla tasca, ne ha tirati fuori due e ha spezzato la testa di uno. Entrambi ne abbiamo pescato uno.<new_line><new_line>A lui è capitato quello più corto.]],
				['128'] = [[Alzando le armi, io e Yury abbiamo iniziato a sparare al mostro, che però era indifferente a noi o ai proiettili che le perforavano la pelle. Solo quando la vittima ha smesso di dimenarsi, il lupo ha rivolto il suo muso insanguinato verso di noi.<new_line><new_line>Ho sentito un urlo da brivido dietro le mie spalle. È durato un secondo, seguito da un'esplosione di colpi di armi automatiche.<new_line><new_line>Con un ringhio basso, il lupo si è diretto verso Yury.]],
				['65'] = [[Nikita si è congelato per un secondo, poi ha sorriso all'improvviso.<new_line><new_line>'Grazie. Sì, questa è la soluzione migliore. Per il mio patrigno, per Gavrilov...'<new_line><new_line>Ha iniziato a indossare il giubbotto.]],
				['35'] = [[L'ex ispettore mi ha guardato negli occhi e ha provato a sorridere. Lo strato di sporcizia misto a sangue sul suo viso ha reso quel sorriso inquietante.<new_line><new_line>'Scusa, ma dovrai andare da Zaitsev per sapere quello che vuoi sapere. Sempre se sopravvivi a questa caccia...']],
				['119'] = [[Sì. Lo portava sempre con sé, sai. Lo teneva sotto il cuscino quando dormiva; non lo posava neppure quando andava a nuotare. Mi ha sempre fatto ridere. Una volta, ho nascosto il coltello per scherzo e il mio patrigno me le ha suonate. Ha infilato la lama in un pezzo di legno e ha detto: 'Quando il fucile s'inceppa, quando la granata non esplode, quando l'amico scappa, non ti rimane altro che il tuo coltello'. Quest'affare gli ha salvato la vita tre volte.']],
				['15'] = [[Sì, non c'era niente che potesse fare.' Nikita ha chiuso gli occhi. 'Aveva solo la Makarov, che per quei lupi neri era come il morso di una pulce. Non so se avrebbe fatto quello che il mio patrigno ha fatto per lui, ma non è da incolpare per la sua morte. La colpa è... di altri.']],
				['25'] = [[Abbiamo alzato le armi.<new_line><new_line>'Fermi! Colpirete Alex!', ha gridato qualcuno.<new_line><new_line>'È già morto!', ha urlato Yury. 'Sparate, maledizione!'<new_line><new_line>'Dietro di te!', mi ha gridato improvvisamente Gavrilov.<new_line><new_line>Mi sono girato e ho visto un lupo saltarmi addosso dalla vegetazione dietro di me.]],
				['6'] = [[In un vecchio mattatoio, a cinque chilometri dall'autostrada per Zhytomyr. Lì sono stati avvistati i cuccioli di lupo', ha risposto Yury. 'E tre giorni fa, sono stati uccisi i nostri amici là fuori.'<new_line><new_line>'Il vento non cambia da tanto tempo', ha aggiunto. 'Ci avvicineremo al mattatoio sottovento. Se funziona e non sentono il nostro odore, arriveremo lì senza farci notare, uccideremo la lupa e ce ne andremo.'<new_line><new_line>'Come sei ottimista, Yury', ha detto Gavrilov con un sorriso cupo. 'Queste creature sono cieche, ma carpirebbero il nostro odore a un chilometro di distanza. Di' quello che vuoi, ma verranno tutti a proteggere la madre.']],
				['5'] = [[Sì. E Yury sta aiutando', ha risposto Gavrilov. 'Quando si tratta di lupi, siamo i più esperti, suppongo...'<new_line><new_line>'Prima Kirill era a capo di tutte le operazioni', ha detto Yury con tono triste. 'Era nelle forze speciali della marina. Il patrigno di Nikita, quello che ha perso la vita a Kiev...']],
				['4'] = [[Uno contro uno?', ha ripetuto il cacciatore. Gli altri mi hanno guardato con rispetto. 'Impressionante. Da queste parti, andiamo a caccia in gruppi minimo da dodici, e solo per la lupa...'<new_line><new_line>'Yury, non fare storie', ha detto l'uomo accanto a lui. 'La situazione è già pessima così com'è.'<new_line><new_line>'Stiamo per uscire', mi ha detto Gavrilov. 'Sei hai domande, chiedile adesso.']],
				['3'] = [[Ed ecco il nostro amico, quello di cui ti ho parlato!', così mi ha presentato l'ex ispettore agli altri. Ho dato loro una stretta di mano. 'È arrivato qui da Tver, quindi ha una certa esperienza nella lotta. Ha promesso di aiutarci con i lupi.'<new_line><new_line>'Hai mai visto i lupi che ci sono da queste parti? I lupi normali non hanno niente a che fare con queste bestie', ha detto il combattente accanto a Gavrilov guardandomi dubbioso. Non era giovane e aveva il viso pieno di rughe e gli occhi stanchi.]],
				['136'] = [[Sei davvero disposto ad affrontare la lupa con un fucile a doppia canna? Siamo rimasti solo in quattro.'<new_line><new_line>'Dov'è la valigetta di Gavrilov?', ha chiesto Nikita improvvisamente. 'È quello il suo piano B?'<new_line><new_line>La valigetta era vicino al corpo del macellaio.]],
				['132'] = [[Inspirando tra i denti serrati, ho iniziato ad avanzare e a sparare alla bestia. Il lupo nero è morto addosso alla sua ultima vittima.<new_line><new_line>Io, Nikita e Grigory eravamo gli unici sopravvissuti di tutta la squadra.<new_line><new_line>Certo, se avessi scelto la via più codarda e non avessi condotto la squadra, le vittime sarebbero state di più. Ma comunque...]],
				['117'] = [[Probabilmente mi ritiene responsabile per la morte di Kirill, il suo patrigno', ha detto Gavrilov sospirando. 'E poi, sono stato io a portarlo via da Kiev e a impedirgli di vendicarsi sui lupi. Lo faresti tu?']],
				['32'] = [[Purtroppo la distanza di fuoco era troppa per gran parte dei miei compagni. Ho ucciso un lupo, ma gli altri avevano solo ferite poco gravi e hanno iniziato a correre verso di noi.<new_line><new_line>'Come avete fatto a mancarli?!', ha gridato Gavrilov ansimando.<new_line><new_line>Anche se avevano completato l'addestramento, quegli uomini di campagna non erano diventanti esperti tiratori dal giorno alla notte. Ma dopo le cose migliorarono. I lupi correvano in fila e uno dopo l'altro sono andati incontro al fuoco dei tre fucili d'assalto. Io ne ho ucciso uno, Gavrilov un altro e il terzo si contorceva in agonia...]],
				['22'] = [[Decidi se vuoi stare nel primo o nel secondo gruppo', ha detto Gavrilov. 'Il primo gruppo è la scelta meno rischiosa perché i lupi saranno addormentati. Ma servi di più nel secondo gruppo. Sei esperto, mentre i nostri uomini non sono neppure cacciatori professionisti. Alcuni non hanno mai neppure impugnato un'arma prima di oggi. E hanno paura, ovviamente. Anche se,' ha aggiunto, 'non bisogna sottovalutare i lupi che dormono'.]],
				['12'] = [[Nikita ha scosso la testa dubbioso e ha continuato a brandire il coltello. Io ero al suo fianco.<new_line><new_line>'Bel coltello, eh?', mi ha chiesto dal nulla.]],
				['72'] = [[No, vado io', ha detto Nikita improvvisamente cercando di prendere il giubbotto.]],
				['62'] = [[Ok, sei tu al comando', ha detto Nikita alzando le spalle e girandosi dall'altra parte.<new_line><new_line>Yury mi ha aiutato a indossare il giubbotto.<new_line><new_line>'Tira il cavo solo quando sei vicino alla bestia, non un secondo prima!', mi ha avvertito. Poi mi ha guardato negli occhi. 'Grazie, a nome di tutti.'<new_line><new_line>Grigory non ha detto niente, ha solo annuito. Nikita guardava da un'altra parte.]],
				['42'] = [[Un silenzio oppressivo aleggiava nell'aria.<new_line><new_line>'Che cos'è?', ho chiesto sperando di allentare la tensione e indicando la valigetta dietro Yury. Avevo già visto quella valigetta in mano a Gavrilov.<new_line><new_line>'Il piano B, nel caso in cui le nostre armi non bastassero per uccidere la lupa. Sappi che non ci sono rimaste molte munizioni.']],
				['92'] = [[È successo tutto quasi in un secondo. Grigory ha visto Nikita ed è andato verso di lui. Quando Grigory l'ha raggiunto, il ragazzo l'ha colpito con un tubo.<new_line><new_line>Era come se la lupa non aspettasse altro. Il lamento è diventato un ringhio, si è seduta sulle zampe posteriori ed è saltata addosso a Nikita...<new_line><new_line>'Nikita!', ha gridato Yury disperato.<new_line><new_line>Nikita ha tirato il cavo del giubbotto di Grigory. L'esplosione è stata così potente che io e Yury siamo stati scaraventati molto lontano.]],
				['82'] = [[Aspetta!', ho sentito qualcuno chiamare mentre mi avvicinavo al buio inquietante del tunnel. Mi sono guardato attorno e ho visto Nikita correre verso di me.<new_line><new_line>'Che succede?'<new_line><new_line>'Il cavo... è aggrovigliato', ha detto Nikita affannato indicando il giubbotto.<new_line><new_line>Ho guardato il cavo, confuso. E poi ho sentito come se fossi stato colpito in testa con un martello: mi girava la testa e ho visto tutto nero.]],
				['129'] = [[Mi sono rimesso in piedi a fatica e mi sono reso conto che era finita. Era buio e silenzioso come in una tomba. Ho alzato la torcia e ho dato un'occhiata attorno. A pochi passi da me, appoggiato a un barile di acciaio, c'era Nikita gravemente ferito. Yury e Grigory erano impegnati a fare qualcosa al suo fianco.<new_line><new_line>Gli altri erano tutti morti.]],
				['105'] = [[Non abbiamo dovuto aspettare molto. Nel giro di dieci secondi, i terribili guaiti dietro il tunnel sono aumentati e poi c'è stato un boato dalla cella frigorifera che ci ha fatto tremare la terra sotto i piedi. Per un attimo ho pensato che il soffitto sarebbe crollato, ma poi la terra ha smesso di tremare. Tra di noi è calato un pesante silenzio.<new_line><new_line>Nikita ce l'aveva fatta. Ho rilasciato il respiro che stavo trattenendo e mi sono alzato...<new_line><new_line>E poi la lupa è apparsa dal tunnel blu notte.]],
				['146'] = [[Stava aspettando la mia decisione, visto che ero ancora il comandante. Ma non sapevo cosa rispondere. Certo, prima di quel momento le mie decisioni avevano avuto conseguenze di vita o di morte per le persone, ma ora avevo davanti una prospettiva orrenda: decidere chi dei due condannare a morte certa.<new_line><new_line>Ho ripensato al cacciatore decapitato. In quell'occasione, non ho avuto altra scelta. Poi ho pensato a Pavel Gavrilov, a Yury, al macellaio e agli altri che sono morti durante la caccia. Ho fatto una scelta, ma non posso essere certo delle conseguenze. Ma stavolta è completamente diverso...]],
				['102'] = [[Quando tornerai all'accampamento', ha detto Nikita all'improvviso, 'vai a casa nostra. C'è una lettera dietro il lavamano. È indirizzata a una ragazza, Tanya. Grigory, tu la conosci. Vive con sua sorella. È proprio... ehm... fantastica.'<new_line><new_line>'Gliela faremo avere, tranquillo', ha detto Grigory annuendo.<new_line><new_line>Nikita ha sorriso e ha iniziato a camminare verso il tunnel.]],
				['143'] = [[Ho ripensato al cacciatore decapitato. In quell'occasione, non ho avuto altra scelta. Poi ho pensato a Pavel Gavrilov, a Yury, al macellaio e agli altri che sono morti durante la caccia. Ho fatto una scelta, ma non posso essere certo delle conseguenze. Ma stavolta è completamente diverso...<new_line><new_line>Io, Yury, Grigory o Nikita. Uno di noi deve sacrificare la vita.]],
				['127'] = [[Mi sono rimesso in piedi a fatica e mi sono reso conto che era finita. Era buio e silenzioso come una tomba. Ho alzato la torcia e ho dato un'occhiata attorno. A pochi passi da me, appoggiato a un barile di acciaio, c'era Nikita gravemente ferito. Grigory era impegnato a fare qualcosa al suo fianco.<new_line><new_line>Gli altri erano tutti morti.]],
				['93'] = [[Quando ci siamo rimessi in piedi, l'abbiamo visto di nuovo...<new_line><new_line>La creatura davanti a noi, illuminata dalla luce fioca e tremolante delle torce, era solo vagamente somigliante a un lupo normale. La lupa non aveva pelliccia o pelo, il suo grottesco corpo gonfio era pieno di tumefazioni e pustole purulente, e del disgustoso liquido giallo le colava dalle fauci e dagli occhi.]],
				['108'] = [[Un cacciatore più anziano era seduto con le gambe a cavalcioni su una cerata rossa e aveva poggiato dei pezzi di pesce appena fritto su dei ritagli di giornale. Ho visto Gavrilov nei paraggi: stava gesticolando con un osso di pollo mezzo mangiucchiato e intratteneva gli uomini attorno a lui con una storia sulla sua turbolenta adolescenza. Gli uomini si sono sganasciati dalle risate.<new_line><new_line>I cacciatori erano sollevati, persino allegri. Troppo allegri. Non capivano che le loro chance di sopravvivenza erano davvero poche. Sicuramente si rifiutavano di prendere in considerazione la possibilità.]],
				['53'] = [[Yury non mi ha sentito. L'arma gli è caduta dalle mani molle. Ho iniziato a sparare di nuovo, il lupo ha ringhiato e ha puntato Yury. Dopo un solo morso, il lupo si è girato verso di me e si è preparato a saltare... ma poi è lentamente collassato su un fianco.<new_line><new_line>È sceso un'altra volta un silenzio mortale.<new_line><new_line>Ho alzato la torcia e ho dato un'occhiata in giro. A pochi passi da me, appoggiato a un barile di acciaio, c'era Nikita gravemente ferito. Grigory era impegnato a fare qualcosa al suo fianco. Tutti gli altri erano morti.]],
				['63'] = [[Aspetta!', ho sentito qualcuno chiamare mentre mi avvicinavo al buio inquietante del tunnel. Mi sono guardato attorno e ho visto Nikita correre verso di me.<new_line><new_line>'Che succede?', mi sono fermato e ho guardato il ragazzo.<new_line><new_line>'Il cavo... è aggrovigliato', ha detto Nikita affannato indicando il giubbotto.<new_line><new_line>Ho guardato il cavo confuso. E poi ho sentito come se fossi stato colpito in testa con un martello: mi girava la testa e ho visto tutto nero.]],
				['73'] = [[Ok, sei tu al comando', ha detto Nikita alzando le spalle e girandoti dall'altra parte.<new_line><new_line>Ho indossato velocemente il giubbotto senza concedermi il tempo di ripensarci.<new_line><new_line>Grigory non ha detto una parola, ha solo annuito. Nikita guardava dall'altra parte.]],
				['83'] = [[Pare che abbia perso i sensi solo per poco tempo. Ho aperto gli occhi e mi girava e mi faceva male la testa.<new_line><new_line>Poi mi sono reso conto di non avere più il giubbotto addosso.<new_line><new_line>Ho capito tutto a quel punto.]],
				['13'] = [[A Kiev, aveva finito le cartucce e ha ucciso un lupo con il suo coltello, ma poi...', la gola di Nikita si chiuse. '...un'altra bestia l'ha attaccato da dietro e gli ha staccato la mano. Che ironia della sorte, eh? Aveva un coltello, ma non la mano per brandirlo... Il lupo non lo ha neppure ucciso. Aveva puntato Gavrilov, ma il mio patrigno lo ha afferrato con le braccia e lo ha trattenuto. Il lupo lo ha azzannato, ma il mio patrigno ha resistito... finché non è morto dissanguato. Se Seva fosse arrivato trenta secondi dopo, il lupo avrebbe ucciso anche Gavrilov.']],
				['23'] = [[Sì! Che ti avevo detto?', Yury ha fatto l'occhiolino a Gavrilov, che l'ha guardato storto. 'Forse ascolterai lui.'<new_line><new_line>'Va bene, Yury, hai vinto', ha detto Gavrilov sospirando. Dopo una pausa, ha continuato: 'Spero che vada tutto bene e velocemente come hai detto, perché altrimenti...' e ha semplicemente sollevato la mano.<new_line><new_line>Era deciso.]],
				['33'] = [[Sì! È fatta! Ottimo lavoro, ragazzi!', ha detto Gavrilov incoraggiando gli altri.<new_line><new_line>L'ultimo lupo è spuntato dal nulla e ha affondato le zanne nel collo di uno dei cacciatori. Un secondo dopo aveva spinto a terra Nikita. Gavrilov è corso in suo aiuto e ha colpito la bestia con il calcio del fucile. Poi il lupo è saltato da Nikita con un ringhio e ha azzannato il ventre di Gavrilov. Entrambi erano morti. Siamo corsi verso la bestia sparandole tante volte la testa. Il lupo è morto.<new_line><new_line>Ma sentivamo ancora degli spari da est.]],
				['43'] = [[Non siamo potuti entrare nell'edificio dall'ingresso principale: quella parte dell'edificio è crollata, ma anche il cancello di carico della carne era bloccato. Anche dopo aver forzato la serratura, i cardini non hanno ceduto.<new_line><new_line>'C'è un altro ingresso', ha detto uno dei cacciatori. 'Il corridoio del macello. Era il passaggio del bestiame prima che noi... beh, avete capito.']],
				['139'] = [[Pensavo che mi capissi!', Nikita si è avvicinato a me respirando pesantemente. 'Mi hai raccontato tutte quelle storie sul cacciatore decapitato! No, so che ti pesa sulla coscienza. Ma non avresti potuto fare altro! Il mio patrigno e Gavrilov sono morti per colpa mia, capito? Per colpa mia! Ho più diritto io a farlo!'<new_line><new_line>'Ed è così che vuoi sbarazzarti del tuo senso di colpa?'<new_line><new_line>'Voglio ripagare il mio debito.']],
				['116'] = [[Nel caso peggiore, ci salta addosso', ha borbottato Yury<new_line><new_line>'Ma se resteremo in trappola, abbiamo un piano B', ha detto Gavrilov. C'era una valigetta a terra tra i suoi piedi e l'ha toccata con aria compiaciuta.]],
				['113'] = [[Ascolta attentamente. Ci sono nove lupi vicino al mattatoio. Quattro a est, che stanno dormendo, e cinque a sud, svegli e all'erta.<new_line><new_line>'Prenderemo le bestie di sorpresa', ha continuato. 'Divideremo la squadra in due gruppi e attaccheremo contemporaneamente. Il primo gruppo lancerà bombe fumogene ai quattro lupi addormentati da dietro una copertura e li annienteremo. L'altro gruppo accerchierà i cinque lupi sottovento, prenderà posizione e aprirà il fuoco al segnale.']],
				['137'] = [[No, ascolta tu!', Nikita si è avvicinato a me respirando pesantemente. 'Il mio patrigno e Gavrilov sono morti per colpa mia, capito? Per colpa mia! E questa è la mia occasione per regolare i conti. Perché non capisci? Tutto ha portato a questo! Questo giubbotto... è per me.'<new_line><new_line>'Ed è così che vuoi sbarazzarti del tuo senso di colpa?'<new_line><new_line>'Voglio ripagare il mio debito.']],
				['10'] = [[Non è stato difficile trovarlo. Nikita era seduto su un troncone e stava intagliando qualcosa da un pezzo di corteccia. Non ha reagito al mio arrivo; ha continuato a intagliare.<new_line><new_line>'Ti ha mandato Gavrilov?', mi ha chiesto di punto in bianco quando ero più vicino.<new_line><new_line>'Mi ha chiesto di venire a prenderti. È preoccupato.'<new_line><new_line>Nikita si è imbronciato ma non ha detto niente.]],
				['68'] = [[No, non è giusto. Devo andare io', ha detto Nikita all'improvviso.<new_line><new_line>Ci siamo girati verso di lui.]],
				['30'] = [[Le cose non stavano andando meglio al secondo gruppo. Quando li abbiamo raggiunti, quattro cacciatori e tre lupi erano già a terra. Due bestie, enormi e terrificanti, stavano per attaccare i cacciatori rimasti, che erano paralizzati dalla paura.]],
				['20'] = [[La piccola squadra condotta da Gavrilov e Yury ci stava già aspettando. Quando ha visto Nikita, l'ispettore mi ha fatto un gesto di approvazione, ma io ero perso nei miei pensieri e non ho risposto.<new_line><new_line>Le parole di Nikita mi ronzavano in testa. Mi sono ricordato del vecchio cacciatore, anche di Galja e di Sasha. Alla fine dei conti, ero io l'unico responsabile per quello che era successo loro.<new_line><new_line>Quando una persona muore, la responsabilità per la loro morte è tua. Non esiste dire: 'Non c'era niente che potessi fare', soprattutto se è una persona che ami.]],
				['50'] = [[Il mostro con sembianze di lupo, con il corpo tutto tumefatto, si è fermato a un passo da Yury. Perdeva sangue dai numerosi fori che aveva sul corpo, ma la creatura non barcollava e non sentiva alcun dolore. Era così terrificante, così incredibile...<new_line><new_line>Yury ha abbassato l'arma.<new_line><new_line>'Non serve...', l'ho sentito sussurrare.]],
				['158'] = [[Ce la farà', ha detto Yury, ma non sembrava convinto. Nikita ha solo digrignato i denti.<new_line><new_line>Di fronte al tunnel, Grigory si è girato l'ultima volta. Ci ha guardati come se fossimo estranei e poi si è inoltrato nell'ombra.]],
				['70'] = [[È un giubbotto antiproiettile!', ha detto Grigory sorpreso. 'Come ci aiuterà?'<new_line><new_line>'No, non è un giubbotto antiproiettile', ha detto Nikita cupamente. 'Gavrilov ha detto che è imbottito di esplosivi. Lo indossi, vai dalla lupa e tiri il cavo...'<new_line><new_line>'Che cosa?! Non se ne parla.' Grigory è sbiancato e con voce tremolante ha detto: 'Dev'esserci un altro modo!'<new_line><new_line>'Ad esempio? Come affrontare una lupa mutante con dei fucili? Sta proteggendo i suoi cuccioli, ci farà a pezzi in pochi secondi!']],
				['60'] = [[Non vedo un'alternativa. Qualcuno dovrà sacrificarsi, indossare il giubbotto, entrare nella cella frigorifera e quando la lupa è nei paraggi, tirare il cavo. Comandante', Yury si è rivolto a me, 'spetta a te decidere cosa fare.'<new_line><new_line>Non so cosa dire. Certo, prima di allora le mie decisioni avevano avuto conseguenze di vita o di morte per questi uomini, ma ora avrei dovuto consapevolmente condannarne uno a morte certa!]],
				['90'] = [[È deciso, allora...'<new_line><new_line>Grigory mi ha guardato senza parlare per un secondo, poi ha scosso la testa. Ho preso il giubbotto e gliel'ho passato. Muovendosi meccanicamente, come qualcuno ignaro di ciò che sta succedendo, il cacciatore ha indossato il giubbotto e si è diretto rigidamente verso il tunnel.]],
				['150'] = [[La lupa era semplicemente enorme, molto più grande dei suoi cuccioli. Tutta la parte destra del corpo - i muscoli, le costole, gli organi interni - erano stati squarciati dall'esplosione. Si è trascinata sulla zampa posteriore, emettendo dei lamenti orribili e lasciando una scia di sangue e melma.<new_line><new_line>Ma nonostante tutte le ferite, sembrava ancora in grado di difendersi.<new_line><new_line>'È l'incarnazione del diavolo...', ha sussurrato Grigory.]],
				['18'] = [[È una situazione completamente diversa.'<new_line><new_line>'È colpa mia?', ho ripetuto più intensamente.<new_line><new_line>'No, non credo che sia colpa tua', ha risposto Nikita che improvvisamente è arrossito. 'Ma sai una cosa? Anche se nessuno e proprio nessuno ti darebbe la colpa, tu sai che è stata colpa tua! Sai di avere il sangue di quell'uomo sulle mani!']],
				['125'] = [[Si è scatenato l'infermo. I cacciatori sono entrati nel panico e correvano e sparavano all'impazzata senza neppure mirare. Nell'aria riecheggiavano gli spari costanti, i ruggiti, i lamenti, le urla di terrore e dolore...<new_line><new_line>E anche se alla fine abbiamo vinto, abbiamo perso sette uomini. Gavrilov, l'ottavo, li avrebbe raggiunti presto.]],
				['38'] = [[Pavel, no, non puoi...', ha risposto Yury. 'Non voglio, non posso...'<new_line><new_line>'Basta!', ha detto Gavrilov. Ha chiuso gli occhi, ma poi mi ha guardato. 'Ora sei tu al comando, al posto mio. Yury, Nikita, fate ciò che dice.'<new_line><new_line>'Che cosa?!', ha esclamato Yury. 'Pavel, stai scherzando?'<new_line><new_line>Gavrilov non ha risposto. Yury mi ha guardato arrabbiato, ha alzato le mani sconfortato e se n'è andato.]],
				['28'] = [[Sfortunatamente non c'erano abbastanza cacciatori per eliminare tutti e quattro i lupi prima che il vento disperdesse il fumo. Solo due lupi erano stati uccisi a quel punto, gli altri solo feriti.<new_line><new_line>'Ah, maledizione!', ha gridato Yury quando il primo lupo che si era ripreso ha morso la gola del suo compagno. Yury ha crivellato la bestia di colpi.<new_line><new_line>Dopo pochi secondi, un altro cacciatore ha urlato dopo essere stato spinto dal secondo lupo.]],
				['58'] = [[Sta' indietro', ha detto Yury scuotendo la testa. Mi ha mostrato la sua arma. 'Sono quasi a secco.'<new_line><new_line>'Controlla gli altri.'<new_line><new_line>'Già fatto. Anche loro sono a secco, e abbiamo portato tutte le 39 mm che c'erano a Gorenichi.']],
				['149'] = [[È finita?', ha chiesto Grigory sottovoce.<new_line><new_line>'Sì', ha detto Yury annuendo. 'Nikita ce l'ha fatta...'<new_line><new_line>'Andiamocene da qui', ha detto Grigory sollevato. Ha sospirato, si è alzato...<new_line><new_line>E poi la lupa è comparsa dal tunnel buio.]],
				['126'] = [[Lungo e stretto', ha risposto il macellaio. 'Dovremo entrare in fila. E non ci sono finestre.'<new_line><new_line>'Accendete le luci', ho ordinato agli altri. 'Preparate le armi!']],
				['154'] = [[È finita?', ha chiesto Grigory sottovoce.<new_line><new_line>'Sì', ho detto annuendo. 'Nikita ce l'ha fatta...'<new_line><new_line>'Andiamocene da qui', ha detto Grigory sollevato. Ha sospirato, si è alzato...<new_line><new_line>E poi la lupa è comparsa dal tunnel buio.]],
				['162'] = [[Il mio patrigno. Quando tanto tempo fa, ai tempi della scuola, mia madre è morta, il mio patrigno mi ha intagliato una statuetta di mia madre che danzava. Era una ballerina e la ricorderò sempre ballare. Mi raccontò che quando era nella marina, fece delle incisioni dei suoi genitori per non sentirne troppo la mancanza. Ma la statuetta in sé non è importante quanto il sentimento che ci metti mentre la realizzi.'<new_line><new_line>Nikita si è girato la testa ghignante del lupo tra le mani. Non aveva gli occhi. Nikita ha riso:<new_line><new_line>'Puoi intagliare qualcosa con amore o con odio. Con quello che ti pare.']],
				['147'] = [[Buona fortuna a tutti', ha salutato Yury. 'La prossima volta che ci incontreremo, sarà in un posto', ha fatto un gesto vago, 'senza epidemie, radiazioni o lupi... se un posto del genere esiste.'<new_line><new_line>Arrivato al tunnel, si è girato di nuovo verso di noi con un sorriso triste:<new_line><new_line>'Sapete, veramente vivevamo in un paradiso, e siamo stati noi a distruggerlo...']],
				['21'] = [[Abbiamo finito di camminare a circa trecento metri dalla nostra destinazione. Il vento non era cambiato e non c'erano stati incidenti sul tragitto.<new_line><new_line>Gavrilov ha mandato avanti i perlustratori. Sono tornati dopo quindici minuti. Dopo aver parlato con Yury di qualcosa, Gavrilov mi ha chiamato.]],
				['31'] = [[Ha senso', ha esclamato Gavrilov. Yury ci ha pensato per un momento, ma poi ha annuito anche lui. Era deciso.<new_line><new_line>L'operazione è iniziata. Cercando di muoverci senza fare rumore, ci siamo avvicinati al mattatoio. Yury e altri cinque cacciatori sono corsi dietro il camion. Ognuno di loro ha preso una bomba fumogena. I lupi dormivano a poca distanza, dall'altro lato del camion.]],
				['69'] = [[Sta' indietro', ha detto Yury scuotendo la testa. 'Con cosa lo ucciderai? Siamo quasi a secco di munizioni.'<new_line><new_line>'Hai controllato le armi delle vittime?'<new_line><new_line>'Sì, stessa storia. E abbiamo portato tutte le 39 mm che c'erano a Gorenichi.']],
				['11'] = [[Non sono affari tuoi. Chi sei?', ha risposto Nikita infastidito. 'Se Gavrilov vuole qualcosa da me, può venire lui stesso.'<new_line><new_line>Ha iniziato a pensare, ancora con il pugnale in mano.<new_line><new_line>'Appena finisco torno.']],
				['61'] = [[I cacciatori si sono guardati.<new_line><new_line>'Vado io', ha detto Nikita improvvisamente cercando di prendere il giubbotto.]],
				['71'] = [[Qualcuno dovrà sacrificarsi, indossare il giubbotto, entrare nella cella frigorifera e quando la lupa è nei paraggi, tirare il cavo. Comandante', Nikita si è rivolto a me, 'spetta a te decidere cosa fare.'<new_line><new_line>Non so cosa dire. Certo, prima di allora le mie decisioni avevano avuto conseguenze di vita o di morte per questi uomini, ma ora avrei dovuto consapevolmente condannarne uno a morte certa!]],
				['41'] = [[Contro ogni aspettativa, Yury ha portato a termine tutti gli ordini di Gavrilov. Per fortuna, nessuno tra i sopravvissuti era gravemente ferito, ma in un modo o nell'altro feriti lo erano tutti. La squadra ormai sterminata aspettava me e Nikita. Yury parlava sottovoce con un altro cacciatore, Grigory.<new_line><new_line>'Aspettiamo i tuoi ordini', ha detto con freddezza quando mi ha visto. 'Ma ricorda che adesso sei responsabile della nostra vita. E se qualcosa va male, la colpa sarà tua.'<new_line><new_line>'Yury, stai zitto', lo ha rimproverato Nikita. Tutti abbiamo guardato il ragazzo sorpresi.]],
				['51'] = [[Yury non mi ha sentito. L'arma gli è caduta dalle mani molle. Ho iniziato a sparare di nuovo, il lupo ha ringhiato e ha puntato Yury. Dopo un solo morso, il lupo si è girato verso di me e si è preparato a saltare... ma poi è lentamente collassato su un fianco.<new_line><new_line>È sceso un'altra volta un silenzio mortale.<new_line><new_line>Ho alzato la torcia e ho dato un'occhiata in giro. A pochi passi da me, appoggiato a un barile di acciaio, c'era Nikita gravemente ferito. Tutti gli altri erano morti.]],
				['135'] = [[Inspirando tra i denti serrati, ho iniziato ad avanzare e a sparare alla bestia. Il lupo nero è morto addosso alla sua ultima vittima.<new_line><new_line>Io, Nikita, Yury e Grigory eravamo gli unici sopravvissuti di tutta la squadra.<new_line><new_line>Certo, se avessi scelto la via più codarda e non avessi condotto la squadra, le vittime sarebbero state di più. Ma comunque...]],
				['19'] = [[Non lo capisci? Non importa se avresti potuto fare qualcosa, è comunque colpa tua. Non ti credo. Non ci credo che dormi sonni tranquilli dopo quello che è successo.'<new_line><new_line>Non sapevo cosa dirgli.<new_line><new_line>'È ora di andare.' Nikita si è alzato. 'Uccideremo quei mostri, fino all'ultimo. E poi forse il dolore non sarà così forte.']],
				['160'] = [[Ehi, pensi davvero che mi va di parlare di questa storia con un estraneo?!', ha esclamato Nikita mollando il coltello e lanciandomi una scontrosa occhiata di sfida. 'Ascolta, io non mi immischio nei tuoi affari, quindi tu non immischiarti nei miei. Lo ripeto: se Gavrilov vuole parlare, può venire di persona e non mandare... chiunque tu sia.']],
				['91'] = [[I secondi passavano e l'attesa è stata atroce. I lamenti nella cella frigorifera sono aumentati, poi un gemito disperato...<new_line><new_line>Poi abbiamo rivisto Grigory. Correva verso di noi con il volto contorto per il terrore. Dietro di lui, abbiamo intravisto una grande sagoma nera nel buio del tunnel.<new_line><new_line>Con un urlo furioso, Nikita è corso da Grigory.]],
				['49'] = [[Ho appena visto il lupo saltare in aria e azzannare senza alcuna pietà la testa del macellaio.<new_line><new_line>Se fossi andato io per primo, sarebbe successo a me.]],
				['59'] = [[È un giubbotto antiproiettile!', ha detto Grigory sorpreso. 'Come ci aiuterà?'<new_line><new_line>'No, non è un giubbotto antiproiettile', ha detto Yury cupamente. 'È imbottito di esplosivi. Hai mai sentito parlare dei kamikaze giapponesi durante la Seconda Guerra Mondiale?'<new_line><new_line>'Che cosa?! Non se ne parla.' Grigory è sbiancato e con voce tremolante ha detto: 'Dev'esserci un altro modo!'<new_line><new_line>'Ad esempio? Come affrontare una lupa mutante con dei fucili? Sta proteggendo i suoi cuccioli, ci farà a pezzi in pochi secondi!']],
				['159'] = [[Ho provato a convincerlo, ma sembrava che Nikita non avesse neppure sentito la mia richiesta.<new_line><new_line>Mi ha dato l'impressione di essere totalmente concentrato sul suo lavoro. Stava aspettando che gettassi la spugna e lo lasciassi in pace.]],
				['39'] = [[Gavrilov si è sforzato per parlare un'altra volta, ma la vita stava già abbandonando il suo corpo.<new_line><new_line>'Nikita... perdonami...'<new_line><new_line>Queste sono state le sue ultime parole.<new_line><new_line>'Perdonarlo? Mi ha salvato!', ha detto Nikita interrompendo il suo triste silenzio. 'Mi ha salvato anche se sapeva che il lupo lo avrebbe ucciso. Perché l'ha fatto? È molto più prezioso lui di me per la squadra!']],
				['133'] = [[Sono lì', ha gridato Grigory, sparando un colpo con il suo fucile e non prendendomi alla spalla per un soffio.<new_line><new_line>Adesso li vedevo anche io. Due lupi, non molto grandi ma comunque letali, stavano per sorprendermi alle spalle. Dopo lo sparo, si sono fermati e hanno digrignato i denti. Non erano feriti: Grigory non li aveva presi.<new_line><new_line>Ho sentito un urlo da brivido davanti a me. È durato un secondo, seguito da un'esplosione di spari.]],
				['157'] = [[La lupa era semplicemente enorme, molto più grande dei suoi cuccioli. Tutta la parte destra del corpo - i muscoli, le costole, gli organi interni - erano stati squarciati dall'esplosione. Si è trascinata sulla zampa posteriore, emettendo dei lamenti orribili e lasciando una scia di sangue e melma.<new_line><new_line>Ma nonostante tutte le ferite, sembrava ancora in grado di difendersi.]],
				['99'] = [[È successo tutto quasi in un secondo. Grigory ha visto Nikita ed è andato verso di lui. Quando Grigory l'ha raggiunto, il ragazzo l'ha colpito con un tubo.<new_line><new_line>Era come se la lupa non aspettasse altro. Il lamento è diventato un ringhio, si è seduta sulle zampe posteriori ed è saltata addosso a Nikita... Poi Nikita ha tirato il cavo del giubbotto di Grigory.<new_line><new_line>L'esplosione è stata così potente da scaraventarmi indietro.]],
				['121'] = [[L'operazione è iniziata. Cercando di muoverci senza fare rumore, ci siamo avvicinati al mattatoio. Poi, al segnale di Gavrilov, i cacciatori sono corsi dietro il camion. Quattro lupi dormivano a poca distanza, dall'altro lato del rimorchio. Abbiamo acceso e lanciato le bombe fumogene oltre il camion.<new_line><new_line>Delle nuvole di fumo puzzolente si alzarono per tutta l'area asfaltata di fronte al mattatoio. Gavrilov ha dato l'ordine di attaccare.]],
				['134'] = [[Mi sono rimesso in piedi a fatica e mi sono reso conto che era finita. Era buio e silenzioso come in una tomba. Ho alzato la torcia e ho dato un'occhiata attorno.<new_line><new_line>A pochi passi da me, seduto vicino a un barile di acciaio, c'era Nikita gravemente ferito. Gli altri erano tutti morti.]],
				['86'] = [[Senza parlare, Yury ha annuito e ha indossato il giubbotto.<new_line><new_line>'Quando Gavrilov me ne ha parlato, ho pensato che avrei dovuto indossarlo io. È un po' stretto!', ha detto per battuta, ma la nostra risata era finta.]],
				['76'] = [[Ok, ci sto', Nikita ha ceduto dopo un secondo. 'È giusto così.'<new_line><new_line>Ha preso una scatola di fiammiferi dalla tasca, ne ha tirati fuori tre e ha spezzato la testa di uno. Ognuno di noi ha preso un fiammifero. Con la mano tremante, Grigory ha pescato per ultimo.<new_line><new_line>A lui è capitato quello più corto.]],
				['66'] = [[Ok, ci sto', Nikita ha ceduto dopo un secondo. 'È giusto così.'<new_line><new_line>Ha preso una scatola di fiammiferi dalla tasca, ne ha tirati fuori quattro e ha spezzato la testa di uno. Ognuno di noi ha preso un fiammifero. Con la mano tremante, Grigory ha pescato per ultimo.<new_line><new_line>A lui è capitato quello più corto.]],
				['56'] = [[Ho sparato al primo lupo in testa, ma il secondo, che era più grande, non ha reagito per niente ai proiettili. Ha solo fatto un salto indietro e poi ha attaccato Grigory.<new_line><new_line>'Sorpresa, lurido animale!', ha gridato il macellaio, posando il fucile. Tra i bagliori degli spari, ho visto che aveva una granata in mano. Grigory ha tirato la sicura e l'ha lanciata verso il lupo.<new_line><new_line>L'esplosione della granata ha fatto a pezzi il cacciatore e la bestia. Io sono stato scaraventato contro il muro.]],
				['46'] = [[Era davvero buio pesto alla fine di quel tunnel. Le torce hanno rivelato dei ganci appuntiti appesi al tetto, dei grandi armadietti di metallo, dei tubi che si snodavano lungo il pavimento, piattaforme, tinozze e macchinari strani e terrificanti che non so neppure come si chiamino. Le pareti e il pavimento erano scuri per le macchie di sangue secche.<new_line><new_line>'Oh Signore...', ha sussurrato Grigory. Ci siamo fermati.]],
				['145'] = [[Un fucile a doppia canna per affrontare la lupa? Siamo rimasti solo in due.'<new_line><new_line>'Dov'è la valigetta di Gavrilov?', ha chiesto Nikita improvvisamente. 'È quello il suo piano B?'<new_line><new_line>La valigetta era vicino al corpo del macellaio.]],
				['26'] = [[Gli avvertimenti di Gavrilov si sono avverati. Abbiamo pagato caro il nostro errore. Attirati dal rumore degli spari, i cinque lupi ci hanno attaccati da dietro.<new_line><new_line>Io ho reagito in tempo ma, a giudicare dalle urla lancinanti, gli altri non sono stati così fortunati. Nikita gridava a fianco a me: un lupo lo aveva spinto a terra. Gavrilov si è precipitato in suo aiuto e ha colpito la bestia con il fucile. Il lupo si è spostato dalla vittima con un ringhio e ha azzannato Gavrilov. Io e Yury siamo accorsi in suo aiuto e abbiamo sparato molti colpi al lupo.]],
				['16'] = [[Io.'<new_line><new_line>Nikita si è girato il lavoro tra le mani e poi l'ha tagliato con il coltello.<new_line><new_line>'Abbiamo sparato a una bestia. Il lupo stava per scappare. Il mio patrigno mi ha gridato di fermarmi, ma io volevo solo dimostrargli che anche io ero tosto, che avrei potuto occuparmi del lupo, ferito oltretutto... Ho seminato gli altri e l'ho inseguito. Con me avevo il nostro unico fucile d'assalto con il caricatore pieno! L'ho raggiunto e l'ho ucciso, ma quando sono tornato dagli altri ho visto il corpo del mio patrigno.']],
				['97'] = [[È deciso, allora...'<new_line><new_line>Grigory mi ha guardato senza parlare per un secondo, poi ha scosso la testa. Ho preso il giubbotto e gliel'ho passato. Muovendosi meccanicamente, come qualcuno ignaro di ciò che sta succedendo, il cacciatore ha indossato il giubbotto e si è diretto rigidamente verso il tunnel.<new_line><new_line>'Non ce la farà', ha detto Nikita a denti stretti.<new_line><new_line>Di fronte al tunnel, Grigory si è girato l'ultima volta. Ci ha guardati come se fossimo estranei e poi si è inoltrato nell'ombra.]],
				['96'] = [[Il mostro non ha ceduto alla morte fino all'ultimo. Quando le munizioni dei fucili d'assalto sono finite, Grigory e Yury hanno cercato di prendere i fucili, ma la lupa ha guaito, cercando di morderci e rifiutandosi di morire.<new_line><new_line>Solo quando abbiamo trasformato il nemico in una massa sanguinante e informe le zampe hanno ceduto e questo incubo vivente, un mostro creato dalle radiazioni, ha esalato l'ultimo respiro.]],
				['95'] = [[Quando tornerai all'accampamento', ha detto Nikita all'improvviso, 'vai a casa nostra. C'è una lettera dietro il lavamano. È indirizzata a una ragazza, Tanya. Yury, tu la conosci. Vive con sua sorella. È proprio... ehm... fantastica.'<new_line><new_line>'La conosco. Gliela faremo avere, tranquillo', ha detto Yury rassicurandolo e poi scherzando: 'Hai buon gusto, ragazzo'.<new_line><new_line>Nikita ha sorriso e ha iniziato a camminare verso il tunnel.]],
				['94'] = [[Il mostro non ha ceduto alla morte fino all'ultimo. Quando le munizioni del fucile d'assalto sono finite, Yury ha cercato di prendere il fucile, ma la lupa ha guaito, cercando di morderci e rifiutandosi di morire. Solo quando abbiamo trasformato il nemico in una massa sanguinante e informe le zampe hanno ceduto e questo incubo vivente, un mostro creato dalle radiazioni, ha esalato l'ultimo respiro.<new_line><new_line>Ho lasciato Yury nell'area di eviscerazione e ho illuminato l'altra parte della cella frigorifera con la torcia. L'intera cucciolata di lupi (più o meno una dozzina) era rannicchiata alla parete. Li ho uccisi tutti.<new_line><new_line>La missione era compiuta.]],
				['9'] = [[Allora iniziamo', ha detto Gavrilov annuendo e alzandosi in piedi. 'Gente, basta chiacchiere, si parte!'<new_line><new_line>I cacciatori hanno iniziato a prepararsi.<new_line><new_line>'Mi faresti un favore?', ha sussurrato Gavrilov portandomi da parte. 'Non vedo Nikita da più di un'ora. Vai a trovarlo. Dovrebbe essere dall'altra parte dell'autostrada. Ci andrei io stesso, ma con me non vuole parlare.']],
				['89'] = [[Il mostro non ha ceduto alla morte fino all'ultimo. Quando le munizioni dei fucili d'assalto sono finite, Grigory e Nikita hanno cercato di prendere i fucili, ma la lupa ha guaito, cercando di morderci e rifiutandosi di morire.<new_line><new_line>Solo quando abbiamo trasformato il nemico in una massa sanguinante e informe le zampe hanno ceduto e questo incubo vivente, un mostro creato dalle radiazioni, ha esalato l'ultimo respiro.]],
				['88'] = [[La creatura davanti a noi, illuminata dalla luce fioca e tremolante delle torce, era solo vagamente somigliante a un lupo normale. La lupa non aveva pelliccia o pelo, il suo grottesco corpo gonfio era pieno di tumefazioni e pustole purulente, e del disgustoso liquido giallo le colava dalle fauci e dagli occhi.]],
				['87'] = [[Non abbiamo dovuto aspettare molto. Nel giro di dieci secondi, i terribili guaiti dall'altra parte il tunnel sono aumentati e poi c'è stato un boato dalla cella frigorifera che ci ha fatto tremare la terra sotto i piedi.<new_line><new_line>Per un attimo ho pensato che il soffitto sarebbe crollato, ma poi la terra ha smesso di tremare.]],
				['81'] = [[Ok, sei tu al comando', ha detto Nikita alzando le spalle e girandosi dall'altra parte.<new_line><new_line>Ho indossato velocemente il giubbotto senza concedermi il tempo di ripensarci.]],
				['80'] = [[Uno di noi dovrà sacrificarsi, indossare il giubbotto, entrare nella cella frigorifera e quando la lupa è nei paraggi, tirare il cavo', ha detto Nikita.]],
				['8'] = [[Non appena la lupa è morta, non prima', è intervenuto l'ispettore guardandomi con uno sguardo freddo. 'Accettate il mio consiglio e dimenticatevi di tutto il resto. Se non siamo concentrati, se non collaboriamo, nessuno tornerà a casa. Ognuno di noi è responsabile per il resto della squadra. Intesi?<new_line><new_line>'Intesi.'<new_line><new_line>'Eccellente.']],
				['106'] = [[La creatura davanti a me, illuminata dalla luce fioca e tremolante della torcia, era solo vagamente somigliante a un lupo normale. La lupa non aveva pelliccia o pelo, il suo grottesco corpo gonfio era pieno di tumefazioni e pustole che spruzzavano pus, e del disgustoso liquido giallo le colava dalle fauci e dagli occhi.]],
				['79'] = [[Nella valigetta c'era un giubbotto antiproiettile.<new_line><new_line>'No, non è un giubbotto antiproiettile', ha detto Nikita dopo una pausa. 'È imbottito di esplosivi. Lo indossi, vai dalla lupa e tiri il cavo...'<new_line><new_line>Quelle parole sono rimaste nell'aria. Ho chiuso la valigetta.<new_line><new_line>'Proviamo a riflettere. Dev'esserci un altro modo...'<new_line><new_line>'Ad esempio? Siamo rimasti solo noi due e la lupa ci farà a pezzi in pochi secondi.']],
				['78'] = [[Sta' indietro', ha detto Nikita scuotendo la testa. 'Con cosa lo ucciderai? Ho quasi finito le munizioni.'<new_line><new_line>'Hai controllato le armi delle vittime?'<new_line><new_line>'Sì, stessa storia. E abbiamo portato tutte le 39 mm che c'erano a Gorenichi.']],
				['142'] = [[Dubito che ci aiuteranno molto contro la lupa. E poi, siamo rimasti solo in tre.'<new_line><new_line>'Dov'è la valigetta di Gavrilov?', ha chiesto Nikita improvvisamente. 'È quello il suo piano B?'<new_line><new_line>La valigetta era vicino al corpo del macellaio.]],
				['44'] = [[Nonostante la brutta sensazione alla bocca dello stomaco, abbiamo attraversato il tunnel del macello senza problemi.<new_line><new_line>Siamo usciti in una stanza spaziosa con il pavimento sporco e appiccicoso e con le pareti di un marrone rossastro. Lungo le pareti luccicavano delle griglie metalliche da cui pendevano dei ganci terrificanti. Di lato alla piattaforma su cui ci trovavamo, c'erano degli strumenti da macello abbandonati a caso e più avanti abbiamo visto un mucchio di ossa e là in mezzo c'era un cranio umano.]],
				['103'] = [[Il mostro non ha ceduto alla morte fino all'ultimo. Quando le munizioni del fucile d'assalto sono finite, Grigory ha cercato di prendere il fucile, ma la lupa ha guaito, cercando di morderci e rifiutandosi di morire. <new_line><new_line>Solo quando abbiamo trasformato il nemico in una massa sanguinante e informe le zampe hanno ceduto e questo incubo vivente, un mostro creato dalle radiazioni, ha esalato l'ultimo respiro.]],
				['153'] = [[La lupa era semplicemente enorme, molto più grande dei suoi cuccioli. Tutta la parte destra del corpo - i muscoli, le costole, gli organi interni - erano stati squarciati dall'esplosione. Ha strisciato verso di me, emettendo dei lamenti orribili e lasciando una scia di sangue e melma.<new_line><new_line>Ma nonostante tutte le ferite, sembrava ancora in grado di difendersi.]],
				['7'] = [[Il piano è semplice: non morire', ha detto uno dei cacciatori, Grigory.<new_line><new_line>'Riflettiamo sul peggiore dei casi, chiamato anche il caso più probabile', ha detto Gavrilov. 'C'è un branco di lupi attorno al mattatoio. Abbiamo delle bombe fumogene con cui prendere le bestie di sorpresa. Le uccidiamo ancor prima che si rendano conto cosa sia successo. Poi entriamo nell'edificio, troviamo la lupa e la eliminiamo in fretta.']],
				['40'] = [[Ma forse', ho aggiunto, 'ha voluto ripagare il suo debito nei confronti del tuo patrigno, che lo ha salvato a Kiev. Non lo so. So solo che era un brav'uomo. Possa la sua anima riposare in pace.'<new_line><new_line>Nikita mi ha guardato addolorato.<new_line><new_line>'So che è morto per colpa mia, come il mio patrigno, e che ora dovrò vivere con questo peso.'<new_line><new_line>Non avevo idea di come aiutarlo.]],
				['67'] = [[Io?', Grigory ha deglutito e ha lanciato un'occhiataccia a noi tre. 'No. No!'<new_line><new_line>'Grigory...', Yury ha cercato di avvicinare Grigory a sé.<new_line><new_line>'Che c'è, Yury, che c'è?!' La voce di Gregory era quasi un lamento. 'A voi non cambia niente, non avete nessuno, ma io ho una moglie e un figlio che sta per compiere due anni... Mi avete trascinato in questa caccia disperata, abbiamo rischiato la vita così tante volte a scapito della vita di tante persone, e non ho mai detto una parola! Ma questo... Non ne abbiamo mai parlato!']],
				['77'] = [[Io?', Grigory ha deglutito e ha lanciato un'occhiataccia a noi tre. 'No. No!'<new_line><new_line>'Grigory...', ho provato a calmarlo.<new_line><new_line>'Che c'è?!', la voce di Gregory era quasi un lamento. 'Lo sai che ho una moglie e un figlio che sta per compiere due anni che mi aspettano? Sono stato trascinato in questa caccia disperata, abbiamo rischiato la vita così tante volte a scapito della vita di tante persone, e non ho mai detto una parola! Ma questo... Non ne abbiamo mai parlato!']],
				['144'] = [[Mi ha detto di non avvicinarci o che l'avrebbe detonata subito.'<new_line><new_line>'È già entrato?'<new_line><new_line>'Sì.']],
				['131'] = [[Lì vicino il macellaio ha emesso un grido furioso, seguito da una pioggia di spari. Il lupo ha piegato la testa da un lato e poi è scomparso.]],
				['148'] = [[La lupa era semplicemente enorme, molto più grande dei suoi cuccioli. Tutta la parte destra del corpo - i muscoli, le costole, gli organi interni - erano stati squarciati dall'esplosione. Ha strisciato verso di noi, emettendo dei lamenti orribili e lasciando una scia di sangue e melma.<new_line><new_line>Ma nonostante tutte le ferite, sembrava ancora in grado di difendersi.]],
				['112'] = [[Cinque uomini erano armati di fucili d'assalto, gli altri di fucili a canne mozze o a doppia canna. Ricordavo che le armi automatiche erano efficaci contro i lupi neri, ma i proiettili singoli non gli facevano né caldo né freddo. Speravamo di non trovarci davanti un grande branco.<new_line><new_line>Gavrilov mi ha visto e ha interrotto il suo racconto per chiamarmi.]],
				['156'] = [[Non hai paura?', ho chiesto.<new_line><new_line>'No. Il mio unico rimpianto è non aver detto a Tanya, la ragazza di Gorenichi, che la trovo fantastica.'<new_line><new_line>Nikita ha sorriso e si è diretto verso il tunnel.]],
				['120'] = [[L'ex ispettore, Nikita e altri cinque cacciatori si sono diretti a sud passando dalla boscaglia, mentre io, Yury e altri quattro siamo corsi dietro il camion. I lupi dormivano a poca distanza, dall'altro lato del rimorchio.<new_line><new_line>Al comando di Yury, abbiamo acceso e lanciato le bombe fumogene oltre il camion. Delle nuvole di fumo puzzolente si alzarono per tutta l'area asfaltata di fronte al mattatoio. Yury ha dato l'ordine di attaccare.]],
				['140'] = [[Ci ha detto di non avvicinarci o che l'avrebbe detonata subito.'<new_line><new_line>'È già entrato?'<new_line><new_line>'Sì.']],
				['111'] = [[Non abbandonerebbe i cuccioli', ho detto cercando di mantenere un tono deciso. 'Significa che è nella cella frigorifera. La porta posteriore è bloccata. È in trappola.'<new_line><new_line>Un po' a conferma delle mie parole, si è sentito un lamento penetrante dal tunnel.]],
				['101'] = [[Il mostro non ha ceduto alla morte fino all'ultimo. La lupa ha guaito, cercando di mordermi e rifiutandosi di morire. Solo quando ho trasformato il mio nemico in una massa sanguinante e informe le zampe hanno ceduto e questo incubo vivente, un mostro creato dalle radiazioni, ha esalato l'ultimo respiro.<new_line><new_line>Senza perdere altro tempo, sono andato nella cella frigorifera. Un'intera cucciolata di lupi (più o meno una dozzina) era rannicchiata alla parete. Li ho uccisi.<new_line><new_line>La missione era compiuta.]],
				['17'] = [[Ora tocca a me parlare', ho detto guardando l'orizzonte. 'Quando stavo andando a Kiev, ho incontrato una brava persona. Pensava che fossi un bandito, allora mi ha legato con l'intenzione di torturarmi. Ma poi un lupo l'ha attaccato, un lupo che mi aveva seguito da Brjansk. Ha decapitato quell'uomo.']],
				['27'] = [[Ok', ha detto Gavrilov. Yury ci ha pensato per un momento, ma poi ha annuito anche lui. Era deciso.<new_line><new_line>L'operazione è iniziata. Cercando di muoverci senza fare rumore, ci siamo avvicinati al mattatoio.]],
				['37'] = [[Hai ragione!', ha sospirato l'uomo in fin di vita con gratitudine. 'Yury, puoi piangere più tardi. Di' agli altri di prepararsi. Ridistribuisci le armi. Raccogli tutte le cartucce rimaste...'<new_line><new_line>'Pavel, e se la lupa non è sola nel mattatoio?', Yury lo ha interrotto con la voce carica di rabbia. 'E se ci sono altri lupi?'<new_line><new_line>'Te la caverai. Devi farlo. Devi porre fine a questa storia. Se lasciamo che si riproducano, massacreranno tutto il villaggio per vendetta...']],
				['47'] = [[Ho avuto a malapena il tempo di vedere le pustole gialle e purulente su un muso irregolare, che il lupo è balzato in aria. Ho fatto un passo indietro. L'enorme creatura ha serrato i denti e mi ha colpito sul petto con una tale forza da farmi sbattere contro il muro.<new_line><new_line>Sono scivolato sulla parete fino a terra. Quando il ronzio nelle orecchie si è placato, ho sentito le urla lancinanti di qualcuno, quasi subito interrotte dal rumore ripetitivo degli spari...<new_line><new_line>Un muso terrificante del colore della resina è apparsa di fronte alla mia faccia e le sue fauci infernali hanno iniziato ad aprirsi...]],
				['57'] = [[Proteggendo la loro madre, i lupi hanno combattuto fino all'ultima goccia di sangue, ma la lupa non era nella stanza di eviscerazione, né viva né morta.<new_line><new_line>'È scappata! È scappata! È stato tutto inutile!', ha esclamato Nikita con le mani alla testa in preda alla rabbia.<new_line><new_line>'Aspetta. Guarda', ho indicato il tunnel che portava alla cella frigorifera. La luce della mia torcia ha illuminato un piccolo cucciolo di lupo nero che si ritirava velocemente nel buio.]],
				['155'] = [[Ho lasciato Grigory nell'area di eviscerazione e ho illuminato l'altra parte della cella frigorifera con la torcia. Come speravo, l'intera cucciolata di lupi (più o meno una dozzina) era morta nell'esplosione.<new_line><new_line>A quel punto non ci restava che uscire da lì.]],
				['100'] = [[Quando mi sono alzato, l'ho visto di nuovo...<new_line><new_line>La creatura davanti a me, illuminata dalla luce fioca della torcia, era solo vagamente somigliante a un lupo normale. La lupa non aveva pelliccia o pelo, il suo grottesco corpo gonfio era pieno di tumefazioni e pustole che spruzzavano pus, e del disgustoso liquido giallo le colava dalle fauci e dagli occhi.]],
				['115'] = [[C'eri anche tu lì, forse hai visto il corpo', ha confermato Gavrilov. 'Aveva finito le cartucce, ma non poteva ritirarsi, quindi ha attaccato il lupo con un coltello. Un coltello!'<new_line><new_line>Mi sono ricordato di quel corpo con le mani sbranate. I cacciatori non hanno parlato per un secondo, in segno di rispetto.<new_line><new_line>'Altre domande?']],
				['104'] = [[Che ne pensi?', ha chiesto Nikita all'improvviso. 'C'è qualcosa qui?'<new_line><new_line>'A cosa ti riferisci?', ho detto non capendo cosa volesse dire.<new_line><new_line>'Sai... lì. Oltre la linea. Sai cosa penso? Qualunque cosa ci sia là fuori, scommetto che non è né un'epidemia né le radiazioni né lupi neri mangia-uomini. Scommetto che si sta bene.']],
				['2'] = [[Ho contato undici combattenti. Erano le uniche persone che avevano deciso di aiutare Gavrilov a combattere i lupi in arrivo. E non erano molte.]],
				['24'] = [[Abbiamo fatto il giro del camion e abbiamo aperto il fuoco. Tre di noi contro ciascun lupo, come Gavrilov ci aveva detto. Li abbiamo presi alla sprovvista. Era tutto finito in trenta secondi. Ma abbiamo festeggiato troppo presto...<new_line><new_line>'Ah, maledizione', ha gridato Yury quando il lupo che un secondo prima si contorceva a terra aveva azzannato la gola del suo compagno. Yury ha crivellato di proiettili la bestia, che ha mollato la preda, spinto un altro cacciatore a terra e ha affondato le zanne nel suo ventre. È caduto gridando per il dolore e per il terrore, cercando invano di colpire il lupo sulla testa con il fucile.]],
				['29'] = [[Ho iniziato a sparare alla creatura, ma era come se i proiettili neppure la sfiorassero: ha continuato ad addentare il volto della sfortunata vittima. Un altro cacciatore è venuto in soccorso, ma il lupo ha fatto un balzo dal corpo sanguinante su cui si trovava e ha agguantato la sua nuova vittima. Io e Yury siamo corsi lì e abbiamo sparato tanti colpi sulla testa del lupo. Il lupo è morto, ma la vittima era ormai spacciata. Le viscere del cacciatore fuoriuscivano dal suo addome squarciato.<new_line><new_line>La breve e terrificante lotta era finita.<new_line><new_line>Ma non avevamo tempo di rilassarci. Da sud arrivava un suono di spari.]],
				['36'] = [[Nikita è venuto da noi insieme a Yury. Un solo sguardo all'uomo ferito gli ha fatto capire quello che gli serviva sapere: non c'era speranza.<new_line><new_line>'Pavel, maledizione...', sono state le sue uniche parole.<new_line><new_line>'È finita per me, Yury. Occupati degli altri feriti. E non perdere tempo, devi finire la missione.'<new_line><new_line>'Finire la missione?!', ha detto Yury impallidendo. 'Chi la finirà? Abbiamo già perso troppe persone! Medicheremo i feriti e torneremo a Gorenichi!']],
				['130'] = [[La scena di fronte ai nostri occhi sembrava uscita da un incubo. C'erano corpi dilaniati vicino ai contenitori. Erano morti da poco, non erano neppure in putrefazione. Alcuni avevano solo la testa intatta: sotto il mento, la spina dorsale luccicava di bianco al buio...<new_line><new_line>Con voce tremolante, Grigory dice: 'Quelli sono Misha, Andrej... Sergey Chernyshow... Questi sono i nostri uomini... Erano così vicini e...'<new_line><new_line>Le sue parole sono state coperte da un terribile ringhio.]],
				['138'] = [[Ho ripensato al cacciatore decapitato. In quell'occasione, non ho avuto altra scelta. Poi ho pensato a Pavel Gavrilov, al macellaio e agli altri che sono morti durante la caccia. Ho fatto una scelta, ma non posso essere certo delle conseguenze. Ma stavolta è completamente diverso...<new_line><new_line>Io, Yury, Grigory o Nikita. Uno di noi deve sacrificare la vita.]],
				['161'] = [[Diciamo', ha sospirato Nikita. 'Il mio patrigno l'aveva portato dall'Europa quando era giovane, più giovane di me adesso. Lo portava sempre con sé, sai. Lo teneva sotto il cuscino quando dormiva; non lo posava neppure quando andava a nuotare. Mi ha sempre fatto ridere. Una volta, ho nascosto il coltello per scherzo e il mio patrigno me le ha suonate. Ha infilato la lama in un pezzo di legno e ha detto: 'Quando il fucile s'inceppa, quando la granata non esplode, quando l'amico scappa, non ti rimane altro che il tuo coltello'. Quest'affare gli ha salvato la vita tre volte.']],
				['152'] = [[È finita?', ha chiesto Grigory sottovoce.<new_line><new_line>'Sì', ha detto Nikita annuendo. 'Yury ce l'ha fatta...'<new_line><new_line>'Andiamocene da qui', ha detto Grigory sollevato. Ha sospirato, si è alzato...<new_line><new_line>E poi la lupa è comparsa dal tunnel buio.]],
				['163'] = [[Nikita ha esitato. Alla fine, ha posato il coltello e il lavoro e si è massaggiato le tempie.<new_line><new_line>'A Kiev, il mio patrigno aveva finito le cartucce e ha ucciso un lupo con il suo coltello, ma poi...', la gola di Nikita si è chiusa, '...un'altra bestia l'ha attaccato da dietro e gli ha staccato la mano. Che ironia della sorte, eh? Aveva un coltello, ma non la mano per brandirlo... Il lupo non lo ha neppure ucciso. Aveva puntato Gavrilov, ma il mio patrigno lo ha afferrato con le braccia e lo ha trattenuto. Il lupo lo ha azzannato, ma il mio patrigno ha resistito... finché non è morto dissanguato. Se Seva fosse arrivato trenta secondi dopo, il lupo avrebbe ucciso anche Gavrilov.']],
				['45'] = [[Nell'angolo, delle gocce d'acqua colavano ritmicamente sul pavimento di cemento. Dall'altra parte del tunnel, un graffio a malapena udibile ci ha raggiunto le orecchie.<new_line><new_line>'Cosa c'è dopo il tunnel?', ho chiesto all'ex macellaio.<new_line><new_line>'La stanza di eviscerazione. Sai, dove si rimuovono le interiora e si dividono le carcasse. Dopo c'è la cella frigorifera. La lupa sarà probabilmente da quelle parti.']],
				['48'] = [[Avevo un ronzio nella testa, la vista era offuscata e sfocata come un acquarello, ma mi sono sforzato di alzarmi. C'era un'arma a portata di mano. L'avevo quasi presa, quando ho sentito un rumore ripugnante.<new_line><new_line>A pochi passi da me, quella bestia orrenda stava strappando a morsi la carne del povero macellaio morto. Proprio come nella radura...<new_line><new_line>No, questa volta sarebbe andata diversamente e non sarei scappato!]],
				['55'] = [[Ho sparato al primo lupo in testa, ma il secondo, che era più grande, non ha reagito per niente ai proiettili. Ha solo fatto un salto indietro e poi ha attaccato Grigory.<new_line><new_line>'Sorpresa, lurido animale!', ha gridato il macellaio, posando il fucile. Tra i bagliori degli spari, ho visto che aveva una granata in mano. Grigory ha tirato la sicura.<new_line><new_line>Più avanti, Nikita ha gridato. Ed è stato in quel momento che il lupo è saltato addosso a Grigory. L'esplosione della granata ha fatto a pezzi il cacciatore e la bestia. Io sono stato scaraventato contro il muro.]]
			},
			['name'] = [[Mattatoio]],
			['choice'] = {
				['10'] = [[Un altro? Chi?']],
				['68'] = [[Scusa, ma sono io il comandante e spetta a me decidere.']],
				['30'] = [[Ascolta attentamente]],
				['20'] = [[Torna dai feriti]],
				['50'] = [[Non è sicuro che sarà sola. Questa stanza di eviscerazione è grande?']],
				['40'] = [[Ascolta, mi dispiace tanto...']],
				['70'] = [[Combatti]],
				['60'] = [[Scusa, Nikita, ma tu non verrai. Ho scelto Grigory e la decisione è definitiva.']],
				['24'] = [[Nikita, fermo!']],
				['14'] = [[Ok, andiamo.']],
				['44'] = [[Torna da lui]],
				['34'] = [[Vado con il secondo gruppo, contro i lupi svegli.']],
				['38'] = [[Perché?']],
				['28'] = [[Procedi]],
				['58'] = [[Fucili a doppia canna?']],
				['48'] = [[Fai andare avanti il macellaio e resta in coda (non è pericoloso come stare in testa)]],
				['2'] = [[Chi è a capo della squadra?']],
				['1'] = [[Li ho già visti prima. Ne ho ucciso uno. A Mosca.']],
				['69'] = [[Perché non l'hai fermato?']],
				['11'] = [[Continua...]],
				['61'] = [[Va bene, se hai deciso così, fai pure.']],
				['71'] = [[Dimmi, pensi davvero che Gavrilov sia responsabile per quello che è successo a Kiev?']],
				['41'] = [[Continua ad ascoltare]],
				['65'] = [[Andrà Grigory.']],
				['35'] = [[Credo che Yury abbia ragione. Non ha senso dividere la squadra. Possiamo occuparci di questi quattro insieme e poi far fuori il resto.']],
				['45'] = [[Vai da lui]],
				['15'] = [[Schiva e spara]],
				['7'] = [[Gavrilov pensa che lo ritieni responsabile per la morte del suo patrigno.']],
				['6'] = [[Non ho altre domande.']],
				['5'] = [[Qual è il piano?']],
				['4'] = [[Quando mi dirai quello che mi hai promesso?']],
				['39'] = [[Forse puoi ancora dirmi cosa ti dà fastidio. So che non mi conosci, ma presto me ne andrò. Parlare ti farà sentire meglio. Provaci.']],
				['32'] = [[E?']],
				['22'] = [[Yury, no, non arrenderti!']],
				['12'] = [[Credi che sia responsabile della morte di quest'uomo?']],
				['62'] = [[Ho un'idea. Lasciamo scegliere il destino. Andrà chi pesca il fiammifero più corto.']],
				['52'] = [[Girati]],
				['42'] = [[Sì. È il coltello di Kirill?']],
				['46'] = [[Spara in aria per fare allontanare i lupi dalle vittime]],
				['26'] = [[Aspetta]],
				['16'] = [[Uccidi il lupo!]],
				['9'] = [[Dimmi, pensi davvero che Gavrilov sia responsabile per quello che è successo?']],
				['82'] = [[Mi dispiace per com'è andata a finire, ma cos'ha fatto di male Gavrilov? Anche lui ha quasi perso la vita.']],
				['67'] = [[Andrò io.']],
				['77'] = [[Uccidiamolo! Subito!']],
				['81'] = [[Chi ti ha insegnato a intagliare così bene?']],
				['80'] = [[Non parlare]],
				['53'] = [[Alzati e punta l'arma]],
				['63'] = [[Nikita, ascolta...']],
				['73'] = [[È stato orribile, ma non c'era niente che potessi fare. Non avresti potuto fare niente neppure tu.']],
				['8'] = [[Capito. Chi ti ha insegnato a intagliare?']],
				['13'] = [[Ma io non c'entravo niente! E tu avresti potuto fare qualcosa?']],
				['23'] = [[Vai nella cella frigorifera]],
				['33'] = [[Vado con il primo gruppo, contro i lupi addormentati.']],
				['43'] = [[Combatti!]],
				['78'] = [[Bel coltello. Non ne ho mai visto uno così.']],
				['37'] = [[Ok, lo troverò.']],
				['47'] = [[Mettiti nella posizione più rischiosa e passa per primo dal tunnel]],
				['57'] = [[Porta la valigia e aprila]],
				['76'] = [[Andrai tu.']],
				['75'] = [[Perché non l'hai fermato?']],
				['29'] = [[Vai verso il corridoio del mattatoio]],
				['3'] = [[Dov'è nascosta la lupa?']],
				['31'] = [[Continua a muoverti]],
				['17'] = [[Corri lì]],
				['18'] = [[Yury, ha ragione. Se non ce ne andiamo adesso, tutte queste persone saranno morte per niente. Mentre perdiamo tempo a riorganizzarci, i lupi invaderanno questo posto. Dobbiamo uccidere la lupa, subito.']],
				['19'] = [[Si sarà sentito davvero in colpa nei tuoi confronti.']],
				['21'] = [[Prosegui nel mattatoio]],
				['49'] = [[Fai andare avanti il macellaio e vai nel centro del gruppo (questa è la posizione migliore)]],
				['25'] = [[Alzati e accendi la torcia]],
				['51'] = [[Continua a sparare]],
				['56'] = [[Spara ai lupi]],
				['59'] = [[Scusa, Nikita, ma tu non verrai. Ho scelto Yury e la decisione è definitiva.']],
				['64'] = [[Andrà Yury.']],
				['66'] = [[Andrà Nikita.'<new_line>]]
			}
		},
		['sasha'] = {
			['episode'] = {
				['114'] = [[Ho ignorato le furiose proteste di Sasha e l'ho fatta rimanere dietro l'UAZ. Non era una copertura ottimale, ma correre in un altro punto sarebbe stato ancora più pericoloso.<new_line><new_line>I combattenti si sono fermati a un paio di passi dalla macchina. Tre venivano da sinistra e due da destra.<new_line><new_line>'Ci uccideranno ancor prima di riuscire a farne fuori anche solo uno! Sono in troppi!', mi ha bisbigliato Sasha nell'orecchio.]],
				['118'] = [[Ci nasconderemo dietro il Kamaz e spareremo da lì!', le ho risposto.<new_line><new_line>Sasha ha guardato dietro il camion dietro di noi e si è leccata le labbra secche:<new_line><new_line>'Sì, buona idea! Ma non so se servirà a qualcosa. Sono in sette.'<new_line><new_line>Abbiamo strisciato fino all'abitacolo del Kamaz. Sparare da qui sarebbe stato più sicuro che da tutte le altre parti. Era la soluzione migliore, date le circostanze.]],
				['141'] = [[Dai, non fate niente di stupido. Non uscirete da Stepnoy vivi', ha sottolineato il soldato calvo e con il pizzetto. 'Arrendetevi e nessuno si farà male.'<new_line><new_line>'Nessuno si farà male?', ha ripetuto Sasha. 'Che coraggio, da parte di qualcuno che ha un'arma puntata addosso.' Si è girata verso di me. 'Cosa ne facciamo di loro?']],
				['64'] = [[Non c'era molta benzina nella tanica, quindi mi sono premurato di versarla per bene. Doveva coprire più spazio possibile.<new_line><new_line>L'edificio era in legno, ma non ero certo che il fuoco avrebbe attecchito subito. Non potevo permettere che gli abitanti spegnessero il fuoco prima che l'edificio bruciasse e prima che le taniche di gasolio fossero a rischio di esplosione!<new_line><new_line>Ora devo solo uscire e appiccare il fuoco.]],
				['54'] = [[Sono nelle docce. Puzza di umidità, di muffa e di qualcos'altro ancora più spiacevole.<new_line><new_line>Sotto le panche nello spogliatoio, ho visto dei ratti morti da tanto tempo.]],
				['84'] = [[Sasha non ha detto niente, come se stesse valutando le sue opzioni.<new_line><new_line>'Va bene. Diciamo che ti credo. Hai un piano? Come pensi di uscire da questa città infestata dai soldati?']],
				['74'] = [[Il secondino mi ha fissato, confuso.<new_line><new_line>'Ho sentito l'allarme, ma devo fare la guardia ai prigionieri, a prescindere da quello che succede. Sono le regole.' Ha rallentato. 'Che ci fai tu qui, in ogni caso? Perché non spegni il fuoco insieme a tutti gli altri?']],
				['109'] = [[I proiettili hanno rotto i finestrini dell'UAZ e siamo stati colpiti da una pioggia di schegge. Pochi secondi dopo, il fuoco ha smesso.<new_line><new_line>Ho alzato la testa e ho visto i nostri inseguitori dai finestrini rotti. Erano in sette, tutti armati di AK. Avevano attraversato il ponte e si stavano avvicinando.<new_line><new_line>'Dobbiamo andare via da qui o ci circonderanno e saremo spacciati!', Sasha ha sussurrato.]],
				['14'] = [[Permesso accordato. Chiedi', ha detto altezzosamente il soldato con i capelli rossi annuendo. Gli altri ridacchiavano.]],
				['110'] = [[Corri alla macchina!', ho risposto.<new_line><new_line>Muovendoci a zig zag tra le moto, siamo arrivati alla macchina. Gli inseguitori hanno aperto il fuoco, tutti contemporaneamente. Ci siamo dovuti sdraiare sull'asfalto.<new_line><new_line>I nostri avversari sapevano dove eravamo diretti e ci stavano intralciando di proposito. Provare a correre più lontano sarebbe un suicidio.]],
				['34'] = [[Il canale è molto profondo, oltre quattro metri, ma è pieno d'acqua solo per metà. Il canale è alimentato dal fiume Tom ed è la fonte di acqua fresca di Stepnoy.<new_line><new_line>Le sponde del canale sono quasi a strapiombo. Se mi tuffo, non riuscirò a risalire senza un'attrezzatura speciale, e neanche un veicolo riuscirebbe ad attraversarlo.<new_line><new_line>L'unico ponte che attraversa il canale è un ponte pedonale che va dal piazzale di lato ai garage verso la piazza d'armi.]],
				['123'] = [[Il veicolo ha solo scoppiettato. Ho provato di nuovo, con lo stesso risultato.<new_line><new_line>'A Stepnoy c'è un buon meccanico, se te ne serve uno', ha detto Boris, concedendosi questa piccola battuta.<new_line><new_line>Ho girato la chiave con così tanta furia che si è leggermente piegata. Il veicolo ha scoppiettato di nuovo e all'improvviso si è accesa! Incredulo della fortuna che avevo appena avuto, ho premuto l'acceleratore.<new_line><new_line>Abbiamo guidato velocemente in avanti.]],
				['107'] = [[I proiettili hanno rotto i finestrini dell'UAZ e siamo stati colpiti da una pioggia di schegge. Pochi secondi dopo, il fuoco ha smesso.<new_line><new_line>Ho alzato la testa e ho visto i nostri inseguitori dai finestrini rotti. Erano in tre, tutti armati di AK. Avevano attraversato il ponte e si stavano avvicinando.<new_line><new_line>'Dobbiamo andare via da qui o ci circonderanno e saremo spacciati!', Sasha ha sussurrato.]],
				['122'] = [[Boris ha socchiuso gli occhi.<new_line><new_line>'E se mi rifiutassi?'<new_line><new_line>'Allora riceverai un regalo. Un regalo letale.'<new_line><new_line>'Nessuno dei due vivrà molto più a lungo di me.']],
				['124'] = [[Dopo circa dieci chilometri, al massimo della velocità, mi sono fermato al lato della strada.<new_line><new_line>'Puoi tornare dai tuoi amici', ho detto al mio prigioniero. 'O puoi aspettare qui che ti trovino. Credo che ti stiano già cercando.'<new_line><new_line>'Lo stai liberando davvero?', mi ha chiesto Sasha stupita. 'Sei serio? È da quando siamo partiti che aspetto di fare saltare le cervella a questo stronzo!'<new_line><new_line>Le ho gridato: 'Basta!']],
				['1'] = [[Ho trascorso il resto della giornata a pensare agitatamente a cosa stavo affrontando. Steso sulla brandina nell'infermeria, ho riflettuto su tutte le possibili soluzioni.<new_line><new_line>Per liberare Sasha, devo aprire la cella. Sasha ha detto che la chiave è nell'ufficio di Boris. Ha senso: lei è una prigioniera importante per lui.<new_line><new_line>Supponiamo che riesca a prendere la chiave e ad aprire la cella. Come faremo a uscire tutti e due dalla città fortificata?]],
				['151'] = [[Non è servito a niente...<new_line><new_line>'Sono arrivati', Sasha ha riso amaramente. 'Ed eravamo così vicini!'<new_line><new_line>È scesa dalla macchina, ha camminato per qualche metro verso il cancello ed è tornata subito indietro.<new_line><new_line>'Abbiamo un minuto per trovare una soluzione. C'è una squadra sul ponte. Dieci o dodici soldati, tutti armati, e Boris è con loro.']],
				['75'] = [[Cosa?!', il secondino ha spalancato gli occhi. 'Sono appena stati qui per prendere dei secchi. Il maggiore, il capitano e alcuni soldati... Hanno preso tutto quello che c'era in cucina e nel deposito. Stai cercando di distrarmi, vero? Perché sei qui?'<new_line><new_line>Il secondino ha spostato la mano verso il fucile. Il tempo delle chiacchiere era finito.]],
				['85'] = [[O sei un pazzo coraggioso o sei del tutto idiota!', ha detto Sasha ridendo e prendendo l'arma. Ma c'era una sfumatura di rispetto nella sua voce. 'Solo non iniziare a dirmi quando sparare o quando fermarmi. So quello che faccio. Non hai idea dell'inferno che ho dovuto affrontare.']],
				['55'] = [[Non ho trovato niente di particolarmente utile, solo vecchi accessori da bagno masticati.<new_line><new_line>Il tanfo in questo posto è insopportabile. Devo prendere una boccata d'aria fresca al più presto.]],
				['65'] = [[Sono nell'unità di comando. Ci sono una bottiglia di vino vuota e due bicchieri sulla scrivania di Boris. Il cappotto del maggiore è appeso alla porta.<new_line><new_line>C'è un ritratto di Brezhnev sulla parete e, di fianco, una grande mappa dell'Unione Sovietica. Nell'angolo, c'è un vecchio radiotelegramma.<new_line><new_line>I mobili, il pavimento e le pareti sono tutte cosparse di benzina.]],
				['35'] = [[Si vede una luce accesa nella finestra dell'edificio di cemento a un piano. Cerco di stare lontano dalla luce così le guardie non mi vedranno; faccio il giro dell'edificio e resto fuori dalla porta. È chiusa.]],
				['119'] = [[Dopo aver eliminato i nostri avversari, ci siamo trascinati alla macchina. Sasha è salita per prima e io l'ho seguita.<new_line><new_line>Dovevamo fare l'ultima piccola cosa: sfondare il cancello. Ho inserito la chiave nell'accensione, l'ho girata e... niente.<new_line><new_line>Porca miseria! Ho provato più volte a far partire la macchina, ma non è servito a niente! Mi sono appoggiato al sedile e ho fatto un verso di impotenza. Ci avrebbero catturato per uno stupido problema al motore!]],
				['15'] = [[Boris è un tipo serio', ha detto il soldato con i capelli rossi. 'Ha esperienza, sa come comportarsi. La gente lo ama. Non è perfetto, ma non è neppure uno stupido o uno stronzo come certi ufficiali. Ha dei principi e non ha mai trattato nessuno con i guanti bianchi, neppure suo figlio.']],
				['25'] = [[Il canale è molto profondo, oltre quattro metri, ma è pieno d'acqua solo per metà. Il canale è alimentato dal fiume Tom ed è la fonte di acqua fresca di Stepnoy.<new_line><new_line>Le sponde del canale sono quasi a strapiombo. Se mi tuffo, non riuscirò a risalire senza un'attrezzatura speciale, e neanche un veicolo riuscirebbe ad attraversarlo.<new_line><new_line>L'unico ponte pedonale è nella piazza d'armi.]],
				['6'] = [[Sono nel giardino abbandonato dietro l'infermeria. Non c'è niente di interessante qui. Il seghetto arrugginito è inutile, ma ho preso il piede di porco.]],
				['5'] = [[Qui, tempo fa, qualcuno aveva creato un giardinetto, ormai in disuso: le bacche si sono appassite sui cespugli, le aiuole sono state invase dalle erbacce e il piccolo gazebo è marcito ed è crollato.<new_line><new_line>Ho notato un seghetto arrugginito senza manico tra le tavole di legno, e c'era anche un piccolo piede di porco in condizioni migliori del coltello.]],
				['4'] = [[Nella stanza principale dell'infermeria, cinque soldati dormono beatamente su delle brande. Sono stati feriti durante l'attacco alla base di Abakan. Il maggiore ha ordinato che il resto dei feriti fosse curato dai civili.]],
				['3'] = [[Sono nel piazzale vuoto tra l'infermeria e il deposito delle armi. La caserma è di lato al deposito. Vicino alla caserma, sei soldati sono seduti su delle casse e si raccontano storie di guerra. Sono tutti armati.<new_line><new_line>Dietro la caserma, vedo l'alloggio degli ufficiali di appena due piani in cui dormono il maggiore e gli altri ufficiali; più avanti, oltre la recinzione metallica, vedo le case dei civili.<new_line><new_line>Da qui, posso camminare lungo la parete dell'infermeria fino alla piazza d'armi principale. Se supero il deposito e giro a sinistra, arriverò alla mensa e alla prigione.]],
				['136'] = [[Nascondersi dietro il generatore non è stata una buona idea. Mi hanno visto subito. Un tenente mi ha richiamato, mi ha passato un secchio e mi ha ordinato di andare a prendere dell'acqua nell'infermeria.]],
				['132'] = [[Solo il maggiore non sa stare fermo', ha continuato il soldato dai capelli rossi. 'Ci ordina un esercizio di allenamento dopo l'altro. E se non ci alleniamo, ci manda ad aiutare i civili con i loro orti, a costruire qualcosa, a scavare o a fare altre cavolate.'<new_line><new_line>'Beh, abbiamo sconfitto gli Abakan, e ora?', ha aggiunto un altro soldato. 'Mi sono stancato! Siamo soldati, non contadini! E cosa dovremmo fare? Starcene qui seduti a non fare niente per un altro anno?']],
				['117'] = [[Ho ignorato le furiose proteste di Sasha e l'ho fatta rimanere dietro l'UAZ. Non era una copertura ottimale, ma correre in un altro punto sarebbe stato ancora più pericoloso. I combattenti si sono fermati a un paio di passi dalla macchina. Tre venivano da sinistra e quattro da destra.<new_line><new_line>'Ci uccideranno ancor prima di riuscire a farne fuori anche solo uno! Sono in troppi!', mi ha bisbigliato Sasha nell'orecchio.<new_line><new_line>Lo vedevo da me che le cose non erano rosee. L'UAZ non ci avrebbe protetto da sette persone diverse.]],
				['32'] = [[Prima di riuscire ad avvicinarmi al cancello, le guardie mi hanno puntato addosso le armi.<new_line><new_line>'Lasciare la città dopo il tramonto è severamente vietato!', mi ha gridato uno dei soldati.]],
				['161'] = [[Dopo aver concluso che il pericolo era passato, il secondino si è messo il fucile in spalla e si è piegato su di me. Questa volta, il piede di porco l'ha colpito perfettamente. Il soldato è caduto come un sacco di patate.<new_line><new_line>Dolorante, mi sono rimesso in piedi a fatica. Spero di non avere niente di rotto. Chissà se il secondino è solo svenuto o se gli ho fratturato il cranio...]],
				['12'] = [[Niente di nuovo', ha detto il tizio con i capelli rossi alzando le spalle. 'L'anno passato è stato una vera noia. Una volta uscivamo per attaccare i banditi, e alcune gang, quelle più grandi o disperate, ci attaccavano. E non se ne sono andati. Sono qui nel terreno.' Ha fatto un sorrisetto. 'Ora ci sono solo pesci piccoli, e solo una volta al mese o giù di lì.']],
				['72'] = [[Sono corso nelle docce e sono andato nello spogliatoio ad aspettare.<new_line><new_line>Il fuoco è divampato più velocemente di quanto mi aspettassi. In meno di dieci minuti, il silenzio fu spezzato dalle sirene. Qualcuno si era accorto dell'incendio.]],
				['62'] = [[La seconda stanza era molto più piccola della prima. Posso solo immaginare il suo scopo. C'era una piccola scrivania, una sedia e uno sgabello nel centro della stanza. Nell'angolo, ho visto un secchio di acqua sporca e una scopa.<new_line><new_line>In un altro angolo, c'era appeso il cappotto dell'uniforme di Boris. Era in perfette condizioni, il che suggeriva che Boris lo avesse indossato al massimo un paio di volte. Non avrà avuto la possibilità di partecipare a eventi che lo richiedessero.<new_line><new_line>C'era una piccola macchia scura sullo sgabello. Sembrava sangue.]],
				['52'] = [[Nessuna delle chiavi ha funzionato. Non sono neppure entrate nella toppa.]],
				['42'] = [[Ci sono tre garage di fila. Due hanno le porte con la serratura integrata, ma il terzo è chiuso solo con un lucchetto.<new_line><new_line>Non riesco a vedere le guardie dietro il Kamaz e loro non possono vedere me.]],
				['92'] = [[Dopo esserci occupati dei soldati, abbiamo continuato il nostro viaggio verso la libertà.<new_line><new_line>Ho notato che Sasha mi stava guardando in modo strano.<new_line><new_line>'Perché mi guardi?'<new_line><new_line>'Sei proprio un santerellino. Non capisco come sei riuscito a sopravvivere così a lungo. Non c'è spazio nelle terre desolate per le buone intenzioni. Se fossi stata in te, li avrei sparati senza spensarci.']],
				['82'] = [[Era buio pesto nell'umida prigione. Non sono riuscito a vedere le sbarre né le celle.<new_line><new_line>Ho cercato la serratura con le mani e ho iniziato a provare le chiavi. Fino a quel momento, non mi aveva neppure sfiorato l'idea che nessuna delle chiavi che avevo trovato al quartier generale potesse non funzionare. Stavo per scoprirlo. La prima chiave non ha funzionato... La seconda chiave non ha funzionato... La terza chiave... si è incastrata. L'ho forzata un po' e con un suono stridulo si è girata. La serratura si è aperta.<new_line><new_line>'Chi c'è?', ha detto Sasha con voce nervosa.]],
				['129'] = [[La guardia ha controllato attentamente la stanza. Quando non ha trovato niente, si è rilassato e ha sbadigliato. Ha guardato l'orologio, poi si è tolto la giacca, ha posato l'arma, ha preso un libro da sotto il materasso e si è seduto al tavolo a leggere.<new_line><new_line>Pare che non tornerò al posto di guardia.]],
				['105'] = [[Muovendoci velocemente lungo la recinzione del perimetro della città, abbiamo raggiunto il generatore senza alcun problema. Come speravo, i soldati di Stepnoy hanno reagito così prontamente all'emergenza che le taniche di gasolio non hanno neppure avuto il tempo di riscaldarsi tanto da infiammarsi. Abbiamo approfittato di un'apertura per correre dal generatore alle docce. Ho spinto dentro Sasha, poi sono entrato e mi sono appoggiato alla porta.<new_line><new_line>Non credo che ci abbia visti qualcuno.]],
				['146'] = [[Io e Sasha corriamo nel giardino chiudendo la porta dietro di noi.<new_line><new_line>L'idea era di aspettare che il tenente prendesse le scorte e se ne andasse, ma invece la porta del giardino si è aperta all'improvviso. Sull'uscio c'era il tenente. Ha visto Sasha e ha alzato il fucile. Uno dei pazienti gli avrà detto che eravamo lì.]],
				['102'] = [[Mia figlia ha preso il fucile del tenente, lo ha osservato e lo ha messo in spalla.<new_line><new_line>'Che aspetti?', mi ha chiesto. 'Non sento più scoppiettare come prima. Il fuoco si spegnerà presto. Possiamo andare?']],
				['143'] = [[Non è entrato nessuno e, dopo qualche minuto, i soldati se ne sono andati. Siamo andati nella cucina. Che ci facevano qui? Un barile d'acqua è scomparso.<new_line><new_line>Non mi ero reso conto che le scorte d'acqua di Stepnoy fossero messe così male. A quel punto ho sperato che il fuoco non raggiungesse le taniche di gasolio...<new_line><new_line>Sasha mi ha distratto da questi pensieri, insistendo perché ci muovessimo.]],
				['127'] = [[Avresti fatto uccidere anche tuo figlio, eh, bastardo?!', Sasha ha urlato a Boris. 'Allora ascolta attentamente! Quando il tuo caro figliolo, il tuo orgoglio e la tua gioia, è stato catturato e portato alla base di Abakan, si è gettato ai piedi dei banditi, col moccio al naso, e li ha implorati di non fargli del male! Non l'hanno torturato, non l'hanno picchiato come tu hai picchiato me! Ti ha venduto! Gli ha detto tutto questo che sapeva: il numero dei soldati a Stepnoy, l'armamento, le fortificazioni difensive, i punti deboli... Si è persino offerto di mostrarglieli! Che ne pensi, eh?'<new_line><new_line>Il sangue colava sul viso di Boris e gli tremavano leggermente le mani.]],
				['93'] = [[Io e Sasha abbiamo fatto il giro l'edificio della mensa, ci siamo precipitati nella cucina e poi siamo passati dalla mensa stessa. Non sembra che i soldati ci abbiano visto.<new_line><new_line>Poi abbiamo sentito un rumore provenire dalla cucina, come se qualcuno stesse spostando qualcosa di pesante. Ho puntato il fucile alla porta. Se fosse entrato qualcuno, gli avrei sparato. Sasha digrignava i denti accanto a me.]],
				['108'] = [[I proiettili hanno rotto i finestrini dell'UAZ e siamo stati colpiti da una pioggia di schegge. Pochi secondi dopo, il fuoco ha smesso.<new_line><new_line>Ho alzato la testa e ho visto i nostri inseguitori dai finestrini rotti. Erano in cinque, tutti armati di AK. Avevano attraversato il ponte e si stavano avvicinando.<new_line><new_line>'Dobbiamo andare via da qui o ci circonderanno e saremo spacciati!', Sasha ha sussurrato.]],
				['53'] = [[Pezzi di legno sono stati tagliati dalla porta. Ho posizionato il piede di porco vicino al lucchetto e ho spinto. Le piccole viti che bloccavano la serratura al legno marcio hanno ceduto, la serratura è caduta e, con uno scricchiolio, la porta si è aperta verso l'interno.]],
				['63'] = [[Le tasche del cappotto erano vuote, ma c'era una chiave nella serratura del cassetto della scrivania. Era in un portachiavi insieme ad altre tre chiavi. Forse una di queste aprirà la porta della prigione.<new_line><new_line>Dentro al cassetto non c'era niente di interessante.]],
				['73'] = [[La fortuna mi ha sorriso: non ho incontrato nessuno mentre correvo per la città. Tutto il personale era impegnato a spegnere il fuoco. Il mio piano stava funzionando.<new_line><new_line>E poi, come una beffa, alle porte del deposito delle provviste, mi sono scontrato con il secondino!<new_line><new_line>Mi ha guardato stupito.]],
				['83'] = [[C'è stata una pausa e poi ho sentito i passi di mia figlia.<new_line><new_line>'Tu?! Senti, se è una specie di scherzo tra te e Boris, puoi andare all'inferno! Voglio dormire. Mi aspetta una giornata pesante domani, pesante e molto breve.']],
				['13'] = [[È caduto di nuovo il silenzio. Era chiaro che i soldati si stessero stancando di me.]],
				['23'] = [[La mensa è chiusa per la notte. Non riuscirò a entrare senza una chiave. Potrei entrare dalla cucina, ma la porta della cucina è proprio vicino al secondino e non riuscirei a usarla senza essere visto.]],
				['33'] = [[Vedendo che mi dirigevo verso la macchina, una delle guardie è corso verso di me e mi ha bloccato la strada.<new_line><new_line>'Mi dispiace, ma stasera l'uso di veicoli è vietato, per ordine speciale del maggiore.']],
				['43'] = [[Una delle chiavi ha funzionato. Sono andato nel garage e ho chiuso la porta senza fare rumore.<new_line><new_line>Era buio pesto e c'era odore di benzina. Ho cercato l'interruttore della luce e l'ho premuto, ma le luci non si sono accese. O non c'era corrente o non c'erano le lampadine o si erano fulminate. Qualcuno aveva gentilmente lasciato una pesante torcia sotto l'interruttore. Funzionava, ma la batteria era quasi scarica e la luce che emanava era molto fioca.]],
				['139'] = [[Il volto di Sasha si è raggelato. Riuscivo a vedere la lotta interna nei suoi occhi. Poi le ho semplicemente preso il fucile dalle mani.<new_line><new_line>Mi ha guardato arrabbiata e ha alzato le spalle.]],
				['116'] = [[Corri alla macchina!', ho risposto.<new_line><new_line>Muovendoci a zig zag tra le moto, siamo arrivati alla macchina. Gli inseguitori hanno aperto il fuoco, tutti contemporaneamente. Ci siamo dovuti sdraiare sull'asfalto.<new_line><new_line>I nostri avversari sapevano dove eravamo diretti e ci stavano intralciando di proposito. Provare a correre più lontano sarebbe stato un suicidio.<new_line><new_line>'Non è stata una delle tue idee migliori', ha sottolineato Sasha con tono acido.]],
				['113'] = [[Ci nasconderemo dietro i garage e gli spareremo addosso non appena si avvicinano.'<new_line><new_line>'Non se ne parla!', Sasha mi ha guardato come se fossi un coglione. 'Ci falcerebbero mentre corriamo!'<new_line><new_line>'Tu corri da sinistra, io corro da destra! Tre... due... uno... via!'<new_line><new_line>Il mio piano ha funzionato! I nostri avversari hanno sparato solo un paio di colpi e nessuno ha centrato il bersaglio. Abbiamo raggiunto i garage senza un graffio.]],
				['137'] = [[Il secondino è caduto all'indietro, e in preda a una sorta di frenesia gli sono salito addosso e ho iniziato a strangolarlo.<new_line><new_line>In due minuti era tutto finito. Mi sono rimesso in piedi e, barcollando, mi sono appoggiato al muro. Non me la sarei cavata con dei lividi soltanto...]],
				['10'] = [[I soldati si sono spostati, facendomi spazio sulla seconda cassa. Mi sono seduto. C'era un silenzio imbarazzante.<new_line><new_line>I soldati mi guardavano con un'aria d'attesa.]],
				['68'] = [[Era come se la sigaretta non avesse neppure toccato il pavimento che le fiamme divamparono e io sono stato colpito da un'ondata di calore insopportabile.<new_line><new_line>Devo trovare un posto in cui nascondermi fino a che il fuoco non si espande in tutto l'edificio e qualcuno dia l'allarme. Inizialmente, ho pensato di farlo io stesso, ma ho cambiato subito idea: andarmene in giro ad annunciare un incendio sarebbe stato troppo sospetto.<new_line><new_line>Il fuoco distrarrà gli abitanti di Stepnoy, ma il mio compito sarà estremamente difficile. In ogni caso, è fatta. Non posso più tornare indietro.]],
				['30'] = [[No, non ne ho più!', ha detto il secondino, annuendo ai soldati. 'Stiamo fumando le ultime, vedi?'<new_line><new_line>'Stai mentendo! Ne avevi tantissime!', ha esclamato arrabbiato uno dei soldati.<new_line><new_line>'Vero, ma gli ufficiali le hanno comprate tutte. Mi hanno lasciato a secco', ha detto il secondino aprendo le braccia.]],
				['20'] = [[Dopo aver sentito la mia domanda, i soldati sono scoppiati a ridere.<new_line><new_line>'Sei serio?', ha chiesto il soldato calmo, dopo essersi calmato. 'Che hai in testa? Neppure gli ufficiali possono entrare lì dentro. Solo l'ufficiale delle armi più consegnare le armi, e solo con il permesso del maggiore e la firma. Tutta l'attrezzatura è attentamente controllata. Scordatelo.']],
				['50'] = [[Il canale è molto profondo, oltre quattro metri, ma è pieno d'acqua solo per metà. Il canale è alimentato dal fiume Tom ed è la fonte di acqua fresca di Stepnoy.<new_line><new_line>Le sponde del canale sono quasi a strapiombo. Se mi tuffo, non riuscirò a risalire senza un'attrezzatura speciale, e neanche un veicolo riuscirebbe ad attraversarlo.<new_line><new_line>L'unico ponte che attraversa il canale è un ponte pedonale che va dalla piazza d'armi al piazzale di lato ai garage e al parcheggio.]],
				['158'] = [[Boris è diventato rosso e ha chiuso i pugni.<new_line><new_line>'Avrei fatto uccidere Vasily se avessi saputo che era un traditore!'<new_line><new_line>Stavo per rispondere, ma poi Sasha è comparsa dietro di me, rossa in faccia e arruffata.]],
				['70'] = [[Il fuoco è divampato più velocemente di quanto mi aspettassi. Sembrava che fossero passati solo pochi minuti da quando le prime fiamme erano apparse sul tetto. E in quel momento le sirene mi hanno quasi rotto i timpani. Qualcuno si era accorto dell'incendio.<new_line><new_line>La gente si è ammassata attorno all'edificio in fiamme, soprattutto soldati mezzi vestiti. Gli ufficiali andavano di qua e di là dando ordini su come spegnere l'incendio. Non c'erano abbastanza secchi e prendere l'acqua direttamente dal canale sarebbe stato molto difficile. Qualcuno ha avuto la brillante idea di riempire i secchi dal rubinetto dell'infermeria.]],
				['60'] = [[I cassetti superiori della scrivania del maggiore contenevano tanti vecchi documenti, un set intero di penne a sfera consumate e altri oggetti da ufficio. Nell'ultimo cassetto ho trovato altre tre bottiglie di vino, un paio di bicchieri, una foto di un giovane ufficiale che somigliava un po' a Boris, un posacenere e un pacchetto di sigarette.<new_line><new_line>C'era solo una sigaretta nel pacchetto.]],
				['90'] = [[Ci affrettiamo lungo il canale verso la mensa. Ci siamo fermati alla porta per riprendere fiato. Poi ci siamo avviati verso il deposito delle armi; Sasha mi ha afferrato il braccio e mi ha indicato qualcosa davanti.<new_line><new_line>Due soldati stavano venendo verso di noi dalla caserma. Correvano e ci avrebbero visti da un momento all'altro. Non potevamo nasconderci nella mensa: la porta era chiusa a chiave.<new_line><new_line>Ho visto che entrambi i soldati erano disarmati e li conoscevo entrambi. Uno aveva i capelli rossi e l'altro era calvo.]],
				['150'] = [[Ugh, cos'è questa puzza?', ha detto Sasha.<new_line><new_line>'Se puzza così tanto, puoi uscire e prendere una boccata d'aria fresca con i soldati', ho risposto.<new_line><new_line>Con una smorfia, Sasha dice: 'Ah ah. Ma non resisterò a lungo qui'.]],
				['18'] = [[Anche io voglio tante cose', ha detto quello calvo socchiudendo gli occhi. 'Dov'è che devi andare?']],
				['125'] = [[Voglio chiederti... perché?'<new_line><new_line>'Perché cosa?'<new_line><new_line>'Perché l'hai fatto? Hai dato fuoco all'unità di comando, ucciso i miei uomini... per cosa?'<new_line><new_line>Mi ha sorpreso che non avesse odio o critica sul suo volto, solo dolore. E la cosa mi ha fatto arrabbiare.]],
				['38'] = [[Mentre ispezionavo, ho trovato un piccolo armadio. Inizialmente non l'ho visto per via delle ragnatele. Conteneva legna da ardere, pezzi di recinzione e sedie senza sedile.<new_line><new_line>Oltre al secchio di trucioli di legno, c'era un vecchio fucile Mosin-Nagant con il calcio spezzato a metà. L'ho preso. Certo, sparare con questo sarebbe stato molto strano, ma efficace in ogni caso.<new_line><new_line>Ho trovato delle cartucce nel cassetto del tavolo.]],
				['28'] = [[Il secondino ha sospirato per il sollievo e ha preso un accendino dalla tasca.<new_line><new_line>'Certo. Ora, facci un favore e lasciaci parlare in pace. Abbiamo una piccola questione da risolvere.'<new_line><new_line>Mi ha fatto accendere, ha posato l'accendino e mi ha fatto capire che stava aspettando che me ne andassi.]],
				['58'] = [[Sono sul retro dell'unità di comando. La finestra è stata rotta con un piede di porco. Le schegge sono cosparse a terra.<new_line><new_line>Se entro, mi taglierò quasi sicuramente col vetro.]],
				['149'] = [[Mi sono avvicinato a Sasha e l'ho guardata con aria minacciosa:<new_line><new_line>'Se avessi iniziato uno scontro a fuoco qui, avremmo avuto tutta Stepnoy addosso. Siamo vicino alla piazza d'armi, quindi smettila di urlare.'<new_line><new_line>Sasha ha borbottato qualcosa. Non l'avevo convinta.]],
				['126'] = [[Lo capisco', ha risposto Boris con più sicurezza. 'Non lo giustifico, ma lo capisco.'<new_line><new_line>E poi ho iniziato a ridergli in faccia. Ho scaricato tutta la tensione nervosa che avevo accumulato. La mia risata era amara e spaventosa, così spaventosa che Boris ha sussultato.<new_line><new_line>'Non lo giustifichi, ma lo capisci, eh?!', ho detto alla fine. 'Lo capisci?! Col cavolo! Secondo la tua morale, sarei dovuto rimanere lì a guardarti uccidere mia figlia?!'<new_line><new_line>Ho preso il fucile e l'ho puntato contro Boris.]],
				['154'] = [[Boris si è grattato la testa brizzolata, poi ha annuito e si è incamminato verso il veicolo. L'ho seguito, continuando a puntargli contro l'arma. Lo squadrone è rimasto vicino al Kamaz, con gli occhi pieni di odio puntati sulla mia schiena.<new_line><new_line>Il maggiore si è seduto goffamente sul sedile posteriore. Sasha si è girata e gli ha puntato il fucile in testa.<new_line><new_line>'Dove andiamo?', ha chiesto Boris con una voce sorprendentemente calma.]],
				['162'] = [[Andiamo per ordine: prima mi serve un piano per prendere delle armi. Tutto quello che possiedo è in macchina, ma non posso prendere una pistola da lì: agli esterni è vietato introdurre armi nella città. Ci sono delle sentinelle di pattuglia nel parcheggio e al cancello. Mi arresterebbero sul posto.<new_line><new_line>Il secondo problema è che, seppure armati, io e Sasha non possiamo affrontare tutta Stepnoy da soli. E non possiamo neppure sgattaiolare fuori: per ordine del maggiore, un piccolo contingente di soldati fa il turno di notte per assicurare che il presidio sia pronto a respingere un attacco dei banditi in ogni momento del giorno o della notte.]],
				['147'] = [[Aprendo attentamente la porta esterna, ho visto delle persone aggirarsi attorno all'edificio in fiamme, soprattutto soldati mezzi vestiti. Gli ufficiali andavano di qua e di là dando ordini su come spegnere l'incendio. Non c'erano abbastanza secchi e prendere l'acqua direttamente dal canale sarebbe stato molto difficile. Qualcuno ha avuto la brillante idea di riempire i secchi nel bagno dell'infermeria.<new_line><new_line>Nascondersi lì è stata una buona idea. Non appena l'area sarà libera, farò una corsa fino alla prigione. Spero di non dover aspettare molto: la puzza nauseabonda mi sta uccidendo.]],
				['21'] = [[Se posso chiedere... Cosa ti serve qui?', ha chiesto quello calvo. 'I civili non possono entrare nella caserma, a meno che il maggiore non dia il permesso. Ed è già mezzanotte passata. La gente normale è a letto. Gli unici in piedi sono i soldati di guardia e i tipi sospetti. Sei uno di quelli?']],
				['31'] = [[Sono nel parcheggio. Non ci sono molti veicoli, forse tre o quattro UAZ militari e circa otto moto. Un Kamaz è parcheggiato al centro, e a sud, vicino alla recinzione della città, c'è la mia macchina.<new_line><new_line>Le guardie sono di pattuglia di fronte al cancello chiuso di Stepnoy. Il cancello è molto più fragile di quello della base di Abakan. Credo di riuscire a sfondarlo con una macchina.<new_line><new_line>I lati ovest e nord del parcheggio sono separati dal canale. La luce fuoriesce dalla finestra del posto di guardia più a est. Verso ovest, oltre il canale, vedo la mensa e il deposito delle provviste, la cui cantina funge da prigione. A nord del parcheggio ci sono tre garage, tutti chiusi. Dai garage, posso attraversare il ponte fino alla piazza d'armi principale.]],
				['69'] = [[Il maggiore ha detto niente eccezioni. Spostati.'<new_line><new_line>'Ascolta, non voglio metterla in moto. Devo solo prendere delle cose. Non armi, tranquillo.'<new_line><new_line>'Qualsiasi uso è vietato.'<new_line><new_line>Non riuscivo a credere alle mie orecchie. Da dove veniva questo divieto? E perché proprio stanotte? Il fucile d'assalto della guardia è stato abbastanza convincente, però.]],
				['11'] = [[Scusa, amico, consideraci dei non fumatori. Le sigarette scarseggiano e questo significa che scompaiono come se niente fosse', ha detto il soldato con i capelli rossi.<new_line><new_line>'Vai nella prigione', ha suggerito uno calvo. 'Alex, il secondino, ha sempre un accendino e tasche piene di sigarette. Nessuno sa dove le prenda. Ma il bastardo ti spennerebbe per una sigaretta, ricordalo.']],
				['61'] = [[Non c'era niente di valore nelle tasche del cappotto. In una tasca ho trovato un grigio pezzo di stoffa stropicciato che Boris probabilmente usava come fazzoletto. Nell'altra tasca, ho trovato un paio di rubli. I rubli hanno perso valore da tanti anni.]],
				['71'] = [[Sono corso nell'infermeria e sono andato nel bagno ad aspettare, osservando quello che succedeva dalla finestrella.<new_line><new_line>Il fuoco è divampato più velocemente di quanto mi aspettassi. In meno di dieci minuti, il silenzio fu spezzato dalle sirene. Qualcuno si era accorto dell'incendio.<new_line><new_line>Ho osservato la gente ammassarsi attorno all'edificio in fiamme, soprattutto soldati mezzi vestiti. Gli ufficiali andavano di qua e di là dando ordini su come spegnere l'incendio. Non c'erano abbastanza secchi e prendere l'acqua direttamente dal canale sarebbe stato molto difficile.]],
				['41'] = [[Mi sono avvicinato al posto di guardia e ho sbirciato dalla finestra. La guardia stava ancora leggendo il libro e non aveva chiaramente nessuna intenzione di smettere.<new_line><new_line>Ho guardato le altre guardie. Stavano passeggiando fiaccamente di fronte al cancello, raccontandosi pettegolezzi. Non erano all'erta: non si aspettavano incidenti per quella sera.]],
				['51'] = [[Quella delle docce era una costruzione con telaio in legno, annerita dal brutto tempo e dalla muffa. Il lato destro dell'edificio era cadente e quello sinistro era crollato. Il problema scaturiva probabilmente dalle fondamenta.<new_line><new_line>La porta graffiata e spezzata era chiusa a chiave.]],
				['135'] = [[Un altro soldato, con i capelli rossi, si è unito alla conversazione.<new_line><new_line>'O stai andando nell'alloggio degli ufficiali? Neanche lì puoi andare. Ed è meglio così. Conosci il tenente Yakushko? L'anno scorso, ha sparato a una donna delle pulizie nel sonno. Vuoi fare la stessa fine?'<new_line><new_line>Un terzo soldato, calvo e con il pizzetto, mi ha chiamato:<new_line><new_line>'Vieni a fare due chiacchiere con noi. Conosci qualche bella storia?']],
				['19'] = [[Il maggiore è nell'alloggio degli ufficiali. Dove altro dovrebbe essere? Ma tu lì non puoi entrare. Odia quando le persone entrano per parlargli, soprattutto quando dorme, e ancora di più se si tratta di questioni stupide.' L'uomo calvo si è grattato la barba. 'Siamo sotto attacco? Il canale ha straripato? No. Allora può aspettare fino a domani. Troverai Boris nell'unità di comando domattina presto.']],
				['160'] = [[Sono corso da Sasha. Era ancora in piedi, piegata in due, e si premeva una mano sulla pancia.<new_line><new_line>'Tutto... più tardi... Andiamo... presto...', ha sussurrato debolmente. Ho seguito il suo sguardo. Dei puntini neri erano apparsi sulla strada che veniva dalla città militare. Non ho perso tempo: ho appoggiato Sasha sul sedile posteriore e ho messo in moto.<new_line><new_line>Mi sono girato l'ultima volta...<new_line>]],
				['91'] = [[I soldati si sono fermati. Stavano per fare una domanda, ma poi hanno visto Sasha. Si sono scambiati uno sguardo senza dire niente.<new_line><new_line>'L'hai fatta evadere?', ha chiesto quello coi capelli rossi senza distogliere lo sguardo dal fucile.]],
				['49'] = [[Il generatore a gasolio è dentro una nuova recinzione metallica color cachi. Non ho modo di entrare.<new_line><new_line>Su delle piattaforme tra il generatore e l'unità di comando ci sono due grandi taniche di metallo piene di gasolio. Una delle taniche è collegata al generatore con un tubo.]],
				['59'] = [[Sono nell'unità di comando, nell'ufficio in cui ho parlato con il maggiore in tre occasioni. Ci sono una bottiglia di vino vuota e due bicchieri sulla scrivania di Boris. Ho notato il cappotto del maggiore appeso mentre entravo.<new_line><new_line>C'è un ritratto di Brezhnev sulla parete e, di fianco, una grande mappa dell'Unione Sovietica. Nell'angolo, c'è un vecchio radiotelegramma.<new_line><new_line>Sulla parete opposta, c'è la porta di un'altra stanza.]],
				['159'] = [[Sono stata io', Sasha ha continuato freneticamente. 'Ho ucciso io tuo figlio! L'ho squartato come un maiale! Con queste mani! Cos'hai da dire adesso? Che non era giusto?! Che è stata un'ingiustizia?! Tuo figlio era un traditore e io ho avuto la mia vendetta! L'hai detto tu stesso: avresti fatto uccidere il tuo stesso figlio! L'avresti ucciso con le tue mani! Allora perché mi guardi con tutto quest'odio? Dovresti baciarmi!']],
				['39'] = [[Sono nel posto di guardia. È freddo e scomodo e gli angoli sono pieni di ragnatele. Tre materassi con dei cuscini sono appoggiati alle pareti. A parte gli sgabelli rudimentali e il tavolo traballante, non c'era altro nella stanza. Solo una delle quattro lampadine funzionava.<new_line><new_line>Nell'angolo più lontano, vedo lo sportello dell'armadio in cui ho trovato il fucile.]],
				['164'] = [[Ma Sasha è stata più veloce.<new_line><new_line>La velocità con cui ha afferrato il fucile e ha sparato al tenente nel ventre sembrava sovrannaturale. Se non l'avessi visto con i miei occhi, non ci avrei creduto. Il tenente ha mollato il fucile e indietreggiando si è accasciato lentamente a terra.<new_line><new_line>Sasha si è girata verso di me e mi ha rivolto un gesto di invito. Non so cosa volesse dire. Poi Sasha ha fatto un cenno con la mano, si è avvicinata al soldato ferito e lo ha sparato sul volto sfigurato.]],
				['133'] = [[Anche io ero nella squadra di Vasily', ha spiegato il soldato con i capelli rossi. 'L'abbiamo visto tutti con i nostri occhi. Vasily le ha detto dieci volte di non andare avanti! Ed è caduta in un'imboscata.' Dopo una pausa, ha ripreso: 'E che differenza fa, in ogni caso?! Ora è una bandita e quando li incontreremo, non avremo molto da dirci.'<new_line><new_line>'È vero', gli altri hanno detto.]],
				['157'] = [[Fino a dove ti saresti spinto per tuo figlio, Boris? Per Vasily? Faresti di tutto per lui, ma se fosse un traditore? Ricordi che hai detto che avresti ucciso il tuo stesso figlio? Ho solo una risposta da darti: cazzate!'<new_line><new_line>Boris non ha risposto subito.<new_line><new_line>'Ti sbagli. Penso che l'avrei ucciso. Ma... non sarei riuscito a vivere dopo. Hai fatto una scelta diversa e io... capisco perché l'hai fatto.'<new_line><new_line>Ho sputato, disgustato.]],
				['99'] = [[Impugnando il piede di porco, sono corso verso il tenente, che immediatamente si è lanciato verso di me e ha sparato. Il proiettile mi ha attraversato la spalla.<new_line><new_line>Con velocità sovraumana, Sasha ha fatto un affondo in avanti e ha spinto la spalla del tenente, che ha perso l'equilibrio e si è aggrappato al telaio della porta. Poi ha barcollato e ha dato un pugno a Sasha, che è caduta di lato.<new_line><new_line>Analizzando velocemente la situazione, il tenente ha alzato di nuovo la sua arma.]],
				['121'] = [[I soldati hanno alzato i fucili, ma Boris ha sollevato una mano come avvertimento. Poi ha buttato il suo fucile a terra e si è girato verso di me. Nel suo sguardo, non ho percepito neppure un pizzico di paura, solo fredda concentrazione. Quanto vorrei avere il suo stesso autocontrollo...<new_line><new_line>'Cosa sperate di ottenere così?', ha chiesto con la voce stanca. 'Ci sono undici persone qui, tutte armate. Non ne uscirete vivi.']],
				['134'] = [[Sì', ha risposto un altro soldato. 'Suo figlio, Vasily, è stato ucciso solo un paio di anni fa, ma gli Abakan ci hanno spedito la sua testa. Boris per poco non moriva di dolore. Pensavamo che si sarebbe attaccato alla bottiglia, ma invece ha mantenuto il controllo. È un tipo tosto. È un peccato che non abbiamo mai scoperto quale di quei bastardi ha ucciso Vasily. Lo sventrerei con le mie mani.'<new_line><new_line>Gli altri hanno annuito.]],
				['86'] = [[Schernendomi Sasha ha detto: 'Beh, ha funzionato?'<new_line><new_line>'E ora ti sto salvando dal plotone di esecuzione mettendo a rischio la mia stessa vita! Potresti almeno fingere un po' di gratitudine?'<new_line><new_line>Sasha non gradiva più la conversazione.<new_line><new_line>'Mi hai dato una pistola, cosa dovrei fare?', ha chiesto rudemente.]],
				['76'] = [[All'ultimo secondo, il secondino è riuscito a spostare la testa e l'ho colpito invece sulla spalla sinistra. Non ha barcollato, si è spostato e mi ha affondato un pugno sullo stomaco e poi mi ha colpito due volte con il calcio del fucile.<new_line><new_line>Sono caduto e sono rimasto fermo sul pavimento, ma ho stretto ancora di più il piede di porco.]],
				['66'] = [[I cassetti superiori della scrivania del maggiore contenevano tanti vecchi documenti, un set intero di penne a sfera consumate e altri oggetti da ufficio. Nell'ultimo cassetto, ho trovato altre tre bottiglie di vino vuote, un paio di bicchieri, una foto di un giovane ufficiale che somigliava un po' a Boris, un posacenere e un pacchetto di sigarette.<new_line><new_line>C'era solo una sigaretta nel pacchetto.]],
				['56'] = [[Di fronte a me c'è un edificio in legno a un piano dipinto di rosso scuro. Due bandiere dell'URSS sventolano sopra la porta imponente. Ci sono delle grandi finestre da entrambi i lati della porta.<new_line><new_line>Non posso entrare; ci sono due lucchetti sulla porta. Potrei spaccare un vetro ed entrare dalla finestra, ma chiunque si trovi sulla piazza d'armi noterebbe il vetro rotto e darebbe l'allarme.]],
				['46'] = [[Versare benzina da una tanica all'altra quasi nel buio totale, cercando di non fare rumore e di non versarne di fuori neanche una goccia è un'impresa quasi impossibile. Mi è sembrato di impiegarci una vita.<new_line><new_line>Quando ho finito, puzzavo di benzina, le mani e il viso erano tutti sporchi e, dopo tutta quella fatica, sono riuscito a racimolare solo cinque litri. Meglio di niente.]],
				['145'] = [[Anziché rispondere, l'ho tirata nel bagno dell'infermeria. I pazienti allettati hanno visto Sasha e stavano per urlare, ma io li ho minacciati con il fucile.<new_line><new_line>'Pensavo che avessi tutto sotto controllo!', ha detto Sasha freddamente. 'Nasconderci qui finché non ci trovano? È questo il tuo grande piano?'<new_line><new_line>'Stai zitta!', le ho detto.<new_line><new_line>Non possiamo attraversare la piazza d'armi. Cosa possiamo fare?]],
				['26'] = [[Sono entrato nel deposito con l'intento di andare direttamente nella prigione, quando ho sentito un urlo alle mie spalle. Il secondino affannato si è precipitato nel deposito:<new_line><new_line>'Dove stai andando?! Vuoi farmi uccidere?! Il maggiore ha vietato l'ingresso a tutti! Se scopre che qualcuno è entrato, domani verrò giustiziato insieme a tua figlia. Dai, esci!'<new_line><new_line>Per dimostrarmi che facesse sul serio, il secondino ha agitato l'arma, poi mi ha preso dalla spalla e mi ha accompagnato fuori dal deposito.]],
				['16'] = [[Il volto dei soldati si è corrucciato.<new_line><new_line>'Cosa c'è da pensare?!', ha detto il soldato con i capelli rossi con tono di sfida. 'La stronza ci ha traditi. E per cosa? Stava male qui con noi? Ha avuto un fucile, è stata assegnata alla squadra migliore, ha avuto tutto quello che voleva. Nessuno ci ha provato con lei. Il maggiore ha giurato che chiunque l'avesse importunata sarebbe finito col cappio al collo. Quando l'hanno presa prigioniera, è stato solo per colpa sua!']],
				['98'] = [[Sasha si è bloccata e ha guardato l'avversario con intensità. Poi ho visto il fucile con la coda dell'occhio. Il tenente si è lanciato verso di me e ha sparato. Il proiettile mi ha attraversato la spalla.<new_line><new_line>Con velocità sovraumana, Sasha ha fatto un affondo in avanti e ha spinto la spalla del tenente, che ha perso l'equilibrio e si è aggrappato al telaio della porta. Poi ha barcollato e ha dato un pugno a Sasha, che è caduta di lato.<new_line><new_line>Analizzando velocemente la situazione, il tenente ha alzato di nuovo la sua arma.]],
				['97'] = [[La velocità con cui Sasha ha afferrato il fucile e ha sparato al tenente nel ventre è stata sovrannaturale. Se non l'avessi visto con i miei occhi, non ci avrei creduto. Il tenente ha mollato il fucile e indietreggiando si è accasciato lentamente a terra.<new_line><new_line>Sasha si è girata verso di me e mi ha rivolto un gesto di invito. Non so cosa volesse dire. Poi Sasha ha fatto un cenno con la mano, si è avvicinata al soldato ferito e lo ha sparato sul volto sfigurato.]],
				['96'] = [[Il rumore della porta dell'infermeria che si apriva mi ha distolto dai miei pensieri agitati. Era entrato qualcuno.<new_line><new_line>Ho spiato un tenente dalla porta socchiusa. Aveva un aspetto terribile: probabilmente i suoi vestiti avevano preso fuoco e si era rotolato a terra per spegnerlo; la faccia e le braccia erano orribilmente ustionate. Era chiaro che il tenente stesse andando nella sala infermieri in cui erano conservate le scorte mediche.]],
				['95'] = [[Non appena abbiamo raggiunto le porte dell'infermeria, mi sono reso conto di quanto sarebbe stato difficile raggiungere il parcheggio senza essere visti. C'erano circa cento persone nella piazza d'armi, una catena umana che si passava secchi pieni d'acqua presa dal canale.<new_line><new_line>'E ora?', ha chiesto Sasha sgarbatamente.]],
				['94'] = [[Non è stata difesa personale, è stato omicidio. Premere il grilletto non è mai stato così difficile come questa volta su due soldati innocenti e disarmati. Quando quello calvo è caduto a terra per un proiettile al petto, il suo compagno dai capelli rossi ha sussurrato:<new_line><new_line>'Amico, perché...']],
				['9'] = [[Dopo essere passato davanti ai soldati seduti sulle casse vicino alla caserma, mi hanno chiamato e mi sono fermato.<new_line><new_line>'Dove credi di andare? I civili non possono stare nella caserma, salvo permesso del maggiore. A meno che tu non sia una bella ragazza... in quel caso ti serve il permesso di uno di noi.'<new_line><new_line>I soldati ridono alla loro stessa battuta.]],
				['89'] = [[Quando ha capito dove eravamo diretti, Sasha si è fermata bruscamente:<new_line><new_line>'Questo è il tuo piano?! Sei impazzito?! Come faremo ad attraversarlo? È profondo quattro metri e le sponde sono troppo ripide per arrampicarsi. Costruiremo un ponte? O forse hai dell'attrezzatura da scalata nascosta nella manica?']],
				['88'] = [[Io e Sasha siamo fuori dal deposito delle provviste. Oltre la piazza d'armi, l'unità di comando è coperta dalle fiamme.<new_line><new_line>Mentre osservo le il fuoco divampare, inizio a pensare. Se tutta la città viene rasa al suolo dalle fiamme, sarà colpa mia. Alcuni abitanti potrebbero morire per le ferite. Ma in ogni caso... questa era l'unica soluzione. Non potevo permettergli di uccidere mia figlia, per niente al mondo.<new_line><new_line>A est, oltre il canale, la mia macchina brilla al chiaro di luna. Non abbiamo molto tempo. Devo decidere quale sarà la nostra prossima mossa.]],
				['87'] = [[Come dici tu', ha risposto Sasha impassibile.<new_line><new_line>Dopo aver verificato che non ci fosse nessuno di fronte al deposito delle provviste, ho fatto uscire Sasha. Quando ha visto l'incendio in lontananza, ha fatto un mezzo sorriso. Non sono riuscito a decifrare il significato di quei sorrisi.<new_line><new_line>'Aspetta', ha detto improvvisamente. È tornata dentro ed è uscita di nuovo pochi secondi dopo impugnando il fucile del secondino. 'Questo ci darà un vantaggio.']],
				['81'] = [[Ho nascosto il corpo nell'angolo del deposito delle provviste e sono uscito di nuovo fuori per controllare che non ci fosse nessuno nei paraggi. L'area sembrava deserta. In lontananza, dopo la piazza d'armi, un grande fuoco ardeva nel punto in cui c'era l'unità di comando.<new_line><new_line>Devo spostarmi velocemente prima che il fuoco inizi a spegnersi da solo.]],
				['80'] = [[Sorprendentemente, le tasche del secondino erano completamente vuote. Non sono riuscito a trovare neppure l'accendino che ha usato per farmi accedere la sigaretta l'ultima volta che ci siamo incontrati. Era stato derubato dalle persone con cui stava parlando?<new_line><new_line>Il lato positivo è stato che ho preso il fucile con le cartucce piene.]],
				['106'] = [[Il ponte era dietro di noi. Noi eravamo nel parcheggio. Come avevo sperato, le guardie erano impegnate a spegnere l'incendio e non c'era nessuno di guardia al cancello. Non ci restava che prendere una macchina, metterla in moto, sfondare il cancello e...<new_line><new_line>'Abbassati!', ha gridato Sasha disperata.<new_line><new_line>Mentre ci spostavamo per tuffarci dietro l'UAZ più vicina, qualcuno ha iniziato a spararci addosso con una mitragliatrice. Proveniva dal ponte.]],
				['8'] = [[Non ho avuto neppure la possibilità di provare la prima chiave, che già due soldati mi hanno visto e si sono diretti verso di me.<new_line><new_line>Uno dei due si è posizionato tra me e la porta.<new_line><new_line>'Ok, vecchio, allontanati subito dalla porta. Non vuoi problemi, vero?'<new_line><new_line>Hanno impugnato meglio i fucili scintillanti.]],
				['79'] = [[Il secondino era forte e aveva esperienza negli scontri corpo a corpo. Il mio primo colpo ha trovato la sua mascella, ma non ha barcollato, si è spostato e mi ha dato una ginocchiata sullo stomaco. Mi sono piegato in due e mi ha colpito due volte col calcio del fucile.<new_line><new_line>Sono caduto e il secondino si è scaraventato su di me. Suppongo di essere stato fortunato che non mi abbia sparato. Abbiamo iniziato ad azzuffarci a terra. Lui gridava e mi colpiva in faccia con tutto quello che trovava. Ho preso una roccia e l'ho colpito alle tempie.]],
				['142'] = [[E grazie al cielo, ma ascolta: ci hanno visti e tu li hai lasciati vivere. Presto la gente si accorgerà che sono scomparsi.'<new_line><new_line>'Occupiamoci di un problema alla volta', ho detto.]],
				['78'] = [[Ho pensato rapidamente, poi ho preso il fucile rubato nel posto di guardia e ho sparato al secondino. Il proiettile deve averlo colpito al cuore, perché è morto quasi all'istante. L'espressione impressa per sempre sul suo viso non era di paura, ma di pura confusione.<new_line><new_line>Ho solo sperato che nessuno avesse sentito lo sparo.]],
				['103'] = [[Sasha era pronta per tornare nell'infermeria, ma l'ho fermata.<new_line><new_line>'Non così in fretta.'<new_line><new_line>'Eh? Non così in fretta? Sei sordo? Non sento più scoppiettare come prima. Il fuoco si spegnerà presto. Non possiamo aspettare. Dobbiamo uscire finché possiamo.'<new_line><new_line>'Non possiamo correre direttamente al ponte. La piazza d'armi è gremita di persone. Hai intenzione di farti strada sparando?'<new_line><new_line>'Hai un'idea migliore?']],
				['153'] = [[Non ho paura di morire, ma dove vuoi arrivare? Perché non la risolviamo pacificamente? Arrenditi, subito. E prometto di risparmiarti la vita.'<new_line><new_line>'Dopo tutto quello che ho fatto? Non ti credo.'<new_line><new_line>'Hai il diritto di essere scettico, ma non hai altra via d'uscita.']],
				['44'] = [[Sono nel garage buio.<new_line><new_line>Con la luce fioca della torcia, ho visto delle taniche. Tante, più di trenta taniche, a occhio e croce. Questo posto è pieno di benzina! Oltre alle taniche, nel garage ci sono solo stracci sporchi.<new_line><new_line>Penso di aver sentito qualcuno fuori gridare. È meglio se esco prima che mi vedano.]],
				['7'] = [[Mi sono avvicinato alla porta del deposito delle armi. L'edificio di mattoni rossi aveva una porta d'acciaio chiusa con un grande lucchetto per proteggerla da visitatori indesiderati.<new_line><new_line>Ho notato che i soldati vicino alla caserma avevano smesso di parlare e mi stavano lanciando delle occhiate sospettose.]],
				['40'] = [[Prima di riuscire ad andare via dal posto di guardia, la porta si è aperta da sola per via del vento e ha sbattuto contro la parete. Le sentinelle avranno probabilmente sentito il rumore. Sono corso dietro l'angolo per osservare.<new_line><new_line>Uno dei soldati si è avvicinato all'edificio. Ha rallentato quando ha visto la porta aperta; poi è entrato nel posto di guardia e ha chiuso la porta dietro di lui.]],
				['77'] = [[È stato un colpo perfetto. Il secondino è caduto come un sacco di patate.<new_line><new_line>Spero di averlo solo messo K.O. Avrei potuto fratturargli il cranio.]],
				['144'] = [[Non l'ho lasciato finire. È caduto a terra, sanguinante, mentre io sono rimasto in piedi, come se fossi paralizzato. Sasha mi ha toccato la spalla:<new_line><new_line>'Perché ti sei fermato? Stai pregando per le loro anime?'<new_line><new_line>'Dobbiamo spostare i corpi', ho risposto con voce stridula. 'Anche se... anche se nessuno ha sentito gli spari, prima o poi qualcuno si accorgerà dei corpi.']],
				['131'] = [[Ho attraversato la stanza principale e sono entrato in un bagno angusto. Da qui, potevo solo andare nella stanza sul retro o uscire nel cortile dietro l'infermeria.<new_line><new_line>Nella stanza dietro l'infermeria, che fungeva sia da sala infermieri che da sala operatoria, c'erano bende, scorte mediche, strumenti per le amputazioni, vecchia strumentazione e altri dispositivi medici. Ora è chiusa a chiave e il capo infermiere ha l'unica chiave.]],
				['148'] = [[Pensi di insegnarmi qualche morale?', ha esclamato Sasha. 'Va' all'inferno! La vita mi ha già insegnato tutto quello che c'è da sapere!'<new_line><new_line>'Non ci avrebbe fatto niente!'<new_line><new_line>'Ah, davvero?!', Sasha ha sollevato la maglietta dell'uomo morto e ho visto l'impugnatura della pistola. 'Stai dicendo che quel serpente non ti avrebbe colpito alle spalle se ne avesse avuto la possibilità?!']],
				['112'] = [[Ci nasconderemo dietro il Kamaz e agiremo da lì!', le ho risposto.<new_line><new_line>Sasha ha guardato dietro il camion dietro di noi e si è leccata le labbra secche:<new_line><new_line>'Sì, buona idea!'<new_line><new_line>Abbiamo strisciato fino all'abitacolo del Kamaz. Sparare da lì sarebbe stato più sicuro che da tutte le altre parti.]],
				['156'] = [[Boris è sceso, ma non se n'è andato, anzi si è girato verso di me:<new_line><new_line>'Prima di andare, posso chiedere una cosa?'<new_line><new_line>Sono sceso dalla macchina e ho chiuso lo sportello. Sasha ha borbottato:<new_line><new_line>'Sì, certo. Perdiamo tempo finché non compare tutta Stepnoy.']],
				['120'] = [[Cosa faremo? Ci arrenderemo? O preferisci spararti in bocca con stile?', ha chiesto Sasha con un sorrisetto triste.<new_line><new_line>Dalla macchina non si vedevano i soldati: tra di noi c'erano il Kamaz e le UAZ. Ho impugnato meglio il fucile e ho aperto la portiera.<new_line><new_line>'Dove diamine stai andando? Ti distruggeranno!']],
				['140'] = [[Mi sono scervellato: non avevo alcuna chance di raggiungere l'altro lato.<new_line><new_line>'Ah, ora ho capito!', ha esclamato Sasha. 'Non sai come fuggire e hai deciso di annegare! Alcune persone non cambiano mai, papà...'<new_line><new_line>'Sì, hai ragione, pessima idea!'. Passandole davanti ho detto: 'Andiamo'.]],
				['111'] = [[Ho ignorato le proteste di Sasha e l'ho fatta rimanere dietro l'UAZ. Non era una copertura ottimale, ma correre in un altro punto sarebbe stato ancora più pericoloso.<new_line><new_line>I combattenti si sono fermati a un paio di passi dalla macchina. Due venivano da sinistra, l'altro da destra.<new_line><new_line>'Sono solo in tre! Tu ti occupi di quello a destra e io degli altri due!', ho bisbigliato a Sasha. Lei ha annuito, non proprio entusiasta.]],
				['101'] = [[Il proiettile ha attraversato il cranio del tenente. È morto sul colpo.<new_line><new_line>'Sei impazzito?!', ha gridato Sasha massaggiandosi le tempie, che erano arrossate per via del colpo del tenente. 'Perché non lo hai sparato subito?! Ti comporti come una femminuccia! È un peccato che quell'idiota è strabico e non ti ha crivellato di proiettili!']],
				['17'] = [[Il soldato con i capelli rossi mi ha sorriso e mi ha dato una pacca amichevole sulla spalla.<new_line><new_line>'Ehi, non prenderla sul personale. Hai fatto un ottimo lavoro recuperando il lanciamissili, ma noi siamo stati i primi ad avanzare con le granate e le mine. Eppure il maggiore beve vino con te come se avessi abbattuto gli Abakan da solo. Non ci ha neppure aumentato le razioni!'<new_line><new_line>'Sì, e come mai la ragazza per poco non ti faceva fuori?', ha esclamato un altro soldato. 'Avevi paura di offenderla?']],
				['27'] = [[Mentre mi avvicinavo, i soldati hanno bisbigliato dei commenti tra di loro e hanno smesso di parlare all'improvviso; hanno persino tolto le sigarette dalla bocca. Tutti e tre si sono irrigiditi in attesa di sentire quello che avevo da dire.<new_line><new_line>Probabilmente ho interrotto un'importante conversazione.]],
				['37'] = [[Sono nell'unica stanza del posto di guardia. È fredda e scomoda e gli angoli sono pieni di ragnatele. Tre materassi con dei cuscini sono appoggiati alle pareti. A parte gli sgabelli rudimentali e il tavolo traballante, non c'era altro nella stanza. Solo una delle quattro lampadine funzionava.]],
				['47'] = [[Sono in un garage buio.<new_line><new_line>Ci sono una trentina di taniche, ma pochissima benzina. Quel poco che sono riuscito a trovare l'ho versato nella mia tanica. Non c'è altro da fare per me qui.]],
				['57'] = [[C'è un'altra finestra grande sul retro dell'unità di comando. Potrei entrare da lì. Mi servirà un piede di porco o qualcosa di simile per rompere il vetro.<new_line><new_line>Il generatore fa rumore, quindi è improbabile che qualcuno sentirà il rumore del vetro rotto.]],
				['155'] = [[Reggiti forte!', ho gridato a Sasha. Il veicolo si è schiantato contro il cancello a tutta velocità. La collisione ci ha sballottati ma, come sospettavo, i cardini hanno ceduto.<new_line><new_line>L'abbiamo sfondato.<new_line><new_line>La città militare era dietro di noi.]],
				['100'] = [[Senza perdere neanche un secondo, ho preso il fucile e ho sparato al tenente. Una smorfia di dolore gli è apparsa sul volto sfigurato. Il tenente ha mollato il fucile e indietreggiando si è accasciato lentamente sul pavimento.<new_line><new_line>Sasha si è girata verso di me e mi ha rivolto un gesto di invito. Non so cosa volesse dire. Poi Sasha ha fatto un cenno con la mano, si è avvicinata al soldato ferito e gli ha tagliato la gola col suo stesso coltello.]],
				['115'] = [[Ci nasconderemo dietro i garage e gli spareremo addosso non appena si avvicinano.'<new_line><new_line>'Non se ne parla!', Sasha mi ha guardato come se fossi un coglione. 'Ci falcerebbero mentre corriamo!'<new_line><new_line>'Tu corri da sinistra, io corro da destra! Tre... due... uno... via!'<new_line><new_line>Sfortunatamente, il mio piano non ha funzionato. I nostri avversari ci hanno visto e hanno aperto il fuoco all'unisono. Ci siamo dovuti nascondere dietro un'altra UAZ.]],
				['104'] = [[Io e Sasha siamo corsi al ponte il più velocemente possibile. Poiché il fuoco era già sotto controllo, la piazza d'armi era poco illuminata e sarebbe stato difficile individuare me e Sasha tra tutta la gente coperta di fuliggine che correva di qua e di là. Ci siamo imbattuti un paio di volte in dei soldati, ma ci hanno solo ricoperto di insulti e poi sono corsi a versare acqua sulle fiamme.<new_line><new_line>Poi alcuni soldati hanno fatto il giro e ci hanno guardato con gli occhi semichiusi...<new_line><new_line>Ecco il ponte che porta al parcheggio!]],
				['22'] = [[Sono a pochi metri dal deposito delle provviste, la cantina che funge anche da prigione della città. Il secondino e due soldati sono tra il deposito e la mensa costruita da poco. Stanno fumando e discutendo di qualcosa a bassa voce.<new_line><new_line>Dietro di me c'è il canale e sull'altra sponda c'è il parcheggio e il cancello d'uscita di Stepnoy. Da qui vedo la mia macchina e un camion Kamaz militare.<new_line><new_line>Se vado a nord e supero la mensa, arriverò al deposito delle armi.]],
				['2'] = [[L'unica soluzione che sono riuscito a trovare è stata quella di creare un diversivo. Ho pensato alle taniche di gasolio vicino al generatore. L'unità di comando era lì vicino. Se avessi versato benzina sull'unità di comando e avessi dato fuoco, la gente del posto sarebbe impazzita per cercare di spegnerlo per evitare che le taniche di gasolio esplodessero.<new_line><new_line>Poi io e Sasha ne approfitteremo del caos e usciremo da qui...<new_line><new_line>È scoccata la mezzanotte. Era una notte serena, luminosa per la luna piena. È il momento di entrare in azione.]],
				['24'] = [[Una delle chiavi è entrata nella serratura, ma non girava. È la chiave sbagliata o semplicemente la serratura è rotta? Le altre chiavi sono troppo piccole. Non riuscirò a entrare nella mensa da questa porta.]],
				['29'] = [[Uno dei soldati ha risposto irritato:<new_line><new_line>'Se vuoi parlare di scemenze, vai nella caserma. Lì troverai tante persone a cui piace chiacchierare. Stiamo cercando di avere una conversazione seria qui. Ok?']],
				['36'] = [[Una delle chiavi ha funzionato. Dopo due mandate e due spinte, la porta si è aperta come se mi stesse invitando a entrare. Era chiaramente un difetto di fabbrica per cui non si chiudeva facilmente.]],
				['130'] = [[(Consiglio: se il sopravvissuto riuscirà ad attraversare la città militare senza attirare l'attenzione, sarà più facile liberare Sasha e scappare. Ma ricorda: il tempo è prezioso!)]],
				['138'] = [[Una marea di persone con dei secchi ha spalancato la porta ed è entrata di corsa. In quel momento ho visto il rubinetto di fianco a me. Ovviamente ero nel bagno.<new_line><new_line>Sono uscito di corsa nel cortile dalla porta posteriore. Qualcuno mi aveva visto? Era possibile...]],
				['152'] = [[Ora vediamo se amano il loro comandante tanto quanto dicono', ho risposto.<new_line><new_line>'No, sei impazzito?'<new_line><new_line>Il mio piano era molto rischioso, ma non ho trovato un'alternativa. Ho fatto il giro delle moto e ho raggiunto il Kamaz, mi sono abbassato e mi sono nascosto dietro le ruote. I soldati avevano appena fatto il giro dell'abitacolo e si stavano spostando verso il veicolo quando ho puntato la canna del fucile sulla schiena del maggiore.]],
				['163'] = [[La mano del maggiore, che per uno spasmo si era aggrappata a un ciuffo di erba gialla, ha mollato la presa e si è poggiata sullo sporco sul bordo della strada...]],
				['45'] = [[La tanica sembrava molto leggera. L'ho agitata e ho illuminato l'interno con la torcia. Vuota.<new_line><new_line>Anche quella dopo era vuota. Ho iniziato a sollevare taniche a caso; per tutte la stessa storia. In alcune era rimasta un po' di benzina, ma giusto un po' sul fondo.<new_line><new_line>Pare che la benzina sia conservata in un altro garage e qui è dove portano le taniche vuote. Che sfiga...]],
				['48'] = [[Sono nella piazza d'armi principale di Stepnoy. Non c'è nessun altro qui.<new_line><new_line>A est l'edificio parzialmente crollato delle docce. La costruzione del nuovo blocco non è ancora iniziata, quindi gli abitanti della città vanno al villaggio per lavarsi.<new_line><new_line>L'unità di comando è a nord della piazza d'armi e dopo l'unità c'è il generatore a gasolio.<new_line><new_line>Da qui, posso attraversare il ponte sul canale fino al parcheggio. Se seguo il canale verso ovest, raggiungerò l'infermeria e il deposito delle armi.]],
				['128'] = [[Uno sparo dalla Makarov ha interrotto il suo discorso. Boris stava ancora impugnando la pistola quando Sasha ha sbattuto contro la portiera, lamentandosi. Il proiettile l'aveva colpita allo stomaco.<new_line><new_line>Ho premuto il grilletto del fucile. Boris è sobbalzato, ha lasciato cadere la pistola ed è caduto lentamente a terra.]]
			},
