	['barQuest'] = {
		['snowmen'] = {
			['text'] = [[Quei maledetti pupazzi di neve stanno causando scompiglio e aggredendo le persone: devi intervenire. Mandali al tappeto in modo che riflettano sul loro comportamento. Più tardi io e Nonno Gelo li rimetteremo in piedi.]],
			['step'] = {
				['1'] = [[Devo raggiungere la località indicata e stendere alcuni pupazzi di neve.]]
			}
		},
		['gravedigger'] = {
			['step'] = {
				['1'] = [[Mi è stato chiesto di occuparmi di un beccamorti impazzito.]]
			},
			['name'] = [[Beccamorti]],
			['text'] = [[Tu, ragazzo, potresti fare al caso mio. Ho un lavoro da offrirti. Un beccamorti è uscito di senno e ha iniziato a dissotterrare cadaveri anziché dare loro una degna sepoltura. È ora di farla finita con questa storia. Pensi di potertene occupare?]]
		},
		['hunt_monsters'] = {
			['text'] = [[Troppi non morti impazziti si aggirano attorno al portale. È tempo che tu faccia ciò che ti riesce meglio e ti sbarazzi di alcuni di loro.]],
			['step'] = {
				['1'] = [[Mi è stata data istruzione di ridurre il numero di non morti nell'area.]]
			}
		},
		['impostor'] = {
			['step'] = {
				['2'] = [[Ora so dove trovare l'impostore, il tempo è di per fargli una visita.]],
				['1'] = [[Un impostore si sta spacciando per Nonno Gelo al fine di derubare le persone. Dev'essere fermato.]],
				['3'] = [[Il Sacco di Loot è con me. Devo restituirlo al popolo.]]
			},
			['name'] = [[Impostore]],
			['text'] = [[Ho sentito parlare di un certo impostore: un bandito si sta spacciando per Nonno Gelo! Sta derubando le persone e danneggiando la sua reputazione. Devi fermarlo.]]
		},
		['cover_the_fighter'] = {
			['step'] = {
				['2'] = [[Siamo riusciti a entrare nel posto e l'esploratore non corre pericoli. Posso tornare all'insediamento.]],
				['1'] = [[L'esploratore ha scoperto un posto, ma è troppo pericoloso per avvicinarsi da soli. Devo aiutarlo nel caso in cui qualcuno lo aggredisca.]],
				['3'] = [[L'esploratore non ha fatto in tempo a occuparsi della cassaforte. Forzala.]]
			},
			['name'] = [[Coprire le spalle]],
			['text'] = [[L'esploratore ha scoperto un posto abbandonato, ma esplorare un posto simile da soli è pericoloso. Coprigli le spalle.]]
		},
		['forest_feeder'] = {
			['step'] = {
				['1'] = [[Mi è stato chiesto di riempire le mangiatoie che sono state posizionate in diverse parti del bosco.]]
			},
			['name'] = [[Mangiatoie nel bosco]],
			['text'] = [[Gli animali non si aspettavano un inverno tanto rigido, pertanto è nostro dovere aiutarli. Ho appeso svariate mangiatoie nel bosco e temo sia già ora di riempirle di nuovo.]]
		},
		['halloween_boss_2022_4'] = {
			['text'] = [[Il Re delle zucche è lo spirito della stagione del raccolto. Si prende cura delle sue zucche e si assicura che la gente che lavora la terra non faccia del male alla natura. Il Re delle zucche sarà pure uno spirito, ma è anche una persona dal sangue nobile, perciò può essere ferito dalla Spada del boia.]],
			['step'] = {
				['2'] = [[Sono sulle loro tracce, ma sono riusciti a scappare... Devo inseguirli.]],
				['1'] = [[Un contadino dice che il suo raccolto è scomparso. Penso ci sia di mezzo lo zampino del Re delle zucche, ma sarà meglio investigare.]],
				['3'] = [[Devo sconfiggere il Re delle zucche. Il contratto diceva che solo la Spada del boia è in grado di ferirlo.]]
			}
		},
		['gift_delivery'] = {
			['step'] = {
				['1'] = [[Ho ricevuto istruzioni di consegnare dei regali.]]
			},
			['name'] = [[Regali da consegnare]],
			['text'] = [[I regali devono essere consegnati a ogni costo. Prendi questi pacchi e portali ai rispettivi destinatari. Ora come non mai, hanno bisogno di tutto il sostegno possibile.]]
		},
		['new_year_tree_quest'] = {
			['step'] = {
				['2'] = [[Ho preso gli alberi, ma ora devo portarli all'insediamento.]],
				['1'] = [[Devo raccogliere e consegnare un sacco di alberi di Natale all'insediamento. Dovrebbe bastare a risollevare lo spirito di tutti durante questo inverno così rigido.]]
			},
			['name'] = [[Consegna gli alberi di Natale]],
			['text'] = [["Questo inverno ogni casa dovrebbe avere un albero di Natale! Le vacanze sono uno dei pochi piaceri che ci sono rimasti. Vai a fare visita ai taglialegna, hanno messo da parte delle cose per noi."]]
		},
		['secret_documents'] = {
			['step'] = {
				['2'] = [[Ho spianato la strada, non mi resta che aprire la cassaforte e prendere i documenti segreti.]],
				['1'] = [[Devo esaminare il laboratorio abbandonato. I documenti segreti di cui ha bisogno il Biologo per continuare la sua ricerca si trovano lì.]]
			},
			['name'] = [[Documenti segreti]],
			['text'] = [["I tuoi sforzi non sono stati vani! Finalmente abbiamo scoperto dove si trovano i documenti segreti collegati alla biostazione più vicina! Raggiungi il laboratorio abbandonato, i documenti dovrebbero essere lì."]]
		},
		['halloween_boss_2022_5'] = {
			['text'] = [[Lo scienziato Victor Frankenstein ha creato un mostro partendo dalle membra dei cadaveri e l'ha portato in vita usando l'elettricità.<new_line>Il Mostro di Frankenstein e il suo amichetto gobbo Igor sono arrivati in questo mondo non molto tempo fa, ma si sono lasciati alle spalle degli orrendi omicidi. Sono stati segnalati numerosi casi di cadaveri rinvenuti senza gli organi.]],
			['step'] = {
				['2'] = [[Devo dirigermi verso il prossimo luogo scritto sulla pagina del Libro dei morti.]],
				['1'] = [[Il Mietitore mi ha dato una pagina del Libro dei morti su cui c'è scritto chi morirà vicino all'ultima scena del crimine di Igor e del Mostro. Devo controllare ogni persona presente sulla pagina che ha strappato. Tra di loro potrebbe esserci la prossima vittima.]],
				['3'] = [[Ho rintracciato Igor e il Mostro di Frankenstein. Devo sconfiggerli.]]
			}
		},
		['progress_voronej'] = {
			['step'] = {
				['1'] = [[L'insediamento di Voronezh è difeso da paracadutisti appostati da Ryazan non molto tempo prima della catastrofe. Il campo di aviazione rimasto costituisce il nucleo dell'insediamento.<new_line><new_line>Da molto tempo, i soldati vogliono tornare a Ryazan e contendere il controllo alla banda che la governa. Sembra che abbiano gli uomini e l'equipaggiamento adatto, ma qualcosa li trattiene.]]
			}
		},
		['witch_delivery'] = {
			['step'] = {
				['2'] = [[Ho le pozioni. Ora ho bisogno di dare loro al barista.]],
				['1'] = [[Devo consegnare un pacco di ingredienti a una strega e farmi dare un lotto di pozioni con cui tornare al portale.]]
			},
			['name'] = [[Consegna alla strega]],
			['text'] = [[Il tuo compito è consegnare un pacco di ingredienti alla strega locale e recuperare un nuovo lotto di pozioni da lei.]]
		},
		['progress_kurgan'] = {
			['step'] = {
				['1'] = [[Sorprendentemente, avevo già familiarità con gli abitanti di Kurgan. A quanto pare, sono loro che gestiscono l'intero sistema ferroviario: se un uomo di Kurgan non si trova su un treno, lo sta riparando. Dunque, meglio evitare disaccordi.<new_line><new_line>I loro treni blindati sono praticamente invulnerabili agli attacchi di banditi e mutanti, ma nelle terre desolate può succedere di tutto. Ecco perché anche per Kurgan è importante mantenere i buoni rapporti con i partner, quantomeno per il carburante e la manutenzione dei treni.]]
			}
		},
		['ambush'] = {
			['step'] = {
				['2'] = [[È ora di tornare alla base e fare rapporto sull'imboscata.]],
				['1'] = [[Il luogo dell'imboscata è segnato sulla mappa. Devo dirigermi lì.]],
				['6'] = [[L'imboscata è fallita.]],
				['5'] = [[L'imboscata è stata un successo ma abbiamo subito delle perdite.]],
				['4'] = [[Mi sono fatto valere in combattimento e ho aiutato i combattenti feriti.]],
				['3'] = [[L'imboscata è stata un successo.]]
			},
			['name'] = [[Imboscata]],
			['text'] = [[Ehi, tu! I nostri ragazzi stanno preparando un'imboscata. Aiutali e forse riuscirai anche a guadagnare qualcosa.']]
		},
		['polar_star'] = {
			['name'] = [[Stella polare]],
			['text'] = [[La sfera che hai trovato è la stella polare. Nonno Gelo la usava nei viaggi più lunghi, ma dovrai scoprirne il funzionamento da solo. Prendi la stella, recati nelle terre desolate in una notte limpida e alzala al cielo.]]
		},
		['halloween_boss_2022_1'] = {
			['step'] = {
				['7'] = [[Devo distruggere questo Demone e la sua setta.]],
				['1'] = [[I seguaci della setta si riuniscono in un fienile abbandonato da qualche parte nella landa desolata. Devo trovare un modo di raggiungerlo.]],
				['5'] = [[I seguaci della setta vogliono che lasci una donazione prima che entri. Devo dare loro una bottiglia di vodka o una candela fatta a mano. In ogni caso, potrei riuscire a corrompere il buttafuori.]],
				['3'] = [[Mi servirà un travestimento per riuscire a entrare. Potrei creare una veste, se avessi i materiali giusti.]]
			},
			['name'] = [[Demone Balthazar]],
			['text'] = [[I seguaci della setta stanno evocando il Demone Balthazar per servirlo. In cambio, lui offre loro dei doni talmente potenti, che se un umano riuscisse ad assimilarli tutti quanti sarebbe in grado di sfidare il Demone in persona.]]
		},
		['progress_ryazan'] = {
			['step'] = {
				['1'] = [["Vedi Ryazan e poi muori." Questa macabra battuta si è estesa nei dintorni dell'insediamento per centinaia di chilometri. Un tempo, un clan di banditi ha preso il controllo della raffineria di petrolio e ha iniziato a commerciare attivamente con una valuta propria: il rublo nero. In breve tempo, intorno all'impianto è sorta una cittadina mercantile.<new_line><new_line>Oggi Ryazan è uno snodo commerciale delle terre desolate, ma il suo ventre criminale è sempre pronto a riaffiorare: disonestà, violenza e depravazione fanno capolino dalle insegne luccicanti e dal lusso dei casinò.]]
			}
		},
		['new_year_deliver_cloth'] = {
			['step'] = {
				['2'] = [[Devo portare le cose a un altro gruppo di abitanti che sono partiti per fare affari.]],
				['1'] = [[Ho deciso di consegnare dei vestiti caldi a coloro che lavorano fuori dall'insediamento.]]
			},
			['name'] = [[Consegna i vestiti]],
			['text'] = [[I bambini lavorano a maglia i cappelli caldi e i calzini per le persone che lavorano al freddo fuori dall'insediamento. Potresti portare loro un paio di pacchetti?]]
		},
		['order_delivery'] = {
			['step'] = {
				['2'] = [[Ho il carico. Ora torno alla base.]],
				['1'] = [[Devo raggiungere la base e raccogliere il carico.]],
				['6'] = [[Il cliente si è rifiutato di comprare il carico, per cui ora ce l'ho io.]],
				['5'] = [[Non ho consegnato il carico.]],
				['4'] = [[Ho consegnato il carico e sono stato rimborsato completamente.]],
				['3'] = [[Il carico è stato consegnato tutto intero.]]
			},
			['name'] = [[Consegna]],
			['text'] = [[Dobbiamo trasferire il carico da un'altra base. Ti va di dare una mano?']]
		},
		['progress_arzamas'] = {
			['step'] = {
				['1'] = [[Un uomo vagava da molti anni nelle terre desolate. Finalmente, quando le catene della vita terrena si dissolsero, davanti al lui comparve un cespuglio ardente di erbe sacre. Circondandolo di fumo, Egli si rivolse all'uomo chiamandolo Profeta e gli ordinò di creare un Nuovo Paradiso.<new_line><new_line>Questo narrano i residenti del vecchio monastero di Arzamas. Nessuno sa per certo cosa facciano davvero lassù. Alcuni membri della comunità sono in contatto con il mondo esterno ed effettuano qualche consegna a Ryazan. Non si sa altro.]]
			}
		},
		['catch_thief'] = {
			['step'] = {
				['2'] = [[Ho distrutto il campo di banditi che rubava dalla base. D'ora in poi non ci saranno problemi.]],
				['1'] = [[Per prima cosa devo andare al magazzino e parlare con quest'uomo.]],
				['10'] = [[Devo riportare al magazzino ciò che è stato rubato.]],
				['9'] = [[Il capo del magazzino mi ha chiesto un favore: occuparmi dei complici del ladro.]],
				['8'] = [[Il capo del magazzino mi ha chiesto un favore: recuperare ciò che è stato rubato.]],
				['7'] = [[Sapendo che la colpa sarebbe stata data a qualcun altro, ho preso qualche cosa dal magazzino.]],
				['6'] = [[Non sono in grado di fare la guardia. Il ladro è fuggito.]],
				['5'] = [[Ho stretto un patto con i banditi e li ho lasciati entrare nel magazzino, poi li ho uccisi tutti.]],
				['4'] = [[Sono riuscito a catturare il ladro vivo.]],
				['3'] = [[Il ladro è caduto nella mia trappola ed è morto.]]
			},
			['name'] = [[Cattura il ladro]],
			['text'] = [[Il capo del magazzino si è lamentato dei ladri. Non riuscirà a catturarli tutti da solo. Dagli una mano.']]
		},
		['progress_chelyabinsk'] = {
			['step'] = {
				['1'] = [[Chelyabinsk è un insediamento industriale densamente popolato. Da questa base, il Сapo controlla la produzione e ogni aspetto della vita sul Fronte di ferro.<new_line><new_line>Il motivo è chiaro: da qui parte il collegamento ferroviario con le altre città. Il Fronte di ferro inonda la Siberia occidentale di metallo e acquista cibo dai siberiani in cambio, dal momento che i campi di Chelyabinsk non sono coltivabili.]]
			}
		},
		['ice_brigands_camp'] = {
			['step'] = {
				['1'] = [[Devo radere al suolo l'accampamento dei briganti di ghiaccio.]]
			},
			['name'] = [[Accampamento dei briganti]],
			['text'] = [[È stato avvistato un nuovo accampamento di briganti di ghiaccio. Vacci prima che inizino a combinare guai.]]
		},
		['reinforcement'] = {
			['step'] = {
				['2'] = [[La battaglia è finita. Posso tornare alla base per la mia ricompensa.]],
				['1'] = [[Devo sbrigarmi e aiutare i nostri uomini. Non sappiamo quanto dureranno senza rinforzi.]],
				['4'] = [[Li abbiamo sconfitti, ma non senza riportare perdite.]],
				['3'] = [[Lavorando insieme siamo riusciti a sconfiggerli.]]
			},
			['name'] = [[Rinforzi]],
			['text'] = [[È arrivato un messaggio urgente: un'unità della nostra base ha bisogno di rinforzi. Vai a dare una mano.']]
		},
		['wood_delivery'] = {
			['step'] = {
				['1'] = [[La fanciulla di neve mi ha chiesto di raccogliere della legna da ardere per le persone in difficoltà.]]
			},
			['name'] = [[Legna da consegnare]],
			['text'] = [[Quando la stagione si fa così fredda, un fascio di legna da ardere può salvare delle vite. Raccogli del legname e mettilo dove tutti possano trovarlo.]]
		},
		['cold_footprints'] = {
			['name'] = [[Pista fredda]],
			['text'] = [[Le lepri sono riuscite a scovare l'accampamento dei briganti di ghiaccio che hanno saccheggiato il terem. Ti indicherò il punto sulla mappa. Dirigiti lì e scopri tutto il possibile.]]
		},
		['march_skeletons'] = {
			['step'] = {
				['1'] = [[Mi è stato chiesto di radunare degli scheletri e praticare un rituale seguendo le istruzioni di un rotolo.<new_line><new_line>Raduna dieci scheletri e posizionali in cerchio, con la testa rivolta verso il centro. Cospargi i teschi con della cenere mentre procedi per tre volte da ovest verso est invocando lo spirito di Tetriandok. A rituale completato, gli scheletri troveranno la loro via.]]
			},
			['name'] = [[La marcia degli scheletri]],
			['text'] = [[Ti è mai stato chiesto di risvegliare i morti? Ho bisogno dei servigi di alcuni scheletri. Prendi questo rotolo: contiene tutte le istruzioni che ti servono. Cerca solo di non commettere errori.]]
		},
		['stash_polar_fox'] = {
			['step'] = {
				['1'] = [[La volpe artica ha trovato qualcosa di interessante qui vicino. Vale la pena controllare.]]
			}
		},
		['stash_handcar'] = {
			['step'] = {
				['1'] = [[Ho trovato un piccolo pezzo di carta con l'indirizzo di una casa a Kola. Vale la pena controllare. Dopo tutto, forse troverò dei rifornimenti.]]
			}
		},
		['progress_krasnoyarsk'] = {
			['step'] = {
				['1'] = [[L'insediamento di Krasnoyarsk sembra una vera e propria utopia della scienza. Le persone lavorano in serre gestite da scienziati. Hanno sviluppato il sistema BIOS-4, che consente di ricavare raccolti tanto abbondanti da sfamare addirittura gli altri insediamenti. Certo, non è mica gratis.<new_line><new_line>Ogni cosa ha il suo prezzo: il processo prevede un consumo ingente di elettricità e acqua pulita. Inoltre, la tecnologia impiegata è così avanzata che la perdita di un solo elemento chiave della squadra potrebbe mandare a rotoli l'intero sistema.]]
			}
		},
		['supply'] = {
			['step'] = {
				['2'] = [[Ho trovato tutto ciò che mi serviva. Ora devo consegnare tutto alla base.]],
				['1'] = [[Devo trovare tutto ciò di cui ha bisogno l'altra base.]],
				['5'] = [[Non ho ricevuto alcuna ricompensa. Oh, beh, immagino che queste cose mi torneranno utili.]],
				['4'] = [[Mi hanno ricompensato, ma avrebbero potuto essere più generosi.]],
				['3'] = [[Il mio duro lavoro è stato ben ricompensato.]]
			},
			['name'] = [[Spedizione]],
			['text'] = [[La base vicina ci ha chiesto rifornimenti. Puoi trovare tutto ciò che chiedono e portarglielo?']]
		},
		['progress_gorenichi'] = {
			['step'] = {
				['1'] = [[Durante la catastrofe, le terre circostanti sono state soggette a bombardamenti di massa, ma alcune zone di terra nera fertile sono rimaste.<new_line>Gli abitanti locali hanno trovato questi piccoli appezzamenti di terra e hanno cominciato a coltivarli.<new_line><new_line>In seguito è comparsa una nuova minaccia ereditata dalle radiazioni: ondate su ondate di creature mutanti hanno iniziato ad assediare l'insediamento. A dire il vero, i lupi neri si sono affermati in cima alla catena alimentare e hanno decimato i rivali, ma la forza dei sopravvissuti umani sta scemando sotto il violento assedio.]]
			}
		},
		['forester'] = {
			['step'] = {
				['1'] = [[Qualcuno mi ha offerto un lavoro: aiutare un boscaiolo. Hanno promesso di ricompensarmi a dovere.]]
			},
			['name'] = [[Boscaiolo]],
			['text'] = [[È entrato un tale con un ordine per te: un boscaiolo ha bisogno del tuo aiuto. Sembra un lavoretto semplice con una buona ricompensa. Fossi in te, non me lo farei ripetere due volte.]]
		},
		['set_up_devices'] = {
			['step'] = {
				['2'] = [[Ho installato e mimetizzato l'attrezzatura. Devo riferirlo al membro del personale del Progresso.]],
				['1'] = [[Il Progresso sta osservando l'area intorno all'insediamento. Devo installare la loro attrezzatura fuori dalla base.]],
				['3'] = [[Ho installato e mimetizzato l'attrezzatura.<new_line>Il branco di biomutanti che avrebbe potuto notarmi è stato distrutto. Ora i dispositivi sono completamente sicuri. Devo riferirlo al membro del personale del Progresso.]]
			},
			['name'] = [[Installa l'attrezzatura]],
			['text'] = [["Stiamo studiando il comportamento dei biomutanti e il modo in cui le spore si diffondono. Installa questa attrezzatura vicino all'insediamento. Le informazioni che raccoglieremo ci aiuteranno con le nostre ricerche sul progetto EMBA."]]
		},
		['science_polygon'] = {
			['step'] = {
				['1'] = [[Devo aiutare gli scienziati del Progresso ad affrontare i biomutanti.]]
			},
			['name'] = [[Poligono di prova]],
			['text'] = [["Stiamo conducendo esperimenti al poligono di prova per comprendere meglio il progetto EMBA. Tuttavia, i biomutanti ci stanno attaccando. Tu hai molta esperienza di combattimento: puoi affrontare la minaccia?"]]
		},
		['provision'] = {
			['step'] = {
				['2'] = [[Ho raccolto tutto il necessario e devo consegnare le provviste agli Archivisti dell'insediamento.]],
				['1'] = [[Un membro del personale del Progresso mi ha chiesto di raccogliere provviste per la spedizione.]]
			},
			['name'] = [[Provviste]],
			['text'] = [["I nostri perlustratori cercheranno strutture collegate al progetto EMBA. Aiuta con la raccolta di provviste per la loro operazione."]]
		},
		['progress_omsk'] = {
			['step'] = {
				['1'] = [[Omsk si è separata dall'A.S.S.A. e ora è governata dall'esercito. Questo insediamento è attraversato dall'unica rotta commerciale che collega l'est con l'ovest.<new_line>A quanto pare, è l'unica cosa che permette la sopravvivenza di una comunità tanto chiusa.<new_line><new_line>Tuttavia, stando a indizi e dicerie, l'esercito ha anche altro per tenere a bada le potenze degli Urali e della Siberia. Puoi provare a scoprire di più su Omsk, ma in pochi riescono a stabilire relazioni con la città, nemmeno con la periferia.]]
			}
		},
		['pumpkin_seed'] = {
			['step'] = {
				['1'] = [[Ho un seme da piantare nelle terre desolate. Non so di cosa l'oste abbia paura né come potrebbe aiutarci. Ha un aspetto del tutto innocuo.]]
			},
			['name'] = [[Seme di zucca]],
			['text'] = [[Se la testa dice la verità, siamo spacciati. Ma forse abbiamo ancora una possibilità, anche se speravo che non saremmo dovuti arrivare a tanto. Prendi questo seme di zucca e piantalo nelle terre desolate.]]
		},
		['lost_soul'] = {
			['step'] = {
				['1'] = [[Da qualche parte nel bosco c'è un uomo bisognoso d'aiuto. Devo sbrigarmi.]]
			},
			['name'] = [[Anima perduta]],
			['text'] = [[Da qualche parte nel bosco c'è un uomo in difficoltà. Trova il suo accampamento e segui le sue tracce: potrebbe aver bisogno di aiuto.]]
		},
		['new_year_boss_2022_6'] = {
			['step'] = {
				['2'] = [[Nella battaglia finale ho il ruolo più importante. Diamoci dentro!]],
				['1'] = [[Sono riuscito a eliminare quel perfido Frosty. Ma c'è stato davvero un miracolo di Natale? Babbo Natale è tornato?]]
			}
		},
		['snowmen_fortress'] = {
			['step'] = {
				['1'] = [[Il mio compito è distruggere il forte di neve e sbarazzarmi dei pupazzi di neve del posto.]]
			},
			['name'] = [[Forte di neve]],
			['text'] = [[I pupazzi di neve hanno eretto un forte di neve e stanno devastando l'area. Dirigiti lì e metti fine alle loro malefatte.]]
		},
		['new_year_boss_2022_5'] = {
			['step'] = {
				['2'] = [[Abbiamo sconfitto Krampus, ma il malvagio Frosty è riuscito a mettere le mani sull'energia magica che abbiamo rilasciato. Devo parlare con i miei compagni Babbi per decidere la prossima mossa.]],
				['1'] = [[Abbiamo fatto tutto il possibile prima del combattimento contro Krampus e la sua super-arma. È giunto il momento di affrontarlo.]]
			}
		},
		['new_year_boss_2022_4'] = {
			['step'] = {
				['2'] = [[Cyberbabbo ci aiuterà a trovare più informazioni sulla super-arma. Devo irrompere nel posto in cui tengono nascoste le informazioni.]],
				['1'] = [[Dobbiamo far smaltire la sbornia a Cyberbabbo. Gli serve dell'acqua.]],
				['4'] = [[Credo che abbiamo completato la missione. Devo raggiungere Cyberbabbo e capire che cosa ha scoperto.]],
				['3'] = [[Mi sono fatto coinvolgere nella bravata di Cyberbabbo. Ora devo collegare il cavo da qualche parte e aspettare che mi chiami.]]
			}
		},
		['drakula'] = {
			['step'] = {
				['1'] = [[Non rimane che un'ultima battaglia; e il mio avversario è il Conte Dracula.]]
			},
			['name'] = [[Conte Dracula]],
			['text'] = [[La testa di zucca si è imbattuta nell'Antico nelle terre desolate. Anche Dracula dev'essere laggiù. Trovalo e distruggilo prima che possa combinare qualche altro disastro.]]
		},
		['new_year_boss_2022_2'] = {
			['step'] = {
				['2'] = [[Devo nascondere la buca di Babbo Naclava con l'erba.]],
				['1'] = [[Devo scavare una buca. Babbo Naclava potrebbe avere in mente di piazzare una trappola.]],
				['5'] = [[Siamo sulle tracce dello scomparso Babbo Natale. Il furfante che è fuggito dall'imboscata potrebbe condurci al nostro obiettivo. Faremmo meglio a seguirlo.]],
				['4'] = [[Abbiamo scoperto che Babbo Natale si è trasformato in Krampus, e che la sua banda sta distruggendo tutto ciò che ha a che fare con il Natale. Dobbiamo trovare Babbo Naffare per discutere delle nostre prossime mosse.]],
				['3'] = [[Stiamo preparando una trappola e io dovrei fare da esca.]]
			}
		},
		['progress_izhevsk'] = {
			['step'] = {
				['1'] = [[Izhevsk è divisa tra il Fronte di ferro e i Monti Urali, con chilometri di terra infetta brulicante di mutanti. Probabilmente è per questo che il Fronte di ferro non ha ancora messo le mani sull'industria locale di armi.<new_line><new_line>Di conseguenza, gli abitanti di Izhevsk hanno interesse a mantenere i contatti con Ryazan: la città è molto lontana e possono serenamente commerciare le loro armi e i prodotti del mattatoio di api.]]
			}
		},
		['frozen'] = {
			['step'] = {
				['1'] = [[I briganti di ghiaccio hanno iniziato a rapire le persone. Devo salvarle.]]
			},
			['name'] = [[Di ghiaccio]],
			['text'] = [[I briganti hanno rapito delle persone e le hanno imprigionate in blocchi di ghiaccio! Se ti sbrighi, potresti riuscire a salvarle. Non indugiare; il gufo ti indicherà la via.]]
		},
		['halloween_boss_2022_3'] = {
			['text'] = [[Il conte Dracula è un antico vampiro, nonché intenditore di arte a cui piace stare in compagnia di donne bellissime. Dicono che sia un tipo galante e gentile, ma ciò non gli impedisce di dare la caccia a gente indifesa e di cibarsi del loro sangue.]],
			['step'] = {
				['2'] = [[Ho trovato una sopravvissuta dell'attacco dei vampiri. Prima devo accompagnarla a casa, poi le chiederò più dettagli sull'imboscata.]],
				['1'] = [[Stando ad alcuni rapporti, un gruppo di viaggiatori è stato attaccato dai vampiri. Devo andare subito sul luogo dell'incidente.]],
				['3'] = [[Mi ritrovo sul menu del conte Dracula. Mi ha invitato a rilassarmi e a godermi la serata. Soltanto dopo le cose si sono messe male.]]
			}
		},
		['gigahive_ufa'] = {
			['name'] = [[Il tipo di api sbagliato]],
			['step'] = {
				['2'] = [[A Ufa c'è un alveare governato da un'ape regina. Per arrivarci, ho bisogno di un antidoto. Ma per ottenerlo dovrò uccidere alcune api madre e portare la cera a uno specialista di Izhevsk. Inoltre, l'oste mi ha promesso una ricompensa per la testa della regina.]],
				['1'] = [[Devo andare a Izhevsk e chiedere all'oste dell'alveare a Ufa.]],
				['3'] = [[Ho ucciso l'ape regina. Ora devo portare la sua testa all'oste di Izhevsk e ottenere la mia ricompensa.]]
			}
		},
		['halloween_boss_2022_2'] = {
			['text'] = [[Il Medico della peste conduce i suoi diabolici esperimenti nascosto tra le esalazioni velenose. Riporta in vita i morti e diffonde malattie tra i vivi. L'armatura demoniaca può proteggerti dalle sue maledizioni, ma raggiungerlo non sarà affatto semplice. L'astuto medico è protetto da orde di zombi.]],
			['step'] = {
				['2'] = [[La gente dice che il Medico della peste sia nascosto nel cimitero, perciò è lì che andrò.]],
				['1'] = [[Una piccola comunità è perseguitata dagli zombi. Significa che il Medico della peste si nasconde da qualche parte qui vicino. Devo assolutamente parlare con queste persone.]],
				['3'] = [[Devo sconfiggere il Medico della peste. Il Mietitore ha detto che la Veste demoniaca mi avrebbe protetto dai suoi attacchi.]]
			}
		},
		['destroy_lair'] = {
			['step'] = {
				['2'] = [[Ho distrutto la tana.]],
				['1'] = [[Devo trovare la tana ed eliminare chi ci vive.]]
			},
			['name'] = [[Distruggi la tana]],
			['text'] = [[Il capo delle guardie ha puntato il dito sulla mappa dietro di lui. "Questa zona è infestata di bestie pericolose. Vai a distruggere la loro tana."]]
		},
		['take_stash'] = {
			['step'] = {
				['2'] = [[Ho trovato il punto in cui era nascosta la cassa. La porterò alla base e scoprirò come procedere.]],
				['1'] = [[Sulla mappa è segnato il luogo in cui è nascosto il tesoro. Devo dirigermi lì e dissotterrarlo.]],
				['6'] = [[Per sbaglio, un abitante dell'insediamento ha nascosto il tesoro in modo pessimo e i banditi lo hanno rubato. Devo tornare indietro per fare rapporto.]],
				['5'] = [[Riconquista la refurtiva dei banditi.]],
				['4'] = [[Dopo aver aperto la cassa lungo la strada, ho deciso di tenere per me il contenuto.]],
				['3'] = [[Quando sono tornato alla base, ho consegnato la cassa all'oste.]]
			},
			['name'] = [[Prendi il tesoro]],
			['text'] = [[Ehi, amico, ho una consegna qui. Beh non proprio qui... L'hanno nascosta lungo la strada. Potresti portarmela?']]
		},
		['scientist_rescue'] = {
			['step'] = {
				['2'] = [[Ho trovato lo scienziato, ora devo riportarlo all'insediamento.]],
				['1'] = [[Devo raggiungere l'ecostazione e riportare lo scienziato all'insediamento.]],
				['9'] = [[Rimuovi dal tetto il dispositivo rimasto.]],
				['8'] = [[Ho trovato lo scienziato, ora devo riportarlo all'insediamento.<new_line><new_line>Prima della partenza, ho aiutato lo scienziato a smontare il dispositivo rimasto.]],
				['7'] = [[Aiuta lo scienziato a medicare una ferita lieve.]],
				['6'] = [[Ho trovato lo scienziato, ora devo riportarlo all'insediamento.<new_line><new_line>Prima della partenza, ho aiutato lo scienziato a medicare una ferita lieve.]],
				['5'] = [[Ho trovato lo scienziato. Sta aspettando che io lo riporti all'insediamento.]],
				['4'] = [[Ho trovato lo scienziato, ora devo riportarlo all'insediamento.<new_line><new_line>Sulla via del ritorno, abbiamo recuperato l'equipaggiamento rubato allo scienziato.]],
				['3'] = [[Prendi l'equipaggiamento dai banditi.]]
			},
			['name'] = [[Riporta lo scienziato]],
			['text'] = [[Il nostro scienziato è di turno nell'ecostazione. Ci ha contattato per dirci che sta avendo problemi con dei banditi locali. Raggiungilo e riportalo a casa.]]
		},
		['immortal_love'] = {
			['step'] = {
				['1'] = [[Il mio compito è salvare una ragazza dal covo di una mummia. Per trovarlo, devo attirare degli scorpioni e lasciare che mi conducano sin lì.]]
			},
			['name'] = [[Mummia]],
			['text'] = [[Ho decifrato l'ultima voce del diario di bordo. C'è scritto che una mummia ha ordinato il rapimento di una ragazza; dobbiamo salvarla. Segui gli scorpioni per trovare la tana della mummia. Attirali fuori e ti condurranno dal loro padrone.]]
		},
		['progress_tver'] = {
			['step'] = {
				['1'] = [[Delle persone sono giunte alla stazione fluviale abbandonata e l'hanno fatta rifiorire. Ora le barche di Tver solcano il fiume da nord a sud, mentre nel rumoroso edificio della stazione si possono acquistare oggetti ottenuti rischiosamente da Mosca o scambiati con Ryazan.<new_line><new_line>I residenti dell'insediamento sono pensano solo agli affari, ma sono lieti di accettare un viandante nella loro vita dinamica dall'indistinguibile odore di pesce e catrame. Solo gli scansafatiche non sono i benvenuti.]]
			}
		},
		['progress_tula'] = {
			['step'] = {
				['1'] = [[Il Cremlino di Tula accoglie i viandanti con una batteria di cannoni moderni che fuoriescono dagli antichi merli delle mura. Guadagnarsi la fiducia degli ex operai della fabbrica di armi non sarà semplice.<new_line><new_line>Diverse fazioni guardano con avidità le abbondanti scorte di armi di Tula, soprattutto le bande che governano Ryazan. Tuttavia, i feroci abitanti della città restano fuori dagli affari e dalle alleanze altrui: si accontentano di commerciare e di mantenere uno sguardo vigile dalle loro mura.]]
			}
		},
		['hunt'] = {
			['step'] = {
				['2'] = [[Devo portare la carne al cliente.]],
				['1'] = [[Mi è stato chiesto di trovare della carne. Devo andare a caccia.]],
				['4'] = [[Non ho consegnato abbastanza carne e il cliente non ha pagato.]],
				['3'] = [[È andato tutto bene e il cliente è soddisfatto.]]
			},
			['name'] = [[Caccia]],
			['text'] = [[Hanno ordinato della carne. Ti va di andare a caccia?']]
		},
		['help_hunter'] = {
			['step'] = {
				['1'] = [[Devo aiutare un altro cacciatore.]]
			},
			['name'] = [[Aiuta il cacciatore]],
			['text'] = [[Un cacciatore è finito nei guai. Va' ad aiutarlo. Ti indicherò il punto sulla mappa.]]
		},
		['progress_sverdlovsk'] = {
			['step'] = {
				['1'] = [[Il Fronte di ferro considera Sverdlovsk la capitale degli armamenti e la fonte delle sue migliori truppe. Questi combattenti veementi e ben equipaggiati si spingono fuori città per saccheggiare le terre desolate.<new_line><new_line>I razziatori affermano di catturare solo i banditi pericolosi per rieducarli con il duro lavoro in fabbrica. In realtà, anche i pacifici viandanti solitari sono in pericolo da queste parti.]]
			}
		},
		['progress_surgut'] = {
			['step'] = {
				['1'] = [[Dopo la catastrofe generale, Surgut è stata circondata dallo straripante fiume Ob'. La sua unica connessione con il mondo esterno è rappresentata dalla strada per Tyumen, la sola rimasta.<new_line><new_line>L'insediamento si limita a rifornire di greggio i partner del Sindacato. Per fortuna, il petrolio scorre a dismisura. Delle rotte fluviali consentirebbero a Surgut di commerciare con altre città siberiane, ma chi è disposto a rispondere a questa necessità?]]
			}
		},
		['bandit_camp'] = {
			['step'] = {
				['2'] = [[Un patto è un patto. Devo dare ai banditi il tempo di andarsene.]],
				['1'] = [[Il capo delle guardie mi ha detto dove si trova il campo dei banditi. Devo andarci.]],
				['6'] = [[Ho stretto un patto coi banditi. Non ci infastidiranno più.]],
				['5'] = [[Ho ucciso tutti i banditi.]],
				['4'] = [[Ora che mi sono occupato dei banditi, devo tornare alla base.]],
				['3'] = [[I banditi dovrebbero essersene andati ora. Dovrei controllare il campo per sicurezza.]]
			},
			['name'] = [[Campo dei banditi]],
			['text'] = [[I banditi sono una spina nel fianco ultimamente. Si sono accampati qui vicino a noi. Puoi occupartene?']]
		},
		['progress_rostov'] = {
			['step'] = {
				['1'] = [[Quando le steppe intorno al Don si sono trasformate in terre desolate, l'atamano ha riunito tutti sopravvissuti sotto il suo controllo. Un gruppo di avventurieri si è radunato intorno a lui: alcuni avevano antenati cosacchi, mentre altri erano solo affascinati dal romanticismo della libertà.<new_line><new_line>Dunque la steppa è tornata alla vita, la vita dei cosacchi... o perlomeno così ritiene l'atamano. Sotto la protezione della druzhina, il corpo militare locale, le persone comuni sono riuscite a ripristinare l'agricoltura e, alla fine, tutta Rostov è stata salvata dalla fame.]]
			}
		},
		['progress_olonec'] = {
			['step'] = {
				['1'] = [[Il primo insediamento popolato sul mio tragitto. È davvero snervante ritrovarsi di nuovo tra la gente. Qui sono tutti abituati ad affidarsi all'aiuto reciproco e alle proprie risorse per fronteggiare le avversità. Probabilmente, un secolo fa vivevano già in questo modo.<new_line><new_line>I pescatori non si inoltrano al di là del lago Ladoga e dubito che qui troverò le risposte a tutte le mie domande. Tuttavia, è un buon posto per scoprire qualche notizia e prepararmi a un altro viaggio.]]
			}
		},
		['camp_supply'] = {
			['step'] = {
				['2'] = [[Ho consegnato i rifornimenti. Non posso fare di più. Torno a casa.]],
				['1'] = [[Devo consegnare i rifornimenti all'accampamento alleato.]],
				['9'] = [[Cura l'alleato ferito nell'accampamento.]],
				['8'] = [[Aiuta gli alleati a respingere l'attacco.]],
				['7'] = [[L'accampamento alleato è sotto attacco. Devo dire all'insediamento di mandare aiuto.]],
				['6'] = [[Quando sono arrivato, erano tutti morti. Non c'era nessuno a cui consegnare i rifornimenti.]],
				['5'] = [[Sono sopravvissuto solo io. Ora non c'è più nessuno a cui consegnare i rifornimenti.]],
				['4'] = [[Ho aiutato gli alleati a difendersi, poi ho consegnato loro i rifornimenti.]],
				['3'] = [[Ho consegnato i rifornimenti. I nostri alleati sono stati felici per l'aiuto.]]
			},
			['name'] = [[Rifornimenti dell'accampamento]],
