	['npc'] = {
		['spiridon'] = {
			['episode'] = {
				['27'] = [[Você de novo? Mas eu já disse que não vou a lugar nenhum. Não tem nem como!]],
				['26'] = [[Boa sorte. Como está indo? Tudo bem? Nos vemos por aí. A Lyuba não está de bom humor hoje. Ela vai arrebentar nós dois se puder.]],
				['23'] = [[(Depois de uma conversa breve e desagradável com Lyuba, eu pude conversar com Spiridon novamente.)]],
				['25'] = [[Já entendi. Está bem. Vou esperar um pouco mais.]],
				['24'] = [[(Spiridon me cumprimentou em tom deliberadamente alto, olhando para a porta, atrás da qual Lyuba estava escondida.)<new_line>Olá, olá!<new_line>(Depois, ele abaixou a voz e continuou.)<new_line>Ora, conseguiu a vodca? Quatro garrafas, sim?]],
				['17'] = [[Dê uma olhada. É uma belezinha, hein? O tripé está meio defeituoso, então vai precisar usar um tijolo. O coordenador desvia do centro, mas é o problema é do projeto original. Todos os carregamentos tiveram o mesmo problema. E ele não se assenta direito nos sulcos. Vai precisar segurar delicadamente. Ah, e...]],
				['9'] = [[Ah, agora eu acredito! Ele também não te conhece! Acha que eu sou besta? De novo?<new_line><new_line>(Lyuba Kugal dá um tapa seco no marido.)]],
				['8'] = [[(Um homem muito menor sai de trás da mulher. Ele tem um rosto de alguém que parece inteligente, mas cansado.)<new_line><new_line>Lyuba, espera. Eu não conheço ele. Não é do nosso assentamento.]],
				['7'] = [[Eu sei por que está aqui! Veio encher a cara, e rugir, e gritar. Foi pra isso que veio! Então, vai ir embora ou precisa de ajuda? Eu ajudo! Eu ajudo!]],
				['6'] = [[Que nada! Não se perde o jeito nisso! E, na minha área de especialização, idade é a vantagem. Você olha pros mais novos hoje em dia, e são todos uns macacos de mão pesada!<new_line>Se prepara porque vamos para Ufa!]],
				['5'] = [[Sistema de mísseis? O quê? Posso consertar um tanque de olhos fechados! Eu até fui convidado para Severodvinsk para a construção do submarino nuclear!]],
				['4'] = [[Ah, desculpe. Você entende. Não posso deixar a Lyuba aqui sozinha... mas me chame se algo aparecer.]],
				['3'] = [[Boris quem? Ele tinha razão sobre o quê?]],
				['2'] = [[(Kugel lambeu a boca e estalou os lábios.)<new_line><new_line>De uma destilaria? Essa é da boa. A nossa vodca caseira não é tão boa assim.<new_line>certo, pode falar. Três garrafas, esse é o acordo. Enquanto isso, preciso ver bem onde vou escondê-las da Lyuba.]],
				['1'] = [[(Os Kugels viviam no primeiro andar do primeiro bloco de cinco andares. Uma mulher forte abriu a porta para mim. Ela tinha uma expressão muito severa.)<new_line><new_line>Você de novo?! Vai embora, seu bêbado maldito! Já é o suficiente! Pela última vez, estou avisando que ele não vai a lugar nenhum!]],
				['12'] = [[(A esposa dá outro tapa no marido, forte o suficiente para fazê-lo gritar.)<new_line><new_line>Esse é o seu agradecimento! A você pode ir embora, desgraçado, porco! Ele estragou a minha vida, dei os meus melhores anos a ele! Minha mãe tinha razão, Deus a tenha! Deveria ter casado com Ptichkin... Talvez assim você tivesse bebido até a cova!]],
				['11'] = [[Como assim para quê? O país sempre precisa de bons engenheiros! Eu tenho até um certificado graças ao comitê regional!]],
				['14'] = [[Bom, eu estava pensando... Você me lembra um velho amigo. Espera, o que foi que disse? Ufa? Está cheio de abelhas lá!<new_line>Não mesmo. Não vou me arrastar para Ufa. Ainda quero viver! Encontre outro besta.]],
				['13'] = [[(Lyuba bateu a porta, fechando-se na cozinha. Spiridon Kugel me olhou com o rosto franzido.)<new_line><new_line>Ela está nervosa desde a manhã por algum motivo. Faz o seguinte, diz por seu pessoal que não vou hoje. Não posso, como pode ver. Amanhã eu vou com certeza!]],
				['20'] = [[Você não precisa me levar de volta. Eu vi uma motocicleta na garagem. Está quase nova, e é uma série limitada. Vou chegar em casa mais rápido com ela. Já ajustei algumas coisas, então nem as abelhas vão me pegar. Nos vemos!]],
				['22'] = [[Obrigado! É de alta qualidade! Vou poder brindar com os rapazes amanhã. Só seja um parceiro e não fale nada na frente da minha esposa. A minha querida tem um gancho de direita que só. Quem imaginaria, né? Ela costumava ser um docinho. Ah, os bons tempos.]],
				['21'] = [[(Kugel balançou a cabeça repreensivamente.)<new_line><new_line>Está bem, camarada. Da próxima então. Você não quer que eu desmonte o sistema, não é? É só que você já me deve quatro garrafas.]],
				['19'] = [[Não trabalho por agradecimentos. Traga-me vodca.]],
				['18'] = [[Aliás, avise o Boris que os velhos mestres ainda estão ativos! Ele pode vir visitar. A Lyuba vai fazer uns bolos, e a gente pode conversar sobre os velhos tempos.]],
				['10'] = [[Ajuda? De que serve o bêbado para alguém? Não serve para ninguém. Ele só fica sentado pela casa! Spiridon, volta pra cozinha!]],
				['15'] = [[Ora, me desculpe. A minha Lyuba, ela não é um doce maravilhoso, mas só existe uma como ela, e tem abelhas demais. Não vou para Ufa, não mesmo!]],
				['16'] = [[Anda, rápido. Não sabe o que a minha esposa vai fazer comigo?]]
			},
			['choice'] = {
				['28'] = [[Sim, tome aqui. O que concordamos e os juros.]],
				['27'] = [[Não, Spiridon, desculpe. Ainda não consegui. Você sabe que não é fácil encontrar essas coisas pela estrada.]],
				['9'] = [[Ei, não bata nele. É a verdade! Estou aqui a negócios. Preciso de ajuda!]],
				['26'] = [[Olá.]],
				['25'] = [[Está bem, está bem. Não vou contar nada a ela.]],
				['6'] = [[Espere, acho que me confundiu com alguém. Eu vim falar com seu marido.]],
				['5'] = [[Isso foi há muito tempo, camarada. Idade, bebida...]],
				['4'] = [[Boris, o major de Stepnoy. Estávamos conversando sobre Kugel, sobre se o engenheiro que tinha reconhecimento do comitê regional seria capaz de construir um tanque antimísseis. E o Boris disse que você perdeu o jeito há muito tempo.]],
				['3'] = [[Que não há motivo para matar os engenheiros inteligentes dos quais o país precisa desesperadamente. É uma pena. Vou ter que me virar.]],
				['2'] = [[(Manipular)<new_line>Que pena. Acho que o Boris tinha razão. Você é só um engenheiro velho mesmo.]],
				['1'] = [[E se eu te conseguir umas vodcas? Coisa boa, de destilaria.]],
				['23'] = [[Tome, três garrafas, como prometido.]],
				['24'] = [[Quatro? Caramba. Eu trago elas logo que puder.]],
				['21'] = [[Eu falo.]],
				['13'] = [[Não sei do que está falando. Eu preciso mesmo da sua ajuda. Preciso encontrar e montar algo... em Ufa.]],
				['22'] = [[Escute, eu sinto muito. Não tenho nada comigo. Eu trago da próxima vez.]],
				['15'] = [[Feito!]],
				['16'] = [[Para Ufa? Está bem, o que quiser.]],
				['17'] = [[Vai com calma.]],
				['18'] = [[Vamos, vamos.]],
				['19'] = [[Obrigado, você me ajudou muito.]],
				['14'] = [[E onde eu vou encontrar outro engenheiro?]],
				['20'] = [[Obrigado, você me ajudou muito.]]
			},
			['name'] = [[Spiridon Kugel]]
		},
		['varya'] = {
			['episode'] = {
				['4'] = [[(Varya deu um passo incerto e tropeçou. Ela se segurou na parede.)<new_line><new_line>Desculpe, mas as minhas pernas não estão cooperando. O que vamos fazer? Será que... você pode me carregar? É só por um tempinho, até as minhas pernas voltarem ao normal.]],
				['3'] = [[Você matou eles? (Ela ficou boquiaberta.) Sozinho? Contra todos eles? Não pode ser! Como? Ah, meu nome é Varya.]],
				['2'] = [[(Primeiro a garota me olhou com raiva, mas a sua expressão logo mudou.)<new_line><new_line>Ei! Eu não reconheço você! Você não é um deles?]],
				['1'] = [[(O celeiro não tem luz além dos feixes de luz do Sol que passam pelos buracos do telhado. As tábuas do chão chiaram enquanto eu andava.<new_line>A prisioneira estava encolhida em um canto escuro.)]],
				['11'] = [[Entediada e não tendo o que fazer... É, eu não quero ter que fazer nada mesmo. Ei, os seus cartuchos são de qual calibre? São dos grandes como os do meu tio ou são menores?]],
				['10'] = [[Pra casa da minha tia? Afe... Tá. Ei, você pode me ensinar a atirar?]],
				['9'] = [[(Confesso que quase deixei Varya com o choque.)<new_line><new_line>É, e? Você é forte, corajoso, pode me proteger. Tipo, você arriscou a vida pra me salvar. Agora eu tenho que devolver o favor! E o que eu vou fazer na casa da minha tia? Vou ficar entediada!]],
				['8'] = [[Claro, mas quer saber? Eu não quero voltar para a minha tia. Talvez eu possa viajar com você?]],
				['7'] = [[Eu disse que ela estava com inveja de mim.]],
				['6'] = [[(Enquanto andávamos em direção à floresta, Varya contou tudo que passou nas mãos dos bandidos. Depois, ela contou a história terrível do seu sequestro e me contou sobre o terror que era a sua tia, com quem ela vivia em uma vila perto de Bryansk. Eu mal prestei atenção. Estava completamente focado nos meus pensamentos.)]],
				['5'] = [[Veja você mesmo.]]
			},
			['choice'] = {
				['4'] = [[Hã... você consegue mesmo andar sozinha?]],
				['3'] = [[Prazer em conhecê-la, Varya. Eu conto tudo no caminho. Você acha que consegue andar?]],
				['2'] = [[Não, não sou um deles. E eles não voltarão mais. Eu matei todos. Levante-se. Precisamos sair daqui. Você consegue andar?]],
				['1'] = [[(Soltar a garota)]],
				['10'] = [[(Encerrar a conversa educadamente)]],
				['9'] = [[Hã... Vamos fazer o seguinte: eu te levo de volta para a sua tia, e vou fazer umas coisas por um tempo. Você vai poder repensar o que quer fazer para me agradecer. Tá bom?]],
				['8'] = [[Comigo?!]],
				['7'] = [[Não, antes. Tem um assentamento de sobreviventes nos arredores de Bryansk? Pode me mostrar o caminho?]],
				['6'] = [[Oi, o que você disse?]],
				['5'] = [[(Levantar Varya e carregá-la para fora do celeiro)]]
			},
			['name'] = [[Varya]]
		},
		['zaycev'] = {
			['episode'] = {
				['9'] = [[Sim. Lançamos todo o nosso arsenal imediatamente em todos os alvos. Mesmo que fadado ao fim, o nosso país não queria morrer. A onda de refugiados infectados se aproveitou do caos e foi demais para as forças aliadas. E foi assim que o R17 se alastrou pelo resto do mundo.]],
				['8'] = {
					['2'] = [[Uma sessão secreta do Clube Nuclear foi realizada no fim de outubro, e a medida de purgação do 'território infectado' foi aprovada. Eles usaram todas as ogivas que tinham. Até mesmo pequenas aldeias foram 'purgadas'. Mas eles erraram de novo.]],
					['1'] = [[Havia. Para cada pessoa morta, duas mais chegava. E a fronteira é vasta, então as pessoas espertas ou desesperadas davam algum jeito. E, quando os primeiros surtos da epidemia aconteceram na China, Mongólia, Turquia, Polônia e Finlândia, foi decidido que medidas extremas deveriam ser tomadas.]]
				},
				['7'] = [[Mas é claro, e quase todos foram infectados. Nossos vizinhos fecharam suas fronteiras, declararam lei marcial, juntaram suas tropas e mataram milhares de refugiados.]],
				['6'] = [[Correto. E que, depois de um pequeno derramamento de sangue, eles teriam acesso às ricas e vastas terras da Mãe Rússia. Mas o R17 os traiu. A epidemia se alastrou devagar, o período de incubação era muito longo, e muitas pessoas eram imunes. E, em outubro, centenas de milhares de pessoas se dirigiam às fronteiras da União Soviética.]],
				['5'] = [[Está bem, está bem. Mas é tudo muito simples. As agências de inteligência americana claramente sobrestimaram a eficácia da sua arma, o R17. Ou talvez tenham subestimado. Talvez tivessem achado que as pessoas da grande Nação Soviética teriam sido quase extintas, ficado desesperadas e enfraquecidas pela doença...]],
				['4'] = [[(O diretor me olha meio confuso, acena com a cabeça e começa a limpar os óculos.)]],
				['3'] = [[Sim, foi assim que a pandemia começou e acabou com toda a população da União Soviética.<new_line>Satisfeito?]],
				['2'] = [[(Zaitsev virou a presa nas mãos e olhou para mim. Ele parecia um pouco incerto.)<new_line><new_line>Onde está o Pavel?]],
