			['choice'] = {
				['1'] = [[Colocar o presente]]
			}
		},
		['gravedigger'] = {
			['episode'] = {
				['2'] = [[O coveiro não queria falar. Encontrei uma nota estranha no manto dele, parecia uma lista de compras para partes corpo: braços, pernas, cabeças. No final, havia uma assinatura: ]],
				['1'] = [[Não foi difícil encontrar o coveiro; o homem de aparência estranha me encontrou no portão do cemitério. Na mão direita, ele carregava uma pá, e na esquerda — uma perna podre. Ele me abriu um sorriso medonho, jogou o pé numa carroça próxima cheia de outras partes de corpos, colocou a pá por cima do ombro e correu cemitério adentro. É hora de cuidar dele!]]
			}
		},
		['emba_search_easy3'] = {
			['choice'] = {
				['5'] = [[Colocar fogo]],
				['1'] = [[Tentar tirar o mato]]
			},
			['episode'] = {
				['3'] = [[Bom, pelo menos isso vai retardar o desenvolvimento e reduzir a fonte de alimentos dos biomutantes.<new_line>Preciso contar aos cientistas sobre esse lugar, com certeza eles vão querer estudar o desenvolvimento das plantas depois desse tipo de estresse.]],
				['2'] = [[Não consegui arrancar os brotos: apesar de ainda serem muito pequenos, as raízes eram fundas demais.<new_line>Só me resta tacar fogo nesse mato.]],
				['1'] = [[Notei uns brotos incomuns no chão. Era, na verdade, o desenvolvimento da nova planta! Os biomutantes se alimentam disso. Como se já não houvesse problemas suficientes...]]
			}
		},
		['new_year_boss_5_4'] = {
			['episode'] = {
				['4'] = [["Ah, que seja. Vou ficar preso aqui com vocês de qualquer jeito... Posso sugerir um plano idiota também.<new_line>E se redirecionarmos o seu portal direto para o esconderijo do Frosty?"]],
				['3'] = [["Que outra escolha temos? Nossas contas já estão com saldo negativo!"]],
				['2'] = [["Nós gastamos quase toda a nossa energia naquele vácuo! Cair direto na armadilha de Frosty não é apenas loucura — é suicídio."]],
				['1'] = [["Sinto o traço da magia que foi roubada de nós... Frosty se escondeu em algum lugar próximo e está a nossa espera para contra-atacar."]],
				['6'] = [["Como se houvesse outra opção. Certo, garoto, cabeça erguida. Acho que você tem uma pequena chance. Decore a árvore uma última vez enquanto eu preparo tudo."]],
				['5'] = [["É, aquele onde você esteve esse tempo todo, gênio. Não temos energia para mais do que isso.<new_line>E aí, quando você estiver no esconderijo de Frosty, você só terá a si mesmo em quem confiar."]]
