			['choice'] = {
				['4'] = [["Deixe-o em paz!"]],
				['3'] = [[Pergunte ao ancião o que está acontecendo]],
				['2'] = [["Ei, parem com isso!"]],
				['1'] = [["O que..."]],
				['8'] = [["É o que aquele velho está fazendo!"]],
				['7'] = [["É tóxica?"]],
				['6'] = [["O que você fez com ele?!"]],
				['5'] = [[Salte para sair da fumaça]]
			}
