		['embacity_biolab_3'] = {
			['choice'] = {
				['4'] = [["General Kurganov? O que está fazendo, recupere o juízo!"]],
				['3'] = [["Estou vendo."]],
				['2'] = [["Sim, é estranho. Pra onde agora?"]],
				['1'] = [["Bem, vamos descer?"]],
				['10'] = [[Pegue a caixa preta e vá buscar ajuda]],
				['9'] = [[Pegue a caixa preta e saia]],
				['8'] = [["A gente precisa correr e conseguir ajuda pra você e essas pessoas."]],
				['7'] = [[Mencione Kurganov]],
				['6'] = [["Você ficou louco, e eu vou te impedir!"]],
				['5'] = [["Com Gerin? O que você fez com o professor?"]]
			},
			['episode'] = {
				['9'] = [["Aaah!"<new_line><new_line>(Algo explodiu com um estrondo e me jogou contra a parede. Bati a cabeça, e a minha visão escureceu)]],
				['8'] = [["O compartimento com a caixa preta fica atrás dela. Digite o código que os cientistas nos deram, pegue a caixa e vamos embora antes que aconteça alguma coisa.<new_line>Vou ficar de olho e cuidar do sistema eletrônico. Talvez eu consiga descobrir o que está acontecendo aqui."]],
				['7'] = [["Esse salão é o próximo. Uau, olha que raiz poderosa está crescendo ali! Aposto que é tão grossa quanto a cabeça do nosso amigo Isaev.<new_line>Droga, e também é oca... deu de piadas. Eu não gosto nada disso, melhor não demorar. Ali, viu aquela porta que precisa de um código?"]],
				['6'] = [["Eles construíram uma mansão inteira no subsolo! Viu os canos no teto? É assim que a biomassa desce até aqui. Mas por que não tem ninguém aqui...<new_line>Se não fosse o Quebrador de Ossos que comeu a biomassa, quem foi? E aquela porta se abriu sozinha?"]],
				['5'] = [["Venha, vamos nessa."]],
				['4'] = [[No meio do salão, cercada de humanoides mutantes, uma figura medonha estava curvada sobre uma mesa de laboratório.<new_line>Da escuridão da escada, eu via os corpos mutilados de micélio e ouvia seus grunhidos animalescos. Ninguém merece um destino desses.]],
				['3'] = [[A escada atrás da porta levava a uma espaçosa sala subterrânea. Acima, restos de biomassa verde fluíam de canos para valas de metal, ao passo que raízes nodosas se estendiam do chão ao teto, abrindo caminho até a superfície da cidade.]],
				['2'] = [["Ah, aí vem o encanador-chefe da Progresso! Quando você tirou toda a biomassa do tanque, sob aquela luz vermelha ali, a porta abriu, viu?<new_line>Tem uma escada atrás dela. Não sei como ela é conectada. E talvez ainda tenha algum monstro sentado atrás daquela porta, e você o deixou sem os doces."]],
				['1'] = [[Espreitei o laboratório com cuidado. A primeira coisa que chamou a minha atenção foi a luz vermelha piscando acima da porta do outro lado da sala.<new_line>Uma porta automática enorme, ligeiramente entreaberta.]],
				['12'] = [["Ainda não pretendo discutir as minhas decisões com algum soldado raso! Você vai descobrir quando se juntar ao meu exército.<new_line>Seu amigo curioso já encontrou meus explosivos e não serve pra mais nada. Mas de você eu vou cuidar. Para a sala de cirurgia."]],
				['11'] = [["Recuperar o juízo? Do que está falando? Falta tão pouco, em breve vamos dar um jeito em tudo. E vamos começar com você. Aprendi um método maravilhoso com um dos meus cientistas..."]],
				['14'] = [["Isso mesmo, combatente. Parece que minha missão termina aqui. O que aconteceu depois da explosão?"]],
				['13'] = [["Você disse que ele falou do professor? Ao que tudo indica, eles se conheceram depois da catástrofe. Queria saber qual é o papel do Gerin nisso tudo..."]],
				['17'] = [["Você não faz ideia do quanto todos estamos em dívida com você. Termine o trabalho. Foi um prazer percorrer essa estrada ao seu lado, combatente."]],
				['10'] = [["Quem está se infiltrando numa instalação militar? Um novo combatente pro meu exército? Em formação!"]],
				['15'] = [["Você compreende o poder que eu tenho nas mãos? Eu vou reconstruir o nosso país das cinzas!<new_line>E então vou espalhar uma nova praga pelo mundo. Mas você não estará aqui pra ver. A pena pra violar uma ordem é a morte."]],
				['16'] = [["A julgar pelo que você diz, essa deve ser a primeira expedição perdida da Progresso. Eles estão a salvo, Isaev não vai se esquecer disso... É tarde demais pra mim. Sabe, foi bom te conhecer."]]
			}
