		['construction_team'] = {
			['choice'] = {
				['4'] = [[Entregue os cigarros]],
				['3'] = [["Qual é o trabalho?"]],
				['2'] = [[Procure cigarros]],
				['1'] = [["Sério, que tipo de trabalho?"]],
