	['barQuest'] = {
		['bandit_camp'] = {
			['text'] = [[„Die Räuber haben sich in letzter Zeit wirklich ausgetobt. Sie haben direkt vor unserer Nase ein Lager aufgeschlagen. Wirst du mit ihnen fertig?“]],
			['step'] = {
				['6'] = [[Ich konnte mit den Räubern verhandeln. Sie werden uns nicht mehr in die Quere kommen.]],
				['5'] = [[Ich habe alle Räuber getötet.]],
				['4'] = [[Jetzt, da ich mich um die Räuber gekümmert habe, muss ich zur Basis zurückkehren.]],
				['3'] = [[Die Räuber sollten jetzt weg sein. Schauen wir uns das Lager an, um sicherzugehen.]],
				['2'] = [[Ein Handel ist ein Handel. Ich muss den Räubern Zeit zum Abzug lassen.]],
				['1'] = [[Der Oberwächter hat mir gesagt, wo das Banditenlager ist. Ich muss dorthin gehen.]]
			},
			['name'] = [[Räuber-Lager]]
		},
		['reinforcement'] = {
			['text'] = [[„Wir haben eine dringende Meldung bekommen: Eine Einheit aus unserer Basis bittet um Beistand. Geh und hilf ihnen.“]],
			['step'] = {
				['3'] = [[Wir haben zusammengearbeitet und konnten sie besiegen.]],
				['2'] = [[Der Kampf ist vorüber. Ich kann zur Basis zurückkehren und meine Belohnung abholen.]],
				['1'] = [[Ich muss mich beeilen und unseren Männern Beistand leisten. Wir wissen nicht, wie lange sie ohne Verstärkung noch durchhalten.]],
				['4'] = [[Wir haben sie vertrieben, aber nicht ohne Opfer.]]
			},
			['name'] = [[Beistand]]
		},
		['halloween_boss_2022_1'] = {
			['text'] = [[Kultisten beschwören den Dämon Balthasar, um ihm zu dienen. Er belohnt sie mit Geschenken, die so mächtig sind, dass ein Mensch, der sie alle in sich aufgenommen hat, den Dämon selbst herausfordern könnte.]],
			['step'] = {
				['3'] = [[Ich werde eine Verkleidung brauchen, um hineinzukommen. Ich könnte eine Robe machen, wenn ich die richtigen Materialien hätte.]],
				['7'] = [[Ich muss diesen Dämon und seinen Kult vernichten.]],
				['1'] = [[Die Kultisten versammeln sich in einer verlassenen Scheune irgendwo in der Wildnis. Ich muss einen Weg dorthin finden.]],
				['5'] = [[Die Kultisten verlangen eine Spende, bevor ich eintreten kann. Ich muss ihnen entweder eine Flasche Wodka oder eine handgemachte Kerze geben. Der Türsteher ist auf jeden Fall bereit, sich bestechen zu lassen.]]
			},
			['name'] = [[Dämon Balthasar]]
		},
		['camp_defence'] = {
			['text'] = [[„Die Räuber haben sich versammelt und werden unser Lager angreifen. Tu mir einen Gefallen und hilf bei der Verteidigung.“]],
			['step'] = {
				['6'] = [[Als ich dorthin kam, war es bereits zu spät.]],
				['5'] = [[Wir haben gewonnen, aber wir haben auch ein paar Leute verloren.]],
				['4'] = [[Okay, wir haben gewonnen, aber es gab trotzdem Opfer.]],
				['3'] = [[Wir konnten die Räuber leicht überwältigen und das Lager verteidigen.]],
				['2'] = [[Ich habe getan, was ich konnte. Jetzt muss ich zur Basis zurück und melden, was im Lager passiert ist.]],
				['1'] = [[Ich muss mich so schnell wie möglich ins Lager unserer Verbündeten aufmachen und ihnen bei der Verteidigung helfen.]]
			},
			['name'] = [[Lagerverteidigung]]
		},
		['impostor'] = {
			['text'] = [[Ich habe Berichte über einen Hochstapler gehört. Ein Bandit gibt sich als Ded Moroz aus! Er raubt die Leute aus und ruiniert unser Ansehen. Du musst ihm das Handwerk legen.]],
			['step'] = {
				['3'] = [[Der Sack von Loot ist mit mir. Ich muss es den Menschen zurück.]],
				['2'] = [[Jetzt weiß ich, wo die Betrüger zu finden, ist es Zeit, ihm einen Besuch zu zahlen.]],
				['1'] = [[Ein Hochstapler ist aufgetaucht, gibt sich als Ded Moroz aus und beraubt die Leute. Er muss gestoppt werden.]]
			},
			['name'] = [[Hochstapler]]
		},
		['lost_soul'] = {
			['text'] = [[Irgendwo im Wald ist ein Mann in Schwierigkeiten. Finde sein Lager und folge seinen Spuren. Vermutlich braucht er Hilfe.]],
			['step'] = {
				['1'] = [[Irgendwo im Wald braucht jemand Hilfe. Ich muss mich beeilen.]]
			},
			['name'] = [[Verlorene Seele]]
		},
		['construction_team'] = {
			['text'] = [[Wir haben nicht genug Arbeiter auf der Baustelle. Leiste deinen Beitrag zum Gemeinwohl und wir werden dich belohnen.]],
			['step'] = {
				['14'] = [[Bring eine Ladung Vorräte zurück.]],
				['3'] = [[Auf der Baustelle gibt es für mich noch einiges zu tun. Ich muss wieder zu Kräften kommen und zum Vorarbeiter zurückkehren.]],
				['2'] = [[Der Vorarbeiter bat um Hilfe auf der Baustelle.]],
				['1'] = [[Der Quartiermeister schickte mich, um den Arbeitern auf einer Baustelle in der Nähe der Siedlung zu helfen.]],
				['6'] = [[Beseitige das Ungeziefernest.]],
				['4'] = [[Teile Zigaretten mit den Arbeitern.]],
				['12'] = [[Finde heraus, was an einigen Orten rund um den Außenposten vor sich geht.]],
				['10'] = [[Kümmere dich um die Räuberüberfälle.]],
				['8'] = [[Beseitige das Mutantennest.]]
			},
			['name'] = [[Bau-Brigade]]
		},
		['help_hunter'] = {
			['text'] = [[Ein Jäger steckt in Schwierigkeiten. Eile ihm zu Hilfe. Ich markiere dir den Ort auf der Karte.]],
			['step'] = {
				['1'] = [[Ich muss einem weiteren Jäger helfen.]]
			},
			['name'] = [[Hilf dem Jäger]]
		},
		['witch_delivery'] = {
			['text'] = [[Ich brauche deine Hilfe, um eine Kiste mit Zutaten an eine örtliche Hexe zu liefern und ein paar Tränke bei ihr abzuholen.]],
			['step'] = {
				['1'] = [[Ich muss eine Kiste mit Zutaten an eine Hexe liefern, ein paar Tränke bei ihr abzuholen und damit zum Portal zurückkehren.]],
				['2'] = [[Ich habe die Tränke. Jetzt muss ich sie zu dem Barkeeper geben.]]
			},
			['name'] = [[Lieferung an die Hexe]]
		},
		['snowmen'] = {
			['step'] = {
				['1'] = [[Ich muss hingehen und ein paar Schneemänner umwerfen.]]
			},
			['text'] = [[Diese bösen Schneemänner machen Ärger und greifen die Leute an. Hau sie um. Sollen sie daliegen und über ihr Benehmen nachdenken. Ded Moroz und ich stellen sie später wieder auf.]]
		},
		['new_year_boss_2022_3'] = {
			['step'] = {
				['1'] = [[Es gab einen großen Fehler im Vergnügungspark. Aber wir können immer noch mit den Vertretern von Krampusbande verhandeln.]]
			}
		},
		['order'] = {
			['text_barman'] = [[„Wir müssen die Küche auffüllen. Bringst du mir ein paar Lebensmittel?“]],
			['step'] = {
				['3'] = [[Ich habe alle notwendigen Vorräte abgeliefert.]],
				['2'] = [[Ich habe alles zusammengetragen, was sie wollten. Es wird Zeit, zur Siedlung zurückzukehren.]],
				['1'] = [[Ich habe mich bereit erklärt, den Auftrag für die Siedlung zu übernehmen. Ich muss die wichtigsten Vorräte für sie besorgen.]]
			},
			['name'] = [[Auftrag]],
			['text'] = [[„Ich habe einen neuen Auftrag für ein paar Vorräte. Es besteht aber keine Eile. Kannst du alles für mich besorgen? Hier ist die Liste.“]],
			['text_workshop'] = [[„Ich habe nicht genug von einigen Vorräten, um meine Arbeit zu beenden. Kannst du sie besorgen?“]],
			['text_nurse'] = [[„Ein Teil der Medizin geht zur Neige. Aber die Menschen werden immer noch krank. Kannst du helfen, mehr zu besorgen?“]]
		},
		['progress_rostov'] = {
			['step'] = {
				['1'] = [[Als sich die Steppen um den Don in Ödland verwandelten, versammelte der Ataman alle Überlebenden unter seiner Kontrolle. Eine Gruppe von Abenteurern scharte sich um ihn – einige hatten kosakische Vorfahren, andere waren einfach von der Romantik der Freiheit ergriffen.<new_line><new_line>Und so erwachte die Steppe wieder zum Leben, zum Kosakenleben. Zumindest in der Interpretation der Atamanen. Unter dem Schutz der Druschina konnten die einfachen Leute wieder Landwirtschaft betreiben, und schließlich wurde ganz Rostow vor dem Verhungern gerettet.]]
			}
		},
		['leprechaun_debts'] = {
			['text'] = [[Die Leprechauns schulden mir einen Haufen Gold für Getränke. Folge dem Regenbogen, um sie zu finden. Treib mein Gold bei ihnen ein und bring es mir. Versuch aber nicht, es selbst auszugeben – es ist nämlich verflucht.]],
			['step'] = {
				['1'] = [[Ich habe den Auftrag erhalten, Gold von verschuldeten Leprechauns einzutreiben.]]
			},
			['name'] = [[Verschuldete Leprechauns]]
		},
		['progress_arzamas'] = {
			['step'] = {
				['1'] = [[Ein Mann wanderte viele Jahre lang durch das Ödland. Schließlich, als die Fesseln seines weltlichen Lebens abgefallen waren, erschien vor ihm ein brennender Busch mit heiligen Kräutern. Er hüllte den Mann in Rauch, nannte ihn den Propheten und befahl ihm, ein neues Paradies zu schaffen.<new_line><new_line>Das erzählen sich die Bewohner des alten Klosters in Arzamas. Niemand weiß, was sie dort tatsächlich treiben. Ein paar Mitglieder der Gemeinschaft haben Kontakt zur Außenwelt, und es gibt einige Lieferungen nach Rjasan, aber das ist alles, was man weiß.]]
			}
		},
		['stash_polar_fox'] = {
			['step'] = {
				['1'] = [[Der Polarfuchs hat in der Nähe etwas Interessantes gefunden. Eine Untersuchung lohnt sich.]]
			}
		},
		['lair'] = {
			['step'] = {
				['1'] = [[Ich muss das Nest der Kreatur finden und – sollte sie aggressiv erscheinen – sie töten.]]
			},
			['text'] = [[Mir wurde berichtet, dass eine Art Bestie ihr Nest in der Nähe gebaut hat. Du musst dich darum kümmern.]]
		},
		['gift_delivery'] = {
			['text'] = [[Die Geschenke müssen zugestellt werden, komme, was wolle. Nimm die Kartons und gib sie den Leuten. Jetzt brauchen sie mehr denn je Unterstützung.]],
			['step'] = {
				['1'] = [[Ich soll ein paar Geschenke ausliefern.]]
			},
			['name'] = [[Geschenkzustellung]]
		},
		['progress_tver'] = {
			['step'] = {
				['1'] = [[Die Menschen kamen zu der verlassenen Flussstation und hauchten ihr neues Leben ein. Jetzt befahren Boote aus Twer den Fluss nach Norden und Süden, während man in dem lärmenden Bahnhofsgebäude seltene Gegenstände kaufen kann, die unter großem Risiko aus Moskau beschafft oder in Rjasan gehandelt werden.<new_line><new_line>Die Bewohner der Siedlung sind mit ihren eigenen Angelegenheiten beschäftigt, nehmen aber gerne einen wandernden Reisenden in ihr geschäftiges Leben mit seinem unverwechselbaren Geruch nach Fisch und Teer auf. Nur die extrem faulen sind nicht willkommen.]]
			}
		},
		['secret_documents'] = {
			['text'] = [[„Kamerad, deine Bemühungen waren nicht umsonst! Endlich haben wir herausgefunden, wo die Geheimdokumente sind, die mit der nächstgelegenen Biostation zu tun haben! Mach dich auf den Weg zum Verlassenen Labor, dort sollten die Dokumente sein.“]],
			['step'] = {
				['1'] = [[Ich muss das Verlassene Labor untersuchen. Dort befinden sich die Geheimdokumente, die der Biologe für seine Forschung braucht.]],
				['2'] = [[Ich habe den Weg für mich selbst freigemacht. Jetzt muss ich nur noch den Safe knacken und die Geheimdokumente mitnehmen.]]
			},
			['name'] = [[Geheimdokumente]]
		},
		['forest_feeder'] = {
			['text'] = [[Die Tiere waren nicht auf diesen Winter gefasst. Es ist unsere Pflicht, ihnen zu helfen. Ich habe im Wald Futterstationen aufgehängt. Es ist Zeit, sie aufzufüllen.]],
			['step'] = {
				['1'] = [[Ich soll ein paar Futterstationen in verschiedenen Bereichen des Waldes auffüllen.]]
			},
			['name'] = [[Waldfütterung]]
		},
		['new_year_boss_2022_4'] = {
			['step'] = {
				['3'] = [[Ich bin in Cybersantas verwirrende Eskapade verwickelt worden. Jetzt muss ich irgendwo ein Kabel anschließen und auf seinen Anruf warten.]],
				['2'] = [[Cybersanta wird uns helfen, etwas über die Superwaffe herauszufinden. Ich muss in den Ort einbrechen, wo die Informationen darüber gespeichert sind.]],
				['1'] = [[Wir müssen Cybersanta ausnüchtern. Er braucht Wasser.]],
				['4'] = [[Ich denke, die Mission ist abgeschlossen. Ich muss Cybersanta einholen und herausfinden, was er aufgedeckt hat.]]
			}
		},
		['halloween_boss_2022_6'] = {
			['text'] = [[Ein sprechender Esel terrorisiert die Bevölkerung: Er frisst ihre Ernte, versucht, gefälschte Münzen zu verkaufen, und hat bereits drei Bauern zu Tode geredet.<new_line>Die Dorfbewohner hätten ihn längst mit einer Mistgabel zur Rechenschaft gezogen, aber der Esel wird von einem Wesen beschützt, das im nahen Sumpf lebt.]],
			['step'] = {
				['3'] = [[Der Sensenmann hat mir einen Vertrag aufgedrückt, und jetzt will er mir meine Seele nehmen. Ich habe herausgefunden, dass mein Gigawattor seine Portale schließen kann.<new_line>Ich muss den Sensenmann im Kampf besiegen.]],
				['2'] = [[Der Esel konnte entkommen, und das Sumpfbiest, das ihn beschützt, könnte auftauchen. Schnell, wir haben nicht viel Zeit!]],
				['1'] = [[Der Esel versteckt sich im Sumpf. Ich muss ihn aufspüren, am besten ohne der Bestie zu begegnen, die über ihn wacht.]]
			},
			['name'] = [[Sprechender Esel]]
		},
		['progress_olonec'] = {
			['step'] = {
				['1'] = [[Die erste bewohnte Siedlung auf meiner Reise. Es ist sehr nervenaufreibend, sich wieder unter Menschen zu befinden. Hier haben sich alle daran gewöhnt, sich aufeinander und auf ihre eigenen Ressourcen zu verlassen, um mit Widrigkeiten fertig zu werden. Hier leben sie wahrscheinlich noch genau wie vor einem Jahrhundert.<new_line><new_line>Die Fischer fahren nicht weiter als bis zum Ladogasee, und ich bezweifle, dass ich hier Antworten auf alle meine Fragen finden werde, aber es ist ein guter Ort, um Neuigkeiten zu erfahren und sich auf Reisen in die Ferne vorzubereiten.]]
			}
		},
		['halloween_boss_2022_5'] = {
			['step'] = {
				['3'] = [[Ich habe Igor und Frankensteins Monster aufgespürt. Ich muss sie im Kampf besiegen.]],
				['2'] = [[Ich muss zum nächsten Ort auf der Seite aus dem Buch des Todes gehen.]],
				['1'] = [[Der Sensenmann gab mir Aufzeichnungen aus dem Buch des Todes über diejenigen, die in dem Gebiet sterben werden, in dem Igor und das Monster zuletzt getötet haben. Ich muss alle auf der Seite, die er herausgerissen hat, überprüfen – einer von ihnen könnte das nächste Opfer sein.]]
			},
			['text'] = [[Der Wissenschaftler Viktor Frankenstein baute ein Monster aus den Körperteilen anderer Menschen zusammen und erweckte es mit Hilfe von Elektrizität zum Leben.<new_line>Frankensteins Monster und sein buckliger Kumpan Igor sind erst vor kurzem in diese Welt gekommen, aber sie haben eine blutige Spur von grausamen Morden hinterlassen: Es gab zahlreiche Fälle von Leichen mit herausgeschnittenen Organen.]]
		},
		['snowman_giant'] = {
			['text'] = [[Ein Riesenschneemann ist am Horizont aufgetaucht. Er muss aufgehalten werden, ehe er die Siedlungen erreicht und Unheil stiftet. Wir treffen uns dort, ich muss dir noch etwas sagen.]],
			['name'] = [[Der Schneemannkönig]]
		},
		['intelligence'] = {
			['text'] = [[„Wir haben eine Militärbasis entdeckt, die mit dem EMBA-Projekt verbunden ist. Sie sollte Daten über die geheimen Einrichtungen in dieser Region haben. Mit diesen Daten können wir vielleicht herausfinden, wo sich die Geheimdokumente für das Projekt befinden.“]],
			['step'] = {
				['1'] = [[Die Wissenschaftler von Progress haben mich beauftragt, die Aufklärungsdaten aus der Militärbasis zu beschaffen.]],
				['2'] = [[Ich habe die Aufklärungsdaten und es ist Zeit, zur Siedlung zurückzukehren.]]
			},
			['name'] = [[Aufklärungsdaten]]
		},
		['progress_leningrad'] = {
			['step'] = {
				['1'] = [[Das verlassene Touristenlager befindet sich außerhalb der strahlenverseuchten Wälder und Ödlande rund um Leningrad. Einige Wagemutige aus Tver haben hier einen Stützpunkt eingerichtet.<new_line><new_line>Die „Touristen“ (wie die Veteranen der Gefahrenzone genannt werden) starten Beutezüge nach Leningrad, bringen sie zurück zum Stützpunkt und schicken sie dann den Fluss hinunter nach Tver.]]
			}
		},
		['order_delivery'] = {
			['text'] = [[„Wir müssen Frachtgut aus einer anderen Basis holen. Kannst du das übernehmen?“]],
			['step'] = {
				['6'] = [[Der Kunde hat sich geweigert, das Frachtgut zu kaufen, jetzt bleibe ich darauf sitzen.]],
				['5'] = [[Ich habe es nicht geschafft, das Frachtgut abzuliefern.]],
				['4'] = [[Ich habe das Frachtgut abgeliefert und wurde vollständig bezahlt.]],
				['3'] = [[Das Frachtgut wurde vollständig abgeliefert.]],
				['2'] = [[Ich habe das Frachtgut. Auf, zurück zur Basis.]],
				['1'] = [[Ich muss zur Basis gehen und das Frachtgut abholen.]]
			},
			['name'] = [[Lieferung]]
		},
		['new_year_tree_quest'] = {
			['text'] = [[„Im Winter sollte jedes Haus einen Weihnachtsbaum haben! Die Feiertage sind eines der wenigen Vergnügen, die wir hier noch haben. Geh die Holzfäller besuchen, sie haben ein paar Dinge für uns beiseite gelegt.“]],
			['step'] = {
				['1'] = [[Ich muss eine Ladung Weihnachtsbäume in der Siedlung abholen und abliefern. Das sollte die Stimmung in diesem strengen Winter aufhellen.]],
				['2'] = [[Ich habe die Bäume, aber jetzt muss ich sie zur Siedlung bringen.]]
			},
			['name'] = [[Den Tannenbaum abliefern]]
		},
		['new_year_deliver_cloth'] = {
			['text'] = [[Die Kinder stricken warme Mützen und Socken für die Menschen, die in der Kälte außerhalb der Siedlung arbeiten. Kannst du ihnen ein paar Pakete bringen?]],
			['step'] = {
				['1'] = [[Ich habe beschlossen, den Leuten, die außerhalb der Siedlung arbeiten, warme Kleidung zukommen zu lassen.]],
				['2'] = [[Ich muss die Sachen zu einer weiteren Gruppe von Siedlern bringen, die zum Geschäftemachen abgereist sind.]]
			},
			['name'] = [[Kleidung abliefern]]
		},
		['march_skeletons'] = {
			['text'] = [[Musstest du schon mal von den Toten wieder auferstehen? Ich brauche einige Skelette als Arbeiter. Nimm diese Schriftrolle – darauf findest du alle nötigen Informationen. Aber bring bloß nichts durcheinander.]],
			['step'] = {
				['1'] = [[Ich wurde gebeten, einige Skelette zu sammeln und mithilfe der Schriftrolle ein Ritual abzuhalten.<new_line><new_line>Sammle zehn Skelette und lege sie in einem Kreis aus, mit den Schädeln zur Mitte. Streue etwas Asche auf die Schädel während du dich dreimal westlich im Kreis läufst und den Geist von Tetriandok rufst. Wenn das Ritual beendet ist, werden die Skelette ihren Weg von allein finden.]]
			},
			['name'] = [[Marsch der Skelette]]
		},
		['pumpkin_seed'] = {
			['text'] = [[Wenn der Kopf die Wahrheit spricht, dann sind wir geliefert. Aber es gibt noch eine Chance, von der ich allerdings hoffte, sie niemals in Anspruch nehmen zu müssen. Nimm diesen Kürbiskern und pflanze ihn im Ödland ein.]],
			['step'] = {
				['1'] = [[Ich habe einen Samen bekommen, den ich im Ödland einpflanzen muss. Ich weiß weder, wovor der Tavernenwirt Angst hat, noch, wie uns das helfen soll. Er sieht doch ganz harmlos aus.]]
			},
			['name'] = [[Kürbiskern]]
		},
		['new_year_boss_2022_1'] = {
			['step'] = {
				['1'] = [[Neben dem Baum ist ein neuer Weihnachtsmann aufgetaucht. Ich muss herausfinden, was er zu bieten hat.]]
			}
		},
		['new_year_boss_2022_2'] = {
			['step'] = {
				['5'] = [[Wir sind dem verschwundenen Weihnachtsmann auf der Spur. Der Gauner, der aus dem Hinterhalt entkommen ist, könnte uns zu unserem Ziel führen. Wir sollten ihm besser folgen.]],
				['4'] = [[Wir haben herausgefunden, dass sich der Weihnachtsmann in den Krampusnachtsmann verwandelt hat und seine Bande nun alles zerstört, was mit Weihnachten zu tun hat. Wir müssen Geschäftsnachtsmann finden, um unsere nächsten Schritte zu besprechen.]],
				['3'] = [[Wir stellen eine Falle, und ich soll der Köder sein.]],
				['2'] = [[Ich muss das Loch von Höhlennachtsmann mit Gras verdecken.]],
				['1'] = [[Ich muss ein Loch graben. Höhlennachtsmann plant vielleicht, eine Falle aufzustellen.]]
			}
		},
		['progress_ryazan'] = {
			['step'] = {
				['1'] = [[„Man muss Rjasan einmal gesehen haben, bevor man stirbt“, lautet der dunkle Witz, der Hunderte von Kilometern um die Siedlung herum erzählt wird. Vor langer Zeit übernahm ein Räuberklan die Kontrolle über die Ölraffinerie und begann einen regen Handel mit Treibstoff gegen ihre eigene Währung, den Schwarzen Rubel. Bald wuchs rund um die Anlage eine ganze Marktstadt heran.<new_line><new_line>Nun ist dies das Zentrum des Handels in den Ödlanden. Aber die kriminelle Schattenseite ist nie weit von der Oberfläche entfernt: Hinter den glitzernden Schildern und dem Luxus der Kasinos lauern Betrug, Gewalt und Ausschweifungen.]]
			}
		},
		['new_year_boss_2022_6'] = {
			['step'] = {
				['1'] = [[Ich habe den bösen Frosti beseitigt. Aber gab es wirklich ein Weihnachtswunder? Ist der Weihnachtsmann wieder da?]],
				['2'] = [[In der letzten Schlacht spiele ich die Hauptrolle. Auf geht's!]]
			}
		},
		['progress_novosibirsk'] = {
			['step'] = {
				['1'] = [[Wenn man in Sibirien jemanden fragt, wo man sich am besten behandeln lassen kann, wird man nach Nowosibirsk verwiesen. Man sagt, das sei dem dortigen Forschungszentrum zu verdanken.<new_line><new_line>Natürlich gibt es alle möglichen Gerüchte ... So heißt es zum Beispiel, dass man noch jahrelang für die Behandlung bezahlen muss und froh sein kann, wenn einem eine normale Gelegenheitsarbeit reicht. Allerdings wird man nirgendwo sonst eine medizinische und pharmakologische Versorgung auf so hohem Niveau finden, was Nowosibirsk einen Ehrenplatz in der S.O.V.A. einräumt.]]
			}
		},
		['halloween_boss_2022_3'] = {
			['step'] = {
				['3'] = [[Ich fand mich auf der Speisekarte von Graf Dracula wieder. Er lud mich ein, mich zu entspannen und mich zu amüsieren – erst danach würde es ungemütlich werden.]],
				['2'] = [[Ich habe eine Überlebende des Vampirangriffs gefunden. Ich muss sie erst nach Hause begleiten und dann werde ich sie nach Einzelheiten zu dem Überfall fragen.]],
				['1'] = [[Es gibt Berichte, dass eine Gruppe von Reisenden von Vampiren angegriffen wurde. Ich muss zum Ort des Geschehens gehen.]]
			},
			['text'] = [[Graf Dracula ist ein uralter Vampir, ein Kunstkenner und ein Bewunderer der Gesellschaft von schönen Frauen. Die Leute sagen, er sei galant und höflich, aber das hält ihn nicht davon ab, Menschen zu jagen und sich an ihrem Blut zu laben.]]
		},
		['progress_sverdlovsk'] = {
			['step'] = {
				['1'] = [[Die Eiserne Front betrachtet Swerdlowsk als ihre Rüstungshauptstadt und als Quelle ihrer besten Truppen. Diese gut bewaffneten und hoch motivierten Kämpfer ziehen von hier aus in das Ödland.<new_line><new_line>Die Plünderer behaupten, dass sie nur gefährliche Räuber fangen und sie dann mit harter Arbeit in der Industrie umerziehen. In Wirklichkeit sind aber auch friedliche Einzelreisende in dieser Gegend in Gefahr.]]
			}
		},
		['provision'] = {
			['text'] = [[„Unsere Späher werden nach Einrichtungen suchen, die mit dem EMBA-Projekt verbunden sind. Hilf beim Sammeln von Vorräten für ihren Einsatz.“]],
			['step'] = {
				['1'] = [[Einer der Mitarbeiter von Progress bat mich, Vorräte für die Expedition zu sammeln.]],
				['2'] = [[Ich habe alles Nötige zusammengetragen und muss die Vorräte den Archivaren in der Siedlung geben.]]
			},
			['name'] = [[Vorräte]]
		},
		['progress_tula'] = {
			['step'] = {
				['1'] = [[Der Kreml von Tula empfängt den Wanderer mit einer Batterie moderner Geschütze, die zwischen den alten Zinnen der Festungsmauern hervorragen. Es wird nicht leicht sein, das Vertrauen der ehemaligen Arbeiter der Waffenfabrik zu gewinnen.<new_line><new_line>Es gibt mehrere Parteien, die gierig auf Tulas reiche Waffenbestände schielen, insbesondere die Banden, die Rjasan beherrschen. Die kämpferischen Tulaner halten sich jedoch aus den Geschäften und Bündnissen anderer Leute heraus und begnügen sich damit, Handel zu treiben und von ihren Mauern aus wachsam zu sein.]]
			}
		},
		['halloween_boss_2022_2'] = {
			['step'] = {
				['3'] = [[Ich muss den Pestarzt besiegen. Der Sensenmann hat angedeutet, dass der Dämonenmantel mich vor seinen Angriffen schützen würde.]],
				['2'] = [[Die Leute sagen, dass sich der Pestarzt auf dem örtlichen Friedhof niedergelassen hat, also muss ich dorthin gehen.]],
				['1'] = [[Eine kleine Gemeinde wird von Zombies heimgesucht. Das ist ein sicheres Zeichen dafür, dass sich der Pestarzt irgendwo in der Nähe niedergelassen hat. Ich muss diese Leute treffen und sie befragen.]]
			},
			['text'] = [[Unter dem Deckmantel giftiger Dämpfe führt der Pestarzt seine teuflischen Experimente durch: Er erweckt die Toten zum Leben und verbreitet Krankheiten unter den Lebenden. Die dämonische Rüstung kann vor den Flüchen des Pestarztes schützen, aber es ist nicht so einfach, ihn zu erreichen. Der gerissene Zaubertrankhersteller lässt sich von Horden von Zombies bewachen.]]
		},
		['scientist_rescue'] = {
			['text'] = [[Unser Wissenschaftler hat Schicht in der Öko-Station. Er hat uns kontaktiert, um zu sagen, dass er Probleme mit den örtlichen Räubern hat. Geh und bring ihn nach Hause.]],
			['step'] = {
				['3'] = [[Nimm den Räubern die Ausrüstung ab.]],
				['2'] = [[Ich habe den Wissenschaftler gefunden, jetzt muss ich ihn zurück in die Siedlung bringen.]],
				['1'] = [[Ich muss die Ökostation erreichen und den dortigen Wissenschaftler zurück in die Siedlung bringen.]],
				['7'] = [[Hilf dem Wissenschaftler, eine kleine Wunde zu versorgen.]],
				['6'] = [[Ich habe den Wissenschaftler gefunden, jetzt muss ich ihn zurück in die Siedlung bringen.<new_line><new_line>Vor der Abreise half ich dem Wissenschaftler, eine kleine Wunde zu versorgen.]],
				['5'] = [[Ich habe den Wissenschaftler gefunden und er wartet darauf, dass ich ihn zur Siedlung zurückbringe.]],
				['4'] = [[Ich habe den Wissenschaftler gefunden, jetzt muss ich ihn zurück in die Siedlung bringen.<new_line><new_line>Auf dem Rückweg sammelten wir die Ausrüstung ein, die dem Wissenschaftler gestohlen worden war.]],
				['9'] = [[Nimm Sie das verbliebene Gerät vom Dach.]],
				['8'] = [[Ich habe den Wissenschaftler gefunden, jetzt muss ich ihn zurück in die Siedlung bringen.<new_line><new_line>Vor der Abreise half ich dem Wissenschaftler, das verbleibende Gerät abzubauen.]]
			},
			['name'] = [[Bring den Wissenschaftler zurück]]
		},
		['progress_voronej'] = {
			['step'] = {
				['1'] = [[Die Siedlung in Woronesch wird von Fallschirmjägern geschützt, die kurz vor der Katastrophe aus Rjasan abkommandiert wurden. Der überlebende Flugplatz wurde zum Fundament ihrer Siedlung.<new_line><new_line>Die Soldaten wollen schon lange nach Rjasan zurückkehren und der dort herrschenden Bande die Kontrolle entreißen. Es scheint, als hätten sie die Arbeitskräfte und die Ausrüstung, um dies zu tun, aber etwas hält sie zurück.]]
			}
		},
		['snowmen_fortress'] = {
			['text'] = [[Die Schneemänner haben eine Schneeburg errichtet und plagen die Gegend. Geh hin und mach ihrem Treiben ein Ende.]],
			['step'] = {
				['1'] = [[Ich muss die Schneeburg zerstören und die Schneemänner loswerden.]]
			},
			['name'] = [[Schneeburg]]
		},
		['frankenstein'] = {
			['text'] = [[Der Notiz des Totengräbers nach zu schließen, versucht Igor, ein verfluchtes Monster wieder zum Leben zu erwecken. Ich weiß, wo er sein könnte. Ich habe von einem Haus gehört, dass ständig vom Blitz getroffen wird. Du solltest die Sache zu Ende bringen, die du begonnen hast.]],
			['step'] = {
				['1'] = [[Ich wurde beauftragt, die Reanimation von Frankensteins Monster zu verhindern.]]
			},
			['name'] = [[Frankensteins Monster]]
		},
		['progress_chelyabinsk'] = {
			['step'] = {
				['1'] = [[Tscheljabinsk ist eine dicht besiedelte Industriesiedlung. Es ist die Basis, von der aus der Vorarbeiter die Produktion und jeden Aspekt des Lebens an der Eisenfront kontrolliert.<new_line><new_line>Das ergibt Sinn – von hier geht die Eisenbahnverbindung mit anderen Städten aus. Die Eiserne Front überschwemmt Westsibirien mit Metall und kauft im Gegenzug Nahrung von den Sibiriern, denn hier gibt es keine Möglichkeit, Landwirtschaft zu betreiben.]]
			}
		},
		['progress_tymen'] = {
			['step'] = {
				['1'] = [[„Natürlich leben sie dort gut mit ihrem schwarzen Gold“. Das ist das, was ich am häufigsten über Tjumen höre. Der Reichtum der Siedlung beruht auf den hohen Kapazitäten ihrer Ölraffinerien.<new_line><new_line>Wer weiß, vielleicht sind die Tjumener ja faul geworden? Ohne die Rohstoffe und die logistische Unterstützung durch ihre Partner im Syndikat würde diese Siedlung kein Jahr überleben.]]
			}
		},
		['polar_star'] = {
			['text'] = [[Die Kugel, die du gefunden hast, ist der Polarstern. Ded Moroz hat ihn auf seinen langen Reisen verwendet, aber du musst selber rausfinden, wie er funktioniert. Nimm den Stern, stell dich in einer klaren Nacht ins Ödland und hebe ihn zum Himmel.]],
			['name'] = [[Polarstern]]
		},
		['progress_surgut'] = {
			['step'] = {
				['1'] = [[Nach der allgemeinen Katastrophe ist Surgut von der über die Ufer getretenen Ob umgeben. Die einzige Verbindung zur Außenwelt führt über die verbleibende Straße nach Tjumen.<new_line><new_line>Alles, was die Siedlung tun kann, ist, ihre Partner im Syndikat mit Rohöl zu versorgen. Zum Glück fließt das Öl ungehindert weiter. Flussläufe würden es Surgut ermöglichen, mit anderen Städten in Sibirien Handel zu treiben, aber wer wird sie mit Flusstransporten versorgen?]]
			}
		},
		['set_up_devices'] = {
			['text'] = [[„Wir untersuchen das Verhalten der Biomutanten und die Art und Weise, wie die Sporen verbreitet werden. Installiere diese Ausrüstung bei der Siedlung. Die Informationen, die wir sammeln, werden uns bei unserer Forschung für das EMBA-Projekt helfen.“]],
			['step'] = {
				['3'] = [[Ich habe die Ausrüstung installiert und getarnt.<new_line>Das Rudel Biomutanten, das mich hätte bemerken können, wurde vernichtet. Die Geräte sind jetzt völlig sicher. Ich muss das dem Mitarbeiter von Progress melden.]],
				['2'] = [[Ich habe die Ausrüstung installiert und getarnt. Ich muss dies dem Mitarbeiter von Progress melden.]],
				['1'] = [[Progress beobachtet das Gebiet rund um die Siedlung. Ich muss ihre Ausrüstung außerhalb der Basis installieren.]]
			},
			['name'] = [[Installiere die Ausrüstung]]
		},
		['halloween_boss_2022_4'] = {
			['step'] = {
				['3'] = [[Ich muss den Kürbiskönig besiegen. Im Vertrag steht, dass nur das Schwert des Henkers ihm schaden kann.]],
				['2'] = [[Ich bin ihnen auf der Spur. Die Ausreißer konnten entkommen ... Ich muss ihnen folgen.]],
				['1'] = [[Ein Bauer behauptet, dass seine Ernte verschwunden ist. Für mich klingt das nach dem Werk des Kürbiskönigs, aber das muss untersucht werden.]]
			},
			['text'] = [[Der Kürbiskönig ist der Geist der Erntezeit. Er kümmert sich um seine Kürbisuntertanen und achtet darauf, dass die Menschen, die das Land bearbeiten, der Natur keinen Schaden zufügen. Der Kürbiskönig ist zwar ein Geist, aber er ist auch ein Mensch von edlem Blut, sodass er nur durch das Schwert des Henkers verletzt werden kann.]]
		},
		['progress_omsk'] = {
			['step'] = {
				['1'] = [[Omsk hat sich von der S.O.V.A. abgespalten und wird nun von der Armee verwaltet. Die einzige Handelsroute zwischen Ost und West führt durch die Siedlung.<new_line>Das scheint der Grund zu sein, warum eine geschlossene Siedlung wie diese überleben kann.<new_line><new_line>Andeutungen und Gerüchte lassen jedoch vermuten, dass die Armee noch etwas Anderes hat, das die anderen Mächte des Urals und Sibiriens in Schach hält. Man kann versuchen, mehr über Omsk herauszufinden, aber nur wenigen Menschen gelingt es, auch nur mit den Außenbezirken der Stadt in Kontakt zu treten.]]
			}
		},
		['take_stash'] = {
			['text'] = [[„Hi Kumpel, ich habe hier eine Lieferung. Naja, nicht direkt hier ... Sie wurde irgendwo an der Straße vergraben. Kannst du sie für mich holen?“]],
			['step'] = {
				['6'] = [[Jemand in der Siedlung hat es vermasselt und die Sachen schlecht versteckt, so dass sie von Räubern gestohlen wurde. Ich muss zurückgehen und das melden.]],
				['5'] = [[Gewinne zurück, was die Räuber gestohlen haben.]],
				['4'] = [[Nachdem ich die Kiste unterwegs durchsucht hatte, beschloss ich, sie zu behalten.]],
				['3'] = [[Nachdem ich zur Basis zurückgekommen war, gab ich die Kiste dem Tavernenwirt.]],
				['2'] = [[Ich habe den Ort gefunden und die Kiste ausgegraben. Ich bringe sie jetzt zur Basis und finde heraus, was ich von dort aus machen kann.]],
				['1'] = [[Der Ort, an dem sich das Versteck befindet, ist auf der Karte markiert. Ich muss dorthin gehen und die Kiste ausgraben.]]
			},
			['name'] = [[Schnapp dir die Sachen]]
		},
		['progress_magnitogorsk'] = {
			['step'] = {
				['1'] = [[Während der Bombardierung des Urals fanden diejenigen, die heute die Eiserne Front anführen, in den Bergwerken von Magnitogorsk Unterschlupf. Jetzt schuften viele Menschen in diesen Minen, aber viele von ihnen machen diese zermürbende Arbeit nicht freiwillig.<new_line><new_line>Ich bin sicher, dass ich stark genug bin, um mich hier als Partner einzurichten und nicht als kostenlose Quelle von Arbeitskraft.]]
			}
		},
		['science_polygon'] = {
			['text'] = [[„Wir führen auf dem Versuchsgelände Experimente durch, um ein besseres Verständnis für das EMBA-Projekt zu bekommen. Allerdings haben wir Probleme mit Biomutanten-Angriffen. Du hast eine Menge Kampferfahrung - kannst du mit der Bedrohung umgehen?“]],
			['step'] = {
				['1'] = [[Ich muss den Wissenschaftlern von Progress beim Umgang mit den Biomutanten helfen.]]
			},
			['name'] = [[Versuchsgelände]]
		},
		['search_killer'] = {
			['text'] = [[Der Mörder hat sich durch das Portal in die Darüberliegende Welt davongestohlen. Du musst ihn finden und ihn aufhalten.]],
			['step'] = {
				['1'] = [[Ich muss den Mörder schnappen und ihn aufhalten. Er befindet sich irgendwo in der Darüberliegenden Welt.]]
			},
			['name'] = [[Finde den Mörder]]
		},
		['new_year_boss_2022_5'] = {
			['step'] = {
				['1'] = [[Wir haben alles getan, was wir vor dem Kampf gegen den Krampusnachtsmann und seine Superwaffe tun konnten. Jetzt ist es an der Zeit, sich ihm zu stellen.]],
				['2'] = [[Wir haben den Krampusnachtsmann besiegt, aber der böse Frosti hat die magische Energie, die wir freigesetzt haben, in seine Hände bekommen. Ich muss mit meinen Mit-Weihnachtsmännern besprechen, wie es weitergehen soll.]]
			}
		},
		['supply'] = {
			['text'] = [[„Die Nachbarbasis hat uns um Vorräte gebeten. Kannst du alles zusammensammeln und hinbringen?“]],
			['step'] = {
				['5'] = [[Ich habe überhaupt keine Belohnung erhalten. Aber na ja, vielleicht erweist sich das Zeug als nützlich.]],
				['4'] = [[Ich wurde für meine Arbeit belohnt, aber sie hätten ruhig ein bisschen großzügiger sein können.]],
				['3'] = [[Meine harte Arbeit hat sich gelohnt.]],
				['2'] = [[Ich habe alles gefunden, was ich brauche. Jetzt muss ich das Frachtgut nur noch zur anderen Basis bringen.]],
				['1'] = [[Ich wurde losgeschickt, um alles zu suchen, was die andere Basis braucht.]]
			},
			['name'] = [[Lieferung]]
		},
		['immortal_love'] = {
			['text'] = [[Ich habe den letzten Eintrag im Logbuch entschlüsselt. Er besagt, dass ein Mädchen auf Befehl einer Mumie entführt wurde. Wir müssen sie retten. Folge den Skorpionen, um das Versteck der Mumie zu finden. Wenn du die Skorpione herauslockst, werden sie dich zu ihrem Meister führen.]],
			['step'] = {
				['1'] = [[Ich muss ein Mädchen aus dem Versteck einer Mumie retten. Um sie aufzuspüren, muss ich Skorpione anlocken und ihnen folgen.]]
			},
			['name'] = [[Mumie]]
		},
		['progress_krasnoyarsk'] = {
			['step'] = {
				['1'] = [[Die Siedlung Krasnojarsk ist wie eine wissenschaftliche Utopie. Die Menschen arbeiten in Gewächshäusern, die von Wissenschaftlern geleitet werden. Das BIOS-4-System, das sie entwickelt haben, ermöglicht nie dagewesene Ernten und sogar die Versorgung anderer Siedlungen. Natürlich nicht zum Nulltarif.<new_line><new_line>Alles hat seinen Preis: Das Verfahren ist mit einem erheblichen Verbrauch an Strom und sauberem Wasser verbunden. Außerdem ist der Prozess so hochtechnisiert, dass schon der Verlust eines einzigen Mitarbeiters das ganze System zum Einsturz bringen kann.]]
			}
		},
		['ice_brigands'] = {
			['text'] = [[Die Tiere berichten, dass in der Nähe eine Bande Eisbriganten ihr Unwesen treibt. Sie stellt eine ernsthafte Gefahr dar. Halte sie auf.]],
			['step'] = {
				['1'] = [[Ich muss eine Bande gefährlicher Eisbriganten besiegen.]]
			},
			['name'] = [[Briganten]]
		},
		['progress_izhevsk'] = {
			['step'] = {
				['1'] = [[Izhevsk ist von der Eisernen Front durch das Uralgebirge und kilometerlange verseuchte Erde getrennt, in der es von Mutanten wimmelt. Das ist wahrscheinlich der Grund, warum die Eiserne Front immer noch nicht die örtliche Waffenindustrie in die Finger bekommen hat.<new_line><new_line>So sind die Siedler aus Izhevsk sehr daran interessiert, die Kontakte mit Rjasan aufrechtzuerhalten – es ist weit weg und sie können friedlich ihre Waffen und die Produkte ihrer Bienenmetzgerei tauschen.]]
			}
		},
		['place_stash'] = {
			['text'] = [[„Hier ist eine Kiste. Siehst du das Kreuz auf der Karte? Vergrab die Kiste dort, aber denk nicht mal dran, sie zu öffnen, okay?“]],
			['step'] = {
				['3'] = [[Der Job ist erledigt. Ich habe die Kiste genau dort versteckt, wo sie gesagt hatten. Jetzt kann ich zur Basis zurückkehren.]],
				['5'] = [[Wozu das alles? Ich behalte die Kiste einfach.]],
				['1'] = [[Ich muss zu der markierten Stelle gehen und die Kiste verstecken.]],
				['4'] = [[Ich habe die Kiste versteckt. Hoffentlich findet sie der vorgesehene Empfänger.]]
			},
			['name'] = [[Das Versteck verbergen]]
		},
		['forester'] = {
			['text'] = [[Ein Mann kam herein und hat einen Auftrag für dich hinterlassen. Ein Förster braucht deine Hilfe. Klingt nach einem Kinderspiel mit einer ordentlichen Entlohnung, wenn du mich fragst.]],
			['step'] = {
				['1'] = [[Jemand hat einen persönlichen Auftrag für mich hinterlassen. Ich soll einen Förster helfen, der versprochen hat, mich gut dafür zu bezahlen.]]
			},
			['name'] = [[Förster]]
		},
		['progress_irkutsk'] = {
			['step'] = {
				['1'] = [[Manchmal geschehen Wunder, und eines davon ereignete sich in Irkutsk. Weder das Wasserkraftwerk Angara noch das Wasser des Baikalsees waren von der Katastrophe betroffen. Die Einwohner von Irkutsk lieben das Wasserkraftwerk jetzt wie ihr eigenes Kind. Sie haben sich sogar direkt auf dem Damm niedergelassen und versorgen nun ganz S.O.V.A. mit Strom und sauberem Wasser.]]
			}
		},
		['progress_gorenichi'] = {
			['step'] = {
				['1'] = [[Während der Katastrophe wurde das umliegende Land massenhaft bombardiert, doch einige Flecken fruchtbarer Schwarzerde überlebten.<new_line>Die Einheimischen entdeckten diese Flecken Land und begannen, ihn zu bewirtschaften.<new_line><new_line>Dann tauchte eine neue Bedrohung auf, das Erbe der Strahlung: Welle um Welle mutierter Kreaturen belagerte die Siedlung hier. Nun aber haben sich die schwarzen Wölfe an die Spitze der Nahrungskette gesetzt und die Konkurrenz dezimiert. Doch die Kraft der menschlichen Überlebenden schwindet unter ihrem bösartigen Ansturm.]]
			}
		},
		['hunt_monsters'] = {
			['step'] = {
				['1'] = [[Ich wurde beauftragt, die Anzahl der Zombies in diesem Gebiet zu reduzieren.]]
