			['text'] = [[Beim Portal treiben zu viele verrückte Untote ihr Unwesen. Es wird Zeit, das zu tun, was du am besten kannst: Zombies ruhigstellen.]]
		},
		['cold_footprints'] = {
			['text'] = [[Die Hasen haben das Lager der Eisbriganten gefunden, die das Terem geplündert haben. Ich markiere es auf deiner Karte. Geh hin und sieh nach, was du herausfinden kannst.]],
			['name'] = [[Kalte Spur]]
		},
		['letter_of_hope'] = {
			['text'] = [[Auch in schwierigen Zeiten schreiben die Leute noch Briefe an Ded Moroz. Sie lassen sie an allen möglichen Orten liegen. Sammle die Briefe ein und bring sie mir.]],
			['step'] = {
				['1'] = [[Sneguroschka hat mich gebeten, die übrigen Briefe an Ded Moroz einzusammeln.]]
			},
			['name'] = [[Briefe der Hoffnung]]
		},
		['herb_collect'] = {
			['text'] = [[Der Winter ist hart für die Tiere. Ich helfe ihnen, so gut ich kann. Bitte sammle diese Heilkräuter und bring sie zu mir auf der Lichtung.]],
			['step'] = {
				['1'] = [[Sneguroschka hat mich gebeten, Kräuter zu sammeln, um den Tieren zu helfen.]]
			},
			['name'] = [[Heilkräuter]]
		},
		['doll'] = {
			['text'] = [[Du hast nach der Puppe gefragt. Ich kenne nur eine Person, die Puppen kontrollieren kann. Der Puppenspieler ist ein übler Geselle, aber uns bleibt keine andere Wahl. Ich markiere dir auf der Karte, wo du nach ihm suchen musst.]],
			['step'] = {
				['1'] = [[Der Tavernenwirt hat die Werkstatt des Puppenspielers auf der Karte markiert. Vielleicht finde ich dort ja Antworten.]]
			},
			['name'] = [[Puppe]]
		},
		['winter_fishing'] = {
			['text'] = [[Ich bereite ein Festmahl vor und brauche noch etwas Fisch. Warum nimmst du dir nicht etwas Zeit und gehst angeln?]],
			['step'] = {
				['1'] = [[Sneguroschka hat mich gebeten, Fisch für ein Festessen zu besorgen.]]
			},
			['name'] = [[Eisangeln]]
		},
		['ice_brigands_camp'] = {
			['text'] = [[Es wurde noch ein Eisbriganten-Lager entdeckt. Geh hin, ehe die Briganten anfangen, Ärger zu machen.]],
			['step'] = {
				['1'] = [[Das Lager der Eisbriganten muss zerstört werden.]]
			},
			['name'] = [[Brigantenlager]]
		},
		['mirror_lady'] = {
			['text'] = [[Ich habe Gerüchte gehört, dass Igor Bloody Mary den Hof macht. Er könnte sich mit ihr gemeinsam versteckt halten. Aber sei besser vorsichtig, denn sie kann sehr gefährlich sein. Du musst vor einem Spiegel eine Kerze entzünden und sie rufen – dann wird sie erscheinen.]],
			['step'] = {
				['1'] = [[Der Tavernenwirt meinte Igor könnte sich bei seiner Geliebten verstecken. Das muss ich überprüfen.]]
			},
			['name'] = [[Die Schöne im Spiegel]]
		},
		['ambush'] = {
			['text'] = [[„Hallo, du da! Unsere Jungs planen einen Hinterhalt. Hilf ihnen. Vielleicht kommt für dich auch etwas rum.“]],
			['step'] = {
				['6'] = [[Der Hinterhalt ist fehlgeschlagen.]],
				['5'] = [[Der Hinterhalt war erfolgreich, aber wir mussten einige Verlust hinnehmen.]],
				['4'] = [[Ich habe mich im Kampf bewiesen und den verwundeten Kämpfern geholfen.]],
				['3'] = [[Der Hinterhalt war erfolgreich.]],
				['2'] = [[Zeit, zur Basis zurückzukehren und über den Hinterhalt zu berichten.]],
				['1'] = [[Der Ort, an dem der Überfall geplant ist, ist auf der Karte markiert. Ich muss dorthin gehen.]]
			},
			['name'] = [[Hinterhalt]]
		},
		['cover_the_fighter'] = {
			['text'] = [[Der Späher hat einen verlassenen Ort gefunden, aber es ist gefährlich, einen solchen Ort allein zu erkunden. Gib ihm Deckung.]],
			['step'] = {
				['3'] = [[Knacke den Safe, für den der Späher keine Zeit hatte.]],
				['2'] = [[Wir haben es geschafft, uns Zugang zu dem Gelände zu verschaffen, und der Späher ist einigermaßen sicher. Ich kann zurück in die Siedlung gehen.]],
				['1'] = [[Der Späher hat einen Ort gefunden, der zu gefährlich ist, um sich ihm allein zu nähern. Ich muss ihm helfen, falls er angegriffen wird.]]
			},
			['name'] = [[Stelle Deckung bereit]]
		},
		['passenger'] = {
			['text'] = [[„Hallo! Kannst du mich zur nächsten Siedlung mitnehmen? Allein durch das Ödland zu reisen ist gefährlich, und du siehst wie ein zuverlässiger Begleiter aus.“]],
			['step'] = {
				['3'] = [[Ich konnte meinen Begleiter nicht beschützen. Er starb, bevor er sein Ziel erreichte.]],
				['2'] = [[Der Siedler ist wohlbehalten an seinem Ziel angekommen.]],
				['1'] = [[Einer der örtlichen Siedler heuerte mich an, um sie sicher zu einem anderen Stützpunkt zu bringen.]]
			},
			['name'] = [[Passagier]]
		},
		['camp_supply'] = {
			['text'] = [[„Das Lager unserer Verbündeten erlebt gerade eine harte Zeit. Wir haben beschlossen zu helfen und schicken ein paar Vorräte. Kannst du sie hinbringen?“]],
			['step'] = {
				['3'] = [[Ich habe die Vorräte ausgeliefert. Unsere Verbündeten waren für die Hilfe sehr dankbar.]],
				['2'] = [[Die Vorräte wurden geliefert, und mehr kann ich nicht tun. Ich mach mich auf den Weg nach Hause.]],
				['1'] = [[Ich wurde angewiesen, Vorräte ins Lager unserer Verbündeten zu bringen.]],
				['7'] = [[Das Lager unserer Verbündeten wird angegriffen. Ich sollte die Siedlung bitten, Hilfe zu schicken.]],
				['6'] = [[Als ich im Lager ankam, waren bereits alle tot. Es war keiner mehr da, dem ich die Vorräte geben konnte.]],
				['5'] = [[Ich bin der Einzige, der noch am Leben ist. Es ist keiner mehr da, dem ich die Vorräte geben kann.]],
				['4'] = [[Ich habe unseren Verbündeten geholfen, einen Angriff zurückzuschlagen, dann habe ich ihnen die Vorräte gegeben.]],
				['9'] = [[Heile den verletzten Verbündeten im Lager.]],
				['8'] = [[Hilf den Verbündeten bei der Abwehr des Angriffs.]]
			},
			['name'] = [[Lagervorräte]]
		},
		['catch_thief'] = {
			['text'] = [[„Der Chef des Lagerhauses beschwert sich über Diebe. Er kann sie keinesfalls alle allein fangen. Geh und hilf ihm.“]],
			['step'] = {
				['3'] = [[Der Dieb ist in meine Falle getappt und gestorben.]],
				['2'] = [[Ich habe das Lager der Räuber, die auf der Basis gestohlen haben, ausgehoben. Ab jetzt sollte alles glatt laufen.]],
				['1'] = [[Das Wichtigste zuerst. Ich muss zum Lagerhaus gehen und mit dem Chef sprechen.]],
				['7'] = [[Wohl wissend, dass jemand anderes die Schuld auf sich nehmen würde, habe ich selbst ein paar Dinge aus dem Lagerhaus mitgehen lassen.]],
				['6'] = [[Ich bin nicht gut als Wächter. Der Dieb konnte entkommen.]],
				['5'] = [[Ich habe mit den Räubern verhandelt und sie ins Lagerhaus gelassen, dann habe ich alle beseitigt.]],
				['4'] = [[Ich habe es geschafft, den Dieb lebend zu fangen.]],
				['10'] = [[Ich muss das, was geplündert wurde, ins Lagerhaus zurückbringen.]],
				['9'] = [[Der Vorarbeiter des Lagerhauses bat mich um einen zusätzlichen Gefallen – die Komplizen des Diebes zu erledigen.]],
				['8'] = [[Der Vorarbeiter des Lagerhauses bat mich um einen zusätzlichen Gefallen – ich sollte das Geplünderte zurückbringen.]]
			},
			['name'] = [[Fang den Dieb]]
		},
		['gravedigger'] = {
			['text'] = [[Du bist genau der, den ich suche. Ich habe einen Job für dich. Ein Totengräber ist verrückt geworden und gräbt jetzt Leichen aus, anstatt sie zu verbuddeln. Dem müssen wir ein Ende setzen. Kannst du das übernehmen?]],
			['step'] = {
				['1'] = [[Mir wurde aufgetragen, mich um den wahnsinnigen Totengräber zu kümmern.]]
			},
			['name'] = [[Totengräber]]
		},
		['comrade_in_trouble'] = {
			['text'] = [[Einer unserer Leute sollte gestern von einer Expedition zurück sein, aber es gibt kein Zeichen von ihm. Geh und sieh nach, ob ihm etwas passiert ist und ob er Hilfe braucht.]],
			['step'] = {
				['3'] = [[Der Späher hielt an, um im Freien zu malen. Gut, dass er nicht in Schwierigkeiten geraten ist. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['2'] = [[Dem Späher geht es blendend. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['1'] = [[Ich muss herausfinden, was mit dem vermissten Späher passiert ist. Der Chef der Wache hat mir auf der Karte gezeigt, wo ich ihn finden kann.]],
				['7'] = [[Der Späher hat eine Strahlungdosis abbekommen. Ich habe ihm etwas Medizin gegeben. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['6'] = [[Entfache ein Feuer im Lager.]],
				['5'] = [[Die Antwort war einfach: Der Späher hat die Nacht durchgearbeitet. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['4'] = [[Die Antwort war einfach: Der Späher hat die Nacht durchgearbeitet. Ich habe vorsichtshalber etwas Holz auf sein Feuer gelegt. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['9'] = [[Rette den Späher ohne Hilfe.]],
				['8'] = [[Der Späher hat eine Strahlungdosis abbekommen. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['17'] = [[Der Späher hat lange gebraucht, um die ganze Beute einzusammeln, und ist erst jetzt auf dem Heimweg. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['16'] = [[Der Späher hat lange gebraucht, um die ganze Beute einzusammeln, und ist erst jetzt auf dem Heimweg. Ich habe mich bereit erklärt, die verbleibende Kiste abzuliefern. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['22'] = [[Der Späher steckte in einem alten Haus fest, als es einstürzte. Ich habe ihm geholfen, da rauszukommen. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['14'] = [[Dem Späher ist das Benzin ausgegangen. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['18'] = [[Bringe die verbleibende Kiste zur Siedlung.]],
				['25'] = [[Der Späher war verwundet. Ich habe ihm erste Hilfe geleistet. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['26'] = [[Der Späher war verwundet. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['29'] = [[Der Späher ist tot. Ich habe beschlossen, seine Leiche nach Hause zu bringen.]],
				['30'] = [[Der Späher ist tot. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['31'] = [[Bring die Leiche des Spähers zur Siedlung.]],
				['20'] = [[Die Räuber haben den Späher in ein halb verfallenes Haus gejagt, aber er hält sie zurück. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['19'] = [[Der Späher verbarrikadierte sich gegen die Räuber in einem halb verfallenen Haus. Ich habe mich um die Situation gekümmert. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['23'] = [[Der Späher steckte in einem alten Haus fest, als es einstürzte. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['13'] = [[Dem Späher ist das Benzin ausgegangen. Ich habe ihm etwas Treibstoff gegeben. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['10'] = [[Ich habe den Späher vor Raubtieren geschützt. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]],
				['11'] = [[Raubtiere haben den Späher angegriffen. Ich sollte zur Siedlung zurückkehren und Bericht erstatten.]]
			},
			['name'] = [[Kamerad in Schwierigkeiten]]
		},
		['frozen'] = {
			['text'] = [[The Eisbriganten haben Leute entführt und in Eisblöcken eingefroren! Beeil dich, vielleicht kannst du sie noch retten. Die Eule zeigt dir den Weg.]],
			['step'] = {
				['1'] = [[Es wurden Leute von den Eisbriganten entführt. Ich muss sie retten.]]
			},
			['name'] = [[Gefroren]]
		},
		['drakula'] = {
			['text'] = [[Der Kürbiskopf ist im Ödland auf den Ältesten getroffen. Dracula muss dort irgendwo sein. Finde und vernichte ihn, bevor er noch mehr Schaden anrichtet.]],
			['step'] = {
				['1'] = [[Ich muss noch zu einem letzten Kampf antreten – und mein Gegner ist Graf Dracula.]]
			},
			['name'] = [[Graf Dracula]]
		},
		['sled_repair'] = {
			['text'] = [[Ich bin so weit gereist, dass der Schlitten in fürchterlichem Zustand ist. Kannst du ihn reparieren?]],
			['step'] = {
				['1'] = [[Sneguroschka hat mich gebeten, ihren Schlitten zu reparieren.]]
			},
			['name'] = [[Schlittenreparatur]]
		},
		['gigahive_ufa'] = {
			['step'] = {
				['3'] = [[Ich habe die Bienenkönigin getötet. Also nehme ich jetzt den Kopf und kehre zum Tavernenwirt in Izhevsk zurück, um meine Belohnung abzuholen.]],
				['2'] = [[In Ufa gibt es einen Bienenstock, der von einer Bienenkönigin beherrscht wird. Um dorthin zu gelangen, brauche ich ein Gegengift. Aber ich muss ein paar Mutterbienen töten und das Wachs zu einem Spezialisten in Izhevsk bringen, um es zu bekommen. Und tatsächlich hat mir der Tavernenwirt für den Kopf der Königin auch noch eine Belohnung versprochen.]],
				['1'] = [[Ich muss nach Izhevsk gehen und den Tavernenwirt zu dem Bienenstock in Ufa befragen.]]
			},
			['name'] = [[Die falsche Bienensorte]]
		},
		['traveler_salvation'] = {
			['text'] = [[Ein Eichhörnchen sagt, dass ein Mann im Wald erfriert. Geh schnell hin und hilf ihm! Hoffentlich lebt er noch.]],
			['step'] = {
				['3'] = [[Reisenden Zustand hat sich verbessert. Jetzt muss ich ihn an einen sicheren Ort zu bekommen.]],
				['2'] = [[Der Mensch ist sehr kalt und erschöpft, er braucht Hilfe.]],
				['1'] = [[Irgendwo im Wald erfriert ein Mann. Ich muss mich beeilen und ihn retten.]]
			},
			['name'] = [[Rettungsaktion]]
		},
		['undertaker'] = {
			['text'] = [[Es gab einen Zombieaufstand auf einem weiteren Friedhof. Die Zombies haben sich beruhigt, aber sie brauchen neue Särge. Nimm den Bestatter mit dir. Du findest die Materialien und er baut die Särge. Und dann bringst du sie an den richtigen Ort.]],
			['step'] = {
				['1'] = [[Ich muss Materialien finden und den Bestatter bitten, Särge anzufertigen.]],
				['2'] = [[Die neuen Särge müssen an den zerstörten Friedhof geliefert werden.]]
			},
			['name'] = [[Der Bestatter]]
		},
		['toy_story'] = {
			['text'] = [[Dank deiner Bemühungen konnte ich einen Teil der Magie wiederherstellen und einen Weg zum Terem finden, aber ich fürchte, dass es Fallen gibt. Bitte geh und sieh nach, aber sei bloß vorsichtig.]],
			['name'] = [[Spielzeuggeschichte]]
		},
		['progress_briansk'] = {
			['step'] = {
				['1'] = [[Während der Katastrophe gab es einen riesigen Flüchtlingsstrom durch Brjansk und eine unglaublich hohe Sterblichkeitsrate unter ihnen. Infolgedessen war aber auch die Zahl der Überlebenden ungewöhnlich hoch, und sie hatten eine Immunität entwickelt.<new_line><new_line>Das Problem ist, dass sie immer noch in der Umgebung von Brjansk festsitzen, ohne wirkliche Organisation oder Unterstützung. Verschiedene Parteien haben bereits ein Auge auf die Siedlung geworfen: Es gibt viele, die solche menschlichen Ressourcen ausnutzen wollen. Und, wie die bittere Erfahrung zeigt, nicht für irgendwelche edlen Zwecke.]]
			}
		},
		['exp_recovery'] = {
			['step'] = {
				['1'] = [[An der Schwelle des Todes verlor ich das Bewusstsein und wurde wie durch ein Wunder gerettet. Aber ich habe meine Erinnerungen an die vorangegangenen Ereignisse verloren. Um sie zurückzubekommen, muss ich an den Ort zurückkehren, an dem alles passiert ist.]],
				['2'] = [[Ich habe meine verlorenen Erinnerungen zurück.]]
			},
			['name'] = [[Bewusstlos]]
		},
		['winter_queen'] = {
			['text'] = [[Es ist Zeit, Ded Moroz zu retten! Wenn du bereit bist, setze den Polarstern ein. Wir reisen zusammen zum Eispalast und treten der Winterkönigin entgegen.]],
			['name'] = [[Die Winterkönigin]]
		},
		['destroy_lair'] = {
			['text'] = [[Der Oberwächter tippte auf eine Karte, die hinter ihm hängt. „In dieser Gegend wimmelt es von gefährlichen Bestien. Geh und zerstöre ihr Versteck.“]],
			['step'] = {
				['1'] = [[Ich muss das Nest finden und die Bewohner töten.]],
				['2'] = [[Ich habe es geschafft, das Nest zu zerstören.]]
			},
			['name'] = [[Zerstöre das Nest]]
		},
		['hunt'] = {
			['text'] = [[„Es ist gerade ein Auftrag für Fleisch reingekommen. Willst du ein bisschen jagen gehen?“]],
			['step'] = {
				['3'] = [[Alles ist gut gegangen und der Kunde ist zufrieden.]],
				['2'] = [[Ich muss das Fleisch zum Kunden bringen.]],
				['1'] = [[Ich wurde gebeten, Fleisch zu sammeln. Ich muss jagen gehen.]],
				['4'] = [[Ich habe nicht genug Fleisch mitgebracht, deshalb wollte der Kunde nicht bezahlen.]]
			},
			['name'] = [[Jagd]]
		},
		['headless_horseman'] = {
			['text'] = [[Das Mädchen, das du gerettet hast, war hier und hat von einem Gespräch erzählt, das die auf dem Schiff mitangehört hat. Anscheinend will Dracula mithilfe dieses Buchs den Ältesten wiedererwecken und die Grenze zwischen den Welten zerstören. Das dürfen wir nicht zulassen! Du musst den Reiter abfangen und ihm das Buch abnehmen.]],
			['step'] = {
				['1'] = [[Ich muss den Reiter abfangen und ihm das Buch abnehmen. Um ihn zu finden, habe ich zur Hilfe ein Wollknäuel erhalten.]]
			},
			['name'] = [[Kopfloser Reiter]]
		},
		['progress_kurgan'] = {
			['step'] = {
				['1'] = [[Überraschenderweise kannte ich die Menschen in Kurgan bereits. Wie sich herausstellte, sind sie es, die das gesamte Eisenbahnsystem betreiben. Wenn ein Kurganer nicht gerade mit der Bahn fährt, repariert er sie. Es ist also klar, mit wem man Unstimmigkeiten vermeiden sollte.<new_line><new_line>Ihre gepanzerten Züge sind praktisch unverwundbar gegen Angriffe von Räuber und Mutanten, obwohl in das Ödland alles passieren kann. Deshalb ist es für Kurgan auch wichtig, gute Beziehungen zu seinen Partnern zu pflegen, und sei es nur, um Treibstoff und Wartungsdienste für seine Züge zu erhalten.]]
			}
		},
		['flying_dutchman'] = {
			['text'] = [[Du sagst, das Buch kann nicht auf trockenem Land gefunden werden? Wenn wir den Gerüchten Glauben schenken können, dann ist der Fliegende Holländer vor Kurzem in unsere Gewässer zurückgekehrt. Ein Zufall? Ich glaube kaum. Wenn du bereit bist, noch tiefer in den Kaninchenbau vorzudringen, nimm diese Glocke an dich. Läute sie an der Küste, um den Weg zum Schiff zu finden.]],
			['step'] = {
				['1'] = [[Der Tavernenwirt hat mir eine Glocke gegeben, die mich zum Geisterschiff bringen soll. Vermutlich hält Igor sich dort mit dem Buch versteckt.]]
			},
			['name'] = [[Fliegender Holländer]]
		},
		['stash_handcar'] = {
			['step'] = {
				['1'] = [[Ich habe ein kleines Stück Papier mit einer Adresse in Kola gefunden. Das muss ich untersuchen. Vielleicht finde ich ja letztendlich ein Versteck mit Vorräten dort.]]
			}
		},
		['firebird_feather'] = {
			['text'] = [[In den alten Geschichten heißt es, dass der Feuervogel goldene Äpfel liebt. Davon haben wir keine, aber ich habe ein paar normale Äpfel verzaubert. Streiche sie mit Honig ein, dann merkt der Vogel den Unterschied nicht und kommt angeflogen.]],
			['name'] = [[Feder des Feuervogels]]
		},
		['wood_delivery'] = {
			['text'] = [[Bei solchem Frost kann ein Bündel Feuerholz den Unterschied zwischen Leben und Tod ausmachen. Sammle etwas Holz und lagere es an einem Ort, den jeder finden kann.]],
			['step'] = {
				['1'] = [[Sneguroschka hat mich gebeten, Feuerholz zu sammeln und für die Leute bereitzulegen.]]
			},
			['name'] = [[Holzlieferung]]
