	['npc'] = {
		['zaycev'] = {
			['choice'] = {
				['3'] = [[Nein, da ist noch etwas anderes. Wie begann der Atomkrieg?]],
				['2'] = [[Das war Tag R.]],
				['1'] = [[Das war vereinbart. Ich habe mein Versprechen eingehalten, die Wölfin ist tot. Jetzt bist du an der Reihe.]],
				['7'] = [[Aber es waren zu viele ...]],
				['6'] = [[Um aus dem sterbenden Land zu fliehen?]],
				['5'] = [[Und sie würden alle Überlebenden selbst töten.]],
				['4'] = [[Ich warte.]],
				['9'] = [[Und es gab nichts, was man tun konnte?]],
				['8'] = [[Die UdSSR schlug zurück?]],
				['17'] = [[Und was hast du getan?]],
				['16'] = [[Was ist geschehen?]],
				['15'] = [[Erklär es so, dass ich es auch verstehe.]],
				['14'] = [[Woran haben sie gearbeitet?]],
				['22'] = [[Nein. Ich habe alles erfahren, was ich wissen wollte. Vielen Dank.]],
				['21'] = [[Ja, da ist noch etwas. Wie begann der Atomkrieg?]],
				['20'] = [[Nun, danke für das Gespräch.]],
				['19'] = [[Ja, danke für die Unterhaltung. Ich muss jetzt gehen ...]],
				['18'] = [[Aber der Virus wurde trotzdem noch eingesetzt.]],
				['12'] = [[Erzähl mir von Nesterow und den anderen Wissenschaftlern, mit denen er zusammengearbeitet hat. Und von dem ganzen Rest.]],
				['13'] = [[Weiter, sei nicht schüchtern.]],
				['10'] = [[Heißt das, dass es auch auf den anderen Kontinenten nichts mehr gibt? Ist die ganze Welt zu einem riesigen Ödland geworden?]],
				['11'] = [[Die Wölfe haben Gavrilov geholt. Ich höre.]]
			},
			['name'] = [[Zaitsev]],
			['episode'] = {
				['3'] = [[Ja, so hat es mit der Pandemie angefangen. Sie hat einen Großteil der Bevölkerung der UdSSR ausgelöscht.<new_line>Bist du jetzt zufrieden?]],
				['2'] = [[(Zaitsev betrachtet den Fangzahn von allen Seiten und sah mich an. Ihm war ein wenig unbehaglich zumute.)<new_line><new_line>Wo ist Pavel?]],
				['1'] = [[(Der einstige Direktor des Instituts für Virologie schlummerte im selben Sessel wie beim letzten Mal. Ich weckte ihn auf und setzte mich ihm gegenüber auf einen Stuhl. Zaitsev sah mich verschlafen an. Dann hielt ich ihm den Fangzahn der Wölfin vor die Nase.)<new_line><new_line>Was ist das?]],
				['7'] = [[Natürlich. Und fast jeder von ihnen war infiziert. Unsere Nachbarländer schlossen ihre Grenzen, riefen das Kriegsrecht aus, sammelten ihre Truppen und erschossen Tausende von Flüchtlingen ...]],
				['6'] = [[Richtig. Nach ein wenig Blutvergießen sollten ihnen die endlosen, reichen Gebiete unseres Vaterlands gehören. Aber R17 war ein Verräter. Die Epidemie breitete sich zu langsam aus, die Inkubationszeit dauerte zu lange und zu viele Menschen waren dagegen immun. Schon im Oktober marschierten Zehntausende Menschen zu den Grenzen der UdSSR ...]],
				['5'] = [[Okay, okay. Aber es ist alles sehr einfach. Ganz offenbar haben die amerikanischen Geheimdienste die Wirksamkeit ihres kleinen Killers R17 überschätzt. Oder vielleicht haben sie ihn unterschätzt. Sie dachten, das gesamte Volk der großen Nation von Sowjets würde an den Rand des Untergangs gedrängt, allen Lebensmut verlieren und durch Krankheiten geschwächt ...]],
				['4'] = [[(Der Direktor sieht mich verwirrt an, nickt dann wieder und putzt sich die Brille.)]],
				['9'] = [[Ja. Wir haben unser gesamtes Arsenal gestartet. Sofort, auf alle Ziele. Obwohl unser Land dem Untergang geweiht war, wollten wir nicht einfach tatenlos untergehen. Die Welle der infizierten Flüchtlinge nutzte das Chaos aus und überrannte die Armeen der Alliierten. Damit breitete sich R17 erfolgreich im Rest der Welt aus.]],
				['8'] = {
					['1'] = [[Das stimmt. Wenn sie einen Menschen töteten, kamen zwei weitere. Die Grenze ist so riesig! Kluge und verzweifelte Menschen fanden einen Weg. Als die Epidemie in China, der Mongolei, der Türkei, Polen und Finnland ausbrach, wollten sie extreme Maßnahmen ergreifen.]],
					['2'] = [[Gegen Ende Oktober fand eine geheime Sitzung dieses Atomvereins statt. Es wurde ein Antrag zur Säuberung des „infizierten Territoriums“ gestellt und verabschiedet. Sie verschossen alles, was sie hatten. Sogar Dörfer wurden mit einer Bombe „desinfiziert“. Aber erneut lief nicht alles nach Plan.]]
				},
				['17'] = {
					['1'] = [[Beletsky fand Nesterows Geheimversteck. Er machte es publik und brachte mir Nesterows Notizbuch. Und eine Sprachaufzeichnung. Ich konnte es anfangs nicht fassen. Doch das waren seine Handschrift und seine Stimme!]],
					['2'] = [[Nesterow und seine Freunde entwickelten eine neue Art der Pest. Nein, es war keine Pestvariante. Dieser Virus, R17, war viel gefährlicher als Yersinia pestis – das Pestbakterium. Und da war noch mehr – dieses Virus wurde maßgeschneidert! Für die CIA! Nesterow hatte eine neue biologische Waffe für unseren Feind entwickelt!]]
				},
				['16'] = {
					['1'] = [[Molekulargenetik. Sie erforschten die Proteine und Gene der Viren, die am weitesten verbreitet waren. Völlig harmlos, solange man eine bestimmte Grenze nicht überschreitet.<new_line>Nesterow hat diese Grenze überschritten. Er experimentierte mit Mutationen. Und er brachte Kulik und Karbyshew dazu, ihm zu helfen.]],
					['2'] = [[Sie verheimlichten den anderen, woran sie arbeiteten. Ich vermutete schon, dass sie abends nicht im Labor waren, nur um gemütlich Tee zu trinken. Aber ich wollte da nicht herumschnüffeln. Doch irgendwann blieb mir einfach keine andere Wahl mehr.]]
				},
				['15'] = [[Ich könnte es erklären, aber du würdest es trotzdem nicht verstehen.]],
				['14'] = [[Was soll ich sagen? Georgy Nesterow war ein national renommierter Wissenschaftler, der Stolz unseres Instituts. Wir haben viele Jahre erfolgreich mit ihm, Kulik und Schaschkow zusammengearbeitet. Karbyshew kam aus Leningrad zu uns. Beletsky, der Jüngste im Team, kam später dazu, Ende 1983.]],
				['20'] = [[Hmm? Noch etwas, junger Mann?]],
				['19'] = [[Leider, ja. Offenbar hatte Nesterow den Virus bereits an die CIA übergeben. Am 17. August 1984 wurde R17 von feindlichen Agenten in vielen Städten mit speziellen Spraydosen versprüht.]],
				['18'] = {
					['3'] = [[Die Anhörung fand hinter verschlossenen Türen statt. Ich durfte aber als Zeuge bleiben. Es ging schnell. Die Beweise waren erdrückend. Und Karbyshew legte ein umfassendes Geständnis ab. Artikel 64, Verrat am Vaterland. Sie wurden direkt aus dem Gerichtssaal zum Erschießungskommando gebracht.]],
					['2'] = [[Dann kamen die Leute vom KGB. Sie verhörten uns und durchsuchten das ganze Institut. Sie nahmen alles mit, was sie in Nesterows Labor fanden, sogar das Türschild. Sie haben das ganze Biomaterial zerstört.]],
					['1'] = [[Ich ... Ich hatte keine Wahl. Wir mussten es den Behörden melden. Wir haben alle drei angezeigt. Auch Beletsky und Schaschkow haben den Bericht unterzeichnet.<new_line>Die Behörden handelten schnell. Nesterow, Kulik und Karbyshew wurden verhaftet.]]
				},
				['12'] = [[Okay, wie du willst. Frisch mein Gedächtnis ein wenig auf, was wolltest du wissen?]],
				['13'] = [[(Der Direktor nickte, nahm seine Brille ab und wischte sie mit geübten Bewegungen mit einem Lappen sauber. Er reinigte sie einige Minuten lang.)]],
				['10'] = [[(Zaitsev lachte verbittert.)<new_line>Nein. Aber da stand jede Nation schon alleine da. Die USA begannen damit, Europa zu bombardieren. Indien griff China an. Tief verwurzelte Konflikte loderten wieder auf ... Es hatte den Anschein, als hätte die ganze Welt den Versand verloren. Nur die Antarktis blieb verschont. Aber die Menschheit hat es wie immer geschafft, alles noch schlimmer zu machen ...]],
				['11'] = [[Um ehrlich zu sein, weiß ich es nicht. Entschuldige, junger Mann, aber wir haben uns schon so lange unterhalten. Ich muss meine Pillen nehmen.]]
			}
		},
		['belaz'] = {
			['choice'] = {
				['5'] = [[Bis später.]],
				['4'] = [[Noch nicht.]],
				['3'] = [[Ja! Ich habe alles gefunden, was du haben wolltest!]],
				['2'] = [[Bist du verrückt? Das ist unmöglich. Das werde ich nicht tun.]],
				['1'] = [[Wow ... Dieser Plan ist wirklich ehrgeizig! Ich weiß nicht, wo ich anfangen soll. Aber es ist einen Versuch wert. Was soll ich tun?]]
			},
			['name'] = [[Wladimir]],
			['episode'] = {
				['7'] = [[Das Haus ist leer. Wladimir lebt jetzt mit seiner Familie im BelAZ.]],
				['6'] = [[Du solltest keine Zeit verschwenden, los jetzt!]],
				['5'] = [[Fantastisch! Ist das möglich? Meine Träume werden wahr! Du hast ja keine Ahnung, wie glücklich mich das macht. Ich schulde dir etwas: Willkommen an Bord! Sobald unsere fahrbare Stadt fertig ist, kannst du dir die Route aussuchen. Wir sind mit jeder deiner Entscheidungen einverstanden.]],
				['4'] = [[Und? Hast du etwas Interessantes gefunden?]],
				['3'] = [[Ich wusste es. Ich höre immer die gleiche Antwort. Niemand will Einzelheiten erfahren ... Was stimmt mit euch nicht? Ihr sucht doch nach dem schnellen Geld, nicht wahr? Jetzt muss ich arbeiten. Wiedersehen.]],
				['2'] = [[Okay, hier ist die Liste. Ich brauche etwas aus dem Atomkraftwerk von Tschernobyl. Vor der Katastrophe habe ich dort gearbeitet und jetzt kann ich einige der speziellen Ersatzteile gebrauchen, die es dort gibt. Außerdem fehlen mir einige Bauteile und ein wenig Gemüse für die Hydrokultur.]],
				['1'] = [[Hey, du hilft mir wohl jetzt, nicht wahr? Großartig! Hör mal: Ich habe schon immer von einem Geländefahrzeug geträumt, das ohne Treibstoff fährt. Das wäre ... eine persönliche fahrbare Stadt! Aber einige Bauteile fehlen mir noch dazu. Und niemand will mir helfen ... Ich habe die Hoffnung schon fast aufgegeben. Ich brauche all diese kleinen Sachen – schau dir die Liste an. Wenn du mir dabei hilfst, mache ich dich zu meinem Beifahrer. Du kannst mit mir und meiner Familie durch die Gegend reisen, wohin du auch willst!]]
			}
		},
		['vanya'] = {
			['choice'] = {
				['7'] = [[Danke, ein anderes Mal.]],
				['6'] = [[Weißt du, mir steht gerade nicht der Sinn danach, mich mit dir zu betrinken.]],
				['5'] = [[Ich möchte Genas Leiche zurückbringen. Damit er anständig beerdigt werden kann.]],
				['4'] = [[Sag mir nur, in welchem Gebäude du ihn gelassen hast.]],
				['3'] = [[Nun, du musst sie noch ein weiteres Mal erzählen.]],
				['2'] = [[Tja, die gibt es aber. Ich möchte dich etwas fragen.]],
				['1'] = [[Ich bin ein Herumtreiber.]]
			},
			['name'] = [[Vanya]],
			['episode'] = {
				['3'] = [[Über Gena, richtig? Ich habe gesehen, wie du seine Mutter besucht hast. Aber ich verrate dir kein Wort. Ich musste die Geschichte schon dreimal erzählen. Ich kann sie nicht mehr hören.]],
				['2'] = [[(Vanya pfiff durch die Zähne.)<new_line><new_line>Ein Herumtreiber? Ich hätte nicht gedacht, dass es noch viele Vollidioten gibt, die mutterseelenalleine durch das Ödland ziehen.]],
				['1'] = [[(Vanya saß auf der Veranda und rauchte. Ich erkannte gleich, dass er angeheitert war.)<new_line><new_line>Hey. Hier war lange kein Fremder mehr. Woher kommst du? Aus welchem Loch bist du denn gekrochen?]],
				['7'] = [[Was? Und du willst ein Russe sein!]],
				['6'] = [[(Vanya kicherte erneut.)<new_line><new_line>Du willst also wirklich wegen einer Leiche bis nach Nowomoskowsk gehen? (Der junge Mann wurde still und sah mich dann faszinierend an.) Hör zu, du Herumtreiber, ich habe eine Idee. Gehen wir zu mir. Ich habe Alkohol aus Nowomoskowsk. Auf dem Etikett steht „Bryanskaya“. Kennst du diese Marke? Es gibt nur ein Problem: Ich habe niemanden, mit dem ich ihn trinken kann. Hier gibt es nur Frauen.]],
				['5'] = [[Ein fünfstöckiges Wohngebäude. Neben einigen Garagen. In einer Wohnung im dritten Stock. Was geht dich das an?]],
				['4'] = [[(Vanya lachte und schnippte seine Zigarette ins Gebüsch. Dann sah er mich spöttisch an. Böser Junge.)<new_line><new_line>Und was möchtest du denn wissen?]],
				['9'] = [[(Vanya ist zu Hause und offenbar nicht allein. Ich will derzeit wirklich nicht dort hineingehen.)]],
				['8'] = [[Hör mal, hey, Herumtreiber, Gena wird nicht so schnell verschwinden, aber dieser Fusel hier wartet nicht auf dich. Hast du deine Meinung geändert?]]
			}
		},
		['gavrilov'] = {
			['choice'] = {
				['3'] = [[(Ich huste, um ihre Aufmerksamkeit zu erregen.)]],
				['2'] = [[In Ordnung, wir unterhalten uns dort.]],
				['1'] = [[Das ist nicht wichtig. Ich muss etwas mit dir besprechen. Es geht um die Wissenschaftler des Instituts für Virologie.]],
				['7'] = [[Warte, geh nicht. Du bist Pavel Gavrilov, der ehemalige Forscher, nicht wahr?]],
				['6'] = [[Wie soll ich es beweisen? Ich weiß nicht ... Ich kann euch beim Transport der Leiche helfen. Sie vor den Wölfen beschützen.]],
				['5'] = [[(Lügen) Ich komme aus Twer.]],
				['4'] = [[Nur die Ruhe, ich bin ein Freund.]],
				['9'] = [[Ich möchte etwas über die Wissenschaftler wissen, die hingerichtet wurden. Über die Epidemie, die gleichzeitig im ganzen Land ausbrach. Und über den 17. August 1984. Den Tag R.]],
				['8'] = [[...]],
				['17'] = [[Ich bin dabei. Diese Sache muss erledigt werden. Diese schwarzen Wölfe sind zu gefährlich.]],
				['16'] = [[Verstehe. Aber warum brennt ihr das Schlachthaus nicht einfach zusammen mit der Wölfin nieder? Und warum räuchert ihr sie nicht aus?]],
				['15'] = [[Nein, ich weiß es nicht.]],
				['14'] = [[Und was soll ich tun?]],
				['11'] = [[Das ist meine Sache.]],
				['19'] = [[Nein. Ich kenne diese Wölfe und weiß, zu was sie in der Lage sind. Ich bin bereit, mein Leben aufs Spiel zu setzen, aber dein Vorschlag, direkt in die Höhle eines ganzen Rudels zu gehen, ist reiner Selbstmord. Mit kalkuliertem Risiko hat das nichts zu tun.]],
				['18'] = [[Ihr seid ansonsten wahrscheinlich ziemlich aufgeschmissen ... In Ordnung, ich bin dabei.]],
				['10'] = [[Trotzdem ... Ich möchte die Wahrheit wissen.]],
				['12'] = [[Ich habe schon eine Menge durchgemacht, um hierherzukommen. Ich habe mein Leben bereits riskiert.]],
				['13'] = [[Mein Versprechen? Was redest du denn da?]],
				['21'] = [[Nein. Bin immer noch nicht lebensmüde.]],
				['20'] = [[Wir verstehen uns.]]
			},
			['name'] = [[Gavrilov]],
			['episode'] = {
				['3'] = [[Nikita, wir gehen zurück. Du kannst entweder mit uns kommen oder die Wölfe alleine töten.]],
				['2'] = [[(Gavrilovs Augen verengten sich zu Schlitzen.)<new_line><new_line>Du hast mein Interesse geweckt. Okay, wir werden darüber beraten. Wenn wir wiederkommen. Gorenichi, das Gemeindezentrum.]],
				['1'] = [[Nikita, ich verstehe, wie du dich fühlst, aber wir müssen gehen. Wir bringen Kirill nach Hause und begraben ihn.<new_line>Nikita, hörst du mir überhaupt zu? Wir gehen. Jetzt. Wie gehen heim nach Gorenichi. Hier ist es zu gefährlich. Und Seva kann kaum laufen. Er muss zu einem Arzt!]],
				['7'] = [[(Der Mann lehnte sich bedrohlich zu dem Kind hinüber.)<new_line><new_line>Wenn sich nicht jemand die letzte Waffe geschnappt und wie ein Schwachsinniger auf Wolfsjagd gegangen wäre, würde Kirill noch leben!]],
				['6'] = {
					['1'] = [[(Der junge Mann schien Gavrilov und den zweiten Mann zu taxieren. Dann zog er langsam eine Waffe und richtete sie auf sie.)]],
					['2'] = [[Ihr seid ... armselige Feiglinge! Kirill hat sich geopfert, damit der Wolf dich nicht erwischt, Gavrilov! Was, verträgst du etwa die Wahrheit nicht? Wir gehen also jetzt dorthin und schlachten sie alle ab!]]
				},
				['5'] = [[Weitermachen? Wir sind doch nur noch zu dritt! Und wir haben kaum zu viert überlebt. Du wirst noch die Gelegenheit erhalten, deinen Stiefvater zu rächen. Du wirst die Gelegenheit dazu bekommen. Aber nicht jetzt. Jetzt müssen wir zurückgehen!]],
				['4'] = [[(Der junge Mann blickte mich mit seinen verwirrten blutunterlaufenen Augen an und sprang plötzlich hoch.)<new_line><new_line>Wir gehen?! Es ist zu gefährlich? Was redest du denn da, Gavrilov? Wir müssen weitermachen! Da draußen sind immer noch Monster! Wir haben Patronen, genug für eine oder zwei dieser Bestien.]],
				['12'] = [[Woher weißt du das?]],
				['11'] = [[(Gavrilovs misstrauische Augen mustern mich vom Kopf bis zu den Zehen. Er schüttelt den Kopf.)<new_line><new_line>Nein, wir schaffen das alleine.]],
				['9'] = [[Ein Freund? Ich kann mich nicht an dich erinnern. Aus welchem Lager kommst du?]],
				['8'] = [[(Alle drei wandten sich plötzlich mir zu. Jetzt deuteten alle drei Makarows direkt in meine Richtung.)<new_line><new_line>Hey! Wer zum Teufel bist du?]],
				['17'] = [[Die Wahrheit? Und was machst du, wenn du die Wahrheit kennst, du namenloser Herumtreiber? Willst du sie allen erzählen?]],
				['16'] = [[(Der Forscher und der Direktor sahen sich wortlos an. Zaitsev runzelte die Stirn und wollte etwas sagen, aber Gavrilov brachte ihn mit einer Geste zum Schweigen. Er kam näher und untersuchte mich vom Kopf bis zu den Zehen.)<new_line><new_line>Den Tag R? Warum willst du das wissen? Wird dieses Wissen etwas ändern? Es ist schließlich schon so lange her.]],
				['15'] = [[Warum so still? Worüber wolltest du mit mir reden?]],
				['14'] = [[(Ich hörte den Namen und war innerlich ein wenig aufgewühlt. Der Direktor des Instituts für Virologie! Offenbar bekomme ich jetzt Antworten auf alle meine Fragen.)]],
				['24'] = [[Ausgezeichnet. Wenn ich mir deine Ausrüstung so ansehe, bist du ein erfahrener Kämpfer. Unsere Erfolgsaussichten sind mit dir gestiegen. Wenn du für die Jagd bereit bist, hol dir Munition, Nahrung und Verbandszeug und geh zum Ausgangspunkt. Ich warte dort auf dich.]],
				['25'] = [[(Gavrilovs Mund verzog sich zu einem schiefen Lächeln, als hätte er diese Antwort erwartet.)<new_line><new_line>Das liegt natürlich an die, aber damit sind meine Lippen versiegelt. Geschäft ist Geschäft. Wenn du deine Meinung änderst – komm zurück. Wir haben noch etwas Zeit.]],
				['26'] = [[Und? Hast du darüber nahgedacht? Mein Angebot gilt noch.]],
				['27'] = [[Wie du willst. Ich schätze, auf diese Weise überlebt man als Einzelgänger so lange.]],
				['10'] = [[Was, du bist den ganzen Weg von Moskau hierhergekommen? Warum sollten wir dir glauben? Ich kann auch charmante Lügen erzählen.]],
				['18'] = [[In Ordnung (er verschränkte seine Hände hinter dem Rücken und spazierte durch das Zimmer). Und was würdest du tun, um diese Wahrheit zu erfahren? Würdest du dein Leben dafür riskieren?]],
				['19'] = [[(Der ehemalige Forscher dachte darüber nach und starrte mich mit gesenktem Kopf an.)<new_line><new_line>Abgemacht. Du wirst die ganze Wahrheit erfahren. Aber zuerst einmal musst du dein Versprechen halten.]],
				['22'] = {
					['3'] = [[Aber noch bleibt Hoffnung. Alle Wölfe, die wir bisher getötet haben, waren Männchen. Wenn es uns also gelingt, die Wölfin zu töten, werden keine neuen Jungen mehr geboren.]],
					['2'] = [[Wir schützten uns so gut es uns möglich war, aber es wurden immer mehr. Wir hingegen haben immer mehr Menschen verloren. Letzte Woche drang einer von ihnen in des Nachts ein Haus ein und zerfleischten eine ganze Familie. Wir schickten einen ganzen Trupp los, um die Bestie zu töten ... Niemand kehrte wieder zurück.]],
					['1'] = [[Diese Bestien, die schwarzen Wölfe, tauchten hier in unserem Land vor etwa drei Jahren auf. Eine Wölfin und zwei Welpen, riesig, schwarz wie die Nacht und blind. Die ersten beiden Jahre verliefen friedlich. Aber dann war das ganze Wild aus dem Wald verschwunden. Vielleicht haben sich die Wölfe irgendwo mit Tollwut angesteckt. Vielleicht war es auch etwas anderes. Jedenfalls jagen sie seitdem Menschen.]],
					['4'] = [[Wir dürfen nicht länger warten. Die Wölfin hat sich in einem alten Schlachthaus versteckt. Dort werden wir sie angreifen. Es ist unsere einzige Chance, sie endlich zu töten.]]
				},
				['23'] = [[Wir haben es bereits versucht. Ohne Erfolg.<new_line>Also, wirst du es versuchen? Wenn du uns hilfst, wirst du viele Leben retten. Und dann erzählen wir dir alles. Wenn nicht, musst du dich woanders umhören.]],
				['13'] = {
					['1'] = [[(Pavel Gavrilov wartete im Chefbüro des ehemaligen Gemeindezentrums auf mich. Außer ihm war noch jemand im Raum, ein etwa sechzig Jahre alter Mann, der in einem Sessel am Herd vor sich hin döste. Als er mich sah, füllte Gavrilov ein Glas Wasser, rüttelte den alten Kerl wach und gab ihm eine Pille, die er schlucken sollte.)]],
					['2'] = [[Da ist er ja. Fühl dich wie zu Hause. Zuerst einmal möchte ich dir meinen Freund vorstellen. Er würde gerne mit dir sprechen. Timofey Zaitsev, ehemaliger Direktor des Instituts für Virologie. Er wollte wie ich nach Polen fliehen, aber er hat es nicht rechtzeitig geschafft, von hier wegzukommen.]]
				},
				['21'] = [[Nichts Großes. Nimm einfach an der Jagd teil. Du weißt wahrscheinlich nicht, was hier geschehen ist, oder?]],
				['20'] = [[Damals in Kiew sagtest du, dass du uns mit den Wölfen helfen würdest. Also mach es auch. Dann werden wir dir alles erzählen.]]
			}
		},
		['default'] = {
			['choice'] = {
				['1004'] = [[Zur Schlafbaracke gehen]],
				['1003'] = [[Zum Händler gehen]],
				['1005'] = [[Verlassen]],
				['1001'] = [[Umsehen und mit den Einheimischen sprechen]],
				['1002'] = [[Zum Anführer der Siedlung gehen]],
				['1006'] = [[Ich bin bald zurück.]]
