			['name'] = {
				['1'] = [[Händler]],
				['2'] = [[Spezialist]]
			},
			['episode'] = {
				['1001'] = [[Das Leben geht in der Siedlung nur langsam voran. Die Menschen sind mit ihren eigenen Problemen beschäftigt und werfen mir nur manchmal desinteressierte Blicke zu.]],
				['1003'] = [[Wenn du die erforderlichen Materialien hast, kann ich alles herstellen, was du brauchst.]],
				['1005'] = [[Ich kann fertigen:]],
				['1002'] = [[Wenn du etwas hast, was ich brauche, kannst du es gegen etwas tauschen, was du brauchst.]],
				['1004'] = [[Ich kann verkaufen:]]
			}
		},
		['varya'] = {
			['choice'] = {
				['3'] = [[Schön, dich kennenzulernen, Varya. Ich werde dir unterwegs alles erzählen. Und, kannst du alleine gehen?]],
				['2'] = [[Nein, ich bin keiner von ihnen. Sie werden dir nichts mehr tun, ich habe sie getötet. Steh auf, wir müssen hier weg. Kannst du alleine gehen?]],
				['1'] = [[(Befreie sie.)]],
				['7'] = [[Nein, davor. Es gibt ein Dorf mit Überlebenden vor Bryansk? Kannst du mir den Weg zeigen?]],
				['6'] = [[Entschuldige, was hast du gesagt?]],
				['5'] = [[(Hebe Varya hoch und trage sie aus der Scheune.)]],
				['4'] = [[Uff ... Kannst du wirklich nicht alleine gehen?]],
				['10'] = [[(Beende das Gespräch sanft, aber bestimmt.)]],
				['9'] = [[Ähm ... Wir machen es so: Ich bringe dich zu deiner Tante und muss eine Weile verschwinden. Dann kannst du solange nachdenken, wie du es wiedergutmachen willst. Einverstanden?]],
				['8'] = [[Mit mir?!]]
			},
			['name'] = [[Varya]],
			['episode'] = {
				['3'] = [[Du hast sie getötet? (Ihr fiel die Kinnlade nach unten.) Alleine? Gegen sie alle? Das gibt's doch nicht! Wie hast du das geschafft? Ich bin übrigens Varya.]],
				['2'] = [[(Zuerst starrte mich das Mädchen hasserfüllt an, aber dann änderte sich der Ausdruck in ihrem Gesicht.)<new_line><new_line>Hey! Ich kenne dich nicht! Du bist keiner von denen, oder?]],
				['1'] = [[(In der Scheune gab es keine Lichtquellen, außer den einsamen Sonnenstrahlen, die sich durch die Löcher im Dach verirrten. Die Dielen knarrten aus Protest, als ich auf sie trat.<new_line>Die Gefangene saß immer noch in der dunklen Ecke.)]],
				['7'] = [[Ich sagte, dass sie nur neidisch auf mich ist.]],
				['6'] = [[(Während wir zum Wald unterwegs waren, erzählte Varya von ihren Problemen und Sorgen mit den Räubern. Dann kam sie zu der Geschichte über ihre abscheuliche Entführung. Danach erzählte sie mir ausführlich, wie schrecklich ihre Tante gewesen war, mit der sie in einem Dorf außerhalb von Bryansk lebte. Ich habe ihrem Gerede kaum zugehört. Ich war zu sehr mit meinen eigenen Gedanken beschäftigt.)]],
				['5'] = [[Schau selbst.]],
				['4'] = [[(Varya war wackelig auf den Beinen und taumelte. Sie stützte sich an der Wand ab.)<new_line><new_line>Es tut mir leid, meine Beine wollen nicht richtig. Was machen wir denn jetzt? Könntest ... Könntest du mich tragen? Nur für eine Weile, bis ich meine Beine wieder richtig bewegen kann.]],
				['11'] = [[Langeweile und nichts zu tun ... Okay, ich schätze, ich bin auch nicht recht scharf auf Arbeit. Hey, welches Kaliber haben deine Patronen? Große wie die von meinem Onkel? Oder kleiner?]],
				['10'] = [[Zurück zu meiner Tante? Herrje ... Gut. Hey, kannst du mir beibringen, wie man schießt?]],
				['9'] = [[(Ich muss gestehen, dass ich Varya fast vor Schreck fallen ließ.)<new_line><new_line>Ja, warum nicht? Du bist stark, tapfer und kannst mich beschützen. Na ja, du hast dein Leben riskiert, um mich zu retten. Das muss ich irgendwie wiedergutmachen! Was soll ich denn bei meiner Tante? Da langweile ich mich doch zu Tode!]],
				['8'] = [[Klar. Aber weißt du, was ich mir dachte? Ich will nicht zurück zu meiner Tante gehen. Vielleicht kann ich mit dir reisen?]]
			}
		},
		['svetlana'] = {
			['choice'] = {
				['3'] = [[Swetlana, alles was du sagst ist wahr, aber ... An meiner Stelle würdest du auch nach deiner Familie suchen, oder? Ganz egal wie gefährlich es wäre?]],
				['2'] = [[Du hast Recht, aber ich muss es einfach versuchen. Ich kann meine Familie nicht im Stich lassen. Und außerdem gibt es wahrscheinlich noch andere Siedlungen. Ich werde weitersuchen, bis ich herausgefunden habe, was passiert ist.]],
				['1'] = [[Ich habe Frau und Tochter. Ich habe keine Ahnung, wo sie sich aufhalten, aber ich bin fest davon überzeugt, dass sie noch am Leben sind und auf mich warten. Ich muss sie finden.]],
				['7'] = [[Wer ich bin? Ein Herumtreiber. Ich komme aus dem Norden.]],
				['6'] = [[Habe ich wahrscheinlich nicht. Wie dem auch sei, es war schön dich kennenzulernen, Swetlana. Ich hoffe, dass wir uns eines Tages wiedersehen ... (Gehen.)]],
				['5'] = [[Aber du machst doch etwas, oder? Du trägst einen Arztkittel. Du behandelst Menschen. Du hast nicht aufgegeben.<new_line>Und ... hast du aufgegeben, als sich die Räuber gefangen nahmen? Nein, du hast sie vergiftet und dabei auch noch dein eigenes Leben aufs Spiel gesetzt!]],
				['4'] = [[Das tut mir leid. Und ich weiß, dass ich nur eine kleine Chance habe. Aber es ist eine Chance. Ich werde mich auf die Suche machen. Wenn es sein muss, gehe ich bis hinter den Ural, in andere Länder ...]],
				['9'] = [[Ich habe nach dir gesucht. Ich habe dein Aufnahmegerät in Leningrad gefunden. Ich war auch in Nowgorod, in der Basis der Räuber ...]],
				['8'] = [[Swetlana aus Leningrad, richtig? Du hattest die Heilung für die Epidemie gefunden! Dann haben dich die Räuber entführt. Nach deiner Flucht bist du zu Fuß den ganzen Weg bis hierher nach Twer gelaufen, richtig?]],
				['18'] = [[Interessant. Wer war dein Vater?]],
				['28'] = [[Ich verstehe es nicht. Was gibt es noch zu entdecken? Du weißt alles. Die Epidemie brach aus, dann gab es einen Atomkrieg ...]],
				['38'] = [[Dein Vater ...]],
				['14'] = [[Nein, ich muss gehen.]],
				['24'] = [[Swetlana, das ist eine schlechte Idee. Das Ödland ist zu gefährlich, wenn man nicht vorbereitet ist. Ich kann dich nicht beschützen. Und die Leute hier haben ohne dich keine medizinische Versorgung mehr. Du solltest besser bleiben.]],
				['34'] = [[Langsam, aber es geht ... Ich bin nur gekommen, um nach dir zu sehen.]],
				['26'] = [[Ja? Ich höre.]],
				['36'] = [[Bis später.]],
				['30'] = [[Swetlana, das ist verrückt. Wer würde so etwas tun wollen? Und was hätten sie davon?]],
				['40'] = [[Das erste Mal?]],
				['32'] = [[Ich bin mir sicher.]],
				['42'] = [[Ja, ich bin einverstanden.]],
				['12'] = [[Swetlana, ich ...]],
				['22'] = [[In Ordnung, ich werde sehen, was ich tun kann.]],
				['10'] = [[Ich habe nach Überlebenden gesucht. Menschen, die mir die Wahrheit darüber erzählen können, was geschehen ist. Damals, im Jahr 1984. Hör mal, ich erinnere mich an nichts ... Ich habe sogar meinen eigenen Namen vergessen.]],
				['20'] = [[Ich werde es versuchen. Aber ich kann nichts versprechen. Für mich ist am wichtigsten, dass ich meine Frau und meine Tochter finde.]],
				['47'] = [[Wiedersehen (Geh und schließe leise dir Tür hinter dir.)]],
				['46'] = [[Swetlana, hör mir zu ... Ich weiß, dass dir deine Mutter schrieb, dass dein Vater einen Herzinfarkt hatte. Aber vielleicht wollte sie nur nicht ...]],
				['45'] = [[Was meinst du mit „Warum“? Du wolltest die Wahrheit wissen.]],
				['44'] = [[Swetlana?]],
				['17'] = [[Aus Nowgorod, von einer Leiche. Aber da ist noch mehr. Ich habe irgendwo auch eine Sprühdose, darauf steht „Tag R“.<new_line>Weißt du, was das bedeutet?]],
				['16'] = [[17. August ... Moment mal ... Dieses Datum kommt mir irgendwie bekannt vor ... (Zeig Swetlana das Telegramm des Toten aus Nowgorod.)]],
				['29'] = [[Ich kann dir nicht folgen.]],
				['19'] = [[Im Institut für Virologie, sagst du? In Moskau? Das ist meine einzige Spur. Ich gehe also nach Moskau.]],
				['15'] = [[(Nichts sagen.)]],
				['39'] = [[Ja, richtig. Es ist so ... äh ... Warum hatte die junge Frau so große Angst vor mir?]],
				['35'] = [[Hallo. Nein, nichts Neues. Wie läuft es bei dir?]],
				['25'] = [[Es tut mir leid, Swetlana, aber du siehst mir nicht wie jemand aus, der sich keine Sorgen mehr um andere macht. Schließlich wolltest du mich retten.]],
				['37'] = [[Hallo mal wieder, Swetlana.]],
				['27'] = [[Okay, und worum geht es?]],
				['41'] = [[Ach, darum also ...]],
				['31'] = [[Swetlana, selbst wenn du Recht hast und diese Epidemie von Menschen verursacht wurde, selbst wenn es irgendeine Verschwörung sein sollte ... Wie willst du die Wahrheit jemals herausfinden? Seitdem sind so viele Jahre ins Land gestrichen. Es ist Wahnsinn.]],
				['43'] = [[(Erzähl ihr alles.)]],
				['33'] = [[Nicht wirklich, um ehrlich zu sein. Ich habe nur nachgedacht ... Ich möchte noch immer nach Moskau reisen, zum Institut für Virologie. Dort werde ich nach Spuren suchen. Wer weiß, womöglich hilft mir das bei meiner eigenen Suche weiter.]],
				['23'] = [[Fass das bitte nicht falsch auf, aber ich ... bin mir nicht sicher, ob ich die Zeit dafür habe ...]],
				['13'] = [[Swetlana, ich danke dir. Vielen Dank für das Angebot, aber ich kann nicht bei euch bleiben.]],
				['21'] = [[Nein, warte eine Sekunde. Ich habe noch nichts entschieden. Und letztendlich ist es für mich am wichtigsten, dass ich meine Frau und meine Tochter finde.]],
				['11'] = [[Ich sage dir doch, dass ich es nicht weiß. Ich bin in Nikel zu mir gekommen. Ich hatte keine Ahnung, wer ich war, woher ich kam, oder wie ich an diesen Ort gekommen war. Überall um mich herum lagen verfaulende Leichen. Überall nur Ruinen ... Ich begann, nach Antworten zu suchen. Nach Menschen, die mir all das erklären könnten. Aber niemand weiß, was passiert ist, oder warum.]]
			},
			['name'] = [[Swetlana]],
			['episode'] = {
				['3'] = [[Sicher gibt es andere Siedlungen, aber warum sollte ich Twer verlassen? Wir haben genug Lebensmittel und die Banditen lassen uns in Ruhe. Sei kein Idiot, bleib hier!]],
				['2'] = [[(Swetlana drehte sich weg.)<new_line><new_line>Schau aus dem Fenster. Es ist ein Ödland. Erst die Epidemie, dann der Atomkrieg ... Die Erde ist unfruchtbar. Da draußen überlebt niemand. Strahlung, Raubtiere. Verrückte, die nichts mehr zu verlieren haben. Räuber. Du wirst dort niemanden finden. Und wenn du gehst, wirst du nicht wieder zurückkehren. Wir wollen dich hier nicht festzuhalten, aber denk doch mal einen Augenblick nach!]],
				['1'] = [[(Die Frau blickte mich ihren traurigen grauen Augen an und lächelte freundlich.)<new_line><new_line>Hallo. Wir haben schon lange kein neues Gesicht mehr gesehen. Und wer bist du? Woher kommst du?]],
				['7'] = [[Echt? (die Frau ist erstaunt) Das ist eine lange und gefährliche Reise. Nun, komm rein, mach es dir gemütlich. Ich bin übrigens Swetlana.]],
				['6'] = [[(Swetlana fasste sich mit den Händen an den Kopf.)<new_line><new_line>Du verstehst es nicht ...]],
				['5'] = [[(Swetlana blickte mir lange direkt in die Augen und wandte dann den Blick ab.)<new_line><new_line>Ich habe ebenfalls gekämpft, so lange es mir möglich war. Ich hatte geglaubt, dass wir noch etwas ausrichten, dass wir wieder in Ordnung bringen könnten, was wir angerichtet hatten ...]],
				['4'] = [[(Swetlana sah mich traurig an.)<new_line><new_line>Weißt du, als ich hierherkam, wollte ich mich ebenfalls auf die Suche nach meinen Verwandten machen. Und andere Überlebende finden. Ich suchte nach einem Weg, meine Kollegen zu kontaktieren, um mehr über diese verfluchte Epidemie, über das Bombardement herauszufinden. Ich reiste von Stadt zu Stadt, bis ich keinen Treibstoff mehr hatte. Aber alles war umsonst. Alle meine Bemühungen haben sich in Rauch aufgelöst!]],
				['9'] = [[Okay, aber ... Warum hast du nach mir gesucht?]],
				['8'] = [[(Swetlana runzelte die Stirn.)<new_line><new_line>Ja, das stimmt. Aber woher weißt du das alles?]],
				['18'] = [[„Aktivierung von Tag R am 17. August wie angewiesen“ ... Was ist das? Wo hast du das gefunden?]],
				['28'] = [[Ich muss die Wahrheit aufdecken. Die Wahrheit über die Katastrophe.]],
				['38'] = [[(Als ich an Swetlana Tür klopfte, machte sie nicht sofort auf.<new_line>Eine junge Frau saß auf der kleinen Couch im Büro. Als sie mich sah, wurde sie verlegen, dankte Swetlana für irgendetwas und rannte aus dem Zimmer.)<new_line><new_line>Das dumme Mädchen! Also, was gibt es Neues?]],
				['14'] = [[(Die Tür zu Swetlanas Zimmer ist geschlossen. Aber es sieht so aus, als wäre jemand im Raum. Ich könnte klopfen, aber ich glaube, dass sie einfach nur ein wenig alleine sein will.)]],
				['24'] = [[(Swetlana runzelte die Stirn, dann drehte sie sich um und ging zum Fenster.)<new_line><new_line>Dann komm zurück, wenn du die Zeit dazu findest.]],
				['34'] = {
					['1'] = [[(Swetlanas Lächeln zeigte eine unverhohlene Freude.)]],
					['2'] = [[Ich gehe jetzt und bereite meinen Aufbruch vor. Wartest du noch?]]
				},
				['26'] = [[(Swetlana lächelt seltsam.)<new_line><new_line>Was ist damit? Ich riskiere nichts. Okay, verdammt, wenn du gehen willst – dann geh. Aber wenn du schon gehst ...<new_line><new_line>(Sie denkt kurz nach und wählt ihre Worte mit Bedacht.)]],
				['36'] = [[(Swetlana grüßt mich lächelnd.)<new_line><new_line>Hallo! Und? Gibt es Neues?]],
				['30'] = {
					['1'] = [[(Swetlana spricht noch leiser.)<new_line><new_line>Die Epidemie! Die Epidemie brach überall gleichzeitig aus, in verschiedenen Städten, an entgegengesetzten Enden der UdSSR! Kein Virus in der Geschichte der Menschheit hat sich je so verbreitet. Man kann jede Krankheit eingrenzen und ihren Ursprung ermitteln, man kann Quarantänezonen einrichten, vorbeugende Maßnahmen ergreifen. Zumindest kann man es versuchen. Aber nicht bei dieser Krankheit.]],
					['2'] = [[Ich habe es überprüft. Der Virus hat alle Menschen befallen und ist überall gleichzeitig aufgetreten. Es gab keine Möglichkeit, ihn aufzuhalten.<new_line>Es klingt verrückt, aber könnte jemand die Epidemie absichtlich ausgelöst haben? Vielleicht wurden Menschen in verschiedenen Städten zur gleichen Zeit infiziert?]]
				},
				['40'] = {
					['3'] = [[(Nach einer Weile beruhigte sich Swetlana ein wenig. Sie wischte sich die Nase mit einem Taschentuch ab und setzte sich auf die Couch. Ich wollte mich neben sie setzen, aber sie wehrte mit einer Handbewegung ab, es nicht zu tun. Ich nickte und ging zur Tür. Swetlana musste jetzt über eine Menge Dinge nachdenken. Und dabei konnte ihr nicht helfen.)<new_line><new_line>Warte ...]],
					['2'] = [[(Swetlana lockerte ihren Griff und begann zu schluchzen. Bittere, unglückliche Schluchzer, die sich wie eine Eisenklammer um meine Brust legten und mir schmerzhaft die Luft abschnürten. Sie weinte. Und ich sah sie wie ein Trottel an und wusste nicht, wie ich sie trösten sollte. Dann machte ich mir schweigend Vorwürfe, dass ich keine überzeugende Lüge gefunden hatte, anstatt Swetlana mit der Wahrheit zu verletzen.)]],
					['1'] = [[(Plötzlich packte mich Swetlana am Hemd. Ich zuckte zurück.)<new_line><new_line>Es reicht! Erwähne meinen Vater nicht! Er war ein wunderbarer Mensch! Er liebte mich, er liebte meine Mutter, er liebte sein Land! Er starb an einem Herzinfarkt, okay?! Meine Mutter hat es mir geschrieben! Ein Herzinfarkt, hörst du ... Ein Herzinfarkt ...]],
					['4'] = [[Vielen Dank, dass du die Wahrheit aufgedeckt hast. Es tut mir leid, dass ich dich angeschrien habe. Du hast keinen Fehler gemacht. Wie du gesagt hast: Es ist mein Schmerz, und es ist mein Problem. Ich allein muss damit fertigwerden.<new_line>Vielen Dank nochmal und ... Wiedersehen.]]
				},
				['32'] = [[(Swetlana springt auf und sieht mich an. Eine seltsame Wut brennt in ihren Augen.)<new_line><new_line>Das weiß ich! Aber es ist nicht weniger wahnsinnig, da hinaus in das Ödland zu gehen, um nach deiner Familie zu suchen!]],
				['42'] = [[Das erste Mal, du weißt schon. Hast du es denn nicht bemerkt? Sie ist schwanger!]],
				['12'] = [[Du musst hier aber für das Wohl des Kollektivs arbeiten. Wir führen ein strenges Regiment: Jeder muss seinen Job erledigen, aber es ist immer noch viel zu tun. Wir könnten noch jemanden brauchen, der uns hilft.]],
				['22'] = {
					['1'] = [[Ich verstehe. Aber du musst mich ebenfalls verstehen. Ich habe keine nahen Verwandten mehr. Außerdem braucht niemand mehr das Heilmittel für diese Krankheit. Was bleibt mir noch? Überleben. Ich will nur noch ein wenig länger am Leben bleiben. Aber diese Art von Existenz kotzt mich an!]],
					['2'] = [[Aber wir können eines tun: die Wahrheit herausfinden. Herausfinden, wer daran schuld ist, dass Millionen von Menschen getötet und unser Land – vielleicht sogar die ganze Welt – in ein Ödland verwandelt wurde. Und wir können herausfinden, warum sie es getan haben. Darum geht es doch, nicht wahr? Nicht nur für mich persönlich, sondern für uns alle, klar?<new_line>Ich weiß, dass du es nicht tun musst. Ich kann dich nur darum bitten. Und eine Belohnung anbieten.]]
				},
				['10'] = [[Du erinnerst dich an nichts? Du weißt nicht, was geschehen ist?]],
				['20'] = [[Er war Mikrobiologe. Einer der führenden Forscher des Landes. Er arbeitete in Moskau, am Institut für Virologie. Da habe ich ihn zum letzten Mal gesehen. Er starb im Juni 1984 an einem Herzinfarkt. Meine Mutter hat es mir am Telefon mitgeteilt. Ich konnte mich nicht einmal von ihm verabschieden. Ich war nicht einmal auf seiner Beerdigung. Ich lag mit einer Mandelentzündung krank im Bett.]],
				['48'] = [[Geh, bitte. Ich habe etwas zu erledigen.]],
				['47'] = [[Ja, die Wahrheit. Aber du erzählst mir hier eine widerliche Lüge nach der anderen. Ich weiß nicht, wer dahintersteckt, aber ich hoffe, dass du es nicht bist. Aber ich will das nicht mehr hören.]],
				['46'] = [[(Swetlana drehte sich zu mir. Ihre Augen waren vollkommen teilnahmslos, aber ihre Hände zitterten.)<new_line><new_line>Und warum erzählst du mir das alles?]],
				['45'] = [[(Als ich geendet hatte, bemerkte ich, dass Swetlana mit ausdruckslosem Gesicht auf die Wand hinter mir starrte. Ich hatte eine Reaktion erwartet, aber nicht das. Ich bewegte meinen Kopf, um ihre Aufmerksamkeit zu erregen, aber sie reagierte nicht auf mich. Es folgte angespannte Stille.)]],
				['44'] = {
					['1'] = [[In Ordnung. Du hast mir offenbar etwas zu sagen. Kommen wir gleich zur Sache. Hast du etwas erfahren? Über das, worüber wir gesprochen haben?]],
					['2'] = [[(Als ich Swetlanas konzentriertes, aber glückliches Gesicht sah, stockte ich und wusste nicht, wie ich anfangen sollte. Ich murmelte etwas Unverständliches. Ich war plötzlich heiser. Swetlana gab mir ein Glas Wasser. Ich leerte es in einem Zug und traf eine Entscheidung.)]]
				},
				['17'] = [[(Swetlana beruhigt sich und senkt wieder die Stimme.)<new_line><new_line>Andererseits wissen wir immer noch etwas. Die ersten Aufzeichnungen über die tödlichen Ausbrüche stammen vom 2. und 3. September. Wenn man die Inkubationszeit berücksichtigt, denke ich, dass sich diese Menschen zwischen dem 17. und 19. August infiziert haben. Das heißt ...]],
				['16'] = [[(Swetlana nickte langsam. Meine Antwort hatte sie nicht überrascht. Aber warum war sie so besorgt?)<new_line><new_line>Du tust mir leid, Herumtreiber. Menschen wie du sterben zuerst. Menschen, die lieben. Die ein Risiko eingehen, um andere zu retten. Ich dachte eigentlich, dass alle Menschen wie du 1984 gestorben wären. Dass es inzwischen nur noch Räuber und Menschen wie mich gibt, die aufgehört haben, sich umeinander zu kümmern.]],
				['29'] = [[Du hast mein Tagebuch gelesen, habe ich recht? Auf einer der Seiten, die ich verloren habe, habe ich eine seltsame Idee niedergeschrieben. Ich weiß noch immer nicht, warum ich nicht früher daran gedacht habe. Aber diese Idee ... Ich liege wegen ihr nachts wach.]],
				['19'] = [[Nein, ich weiß nicht. Aber ich glaube, ich habe diesen Namen schon einmal gehört. Von meinem Vater. Als er im Institut zu Besuch war, sprach er mit jemandem am Telefon und erwähnte diesen „Tag R“. Ich habe ihn gefragt, was das bedeutet, aber er hat es mit einem Witz überspielt. Ich wusste jedoch, dass ich das nicht vergessen darf.]],
				['15'] = [[(Als ich aufwachte, stand Swetlana neben mir. Sie sah mich erwartungsvoll an und kaute nervös auf ihrer Unterlippe.)<new_line><new_line>Hast du deine Meinung geändert? Wirst du vielleicht bleiben?]],
				['39'] = [[Lass die Formalitäten. Schließlich sind wir jetzt alte Freunde, nicht wahr?]],
				['35'] = [[Es geht mir gut.<new_line><new_line>(Swetlanas Tonfall ist kalt und sachlich. Sie ist eindeutig verärgert.)]],
				['25'] = [[Bist du sicher?]],
				['37'] = [[Sehr gut. Wenn man gegen die Verzweiflung und die Hoffnungslosigkeit ankommt.]],
				['27'] = [[(Swetlana spricht leiser, als hätte sie Angst, dass uns jemand belauscht.)<new_line><new_line>Es gibt etwas, was du tun kannst. Ich habe vor langer Zeit damit angefangen, es aber niemals beendet. Ich konnte es nicht und habe nach der Hälfte aufgegeben. (Swetlana verzieht das Gesicht.). Aber du könntest es schaffen!]],
				['41'] = [[(Swetlana lachte wieder. Sie war in guter Stimmung.)<new_line><new_line>Achte nicht darauf. Sie ist nicht gut drauf. Es ist für sie das erste Mal.]],
				['31'] = [[Ich weiß es nicht. Das will ich herauszufinden, seit mich die Räuber gefangen genommen haben. Ich wollte eine vernünftige Erklärung finden. Dann habe ich hier die anderen Flüchtlinge befragt, außerhalb von Twer. Ich durchforstete die Zeitungen, las die Geschichten über die Krankheit ...]],
				['43'] = [[Nicht nur sie freut sich darüber. Wir alle sind ganz aus dem Häuschen. Wir haben eine Epidemie und einen Atomkrieg überlebt. Und anschließend jahrelang gegen den Hunger gekämpft! Es gibt allerdings einen Feind, der viel schlimmer ist als der Hunger, und zwar die Hoffnungslosigkeit. Und weißt du was? Heute hat der Feind nicht gewonnen. Es ist das erste Baby nach unendlich langer Zeit. Dieses Kind bedeutet, dass wir eine Zukunft haben!]],
				['33'] = [[(Als sie mich sah, lächelte Swetlana kurz, ehe sie dieses Verlangen unterdrückte und einen Ausdruck kühler Distanziertheit aufsetzte.)<new_line><new_line>Nun, wie läuft es mit deiner Suche nach Frau und Tochter?]],
				['23'] = [[Ich gehe jetzt und bereite meinen Aufbruch vor. Wartest du noch?]],
				['13'] = [[(Swetlana schwieg, dann stand sie auf und ging zum Fenster.)<new_line><new_line>Warum nicht?]],
				['21'] = [[(Swetlana hielt den Atem an.)<new_line><new_line>Du willst mir also helfen?]],
				['11'] = [[(Swetlana sah mich mitfühlend an.)<new_line><new_line>Das ist schrecklich! Es ist ein Wunder, dass du es überhaupt so weit geschafft hast! (Sie lächelte freundlich.) Aber jetzt liegt das alles hinter dir. Wir kümmern uns um dich. Wir geben dir ein Zimmer und zu essen. Und mit der Zeit werden deine Erinnerungen garantiert zurückkommen.]]
			}
		},
		['spiridon'] = {
			['choice'] = {
				['3'] = [[Dass es keinen Grund gibt, die cleveren Ingenieure zu töten, die in diesem Land so dringend gebraucht werden ... Es ist schade. Ich werde es alleine schaffen müssen.]],
				['2'] = [[(Manipulieren.)<new_line>Schade. Boris hatte offenbar recht. Du wirst alt, Ingenieur.]],
				['1'] = [[Und wenn ich dir ein wenig Wodka besorge? Echt gutes Zeug, aus einer Brennerei.]],
				['6'] = [[Moment mal, du hast mich mit jemandem verwechselt. Ich möchte deinen Ehemann sprechen.]],
				['5'] = [[Das ist schon lange her, Kamerad. Alter, trink ...]],
				['4'] = [[Boris, der Major von Stepnoy. Wir stritten darüber, ob Kugel, der Ingenieur, der vom Regionalkomitee offiziell geehrt worden war, eine Panzerabwehrlenkwaffe bauen könnte. Und Boris meinte, du hättest es nicht mehr drauf.]],
				['9'] = [[Hey, schlag ihn nicht, er sagt die Wahrheit! Ich bin geschäftlich hier. Ich brauche Hilfe.]],
				['17'] = [[Immer mit der Ruhe.]],
				['16'] = [[Nach Ufa? Okay, wie du meinst.]],
				['15'] = [[Abgemacht!]],
				['14'] = [[Und wo finde ich meinen Armeeingenieur?]],
				['24'] = [[Vier. Verdammt. Ich bringe sie, sobald ich kann.]],
				['25'] = [[Okay, okay. Ich werde ihr nichts sagen.]],
				['26'] = [[Hallo.]],
				['27'] = [[Nein, Spiridon, tut mir leid. Ich habe ihn noch nicht. Du weißt selbst am besten, dass man dieses Zeug nicht einfach am Straßenrand findet.]],
				['28'] = [[Ja, hier, nimm. Wie abgemacht, mit Zinsen.]],
				['19'] = [[Vielen Dank, du hast mir wirklich sehr geholfen.]],
				['22'] = [[Hör mal, es tut mir leid. Ich habe ihn nicht dabei. Ich werde ihn dir das nächste Mal geben.]],
				['18'] = [[Los jetzt, gehen wir.]],
				['23'] = [[Hier, drei Flaschen. Wie versprochen.]],
				['13'] = [[Ich weiß nicht, wovon du redest. Ich brauche wirklich deine Hilfe. Ich muss etwas finden und zusammenbauen. In Ufa.]],
				['21'] = [[Ich werde es ihm sagen.]],
				['20'] = [[Vielen Dank, du hast mir wirklich sehr geholfen.]]
			},
			['name'] = [[Spiridon Kugel]],
			['episode'] = {
				['3'] = [[Boris wer? Womit hatte er recht?]],
				['2'] = [[(Kugel befeuchtet seine Lippen und schmatzte.)<new_line><new_line>Aus einer Brennerei? Das ist gutes Zeug. Unser hausgemachter Wodka ist Dreck dagegen.<new_line>Okay, kommen wir zur Sache. Drei Flaschen, das ist der Deal. In der Zwischenzeit muss ich mir überlegen, wo ich sie vor Lyuba verstecken kann.]],
				['1'] = [[(Die Kugels wohnten im ersten Stock des fünfstöckigen Blocks. Eine sehr kräftige Frau mit einem grimmigen Gesichtsausdruck öffnete mir die Tür.)<new_line><new_line>Wieder da! Raus hier, du verdammter Säufer! Es reicht! Ich sage dir zum letzten Mal, dass er nirgendwo hingeht!]],
				['7'] = [[Ich weiß, warum du hier bist! Um dich zu besaufen und herumzupöbeln, darum bist du hier! Du wirst jetzt von hier verschwinden. Oder muss ich nachhelfen? Das werde ich! Ich werde ...]],
				['6'] = [[Unsinn! Man verlernt sein Handwerk nicht! Und in meinem Arbeitsgebiet ist Alter nur ein Vorteil. Schau dir doch die Jugend von heute an, das sind doch alles unfähige Trottel, jeder einzelne von ihnen!<new_line>Mach dich bereit, wir gehen nach Ufa!]],
				['5'] = [[Raketensystem? Wie bitte? Ich kann mit geschlossenen Augen einen Panzer reparieren! Ich wurde sogar einmal nach Sewerodwinsk eingeladen, um das Atom-U-Boot zu bauen!]],
				['4'] = [[Ach ... Es tut mir leid ... Du verstehst schon ... Ich kann Lyuba nicht alleine hierlassen ... Aber sag Bescheid, wenn sich etwas ergibt.]],
				['17'] = [[Schau dir das an. Wunderschön, nicht wahr? Das Dreibein ist ein wenig schief. Da musst du einen Ziegelstein darunterlegen. Das Zielsystem ist nicht ganz mittig, aber das ist ein Konstruktionsfehler. Jede Charge hatte das gleiche Problem. Und es sitzt nicht wirklich fest in den Rillen. Man muss sanft dagegendrücken. Und noch etwas ...]],
				['23'] = [[(Nach einer kurzen, aber unangenehmen Unterhaltung mit Lyuba konnte ich erneut mit Spiridon reden.)]],
				['9'] = [[Oh, jetzt verstehe ich! Er kennt dich auch nicht! Hältst du mich wieder zum Narren?<new_line><new_line>(Lyuba Kugel gab ihrem Mann eine schallende Ohrfeige.)]],
				['8'] = [[(Ein viel kleinerer Mann schielt hinter der großen Frau hervor. Er hat ein intelligentes Gesicht, das von Sorgenfalten durchzogen ist.)<new_line><new_line>Lyuba, warte mal. Ich kenne ihn nicht. Er ist nicht aus unserer Siedlung.]],
				['18'] = [[Ach, du kannst Boris übrigens erzählen, dass die alten Meister noch arbeiten! Er kann mich besuchen kommen. Lyuba wird ihm einen Kuchen backen und wir können uns zusammensetzen und in Erinnerungen über die alten Zeiten schwelgen.]],
				['16'] = [[Komm schon, beeil dich. Weiß du nicht, was meine Frau mit mir anstellen wird?]],
				['15'] = [[Nun, vergib mir. Meine Lyuba, sie ist kein Sonnenschein, aber sie ist einzigartig, und es gibt jede Menge dieser Bienen. Ich werde nicht nach Ufa gehen, niemals!]],
				['14'] = [[In Ordnung, ich dachte nur, dass du mich an einen alten Kumpel erinnerst ... Warte mal, was hast du eben gesagt? Ufa? Da sind lauter Bienen!<new_line>Auf keinen Fall! Ich gehe doch nicht nach Ufa. Ich würde gerne noch ein wenig weiterleben! Such dir einen anderen Trottel.]],
				['24'] = [[(Spiridon grüßte mich und sprach dabei absichtlich ein wenig lauter, lugte dabei schief auf die Tür, hinter der sich Lyuba versteckte.)<new_line>Hallo, hallo, hallo!<new_line>(Dann senkte er seine Stimme und sprach weiter.)<new_line>Nun, hast du den Wodka dabei? Vier Flaschen, nicht wahr?]],
				['25'] = [[Verstanden ... Okay, ich werde noch ein wenig warten.]],
				['26'] = [[Viel Glück. Wie geht's? Nun, man sieht sich. Lyuba ist heute nicht sehr gut aufgelegt. Sie macht uns beiden die Hölle heiß, wenn sie uns in die Finger kriegt.]],
				['27'] = [[Du schon wieder? Ich habe dir schon gesagt, dass ich nirgendwohin gehe, keine Chance. Auf keinen Fall!]],
				['20'] = [[Du musst mich nicht zurückbringen. Ich habe in der Garage ein Motorrad gesehen. Fast neu, eine limitierte Auflage. Damit komme ich viel schneller nach Hause. Ich habe ein paar Sachen verbessert. Jetzt können mich die Bienen nicht mehr einholen. Bis bald!]],
				['22'] = [[Danke! Beste Qualität! Damit kann ich die Jungs morgen verwöhnen. Sei so nett und sag meiner Frau nichts davon. Mein Schatz hat einen deftigen rechten Haken. Wer hätte das gedacht? Und sie war einst süß wie ein Lamm. Ach, die gute alte Zeit ...]],
				['21'] = [[(Kugel schüttelte vorwurfsvoll den Kopf.)<new_line><new_line>In Ordnung, Kamerad, dann das nächste Mal. Ich soll das System doch nicht wieder auseinanderbauen, oder? Allerdings schuldest du mir jetzt schon vier Flaschen.]],
				['19'] = [[Ich arbeite nicht für einen freundlichen Händedruck. Bring mir den Wodka.]],
				['12'] = [[(Die Frau schlägt ihren Mann erneut, so hart, dass er vor Scherz aufschreit.)<new_line><new_line>Da hast du deinen Dank! Und du kannst dich verziehen, du Mistkerl, du Schwein. Er hat mein ganzes Leben ruiniert. Ich habe ihm die besten Jahre meines Lebens geschenkt! Meine Mutter – Gott hab sie selig – hatte Recht. Ich hätte Ptichkin heiraten sollen ... Dann hättest du dich wahrscheinlich zu Tode gesoffen!]],
				['13'] = [[(Lyuba schlug die Tür zu uns schloss sich in der Küche ein. Spiridon Kugel sah mich stirnrunzelnd an.)<new_line><new_line>Aus irgendeinem Grund ist sie schon den ganzen Morgen auf hundertachtzig. Okay, sag unseren Leuten, dass ich heute nicht rauskomme. Ich kann nicht, das siehst du doch selbst. Aber morgen, versprochen!]],
				['10'] = [[Hilfe? Wozu soll dieser Säufer noch gut sein? Zu rein gar nichts. Er hockt den ganzen Tag nur im Haus herum! Spiridon, geh zurück in die Küche!]],
				['11'] = [[Was meinst du damit, wozu ich noch gut bin? Das Land braucht immer gute Ingenieure! Ich habe sogar ein Dankesschreiben von Regionalkomitee!]]
			}
		},
		['nadezhda'] = {
			['choice'] = {
				['10'] = [[Ich wollte dir nur helfen.]],
				['14'] = [[Hmm. Ich habe es noch nicht zu ihm geschafft.]],
				['3'] = [[Du hast mein Mitgefühl ...]],
				['2'] = [[Ich kann die Leiche deines Sohns finden und sie zu dir bringen.]],
				['1'] = [[Nadezhda? Hab keine Angst, ich werde dir nichts tun.]],
				['11'] = [[Jemand ist verschwunden, der dir sehr nahestand. Wer?]],
				['7'] = [[Nadezhda, hör mich an, es ist wichtig ...]],
				['6'] = [[Ich wollte dich etwas fragen. Es geht um deinen Mann.]],
				['5'] = [[Entschuldige die Störung. Ich brauche wirklich deine Hilfe. Hast du kurz für mich Zeit? Dann werde ich gehen und dich in Ruhe lassen.]],
				['4'] = [[Ja, ich werde ihn zu dir bringen.]],
				['12'] = [[Was ist mit ihm passiert?]],
				['13'] = [[Nichts ... Entschuldige.]],
				['9'] = [[(Warte weiter.)]],
				['8'] = [[(Zieh dich zur Tür zurück und warte.)]]
			},
			['name'] = [[Nadezhda]],
			['episode'] = {
				['3'] = [[(Nadezhda sieht mich skeptisch an.)<new_line><new_line>Du, du machst das wirklich? Du wirst mich und Gena wieder vereinen?]],
				['2'] = [[(Die alte – nun ja, die vorzeitig gealterte – Frau zuckte nicht zusammen. Sie sah mich mit müden, grauen Augen misstrauisch an und sprach kein Wort.)]],
				['1'] = [[(In dem kleinen Haus war es kalt und dunkel. Auf einer alten Matratze saß eine kleine, grauhaarige Frau und lehnte sich mit dem Rücken gegen die Wand. Das muss Nadezhda Kulik sein. Auf der gegenüberliegenden Seite des Raums lag auch eine Matratze, aber dort war niemand.<new_line>Ich ging zu Nadezhda.)]],
				['7'] = [[(Bei der Erwähnung ihres Mannes zuckte Nadezhda zusammen. Dann runzelte sie die Stirn und wandte sich von mir ab.)<new_line><new_line>Geh, bitte.]],
				['6'] = [[Wer bist du? Was willst du von mir?]],
				['5'] = [[(Nadezhda konnte wahrscheinlich kaum glauben, dass ein Fremder ihr helfen wollte, während sie von allen anderen Bewohnern des Lagers zurückgewiesen worden war. Aber sie riss sich zusammen und sprach dann flüsternd.)<new_line><new_line>Nun, dann ... Komm möglichst bald wieder. Und sei vorsichtig.]],
				['4'] = [[Geh, bitte ...]],
				['9'] = [[(Nadezhda saß einige Minuten lang vollkommen reglos da. Dann stand sie auf, wobei sie ihre Mühe hatte, und schenkte sich ein wenig Wasser aus einem Krug ein. Da erst bemerkte ich, dass zwei Becher auf dem Tisch standen.<new_line>Auf dem zweiten Becker war ein Bild eines Zeichentrick-Löwenjungen. Unter dem Kissen auf der zweiten Matratze lugte ein Jugendmagazin hervor.)]],
				['8'] = [[(Die Lippen der Frau zitterten. Sie sah mich wütend an.)<new_line><new_line>Wichtig? Hättest du dir keinen besseren Zeitpunkt aussuchen können? Raus hier!]],
				['17'] = [[Oh, du bist es. Entschuldige bitte, ich bin beschäftigt. Aber nochmal danke für alles ...]],
				['16'] = [[Oh, nun ja, komm ... einfach schnell zurück.]],
				['15'] = [[Oh, du bist es. Was ist mit Gena?]],
				['14'] = [[Du schon wieder? Was willst du von mir?]],
				['12'] = [[(Der Mann dieser Frau kennt die einzige Spur, durch die ich die Wahrheit zu längst vergangenen Ereignissen finden könnte. Ich muss sie zum Reden bringen.)<new_line><new_line>Gena. Sohn.]],
				['13'] = {
					['1'] = [[(Nadezhda seufzte und wischte sich mit einem Taschentuch die Nase ab. Erneut herrschte eine angespannte Stille. Schließlich sah mich Nadezhda wieder an.)<new_line><new_line>Er ging mit einem seiner Freunde, Vanya, nach Nowomoskowsk. Vor zwei Tagen, um Lebensmittel zu besorgen. Aber Vanya kam alleine zurück. Er sagte, das ganze Gebäude wäre eingestürzt und Gena ums Leben gekommen ...]],
					['2'] = [[Ich habe nicht viel verlangt. Ich wollte nur meinen Sohn begraben. Sein Gesicht ein letztes Mal sehen, weißt du? Ich flehte unsere Leute an, seine Leiche zu bergen, aber sie weigerten sich. Warum das Leben für eine Leiche riskieren? Das wäre es nicht wert, sagten sie. Ich flehte sie weiter an, aber es bestand keine Hoffnung.]]
				},
				['10'] = [[(Ich erinnerte mich an die feindlichen Blicke der Lagerbewohner. Hatten sie etwas mit Nadezhdas vermisstem Mitbewohner zu tun? Nachdem Nadezhda ein paar Schluck Wasser getrunken und sich ein wenig beruhigt hatte, drehte sie sich zu mir um.)<new_line><new_line>Du bist immer noch hier? Was starrst du mich so an? Lass mich alleine!]],
				['11'] = [[Wenn du mir helfen willst, dann geh, jetzt!]]
			}
		},
		['sasha'] = {
			['choice'] = {
				['3'] = [[Sasha?]],
				['2'] = [[(Das Mädchen erschießen.)]],
				['1'] = [[Hab keine Angst vor mir. Ich werde dich von hier wegbringen!]],
				['7'] = [[Man ... Man hat mir gesagt, du wurdest gefangen genommen. Ich ... Ich wollte dich ... retten ...]],
				['6'] = [[Sasha, es tut mir leid, vergib mir ...]],
				['5'] = [[Sasha, ich ... Ich weiß, dass es meine Schuld war. Ich habe es zu verantworten, dass du und deine Mutter und ...]],
				['4'] = [[Sasha ... Ich habe so lange nach dir gesucht ...]],
				['9'] = [[Sasha ...]],
				['8'] = [[Sasha ... Warte ...]],
				['18'] = [[Wer hat den Schlüssel für das Gefängnis?]],
				['28'] = [[Leise!]],
				['38'] = [[Hat er dich unter Druck gesetzt?]],
				['14'] = [[Du hast es nicht tun können.]],
				['24'] = [[Hör auf mit den Witzen. Sag mir, was passiert ist, nachdem du die Räuber in Großvaters Haus getötet hattest.]],
				['34'] = [[Was würde ich nicht verstehen?]],
				['26'] = [[Habt ihr den Angriff zurückgeschlagen?]],
				['36'] = [[Wirst du mich töten?]],
				['30'] = [[Du hast mir immer noch nicht alles erzählt.]],
				['40'] = [[Hast du angefangen, ihm zu vertrauen?]],
				['32'] = [[Bist du froh, dass du dich so entschieden hast?]],
				['42'] = [[Und du wolltest ein Wolf werden? Einer dieser Räuber?]],
				['12'] = [[Du hättest nicht hoffen, sondern schießen sollen, als du die Chance dazu hattest. Aber du konntest es nicht.]],
				['22'] = [[Über was? Vielleicht darüber, dass du mich töten wolltest und ich dich vor einem Exekutionskommando gerettet habe?]],
				['10'] = [[Sasha?]],
				['20'] = [[Du kommst heute Abend hier raus. Sei bereit.]],
				['44'] = [[Und Ruslan hat sie freigelassen?]],
				['17'] = [[Aber warum ...]],
				['16'] = [[Deine Arme. Haben sie dich geschlagen?]],
				['29'] = [[(Lass sie in Ruhe.)]],
				['19'] = [[Wer hat den Schlüssel für das Gefängnis?!]],
				['15'] = [[Wer hat das getan?]],
				['39'] = [[Hast du nicht bemerkt, dass er dich auf seine Seite ziehen wollte?]],
				['35'] = [[Ist das nicht ein wenig gewagt? Es sind nur noch drei oder vier von euch übrig.]],
				['25'] = [[Und danach?]],
				['37'] = [[Er sprach mit dir über den Kampf?]],
				['27'] = [[Boris hat mir nie erzählt, dass du alleine zurückgelassen wurdest.]],
				['41'] = [[Er ist abscheulich! Ein Verbrecher! An seinen Händen klebt das Blut unschuldiger Menschen!]],
				['31'] = [[Das ist verrückt ...]],
				['43'] = [[Wovon sprichst du?]],
				['33'] = [[Da liegst du falsch. Ich verstehe, was du durchgemacht hast. Du wolltest Respekt und Anerkennung. Du wolltest frei sein. Und das haben dir deine neuen Freunde ermöglicht.]],
				['23'] = [[Das dachte ich mir schon. Wenn du nicht darüber sprechen möchtest, dann erzähl mir alles, was mit dir passiert ist.]],
				['13'] = [[Du konntest mich nicht töten. Ich erinnere mich nur noch an die Stille. Du hast gezögert. Du hättest mich einfach umbringen können, aber du hast gezögert. Dann kam Boris und hat dich verwundet.]],
				['21'] = [[Können wir uns unterhalten?]],
				['11'] = [[Ich kann dir bei der Flucht helfen.]]
			},
			['name'] = [[Sasha]],
			['episode'] = {
				['3'] = {
					['1'] = [[(Die Wut verschwand aus ihrem Gesicht. Das Mädchen erlitt einen Hustenanfall, verkrampfte sich, nickte und senkte die Klinge. Ich ging zu ihr und streckte meine Hand aus.)<new_line>Plötzlich stach sie mit dem Messer auf mich ein.<new_line>Ich keuchte, ließ meine Waffe fallen und fiel auf den Boden. Das Mädchen sprang auf und trat mir ins Gesicht.]],
					['2'] = [[Typen wie du lernen es nie. Ich bin ein Mädchen, und deswegen bin ich wohl ein hilfloses Opfer, was?]]
				},
				['2'] = [[Komm nicht näher! Noch ein Schritt und ich schlitze dir den Bauch auf!]],
				['1'] = [[(Ich rannte in das oberste Stockwerk. Die Körper unserer Kämpfer waren zerfetzt worden, und unter ihnen lag ein verletztes Mädchen. Ihr Gesicht war mit einer Mischung aus Tarnfarbe und Blut bedeckt. Als sie mich sah, zog sie ein dünnes Messer mit lilafarbenem Griff.)]],
				['7'] = [[(Sasha kam ganz nah an mich heran, dass sich unsere Nasenspitzen fast berührten, und blickte mir ins Gesicht. Sie lächelte unschuldig.)<new_line><new_line>Ach, hast du nach mir gesucht? Tut mir leid, Papa, aber du kommst ein wenig zu spät.]],
				['6'] = [[(Sasha sah mich fragend an. Dann wurde ihr Blick aufmerksamer. Endlich dämmerte es ihr. Dann schoss brennender Hass durch ihre Adern, wie ich ihn noch nie zuvor gesehen hatte. Ich hatte noch nie erlebt, dass jemand einen anderen Menschen so anstarrt.)<new_line><new_line>Nun, schau dir das mal an. Papa ist wieder da.]],
				['5'] = [[(Das Mädchen spuckte aus und wischte sich das Rinnsal aus Blut weg, das ihr über das Gesicht lief. Dann nahm sie das Gewehr von einem der Toten und zielte.<new_line>Da erst sah ich ihr Gesicht.<new_line>Nein ...<new_line>Das kann nicht sein ...)]],
				['4'] = [[(Ich schoss auf sie, aber sie sprang im letzten Augenblick zur Seite. Dann sprang sie mit erstaunlicher Wendigkeit auf mich zu und stach mit dem Messer auf mich ein.<new_line>Ich keuchte, ließ meine Waffe fallen und fiel zu Boden. Das Mädchen sprang auf und trat mir ins Gesicht.)<new_line><new_line>So unehrlich wie alle anderen auch!<new_line><new_line>(Sie zeigte auf die von Kugeln zerfetzten Leichen.)]],
				['9'] = [[(Sasha Lippen verzogen sich vor Abscheu.)<new_line><new_line>Widerlich. Aber weine dich ruhig aus, Papa. Vielleicht hilft das. Vielleicht vergebe ich dir. Oh, ja, hätte ich fast vergessen. Du hast mein Messer. Ich werde es mir jetzt wieder zurückholen.<new_line><new_line>(Sasha packte den Messergriff und riss die Klinge aus mir heraus. Blut quoll aus der Wunde.)]],
				['8'] = [[Papa, lass es einfach, sei nicht albern. Wofür entschuldigst du dich? Okay, Mama ist wegen dir gestorben. Wirklich schlimm. Und Onkel Arthur und Tante Marina. Und, tja, Opa ist tot. Aber er war schon ziemlich alt. Und jetzt kommst du aus dem Nichts und tötest meine Freunde. Aber das ist nicht so schlimm, ich suche mir einfach neue! Habe ich recht? Habe ich recht?]],
				['18'] = [[(Sasha schnaubte und drehte mir den Rücken zu. Das Licht der Fackel beleuchtete kurz ihre Arme, die mit blauen Flecken übersät waren. Frische blaue Flecken, sehr frisch.)<new_line><new_line>Das kannst du ruhig glauben, wenn dich das glücklich macht. Es ist mir scheißegal. Morgen werden sie mich erschießen.]],
				['28'] = [[Du willst alles über mich wissen? Tja, zuerst hat mich meine Mutter zur Welt gebracht.]],
				['38'] = [[Respekt, Anerkennung ... Aber das war erst der Anfang. Später gaben sie mir noch so viel mehr. Sachen, die ich von deinen Leuten aus Nowokusnezk niemals erhalten hätte.]],
				['14'] = [[(Als sie meine Stimme hörte, hob Sasha den Kopf und verzog das Gesicht zu einer Grimasse, als hätte sie plötzlich Zahnschmerzen.)<new_line><new_line>Du?! Ich hatte gehofft, du wärst tot!]],
				['24'] = [[(Sasha sitzt ganz hinten in der Zelle des behelfsmäßigen Gefängnisses und blickte mich mit leichter Verachtung an. Ich kann nicht erkennen, ob sie mir geglaubt hat oder nicht.)]],
				['34'] = [[(Es ging Sasha viel besser. Sie konnte stehen und sogar gehen, wenn sie sich zur Unterstützung an meinen Arm klammerte.)]],
				['26'] = [[(Sasha hatte genug davon, mich anzustarren, und seufzte schwer.)<new_line><new_line>Über was?]],
				['36'] = [[Am nächsten Tag kam Ruslan zurück und legte eine Waffe vor mir auf den Tisch. Er sagte, er würde mich nicht mehr länger umsonst durchfüttern. Er brauche keine Gefangenen, sondern Kämpfer. Er stellte mich vor die Wahl, mich seiner Bande anzuschließen oder nach Nowokusnezk zurückzubringen. Und ich traf meine Wahl.]],
				['30'] = [[Dann ... griffen sie uns an. In der Nacht. Ein Dutzend Menschen waren bereits verwundet, als der Alarm losging und alle weckte. Durch das Tor kamen mehrere Räuber. Die Mädchen zitterten vor Angst, aber ich schlug einen der Drecksäcke bewusstlos, schnappte mir seine Waffe und erschoss den Rest.]],
				['40'] = [[Nein, wir sind viele. Tausende. Und bald werden wir noch viel mehr sein. (Plötzlich grinste Sasha.) Und trotzdem können drei oder vier Menschen viel erreichen. Hinter dir!<new_line><new_line>(Ich drehte mich um. Zwei Räuber standen einige Meter mir und zielten auf meinen Kopf.)]],
				['32'] = [[Ja, klar, er würde niemals schlecht von seinem kostbaren Sohn reden. Weißt du, er ...]],
				['42'] = [[Ja. Er sagte, ich hätte Mut, und dass ich zehn seiner Kämpfer ersetzen könnte. Dass er mir niemals antun würde, was Wassili getan hatte. Er würde niemals einen einzelnen Soldaten im Stich lassen, der einem ganzen Trupp gegenüberstand. Er sagte, das Lager Nowokusnezk hätte die besseren Aussichten auf einen Sieg gehabt, aber sie hätten die Nerven verloren. Er hat mir viele solche Sachen erklärt. Ich blieb ruhig. Ich wollte ihm zwar widersprechen, aber ...]],
				['12'] = [[(Die Blutlache zu meinen Füßen sah aus wie ein Ozean. Ich kniete kraftlos vor ihr und konnte nur noch abwarten. Sekunden kamen mir vor wie Stunden ...)]],
				['22'] = [[(Sasha reagiert nicht.)]],
				['10'] = [[(Sie schwieg eine Weile. Dann beugte sie sich wieder zu mir herüber.)<new_line><new_line>Du kannst dir gar nicht vorstellen, Papa, wie gerne ich dir in deine Visage schießen werde. Immer und immer wieder, bis es in diesem ganzen verfluchten Gebäude keine Patronen mehr gibt.]],
				['20'] = [[Warum hätten sie mich nicht zusammenschlagen sollen? Hast du geglaubt, sie würden mich mit Samthandschuhen anfassen, du Judas? (Eine Sekunde lang lag Verbitterung in ihrer Stimme.) Tja, Boris hat mich richtig hart rangenommen.]],
				['49'] = {
					['1'] = [[Ja. Aber sie haben es nicht einmal aus dem Zimmer geschafft. Ich schnappte mir Ruslans Messer und schnitt ihnen die Kehlen durch.]],
					['2'] = [[(Eine Pause.)<new_line><new_line>Es hat mir nicht einmal so viel Vergnügen bereitet, die Monster abzuschlachten, die Großvater umgebracht hatten. Ich schaute mir an, wie die drei zu meinen Füßen verbluteten, und fühlte mich frei. Glücklich. Stark. Es war keine Mordlust und keine Rache für ihren Verrat. Es war Gerechtigkeit.]]
				},
				['48'] = {
					['1'] = [[Ich war noch nicht fertig. Eine Woche später brachten sie einige deiner „Unschuldigen“ in mein Zimmer – Wassili und zwei andere Kerle aus Nowokusnezk.]],
					['2'] = [[Ruslan machte ihnen ein Angebot: Sie sollten ihm alles erzählen, was sie über Stepnoy wussten – die Leute, die Waffen, die Schwachstellen in den Befestigungsanlagen – und dafür würde Ruslan sie freilassen.<new_line>Er hatte den Satz noch nicht einmal beendet, als diese zitternden und totenbleichen Würmer schon losplapperten und ihm alles erzählten.]]
				},
				['47'] = [[Nein, Papa. Wir sind keine Räuber. Wir sind viel mehr.]],
				['46'] = {
					['1'] = [[(Sasha lachte verächtlich.)<new_line><new_line>Unschuldige Menschen?! Wo hast du denn unschuldige Menschen gesehen?! Oder meinst du damit du die faulen, gierigen, dämlichen Schafe, die nur nutzlos blöken, während sie abgeschlachtet werden?]],
					['2'] = [[Schau dich um, Es gibt kein Land mehr. Es gibt keinen sicheren Hafen mehr, wo man friedlich sein Essen in sich hineinschaufeln kann, und schöne dicke Wolle scheren kann, um sich hübsche Kleider zu nähen. Wir leben in der Wildnis. Und da ist man entweder ein Schaf ... oder ein Wolf.]]
				},
				['45'] = [[Nein. Nicht zu diesem Zeitpunkt. Aber ich sah, dass er mich verstand. Er verstand mich besser als dieser Abschaum aus Nowokusnezk. Die mich ... benutzt und dann wie stinkenden Müll weggeworfen hatten.]],
				['44'] = [[Natürlich habe ich das bemerkt. Aber seine Geschichten waren die Wahrheit. Und das habe ich ebenfalls erkannt. Eine Woche lang besuchte er mich täglich. Er erzählte mir von sich selbst und seiner Familie. Und dann von einigen der anderen Kämpfer. Dann erzählte ich ihm von mir. Er hat mir aufmerksam zugehört. Er hat mir Fragen gestellt.]],
				['16'] = [[Wie bitte?! Was soll das jetzt?]],
				['17'] = [[Du Idiot. Ich ... Ich habe den Moment genossen!]],
				['29'] = {
					['1'] = [[Seltsam. Ich kann mich nicht daran erinnern, dass du dich jemals für mein Leben interessiert hättest. (Sasha schüttelte den Kopf.) Gut. Die Leute aus Nowokusnezk brachten mich in ihr Lager. Aber sie ließen mich nicht kämpfen. Sie nahmen mir sogar Großvaters Waffe weg. Ich ging zum Major. Er sagte mir, unter seinen Kämpfern gäbe es keinen Platz für ein Mädchen. Ich sollte mich nur um die Hausarbeit kümmern.]],
					['2'] = [[Die Arbeit in der Küche war wirklich widerwärtig. Ich habe mich geweigert, sie zu verrichten. Die Mädchen wurden sauer auf mich und beschwerten sich beim Anführer über meine „Faulheit“. Aber der Mistkerl sagte nur: „Ist schon in Ordnung. Ein paar Tage ohne Essen, und sie wird ihre Meinung ändern ...“]]
				},
				['19'] = [[Was getan?]],
				['15'] = {
					['1'] = [[(Sasha lachte laut auf, Tränen liefen ihr über das Gesicht.)<new_line><new_line>Und Boris nannte mich eine dreckige Verräterin?! Mich?! Du bist tausendmal schlimmer! Für dich ist Verrat so natürlich wie Atmen! Boris hat dir das Leben gerettet. Und jetzt willst du seiner Gefangenen bei der Flucht helfen?!]],
					['2'] = [[(Sashas hysterisches Lachen verwandelte sich plötzlich in ein leises Schluchzen. Das hatte ich schon einmal gehört: vor langer Zeit, als ich meiner Tochter sagte, dass ich für immer gehen würde. Sasha riss sich wieder zusammen, unterdrückte ihre Tränen mit einem wilden Brüllen und entfernte sich von den Gitterstäben.)]]
				},
				['39'] = [[Der Unterschied zwischen Leben und Überleben! (Sashas Augen leuchteten fieberhaft) Bedeutet dir das Wort „Mission“ gar nichts?<new_line>Wir sind keine Räuber, Papa. Wir sind Siedler. Wir tilgen die verfaulenden Überreste der alten Welt vom Angesicht der Erde und errichten eine neue Welt – besser und gerechter.<new_line>]],
				['35'] = {
					['1'] = [[Alles klar, hör zu (sie war erschöpft, und darum willigte sie ein). Sie schleppten mich zur Abakan-Basis. Sperrten mich ein. Ich dachte, sie würden mich vergewaltigen. Aber das haben sie nicht. In der ersten Woche kam täglich ein Mädchen zu mir und brachte mir etwas zu essen. Sonst habe ich niemanden gesehen.]],
					['2'] = [[Irgendwann kam ihr Anführer zu mir, Ruslan. Später erfuhr ich, dass er früher ein politischer Gefangener gewesen war. Lange Zeit sagte er kein Wort. Aber dann fragte er mich urplötzlich, was ich angestellt hatte, damit mich meine Kameraden verlassen haben.]]
				},
				['25'] = [[(Sasha ächzte nicht mehr. Sie war in eine seltsame Lethargie gefallen und ihre Augen wanderten unruhig hin und her.)]],
				['37'] = [[Es spielt keine Rolle, was ich sage. Du würdest es trotzdem nicht verstehen.]],
				['27'] = [[(Sasha drehte sich weg.)<new_line><new_line>Nicht gerade ein tolles Thema.]],
				['41'] = {
					['3'] = [[(Ruslan hob Sasha vorsichtig in das Fahrzeug und setzte sich neben den Fahrer auf den Beifahrersitz. Der dritte Räuber wollte gerade die Tür zuschlagen, aber Sasha hielt sie auf.)<new_line><new_line>Ich werde niemals vergessen, und ich werde dir niemals vergeben, dass du Mama und mich im Stich gelassen hast. Wenn du das nicht getan hättest, wäre nichts hiervon jemals passiert. Kapierst du das überhaupt? Nichts davon wäre jemals geschehen!]],
					['2'] = [[(Einer der Männer half Sasha auf die Füße. Im Dickicht ratterte ein Motor und ein altes UAZ-Fahrzeug tauchte auf der steinigen Böschung auf.)<new_line><new_line>Wir sind quitt. Ein Leben für ein Leben.<new_line>Geh jetzt. Geh weit weg, so weit, dass wir uns nie wieder über den Weg laufen. Ich will dich nicht noch einmal sehen. Und auch nichts mehr von dir hören.]],
					['1'] = [[Nein. Du hast mich gerettet, obwohl ich dich töten wollte. Daher werden wir bei dir Gnade walten lassen. Das ist nur gerecht. Gerechtigkeit über alles.<new_line>Ruslan, hilf mir.]]
				},
				['31'] = {
					['3'] = [[Langer Rede kurzer Sinn, das Märchen nahm ein jähes Ende. Wir haben die Abakans abgegriffen. Ja, sie haben uns einen Hinterhalt gestellt. Aber wir waren mehr und besser bewaffnet. Aber dieses feige Schwein, Wassili, befahl den Rückzug. Obwohl er sah, dass ich vom Rest der Gruppe getrennt worden war.]],
					['2'] = [[Ich habe mich gut mit den Jungs von der Truppe verstanden. Sie sagten mir, sie würden mich respektieren, dass ich zu ihnen gehören würde. Und ich, die dumme Göre, die ich bin, warf mich in den Kugelhagel, um ihnen allen zu zeigen, aus welchem Holz ich geschnitzt bin. (Sasha biss ihre Zähne zusammen.) Herr im Himmel, ich war so dämlich ...]],
					['1'] = [[Wir haben sie zurückgeschlagen. Danach gaben sie mir meine Waffe zurück. Ich durfte mit auf die Jagdausflüge und ich wurde sogar in eine Kampfgruppe aufgenommen. Der Truppenkommandant war Wassili. Ja, der Sohn von Boris.]],
					['4'] = [[Ich habe vier von ihnen abgeknallt. Aber dann ist mir die Munition ausgegangen und sie haben mich erwischt. Erst wollten sie mich gleich erschießen, aber ihr Anführer ging dazwischen. Er befahl ihnen, mich zu ihrer Basis zu bringen.]]
				},
				['43'] = [[Nein, er hat nur geraucht und geredet. Am zweiten Tag kam er zurück. Er sagte, meine Leute würden sich weigern, das Lösegeld für mich zu bezahlen. Und dass er nur ein paar Waffen und eine Kiste mit Konserven für mich haben wollte. Er sagte, sie hätten keinen Versuch unternommen, um mich zu befreien. Ich erfuhr viele andere Sachen und ähnliche Geschichten. Danach brachte er mir selbst das Essen. Viel größere Portionen als es bei unseren Leuten gab. Und es schmeckte auch viel besser.]],
				['33'] = [[(Aus der Ferne ertönte aus der Richtung von Nowokusnezk ein Schuss. Dann ein weiterer. Ich schnappte mir meine Waffe, kletterte auf den nächsten Hügel und blickte in die Ferne. Vor mir erstreckte sich das graue Ödland.<new_line>Als ich zu Sasha zurückkam, schlief sie schon.)]],
				['23'] = [[Boris. Was hast du vor?]],
				['13'] = [[(Im dunklen Keller war alles sehr feucht und kalt. Darin würden es sich nicht einmal Ratten gemütlich machen. Im Licht der Fackel sah ich hinter den dicken Gitterstäben drei Bänke aus massivem Holz. Auf einer davon saß Sasha. Der Wächter ließ mich die Zelle betreten, während er draußen eine Zigarette rauchte.)]],
				['21'] = [[Er wollte wissen, wer seinen ganzen Stolz ermordet hatte, Wassili. Ich habe es ihm nicht verraten.<new_line><new_line>(Sasha kicherte freudlos.)<new_line><new_line>Wenn ich mit dem Rücken an der Wand stehe, werde ich ihm alles erzählen. Ganz ausführlich. Bist du jetzt glücklich? Dann verzieh dich endlich.]],
				['11'] = [[(Alles verschwamm vor meinen Augen und ich hörte ein Rauschen in meinen Ohren. Der Blutverlust schwächte mich sehr schnell. Sasha hob erneut das Gewehr auf.)<new_line><new_line>Es ist schade, dass Großvaters Haus niedergebrannt ist. Dort hättest du viele Bilder gefunden, mein liebster Papa. Bilder, die dein süßes Mädchen gemalt hat. Und auf jedem ... einzelnen ... verdammten ... Bild ... verreckst du gerade ...]]
			}
		},
		['boris'] = {
			['choice'] = {
				['3'] = [[Und ich kann den Grund verstehen ... Darum schickt niemand Menschen dorthin. Es ist ein weiter Weg. Ich werde irgendwo nach dem System suchen, wo es keine gigantischen Bienen gibt.]],
				['2'] = [[Sieht so aus, als ginge ich nach Ufa.]],
				['1'] = [[Hallo! Ich komme aus dem Ödland. Ich suche nach einem Mädchen. Sie heißt Sasha und ist 1986 von Barnaul aus hierhergekommen ...]],
				['7'] = [[Richtig. Glaubst du, dass Sasha noch dort ist?]],
				['6'] = [[Worauf wartest du dann noch? Warum hast sie nicht angegriffen, um dich zu rächen? Du hast die Männer dazu, und mehr als genug Feuerkraft.]],
				['5'] = [[Sie ist meine Tochter.]],
				['4'] = [[Räuber haben sie verschleppt? Von hier?]],
				['23'] = [[Wo ist sie jetzt?]],
				['9'] = [[Vielleicht gibt es einen Weg, um die Basis der Abakaner ohne eine Armee einzunehmen? Wäre das möglich?]],
				['8'] = [[Willst du den Tod deines Sohnes rächen?]],
				['17'] = [[Ich habe es, Major.]],
				['16'] = [[Nein, habe ich nicht. Bis dann.]],
				['15'] = [[Ich habe es bekommen, zusammengebaut und hierhergebracht. Fangen wir an. Wir dürfen keine Zeit verschwenden.]],
				['14'] = [[Nein.]],
				['24'] = [[Ihr werdet sie exekutieren? Sasha?]],
				['25'] = [[Boris, darf ich sie sehen? Ein letztes Mal?]],
				['26'] = [[Okay, ich werde es versuchen.]],
				['22'] = [[Du ... Du hast sie getötet?]],
				['21'] = [[Und dann?]],
				['20'] = [[Boris, wie hast du mich gerettet? Da, im obersten Stock des Gebäudes?]],
				['19'] = [[Ich habe gerne geholfen.]],
				['18'] = [[Auf den Sieg ... (trink einen Schluck).]],
				['12'] = [[Ja ... Ich brauche deine Ufa-Infos.]],
				['13'] = [[Großartig, das mache ich.]],
				['10'] = [[Vielleicht kann ich ein anderes System finden?]],
				['11'] = [[Ich werde es trotzdem versuchen.]]
			},
			['name'] = [[Boris]],
			['episode'] = {
				['3'] = [[Ja, ich erinnere mich an sie. Sie hatte ein wildes Temperament ... Aber du kommst zu spät. Ein oder zwei Jahre (Boris schüttelte den Kopf). Die Räuber haben sie bei ihrem letzten Überfall entführt. Wir haben sie niemals wiedergesehen.]],
				['2'] = [[(Boris, ein ehemaliger Major und der Anführer der keinen Militärsiedlung von Stepnoy, begrüßte mich gefühlskalt. Er rauchte eine krumme Zigarette und befahl mir, ich solle mich beeilen und gleich sagen, was ich wollte)]],
				['1'] = [[(Zuerst wollten sie mich nicht in die Militärstadt lassen. Sie durchsuchten mich und gingen auf Nummer sicher bestätigten, dass ich kein Spion einer der Banden aus der Umgebung war. Dann erst ließen sie mich mit ihrem Anführer sprechen. Meine Waffen musste ich dabei bei ihnen im Lagerhaus lassen. Die ganze Zeit begleiteten mich zwei bewaffnete Typen.)]],
				['29'] = [[(Boris sah mir schweigend in die Augen. Er stellte keine Frage. Es war wahrscheinlich eine Sympathiebekundung.<new_line> Ich hatte ihm nichts zu sagen.)]],
				['7'] = [[(Boris seufzte)<new_line><new_line>Deine Tochter? Verstehe. Sie haben auch meinen Sohn niedergemetzelt. Auch die aus Abakan. Sie warfen seinen Kopf vor unser Tor. Mein Wassili war für uns ein Held, ein geborener Anführer. Wir alle waren stolz auf ihn. Ich träume noch immer davon, dem Mistkerl, der ihn getötet hat, die Kehle durchzuschneiden.]],
				['6'] = [[Nein, nicht aus der Gegend, aus Abakan. Warum suchst du nach ihr? Seid ihr verwandt?]],
				['5'] = [[(Boris schmunzelte. Er wollte noch etwas hinzufügen, überlegte es sich aber im letzten Augenblick anders.)<new_line><new_line>Dann viel Glück. Ich kenne keine anderen Orte in der Nähe, die sicher sind. Du musst dich alleine auf die Suche machen.]],
				['4'] = [[Nun, das ist eine ganz andere Geschichte. Du wirst nichts finden. Aber wenn, musst du es zusammenbauen. In der Siedlung nahe Tscheljabinsk gibt es einen Militäringenieur namens Spiridon Kugel. Einst arbeitete er in der Ufa-Einheit. Er sollte dir helfen. Wenn er sich nicht schon zu Tode gesoffen hat. Grüße ihn von seinem alten Saufkumpan.]],
				['19'] = [[(Dieses Mal war nur Boris in der Kommandozentrale. Als ich eintrat, standen da eine teure Flasche Wein und zwei Gläser. Boris sagte, ich solle etwas mit ihm trinken. Er hatte wirklich gute Laune. Ich lehnte nicht ab.)<new_line><new_line>Auf den Sieg! Mögen alle Siege wie dieser werden!]],
				['23'] = [[Das kannst du dir schon denken. Ich habe bemerkt, dass du in Schwierigkeiten steckst. In der nächsten Sekunde hätte dich dieses Mädchen umgebracht. Aber ich habe zuerst geschossen. Du schuldest mir also was, Kumpel.]],
				['9'] = [[Woher soll ich das wissen? Hör zu, ich kann wirklich nachvollziehen, wie es dir geht, aber ... Du solltest es einfach akzeptieren. Und weiterleben. Du wirst deine Tochter nicht wiederbekommen. Zuerst einmal braucht man eine ganze Armee, um es mit den Abakanern aufzunehmen. Und dann ...<new_line><new_line>(Als ich mir den Major so ansah, erkannte ich, dass er sich unbedingt an diesen Verbrechern rächen wollte. Also beschloss ich, es noch einmal zu versuchen.)]],
				['8'] = [[Glaub mir, jeder hier möchte diese Scheusale abschlachten. Aber das ist nicht so leicht. Sie haben einen alten Militärstützpunkt erobert. Seine Befestigungen übersteigen die von Stepnoy um das Zehnfache. Ich habe meinen Sohn geliebt, aber ich kann meine Leute unmöglich in den sicheren Tod schicken.]],
				['17'] = [[(Boris wollte etwas sagen, verlor sich aber in Gedanken. Dann riss der den Arm nach oben und ließ die Faust auf den Tisch krachen.)<new_line><new_line>Es ist entschieden. Wir geben ihnen, was sie verdient haben! Leg dich schlafen. Ich sage den Männern, dass sie sich vorbereiten sollen. Morgen früh brechen wir auf. Wir werden Wassili und all die anderen rächen!]],
				['16'] = [[(Boris war wirklich überrascht, mich wiederzusehen. Er hatte offenbar nicht geglaubt, dass ich zurückkommen würde.)<new_line><new_line>Du hast Mumm in den Knochen. Verflucht, hast du das System wirklich dabei?]],
				['15'] = [[Hör erst einmal zu. Du wirst nichts finden. Aber wenn, musst du es zusammenbauen. In der Siedlung nahe Tscheljabinsk gibt es einen Militäringenieur namens Spiridon Kugel. Einst arbeitete er in der Ufa-Einheit. Er sollte dir helfen. Wenn er sich nicht schon zu Tode gesoffen hat. Grüße ihn von seinem alten Saufkumpan.]],
				['14'] = [[Noch Fragen?]],
				['24'] = [[Beinahe. Sie hat die Reflexe einer Wildkatze. Die Kugel hat keine lebenswichtigen Organe getroffen. Aber sie ging trotzdem zu Boden. Meine Männer haben sie gefesselt und deine Wunden verbunden. Danach haben sie dich möglichst schnell hierhergebracht. Ich dachte schon, du überlebst es nicht.]],
				['25'] = [[Nun, wir nennen diesen Ort das Gefängnis. Eigentlich ist es gar kein Gefängnis, nur ein ganz normaler Keller. Sie wird nicht lange da unten versauern. Wir stellen sie an die Wand.]],
				['26'] = [[(Boris setzte sich hin und trommelte mit den Fingern auf dem Tisch.)<new_line><new_line>Hör mir mal zu. Da läuft ein Krieg. Das ist keine Zeit für Weicheier. Deine Tochter und die Räuber haben gemeinsame Sache gemacht. Sie hat meine Leute getötet. Verflucht, sie wollte dich persönlich kaltmachen! Und jetzt weinst du dich bei mir aus?]],
				['27'] = [[(Der Major kratzte sich am Kopf. Dann kam er zu mir und legte mir eine Hand auf die Schulter)<new_line><new_line>In Ordnung. Schließlich bist du ihr Vater. Aber hör mal, ich kann mir nicht einmal vorstellen, wie es mir gehen würde, wenn mein Wassili so etwas tun würde ... Was deine Sasha gemacht hat, gemeinsame Sache mit den Räubern, ist unverzeihlich. Ich hätte meinem Sohn dafür persönlich den Kopf abgeschnitten ... Du musst das durchstehen, versteh das doch.]],
				['22'] = [[Nun ja, ich hatte mir schon Sorgen gemacht und mich gefragt, wohin mein bester Kämpfer verschwunden ist. Ich habe mich bei den Männern erkundigt und wir haben die Umgebung durchkämmt.]],
				['21'] = [[Wir haben natürlich größere Verluste erlitten, als ich gehofft hatte. Und diese drei Kerle sind im UAZ abgehauen. Aber den Rest von ihnen haben wir erwischt. Und wir haben die Basis zerstört – das war keine leichte Aufgabe.]],
				['20'] = [[(Boris klopfte mir auf die Schulter.)<new_line><new_line>Trink aus, los. Du musst zu Kräften kommen. Und guter Wein ist besser als alle Vitamine dieser Welt.<new_line>Ja, es war eine verdammt gute Schlacht, nach allen Regeln der Kriegsführung! Ich fühle mich zwanzig Jahre jünger.<new_line>Du hast dich gut geschlagen. Ohne das Raketensystem hätten wir es nicht geschafft!]],
				['18'] = [[(Der Major ist nicht da. Offenbar bereitet er den Angriff vor.)]],
				['12'] = [[(Boris kicherte)<new_line><new_line>Du sagst das so beiläufig, als hätte ich dich um eine Makarow-Handfeuerwaffe gebeten. Hast du überhaupt eine Idee, nach was du suchen musst?]],
				['13'] = [[Moment mal, da fällt mir etwas ein. Wenn du vollkommen verzweifelt bist, dann geh nach Ufa, zum Militärstützpunkt. 1983 wurde eine Lieferung von Metis-Systemen direkt aus dem Werk dorthin geschickt. Ich glaube nicht, dass man sie alle eingesetzt hat. Und seit diese gigantischen Bienen in Ufa aufgetaucht sind, war niemand mehr dort.]],
				['10'] = [[(Boris wurde still)<new_line><new_line>Ja, das tue ich. Aber all diese Jungs, meine Soldaten – sie alle sind Kinder und haben selbst Eltern! Und sie würden wie Lämmer auf die Schlachtbank geführt. Ich kann es nicht tun.]],
				['11'] = {
					['1'] = [[(Boris kratzte sich am Kinn)<new_line><new_line>Theoretisch könnten wir es schaffen. Im Gegensatz zum Haupttor ist das Südtor ihr Schwachpunkt. Wir könnten es aufsprengen. Aber sie werden nicht einfach tatenlos zusehen, wie du mit Dynamit in der Hand zum Tor spazierst. Da sind alte Schützengräben und dann offenes Gelände. Ein einziges Maschinengewehr würde unsere Jungs in Stücke fetzen.]],
					['2'] = [[Wir hatten einmal eine Panzerabwehrlenkwaffe, Metis. Irgendein Armleuchter, ich werde nicht mit dem Finger auf jemanden deuten, hat sie aus Versehen im Sumpf versenkt. Wir haben noch einige Raketen, aber keine Möglichkeit, um sie abzufeuern.]]
				}
			}
		},
		['ignat'] = {
			['choice'] = {
				['3'] = [[Kein Lager. Ich bin ein Herumtreiber.]],
				['2'] = [[Willst du sterben?]],
				['1'] = [[Ich habe mich gefragt, ob du vielleicht ein wenig Hilfe brauchst?]],
				['7'] = [[Du bist also ein Gießer?]],
				['6'] = [[Du hast es also satt?]],
				['5'] = [[Was brauchst du?]],
				['4'] = [[Bei diesem Husten sollte ich dir keine Zigarette geben. Ein Zug und du fällst tot um.]],
				['9'] = [[Du schaffst es. Was hast du schon zu verlieren? Wenn es nicht klappt, kannst du dich einfach wieder auf deinen Hocker setzen, dich an den Rockzipfel deiner Frau klammern und Räuber um Zigaretten anbetteln.]],
				['8'] = [[Ich habe eine Idee. Ich helfe dir. Ich baue eine Schmiede für dich. Du weißt schon, wie man sie aus den Dörfern kennt? Mit Amboss, Ofen, Blasebalg ... Du kannst wieder mit Metall arbeiten. Und dafür bringst du es mir bei.]],
				['18'] = [[Tut mir leid, Mann, aber mit dem Asthma darf man nicht spaßen.]],
				['16'] = [[Sag das nicht immer. Ich bin kein Räuber.]],
				['15'] = [[Ich habe sie gebracht, ich habe sie gebracht. Schau!]],
				['14'] = [[Bald, bald, ich habe noch nicht alles gefunden.]],
				['20'] = [[Bis demnächst, Ignat.]],
				['17'] = [[Was?]],
				['19'] = [[In Ordnung, ich kann dir eine geben.]],
				['12'] = [[Viele Fenster?]],
				['13'] = [[(Geh zum alten Lager und baue die Schmiede.)]],
				['10'] = [[Und du wirst deine Schuld bei deiner Frau und den anderen abarbeiten können ... Dass sie sich um dich gekümmert haben. Aber immer schön langsam, sonst verletzt du dich noch.]],
				['11'] = [[Bauen wir eine Schmiede. Ziegel, Altmetall, Zement, wir haben alles, was wir brauchen.]]
			},
			['name'] = [[Ignat]],
			['episode'] = {
				['3'] = [[Vielleicht, vielleicht auch nicht. Das geht dich nichts an. Woher kommst du überhaupt? Aus welchem Lager kommst du hereingeschneit?]],
				['2'] = [[Tue ich nicht. Ich habe hier ein ganzes Lager voller Helfer. Sie bringen mir Pillen, sie bringen mir Schüsseln mit heißem Wasser, damit ich inhalieren kann. Und wenn es so weit ist, werden sie um mich weinen und mich beerdigen. Ich kann keinen weiteren Aufpasser gebrauchen.]],
				['1'] = {
					['1'] = [[(Meine Aufmerksamkeit wurde auf einen blassen Mann mittleren Alters gelenkt, der auf einem Holzstuhl saß. Vor ihm stand eine Blechschüssel, in der er seine Wäsche wusch.<new_line>Plötzlich hustete er laut und bellend, dann wischte er sich mit einem rauen, grauen Lumpen die Lippen ab. Er bemerkte, dass ich ihn anstarrte, und errötete.)]],
					['2'] = [[Was starrst du denn so? Verschwinde.]]
				},
				['7'] = [[Mann, ich war ein Stoßarbeiter. Ich habe mir diesen Titel als erste Person unseres Metallwerks verdient. Und dann ging alles schief. Durch die Epidemie habe ich meine Söhne verloren. Dann fielen die Bomben. Ich dachte, ich hätte alles hinter mir, aber so war es nicht. Jetzt arbeiten meine Lungen nicht mehr richtig. Meine Frau spielt meine Krankenschwester. Sie würde mich sogar mit einem Löffel füttern, wenn ich es zuließe. Ich bin doch kein alter Mann, ich bin nicht einmal vierzig Jahre alt!]],
				['6'] = [[Ich werde bald sterben. Hier machen sie nur einen großen Wirbel um mich, geben mir zu essen, bringen mir Medikamente. Ich bin wie ein Parasit, der nur untätig herumsitzt. Heute haben sie mir einen großen Gefallen getan: Ich durfte meine eigenen Lumpen waschen. Sie sagen mir andauernd, ich soll mich nicht anstrengen, solange ich krank bin.]],
				['5'] = {
					['1'] = [[(Ignat steht plötzlich auf und geht auf mich zu. Er kommt ganz nahe an mich heran und spuckt auf den Boden, trifft fast meine Schuhe.)<new_line><new_line>Und was ist, wenn ich eine brauche?]],
					['2'] = [[(Ignats Gesicht wurde grau. Schwer atmend ließ er den Kopf hängen und zuckte zusammen. Er schlurfte zurück auf seinen Stuhl und setzte sich, starrte zu Boden. Einen Moment später sieht er mich wieder an, fast flehend.)<new_line><new_line>Hey, Mann, komm her.]]
				},
				['4'] = [[Dann also ein Räuber (Ignat hustet erneut). Sonst überlebt niemand im Ödland. Hey, Räuber, hast du etwas zu rauchen dabei?]],
				['9'] = [[(Ignats Augen leuchten kurze Zeit auf, aber dann lässt er den Kopf wieder sinken.)<new_line><new_line>Ich weiß es nicht, Mann. Es ist schon zu viele Jahre her. Und ich habe noch nie in einer Schmiede auf dem Land gearbeitet. Ich habe meinem Großvater beim Schmieden zugesehen. Ich würde das nicht schaffen.]],
				['8'] = [[Gießer, Schlosser, ich arbeitete sogar mal als Schweißer. Mein ganzes Leben lang habe ich mit Eisen gearbeitet.]],
				['17'] = [[Okay, du sagst also, dass du kein Räuber bist – dann bist du auch kein Räuber. Komm rein und schau dich um. Ich schmiede gerade ein Waschbecken für die Wäsche. Damit meine Frau nicht glaubt, das wäre vollkommener Schwachsinn. Sie muss jetzt die Wäsche waschen.<new_line>Das ist doch was, oder?]],
				['19'] = [[Du redest immer so klug daher ... Gut, ich danke dir trotzdem, Mann. Ich schulde dir was für die Schmiede. Bis zu meinem letzten Tag.<new_line>Wenn du den Ofen brauchst, kannst du ihn benutzen. Ich halte das Feuer am Laufen.]],
				['15'] = [[Ich habe keine Sekunde lang geglaubt, dass du dein Versprechen halten würdest.<new_line>Meine Frau ist übrigens fast durchgedreht, als ich ihr von der Schmiede erzählt habe. Nun ja, aber jetzt ist es zu spät. Jemand anderes kann die Wäsche waschen!]],
				['14'] = [[Du fängst einfach an und ich helfe dir.]],
				['21'] = [[Vielen Dank für alles, Mann! Ich fühle mich, als hätte ich mein altes Leben zurück!]],
				['16'] = [[Vielen Dank für alles, Mann. Du hast mir echt geholfen. Ich weiß gar nicht, was ich sagen soll. Die anderen Räuber können sich eine Scheibe von dir abschneiden!]],
				['18'] = [[Du gibst mir doch immer noch eine Zigarette, oder? Ich habe seit fünf Jahren keine mehr gesehen. Ich werde nicht einmal einen Lungenzug machen. Ich möchte sie mir nur zwischen die Lippen klemmen. Die wird mir nicht schaden.]],
				['20'] = [[Oh! Das hört sich schon besser an. Danke, Mann!<new_line>Wenn du den Ofen brauchst, kannst du ihn jederzeit gerne benutzen. Ich halte das Feuer am Laufen.]],
				['12'] = [[Nicht weit von hier gibt es am Fluss ein altes Lagerhaus. Stabil gebaut, aus Ziegelsteinen. Eine bessere Belüftung gibt es nicht.]],
				['13'] = [[Es gibt keine Wand.]],
				['10'] = [[Ich werde Schmied ... So wie Großvater. In Ordnung, Mann, abgemacht. Wir bauen eine Schmiede. Dann bringe ich dir bei, an was ich mich noch erinnern kann.]],
				['11'] = [[Bringst du mir bald das Material, oder was? Ich kann das Wäschewaschen kaum noch ertragen.]]
			}
		}
	},
