	['quests'] = {
		['belaz'] = {
			['3'] = [[Geschafft.<new_line>Der Besitzer des Bergswerkslasters, Wladimir, war sehr dankbar, dass ich ihm geholfen habe ... Endlich konnte er seinen Traum verwirklichen. Er ist einverstanden, dass ich mit ihm und seiner Familie reise – und ich darf das Ziel aussuchen. Er will seine persönliche, fahrbare Stadt ausprobieren. Jetzt muss ich noch entscheiden, wohin die Reise gehen soll.]],
			['2'] = [[Dieser Kerl ist verrückt. Er will einen Atomreaktor in seinen Bergswerkslaster einbauen! Er gab mir die Liste der Ersatzteile und die Karte. Ich bin offenbar auch nicht mehr ganz dicht ...]],
			['1'] = [[Jemand hat mir von einem Kerl erzählt, der nach Assistenten sucht, um einen Bergswerkslaster zu reparieren. Ich sollte mich mal umhören.]],
			['name'] = [[Fahrbare Stadt]]
		},
		['emba_briefing'] = {
			['6'] = [[Ich muss die Blackbox zum Biologen bringen und ihm bei seinen Forschungen helfen. Aber vorher kann es nicht schaden, Biomasse zu sammeln, um Waffen oder etwas anderes Nützliches im Labor des Biologen herzustellen.<new_line>Der Navigator hat einen Ort gefunden, an dem eine Gruppe von Biomutanten lebt, von denen ich etwas Myzel bekommen kann.]],
			['5'] = [[Ich habe die Bio-Station durchsucht und die Blackbox gefunden, jetzt muss ich zurück zum Navigator.]],
			['4'] = [[Ich muss die Blackbox finden. Sie befindet sich im Zentrum der Wissenschaftsstadt, in einem versteckten Safe in der Bio-Station.]],
			['3'] = [[Ich muss die Blackbox finden. Der Navigator wird mir dabei helfen. Ich muss mit ihm reden.]],
			['2'] = [[Ich muss mir einen AIDA-Spezialschutzanzug besorgen. Damit kann man sich in einer biologischen Gefahrenzone aufhalten. Das Kit kann vom Progress-Personal in der Abteilung für Verbrauchsgüter gegen Disketten eingetauscht werden.<new_line>Ich muss mit dem AIDA-Kit zu Dr. Issajew zurückkehren.]],
			['1'] = [[Ich muss die Dokumente zum Biologen bringen, um ihm bei der Forschung zu helfen. Danach muss ich mit Dr. Issajew sprechen.]],
			['name'] = [[Einweisung]]
		},
		['new_year2020'] = {
			['14'] = [[Gemeinsam haben Sneguroschka und ich Ded Moroz und das neue Jahr gerettet.]],
			['10'] = [[Ich muss den Feuervogel zu mir locken, damit ich eine Feder kriege. Sneguroschka hat mir dazu ein paar verzauberte Äpfel gegeben.]],
			['name'] = [[Festwunder]],
			['3'] = [[Das Terem wurde von den Eisbriganten geplündert. Ich sollte den Festbaum schmücken, damit Sneguroschka ihr Lager findet.]],
			['2'] = [[Sneguroschka hat den Weg zu Moroz' Terem gefunden. Ich soll hingehen und mich umsehen.]],
			['1'] = [[Sneguroschka braucht meine Hilfe. Ded Moroz ist verschwunden, und die festliche Magie mit ihm. Um sie zurückzuholen, muss ich Sneguroschkas Anweisungen folgen und den Festbaum schmücken.]],
			['11'] = [[Zur Vorbereitung des Treffens mit der Winterkönigin muss ich den übrigen Schmuck an den Festbaum hängen.]],
			['7'] = [[Sneguroschka untersucht die Zauberkugel, und ich sollte weiter den Baum schmücken.]],
			['6'] = [[Ein Riesenschneemann kommt über das Ödland. Ich muss ihn aufhalten, bevor er Unheil stiftet.]],
			['5'] = [[Ded Moroz wurde von einer mysteriösen Frau entführt. Sie sprach von einem ewigen Winter, der die Menschheit umfangen würde. Jetzt überlegt Sneguroschka den nächsten Schritt, und ich schmücke den Festbaum.]],
			['4'] = [[Wir haben das Lager der Briganten gefunden. Dort kann ich herausfinden, was Ded Moroz zugestoßen ist.]],
			['12'] = [[Es ist alles bereit. Zeit, den Polarstern einzusetzen und zum Eispalast aufzubrechen. Ded Moroz, wir kommen!]],
			['13'] = [[Die Winterkönigin ist besiegt, doch jetzt sitzt Ded Moroz mit ihr in einem Eisgefängnis. Sneguroschka hat einen Plan. Ich soll den Baum fertigschmücken.]],
			['9'] = [[Zum Schutz vor der Winterkönigin brauche ich eine Feder vom Feuervogel. Sneguroschka findet heraus, wie ich eine bekomme. Ich schmücke solange den Baum weiter.]],
			['8'] = [[Die Kugel, die ich gefunden habe, hat sich als geheimnisvoller Polarstern erwiesen. Sneguroschka meint, ich sollte in einer klaren Nacht ins Ödland gehen und die Kugel zum Himmel heben.]]
		},
		['halloween2020'] = {
			['name'] = [[Reise in den Schatten]],
			['3'] = [[Ich weiß, wer die Leichen ausgebuddelt hat. Ein Mann namens Igor sammelt Körperteile, damit er ein Frankenstein-Monster zusammenflicken kann. Und mein Job ist es, seinem Treiben ein Ende zu setzen.]],
			['2'] = [[Als ich das Portal durchschritten habe, fand ich mich in einer lauten Taverne voller Jäger wieder, die sich Tische mit unglaublichen Kreaturen teilten. Niemand nahm von mir Notiz außer der dämonisch aussehende Tavernenwirt.]],
			['1'] = [[Ich wurde eingeladen, mich einer Jagd in der Schattenwelt anzuschließen, also muss ich durch das nächste Portal gehen.]],
			['11'] = [[Ich weiß nicht, ob der Plan des Tavernenwirts funktionieren wird, aber er ist unsere einzige Hoffnung.]],
			['7'] = [[Ich weigere mich immer noch, zu glauben, was ich gesehen habe, aber das spielt jetzt keine Rolle. Jeder, den ich getroffen habe, arbeitet für Dracula – und der führt etwas Schlimmes im Schilde. Und was Igor angeht: Ich sollte den Tavernenwirt fragen, was „kann nicht auf trockenem Land gefunden werden“ heißen könnte.]],
			['6'] = [[Igor war nicht bei Bloody Mary, aber ich habe dort eine verdächtig aussehende Puppe gesehen. Vielleicht weiß der Tavernenwirt ja etwas über sie.]],
			['5'] = [[Die Mission war eine Falle. Ich hätte es wissen sollen. Ich muss mich beeilen und dem Tavernenwirt alles erzählen.]],
			['4'] = [[Ich habe es geschafft, das Monster zu vernichten, aber Igor konnte entkommen. Er hat einen großen Folianten mitgenommen, der mit dem Bild eines Auges in einem schrägen Stern verziert war.]],
			['12'] = [[Wir haben es gerade noch geschafft, die Schattengrenze vor der Zerstörung zu bewahren. Ich will gar nicht daran denken, was uns im nächsten Jahr noch bevorstehen wird.]],
			['10'] = [[Aus dem Kopf des Reiters bin ich auch nicht schlauer geworden. Mal sehen, ob der Tavernenwirt ihn zum Reden bringen kann.]],
			['9'] = [[Ich konnte das Mädchen vor der Mumie retten, doch sie ist verschwunden, ohne auch nur ein Wort zu sagen. Jetzt kann ich mich wieder auf die Suche nach dem Reiter konzentrieren. Hoffentlich hat der Tavernenwirt Informationen für mich.]],
			['8'] = [[Igor wurde in den Abgrund gerissen, als er versuchte zu fliehen. Doch sein mysteriöses Buch hat sich der Kopflose Reiter unter den Nagel gerissen und ist damit geradewegs über das Wasser davongeritten. Ich habe das Logbuch des Schiffs gefunden, aber die Aufzeichnungen sind verschlüsselt. Ich muss wohl wieder zurück in die Taverne.]]
		},
		['death_recovery'] = {
			['name'] = [[Verloren und gefunden]],
			['4'] = [[Ich habe die Plünderer besiegt und meine Sachen zurückerhalten.]],
			['3'] = [[Zumindest habe ich einige der gestohlenen Sachen zurückerhalten. Die Plünderer haben den Rest meiner Sachen schon in ihr Lagerhaus in der Nähe gebracht. Bei einer ihrer Leichen fand ich eine Karte, auf der es eingezeichnet ist. Das Lagerhaus ist wahrscheinlich gut bewacht. Daher sollte ich mich auf den Kampf vorbereiten.]],
			['2'] = [[Es war nicht anders zu erwarten – die Plünderer waren schon hier und haben so ziemlich alles mitgenommen. Zum Glück haben sie eine Spur hinterlassen und können noch nicht weit gekommen sein. Ich werde sie problemlos aufspüren.]],
			['1'] = [[Ich habe keine Ahnung, wie ich es geschafft habe, aber ich habe überlebt und bin zu einem Zufluchtsort gekommen. Ich denke, dass ich einige meiner Sachen verloren habe, als mich die Ohnmacht übermannt hat. Ich sollte zurückgehen und sie holen.]],
			['hint'] = [[Vergiss nicht, meine wiedererlangten Gegenstände abzuholen.]]
		},
		['halloween2022'] = {
			['10'] = [[Ich habe einen Auftrag vom Sensenmann angenommen. Ich muss Frankensteins Monster und Igor, den Buckligen, vernichten.]],
			['name'] = [[Halloween 2022]],
			['3'] = [[Ich habe mich um den Dämon gekümmert. Ich muss den nächsten Auftrag des Sensenmannes kaufen.]],
			['2'] = [[Ich habe einen Auftrag vom Sensenmann angenommen. Ich muss den Dämon Balthasar vernichten.]],
			['1'] = [[Der Sensenmann bot mir an, einen Dämonenvertrag für Goldmünzen zu verkaufen.]],
			['11'] = [[Ich habe mich um Frankensteins Monster gekümmert. Ich muss den nächsten Auftrag des Sensenmannes kaufen.]],
			['7'] = [[Ich habe mich um Dracula gekümmert. Ich muss den nächsten Auftrag des Sensenmannes kaufen.]],
			['6'] = [[Ich habe einen Auftrag vom Sensenmann angenommen. Ich muss Dracula vernichten.]],
			['5'] = [[Ich habe mich um den Pestarzt gekümmert. Ich muss den nächsten Auftrag des Sensenmannes kaufen.]],
			['4'] = [[Ich habe einen Auftrag vom Sensenmann angenommen. Ich muss den Pestarzt vernichten. Ich brauche einen Dämonenmantel, um ihn zu besiegen.]],
			['12'] = [[Ich habe einen Auftrag vom Sensenmann angenommen. Ich muss den sprechenden Esel vernichten.]],
			['13'] = [[Der Sensenmann hat einen schlauen Plan gegen mich ausgeheckt, aber ich habe ihn mit Bravour gemeistert! Mal sehen, was die jenseitige Welt nächstes Jahr für mich auf Lager hat.]],
			['9'] = [[Ich habe mich um den Kürbiskönig gekümmert. Ich muss den nächsten Auftrag des Sensenmannes kaufen.]],
			['8'] = [[Ich habe einen Auftrag vom Sensenmann angenommen. Ich muss den Kürbiskönig vernichten. Ich brauche das Schwert des Henkers, um ihn zu besiegen.]]
		},
		['familyMatters'] = {
			['4_dream'] = {
				['1'] = [[„Sasha, Schätzchen, schau nur, was Mama für dich gemacht hat!“ Galya zeigt Sasha die Puppe, die sie aus alten Hemden zusammengenäht hatte. Eine kleine Hand erschien über dem Fußende des Bettes und griff nach dem Bein der Puppe.<new_line>„Offenbar sind nicht alle Dinge schlecht. Zumindest können wir jetzt das Geld für Sashas Puppen sparen“, scherze ich, nehme das bisschen Kleingeld aus der Schreibtischschublade und lasse es ins Münzfach meiner Brieftasche fallen.]],
				['2'] = [[Galya kommt zu mir und legt ihre Arme um meinen Hals.<new_line>„Alles wird gut, Liebling. Ich kann bald wieder in der Kindertagesstätte arbeiten. Dann wird alles ein wenig leichter für dich. Du wirst sehen, bald haben wir genug Geld für ein Auto.“<new_line>„Hast du schon einmal einen Lehrer mit einem eigenen Auto gesehen?“ Ich seufze niedergeschlagen und gehe, um Sasha süße Träume zu wünschen ...]]
			},
			['3_dream'] = {
				['1'] = [[... Wir traten aus dem Restaurant, um ein wenig Luft zu schnappen. In ihrem Hochzeitskleid sah sie umwerfend aus!<new_line>„Es ist das Kleid meiner Mutter“, sagte die junge Frau und zupfte ihren Ärmel zurecht. „Es war etwas weit, aber meine Schwester und ich haben es umgeschneidert. Glaubst du, es steht mir gut?“<new_line>„Galya, du bist wunderschön“, sagte ich und legte meinen Arm um sie. „Die Hochzeit war umwerfend, nicht wahr?“<new_line>„Vorsicht, da ist eine Laufmasche!“, scherzte Galya. „Unsere Tochter wird einmal in diesem Kleid heiraten!“]],
				['2'] = [[„Woher weiß du denn so genau, dass es ein Mädchen wird?“<new_line>„Es wäre schade um dieses Kleid, wenn es kein Mädchen wird!“, erwiderte Galya lachend. Dann wurde sie plötzlich ernst. „Alles wird gut werden, nicht wahr? So wie im Märchen?“<new_line>„So wie im Märchen“, wiederholte ich zuversichtlich ...]]
			},
			['2_dream'] = {
				['1'] = [[„Hey, komm hier herauf. Worauf wartest du noch?“, ruft mir das vertraute Mädchen zu und steht ganz oben auf der Brüstung. Hinter ihr fließt das Wasser des endlosen Stroms vorbei. „Du musst doch keine Angst haben!“<new_line>„Schätzchen, ich habe dir schon gesagt, dass ich nicht will“, antworte ich und gehe ein Stück zurück. „Und komm da wieder runter!“<new_line>„Du hast also keine Angst?“, fragt das Mädchen spielerisch. „Ach du meine Güte! Du hast also Angst davor, hier heraufzuklettern, aber du hast keine Angst davor, mich zu lieben?“]],
				['2'] = [[„Ich habe keine Angst. Ich liebe dich.“<new_line>„Dann beweise es!“ Das Mädchen springt auf der Stelle auf und ab. „Komm hier rauf! Mach schon!“]]
			},
			['1_dream'] = {
				['3'] = [[„Das werde ich. Weil sie dir so wichtig ist.“<new_line>Das Mädchen lächelt mich warmherzig an und küsst mich auf die Nase.]],
				['2'] = [[„Vielen Dank für den Tee, Anna Sergeyevna, wir kommen nächste Woche wieder, um nach dir zu sehen“, sagte das Mädchen mit dem dunkelblonden Haar und hielt meine Hand.<new_line>„Vielen Dank, meine Liebe, dass du gekommen bist und immer an mich denkst. Ohne dich wäre ich schon lange vor Verzweiflung gestorben ... Mein eigener Sohn hat mich vollkommen vergessen. Er hat schon seit über einem Jahr nicht mehr angerufen ...“<new_line>Die alte Frau begleitet uns zur Tür. Das Mädchen flüstert mir zu:<new_line>„Ich gehe den Sommer über von hier weg. Aber du wirst sie besuchen, nicht wahr? Sie hat sonst niemanden mehr ...“]],
				['1'] = [[Ich stehe auf und untersuche die Medaille. Eine kleine alte Frau sieht sich mit mir zusammen die Medaille an.<new_line>„Sie gehörte meinem Mann“, erklärt sie. „Er kämpfte sogar in der Schlacht um Berlin, stand gegen die Faschisten immer an vorderster Front! Nach dem Krieg lebte er nur noch ein Jahr. Er starb an Lungenentzündung.“]]
			},
			['3_down'] = [[Jetzt erinnere ich mich an einen Namen. Galya. Wir haben geheiratet, sie war schwanger. Gott, ich kann mich an nichts anderes erinnern. Wo ist sie? Was ist mit ihr passiert? Ich muss sie finden!]],
			['2_down'] = [[Bruchstücke von Erinnerungen drängen sich in mein Gedächtnis ... Ein Mädchen, das ins Wasser fällt ... Ich eile zu dem Geländer, klettere hinüber, aber ... das Wasser ... Das schwarze, kalte, bodenlose Wasser ... Ich hasse mich selbst, aber ich kann mich nicht bewegen.<new_line>Ein Arbeiter zog das Mädchen heraus. Zum Glück ging es ihr gut. Aber auf dem Weg nach Hause sagte niemand ein Wort.]],
			['1_down'] = [[Ich zitterte und öffnete die Augen. Um mich herum die gleiche tote Stadt. <new_line>Was ist hier passiert? Wer war das Mädchen?]],
			['7_down'] = [[Es durchzuckt meinen Verstand wie ein Stromschlag. Gorky, Bakinsky-Straße 10, Wohnung 3! Ich habe sie gefunden! Die Adresse unserer alten Wohnung!<new_line>Mein Herz rast. Jetzt weiß ich, wo ich mit meiner Suche anfangen muss.]],
			['5_down'] = [[Ich werde nach ihnen suchen. Nach Galya und Sasha. Und wenn sie noch leben, werde ich sie finden! Ich muss mich noch an etwas anderes erinnern, und wenn es nur der Name einer Stadt ist!]],
			['4_down'] = [[Meine Tochter heißt Sasha. Wo ist sie jetzt? Lebt sie noch? Wie alt ist sie? Jede Erinnerung ist schmerzlicher als die vorherige ...]],
			['28_1'] = [[Lebe ich noch? Ich höre Menschen sprechen. Ich fühle die harte Matratze unter meinem Rücken. Ich sehe die getünchte, von Rissen durchzogene Decke über mir. Das muss die Krankenstation von Stepnoy sein.<new_line>Sasha hat mich nicht getötet? Was ist geschehen?<new_line>Offenbar haben sie bei mir nicht mit Medizin geknausert. Die Stichwunde, die mir Sasha zugefügt hatte, tat höllisch weh, aber ansonsten ging es mir nicht einmal sonderlich schlecht.<new_line>Einer der Kämpfer kam in mein Zimmer und sagte, dass mich der Major sehen wollte.]],
			['9'] = [[Plünderer hatten die Wohnung verwüstet. Sie hatten all unsere Sachen aus den Kommoden und Schränken geworfen. Die Küche war mit Essensresten übersät. Auf dem Tisch standen Einmachgläser, in denen noch kleine Reste Marmelade waren. Auf dem Schlafzimmerboden lagen die Scherben unseres alten Teeservices. Die Kissen waren aufgeschlitzt und der Bücherschrank ausgeräumt. In den Regalen waren nur noch wenige Bücher. Mitten im Zimmer stand eine große Blechschüssel voller Asche ... Um sie herum lagen Zigarettenkippen und leere Flaschen ...<new_line>In Sashas altem Zimmer stand noch ihr Bett. Automatisch schob ich meine Hand unter das Kissen und zog eine Puppe hervor. Es war die Puppe, die ihre Mutter für sie genäht hatte. Sasha hatte ihre Puppe immer dort unter dem Kissen versteckt, damit der schwarze Mann sie nicht fand ...<new_line>Wo sind sie? Wo sind Galya und Sascha? Haben sie die Epidemie überlebt?<new_line>Wenn eine von ihnen krank wurde, gibt es darüber vielleicht Akten im örtlichen Krankenhaus. Ich muss da hingehen.]],
			['28_2'] = [[Auf dem Weg zu Boris dachte ich über Sasha nach. Nein, sie war definitiv nicht mehr das Kind, das ich in Erinnerung hatte. Inzwischen war sie eine kaltblütige und durchtriebene Mörderin.<new_line>Wie ist sie dazu geworden. Wie?]],
			['18'] = [[Ich hatte kaum den letzten Satz gelesen, als mich die Kraft verließ und ich auf dem Boden zusammenbrach. Jetzt weiß ich alles. Galya ist verschwunden ... Es wäre besser gewesen, wenn ich mich niemals an sie erinnert hätte. Es war eine bittere Ironie! Wie schön wäre es gewesen, mich an überhaupt nichts zu erinnern!<new_line>Aber ... Wann hat sie sich infiziert? Und von wem?<new_line>Dieses kleine Mädchen, Nika ... Darum müssen ihre Eltern sie verlassen haben. Darum waren Galyas Augen geschwollen. Darum war die Tür verschlossen.<new_line>Wusste Galya, dass das kleine Mädchen bald sterben würde? Sie muss es geahnt haben. Aber meine Galya hätte ein Kind niemals alleine im Hausflur sterben lassen. Niemals.<new_line>Galya opferte ihr Leben für das Kind eines Fremden. Und ich ... Ich habe mein eigenes Kind und meine Frau im Stich gelassen. Marina hatte recht. Wenn ich da gewesen wäre, wäre Galya noch am Leben. Es ist meine Schuld. Nur ich ...<new_line>Ich werde nach Barnaul gehen und nach Sasha suchen. Wenn sie sich noch nicht infiziert hat, kann ich sie noch finden. <new_line>Aber wird sie mir den Tod ihrer Mutter jemals verzeihen? Werde ich mir jemals selbst vergeben?]],
			['28'] = [[Lebe ich noch? Ich höre Menschen sprechen. Ich fühle die harte Matratze unter meinem Rücken. Ich sehe die getünchte, von Rissen durchzogene Decke über mir. Das muss die Krankenstation von Stepnoy sein.<new_line>Sasha hat mich nicht getötet? Was ist geschehen?<new_line>Offenbar haben sie bei mir nicht mit Medizin geknausert. Die Stichwunde, die mir Sasha zugefügt hatte, tat höllisch weh, aber ansonsten ging es mir nicht einmal sonderlich schlecht.<new_line>Einer der Kämpfer kam in mein Zimmer und sagte, dass mich der Major sehen wollte.<new_line>Auf dem Weg zu Boris dachte ich über Sasha nach. Nein, sie war definitiv nicht mehr das Kind, das ich in Erinnerung hatte. Inzwischen war sie eine kaltblütige und durchtriebene Mörderin.<new_line>Wie ist sie dazu geworden. Wie?]],
			['14'] = [[Ich habe viele Stunden lang gelesen.<new_line>Ich bin am Ende der Liste angelangt und vor Starrheit wie gelähmt. Ich habe Kopfschmerzen, aber ich weiß nicht, wo ich jetzt hingehen soll ...<new_line>Ich muss weitere Einzelheiten in meinem Gedächtnis abrufen! Ich sollte durch die Straßen ziehen, um meine Erinnerung aufzufrischen!]],
			['24'] = [[Während der Ingenieur an dem Raketensystem bastelte, wurden wir von einem weiteren Tier angegriffen. Wir müssen die Arbeit hier erledigen und aus Ufa verschwinden, ehe der ganze Bienenstock auf uns aufmerksam wird.<new_line>Aber zuerst muss ich Kugel nach Hause bringen.]],
			['34'] = [[Sasha stöhnte erneut. Dieses Mal öffnete sie einen Spaltbreit die Lider. Ich beugte mich über sie.<new_line>„Sasha, kannst du mich hören?“<new_line>Sasha atmete schwer und schloss wieder die Augen.]],
			['26'] = [[Boris hat Recht, wir müssen und in der Nacht vor dem Kampf ausruhen und schlafen. Morgen wird ein harter Tag.<new_line>Ich habe das Gefühl, dass die Anstrengung nicht umsonst sein wird. Sasha lebt – und sie ist da drin. Morgen werde ich sie sehen. Morgen werde ich ihr alles sagen, was ich ihr schon lange sagen will, und was ich ihr unbedingt sagen muss.<new_line>Und danach werde ich – wie ein Angeklagter vor Gericht – auf meine Strafe oder Absolution warten.]],
			['36'] = [[Der UAZ wendete und fuhr davon, nahm meine Sasha mit ...<new_line>Nein, das war jemand anderes in diesem Wagen.<new_line>Meine Sasha gab es nicht mehr. Genau wie Galya. Ich holte die kleine Stoffpuppe aus meinem Rucksack. Sie war alles, was ich von meiner Frau und meiner Tochter noch hatte.<new_line>Meine Hand tastete nach meiner Waffe ...<new_line>Nein, auf keinen Fall! Selbstmord ist nur ein weiterer Weg, um vor meiner Verantwortung wegzulaufen. Es wäre eine dämliche und feige Tat, in einem Leben, das vor dämlichen und feigen Taten überquoll.<new_line>Nein, ich werde es nicht tun.<new_line>Trotz allem werde ich weiterleben. <new_line>Ich werde leben und meinen Platz in dieser neuen Welt finden ...]],
			['30'] = [[Was ich dachte, war verrückt. Aber je mehr ich darüber nachdachte, desto mehr erfüllte mich eine Art kranker Gewissheit, dass ich recht hatte.<new_line>Sasha hatte mich nicht getötet, als sie die Gelegenheit dazu hatte. Und ich wollte nicht zulassen, dass ihr Leben vor einem Erschießungskommando endete. Nein, das durfte nicht geschehen. Boris und die anderen konnten mich verfluchen, mich jagen ... Aber ich musste Sasha da rausholen.<new_line>Wir werden weit von hier weggehen. Und wenn es nicht mehr so gefährlich ist, werde ich endlich die Wahrheit erfahren.]],
			['32'] = [[Sashas Zustand verschlechterte sich. Ihr Kopf rollte nach hinten, ihre Atmung wurde schwer und ungleichmäßig und ihre Haut verlor an Farbe.<new_line>Ich musste anhalten und ihre Wunden versorgen. Bald schon würde ich ihr nicht mehr helfen können. <new_line>Ich fuhr mit dem Auto in das Dickicht am Ufer des Flusses Tschumysch. Dann hob ich Sasha vorsichtig aus dem Wagen und legte sie auf eine Matte. Sie war bewusstlos. Ich befeuchtete ihre Lippen mit Wasser und sah mir ihre Verletzungen an. Die Kugel war ein glatter Durchschuss. Ich desinfizierte und verband beide Wunden, dann ging ich zurück zum Auto, um meine Waffe zu holen. Es war möglich, dass der Feind jede Sekunde kam.]],
			['12'] = [[Viele Namen und Vornamen ... Bei jedem Vornamen „Galya“ oder „Sasha“ schlägt mir das Herz bis zum Hals. Aber ich habe sie noch nicht gefunden. Ich muss alles durchlesen.]],
			['22'] = [[Ich habe den Ingenieur Kugel überredet, mit mir nach Ufa zu fahren und mir mit dem Raketensystem zu helfen. Aber ich sollte mich besser vorbereiten, bevor wir uns auf den Weg machen: Ich muss meine Waffen und meine Rüstung überprüfen, falls wir ein paar Riesenbienen begegnen.]],
			['10'] = [[Auf dem Weg zum Krankenhaus dachte ich an meine Frau und meine Tochter. Egal wie klein die Chance auch war, sie jemals wiederzusehen, ich redete mir zwanghaft ein, dass wir eines Tages erneut vereint sein würden.<new_line>Aber ... was würde ich ihnen erzählen? Wenn sie noch am Leben sind, ist Sasha inzwischen kein Kind mehr, und Galya könnte schon mit einem anderen Mann zusammenleben. Was mache ich denn, wenn das so ist?<new_line>Zumindest habe ich jetzt einen Grund, um weiterzumachen. Ich will nur eines wissen – ob meine Frau und meine Tochter noch leben.<new_line>Ich zwinge mich selbst dazu, die Hoffnung nicht zu verlieren, immer und immer wieder. Wenn ich die Hoffnung verliere – hat mein Leben keinen Sinn mehr.]],
			['20'] = [[In einer alten, schiefen Scheune neben dem Grab des alten Mannes fand ich einige Seiten aus Sashas Tagebuch. Ein Kerzenstummel, die leeren Konservendose und die Matratze auf dem nackten Holzboden deuteten an, dass Sasha vor Kurzem hier gelebt hatte.<new_line>Die letzten Seiten war viel später geschrieben worden: auf einem anderen Papier, mit einem anderen Stift und auch mit einer etwas veränderten Handschrift.<new_line><new_line>Ich war schockiert, wie sehr sich meine Tochter in nur zwei Jahren verändert hatte ... Es war noch nicht lange her, da war Sasha beim Anblick ihres ermordeten Onkels und ihrer getöteten Tante speiübel gewesen, und zwei Jahre später schlitzte sie zehn Männern die Kehlen auf, ohne mit der Wimper zu zucken.<new_line>Nein, ich war froh, dass meine Tochter überlebt hatte. Ich war glücklich darüber, dass sie gelernt hatte, sich zu verteidigen, aber ... Egal wie jämmerlich sich das jetzt auch anhören mag, es war nicht die Zukunft, die ich mir für sie gewünscht hatte.<new_line>Ich muss ihr auf der Spur bleiben und nach Osten gehen.]],
			['heal_sasha'] = [[<new_line><new_line>Erforderlich:<new_line>- Sasha heilen]],
			['name'] = [[Heimweg]],
			['8'] = [[Wenn ich mir das Gebäude mit der Hausnummer 10 in der Bakinsky-Straße betrachte, brodelt in mir ein Sturm der Gefühle. Das Haus hat die Bombardierung beinahe unbeschadet überstanden: Die einzigen Anzeichen der Katastrophe waren die zersplitterten Fenster und der Müll, der vor der Tür lag. Und die Stille. Und das Halbdunkel. Alles andere war so, wie ich es in meiner letzten Erinnerung gesehen hatte.<new_line>Ich stand vor der Tür unserer Wohnung. Ich musste einfach auf die Türklingel drücken. Einen Moment lang bildete ich mir ein, ich würde auf der anderen Seite Schritte hören, die sich der Tür näherten. Aber es war nicht so. Ich packte den Türgriff und die Tür schwang quietschend auf. <new_line>Als ich die Wohnung sah, ballte ich die Hände zu Fäusten. Die Wohnung, unsere Wohnung, war geplündert worden!]],
			['7_up'] = [[Als ich die Sachen durchsuchte, zerkratzte mir etwas die Handfläche. Ich leuchtete in die Tasche und zog einen Wolfszahn heraus. Warum trage ich diesen Mist mit mir herum? Der Fangzahn war vergilbt und roch nach Schimmel, aber er war immer noch scharf ...]],
			['7_dream'] = {
				['3'] = [[„Ich gehe jetzt besser“, stammle ich und drehe mich zur Tür um.<new_line>„Darf ich dir ... einen Abschiedskuss geben?“<new_line>„Ich ... Ja ... In Ordnung ...“<new_line>Das war unser letzter Kuss.<new_line>Ich gehe hinaus in den Gang und schließe hinter mir die Tür. Ich schaue mir das Türschild an, die von Gänseblümchen aus Metall eingefasste Wohnungsnummer. Ich werde sie niemals wiedersehen. Ich steige die Treppe hinunter und gehe über die Bakinsky-Straße. Die kühle Aprilsonne scheint auf mich herab ...]],
				['2'] = [[„Kommst du zurück?“<new_line>Ich antworte nicht. Das ist auch nicht nötig, denn sie hat es schon verstanden.<new_line>„Du weißt, dass es so besser ist“, sage ich und starre zu Boden.<new_line>„Wahrscheinlich“, antwortet Galya.<new_line>Ich hebe meinen Kopf und sehe eine kleine Träne in ihrem Augenwinkel.]],
				['1'] = [[... Ich stehe mit einem Koffer in der Hand und einem schweren Rucksack auf dem Rücken vor der Tür unserer Wohnung.<new_line>Galya hält mir einen alten Talisman entgegen – den Fangzahn eines Wolfs.<new_line>„Du solltest ihn besser behalten. Du weißt, dass ich nicht an dieses Zeug glaube.“<new_line>Galya seufzt traurig.<new_line>„Wirst du dich von Sasha verabschieden?“<new_line>„Sie schläft noch. Ich brauche sie jetzt nicht zu wecken. Wir haben uns gestern schon über alles unterhalten.“]]
			},
			['6_up'] = [[Eine Scherbe liegt auf dem Boden – von einer Keramiktasse. Eine weitere Erinnerung ...]],
			['23_1'] = [[Schon als wir uns Ufa näherten, sahen wir die dunklen Punkte, die über den Ruinen kreisten.<new_line>Wir kamen rasch beim Militärstützpunkt an. Wir hatten ihn noch einmal betreten, als Kugel auf das Dach der Basis zeigte, sein Gesicht weiß wie ein Bettlaken. Darüber schwirrte eine Biene von alptraumhaften Ausmaßen. Das mutierte Insekt sah uns und kam im Sturzflug auf uns zu.]],
			['6_dream'] = {
				['3'] = [[Ich fühle, wie mich all meine Kraft verlässt. Ich spüre ein schmerzhaftes, beklemmendes Gefühl in meiner Brust. Ich gehe von der Tür weg.<new_line>Galya steht auf und wirft sich einen alten Mantel über.<new_line>„Weißt du, was sie gestern zu mir gesagt hat? ‚Mama, können wir uns nicht einfach einen anderen Papa suchen?‘“<new_line>Meine Ohren klingeln. Galya geht. Ich blicke betreten zu Boden und sehe die Scherben von Galyas Lieblingstasse.]],
				['2'] = [[Galya lässt sich schlapp auf den Stuhl sinken.<new_line>„Komm schon!“ Inzwischen rede ich nicht mehr ruhig mit ihr, sondern schreie sie an.<new_line>„Ich bin dir vollkommen egal, das weiß ich schon lange“, fährt mich Galya schnippisch an. „Aber was hat Sasha dir getan? Siehst du nicht, dass sie ohne Vater aufwächst? Wann hast du das letzte Mal etwas mit ihr unternommen? Wann hast du dich das letzte Mal mit ihr unterhalten? Weißt du, dass man mich jede Woche in die Schule ruft? Ich gehe immer nach meiner Schicht dorthin und muss mir anhören, dass sie kein Lehrer mehr zügeln kann und sie in allen Kursen durchfällt. Wenn ich dann wieder nach Hause komme, rede ich mit ihr, um sie wieder zur Vernunft zu bringen. Hast du überhaupt eine Ahnung, was hier vor sich geht?“]],
				['1'] = [[„Galya, es reicht, du musst mich nicht mehr länger mit deinem Schweigen bestrafen!“ Ich tigerte wie ein verwundetes Tier durch die Wohnung. „Die nächsten Tage will ich nur herauszufinden, womit ich dich dieses Mal verärgert habe.“<new_line>Ich greife nach Galyas Armen.<new_line>„Fass mich nicht an!“, krächzt Galya. „Lass mich gehen.“<new_line>„Du gehst nirgendwohin!“ Ich stelle mich vor die Tür. „Sag mir, was jetzt schon wieder los ist!“]]
			},
			['5_up'] = [[Ein scharfes, unangenehmes Geräusch ertönte irgendwo über unseren Köpfen. Ich blickte hinauf und sah einen gebogenen Blechstreifen unter der Dachkante herausragen. Die Windböen hatten ihn verbogen, aber er bewegte sich im Luftzug und klapperte schrill. Das Geräusch weckte in mir eine Erinnerung.]],
			['23_2'] = [[Der Kampf war schnell vorbei, aber der Ingenieur fand immer noch die Zeit, mich währenddessen mehrere Male zu verfluchen.<new_line>Wir müssen uns an die Arbeit machen, ehe noch andere Bienen auftauchen.]],
			['5_dream'] = {
				['3'] = [[Galya eilt zum Tisch und nimmt meine Hand in die ihre.<new_line>„Liebling, bitte vergib mir, vergib mir all die schrecklichen Dinge, die ich gesagt habe! Ich lag falsch, ich bin nicht viel wert, aber ich verspreche, dass ich mich nicht mehr so verhalten werden. Als du gestern Abend nicht nach Hause gekommen bist, bin ich durchgedreht! Ich weiß, was du alles durchmachst, wie hart du arbeiten musst. Ich weiß, dass du unglücklich bist. Du hast dir das Leben anders vorgestellt! Sag mir einfach, was ich tun soll und wie ich dir helfen kann. Ich liebe dich! Ich tue alles für dich! Sag mir nur, was du brauchst!“]],
				['2'] = [[„Ich möchte reden“, antwortet Galya ebenso leise.<new_line>„Du hast schon alles gesagt, was es zu sagen gibt. Und du hast kein Blatt vor den Mund genommen. Was gibt es da noch hinzuzufügen?“]],
				['1'] = [[... Hinter dem Fenster ist es dunkel, und der Schnee heult vorbei. Aber da ist noch ein anderes Geräusch – ein scharfes, kreischendes Geräusch, das sich anfühlt, als würde es meinen Schädel durchbohren. Einer der Nachbarn wirft Metallabfall aus dem Fenster.<new_line>Galya kommt langsam in die Küche. Sie sieht viel älter aus als in meiner letzten Erinnerung. Sie wirkt, als hätte sie geweint. Sie steht nur da und starrt mich schweigend an.<new_line>Ich drehe mich weg und angle eine weitere Zigarette aus der Packung.<new_line>„Was willst du?“, frage ich leise. Sasha schläft im gegenüberliegenden Zimmer.]],
				['4'] = [[Ich sehe ihr in die Augen. Ich sehe darin den Schmerz und die Leidenschaft. Aber was sieht sie in meinen Augen? Angst und Verzweiflung?]]
			},
			['27_1'] = [[Wir mussten nur eine Rakete abfeuern, um das Tor zu öffnen. Kugel hat nicht zu viel versprochen. <new_line>Boris hatte recht, wir sind mit nur minimalen Verlusten durchgebrochen. Aber der Anführer der Räuber war leider zu klug. Als hätte er von dem geplanten Angriff gewusst. Als die Rakete das Tor zerfetzte, standen die Banditen bereits in Kampfposition. Einige von ihnen waren mit Granatwerfern bewaffnet. Die anstürmenden Kämpfer aus Nowokusnezk liefen in einen Hinterhalt. Und die Räuber zogen sich noch immer nicht zurück. Nicht einmal, als die ersten Antipersonenminen explodierten.]],
			['27_2'] = [[Wir haben es geschafft, allerdings nicht ohne Opfer. Zwei oder drei Räuber hatten überlebt, sprangen in einen UAZ und fuhren durch das Haupttor in das Ödland. Wir haben sie nicht verfolgt, da wir uns erst einmal um unsere Verwundeten kümmern mussten.<new_line>Dann detonierte eine Granate im obersten Stockwerk eines der Gebäude. Was war da los?]],
			['16'] = [[Zu meiner Überraschung fand ich Marinas Wohnung auf Anhieb. Ich war erst zweimal dort gewesen, aber mein Gedächtnis ließ mich nicht im Stich.<new_line>Die Wohnung sah so aus, als hätten Marina und ihr Ehemann Arthur in aller Seelenruhe die Sachen gepackt und wären ohne jede Hast verschwunden. Da war kein Durcheinander, selbst das Bett war ordentlich gemacht, und auf der Kommode standen noch die dämlichen Figürchen aus Buntglas.<new_line>Auf dem Kaffeetisch im Nebenzimmer fand ich eine alte Bleistiftzeichnung. Ich erkannte sofort, dass sie von Sasha war. Meine Tochter war künstlerisch sehr begabt, aber sie zeichnete nicht sehr oft. Sie hatte einfach nicht die Geduld dafür.<new_line>Auf der Zeichnung sah ich eine Wiese. Da saßen drei Menschen und aßen glücklich Sandwiches auf einer beigefarbenen Picknickdecke. Galya, Sasha und ... ich.<new_line>Ich muss jeden Winkel der Wohnung durchsuchen.]],
			['29'] = [[Als ich die Kommandozentrale verließ, rief ich mir meine Unterhaltung mit Sasha in der Abakan-Basis ins Gedächtnis zurück. War in ihrem Körper noch ein Rest meiner Tochter? Ich erinnerte mich an die Qual in ihren Augen, als sie mich erkannte. Vielleicht war doch noch nicht alles verloren? Wenn die Erinnerung schmerzt, kann man noch fühlen! <new_line>Eines war gewiss: Wir mussten uns noch einmal unterhalten. Ich musste einfach erfahren, was sie in der Abakan-Basis mit ihr angestellt und wie sie diese Räuber dazu überredet hatten, die Seiten zu wechseln. Ich musste herausfinden, wie sie so geworden war.<new_line>Eine andere Sache nagte auch noch an mir.]],
			['19'] = [[Im Haus des pensionierten Generals, des schrecklich misstrauischen und ewig mürrischen Vaters von Marina und Galya, fand ich weder ihn, den General, noch Sasha, noch Marina und auch nicht ihren Mann. Ich habe nicht einmal das Haus gefunden.<new_line>Nur Asche.]],
			['15'] = [[... Ich starre auf das zerschmetterte Fenster des alten Cafés und meine Aufmerksamkeit wird nach innen gelenkt. Ganz hinten in meinem Gedächtnis wabert etwas, aber es steigt nicht bis an die Oberfläche meines Bewusstseins. Plötzlich erinnere ich mich an einen Streit. Einen lauten und bösen Streit mit Marina, Galyas älterer Schwester. Wir sind nie gut miteinander ausgekommen, aber an diesem Tag hatte Marina versucht, vor Sasha auf mir herumzuhacken. Ich antwortete Marina ziemlich bissig, packte Sasha wütend bei der Hand, stand von Tisch auf und wir gingen nach Hause. In mir brodelte der Zorn. Es ist doch egal, sagte ich mir immer wieder, du musst sie doch nur noch einen einzigen Tag ertragen, denn morgen geht diese Schlampe Marina wieder nach Hause – nach Perm ...<new_line>Perm! Marinas Wohnung ist in Perm! Galya und Sasha könnten dorthin gegangen sein.]],
			['33'] = [[Sasha warf sich herum und stöhnte. Leise murmelte sie etwas, das für mich keinen Sinn ergab. Ich hörte aufmerksam zu, aber ich verstand sie nicht. Dann fiel sie wieder in einen unruhigen Schlaf.]],
			['35'] = [[Ich denke, dass es Sasha blad wieder gut gehen wird. Sie hat eine überraschend widerstandsfähige Konstitution, Und die Arzneimittel helfen auch.]],
			['25'] = [[Kugel ist weg. Jetzt kann ich zurück nach Nowokusnezk gehen und die Basis der Abakan-Räuber angreifen. Hoffentlich hat Boris seine Meinung in meiner Abwesenheit nicht geändert. Ich hoffe auch, dass der Angriff nicht umsonst ist. Wer weiß, ob meine Sasha noch lebt ...]],
			['31'] = [[Ich fuhr mit Höchstgeschwindigkeit querfeldein, zerfetzte dabei Setzlinge, pflügte durch Löcher und brachte den Wagen in jeder Kurve fast zum Überschlagen. Sasha stöhnte, krümmte sich und drückte einen Verband auf ihren Bauch: Es war eine schlimme Wunde. Jedes Mal, wenn wir über ein Schlagloch fuhren, schrie Sasha vor Schmerzen. Aber was konnte ich schon anderes tun?<new_line>„Halte durch, mein Schatz“, flüsterte ich ihr zu. „Alles wird gut. Halte nur noch ein wenig durch.“<new_line>Ich würde sie heilen. Egal was es mich kostet.<new_line>Wir waren noch immer zu nah bei Stepnoy. Nach allem, was passiert war, würden sie uns garantiert verfolgen.]],
			['27'] = [[Wir mussten nur eine Rakete abfeuern, um das Tor zu öffnen. Kugel hat nicht zu viel versprochen. <new_line>Boris hatte recht, wir sind mit nur minimalen Verlusten durchgebrochen. Aber der Anführer der Räuber war leider zu klug. Als hätte er von dem geplanten Angriff gewusst. Als die Rakete das Tor zerfetzte, standen die Banditen bereits in Kampfposition. Einige von ihnen waren mit Granatwerfern bewaffnet. Die anstürmenden Kämpfer aus Nowokusnezk liefen in einen Hinterhalt. Und die Räuber zogen sich noch immer nicht zurück. Nicht einmal, als die ersten Antipersonenminen explodierten.<new_line>Wir haben es geschafft, allerdings nicht ohne Opfer. Zwei oder drei Räuber hatten überlebt, sprangen in einen UAZ und fuhren durch das Haupttor in das Ödland. Wir haben sie nicht verfolgt, da wir uns erst einmal um unsere Verwundeten kümmern mussten.<new_line>Dann detonierte eine Granate im obersten Stockwerk eines der Gebäude. Was war da los?]],
			['17'] = [[Neben dem Tisch, auf dem Boden, fand ich einige zerknüllte Seiten, die mit der schiefen Handschrift meiner Tochter vollgekritzelt waren.<new_line>Ich las beide Seiten und spürte, wie sich meine Stimmung hob. Galya und Sasha, gesund und munter, waren hier gewesen, genau in dieser Wohnung! Und es kam noch besser! Ich wusste, wohin sie gegangen waren!<new_line>Voller Optimismus las ich den gesamten Tagebucheintrag meiner Tochter noch einmal. Mannomann, ihre Handschrift war wirklich fast unleserlich! Vielleicht ist es ihr Schicksal, dass sie einmal eine Ärztin wird. Und Galya, es sah ihr ähnlich, ein verlassenes Kind bei sich aufzunehmen und durchzufüttern!<new_line>Doch als ich die Seiten weglegte, kam mir ein Gedanke. Was war mit dem kleinen Mädchen geschehen? Sind ihre Eltern mitten in der Nacht zurückgekehrt, von Schuld getrieben, und haben ihre Tochter geholt. Warum hätten sie eigentlich alleine abhauen sollen? Und warum hat sie Galya nicht mitgenommen? Und was hat es dieser verschlossenen Tür auf sich?<new_line>Was auch immer das bedeutet, ich muss nach Kurgan.]],
			['1_up'] = [[An der Bushaltestelle sah ich in einem Haufen alter Lumpen etwas Ungewöhnliches. Es war eine angelaufene Veteranenmedaille aus dem Zweiten Weltkrieg. Ich hob sie auf und aus dem Tiefen meines Verstands schwappte eine Erinnerung nach oben ...]],
			['2_up'] = [[Ich ging zum Flussufer. Ich streckte beide Hände aus und tauchte sie ins kalte Wasser. Als das Wasser zwischen meinen Fingern hindurchfloss, erwachte eine neue Erinnerung in meinem Kopf ...]],
			['3_up'] = [[Ich sah mich in der Wohnung im zweiten Stock des Wohngebäudes um. Im ganzen Schlafzimmer waren Habseligkeiten verstreut und auf dem Boden lag ein großer Koffer. Offenbar hatten die Bewohner ihre Sachen gepackt, als irgendetwas vorgefallen war und sie schnell aus der Wohnung fliehen mussten. In dem offenen Koffer lag nur ein Hochzeitskleid. Ich legte es auf das Bett und strich mit der Hand darüber. Eine weitere Erinnerung ...]],
			['4_up'] = [[Neben dem umgestürzten Mülleimer lag eine kleine Stoffpuppe. Eines ihrer Knopfaugen fehlte, ein Teil ihres Beines war abgerissen, und die graue Füllung quoll heraus. Auf dem kleinen Kleidchen hatte sie einen großen, hässlichen Fleck ... Eine neue Erinnerung.]],
			['13'] = [[Acht Seiten ... Hier steht viel über „Jane Doe“, aber ich hoffe, Galya hat alle Dokumente für den Krankenhausaufenthalt vorbereitet. In ihrer typisch gewissenhaften Art hat sie auch alle Etiketten von Sashas Kleidungsstücken gekennzeichnet. Ich muss weiterlesen.]],
			['21'] = [[Wenn Sasha noch immer von den Abakan-Räubern gefangen gehalten wird, werde ich sie retten. Wenn nicht, werde ich mich vergewissern müssen, dass ich nicht mehr länger weitersuchen muss.<new_line>Das Oberhaupt der Stadt Nowokusnezk sagte, sie würden für den Angriff eine Panzerabwehrlenkwaffe brauchen. Der Mann schlug vor, dass ich mich in Ufa danach umsehe. Auf dem Weg dorthin muss ich einen Abstecher nach Tscheljabinsk machen, um dort mit dem Ingenieur Kugel zu sprechen.]],
			['11'] = [[In der Schreibtischschublade im Büro des Krankenhausleiters fand ich einen Ordner mit den Listen der Toten. Die Listen wurden solange erstellt, bis offensichtlich wurde, dass die Krankheit letztendlich alle Menschen töten würde. Ich sollte sie besser durchgehen.]]
		},
		['project_emba'] = {
			['48'] = [[Ich habe den Archivaren geholfen, mit der biologischen Bedrohung fertig zu werden, und wir haben das Relikt-Myzel in Sicherheit gebracht.<new_line>Die katastrophalen Folgen des E.M.B.A.-Projekts haben die Pläne von Progress beeinträchtigt. Die Wissenschaftler haben beschlossen, ihre Aktivitäten auf den Schutz der Technologie zu verlagern, anstatt sie zu erfinden und zu restaurieren. Ich hoffe, dass ich Dr. Issajew eines Tages wiedersehen werde.]],
			['3'] = [[Das Progress-Team ist bereit, zur nächsten Siedlung aufzubrechen. Sobald ich fertig bin, muss ich mich bei Dr. Issajew melden.]],
			['2'] = [[Ich habe dem Biologen bei der Forschung geholfen, jetzt muss ich zurück zu Dr. Issajew.]],
			['1'] = [[Ich habe die Blackbox gefunden. Jetzt muss ich die notwendigen Gegenstände für die Forschung sammeln und dem Biologen helfen, das Labor zu verbessern.]],
			['101'] = [[Hilf dem Biologen bei seinen Forschungen.]],
			['name'] = [[E.M.B.A.-Projekt]],
			['6'] = [[Ich muss die Blackbox finden. Der Navigator kann dabei helfen. Ich muss mit ihm reden.]],
			['5'] = [[Ich muss die Aufträge der Archivare ausführen, damit wir die geheimen Dokumente finden können. Ich muss die Dokumente dem Biologen geben und ihm bei seinen Nachforschungen helfen.<new_line>Die Aufträge können in der Bar der Siedlung erhalten werden.]],
			['4'] = [[Ich nehme an der Forschungsexpedition der Progress-Organisation auf der Suche nach einer geheimen Technologie teil.<new_line>Ich habe die notwendigen Daten erhalten. Ich muss zum nächsten Standort der Archivare gehen und mit Dr. Issajew sprechen.]],
			['47'] = [[Die Begrünungsaktion ist fehlgeschlagen, aber Issajew hat noch weitere Aufgaben für mich, also muss ich zurückgehen und mit ihm reden.]],
			['24'] = [[Ich muss die Blackbox finden, um die Forschung von Progress fortzusetzen. Die Blackbox befindet sich in der Bio-Station in der Wissenschaftsstadt.]],
			['9'] = [[Ich habe die Blackbox gefunden. Jetzt muss ich die notwendigen Gegenstände für die Forschung sammeln und dem Biologie helfen, das Labor zu verbessern.<new_line>Danach muss ich mit Dr. Issajew sprechen.]],
			['8'] = [[Ich muss die Blackbox finden, um die Forschung von Progress fortzusetzen. Die Blackbox befindet sich in der Bio-Station in der Wissenschaftsstadt. Um die Box zu bekommen, muss ich mich jedoch mit dem dort lebenden Biomutanten auseinandersetzen.]]
		},
		['common_complite'] = [[Ich habe alles gefunden, was ich brauche. Jetzt muss ich zurück und meine Belohnung abholen.]],
		['new_tutorial'] = {
			['name'] = [[Los jetzt!]],
			['3'] = [[Das Gemüse im Kühlschrank war schon lange zu einem stinkenden Matsch verfault, aber im unteren Regal eines heruntergekommenen Schranks fand ich Streichhölzer, Buchweizen und eine Dose gedünstetes Fleisch. Diese kleine Dose zu finden fühlte sich an, als hätte ich den Jackpot einer Lotterie geknackt!<new_line><new_line>- Öffne das Inventar.<new_line>- Wähle das Dosenrindfleisch.<new_line>Tippe auf „Essen“.]],
			['2'] = [[Während ich ging, konnte ich meine Beine kaum vorwärtsbewegen und neue Fragen kamen mir in den Sinn. Fragen, die ich nicht beantworten konnte.<new_line>Wer bin ich? Woher komme ich? Wie bin ich hierhergekommen?<new_line>Ich versuchte, mich an Kleinigkeiten aus meiner Vergangenheit zu erinnern, aber mein Gedächtnis versagte mir den Dienst.<new_line>Das Gebäude steht leer – und das offensichtlich schon lange. Die Türen zu den Wohnungen sind verschlossen, aber durch ein Loch in der Wand konnte ich eine davon betreten.<new_line>Kaputte Möbel, verdorrte Zimmerpflanzen, zerbrochene Teller auf dem Boden, aus einem zusammengebrochenen Regal gefallene Bücher ... Ich saß in einem einsamen, unversehrten Stuhl und fühlte einen nagenden Schwerz in meinen Eingeweiden. Ich brauche Nahrung.<new_line><new_line>- Öffne das Ortsmenü.<new_line>- Tippe auf Suchen, um Gegenstände zu erhalten.<new_line>- Tippe auf „Alles nehmen“.]],
			['1'] = [[Ich öffnete meine Augen und atmete durch. Eiskalte Luft brannte in mir. <new_line>Ich konnte mich nur unter Schmerzen bewegen. Ich stützte meinen Ellbogen auf den rauen Asphalt, biss die Zähne zusammen und stemmte mich etwas vom Boden hoch. Der schwarze Schleier begann sich vor mir zu lüften und etwas schälte sich aus dem Nebel.<new_line>Leblose Ruinen ... <new_line>Nicht eine lebende Seele ...<new_line>Was ist hier geschehen? Wo bin ich? Was mache ich jetzt?<new_line>Ich zitterte unkontrolliert. Ich musste einen Plan schmieden. Ich musste mich bewegen, solange ich noch die Kraft dazu hatte. Der Wind begann zu heulen und schleuderte mir Sand und Staub in das Gesicht, verklebte Augen, Nase und Mund. Ich hustete und mein ganzer Körper erbebte.<new_line>Direkt neben mir stand ein Gebäude. Darin konnte ich Unterschlupf finden. Der Schmerz war unerträglich, aber ich kämpfte mich auf die Füße und taumelte vorwärts.<new_line><new_line>- Tippe auf die Karte und wähle eine Richtung.<new_line>- Oder benutze in den Aufgaben die Schaltfläche „Auf Karte ansehen“.<new_line>- Mit der Schaltfläche „Los“ beginnt deine Reise.]],
			['7'] = [[Ich zwang mich dazu, dieses Wasser zu trinken. Bereits nach wenigen Schlucken fühlte ich mich schlecht, aber dann saugte ich die Flüssigkeit in mich hinein wie ein Verdurstender in der Wüste. Der Durst ist eine Macht. <new_line>Ich kletterte vom Kraterrand und setzte mich. Ich dachte, ich müsste mich übergeben, aber dieses Gefühl ging vorbei.<new_line>Ich brauche mehr Wasser, um meine Vorräte aufzufüllen, aber der schmierige Schleim aus diesem Krater wird mich schneller umbringen als jeder Durst. Ich frage mich, ob es auch Krater mit sauberem Wasser gibt?<new_line>Ich muss sie finden. Dann verlasse ich diese Stadt. Der Anblick dieser Ruinen lässt mich schaudern.<new_line><new_line>- Gehe zu einem anderen Krater.]],
			['6'] = [[Trotz meines schrecklichen Durstes durchläuft mich Ekel, als das Wasser meine Lippen berührt. Der unterschwellige bittere Geschmack und die gelben Ölflecke auf der Oberfläche erfüllen mich nicht gerade mit Zuversicht, aber ich habe keine andere Wahl. Wenn ich nichts trinke, werde ich gleich hier an diesem Krater zusammenbrechen – und nie wieder aufstehen.<new_line><new_line>- Öffne das Inventar.<new_line>- Wähle das schmutzige Wasser.<new_line>- Tippe auf „Trinken“.]],
			['5'] = [[Ich humple zu dem Krater und ersticke fast an der staubigen Luft. Meine Kehle sticht, als würde ich von wütenden Wespen angegriffen. Meine Lippen sind ausgetrocknet und aufgeplatzt und meine Augen tränen, dass ich kaum noch etwas erkennen kann. <new_line>Meine Hände sind zerschrammt und blutig. Ich klettere auf den Rand des Kraters und sehe hinein. <new_line>Ich hatte recht – Wasser!<new_line><new_line>- Öffne das Ortsmenü.<new_line>- Tippe auf „Suchen“, um Gegenstände zu erhalten.<new_line>- Tippe auf „Alles nehmen“.]],
			['4'] = [[Die Nahrung hat meine Stärke etwas regeneriert. Selbst die Schmerzen haben nachgelassen.<new_line>Was jetzt? Wo soll ich hingehen? <new_line>Ich starre auf das Foto des Wohnungseigentümers, der Rahmen völlig verstaubt. Ich muss nach anderen Menschen suchen. Gibt es in dieser zerstörten Stadt überhaupt noch lebende Menschen? Und wenn nicht, was ist mit ihnen passiert? Was zum Teufel ist hier geschehen? <new_line>Wenn es in dieser Stadt keine Menschen gibt, werde ich sie anderswo finden. Aber zuerst einmal muss ich einen Vorrat an Nahrung und Wasser anlegen. In dieser Wohnung gibt es kein Wasser. <new_line>Durch das zerbrochene Fenster sehe ich auf der anderen Straßenseite einen Krater. Darin glitzert etwas. Ist das vielleicht Wasser?<new_line><new_line>- Gehe zur Karte.<new_line>- Oder benutze die Schaltfläche „Auf Karte ansehen“.<new_line>- Gehe zu dem Krater.]],
			['9'] = [[Ich sammelte die Feuersteine in dem Krater ein, als ich hinter mir ein Rascheln hörte. Auf dem Kraterrand saß eine Ratte. Ich sah genauer hin und da waren drei weitere Ratten. Sie bewegten sich nicht, sondern starrten mich nur mit ihren roten Augen an. Die kleinen Klauen an ihren Zehen klackerten zitternd auf dem Boden.<new_line>Die Ratten waren abgemagert, aber riesig. Sie gingen mir bis zum Knie. Ich bekam Angst. Ich hielt den Atem an, kletterte langsam aus dem Krater und zog mich zu dem nächsten Gebäude zurück. <new_line>Die Ratten folgten mir zögerlich. Es wurden immer mehr. Fünf ... Nein, sechs ... Sie wirkten ebenfalls ängstlich, aber sie waren viele, und starker Hunger kann die Angst verdrängen.<new_line>Sieben ... Neun ...<new_line>Für den Bruchteil einer Sekunde erstarrte ich in der Bewegung. Dann rannte ich blindlings aus der Stadt.<new_line><new_line>- Verlasse die Stadt.<new_line>- Erreiche den Wald.<new_line>- Wenn dich eine Ratte angreift, fliehe vor ihr.]],
			['8'] = [[Diesen Krater erreichte ich viel schneller als den ersten. Ich fühlte mich besser, selbst nach diesem widerlichen Schleim. Wenn der Tod die einzige Alternative ist, kann sich der Körper an alles anpassen.<new_line>Aus diesem Krater strömte kein fauliger Geruch, und das weckte Hoffnung in mir.<new_line>Aber es war umsonst. In diesem Krater war kein Wasser! Ich nahm einen glatten Stein in die Hand und untersuchte ihn näher. Er sah aus wie ein Feuerstein. Ich könnte ihn noch gebrauchen, um ein Feuer zu entfachen.<new_line><new_line>- Sammle alle Feuersteine an diesen Ort.<new_line>- Drücke im Ortsmenü auf „Suchen“, bis du alle Gegenstände gefunden hast.]],
			['17'] = [[Du bist jetzt auf dich alleine gestellt, viel Glück!<new_line><new_line>Noch ein paar letzte Tipps:<new_line><new_line>- Du hast neue Aufgaben – schau sie dir an.<new_line><new_line>- Die Zahlen oben zeigen den Zustand deines Charakters an: Tippe auf die Symbole vor einer Zahl, um mehr zu erfahren.<new_line><new_line>- In den kommenden Dörfern gibt es hohe Strahlenwerte. Achte darauf und lass die Verstrahlung nicht über den Wert 40 steigen.<new_line><new_line>- In der Nacht solltest du dich nicht bewegen und nach nichts suchen: Du könntest dir leicht die Knochen brechen.<new_line><new_line>- Sammle mehr Wasser und Lebensmittel, ehe du auf eine lange Reise gehst.<new_line><new_line>- Dein Charakter könnte in Ohnmacht fallen, wenn er sich nicht rechtzeitig ausruht.<new_line><new_line>- Wenn du kein Floß hast, solltest du nicht zu nah an das Wasser gehen.<new_line><new_line>- Denk nach, ehe du etwas tust, und du wirst viel länger leben ...]],
			['16'] = [[Ich hätte nie gedacht, dass einfacher gekochter Buchweizen ohne Salz, Milch oder Öl so köstlich schmecken kann! Ich schlang ihn gierig in mich hinein, als könnte ihn mir jemand jeden Augenblick aus der Hand reißen.<new_line>Jetzt ist die Zeit gekommen, um ein wenig zu schlafen. Zu etwas anderem bin ich nicht mehr in der Lage.<new_line>Und wenn ich aufwache, muss ich weiterziehen. Ich muss los und andere Menschen finden.<new_line>Und Antworten.<new_line><new_line>- Schließe das Aufgabenmenü.<new_line>- Um zu schlafen, tippe auf die Zeltschaltfläche.<new_line>- Wenn du aufwachst, gehe nach Sapoljarny.<new_line><new_line>(In einem Lager mit einem Feuer und einer Hütte schläft dein Charakter schneller aus.)]],
			['15'] = [[Ich konnte das Feuer in wenigen Sekunden entzünden. Ich streckte meine Hände danach aus und fühlte, wie endlich wieder Leben in mich floss. Eine angenehme Wärme breitete sich in meinem ausgemergelten Körper aus. Ich wollte gerade in meine kleine Hütte kriechen und ein wenig ausschlafen, als sich mein Magen verkrampfte. <new_line>Ich betrachtete die fetten weißen Pilze und beäugte argwöhnisch die verfaulte Schmiere, die auf ihnen klebte. Nein, das sollte ich besser nicht riskieren. Ich wollte stattdessen lieber Buchweizen kochen. <new_line>Dafür ist sauberes Wasser nötig. Allein der Gedanke an das widerliche Wasser in dem Krater ließ in mir wieder die Übelkeit hochsteigen. <new_line>Ich muss das Wasser in einer leeren Dose abkochen.<new_line><new_line>- Öffne das Inventar.<new_line>- Wähle das schmutzige Wasser.<new_line>- Tippe auf „Kochen“.<new_line>- Koche den Buchweizen und iss den gekochten Buchweizen.]],
			['14'] = [[Nachdem ich das fertige Messer gewissenhaft untersucht und unter meinen Gürtel gesteckt hatte, fühlte ich mich etwas zuversichtlicher. <new_line>Jetzt muss ich ein Feuer entfachen und eine Hütte errichten. Die Temperatur ist gesunken und ich zittere in meiner dünnen Jacke.<new_line><new_line>- Öffne das Plänemenü.<new_line>- Wähle das Feuer und tippe auf „Entzünden“.<new_line>- Wähle die Hütte und tippe auf „Zusammenbauen“.]],
			['12'] = [[Das fertige Werkzeug hat nur vage Ähnlichkeit mit einer Axt, aber ich habe keine Zeit, jetzt noch einmal von vorne anzufangen.<new_line>Es wird Zeit, diese Aufgabe zu erledigen.<new_line><new_line>- Öffne das Inventar.<new_line>- Wähle das Feuersteinbeil.<new_line>- Tippe auf „Holz hacken“.]],
			['13'] = [[Es ging jetzt viel schneller, sogar mit einer selbst gemachten Axt. Ich hackte Zweige, bis mir die Arme und der Rücken vor Erschöpfung brannten. Jetzt hatte ich ausreichend Brennholz. Das sollte eine Weile reichen. Ich schnürte es zu einem Bündel zusammen und kehrte zurück zu dem Dickicht.<new_line>Die Axt funktionierte wie geplant. Aus dem zweiten Stück Feuerstein werde ich ein Messer herstellen. Wenn ich von den Ratten angegriffen werde, kann ich mich verteidigen.<new_line><new_line>- Fertige ein Feuersteinmesser.]],
			['10'] = [[Die Ratten haben mich nicht verfolgt. Ich rannte, bis ich ein verdorrtes Gebüsch erreichte, wo ich müde auf die rissige Erde sank.<new_line>Mein erschöpfter Körper verlangte nach Schlaf, aber ich zwang mich wieder auf die Beine. Zuerst einmal musste ich sicherstellen, dass ich hier geschützt war. Und ein wenig Nahrung auftreiben.<new_line>Hinter dem trockenen Gestrüpp liegt eine Waldfläche. Ich muss einige Zweige abbrechen und ein Feuer machen. Das wird auch unerwünschte Besucher fernhalten.<new_line>Und wenn es genug Zweige gibt, kann ich mir eine Hütte bauen.<new_line><new_line>- Öffne das Ortsmenü.<new_line>- Drücke auf „Suchen“, bis du genug Brennholz gefunden hast.]],
			['11'] = [[Ich brach die abgestorbenen Zweige von den Bäumen, als ich verschwommene Schatten zwischen den Bäumen herumhuschen sah. Vielleicht spielte mir mein Verstand einen Streich, aber vielleicht auch nicht ...<new_line>Ich stapelte meine Beute und beäugte sie kritisch. Nein, das würde nicht funktionieren! Es würde die ganze Nacht dauern, auf diese Weise ein Feuer aufzuschichten. Was kann ich sonst noch verwenden? Vielleicht kann ich aus dem Feuerstein eine Axt fertigen?<new_line><new_line>- Öffne das Plänemenü.<new_line>- Öffne den Mechanik-Reiter.<new_line>- Wähle den Plan einer Feuersteinaxt.<new_line>- Tippe auf „Herstellen“.]]
		},
		['searchOfTruth'] = {
			['3'] = [[Nach allem, was ich bisher gesehen habe, hat eine Krankheit in der Stadt gewütet. Eine regelrechte Epidemie: Neben der örtlichen Schule wurde sogar ein Feldlazarett eingerichtet, um die Massen der Erkrankten zu bewältigen.]],
			['2'] = [[Ich wurde von der Stadt mit Totenstille begrüßt. Ich ging hinein und stolperte über einen hässlichen Explosionskrater. Hatte es einen Krieg gegeben?<new_line><new_line>Ich muss möglichst viele Vorräte sammeln. Medizin, Nahrung, Kleidung. Fangen wir mit der Apotheke an.]],
			['1'] = [[Ich muss nach Menschen suchen und herausfinden, was geschehen ist. Hoffentlich finde ich in dieser Stadt Antworten.]],
			['7'] = [[Murmansk ist ausgestorben. Es gibt weder Strom noch Wasser. Überall liegen Skelette: In den Häusern, auf den Straßen, einfach überall ... Ich werde nach Hinweisen suchen, die die schrecklichen Tode und den Grund für mein Überleben erklären.<new_line>Ich muss den Militärstützpunkt untersuchen – die Armee muss gewusst haben, was los ist!]],
			['6'] = [[Einige Kilometer vor Murmansk endeten die Gleise an einem großen Krater, den eine Explosion in den Boden gesprengt hatte. <new_line><new_line>Daran komme ich nicht vorbei. Es wird Zeit, die Draisine zu verlassen und die Stadt zu Fuß zu erkunden.]],
			['5'] = [[Unterwegs stieß ich auf eine Draisine, so alt wie die Gleise. Als ich den Handhebel betätigte, bewegte sie sich trotz ihres maroden Aussehens problemlos. <new_line><new_line>Damit komme ich jedenfalls schneller nach Murmansk als zu Fuß.]],
			['4'] = [[Wenn ich mir den Evakuierungsplan so ansehe, haben die Bewohner Sapoljarny schon vor langer Zeit verlassen. Sie fuhren mit dem Zug nach Murmansk – näher an die Zivilisation, zu den großen Krankenhäusern mit besserer Versorgung. <new_line><new_line>Ich sollte den Eisenbahnschienen in Richtung Osten folgen. Dort finde ich vielleicht einen schnelleren Weg nach Murmansk.]],
			['9'] = [[Im November 1984 fielen die Atombomben auf Klein- und Großstädte in der UdSSR. Der Täter und seine Motive sind noch immer unbekannt.<new_line>Ich habe auch Funkmeldungen aus Kandalaksha abgefangen. Das ist weit weg, aber ich muss dorthin. Zuerst muss ich aber in Pushnoy anhalten und mehr Vorräte einpacken.]],
			['8'] = [[Der Militärstützpunkt befand sich in einem bemitleidenswerten Zustand: Ein paar der Gebäude lagen in Trümmern, andere waren ausgebrannt. Alles war darin verbrannt und das verkohlte Holz und das geschwärzte Metall war mit einer dicken Staubschicht bedeckt. Aber da waren nicht sehr viele Leichen. Wohin waren alle Bewohner der Basis verschwunden?]],
			['16'] = [[Wassili ist tot. Ich muss dem Schiffer alles erzählen und ihm das Tagebuch geben.]],
			['hint4'] = [[Erkunde die Kommandozentrale des Militärstützpunkts.]],
			['46'] = [[Nachdem ich Varya zu ihrer Tante gebracht hatte – übrigens eine sehr freundliche und nette Frau –, fragte ich die Einheimischen nach dem Forscher Gavrilov. <new_line>Zufällig kannte man in dieser Gegend seinen Namen. Als die Epidemie gerade ihren Höhepunkt erreicht hatte, floh Pavel Gavrilov zusammen mit seiner Frau von Zuhause und ging nach Kiew. Sie wollten wahrscheinlich nach Polen ausreisen, solange die Grenzen noch offen waren.]],
			['56'] = [[Im Touristenlager wurde ich gewarnt, dass der Zentralteil des Landes gefährlich hohe Strahlungswerte aufweist. Ich muss mich besser vorbereiten und den Einheimischen helfen, wenn ich schon dabei bin.]],
			['26'] = [[Endlich, ich habe etwas Interessantes gefunden! Das Tagebuch einer Wissenschaftlerin, einer Ärztin, die diese unbekannte Krankheit erforschte.<new_line>Sie hatte offenbar Erfolg ... Großartig! Sie hat also tatsächlich eine Behandlungsmethode gegen die Infektion gefunden! Aber was ist mit der Ärztin passiert? In ihren Aufzeichnungen erwähnte sie die Stadt Nowgorod.<new_line>Aber wenn sie einige Menschen heilen konnte, wo sind diese Menschen jetzt? Sind sie alle bei der Bombardierung ums Leben gekommen? Hoffentlich nicht. Wahrscheinlich sind sie vor den Einschlagstellen der Atombomben geflohen.<new_line>Es wird Zeit, dass ich mich zu diesem Krankenhaus in Nowgorod aufmache.]],
			['36'] = [[Die Nummer 28 am Krasnoarmeysky-Prospekt existiert nicht mehr. In einem Umkreis von einem halben Kilometer steht kein einziges Gebäude mehr.<new_line>Ich befand mich mitten in einem weitläufigen Ödland, übersät mit Ziegelsteinen, Beton und verkohltem Müll. An einigen Stellen ragten kläglich die Reste von Ziegelmauern und Betonplatten aus dem Boden. Ich vermute, dass hier im Jahr 1984 eine Atombombe explodierte, genau an der Stelle, an der ich jetzt stehe. <new_line>Niemand hier hätte das überlebt. Aber war Nadezhda Kulik aus der Stadt geflohen, ehe die Bombe gezündet worden war?]],
			['52'] = [[Die Wölfin ist tot. Doch dafür mussten wir einen hohen Preis bezahlen!<new_line>Als ich zum Schlachthaus kam, sah ich die Leichen von Gavrilov und den anderen. Die Toten mussten begraben werden. Aber wir waren nur so wenige. Und die restlichen Wölfe konnten jeden Augenblick zurückkommen?<new_line>Eine Stunde später waren wir wieder auf dem Weg nach Gorenichi. Hinter uns brannte ein riesiger Scheiterhaufen und fraß sich durch die Leichen der tapferen Krieger, die ihr Leben für ihre Familie und ihre Freunde geopfert hatten.]],
			['hint6'] = [[Durchsuche das Haus, den Krater und die Apotheke. Nimm alle Gegenstände mit, die du findest.]],
			['32'] = [[Ich habe herausgefunden, wo sie Swetlana festgehalten haben. Sie wurde bis Januar 1985 gefangen gehalten. Danach machte sie sich auf den Weg nach Twer. Und auch wenn bereits 5 Jahre vergangen sind, besteht immer noch die Chance, dass sie noch dort ist. <new_line>Zuerst muss ich aber ins Touristenlager zurückkehren und herausfinden, ob es in der Richtung noch eine Siedlung mit Überlebenden gibt.]],
			['42'] = [[Die Tür flog mit einem ohrenbetäubenden Krachen aus den Angeln und stürzte in die Wohnung. Ich wartete ab, bis sich der Staub gelegt hatte. Dann sah ich mich um. <new_line>Die Wohnung bestand aus zwei Zimmern. Der eine Raum war leer. Im anderen stand ich eine Couch. Und darauf saß das Skelett eines Mannes. Zuerst hielt ich ihn für Larisas Ehemann, aber in seiner Hosentasche fand ich einen Zugangspass für das Institut für Virologie mit dem Namen Leonid Schaschkow. Offenbar hatte ich meinen Wissenschaftler gefunden. Und die Krankheit hatte ihn getötet. <new_line>Schaschkows Brieftasche war leer. Und ich fand im ganzen Zimmer nichts Nützliches. Ich muss den anderen Raum durchsuchen.]],
			['12'] = [[Am Rande der Stadt sah ich den frischen Kadaver eines Wolfes. Er wurde mit einer Eisenfalle getötet! Hier muss es Überlebende geben! Ich muss die Gebäude durchsuchen ... Sie können nicht weit gekommen sein!]],
			['22'] = [[Am Stadtrand fand ich die Leiche eines Mannes, dessen totenstarre Hände eine Filmkamera umklammert hielten. Vor seinem Tod hatte er offensichtlich etwas gefilmt.<new_line>Keine Ahnung, ob ich aus dem Film etwas Neues erfahre, aber ich muss es unbedingt überprüfen. Ich muss ein Fotolabor finden, um diesen Film zu entwickeln.]],
			['name'] = [[Die Suche nach der Wahrheit]],
			['33_2'] = [[Ich muss mit Swetlana sprechen.]],
			['58'] = [[Der Schiffer erwähnte ein Wissenschaftsinstitut, in dem Menschen an einer Art Virus gearbeitet haben, er wollte mir aber nicht sagen, wo es ist. Er sagte, es sei zu gefährlich und ich wäre noch nicht bereit. Vielleicht hat er Recht. Ich habe gerade erst meine Erinnerungen zurückbekommen, und in meinem Kopf ist herrscht immer noch ein Durcheinander.<new_line>Vielleicht ist es eine gute Idee, ein paar Aufträge im Fischerdorf zu erledigen, um ein bisschen Geld zu verdienen und in Form zu kommen.]],
			['37_2'] = [[Als ich mich nach Nadezhda Kulik erkundigte, schickte man mich zur kleinsten Hütte. Es hatte den Anschein, als Stünde mir das Glück dieses Mal zur Seite. <new_line>Aber eines beunruhigte mich: Warum waren die Einheimischen so traurig, wenn sie über Nadezhda sprachen?]],
			['29'] = [[Warum habe ich das nicht schon früher bemerkt? Das ist keine Schranke! Es ist nur eine improvisierte Absperrung, eine einfache Planke, um die ein rotes Band gewickelt wurde. Aber in der Schlucht sehe ich einen noch viel verstörenderen Anblick: einen Haufen Skelette.<new_line>Vielleicht ist diese Ärztin darunter. Doch selbst die Kleidung gibt keine eindeutigen Hinweise. Sie hat schon längst die Farbe des Schmutzes angenommen. <new_line>All diese Menschen wurden wahrscheinlich von Räubern getötet. Als sie mitbekommen hatten, dass der starke Arm des Gesetzes schwach geworden war, sind die Räuber wagemutiger geworden und haben einen „Kontrollpunkt“ aufgestellt. Sie haben diese Menschen ausgeraubt, sie umgebracht und ihre Fahrzeuge und Habseligkeiten gestohlen. Die Ärztin hatte keine große Chance ...<new_line>Eine Schotterpiste führt von der Absperrung weg. Vielleicht führt er zum Lager dieser Räuber. Das alles geschah vor so langer Zeit, dort sollte es inzwischen nicht mehr gefährlich sein. Aber ich werde vorsichtig vorgehen.]],
			['19'] = [[Okay, das war‘s. In der Stadt gibt es keine Verrückten mehr. Ich sollte zum Boot zurückkehren, damit wir nach Belomorsk loskönnen und das kontaminierte Gelände vermeiden.]],
			['49'] = [[Ich hörte Schüsse. Sie führten mich zu einer Statue von Lysenko, dem berühmten Komponisten. Davor lag ein weiterer Wolf in einer riesigen dunkelroten Blutlache und verendete. Sein gesamter Körper war von Schusswunden übersät, sein Fell von Blut verklebt und regelrecht verfilzt.<new_line>Ein junger Mann mit einer Kalaschnikow lief leichtfüßig zu dem Wolf und leerte sein gesamtes Magazin in einem Anfall von Wahnsinn in den schon fast leblosen Körper des Tiers. Seine Gefährten, zwei ältere Männer, warteten in einiger Entfernung. <new_line>Schließlich ging dem jungen Mann die Munition aus. Er warf die Waffe auf die Treppe, völlig erschöpft und taumelnd. Er setzte sich daneben und vergrub sein Gesicht in den Händen. Die Männer kamen zu ihm. Einer legte ihm die Hand auf die Schulter.]],
			['39'] = [[Ich weiß jetzt, wo ich nach Genas Leiche suchen muss. Ich muss mich beeilen, ehe die Ratten oder andere Aasfresser ihn finden. Soll ich seiner Mutter etwa die verstümmelte Leiche ihres Sohnes bringen? Nein, auf dieses Vergnügen kann ich verzichten. <new_line>Wenn ich dort ankomme, muss ich die Leiche aus den Trümmern graben. Ich sollte eine Brechstange und eine Schaufel mitnehmen.]],
			['35'] = [[Als ich das Institut verließ, nahm ich zuerst einmal meine wichtigste Trophäe heraus und untersuchte sie: Es war der Brief aus dem Versteck der Staatlichen Virensammlung. Wie befürchtet war das billige Papier feucht geworden und die Tinte war verlaufen. Sowohl auf dem Umschlag als auch auf dem eigentlichen Brief.<new_line>Ich konnte nur ein wenig entziffern, aber das war ausgesprochen faszinierend. „Ich will dich nicht erschrecken, aber die Dinge stehen schlecht. Nesterow kam heute nicht ins Labor.“ Offenbar wurde hier ein Geheimnis gehütet. Ich muss nach Tula gehen und mit dieser Nadezhda Kulik sprechen. Ich hoffe, dass sie etwas weiß. Und dass sie noch lebt. <new_line>Wenn ich Glück habe, finde ich den Menschen, der diesen Brief geschrieben hat.]],
			['25'] = [[Im Institut habe ich einen Kassettenrekorder gefunden. Wenn ich Glück habe, gehörte er einem der Wissenschaftler hier. Ich muss die Batterien austauschen.]],
			['55'] = [[Anscheinend bin ich fertig. Es ist Zeit, den Schiffer zu finden und zu erfahren, wo das Wissenschaftsinstitut ist.]],
			['45'] = [[Es versteht sich von selbst: Ich habe nicht vor, wegen Varya zurückzugehen. Ich werde sie zu ihrer Tante bringen, die Einheimischen nach Gavrilov ausfragen und von dort verschwinden.<new_line>Ein merkwürdiges Detail riss mich aus meinen Gedanken: Das Gras um meine Sachen herum war zertrampelt. Ich sagte Varya, sie solle dort auf mich warten. Ich wollte herausfinden, woher der ungebetene Besucher gekommen war.<new_line>Ich musste nicht weit gehen. In der nassen Erde neben dem Gras fand ich Spuren.<new_line>Und diese Spuren hatte ich schon einmal gesehen. Beim Institut für Virologie. Sie sahen wie Wolfsspuren aus, aber sie waren viel zu groß, um zu einem normalen Wolf zu gehören.<new_line>Ein blinder schwarzer Wolf, gegen den Kugeln keine Wirkung zeigen ...<new_line>Wir müssen schnell von hier verschwinden.]],
			['41'] = [[Ich habe ewig gebraucht, um das Gebäude von Larisa Schaschkowa zu finden. Es ist ein Wunder, dass sich Nadezhda Kulik an die richtige Adresse erinnert hat.<new_line>Das Gebäude hat die Bombardierung fast ohne einen Kratzer überstanden. Ich stieg die Treppe nach oben und untersuchte dabei sorgfältig jedes einzelne Stockwerk. Ich hatte weiterhin Glück – unter der Türklingel an jeder Wohnung war ein Schild mit dem Nachnamen der Person, die darin gewohnt hatte.]],
			['31'] = [[Dem Anschein nach gehörte das Tagebuch einer Gefangenen, einer Ärztin mit dem Namen Swetlana. Vielleicht ist sie die Forscherin aus Leningrad?<new_line>Das bedeutet, dass sie an der Absperrung nicht getötet worden ist. Man hat sie hierher verschleppt. Sie haben die Gefangenen in dem Gebäude eingesperrt und sie gezwungen, die Räuber medizinisch zu versorgen. <new_line>Offensichtlich waren einige Menschen – die Räuber und die Ärztin – gegen das Virus immun. Leider weiß Swetlana auch nicht, wer die UdSSR bombardiert hat.<new_line>Sie schreibt, dass die Epidemie gleichzeitig im ganzen Land ausbrach. Das kann nicht stimmen.<new_line>Egal, ich sollte besser herausfinden, woran diese Kerle gestorben sind und was mit Swetlana passiert ist.]],
			['51'] = [[Als ich das Gemeindezentrum verließ, erschien vor meinem geistigen Auge eine blutverschmierte schwarze Schnauze. Ich dachte mich an meine früheren Begegnungen mit den schwarzen Wölfen. Ich hatte schon einmal eine dieser Bestien überlebt. Aber dieser Wolf ist nicht allein. Und unter dem Schutz ihrer Mutter sind Tiere meist wilder als sonst. <new_line>Von dieser Jagd werden viele Menschen nicht zurückkehren. <new_line>Aber ich verspürte keine Angst. Es war ein völlig anderes Gefühl. Ich sah den kopflosen Körper des Jägers, völlig zerfetzt, als würde es immer und immer wieder geschehen. Und ich konnte nichts dagegen tun.]],
			['21'] = [[Ich verstehe das einfach nicht. Was ist wirklich auf diesem Eisbrecher passiert?<new_line>Ich hoffe, dass die Überlebenden aus dem Lager etwas wissen. Der Schiffer hat mir erzählt, dass das Lager im Westen von Petrozavodsk liegt. Ich muss mich auf den Weg machen.]],
			['11'] = [[Leichen, zerstörte Häuser ... Das fühlt sich wie ein Alptraum an.<new_line>Monchegorsk ist noch ein Stückchen weiter voraus. Hoffentlich werde ich dort endlich Überlebende finden.]],
			['18'] = [[Die Zutaten für das Schießpulver sind jetzt zusammengetragen. Zeit, zum Treffpunkt zu gehen und sich ein für alle Mal um diese Verrückten zu kümmern.]],
			['28'] = [[Hier stimmt etwas nicht. Diese Ärztin ist offensichtlich nicht an ihrem Ziel angekommen. Ich sollte zu dieser seltsamen Absperrung zurückkehren, die ich auf meinem Weg hierher gesehen hatte. Sie sah wirklich sehr verdächtig aus.]],
			['38'] = [[Ich muss die Leiche eines Jungen zu seiner Mutter zurückbringen. Dies ist ein unglaublich trauriger Auftrag. <new_line>Es ist überraschend, wie das Leben manchmal so spielt. Man überlebt eine Epidemie, einen Atomkrieg, fünf Jahre Hungersnot und Entbehrungen. Und dann bricht das Leben wegen eines dummen Unfalls einfach so zusammen. Wird es mir ebenfalls so ergehen?<new_line>Ich muss diesen Vanya finden, der mit Gena losgezogen ist. Und ich muss herausfinden, in welchem Gebäude sich dieser Einsturz ereignet hat.]],
			['14'] = [[Als ich den Raum durchstöberte, entdeckte ich eine Sprühdose mit dem Etikett „Tag R“ und die Dokumente eines Marineoffiziers, der auf einem Eisbrecher in Belomorsk gedient hatte.<new_line>Seeleute verlieren nicht einfach so den Verstand. Wenn der Eisbrecher noch im Hafen von Belomorsk liegt, erfahre ich dort vielleicht mehr.<new_line>Zuerst muss ich aber herausfinden, was es mit diesem Funksignal aus Kandalaksha auf sich hat.]],
			['24'] = [[Der Schiffer hatte eine gute Idee: Ich sollte das Leningrader Wissenschaftsinstitut durchsuchen. Dort finde ich ganz sicher mehr Informationen. Vielleicht erfahre ich dann endlich, was im Herbst 1984 geschehen ist.]],
			['34'] = [[Eine vom Menschen geschaffene Epidemie. Das klingt wie das verrückte Gerede eines Wahnsinnigen. Aber wer kann schon behaupten, dass es nicht doch wahr ist? <new_line>Es gibt einige Dinge, die man weiterverfolgen muss. Tag R, das Telegramm, die Spraydose, Swetlanas Vater, das Institut für Virologie in Moskau. Wenigstens weiß ich, wohin ich gehen muss. Und die Belohnung, die am Ende auf mich wartet, wird die Mühe wert sein.<new_line>Aber wo soll ich nach meiner Familie suchen? Wo?]],
			['44'] = [[Wohin könnte der Forscher verschwunden sein? Ich habe das ganze Haus auf den Kopf gestellt und dennoch keine Hinweise gefunden. Wo könnte der Forscher bloß sein? Wäre die Datscha verlassen worden, hätte ich dort sicher etwas gefunden. Aber die ungebetenen Gäste haben dem Ort bereits ihren Stempel aufgedrückt.<new_line>Ich sollte die Gefangene befreien. Ich schnappte mir den Schlüssel von Temas Leiche und machte mich auf den Weg zur Scheune.]],
			['hint9'] = [[Finde Reagenzien im Elektronikladen und entwickle den Film.<new_line>(Gegenstände in der Kategorie „Aufgaben“.)]],
			['30'] = [[Ich fand ein Gebäude, das einst als Räuberlager gedient hatte. Dort standen viele verschiedene Fahrzeuge und es gab einen ganzen Berg von Sachen, die geraubt oder den Toten gestohlen wurden. Da lagen viele Waffen.<new_line>Im Speisesaal fand ich acht verfaulende männliche Leichen. Sie hatten sich zusammengekrümmt, als würden sie unter schrecklichen Bauchschmerzen leiden. <new_line>Neben einer der Leichen lag eine Tagebuchseite. Ich frage mich, ob der Räuber sie geschrieben hatte – oder stammt sie vielleicht von jemanden anderen?]],
			['40'] = [[Das Gespräch mit Nadezhda hatte mich völlig erschöpft. Aber das spielt keine Rolle, denn offensichtlich gehe ich nach Oryol. Und dann was? Wie hoch war die Wahrscheinlichkeit, dass ich im Haus von Schaschkows Schwester etwas Nützliches finde. Und woher soll ich bitte schön wissen, ob er seiner Schwester überhaupt nahestand?<new_line>Dann kroch unvermittelt ein Gedanke in meinen Verstand. Bestand womöglich ein Zusammenhang zwischen Andrey Kuliks Verrat und der Epidemie, die im ganzen Land zur gleichen Zeit ausbrach? Und wenn es eine Verbindung gibt, welche wäre das? <new_line>Ich habe mir das Telegramm des Nowgorod-Selbstmords noch einmal durchgelesen. Tag R ... <new_line>Nein, solange es noch die kleinste Aussicht auf Erfolg gibt, werde ich weitermachen!]],
			['50'] = [[Ich stand kurz vor des Rätsels Lösung. Zum ersten Mal hatte ich einem Menschen gegenübergestanden, der direkt mit diesen Ereignissen zu tun hatte. Wer sonst als der leitende Forscher sollte die reine Wahrheit kennen.<new_line>Ich muss nach Gorenichi!]],
			['10'] = [[Ich habe das Tagebuch eines Krankenpflegers gefunden. Etwas Schlimmes muss diesen Menschen passiert sein – und zwar lange, bevor die Bomben fielen. Schließlich hatten sie im Rathaus ein Krankenhaus eingerichtet. Vielleicht eine Epidemie?<new_line>Werde ich wohl das gleiche Bild in Olenegorsk vorfinden?]],
			['20'] = [[In Belomorsk erwartete mich die gleiche leblose Umgebung.<new_line>Ich sehe die Umrisse eines massiven Schiffs in der Ferne. Der alte Eisbrecher des Verrückten liegt noch im Hafen.]],
			['hint5'] = [[Die Schule durchsuchen]],
			['37_1'] = [[Wenn Swetlana und ihre Gefährten in dem Wohnblock Zuflucht gesucht haten, hatten die Überlebenden der Gegend ein ganzes Lager aus primitiven Holzhütten gebaut. Dem Rauch nach zu urteilen, hat sogar jemand einen funktionierenden Ofen gebaut. <new_line>Es waren insgesamt nur zwei Dutzend, die meisten davon Frauen unterschiedlichen Alters.]],
			['17'] = [[Der Schiffer sinnt auf Rache. Er hat mich gebeten, den Baumarkt nach Schwefel und Salpeter zu durchsuchen, damit er Schießpulver herstellen kann.]],
			['hint1'] = [[Ich muss Sapoljarny suchen, am Leben bleiben und den verlassenen Zug erreichen.]],
			['48'] = [[Je näher ich meinem Ziel kam, desto deutlicher konnte ich das Geräusch einordnen: Jemand schoss mit einer AK. Aber wer war der Schütze? Und wer war das Ziel?<new_line>An einer Kreuzung sah ich drei frische Leichen. Da lag einmal ein großer Mann von etwa 50 Jahren – sein Körper war mit Bissspuren übersät und seine Arme waren nur noch Stümpfe. Der zweite Leichnam war ein schwarzer Wolf, wie ich ihnen schon zuvor begegnet war, nur etwas kleiner. Seine Schnauze und sein Hals waren mit einem Messer aufgeschlitzt worden. Die dritte Leiche war kein ganzer Körper mehr, sondern nur noch Teile davon: Auf der ganzen Kreuzung waren Fleischklumpen und schwarzes Fell verstreut. Als wäre der Wolf mit Plastiksprengstoff oder etwas Ähnlichem zerfetzt worden.<new_line>Dann ratterte das Maschinengewehr wieder los.]],
			['hint3'] = [[Gehe nach Murmansk.]],
			['57'] = [[Ich habe erfahren, dass der Zentralteil des Landes durch den Atomkrieg ernsthaften Schaden genommen hat, aber in der Gegend von Twer leben noch Menschen.<new_line>Ich muss mich auf die Suche nach Swetlana machen. Der Chef der Wache sagte, sie könnte wohl noch in Twer leben.]],
			['47'] = [[Wie Moskau war Kiew von den Bomben schwer getroffen worden. Dutzende Gebäude waren nur noch Haufen aus Ziegelsteinen und Betonbrocken. Immer nach etwa zehn Minuten kam ich zum nächsten verbrannten Ödland.<new_line>Plötzlich hörte ich in der Ferne eine Explosion. Ich konnte es nicht fassen. Was konnte dort nach all den Jahren noch explodieren?<new_line>Ich rannte gen Stadtzentrum.]],
			['15'] = [[Der Schiffer bittet mich, seinen Freund zu finden. Dann hilft er mir, dieses radioaktive Ödland zu überqueren, um nach Belomorsk zu gelangen.]],
			['27'] = [[Als ich in die Stadt kam, fand ich das ausgebrannte Wrack eines Autos der Marke Wolga. Daneben lag die Leiche eines Mannes. Ich erkannte ein kleines rundes Loch im Scheitelbein des Schädels. So etwas hatte ich noch nie zuvor gesehen. Offenbar hatte er sich eine Waffe in den Mund gesteckt und sich das Gehirn weggepustet. Aber hier lag nirgendwo eine Waffe. Ich suchte in den Taschen der löchrigen Hose nach Patronen und stieß auf ein Stück Papier.<new_line>Tag R? Das kam mir bekannt vor. Das stand auch auf der Sprühdose dieses Verrückten aus Monchegorsk.]],
			['43'] = [[In der unteren Schublade des Schreibtischs fand ich ein ganzes Bündel Briefe. Es war überwiegend gehaltlose persönliche Korrespondenz, aber der letzte Brief erregte meine Aufmerksamkeit.<new_line>Was war also geschehen? Die anderen Wissenschaftler haben von meinem Verrat erfahren und es den Behörden gemeldet. Sie haben nicht einfach nur Kulik angezeigt, sondern auch Nesterow und dann noch jemand anderen. <new_line>Irgendetwas stimmt hier nicht. Der Brief ist an Schaschkow adressiert – mit einem Datum Ende April 1984. Aber im Juni dieses Jahres schrieb erhielt Swetlana einen Brief von ihrer Mutter, in dem sie schrieb, dass ihr Vater, Nesterow, an einem Herzinfarkt gestorben wäre.<new_line>Entweder habe ich etwas falsch verstanden oder Nesterow ist überhaupt nicht an einem Herzinfarkt gestorben. Wahrscheinlich hat man ihn zusammen mit Kulik hingerichtet. Svetlanas Mutter wollte ihrer Tochter nur die traurige Wahrheit ersparen. Sie wollte ihr nicht sagen, dass ihr Vater ein Verräter war. Herr im Himmel, was soll ich ihr sagen?<new_line>Ich weiß allerdings noch immer nicht, wie das alles mit der Epidemie zusammenhängt. <new_line>Hier werde ich nichts mehr finden. Ich denke, ich werde nach Bryansk gehen und nach diesem Forscher Gavrilov suchen.]],
			['53'] = [[Durch meinen Kopf waberten verschiedene Versionen, wie sich alles zugetragen haben könnte. Aber der Bericht des Direktors brachte alle Bruchstücke in die richtige Reihenfolge. Die letzten Lücken hatten sich geschlossen.<new_line>Würde mich die Wahrheit erleuchten? Würde ich die Menschen hassen, die höchstpersönlich die gesamte Zivilisation vernichtet hatten? Würde ich auf Rache sinnen? Nein. Für derart starke Emotionen war ich viel zu ausgelaugt. In mir war nur eine stille Trauer. <new_line>Es blieb nur eine Frage: Wie sollte ich es Swetlana erklären, dass ... ihr Vater für all dies verantwortlich ist?<new_line>In Kiew hält mich inzwischen nichts mehr. Es wird Zeit, dass ich nach Twer zurückkehre und Swetlana gegenübertrete.]],
			['hint2'] = [[Suche den Zug und lies die Zeitung.]],
			['41_lock'] = [[Ich fand die Wohnung der Schaschkows im obersten Stockwerk. Und dort wartete eine Überraschung auf mich: Die Tür dieser Wohnung war aus Stahl. Und das Schloss ließ sich nicht öffnen, egal was ich auch versuchte. <new_line>Mir blieb nur eine Möglichkeit: Ich musste sie aufsprengen.]],
			['23'] = [[Dieser Film- und Naturliebhaber hat die Explosion gefilmt. Eine Atomexplosion ist einfach unverwechselbar!<new_line>Eine Epidemie, Atombomben ... Was ist hier passiert? Hoffentlich finde ich im Fischerlager ein paar Antworten.]],
			['13'] = [[Das war niemand, dem ich begegnen wollte.<new_line>Wer auch immer dieser Verrückte früher gewesen sein mag - jetzt hatte er beinahe jede Spur von Menschlichkeit verloren.<new_line>Hatte ihm dieses Ödland den Verstand geraubt?]],
			['54'] = [[Es muss unerträglich sein, wenn man erkennt, dass der eigene Vater ein Verräter ist. Aber ich wusste, dass Swetlana eine starke Frau ist. Sie würde den Schmerz aushalten und damit zu leben lernen.<new_line>Sie tat mir so leid, aber ich konnte ihr nicht helfen.<new_line>Ich verließ das Gebäude und blickte hinauf zum Himmel. Es war kalt und dort oben zogen aschegraue Wolken vorbei. Ich dachte an die ferne Vergangenheit, und jeder Gedanke daran war von einem düsteren, leblosen Schleier getrübt.<new_line>Galya und Sasha. Ich weiß noch immer nicht, wo ich nach ihnen suchen soll. Aber das ist nicht wichtig. Ich werde sie trotz aller Widrigkeiten finden. Selbst wenn ich den Rest meines Lebens nach ihnen suchen muss.]],
			['33_1'] = [[Nach allem, was ich bisher gesehen habe, scheint Twer die letzte Bastion der Zivilisation zu sein.<new_line>Das Lager hat nicht nur überlebt, es blüht auf! Überall leben die Leute einfach ihr Leben. In der Ferne ist ein Dock und Arbeiter entladen aus den Schiffen Kisten mit Vorräten.<new_line>Schau, wie viele Menschen es gibt! Fast fühlt es sich wie ein Traum an. Als wäre nie etwas passiert und alles wäre, wie es sein soll.]]
		},
		['easter_quest'] = {
			['6'] = [[Ich habe alle Aufträge in Bezug auf die Körbe mit bemalten Eiern erledigt. Jetzt sollte ich zurückgehen und mir den versprochenen Hasenanzug holen.]],
			['5'] = [[Es muss noch ein Auftrag für den Osterhasen erledigt werden, dann bekomme ich den Hasenanzug.<new_line>Auftrag 4]],
			['4'] = [[Es müssen noch zwei Aufträge für den Osterhasen erledigt werden.<new_line>Auftrag 3]],
			['3'] = [[Es müssen noch drei Aufträge für den Osterhasen erledigt werden.<new_line>Auftrag 2]],
			['2'] = [[Ein seltsamer Typ in einem Hasenkostüm hat mich gebeten, für ihn zu arbeiten. Ich soll ihm Körbe mit bemalten Eiern bringen und er wird für jeden einzelnen bezahlen. Und wenn ich alle seine Aufträge erledige, schenkt er mir seinen Hasenanzug.<new_line>Auftrag 1.]],
			['1'] = [[Es geht das Gerücht um, dass ein wirklich seltsamer Typ Leute für eine sehr profitable Arbeit sucht. Ich sollte mich in der nächsten Siedlung von Überlebenden umhören, ob jemand von ihm gehört hat.]],
			['name'] = [[Osterauftrag]]
		},
		['halloween_hunt'] = {
			['name'] = [[Monsterjagd]],
			['3'] = [[Frankensteins Monster kann nur mit Baba Yagas magischem Garnknäuel gefunden werden. Der Jäger hat mir Anweisungen gegeben, um sie zu beschwören. Ich muss das Ritual unbedingt ordnungsgemäß durchführen, sonst muss ich wieder von vorne anfangen.]],
			['2'] = [[Die Mumie ist tot. Diesmal für immer (ich habe extra noch einmal nachgesehen). Ich muss nun zurück zum Jäger.]],
			['1'] = [[Ich stieß auf einen verwundeten Monsterjäger. Er bat mich, eine uralte Kreatur zu finden und zu vernichten: eine Mumie. Ich muss zu dem Ort gehen, der auf der Karte eingezeichnet ist, und mir das ansehen.]],
			['8'] = [[Der Kürbiskönig ist besiegt. Jetzt muss ich seinen Kopf zum Jäger bringen.]],
			['7'] = [[Ich muss die Anweisungen des Jägers befolgen, um den Kürbiskönig zu beschwören und ihn zu vernichten.]],
			['6'] = [[Ich habe Frankensteins Monster vernichtet. Jetzt kann ich zum Jäger zurückkehren. Dieses Mal wird er mir garantiert die Belohnung geben, die ich mir verdient habe.]],
			['5'] = [[Ich habe jetzt den verzauberten Garnknäuel. Jetzt muss ich damit nur noch Frankensteins Monster finden.]],
			['4'] = [[Um den verzauberten Garnknäuel zu erhalten, muss ich die Sachen auf Baba Yagas Liste sammeln. Dann kann ich sie erneut beschwören.]],
			['12'] = [[Dracula wurde besiegt und die Jagd ist vorbei. Der Jäger hat gesagt, dass wir uns vielleicht im kommenden Jahr wiedersehen.]],
			['10'] = [[Der Reiter wurde besiegt und sein Pferd wird jetzt mir dienen. Ich muss zurückkehren und es dem Jäger berichten.]],
			['9'] = [[Jetzt muss ich mich dem Kopflosen Reiter stellen und ihn besiegen. Tja, dazu muss ich wohl nur durch die Gegend fahren und abwarten.]],
			['11'] = [[Unser letztes Ziel heißt Graf Dracula. Der Jäger hat mir gesagt, dass ich ihm Bescheid geben soll, wenn ich für den Kampf bereit bin.]]
		},
		['emba_intro'] = {
			['5'] = [[Ich nehme an der Forschungsexpedition der Progress-Organisation auf der Suche nach einer geheimen Technologie teil. Ich muss mit Dr. Issajew sprechen.]],
			['4'] = [[Ich habe die Bedrohung durch den Bunker ausgeschaltet. Ich muss zum Navigator zurückkehren.]],
			['3'] = [[Die Expedition muss sich in der Siedlung niederlassen, aber die Bewohner sind misstrauisch gegenüber Wissenschaftlern. Ich werde das Problem lösen, indem ich die Bedrohung in der Nähe der Siedlung ausschalte.]],
			['2'] = [[Die Expedition muss sich in der Siedlung niederlassen, aber die Bewohner sind misstrauisch gegenüber Wissenschaftlern. Ich muss helfen, ihr Vertrauen zu gewinnen. Der Expeditionsnavigator wird mich an dem angegebenen Ort auf den neuesten Stand bringen.]],
			['1'] = [[Ich helfe einer Expedition von Wissenschaftlern der Progress-Organisation. Sie untersuchen neue biologische Anomalien.<new_line>Dr. Issajew, der Leiter der Expedition, weiß vielleicht, woher diese Anomalien stammen und welche Bedrohung sie für die überlebenden Reste der Menschheit darstellen. Ich werde ihn in der Nähe von Twer finden.<new_line>(Die Aufgaben von Dr. Issajew in der Nähe von Twer sind ab Stufe 25 verfügbar)]],
			['name'] = [[Expeditionsunterkunft]]
		},
		['easter_quest_2'] = {
			['2'] = [[Ich habe genug Eier an den Käufer verkauft. Es wird Zeit, die versprochene Belohnung zu kassieren.]],
			['1'] = [[Der Käufer hat mir angeboten, ihm bemalte Eier zu einem guten Preis zu verkaufen. Wenn ich ihm bis zum Ende der Festtage genug Eier verkaufe, verspricht er mir eine Belohnung.]],
			['name'] = [[Auf der Jagd nach der Belohnung]]
		},
		['new_year2022'] = {
			['name'] = [[Neues Jahr 2023]],
			['3'] = [[Ich muss gute Taten vollbringen, um die Magie in diese Welt zurückzubringen. Als Belohnung bekomme ich Schmuck, mit denen ich den Weihnachtsbaum schmücken kann. Dann werden wir die nächste magische Plattform aktivieren.]],
			['2'] = [[Als ich den Weihnachtsbaum geschmückt habe, ist so ein komischer Typ aufgetaucht. Er sieht aus wie der Weihnachtsmann, aber er sagt, er sei nicht von dieser Welt. Ich willigte ein, ihm zu helfen, unseren Weihnachtsmann zu finden.]],
			['1'] = [[Ich befinde mich an einem seltsamen Ort. In der Mitte steht ein prächtiger Weihnachtsbaum. Intuitiv weiß ich, dass das Ornament, das ich gefunden habe, daran hängen sollte.]],
			['6'] = [[Bärnachtsmann sagte mir, ich solle mich um das Geschenkelager im Vergnügungspark „kümmern“. Sobald ich den Ort unter Kontrolle habe, werden wir seinen Plan zur gerechten Verteilung der Ressourcen in die Tat umsetzen.]],
			['4'] = [[Es stellt sich heraus, dass Höhlennachtsmann der Fährtenleser ist. Offenbar hat er die Spur des Weihnachtsmanns aufgenommen. Wir müssen ihm folgen.]],
			['12'] = [[Alles ist bereit für den Kampf gegen Frosti. Zaubernachtsmann wartet auf mein Signal, mich zu Frostis Versteck zu schicken.]],
			['13'] = [[Wir haben Weihnachten gerettet! Ich feiere diesen Sieg mit meinen neuen Freunden.<new_line>Jetzt werden wir gemeinsam daran arbeiten, Weihnachten wiederzubeleben und das Fest zu den Menschen zu bringen.]],
