	['randomEvent'] = {
		['destroy_lair_quest'] = {
			['episode'] = {
				['16'] = [[Ich löschte die örtlichen Mutanten aus. Sie werden keinen Ärger mehr machen.]],
				['15'] = [[Ich zerstörte den Stock der mutierten Insekten.]],
				['14'] = [[Ich habe das Skorpionennest ausgeräuchert.]],
				['3'] = [[In den Wäldern kam ich an einem Wildschweinlager vorbei. Anscheinend war ich am richtigen Ort.]],
				['2'] = [[Ich kam am Standort an. Nur ein paar Minuten zuvor war ich von einem Wolfsrudel angegriffen worden.]],
				['1'] = [[Als ich hierher kam, sah ich als Erstes eine Bärenhöhle.]],
				['5'] = [[Als ich in geringem Abstand vom vorgesehenen Standort unterwegs war, kam ich an einem Stock mit Mutanten-Bienen vorbei.]],
				['4'] = [[Bei meiner Ankunft am vorgesehenen Standort bemerkte ich mehrere Skorpione.]],
				['12'] = [[Dieses Wolfsrudel wird keine Probleme mehr bereiten.]],
				['13'] = [[Das Lager ist zerstört. Jetzt kann ich zur Basis zurückkehren.]],
				['9'] = [[Die Koordinaten führten mich tief in den Wald hinein. Ich hatte nicht einmal die Gelegenheit, mich umzusehen, bevor ich von mutierten Kreaturen angegriffen wurde.]],
				['11'] = [[Ich schaffte mir die Bären vom Hals, damit kein anderer verletzt würde.]]
			},
			['choice'] = {
				['3'] = [[Zurückschlagen]]
			}
		},
		['handcar'] = {
			['episode'] = {
				['3'] = [[Nachdem ich mir die Wiese genauer angesehen hatte, pflückte ich einige Spitzwegerich-Blätter. Dabei verdunkelte sich der Himmel vollständig. Bald würde die Nacht hereinbrechen.]],
				['2'] = [[Ich stopfte meinen Bauch mit Erdbeeren voll und pflückte sogar noch welche für unterwegs. Als ich damit fertig war, war die Sonne schon beinahe am Horizont untergegangen. Es würde bald dunkel sein.]],
				['1'] = [[Nachdem ich einige Kilometer mit der Draisine gefahren war, sah ich am Fuße einer Böschung einen Bach. Um ihn herum war eine Wiese, auf der viele wilde Kräutern und Erdbeersträucher direkt am Wasser wuchsen. Ein reiner und friedlicher Ort.]],
				['7'] = [[Meine Augenlider wurden zwar schwer, aber ich setzte meinen Weg fort. Plötzlich vernahm ich hinter mir ein schnaufendes Geräusch, als würde eine Dampfmaschine auf mich zu rasen. Durch den Nebel schälte sich langsam eine gigantische Silhouette ...]],
				['6'] = [[Am Morgen stand der Nebel weiterhin wie eine Mauer um mein kleines Lager. Ich hatte mich glücklicherweise trotzdem gut erholt und setzte meine Reise mit neuer Kraft fort.]],
				['5'] = [[Ich hielt die Draisine an. Ich wollte keinesfalls auf der fahrenden Draisine einschlafen und von den Gleisen stürzen. Ich entfachte ein Feuer am Fuße der Böschung und bereitete mein Nachtlager vor.]],
				['4'] = [[Die Nacht begann. Die Erde war in einen Dunstschleier gehüllt. Nach wenigen Kilometern verschluckte der Nebel die ganze Welt um mich herum. Ich war erschöpft und fiel in einen tiefen Schlummer.]],
				['9'] = [[Der Igel näherte sich rasend schnell. Seine Pfoten verbogen die Schienen wie Papier. In wenigen Sekunden würde er mich zerfetzen.]],
				['8'] = [[Aus dem Nebel tauchte ein Igel auf, so groß wie ein Lkw. Seine Augen leuchteten wie zwei Projektionsapparate und aus seinem weit aufgerissenen Rachen strömte sein heißer Atem wie Dampf. Sein ohrenbetäubendes Brüllen warf mich fast von der Draisine.]],
				['18'] = [[Ich war zwar vorsichtig, aber ich schnitt mich trotzdem an einer der Stangen und zerriss eines meiner Hosenbeine.]],
				['28'] = [[In den umgestürzten Güterwaggons war außer einem Haufen Altmetall nichts Nützliches.]],
				['38'] = [[Offenbar ist das ein seltsames Radio, aus dem ein Draht mit einem kurzen Metallrohr am Ende ragt. In der Ledertasche war eine Gebrauchsanleitung für das Dosimeter-Radiometer DP-5V.]],
				['14'] = [[Ich sprang und stürzte die Böschung hinab. Der Igel stieß die Draisine mit einem Schlag von den Gleisen. Ich sah nur noch, dass dieser verbogene Metallklumpen direkt in meine Richtung flog.]],
				['24'] = [[Meine hart erkämpfte Flasche Wodka zersplitterte direkt vor dem Luchs. Erschrocken sprang er davon und verschwand im Nebel. Jetzt kann ich von hier verschwinden.]],
				['34'] = [[Die Leiche trug einen schmutzigen Mantel, eine Gasmaske ohne Filter und eine Reisetasche über den Schultern. In den Manteltaschen entdeckte ich eine zusammengerollte Zeitung.]],
				['26'] = [[Die Vorderseite der Lokomotive hatte sich am Fuße der Böschung in die Erde gegraben. Die Güterwaggons hatten sich dahinter zu einem Haufen Metall zusammengeschoben, aber einige Personenwagen standen noch auf den Gleisen.]],
				['36'] = [[Durch die Gasmaske sah ich das abgemagerte Gesicht eines Mannes, das noch nicht verfault war.]],
				['30'] = [[Als ich den Waggon verließ, sah ich ein menschliches Skelett im Gras. Wilde Tiere hatten die Überlebenden des Zugunglücks zerfleischt und bis auf die Knochen abgenagt.]],
				['40'] = [[Ich zog die Leiche zum Fuß der Böschung hinab. Jetzt wusste ich, was den armen Kerl getötet hatte: Die Strahlenkrankheit hatte ihn von innen heraus verzehrt.]],
				['32'] = [[Der Rest des Wolfsrudels stimmte in das Heulen ein. Ich lief zur Draisine, betätigte den Handhebel mit aller Kraft und versuchte, nicht über meine Schulter zu blicken.]],
				['42'] = [[Ehe ich ging, sah ich, dass aus seiner Tasche ein kleines Stück Papier ragte. Darauf stand die Adresse eines Hauses in der Stadt Kola. Was befindet sich dort? Überlebende? Oder Vorräte?]],
				['12'] = [[Ich schoss dem Igel einige Male ins Gesicht, aber das schien die riesige Kreatur gar nicht zu spüren. Der Igel riss sein Maul weiter auf, sprang auf mich zu und verschlang mich und ein Stück der Draisine.]],
				['22'] = [[Ich brüllte, schwenkte meine Fackel über dem Luchs. Erschrocken sprang er davon und verschwand im Nebel. Jetzt kann ich von hier verschwinden.]],
				['10'] = [[Verzweifelt betätigte ich mit aller Kraft den Handhebel, doch schon bald brannte der feurige Atem des Igels in meinem Rücken. Das war das letzte Gefühl, ehe die Finsternis alles um mich herum verschluckte.]],
				['20'] = [[Ich fand schnell eine halb leere Ölkanne. Ich ging wieder zur Draisine zurück, ölte den Hebel der Gleisweiche und schaltete die Gleisstellung um. Jetzt kann ich weiterfahren ... oder hierbleiben und meine Suche fortsetzen.]],
				['17'] = [[Eine Betonplattform und ein baufälliges Gebäude. Überreste von Stangen ragen aus den zerbrochenen Fenstern. In der verschlossenen Tür sieht man tiefe Klauenspuren.]],
				['16'] = [[Einige Stunden später schälte sich der Umriss eines kleinen Bahnhofs aus dem Nebel. Dort gabelten sich die Schienen. Die Gleisweiche würden die Draisine in eine Sackgasse lenken, aber die Weiche war vollständig verrostet. Ich muss einen Weg finden, sie umzustellen.]],
				['29'] = [[In den Personenwagen fand ich lediglich ein paar durchwühlte Koffer, eine Packung Bandagen und ... jede Menge Blutflecken. Aber wo sind all die Menschen?]],
				['19'] = [[Ein paar Schläge mit der Axt lösten das Problem der verschlossenen Tür. Ich betrat das Haus.]],
				['15'] = [[Ich landete am Fuße der Böschung. Ich musste eingeschlafen und von der Draisine gestürzt sein. Beulen und blaue Flecken überzogen meinen ganzen Körper. Durch diesen irrsinnigen Traum hätte mir fast den Hals gebrochen.]],
				['39'] = [[Als ich den Schalter umlegte, surrte das Dosimeter. Ich schwenkte mit der Sensorröhre durch die Gegend und als ich sie über die Leiche bewegte, verstärkte sich das Surren. Die Leiche ist extrem radioaktiv!]],
				['35'] = [[Die Gespräche mit den USA verschlechtern sich ...]],
				['25'] = [[Der Nebel verzog sich ein wenig und die Sümpfe wichen zurück und machten den Wäldern Platz. Aus den Tiefen des Waldes führte parallel zu den Gleisen, auf denen ich unterwegs war, eine weitere Schmalspurbahn. Ein Stück weiter entdeckte ich einen entgleisten Zug.]],
				['37'] = [[In der Tasche waren einige Bandagen und ein kleines Gerät in einer Ledertasche mit Riemen.]],
				['27'] = [[Die Lokomotive war leer, aber in der Schaffnerkabine fand ich ein kleines Päckchen mit einer Handvoll Pistolenkugeln. Die Pistole selbst fand ich aber nirgendwo.]],
				['41'] = [[Vielleicht setze ich mich der Strahlung aus, aber zumindest finden ihn so keine wilden Tiere. Ich sammelte einige Steine und errichtete über ihm einen kleinen Grabhügel.]],
				['31'] = [[Die Spur der Knochen führte in den Wald. Zwischen den Bäumen zerriss plötzlich das Heulen eines Wolfes die Stille. Ich verschwinde besser von hier!]],
				['43'] = [[Je länger bei dieser Leiche bleibe, desto länger bin ich der Strahlung ausgesetzt. Ich verschwinde besser und gehe schnell zurück zur Draisine.]],
				['33'] = [[Als ich mich Murmansk näherte, sah ich etwas vor mir auf den Gleisen liegen. Ich hielt die Draisine an und trat näher. Es war eine Leiche.]],
				['23'] = [[Ich zog meine Pistole und schoss auf den Luchs. Die Kugel traf die Draisine und die Funken stoben davon. Erschrocken sprang der Luchs davon und verschwand im Nebel. Jetzt kann ich von hier verschwinden.]],
				['13'] = [[Als ich die Augen öffnete, lag ich auf der Draisine. Der Schlaf musste mich übermannt haben. Während dieses wahnsinnigen Alptraums hatte ich auch meine Pistole verloren. Nach einiger Zeit fand ich sie endlich etwas weiter unten auf den Gleisen.]],
				['21'] = [[Bei meiner Suche fand ich lediglich ein Seil und eine Flasche Wodka, die jemand versteckt hatte. Als ich zur Draisine zurückkehrte, sah ich einen großen Luchs, der um sie herumschlich. Als er mich bemerkte, krümmte er seinen Rücken und fauchte.]],
				['11'] = [[Ich öffne meine Augen. Mich umgab eine Nebelwand. Nur mein schweres Atmen erklang in der Stille. Ich musste eingeschlafen sein und hatte mich die ganze Zeit über am Handhebel der Draisine festgeklammert. Was für verrücktes Zeug hatte ich da nur geträumt?!]]
			},
