		['embacity_reservoir_empty'] = {
			['episode'] = {
				['1'] = [[Vor mir befindet sich eine Pumpstation mit Rohren, die zu einer großen Zisterne und in den Untergrund führen. Jetzt ist die Zisterne leer. Die gleichen Rohre sind auch im Stadtzentrum in der Nähe des Biolabors zu sehen.]],
				['2'] = [[Vor mir befindet sich eine Pumpstation mit Rohren, die zu einer großen Zisterne und in den Untergrund führen. Neben dem Pumpenhebel befindet sich eine Tafel mit Tasten, auf denen Zahlen eingezeichnet sind. Offenbar braucht man einen Zugangscode.<new_line>Da ich keinen habe, drücke ich eine zufällige Kombination von Tasten und lege den Hebel um. Ein lauter, unangenehmer Piepton ertönte, und der Hebel schnappte in seine vorherige Position zurück.]]
			}
		},
		['embacity_biolab_0'] = {
			['episode'] = {
				['3'] = [[Ein modriger Geruch strömte aus dem offenen Fach. Als ich genau hinsah, sah ich einen Behälter und darüber ein Schild mit der Aufschrift „Notfallrekorder“. Das war die Blackbox, nach der ich gesucht hatte.]],
				['2'] = [[Hinter der Tür befand sich eine Treppe. Sie führte mich in eine geräumige Halle, die völlig verlassen aussah. Ich ging durch den Raum und fand eine unauffällige Tür mit einem Codeschloss.]],
				['1'] = [[Das erste, was mir auffiel, als ich das Labor betrat, war das rot blinkende Licht über der Tür an der hinteren Wand des Raumes. Diese Tür erwies sich als eine sehr massive, automatisch gesteuerte Tür. Sie war jetzt angelehnt.]]
			},
			['choice'] = {
				['1'] = [[Durch die Tür gehen]],
				['2'] = [[Den Code eingeben]]
			}
		},
		['gigahive_bar'] = {
			['episode'] = {
				['5'] = [[„Nun, Kamerad, du riechst echt nach Bienen! Hast du wirklich die Königin selbst erlegt?! Ich glaube dir kein Wort, bring mir ihren Kopf hier her.“]],
				['4'] = [[„Ich kann kaum glauben, dass du die Bienenkönigin getötet hast! Du bist jetzt ein lokaler Held! Ich werden diese Trophäe direkt hier in der Taverne aufhängen! Und ich habe dir eine ordentliche Belohnung versprochen. Hier, nimm das!“]],
				['3'] = [[„Im Moment haben wir nichts mehr, aber wenn du ein bisschen Wachs für unseren Spezialisten mitbringst, kann er dir eine Dosis verpassen. Du findest Stöcke mit Mutterbienen überall in der Nähe von Ufa, hol dort das Wachs.“]],
				['2'] = [[So schützen sie sich vor Angreifern von außen. Wir haben für dieses Zeug sogar ein Gegenmittel entwickelt.<new_line>Willst du ernsthaft da rein gehen? Okay, wenn du die Königin irgendwie töten kannst, dann bring mir ihren Kopf und ich werde dich ordentlich belohnen.“<new_line>]],
				['1'] = [[„Das ist ein sehr gefährlicher Ort: Das Heim der Bienenkönigin. Viele haben versucht sie zu töten, aber keiner hat den Bienenstock je lebend verlassen. Mögen sie in Frieden ruhen.“]]
			},
			['choice'] = {
				['3'] = [[Ihm zunicken und gehen]],
				['2'] = [[„Aber wo bekomme ich ein Gegenmittel?“]],
				['1'] = [[Die Blasen erwähnen]]
			}
		},
		['lair2'] = {
			['episode'] = {
				['1'] = [[Der aromatische Geruch der Öle und anderer Zutaten stieg mir in die Nase, sobald ich den Raum betrat. Im Inneren war es stockdunkel. Plötzlich bewegte sich etwas, das ich im ersten Moment für eine Statue hielt, schlagartig auf mich zu. Wie durch ein Wunder konnte ich noch ausweichen. Es war der Pestarzt.]]
			}
		},
		['guard_leningrad'] = {
			['episode'] = {
				['6'] = [[„Gut, ziehe nach Süden. Dort gibt es eine Siedlung mit Überlebenden am Hafen von Twer. Ursprünglich haben wir dort gelebt. Ich kenne Swetlana auch. Sie hat viele Leben gerettet. Ich glaube, dass sie noch dort ist.<new_line><new_line>Nimm deine Ausrüstung, nutzte sie klug und hab ein Auge auf den Geigerzähler. Man landet so leicht im radioaktiven Ödland.“]],
				['5'] = [[„Bist du jetzt besser vorbereitet?“]],
				['4'] = [[„Heutzutage gibt es dort nicht mehr viele von uns. Wir müssen einander helfen. Und du bist nützlicher, wenn du am Leben bleibst.“]],
				['3'] = [[„Hör zu, in der Gegend um Moskau ist es gefährlich. Auch in Twer. Das Gebiet war ein wichtiges feindliches Ziel und sie wurden schwer von Atomschlägen getroffen. Jetzt ist die Strahlung sehr hoch!<new_line><new_line>Mir ist gerade etwas eingefallen. Hilf mir hier ein bisschen. Ich helfe dir bei der Vorbereitung der gefährlichen Reise, die du planst. Als Dankeschön gebe ich sogar ein bisschen Ausrüstung dazu. So wirst du nicht einfach auf der Stelle tot umfallen, wenn du dort ankommst.“]],
				['2'] = [[„Was brauchst du dort? Wir könnten hier wirklich deine Hilfe brauchen.“]],
				['1'] = [[„Suchst du etwas?“]]
			},
			['choice'] = {
				['7'] = [[„Vielen Dank.“]],
				['6'] = [[„Ja.“]],
				['5'] = [[„Noch nicht.“]],
				['4'] = [[„Okay. Bis zum nächsten Mal.“]],
				['3'] = [[„Warum kümmert dich das?“]],
				['2'] = [[„Ich muss etwas Wichtiges erledigen. Ich suche jemanden.“]],
				['1'] = [[„Ich möchte wissen, ob es eine Siedlung von Überlebenden in Twer gibt.“]]
			}
		},
		['emba_energy_easy'] = {
			['episode'] = {
				['7'] = [[„Oh, verdammt noch mal! Es hat keinen Sinn, es wird wieder nachwachsen und alles fängt von vorne an. Wie auch immer. Danke für den Versuch, aber du solltest deine Grenzen kennen …“<new_line><new_line>Der Bauer stapft niedergeschlagen auf seine Hütte zu und tritt dabei gegen eine Wurzel, die direkt vor deinen Augen in den Boden wächst.]],
				['6'] = [[„Oof, jetzt sind wir fertig! Endlich wird der ganze Müll verbrannt und ich kann mich wieder um die Farm kümmern.<new_line>Vielen Dank, du bist ein toller Kerl. Ich werde dich für die Arbeit bezahlen, so gut ich kann.“]],
				['5'] = [[Es ist, als ob das Myzel versucht hätte, die Scheune mit einem festen Knäuel aus Ästen und Wurzeln zu umhüllen, aber wir haben es geschafft.<new_line>Jetzt müssen wir nur noch das ganze mutierte Grünzeug auf einen Haufen sammeln und verbrennen. Der Bauer ist losgezogen, um Benzin zu holen, und hat mir gesagt, ich solle die Wurzeln einsammeln.]],
				['4'] = [[Wir haben den ersten Teil geschafft und die Felder geräumt, aber das ist noch nicht alles, wir müssen zur Scheune weitergehen.]],
				['3'] = [[„Kumpel, du bist mein Retter!<new_line>Siehst du, du musst alle diese Äste auf einmal abschneiden, ins Feuer legen und anzünden. Du hast keine Zeit, dich auszuruhen, sonst wird dieser Dreck wieder Wurzeln schlagen und seine Sporen überall verbreiten. Fangen wir mit den Feldern an.“]],
				['2'] = [[„Das war meine eigene Dummheit! Mein Nachbar erzählte mir Geschichten über dieses Myzel, das den Boden düngt und für bessere Ernten sorgt. Ich habe es in den Gewächshäusern gepflanzt und es ist so gut gewachsen, dass ich es jetzt nicht einmal mehr selbst beschneiden kann. Bitte hilf mir!“]],
				['1'] = [[„Hey, warte! Ich brauche Hilfe. Diese verdammten Wurzeln haben die ganze Farm bedeckt. Die grünen Mutanten haben schon angefangen, uns zu besuchen, als ob wir ihre persönliche Kantine wären.“]]
			},
			['choice'] = {
				['3'] = [[Staple die Wurzeln auf]],
				['2'] = [[Zerschneide die Wurzeln]],
				['1'] = [[„Wie ist das passiert?“]]
			}
		},
