		['santa_bear'] = {
			['choice'] = {
				['3'] = [[„Sollen wir ihm einfach die Geschenke wegnehmen?“]],
				['2'] = [[„Was genau meinst du?“]],
				['1'] = [[„Das ist eine Weihnachtstradition.“]]
			},
			['episode'] = {
				['6'] = [[„Geschäftsnachtsmann verhandelt mit dem Manager. Ich werde mich mit der Bande in Verbindung setzen. Und deine Aufgabe ist es, dich um das Geschenkelager im Park zu kümmern. Gut, fangen wir mit der Aktion an, bevor der Krampusnachtsmann uns zuvorkommt!“]],
				['5'] = [[„Hmm, wenn wir Krampus ein gutes Angebot machen … Nicht weit von hier gibt es einen Vergnügungspark.<new_line>Ich kenne den Geschäftsführer dort recht gut, ich habe ihn mit Geschenken und Schmuck versorgt. Vielleicht hat die Bande den Park noch nicht gefunden.“]],
				['4'] = [[„Ich weiß nicht, wie die Dinge in deinen Fabriken laufen, aber hier? Sieh nur, wozu das geführt hat! Die Arbeiter dürfen nicht von den Produktionsmitteln entfremdet werden. Wir müssen die materiellen Güter umverteilen.“]],
				['3'] = [[„Und schon wieder diese Ausbeutungsgeschichte …“]],
				['2'] = [[„Weißt du, was in eurem Sozialsystem falsch läuft? Ihr beutet seit Jahren den gesamten Urlaubsindustriekomplex aus.“]],
				['1'] = [[„Hallo, Genosse! Geschäfti, wie geht es dir, du alter Fuchs? Uuh ah, Höhle!“]]
			}
		},
		['rabbit_teleport'] = {
			['episode'] = {
				['3'] = [[Die Stimme in meinen Kopf fuhr fort: <new_line>„Eine gute Wahl. Wirf eine Leckerei in den Hasenbau und spring dann hinein.“]],
				['2'] = [[Die Stimme blieb einige Sekunden lang still und fuhr dann fort: <new_line>„Momentan können wir zu einigen Städten reisen. Welche wählst du?“]],
				['1'] = [[Als ich mich dem Hasenbau näherte, hörte ich eine Stimme in meinem Kopf:<new_line>„Sei gegrüßt, Wanderer. Wie ich sehe, machst du dich auf zu einer Reise.“]],
